arasuperobject.biblemap.put("b1_7_2", new arasuperobject(1,7,2,"من جميع البهائم الطاهرة تأخذ معك سبعة سبعة ذكرا وانثى. ومن البهائم التي ليست بطاهرة اثنين ذكرا وانثى"));
arasuperobject.biblemap.put("b1_7_3", new arasuperobject(1,7,3,"ومن طيور السماء ايضا سبعة سبعة ذكرا وانثى. لاستبقاء نسل على وجه كل الارض"));
