arasuperobject.biblemap.put("b1_8_17", new arasuperobject(1,8,17,"وكل الحيوانات التي معك من كل ذي جسد الطيور والبهائم وكل الدبابات التي تدب على الارض اخرجها معك. ولتتوالد في الارض وتثمر وتكثر على الارض"));
arasuperobject.biblemap.put("b1_8_18", new arasuperobject(1,8,18,"فخرج نوح وبنوه وامرأته ونساء بنيه معه"));
