arasuperobject.biblemap.put("b1_9_2", new arasuperobject(1,9,2,"ولتكن خشيتكم ورهبتكم على كل حيوانات الارض وكل طيور السماء. مع كل ما يدبّ على الارض وكل اسماك البحر قد دفعت الى ايديكم"));
arasuperobject.biblemap.put("b1_9_3", new arasuperobject(1,9,3,"كل دابة حية تكون لكم طعاما. كالعشب الاخضر دفعت اليكم الجميع"));
arasuperobject.biblemap.put("b1_9_4", new arasuperobject(1,9,4,"غير ان لحما بحياته دمه لا تاكلوه"));
arasuperobject.biblemap.put("b1_9_5", new arasuperobject(1,9,5,"واطلب انا دمكم لانفسكم فقط. من يد كل حيوان اطلبه. ومن يد الانسان اطلب نفس الانسان. من يد الانسان اخيه"));
arasuperobject.biblemap.put("b1_9_6", new arasuperobject(1,9,6,"سافك دم الانسان بالانسان يسفك دمه. لان الله على صورته عمل الانسان"));
arasuperobject.biblemap.put("b1_9_7", new arasuperobject(1,9,7,"فاثمروا انتم واكثروا وتوالدوا في الارض وتكاثروا فيها"));
arasuperobject.biblemap.put("b1_9_8", new arasuperobject(1,9,8,"وكلم الله نوحا وبنيه معه قائلا"));
arasuperobject.biblemap.put("b1_9_9", new arasuperobject(1,9,9,"وها انا مقيم ميثاقي معكم ومع نسلكم من بعدكم"));
arasuperobject.biblemap.put("b1_9_10", new arasuperobject(1,9,10,"ومع كل ذوات الانفس الحيّة التي معكم. الطيور والبهائم وكل وحوش الارض التي معكم من جميع الخارجين من الفلك حتى كل حيوان الارض"));
arasuperobject.biblemap.put("b1_9_11", new arasuperobject(1,9,11,"اقيم ميثاقي معكم فلا ينقرض كل ذي جسد ايضا بمياه الطوفان. ولا يكون ايضا طوفان ليخرب الارض"));
arasuperobject.biblemap.put("b1_9_12", new arasuperobject(1,9,12,"وقال الله هذه علامة الميثاق الذي انا واضعه بيني وبينكم وبين كل ذوات الانفس الحيّة التي معكم الى اجيال الدهر"));
arasuperobject.biblemap.put("b1_9_13", new arasuperobject(1,9,13,"وضعت قوسي في السحاب فتكون علامة ميثاق بيني وبين الارض"));
