arasuperobject.biblemap.put("b1_9_17", new arasuperobject(1,9,17,"وقال الله لنوح هذه علامة الميثاق الذي انا اقمته بيني وبين كل ذي جسد على الارض"));
arasuperobject.biblemap.put("b1_9_18", new arasuperobject(1,9,18,"وكان بنو نوح الذين خرجوا من الفلك ساما وحاما ويافث. وحام هو ابو كنعان"));
