arasuperobject.biblemap.put("b1_11_5", new arasuperobject(1,11,5,"فنزل الرب لينظر المدينة والبرج اللذين كان بنو آدم يبنونهما"));
arasuperobject.biblemap.put("b1_11_6", new arasuperobject(1,11,6,"وقال الرب هوذا شعب واحد ولسان واحد لجميعهم وهذا ابتداؤهم بالعمل. والآن لا يمتنع عليهم كل ما ينوون ان يعملوه"));
arasuperobject.biblemap.put("b1_11_7", new arasuperobject(1,11,7,"هلم ننزل ونبلبل هناك لسانهم حتى لا يسمع بعضهم لسان بعض"));
