arasuperobject.biblemap.put("b1_11_10", new arasuperobject(1,11,10,"هذه مواليد سام. لما كان سام ابن مئة سنة ولد ارفكشاد بعد الطوفان بسنتين"));
arasuperobject.biblemap.put("b1_11_11", new arasuperobject(1,11,11,"وعاش سام بعدما ولد ارفكشاد خمس مئة سنة وولد بنين وبنات"));
arasuperobject.biblemap.put("b1_11_12", new arasuperobject(1,11,12,"وعاش ارفكشاد خمسا وثلاثين سنة وولد شالح"));
