arasuperobject.biblemap.put("b1_12_2", new arasuperobject(1,12,2,"فاجعلك امة عظيمة واباركك واعظم اسمك وتكون بركة"));
arasuperobject.biblemap.put("b1_12_3", new arasuperobject(1,12,3,"وابارك مباركيك ولاعنك العنه. وتتبارك فيك جميع قبائل الارض"));
arasuperobject.biblemap.put("b1_12_4", new arasuperobject(1,12,4,"فذهب ابرام كما قال له الرب وذهب معه لوط. وكان ابرام ابن خمس وسبعين سنة لما خرج من حاران"));
arasuperobject.biblemap.put("b1_12_5", new arasuperobject(1,12,5,"فاخذ ابرام ساراي امرأته ولوطا ابن اخيه وكل مقتنياتهما التي اقتنيا والنفوس التي امتلكا في حاران. وخرجوا ليذهبوا الى ارض كنعان. فأتوا الى ارض كنعان"));
arasuperobject.biblemap.put("b1_12_6", new arasuperobject(1,12,6,"واجتاز ابرام في الارض الى مكان شكيم الى بلّوطة مورة. وكان الكنعانيون حينئذ في الارض"));
arasuperobject.biblemap.put("b1_12_7", new arasuperobject(1,12,7,"وظهر الرب لابرام وقال لنسلك اعطي هذه الارض. فبنى هناك مذبحا للرب الذي ظهر له"));
arasuperobject.biblemap.put("b1_12_8", new arasuperobject(1,12,8,"ثم نقل من هناك الى الجبل شرقي بيت ايل ونصب خيمته. وله بيت ايل من المغرب وعاي من المشرق. فبنى هناك مذبحا للرب ودعا باسم الرب"));
arasuperobject.biblemap.put("b1_12_9", new arasuperobject(1,12,9,"ثم ارتحل ابرام ارتحالا متواليا نحو الجنوب"));
arasuperobject.biblemap.put("b1_12_10", new arasuperobject(1,12,10,"وحدث جوع في الارض. فانحدر ابرام الى مصر ليتغرب هناك. لان الجوع في الارض كان شديدا"));
arasuperobject.biblemap.put("b1_12_11", new arasuperobject(1,12,11,"وحدث لما قرب ان يدخل مصر انه قال لساراي امرأته اني قد علمت انك امرأة حسنة المنظر"));
arasuperobject.biblemap.put("b1_12_12", new arasuperobject(1,12,12,"فيكون اذا رآك المصريون انهم يقولون هذه امرأته. فيقتلونني ويستبقونك"));
arasuperobject.biblemap.put("b1_12_13", new arasuperobject(1,12,13,"قولي انك اختي. ليكون لي خير بسببك وتحيا نفسي من اجلك"));
arasuperobject.biblemap.put("b1_12_14", new arasuperobject(1,12,14,"فحدث لما دخل ابرام الى مصر ان المصريين رأوا المرأة انها حسنة جدا"));
arasuperobject.biblemap.put("b1_12_15", new arasuperobject(1,12,15,"ورآها رؤساء فرعون ومدحوها لدى فرعون. فأخذت المرأة الى بيت فرعون"));
arasuperobject.biblemap.put("b1_12_16", new arasuperobject(1,12,16,"فصنع الى ابرام خيرا بسببها. وصار له غنم وبقر وحمير وعبيد واماء وأتن وجمال"));
arasuperobject.biblemap.put("b1_12_17", new arasuperobject(1,12,17,"فضرب الرب فرعون وبيته ضربات عظيمة بسبب ساراي امرأة ابرام"));
arasuperobject.biblemap.put("b1_12_18", new arasuperobject(1,12,18,"فدعا فرعون ابرام وقال ما هذا الذي صنعت بي. لماذا لم تخبرني انها امرأتك"));
arasuperobject.biblemap.put("b1_12_19", new arasuperobject(1,12,19,"لماذا قلت هي اختي حتى اخذتها لي لتكون زوجتي. والآن هوذا امرأتك. خذها واذهب"));
arasuperobject.biblemap.put("b1_12_20", new arasuperobject(1,12,20,"فاوصى عليه فرعون رجالا فشيعوه وامرأته وكل ما كان له"));
arasuperobject.biblemap.put("b1_13_1", new arasuperobject(1,13,1,"فصعد ابرام من مصر هو وامرأته وكل ما كان له ولوط معه الى الجنوب"));
arasuperobject.biblemap.put("b1_13_2", new arasuperobject(1,13,2,"وكان ابرام غنيا جدا في المواشي والفضة والذهب"));
arasuperobject.biblemap.put("b1_13_3", new arasuperobject(1,13,3,"وسار في رحلاته من الجنوب الى بيت ايل. الى المكان الذي كانت خيمته فيه في البداءة بين بيت ايل وعاي"));
arasuperobject.biblemap.put("b1_13_4", new arasuperobject(1,13,4,"الى مكان المذبح الذي عمله هناك اولا. ودعا هناك ابرام باسم الرب"));
arasuperobject.biblemap.put("b1_13_5", new arasuperobject(1,13,5,"ولوط السائر مع ابرام كان له ايضا غنم وبقر وخيام"));
arasuperobject.biblemap.put("b1_13_6", new arasuperobject(1,13,6,"ولم تحتملهما الارض ان يسكنا معا. اذ كانت املاكهما كثيرة. فلم يقدرا ان يسكنا معا"));
arasuperobject.biblemap.put("b1_13_7", new arasuperobject(1,13,7,"فحدثت مخاصمة بين رعاة مواشي ابرام ورعاة مواشي لوط. وكان الكنعانيون والفرزّيون حينئذ ساكنين في الارض"));
arasuperobject.biblemap.put("b1_13_8", new arasuperobject(1,13,8,"فقال ابرام للوط لا تكن مخاصمة بيني وبينك وبين رعاتي ورعاتك. لاننا نحن اخوان"));
arasuperobject.biblemap.put("b1_13_9", new arasuperobject(1,13,9,"أليست كل الارض امامك. اعتزل عني. ان ذهبت شمالا فانا يمينا وان يمينا فانا شمالا"));
arasuperobject.biblemap.put("b1_13_10", new arasuperobject(1,13,10,"فرفع لوط عينيه ورأى كل دائرة الاردن ان جميعها سقي قبلما اخرب الرب سدوم وعمورة كجنة الرب كارض مصر. حينما تجيء الى صوغر"));
arasuperobject.biblemap.put("b1_13_11", new arasuperobject(1,13,11,"فاختار لوط لنفسه كل دائرة الاردن وارتحل لوط شرقا. فاعتزل الواحد عن الآخر"));
arasuperobject.biblemap.put("b1_13_12", new arasuperobject(1,13,12,"ابرام سكن في ارض كنعان ولوط سكن في مدن الدائرة ونقل خيامه الى سدوم"));
arasuperobject.biblemap.put("b1_13_13", new arasuperobject(1,13,13,"وكان اهل سدوم اشرارا وخطاة لدى الرب جدا"));
arasuperobject.biblemap.put("b1_13_14", new arasuperobject(1,13,14,"وقال الرب لابرام بعد اعتزال لوط عنه. ارفع عينيك وانظر من الموضع الذي انت فيه شمالا وجنوبا وشرقا وغربا"));
arasuperobject.biblemap.put("b1_13_15", new arasuperobject(1,13,15,"لان جميع الارض التي انت ترى لك اعطيها ولنسلك الى الابد"));
arasuperobject.biblemap.put("b1_13_16", new arasuperobject(1,13,16,"واجعل نسلك كتراب الارض. حتى اذا استطاع احد ان يعد تراب الارض فنسلك ايضا يعدّ"));
arasuperobject.biblemap.put("b1_13_17", new arasuperobject(1,13,17,"قم امش في الارض طولها وعرضها. لاني لك اعطيها"));
arasuperobject.biblemap.put("b1_13_18", new arasuperobject(1,13,18,"فنقل ابرام خيامه واتى واقام عند بلوطات ممرا التي في حبرون. بنى هناك مذبحا للرب"));
arasuperobject.biblemap.put("b1_14_1", new arasuperobject(1,14,1,"وحدث في ايام أمرافل ملك شنعار وأريوك ملك ألاسار وكدرلعومر ملك عيلام وتدعال ملك جوييم"));
arasuperobject.biblemap.put("b1_14_2", new arasuperobject(1,14,2,"ان هؤلاء صنعوا حربا مع بارع ملك سدوم وبرشاع ملك عمورة وشنآب ملك أدمة وشمئيبر ملك صبوييم وملك بالع التي هي صوغر"));
arasuperobject.biblemap.put("b1_14_3", new arasuperobject(1,14,3,"جميع هؤلاء اجتمعوا متعاهدين الى عمق السديم الذي هو بحر الملح"));
arasuperobject.biblemap.put("b1_14_4", new arasuperobject(1,14,4,"اثنتي عشرة سنة استعبدوا لكدرلعومر والسنة الثالثة عشرة عصوا عليه"));
arasuperobject.biblemap.put("b1_14_5", new arasuperobject(1,14,5,"وفي السنة الرابعة عشرة اتى كدرلعومر والملوك الذين معه وضربوا الرفائيّين في عشتاروث قرنايم والزوزيين في هام والإيميين في شوى قريتايم"));
arasuperobject.biblemap.put("b1_14_6", new arasuperobject(1,14,6,"والحوريين في جبلهم سعير الى بطمة فاران التي عند البرية"));
arasuperobject.biblemap.put("b1_14_7", new arasuperobject(1,14,7,"ثم رجعوا وجاءوا الى عين مشفاط التي هي قادش. وضربوا كل بلاد العمالقة وايضا الاموريين الساكنين في حصون تامار"));
arasuperobject.biblemap.put("b1_14_8", new arasuperobject(1,14,8,"فخرج ملك سدوم وملك عمورة وملك أدمة وملك صبوييم وملك بالع التي هي صوغر ونظموا حربا معهم في عمق السّدّيم"));
arasuperobject.biblemap.put("b1_14_9", new arasuperobject(1,14,9,"مع كدرلعومر ملك عيلام وتدعال ملك جوييم وأمرافل ملك شنعار وأريوك ملك ألاسار. اربعة ملوك مع خمسة"));
arasuperobject.biblemap.put("b1_14_10", new arasuperobject(1,14,10,"وعمق السديم كان فيه آبار حمر كثيرة. فهرب ملكا سدوم وعمورة وسقطا هناك. والباقون هربوا الى الجبل"));
arasuperobject.biblemap.put("b1_14_11", new arasuperobject(1,14,11,"فأخذوا جميع املاك سدوم وعمورة وجميع اطعمتهم ومضوا"));
arasuperobject.biblemap.put("b1_14_12", new arasuperobject(1,14,12,"وأخذوا لوطا ابن اخي ابرام واملاكه ومضوا. اذ كان ساكنا في سدوم"));
arasuperobject.biblemap.put("b1_14_13", new arasuperobject(1,14,13,"فأتى من نجا واخبر ابرام العبراني. وكان ساكنا عند بلوطات ممرا الاموري اخي اشكول واخي عانر. وكانوا اصحاب عهد مع ابرام"));
arasuperobject.biblemap.put("b1_14_14", new arasuperobject(1,14,14,"فلما سمع ابرام ان اخاه سبي جرّ غلمانه المتمرّنين ولدان بيته ثلث مئة وثمانية عشر وتبعهم الى دان"));
arasuperobject.biblemap.put("b1_14_15", new arasuperobject(1,14,15,"وانقسم عليهم ليلا هو وعبيده فكسرهم وتبعهم الى حوبة التي عن شمال دمشق"));
arasuperobject.biblemap.put("b1_14_16", new arasuperobject(1,14,16,"واسترجع كل الاملاك واسترجع لوطا اخاه ايضا واملاكه والنساء ايضا والشعب"));
arasuperobject.biblemap.put("b1_14_17", new arasuperobject(1,14,17,"فخرج ملك سدوم لاستقباله بعد رجوعه من كسرة كدرلعومر والملوك الذين معه الى عمق شوى الذي هو عمق الملك"));
arasuperobject.biblemap.put("b1_14_18", new arasuperobject(1,14,18,"وملكي صادق ملك شاليم اخرج خبزا وخمرا. وكان كاهنا للّه العلي"));
arasuperobject.biblemap.put("b1_14_19", new arasuperobject(1,14,19,"وباركه وقال مبارك ابرام من الله العلي مالك السموات والارض"));
arasuperobject.biblemap.put("b1_14_20", new arasuperobject(1,14,20,"ومبارك الله العلي الذي اسلم اعداءك في يدك. فاعطاه عشرا من كل شيء"));
arasuperobject.biblemap.put("b1_14_21", new arasuperobject(1,14,21,"وقال ملك سدوم لابرام اعطني النفوس واما الاملاك فخذها لنفسك"));
arasuperobject.biblemap.put("b1_14_22", new arasuperobject(1,14,22,"فقال ابرام لملك سدوم رفعت يدي الى الرب الاله العلي مالك السماء والارض"));
arasuperobject.biblemap.put("b1_14_23", new arasuperobject(1,14,23,"لا آخذنّ لا خيطا ولا شراك نعل ولا من كل ما هو لك. فلا تقول انا اغنيت ابرام"));
arasuperobject.biblemap.put("b1_14_24", new arasuperobject(1,14,24,"ليس لي غير الذي اكله الغلمان. واما نصيب الرجال الذين ذهبوا معي عانر واشكول وممرا فهم يأخذون نصيبهم"));
arasuperobject.biblemap.put("b1_15_1", new arasuperobject(1,15,1,"بعد هذه الامور صار كلام الرب الى ابرام في الرؤيا قائلا. لا تخف يا ابرام. انا ترس لك. اجرك كثير جدا"));
arasuperobject.biblemap.put("b1_15_2", new arasuperobject(1,15,2,"فقال ابرام ايها السيد الرب ماذا تعطيني وانا ماض عقيما ومالك بيتي هو أليعازر الدمشقي"));
arasuperobject.biblemap.put("b1_15_3", new arasuperobject(1,15,3,"وقال ابرام ايضا انك لم تعطني نسلا وهوذا ابن بيتي وارث لي"));
arasuperobject.biblemap.put("b1_15_4", new arasuperobject(1,15,4,"فاذا كلام الرب اليه قائلا. لا يرثك هذا. بل الذي يخرج من احشائك هو يرثك"));
arasuperobject.biblemap.put("b1_15_5", new arasuperobject(1,15,5,"ثم اخرجه الى خارج وقال انظر الى السماء وعدّ النجوم ان استطعت ان تعدّها. وقال له هكذا يكون نسلك"));
arasuperobject.biblemap.put("b1_15_6", new arasuperobject(1,15,6,"فآمن بالرب فحسبه له برا"));
arasuperobject.biblemap.put("b1_15_7", new arasuperobject(1,15,7,"وقال له انا الرب الذي اخرجك من أور الكلدانيين ليعطيك هذه الارض لترثها"));
arasuperobject.biblemap.put("b1_15_8", new arasuperobject(1,15,8,"فقال ايها السيد الرب بماذا اعلم اني ارثها"));
arasuperobject.biblemap.put("b1_15_9", new arasuperobject(1,15,9,"فقال له خذ لي عجلة ثلثية وعنزة ثلثية وكبشا ثلثيا ويمامة وحمامة"));
arasuperobject.biblemap.put("b1_15_10", new arasuperobject(1,15,10,"فأخذ هذه كلها وشقها من الوسط وجعل شق كل واحد مقابل صاحبه. واما الطير فلم يشقه"));
arasuperobject.biblemap.put("b1_15_11", new arasuperobject(1,15,11,"فنزلت الجوارح على الجثث وكان ابرام يزجرها"));
arasuperobject.biblemap.put("b1_15_12", new arasuperobject(1,15,12,"ولما صارت الشمس الى المغيب وقع على ابرام سبات. واذا رعبة مظلمة عظيمة واقعة عليه"));
arasuperobject.biblemap.put("b1_15_13", new arasuperobject(1,15,13,"فقال لابرام اعلم يقينا ان نسلك سيكون غريبا في ارض ليست لهم ويستعبدون لهم. فيذلونهم اربع مئة سنة"));
arasuperobject.biblemap.put("b1_15_14", new arasuperobject(1,15,14,"ثم الامة التي يستعبدون لها انا ادينها. وبعد ذلك يخرجون باملاك جزيلة"));
arasuperobject.biblemap.put("b1_15_15", new arasuperobject(1,15,15,"واما انت فتمضي الى آبائك بسلام وتدفن بشيبة صالحة"));
arasuperobject.biblemap.put("b1_15_16", new arasuperobject(1,15,16,"وفي الجيل الرابع يرجعون الى ههنا. لان ذنب الاموريين ليس الى الآن كاملا"));
arasuperobject.biblemap.put("b1_15_17", new arasuperobject(1,15,17,"ثم غابت الشمس فصارت العتمة. واذا تنور دخان ومصباح نار يجوز بين تلك القطع"));
arasuperobject.biblemap.put("b1_15_18", new arasuperobject(1,15,18,"في ذلك اليوم قطع الرب مع ابرام ميثاقا قائلا. لنسلك اعطي هذه الارض من نهر مصر الى النهر الكبير نهر الفرات"));
arasuperobject.biblemap.put("b1_15_19", new arasuperobject(1,15,19,"القينيين والقنزّيين والقدمونيين"));
arasuperobject.biblemap.put("b1_15_20", new arasuperobject(1,15,20,"والحثّيين والفرزّيين والرفائيين"));
arasuperobject.biblemap.put("b1_15_21", new arasuperobject(1,15,21,"والأموريين والكنعانيين والجرجاشيين واليبوسيين"));
arasuperobject.biblemap.put("b1_16_1", new arasuperobject(1,16,1,"واما ساراي امرأة ابرام فلم تلد له. وكانت لها جارية مصرية اسمها هاجر"));
arasuperobject.biblemap.put("b1_16_2", new arasuperobject(1,16,2,"فقالت ساراي لابرام هوذا الرب قد امسكني عن الولادة. ادخل على جاريتي. لعلي أرزق منها بنين. فسمع ابرام لقول ساراي"));
arasuperobject.biblemap.put("b1_16_3", new arasuperobject(1,16,3,"فاخذت ساراي امرأة ابرام هاجر المصرية جاريتها من بعد عشر سنين لاقامة ابرام في ارض كنعان واعطتها لابرام رجلها زوجة له"));
arasuperobject.biblemap.put("b1_16_4", new arasuperobject(1,16,4,"فدخل على هاجر فحبلت. ولما رأت انها حبلت صغرت مولاتها في عينيها"));
arasuperobject.biblemap.put("b1_16_5", new arasuperobject(1,16,5,"فقالت ساراي لابرام ظلمي عليك. انا دفعت جاريتي الى حضنك. فلما رأت انها حبلت صغرت في عينيها. يقضي الرب بيني وبينك"));
arasuperobject.biblemap.put("b1_16_6", new arasuperobject(1,16,6,"فقال ابرام لساراي هوذا جاريتك في يدك. افعلي بها ما يحسن في عينيك. فاذلّتها ساراي. فهربت من وجهها"));
arasuperobject.biblemap.put("b1_16_7", new arasuperobject(1,16,7,"فوجدها ملاك الرب على عين الماء في البرية. على العين التي في طريق شور"));
arasuperobject.biblemap.put("b1_16_8", new arasuperobject(1,16,8,"وقال يا هاجر جارية ساراي من اين أتيت والى اين تذهبين. فقالت انا هاربة من وجه مولاتي ساراي"));
arasuperobject.biblemap.put("b1_16_9", new arasuperobject(1,16,9,"فقال لها ملاك الرب ارجعي الى مولاتك واخضعي تحت يديها"));
arasuperobject.biblemap.put("b1_16_10", new arasuperobject(1,16,10,"وقال لها ملاك الرب تكثيرا اكثر نسلك فلا يعد من الكثرة"));
arasuperobject.biblemap.put("b1_16_11", new arasuperobject(1,16,11,"وقال لها ملاك الرب ها انت حبلى فتلدين ابنا. وتدعين اسمه اسماعيل لان الرب قد سمع لمذلّتك"));
arasuperobject.biblemap.put("b1_16_12", new arasuperobject(1,16,12,"وانه يكون انسانا وحشيّا. يده على كل واحد ويد كل واحد عليه. وامام جميع اخوته يسكن"));
arasuperobject.biblemap.put("b1_16_13", new arasuperobject(1,16,13,"فدعت اسم الرب الذي تكلم معها انت ايل رئي. لانها قالت أههنا ايضا رأيت بعد رؤية"));
arasuperobject.biblemap.put("b1_16_14", new arasuperobject(1,16,14,"لذلك دعيت البئر بئر لحي رئي. ها هي بين قادش وبارد"));
arasuperobject.biblemap.put("b1_16_15", new arasuperobject(1,16,15,"فولدت هاجر لابرام ابنا. ودعا ابرام اسم ابنه الذي ولدته هاجر اسماعيل"));
arasuperobject.biblemap.put("b1_16_16", new arasuperobject(1,16,16,"وكان ابرام ابن ست وثمانين سنة لما ولدت هاجر اسماعيل لابرام"));
arasuperobject.biblemap.put("b1_17_1", new arasuperobject(1,17,1,"ولما كان ابرام ابن تسع وتسعين سنة ظهر الرب لابرام وقال له انا الله القدير. سر امامي وكن كاملا"));
arasuperobject.biblemap.put("b1_17_2", new arasuperobject(1,17,2,"فاجعل عهدي بيني وبينك واكثرك كثيرا جدا"));
arasuperobject.biblemap.put("b1_17_3", new arasuperobject(1,17,3,"فسقط ابرام على وجهه. وتكلم الله معه قائلا"));
arasuperobject.biblemap.put("b1_17_4", new arasuperobject(1,17,4,"اما انا فهوذا عهدي معك وتكون ابا لجمهور من الامم"));
arasuperobject.biblemap.put("b1_17_5", new arasuperobject(1,17,5,"فلا يدعى اسمك بعد ابرام بل يكون اسمك ابراهيم. لاني اجعلك ابا لجمهور من الامم"));
arasuperobject.biblemap.put("b1_17_6", new arasuperobject(1,17,6,"وأثمرك كثيرا جدا واجعلك امما. وملوك منك يخرجون"));
arasuperobject.biblemap.put("b1_17_7", new arasuperobject(1,17,7,"واقيم عهدي بيني وبينك وبين نسلك من بعدك في اجيالهم عهدا ابديا. لاكون الها لك ولنسلك من بعدك"));
arasuperobject.biblemap.put("b1_17_8", new arasuperobject(1,17,8,"واعطي لك ولنسلك من بعدك ارض غربتك كل ارض كنعان ملكا ابديا. واكون الههم"));
arasuperobject.biblemap.put("b1_17_9", new arasuperobject(1,17,9,"وقال الله لابراهيم واما انت فتحفظ عهدي. انت ونسلك من بعدك في اجيالهم"));
arasuperobject.biblemap.put("b1_17_10", new arasuperobject(1,17,10,"هذا هو عهدي الذي تحفظونه بيني وبينكم وبين نسلك من بعدك. يختن منكم كل ذكر"));
arasuperobject.biblemap.put("b1_17_11", new arasuperobject(1,17,11,"فتختنون في لحم غرلتكم. فيكون علامة عهد بيني وبينكم"));
arasuperobject.biblemap.put("b1_17_12", new arasuperobject(1,17,12,"ابن ثمانية ايام يختن منكم كل ذكر في اجيالكم. وليد البيت والمبتاع بفضة من كل ابن غريب ليس من نسلك"));
arasuperobject.biblemap.put("b1_17_13", new arasuperobject(1,17,13,"يختن ختانا وليد بيتك والمبتاع بفضتك. فيكون عهدي في لحمكم عهدا ابديا"));
arasuperobject.biblemap.put("b1_17_14", new arasuperobject(1,17,14,"واما الذكر الاغلف الذي لا يختن في لحم غرلته فتقطع تلك النفس من شعبها. انه قد نكث عهدي"));
arasuperobject.biblemap.put("b1_17_15", new arasuperobject(1,17,15,"وقال الله لابراهيم ساراي امرأتك لا تدعو اسمها ساراي بل اسمها سارة"));
arasuperobject.biblemap.put("b1_17_16", new arasuperobject(1,17,16,"واباركها واعطيك ايضا منها ابنا. اباركها فتكون امما وملوك شعوب منها يكونون"));
arasuperobject.biblemap.put("b1_17_17", new arasuperobject(1,17,17,"فسقط ابراهيم على وجهه وضحك. وقال في قلبه هل يولد لابن مئة سنة وهل تلد سارة وهي بنت تسعين سنة"));
arasuperobject.biblemap.put("b1_17_18", new arasuperobject(1,17,18,"وقال ابراهيم لله ليت اسماعيل يعيش امامك"));
arasuperobject.biblemap.put("b1_17_19", new arasuperobject(1,17,19,"فقال الله بل سارة امرأتك تلد لك ابنا وتدعو اسمه اسحق. واقيم عهدي معه عهدا ابديا لنسله من بعده"));
arasuperobject.biblemap.put("b1_17_20", new arasuperobject(1,17,20,"واما اسماعيل فقد سمعت لك فيه. ها انا اباركه وأثمره واكثره كثيرا جدا. اثني عشر رئيسا يلد واجعله امة كبيرة"));
arasuperobject.biblemap.put("b1_17_21", new arasuperobject(1,17,21,"ولكن عهدي اقيمه مع اسحق الذي تلده لك سارة في هذا الوقت في السنة الآتية"));
arasuperobject.biblemap.put("b1_17_22", new arasuperobject(1,17,22,"فلما فرغ من الكلام معه صعد الله عن ابراهيم"));
arasuperobject.biblemap.put("b1_17_23", new arasuperobject(1,17,23,"فاخذ ابراهيم اسماعيل ابنه وجميع ولدان بيته وجميع المبتاعين بفضته كل ذكر من اهل بيت ابراهيم وختن لحم غرلتهم في ذلك اليوم عينه كما كلمه الله"));
arasuperobject.biblemap.put("b1_17_24", new arasuperobject(1,17,24,"وكان ابراهيم ابن تسع وتسعين سنة حين ختن في لحم غرلته"));
arasuperobject.biblemap.put("b1_17_25", new arasuperobject(1,17,25,"وكان اسماعيل ابنه ابن ثلاث عشرة سنة حين ختن في لحم غرلته"));
arasuperobject.biblemap.put("b1_17_26", new arasuperobject(1,17,26,"في ذلك اليوم عينه ختن ابراهيم واسماعيل ابنه"));
arasuperobject.biblemap.put("b1_17_27", new arasuperobject(1,17,27,"وكل رجال بيته ولدان البيت والمبتاعين بالفضة من ابن الغريب ختنوا معه"));
arasuperobject.biblemap.put("b1_18_1", new arasuperobject(1,18,1,"وظهر له الرب عند بلوطات ممرا وهو جالس في باب الخيمة وقت حر النهار"));
arasuperobject.biblemap.put("b1_18_2", new arasuperobject(1,18,2,"فرفع عينيه ونظر واذا ثلاثة رجال واقفون لديه. فلما نظر ركض لاستقبالهم من باب الخيمة وسجد الى الارض"));
arasuperobject.biblemap.put("b1_18_3", new arasuperobject(1,18,3,"وقال يا سيد ان كنت قد وجدت نعمة في عينيك فلا تتجاوز عبدك"));
arasuperobject.biblemap.put("b1_18_4", new arasuperobject(1,18,4,"ليؤخذ قليل ماء واغسلوا ارجلكم واتكئوا تحت الشجرة"));
arasuperobject.biblemap.put("b1_18_5", new arasuperobject(1,18,5,"فآخذ كسرة خبز فتسندون قلوبكم ثم تجتازون. لانكم قد مررتم على عبدكم. فقالوا هكذا تفعل كما تكلمت"));
arasuperobject.biblemap.put("b1_18_6", new arasuperobject(1,18,6,"فاسرع ابراهيم الى الخيمة الى سارة وقال اسرعي بثلاث كيلات دقيقا سميذا. اعجني واصنعي خبز ملّة"));
arasuperobject.biblemap.put("b1_18_7", new arasuperobject(1,18,7,"ثم ركض ابراهيم الى البقر واخذ عجلا رخصا وجيدا واعطاه للغلام فاسرع ليعمله"));
arasuperobject.biblemap.put("b1_18_8", new arasuperobject(1,18,8,"ثم اخذ زبدا ولبنا والعجل الذي عمله ووضعها قدامهم. واذ كان هو واقفا لديهم تحت الشجرة اكلوا"));
arasuperobject.biblemap.put("b1_18_9", new arasuperobject(1,18,9,"وقالوا له اين سارة امرأتك. فقال ها هي في الخيمة"));
arasuperobject.biblemap.put("b1_18_10", new arasuperobject(1,18,10,"فقال اني ارجع اليك نحو زمان الحياة ويكون لسارة امرأتك ابن. وكانت سارة سامعة في باب الخيمة وهو وراءه"));
arasuperobject.biblemap.put("b1_18_11", new arasuperobject(1,18,11,"وكان ابراهيم وسارة شيخين متقدمين في الايام. وقد انقطع ان يكون لسارة عادة كالنساء"));
arasuperobject.biblemap.put("b1_18_12", new arasuperobject(1,18,12,"فضحكت سارة في باطنها قائلة أبعد فنائي يكون لي تنعم وسيدي قد شاخ"));
arasuperobject.biblemap.put("b1_18_13", new arasuperobject(1,18,13,"فقال الرب لابراهيم لماذا ضحكت سارة قائلة أفبالحقيقة الد وانا قد شخت"));
arasuperobject.biblemap.put("b1_18_14", new arasuperobject(1,18,14,"هل يستحيل على الرب شيء. في الميعاد ارجع اليك نحو زمان الحياة ويكون لسارة ابن"));
arasuperobject.biblemap.put("b1_18_15", new arasuperobject(1,18,15,"فانكرت سارة قائلة لم اضحك. لانها خافت. فقال لا بل ضحكت"));
arasuperobject.biblemap.put("b1_18_16", new arasuperobject(1,18,16,"ثم قام الرجال من هناك وتطلعوا نحو سدوم. وكان ابراهيم ماشيا معهم ليشيعهم"));
arasuperobject.biblemap.put("b1_18_17", new arasuperobject(1,18,17,"فقال الرب هل اخفي عن ابراهيم ما انا فاعله"));
arasuperobject.biblemap.put("b1_18_18", new arasuperobject(1,18,18,"وابراهيم يكون امة كبيرة وقوية ويتبارك به جميع امم الارض"));
arasuperobject.biblemap.put("b1_18_19", new arasuperobject(1,18,19,"لاني عرفته لكي يوصي بنيه وبيته من بعده ان يحفظوا طريق الرب ليعملوا برا وعدلا لكي ياتي الرب لابراهيم بما تكلم به"));
arasuperobject.biblemap.put("b1_18_20", new arasuperobject(1,18,20,"وقال الرب ان صراخ سدوم وعمورة قد كثر وخطيتهم قد عظمت جدا"));
arasuperobject.biblemap.put("b1_18_21", new arasuperobject(1,18,21,"انزل وأرى هل فعلوا بالتمام حسب صراخها الآتي اليّ. والا فاعلم"));
arasuperobject.biblemap.put("b1_18_22", new arasuperobject(1,18,22,"وانصرف الرجال من هناك وذهبوا نحو سدوم. واما ابراهيم فكان لم يزل قائما امام الرب"));
arasuperobject.biblemap.put("b1_18_23", new arasuperobject(1,18,23,"فتقدم ابراهيم وقال أفتهلك البار مع الاثيم"));
arasuperobject.biblemap.put("b1_18_24", new arasuperobject(1,18,24,"عسى ان يكون خمسون بارا في المدينة. أفتهلك المكان ولا تصفح عنه من اجل الخمسين بارا الذين فيه"));
arasuperobject.biblemap.put("b1_18_25", new arasuperobject(1,18,25,"حاشا لك ان تفعل مثل هذا الامر ان تميت البار مع الاثيم فيكون البار كالاثيم. حاشا لك. أديان كل الارض لا يصنع عدلا"));
arasuperobject.biblemap.put("b1_18_26", new arasuperobject(1,18,26,"فقال الرب ان وجدت في سدوم خمسين بارا في المدينة فاني اصفح عن المكان كله من اجلهم"));
arasuperobject.biblemap.put("b1_18_27", new arasuperobject(1,18,27,"فاجاب ابراهيم وقال اني قد شرعت اكلم المولى وانا تراب ورماد"));
arasuperobject.biblemap.put("b1_18_28", new arasuperobject(1,18,28,"ربما نقص الخمسون بارا خمسة. أتهلك كل المدينة بالخمسة. فقال لا اهلك ان وجدت هناك خمسة واربعين"));
arasuperobject.biblemap.put("b1_18_29", new arasuperobject(1,18,29,"فعاد يكلمه ايضا وقال عسى ان يوجد هناك اربعون. فقال لا افعل من اجل الاربعين"));
arasuperobject.biblemap.put("b1_18_30", new arasuperobject(1,18,30,"فقال لا يسخط المولى فاتكلم. عسى ان يوجد هناك ثلاثون. فقال لا افعل ان وجدت هناك ثلاثين"));
arasuperobject.biblemap.put("b1_18_31", new arasuperobject(1,18,31,"فقال اني قد شرعت اكلم المولى. عسى ان يوجد هناك عشرون. فقال لا اهلك من اجل العشرين"));
arasuperobject.biblemap.put("b1_18_32", new arasuperobject(1,18,32,"فقال لا يسخط المولى فاتكلم هذه المرة فقط. عسى ان يوجد هناك عشرة. فقال لا اهلك من اجل العشرة"));
arasuperobject.biblemap.put("b1_18_33", new arasuperobject(1,18,33,"وذهب الرب عندما فرغ من الكلام مع ابراهيم ورجع ابراهيم الى مكانه"));
arasuperobject.biblemap.put("b1_19_1", new arasuperobject(1,19,1,"فجاء الملاكان الى سدوم مساء وكان لوط جالسا في باب سدوم. فلما رآهما لوط قام لاستقبالهما وسجد بوجهه الى الارض"));
arasuperobject.biblemap.put("b1_19_2", new arasuperobject(1,19,2,"وقال يا سيّديّ ميلا الى بيت عبدكما وبيتا واغسلا ارجلكما. ثم تبكران وتذهبان في طريقكما. فقالا لا بل في الساحة نبيت"));
arasuperobject.biblemap.put("b1_19_3", new arasuperobject(1,19,3,"فألحّ عليهما جدا. فمالا اليه ودخلا بيته. فصنع لهما ضيافة وخبز فطيرا فاكلا"));
arasuperobject.biblemap.put("b1_19_4", new arasuperobject(1,19,4,"وقبلما اضطجعا احاط بالبيت رجال المدينة رجال سدوم من الحدث الى الشيخ كل الشعب من اقصاها"));
arasuperobject.biblemap.put("b1_19_5", new arasuperobject(1,19,5,"فنادوا لوطا وقالوا له اين الرجلان اللذان دخلا اليك الليلة. اخرجهما الينا لنعرفهما"));
arasuperobject.biblemap.put("b1_19_6", new arasuperobject(1,19,6,"فخرج اليهم لوط الى الباب واغلق الباب وراءه"));
arasuperobject.biblemap.put("b1_19_7", new arasuperobject(1,19,7,"وقال لا تفعلوا شرا يا اخوتي"));
arasuperobject.biblemap.put("b1_19_8", new arasuperobject(1,19,8,"هوذا لي ابنتان لم تعرفا رجلا. اخرجهما اليكم فافعلوا بهما كما يحسن في عيونكم. واما هذان الرجلان فلا تفعلوا بهما شيئا لانهما قد دخلا تحت ظل سقفي"));
arasuperobject.biblemap.put("b1_19_9", new arasuperobject(1,19,9,"فقالوا ابعد الى هناك. ثم قالوا جاء هذا الانسان ليتغرب وهو يحكم حكما. الآن نفعل بك شرا اكثر منهما. فألحّوا على الرجل لوط جدا وتقدموا ليكسروا الباب"));
arasuperobject.biblemap.put("b1_19_10", new arasuperobject(1,19,10,"فمدّ الرجلان ايديهما وادخلا لوطا اليهما الى البيت واغلقا الباب"));
arasuperobject.biblemap.put("b1_19_11", new arasuperobject(1,19,11,"واما الرجال الذين على باب البيت فضرباهم بالعمى من الصغير الى الكبير. فعجزوا عن ان يجدوا الباب"));
arasuperobject.biblemap.put("b1_19_12", new arasuperobject(1,19,12,"وقال الرجلان للوط من لك ايضا ههنا. اصهارك وبنيك وبناتك وكل من لك في المدينة اخرج من المكان"));
arasuperobject.biblemap.put("b1_19_13", new arasuperobject(1,19,13,"لاننا مهلكان هذا المكان. اذ قد عظم صراخهم امام الرب فارسلنا الرب لنهلكه"));
arasuperobject.biblemap.put("b1_19_14", new arasuperobject(1,19,14,"فخرج لوط وكلم اصهاره الآخذين بناته وقال قوموا اخرجوا من هذا المكان. لان الرب مهلك المدينة. فكان كمازح في اعين اصهاره"));
arasuperobject.biblemap.put("b1_19_15", new arasuperobject(1,19,15,"ولما طلع الفجر كان الملاكان يعجلان لوطا قائلين قم خذ امرأتك وابنتيك الموجودتين لئلا تهلك باثم المدينة"));
arasuperobject.biblemap.put("b1_19_16", new arasuperobject(1,19,16,"ولما توانى امسك الرجلان بيده وبيد امرأته وبيد ابنتيه لشفقة الرب عليه واخرجاه ووضعاه خارج المدينة"));
arasuperobject.biblemap.put("b1_19_17", new arasuperobject(1,19,17,"وكان لما اخرجاهم الى خارج انه قال اهرب لحياتك. لا تنظر الى ورائك ولا تقف في كل الدائرة. اهرب الى الجبل لئلا تهلك"));
arasuperobject.biblemap.put("b1_19_18", new arasuperobject(1,19,18,"فقال لهما لوط لا يا سيد"));
arasuperobject.biblemap.put("b1_19_19", new arasuperobject(1,19,19,"هوذا عبدك قد وجد نعمة في عينيك وعظمت لطفك الذي صنعت اليّ باستبقاء نفسي. وانا لا اقدر ان اهرب الى الجبل. لعل الشر يدركني فاموت"));
arasuperobject.biblemap.put("b1_19_20", new arasuperobject(1,19,20,"هوذا المدينة هذه قريبة للهرب اليها وهي صغيرة. اهرب الى هناك. أليست هي صغيرة. فتحيا نفسي"));
arasuperobject.biblemap.put("b1_19_21", new arasuperobject(1,19,21,"فقال له اني قد رفعت وجهك في هذا الامر ايضا ان لا اقلب المدينة التي تكلمت عنها"));
arasuperobject.biblemap.put("b1_19_22", new arasuperobject(1,19,22,"اسرع اهرب الى هناك. لاني لا استطيع ان افعل شيئا حتى تجيء الى هناك. لذلك دعي اسم المدينة صوغر"));
arasuperobject.biblemap.put("b1_19_23", new arasuperobject(1,19,23,"واذ اشرقت الشمس على الارض دخل لوط الى صوغر"));
arasuperobject.biblemap.put("b1_19_24", new arasuperobject(1,19,24,"فامطر الرب على سدوم وعمورة كبريتا ونارا من عند الرب من السماء"));
arasuperobject.biblemap.put("b1_19_25", new arasuperobject(1,19,25,"وقلب تلك المدن وكل الدائرة وجميع سكان المدن ونبات الارض"));
arasuperobject.biblemap.put("b1_19_26", new arasuperobject(1,19,26,"ونظرت امرأته من وراءه فصارت عمود ملح"));
arasuperobject.biblemap.put("b1_19_27", new arasuperobject(1,19,27,"وبكّر ابراهيم في الغد الى المكان الذي وقف فيه امام الرب"));
arasuperobject.biblemap.put("b1_19_28", new arasuperobject(1,19,28,"وتطلع نحو سدوم وعمورة ونحو كل ارض الدائرة ونظر واذا دخان الارض يصعد كدخان الاتون"));
arasuperobject.biblemap.put("b1_19_29", new arasuperobject(1,19,29,"وحدث لما اخرب الله مدن الدائرة ان الله ذكر ابراهيم وارسل لوطا من وسط الانقلاب. حين قلب المدن التي سكن فيها لوط"));
arasuperobject.biblemap.put("b1_19_30", new arasuperobject(1,19,30,"وصعد لوط من صوغر وسكن في الجبل وابنتاه معه. لانه خاف ان يسكن في صوغر. فسكن في المغارة هو وابنتاه"));
arasuperobject.biblemap.put("b1_19_31", new arasuperobject(1,19,31,"وقالت البكر للصغيرة ابونا قد شاخ وليس في الارض رجل ليدخل علينا كعادة كل الارض"));
arasuperobject.biblemap.put("b1_19_32", new arasuperobject(1,19,32,"هلم نسقي ابانا خمرا ونضطجع معه. فنحيي من ابينا نسلا"));
arasuperobject.biblemap.put("b1_19_33", new arasuperobject(1,19,33,"فسقتا اباهما خمرا في تلك الليلة. ودخلت البكر واضطجعت مع ابيها. ولم يعلم باضطجاعها ولا بقيامها"));
arasuperobject.biblemap.put("b1_19_34", new arasuperobject(1,19,34,"وحدث في الغد ان البكر قالت للصغيرة اني قد اضطجعت البارحة مع ابي. نسقيه خمرا الليلة ايضا فادخلي اضطجعي معه. فنحيي من ابينا نسلا"));
arasuperobject.biblemap.put("b1_19_35", new arasuperobject(1,19,35,"فسقتا اباهما خمرا في تلك الليلة ايضا. وقامت الصغيرة واضطجعت معه. ولم يعلم باضطجاعها ولا بقيامها"));
arasuperobject.biblemap.put("b1_19_36", new arasuperobject(1,19,36,"فحبلت ابنتا لوط من ابيهما"));
arasuperobject.biblemap.put("b1_19_37", new arasuperobject(1,19,37,"فولدت البكر ابنا ودعت اسمه موآب. وهو ابو الموآبيين الى اليوم"));
arasuperobject.biblemap.put("b1_19_38", new arasuperobject(1,19,38,"والصغيرة ايضا ولدت ابنا ودعت اسمه بن عمي. وهو ابو بني عمون الى اليوم"));
arasuperobject.biblemap.put("b1_20_1", new arasuperobject(1,20,1,"وانتقل ابراهيم من هناك الى ارض الجنوب وسكن بين قادش وشور وتغرب في جرار"));
arasuperobject.biblemap.put("b1_20_2", new arasuperobject(1,20,2,"وقال ابراهيم عن سارة امراته هي اختي. فارسل ابيمالك ملك جرار واخذ سارة"));
arasuperobject.biblemap.put("b1_20_3", new arasuperobject(1,20,3,"فجاء الله الى ابيمالك في حلم الليل وقال له ها انت ميّت من اجل المرأة التي اخذتها فانها متزوجة ببعل"));
arasuperobject.biblemap.put("b1_20_4", new arasuperobject(1,20,4,"ولكن لم يكن ابيمالك قد اقترب اليها. فقال يا سيد أأمة بارة تقتل"));
arasuperobject.biblemap.put("b1_20_5", new arasuperobject(1,20,5,"ألم يقل هو لي انها اختي وهي ايضا نفسها قالت هو اخي. بسلامة قلبي ونقاوة يديّ فعلت هذا"));
arasuperobject.biblemap.put("b1_20_6", new arasuperobject(1,20,6,"فقال له الله في الحلم انا ايضا علمت انك بسلامة قلبك فعلت هذا. وانا ايضا امسكتك عن ان تخطئ اليّ. لذلك لم ادعك تمسّها"));
arasuperobject.biblemap.put("b1_20_7", new arasuperobject(1,20,7,"فالآن رد امرأة الرجل فانه نبيّ فيصلّي لاجلك فتحيا. وان كنت لست تردها فاعلم انك موتا تموت انت وكل من لك"));
arasuperobject.biblemap.put("b1_20_8", new arasuperobject(1,20,8,"فبكّر ابيمالك في الغد ودعا جميع عبيده وتكلم بكل هذا الكلام في مسامعهم. فخاف الرجال جدا"));
arasuperobject.biblemap.put("b1_20_9", new arasuperobject(1,20,9,"ثم دعا ابيمالك ابراهيم وقال له ماذا فعلت بنا وبماذا اخطأت اليك حتى جلبت عليّ وعلى مملكتي خطية عظيمة. اعمالا لا تعمل عملت بي"));
arasuperobject.biblemap.put("b1_20_10", new arasuperobject(1,20,10,"وقال ابيمالك لابراهيم ماذا رأيت حتى عملت هذا الشيء"));
arasuperobject.biblemap.put("b1_20_11", new arasuperobject(1,20,11,"فقال ابراهيم اني قلت ليس في هذا الموضع خوف الله البتة. فيقتلونني لاجل امرأتي"));
arasuperobject.biblemap.put("b1_20_12", new arasuperobject(1,20,12,"وبالحقيقة ايضا هي اختي ابنة ابي. غير انها ليست ابنة امي. فصارت لي زوجة"));
arasuperobject.biblemap.put("b1_20_13", new arasuperobject(1,20,13,"وحدث لما اتاهني الله من بيت ابي اني قلت لها هذا معروفك الذي تصنعين اليّ. في كل مكان نأتي اليه قولي عني هو اخي"));
arasuperobject.biblemap.put("b1_20_14", new arasuperobject(1,20,14,"فاخذ ابيمالك غنما وبقرا وعبيدا واماء واعطاها لابراهيم. ورد اليه سارة امرأته"));
arasuperobject.biblemap.put("b1_20_15", new arasuperobject(1,20,15,"وقال ابيمالك هوذا ارضي قدامك. اسكن في ما حسن في عينيك"));
arasuperobject.biblemap.put("b1_20_16", new arasuperobject(1,20,16,"وقال لسارة اني قد اعطيت اخاك الفا من الفضة. ها هو لك غطاء عين من جهة كل ما عندك وعند كل واحد فأنصفت"));
arasuperobject.biblemap.put("b1_20_17", new arasuperobject(1,20,17,"فصلّى ابراهيم الى الله. فشفى الله ابيمالك وامرأته وجواريه فولدن"));
arasuperobject.biblemap.put("b1_20_18", new arasuperobject(1,20,18,"لان الرب كان قد اغلق كل رحم لبيت ابيمالك بسبب سارة امرأة ابراهيم"));
arasuperobject.biblemap.put("b1_21_1", new arasuperobject(1,21,1,"وافتقد الرب سارة كما قال. وفعل الرب لسارة كما تكلم"));
arasuperobject.biblemap.put("b1_21_2", new arasuperobject(1,21,2,"فحبلت سارة وولدت لابراهيم ابنا في شيخوخته. في الوقت الذي تكلم الله عنه"));
arasuperobject.biblemap.put("b1_21_3", new arasuperobject(1,21,3,"ودعا ابراهيم اسم ابنه المولود له الذي ولدته له سارة اسحق"));
arasuperobject.biblemap.put("b1_21_4", new arasuperobject(1,21,4,"وختن ابراهيم اسحق ابنه وهو ابن ثمانية ايام كما امره الله"));
arasuperobject.biblemap.put("b1_21_5", new arasuperobject(1,21,5,"وكان ابراهيم ابن مئة سنة حين ولد له اسحق ابنه"));
arasuperobject.biblemap.put("b1_21_6", new arasuperobject(1,21,6,"وقالت سارة قد صنع اليّ الله ضحكا. كل من يسمع يضحك لي"));
arasuperobject.biblemap.put("b1_21_7", new arasuperobject(1,21,7,"وقالت من قال لابراهيم سارة ترضع بنين. حتى ولدت ابنا في شيخوخته"));
arasuperobject.biblemap.put("b1_21_8", new arasuperobject(1,21,8,"فكبر الولد وفطم. وصنع ابراهيم وليمة عظيمة يوم فطام اسحق"));
arasuperobject.biblemap.put("b1_21_9", new arasuperobject(1,21,9,"ورأت سارة ابن هاجر المصرية الذي ولدته لابراهيم يمزح"));
arasuperobject.biblemap.put("b1_21_10", new arasuperobject(1,21,10,"فقالت لابراهيم اطرد هذه الجارية وابنها. لان ابن هذه الجارية لا يرث مع ابني اسحق"));
arasuperobject.biblemap.put("b1_21_11", new arasuperobject(1,21,11,"فقبح الكلام جدا في عيني ابراهيم لسبب ابنه"));
arasuperobject.biblemap.put("b1_21_12", new arasuperobject(1,21,12,"فقال الله لابراهيم لا يقبح في عينيك من اجل الغلام ومن اجل جاريتك. في كل ما تقول لك سارة اسمع لقولها. لانه باسحق يدعى لك نسل"));
arasuperobject.biblemap.put("b1_21_13", new arasuperobject(1,21,13,"وابن الجارية ايضا ساجعله امة لانه نسلك"));
arasuperobject.biblemap.put("b1_21_14", new arasuperobject(1,21,14,"فبكر ابراهيم صباحا واخذ خبزا وقربة ماء واعطاهما لهاجر واضعا اياهما على كتفها والولد وصرفها. فمضت وتاهت في برية بئر سبع"));
arasuperobject.biblemap.put("b1_21_15", new arasuperobject(1,21,15,"ولما فرغ الماء من القربة طرحت الولد تحت احدى الاشجار"));
arasuperobject.biblemap.put("b1_21_16", new arasuperobject(1,21,16,"ومضت وجلست مقابله بعيدا نحو رمية قوس. لانها قالت لا انظر موت الولد. فجلست مقابله ورفعت صوتها وبكت"));
arasuperobject.biblemap.put("b1_21_17", new arasuperobject(1,21,17,"فسمع الله صوت الغلام. ونادى ملاك الله هاجر من السماء وقال لها ما لك يا هاجر. لا تخافي لان الله قد سمع لصوت الغلام حيث هو"));
arasuperobject.biblemap.put("b1_21_18", new arasuperobject(1,21,18,"قومي احملي الغلام وشدي يدك به. لاني ساجعله امة عظيمة"));
arasuperobject.biblemap.put("b1_21_19", new arasuperobject(1,21,19,"وفتح الله عينيها فابصرت بئر ماء. فذهبت وملأت القربة ماء وسقت الغلام"));
arasuperobject.biblemap.put("b1_21_20", new arasuperobject(1,21,20,"وكان الله مع الغلام فكبر. وسكن في البرية وكان ينمو رامي قوس"));
arasuperobject.biblemap.put("b1_21_21", new arasuperobject(1,21,21,"وسكن في برية فاران. وأخذت له امه زوجة من ارض مصر"));
arasuperobject.biblemap.put("b1_21_22", new arasuperobject(1,21,22,"وحدث في ذلك الزمان ان ابيمالك وفيكول رئيس جيشه كلما ابراهيم قائلين الله معك في كل ما انت صانع"));
arasuperobject.biblemap.put("b1_21_23", new arasuperobject(1,21,23,"فالآن احلف لي بالله ههنا انك لا تغدر بي ولا بنسلي وذريّتي. كالمعروف الذي صنعت اليك تصنع اليّ والى الارض التي تغربت فيها"));
arasuperobject.biblemap.put("b1_21_24", new arasuperobject(1,21,24,"فقال ابراهيم انا احلف"));
arasuperobject.biblemap.put("b1_21_25", new arasuperobject(1,21,25,"وعاتب ابراهيم ابيمالك لسبب بئر الماء التي اغتصبها عبيد ابيمالك"));
arasuperobject.biblemap.put("b1_21_26", new arasuperobject(1,21,26,"فقال ابيمالك لم اعلم من فعل هذا الامر. انت لم تخبرني ولا انا سمعت سوى اليوم"));
arasuperobject.biblemap.put("b1_21_27", new arasuperobject(1,21,27,"فاخذ ابراهيم غنما وبقرا واعطى ابيمالك فقطعا كلاهما ميثاقا"));
arasuperobject.biblemap.put("b1_21_28", new arasuperobject(1,21,28,"واقام ابراهيم سبع نعاج من الغنم وحدها"));
arasuperobject.biblemap.put("b1_21_29", new arasuperobject(1,21,29,"فقال ابيمالك لابراهيم ما هذه السبع النعاج التي اقمتها وحدها"));
arasuperobject.biblemap.put("b1_21_30", new arasuperobject(1,21,30,"فقال انك سبع نعاج تاخذ من يدي لكي تكون لي شهادة باني حفرت هذه البئر"));
arasuperobject.biblemap.put("b1_21_31", new arasuperobject(1,21,31,"لذلك دعا ذلك الموضع بئر سبع. لانهما هناك حلفا كلاهما"));
arasuperobject.biblemap.put("b1_21_32", new arasuperobject(1,21,32,"فقطعا ميثاقا في بئر سبع. ثم قام ابيمالك وفيكول رئيس جيشه ورجعا الى ارض الفلسطينيين"));
arasuperobject.biblemap.put("b1_21_33", new arasuperobject(1,21,33,"وغرس ابراهيم أثلا في بئر سبع ودعا هناك باسم الرب الاله السرمدي"));
arasuperobject.biblemap.put("b1_21_34", new arasuperobject(1,21,34,"وتغرب ابراهيم في ارض الفلسطينيين اياما كثيرة"));
arasuperobject.biblemap.put("b1_22_1", new arasuperobject(1,22,1,"وحدث بعد هذه الامور ان الله امتحن ابراهيم. فقال له يا ابراهيم. فقال هانذا"));
arasuperobject.biblemap.put("b1_22_2", new arasuperobject(1,22,2,"فقال خذ ابنك وحيدك الذي تحبه اسحق واذهب الى ارض المريّا واصعده هناك محرقة على احد الجبال الذي اقول لك"));
arasuperobject.biblemap.put("b1_22_3", new arasuperobject(1,22,3,"فبكّر ابراهيم صباحا وشدّ على حماره واخذ اثنين من غلمانه معه واسحق ابنه وشقّق حطبا لمحرقة وقام وذهب الى الموضع الذي قال له الله"));
arasuperobject.biblemap.put("b1_22_4", new arasuperobject(1,22,4,"وفي اليوم الثالث رفع ابراهيم عينيه وابصر الموضع من بعيد"));
arasuperobject.biblemap.put("b1_22_5", new arasuperobject(1,22,5,"فقال ابراهيم لغلاميه اجلسا انتما ههنا مع الحمار. واما انا والغلام فنذهب الى هناك ونسجد ثم نرجع اليكما"));
arasuperobject.biblemap.put("b1_22_6", new arasuperobject(1,22,6,"فاخذ ابراهيم حطب المحرقة ووضعه على اسحق ابنه واخذ بيده النار والسكين. فذهبا كلاهما معا"));
arasuperobject.biblemap.put("b1_22_7", new arasuperobject(1,22,7,"وكلم اسحق ابراهيم اباه وقال يا ابي. فقال هانذا يا ابني. فقال هوذا النار والحطب ولكن اين الخروف للمحرقة"));
arasuperobject.biblemap.put("b1_22_8", new arasuperobject(1,22,8,"فقال ابراهيم الله يرى له الخروف للمحرقة يا ابني. فذهبا كلاهما معا"));
arasuperobject.biblemap.put("b1_22_9", new arasuperobject(1,22,9,"فلما أتيا الى الموضع الذي قال له الله بنى هناك ابراهيم المذبح ورتب الحطب وربط اسحق ابنه ووضعه على المذبح فوق الحطب"));
arasuperobject.biblemap.put("b1_22_10", new arasuperobject(1,22,10,"ثم مدّ ابراهيم يده واخذ السكين ليذبح ابنه"));
arasuperobject.biblemap.put("b1_22_11", new arasuperobject(1,22,11,"فناداه ملاك الرب من السماء وقال ابراهيم ابراهيم. فقال هانذا"));
arasuperobject.biblemap.put("b1_22_12", new arasuperobject(1,22,12,"فقال لا تمد يدك الى الغلام ولا تفعل به شيئا. لاني الآن علمت انك خائف الله فلم تمسك ابنك وحيدك عني"));
arasuperobject.biblemap.put("b1_22_13", new arasuperobject(1,22,13,"فرفع ابراهيم عينيه ونظر واذا كبش وراءه ممسكا في الغابة بقرنيه. فذهب ابراهيم واخذ الكبش واصعده محرقة عوضا عن ابنه"));
arasuperobject.biblemap.put("b1_22_14", new arasuperobject(1,22,14,"فدعا ابراهيم اسم ذلك الموضع يهوه يرأه. حتى انه يقال اليوم في جبل الرب يرى"));
arasuperobject.biblemap.put("b1_22_15", new arasuperobject(1,22,15,"ونادى ملاك الرب ابراهيم ثانية من السماء"));
arasuperobject.biblemap.put("b1_22_16", new arasuperobject(1,22,16,"وقال بذاتي اقسمت يقول الرب. اني من اجل انك فعلت هذا الامر ولم تمسك ابنك وحيدك"));
arasuperobject.biblemap.put("b1_22_17", new arasuperobject(1,22,17,"اباركك مباركة واكثر نسلك تكثيرا كنجوم السماء وكالرمل الذي على شاطئ البحر. ويرث نسلك باب اعدائه"));
arasuperobject.biblemap.put("b1_22_18", new arasuperobject(1,22,18,"ويتبارك في نسلك جميع امم الارض. من اجل انك سمعت لقولي"));
arasuperobject.biblemap.put("b1_22_19", new arasuperobject(1,22,19,"ثم رجع ابراهيم الى غلاميه. فقاموا وذهبوا معا الى بئر سبع. وسكن ابراهيم في بئر سبع"));
arasuperobject.biblemap.put("b1_22_20", new arasuperobject(1,22,20,"وحدث بعد هذه الامور ان ابراهيم أخبر وقيل له هوذا ملكة قد ولدت ايضا بنين لناحور اخيك"));
arasuperobject.biblemap.put("b1_22_21", new arasuperobject(1,22,21,"عوصا بكره وبوزا اخاه وقموئيل ابا ارام"));
arasuperobject.biblemap.put("b1_22_22", new arasuperobject(1,22,22,"وكاسد وحزوا وفلداش ويدلاف وبتوئيل"));
arasuperobject.biblemap.put("b1_22_23", new arasuperobject(1,22,23,"وولد بتوئيل رفقة. هؤلاء الثمانية ولدتهم ملكة لناحور اخي ابراهيم"));
arasuperobject.biblemap.put("b1_22_24", new arasuperobject(1,22,24,"واما سرّيته واسمها رؤومة فولدت هي ايضا طابح وجاحم وتاحش ومعكة"));
arasuperobject.biblemap.put("b1_23_1", new arasuperobject(1,23,1,"وكانت حياة سارة مئة وسبعا وعشرين سنة سني حياة سارة"));
arasuperobject.biblemap.put("b1_23_2", new arasuperobject(1,23,2,"وماتت سارة في قرية اربع التي هي حبرون في ارض كنعان. فاتى ابراهيم ليندب سارة ويبكي عليها"));
arasuperobject.biblemap.put("b1_23_3", new arasuperobject(1,23,3,"وقام ابراهيم من امام ميته وكلم بني حثّ قائلا"));
arasuperobject.biblemap.put("b1_23_4", new arasuperobject(1,23,4,"انا غريب ونزيل عندكم. اعطوني ملك قبر معكم لادفن ميتي من امامي"));
arasuperobject.biblemap.put("b1_23_5", new arasuperobject(1,23,5,"فاجاب بنو حثّ ابراهيم قائلين له"));
arasuperobject.biblemap.put("b1_23_6", new arasuperobject(1,23,6,"اسمعنا يا سيدي انت رئيس من الله بيننا. في افضل قبورنا ادفن ميتك. لا يمنع احد منا قبره عنك حتى لا تدفن ميتك"));
arasuperobject.biblemap.put("b1_23_7", new arasuperobject(1,23,7,"فقام ابراهيم وسجد لشعب الارض لبني حثّ"));
arasuperobject.biblemap.put("b1_23_8", new arasuperobject(1,23,8,"وكلمهم قائلا ان كان في نفوسكم ان ادفن ميتي من امامي فاسمعوني والتمسوا لي من عفرون بن صوحر"));
arasuperobject.biblemap.put("b1_23_9", new arasuperobject(1,23,9,"ان يعطيني مغارة المكفيلة التي له التي في طرف حقله. بثمن كامل يعطيني اياها في وسطكم ملك قبر"));
arasuperobject.biblemap.put("b1_23_10", new arasuperobject(1,23,10,"وكان عفرون جالسا بين بني حثّ. فاجاب عفرون الحثّي ابراهيم في مسامع بني حثّ لدى جميع الداخلين باب مدينته قائلا"));
arasuperobject.biblemap.put("b1_23_11", new arasuperobject(1,23,11,"لا يا سيدي اسمعني. الحقل وهبتك اياه. والمغارة التي فيه لك وهبتها. لدى عيون بني شعبي وهبتك اياها. ادفن ميتك"));
arasuperobject.biblemap.put("b1_23_12", new arasuperobject(1,23,12,"فسجد ابراهيم امام شعب الارض"));
arasuperobject.biblemap.put("b1_23_13", new arasuperobject(1,23,13,"وكلم عفرون في مسامع شعب الارض قائلا بل ان كنت انت اياه فليتك تسمعني. اعطيك ثمن الحقل. خذ مني فادفن ميتي هناك"));
arasuperobject.biblemap.put("b1_23_14", new arasuperobject(1,23,14,"فاجاب عفرون ابراهيم قائلا له"));
arasuperobject.biblemap.put("b1_23_15", new arasuperobject(1,23,15,"يا سيدي اسمعني. ارض باربع مئة شاقل فضة ما هي بيني وبينك. فادفن ميتك"));
arasuperobject.biblemap.put("b1_23_16", new arasuperobject(1,23,16,"فسمع ابراهيم لعفرون ووزن ابراهيم لعفرون الفضة التي ذكرها في مسامع بني حثّ. اربع مئة شاقل فضة جائزة عند التجار"));
arasuperobject.biblemap.put("b1_23_17", new arasuperobject(1,23,17,"فوجب حقل عفرون الذي في المكفيلة التي امام ممرا. الحقل والمغارة التي فيه وجميع الشجر الذي في الحقل الذي في جميع حدوده حواليه"));
arasuperobject.biblemap.put("b1_23_18", new arasuperobject(1,23,18,"لابراهيم ملكا لدى عيون بني حثّ بين جميع الداخلين باب مدينته"));
arasuperobject.biblemap.put("b1_23_19", new arasuperobject(1,23,19,"وبعد ذلك دفن ابراهيم سارة امرأته في مغارة حقل المكفيلة امام ممرا التي هي حبرون في ارض كنعان"));
arasuperobject.biblemap.put("b1_23_20", new arasuperobject(1,23,20,"فوجب الحقل والمغارة التي فيه لابراهيم ملك قبر من عند بني حثّ"));
arasuperobject.biblemap.put("b1_24_1", new arasuperobject(1,24,1,"وشاخ ابراهيم وتقدم في الايام. وبارك الرب ابراهيم في كل شيء"));
arasuperobject.biblemap.put("b1_24_2", new arasuperobject(1,24,2,"وقال ابراهيم لعبده كبير بيته المستولي على كل ما كان له. ضع يدك تحت فخذي"));
arasuperobject.biblemap.put("b1_24_3", new arasuperobject(1,24,3,"فاستحلفك بالرب اله السماء واله الارض ان لا تأخذ زوجة لابني من بنات الكنعانيين الذين انا ساكن بينهم"));
arasuperobject.biblemap.put("b1_24_4", new arasuperobject(1,24,4,"بل الى ارضي والى عشيرتي تذهب وتأخذ زوجة لابني اسحق"));
arasuperobject.biblemap.put("b1_24_5", new arasuperobject(1,24,5,"فقال له العبد ربما لا تشاء المرأة ان تتبعني الى هذه الارض. هل ارجع بابنك الى الارض التي خرجت منها"));
arasuperobject.biblemap.put("b1_24_6", new arasuperobject(1,24,6,"فقال له ابراهيم احترز من ان ترجع بابني الى هناك"));
arasuperobject.biblemap.put("b1_24_7", new arasuperobject(1,24,7,"الرب اله السماء الذي اخذني من بيت ابي ومن ارض ميلادي والذي كلمني والذي اقسم لي قائلا لنسلك اعطي هذه الارض هو يرسل ملاكه امامك فتاخذ زوجة لابني من هناك"));
arasuperobject.biblemap.put("b1_24_8", new arasuperobject(1,24,8,"وان لم تشإ المرأة ان تتبعك تبرأت من حلفي هذا. اما ابني فلا ترجع به الى هناك"));
arasuperobject.biblemap.put("b1_24_9", new arasuperobject(1,24,9,"فوضع العبد يده تحت فخذ ابراهيم مولاه وحلف له على هذا الامر"));
arasuperobject.biblemap.put("b1_24_10", new arasuperobject(1,24,10,"ثم اخذ العبد عشرة جمال من جمال مولاه ومضى وجميع خيرات مولاه في يده. فقام وذهب الى ارام النهرين الى مدينة ناحور"));
arasuperobject.biblemap.put("b1_24_11", new arasuperobject(1,24,11,"واناخ الجمال خارج المدينة عند بئر الماء وقت المساء وقت خروج المستقيات"));
arasuperobject.biblemap.put("b1_24_12", new arasuperobject(1,24,12,"وقال ايها الرب اله سيدي ابراهيم يسّر لي اليوم واصنع لطفا الى سيدي ابراهيم"));
arasuperobject.biblemap.put("b1_24_13", new arasuperobject(1,24,13,"ها انا واقف على عين الماء وبنات اهل المدينة خارجات ليستقين ماء"));
arasuperobject.biblemap.put("b1_24_14", new arasuperobject(1,24,14,"فليكن ان الفتاة التي اقول لها اميلي جرتك لاشرب فتقول اشرب وانا اسقي جمالك ايضا هي التي عيّنتها لعبدك اسحق. وبها اعلم انك صنعت لطفا الى سيدي"));
arasuperobject.biblemap.put("b1_24_15", new arasuperobject(1,24,15,"واذ كان لم يفرغ بعد من الكلام اذا رفقة التي ولدت لبتوئيل ابن ملكة امرأة ناحور اخي ابراهيم خارجة وجرتها على كتفها"));
arasuperobject.biblemap.put("b1_24_16", new arasuperobject(1,24,16,"وكانت الفتاة حسنة المنظر جدا وعذراء لم يعرفها رجل. فنزلت الى العين وملأت جرتها وطلعت"));
arasuperobject.biblemap.put("b1_24_17", new arasuperobject(1,24,17,"فركض العبد للقائها وقال اسقيني قليل ماء من جرتك"));
arasuperobject.biblemap.put("b1_24_18", new arasuperobject(1,24,18,"فقالت اشرب يا سيدي. واسرعت وانزلت جرتها على يدها وسقته"));
arasuperobject.biblemap.put("b1_24_19", new arasuperobject(1,24,19,"ولما فرغت من سقيه قالت استقي لجمالك ايضا حتى تفرغ من الشرب"));
arasuperobject.biblemap.put("b1_24_20", new arasuperobject(1,24,20,"فاسرعت وافرغت جرتها في المسقاة وركضت ايضا الى البئر لتستقي. فاستقت لكل جماله"));
arasuperobject.biblemap.put("b1_24_21", new arasuperobject(1,24,21,"والرجل يتفرس فيها صامتا ليعلم أأنجح الرب طريقه ام لا"));
arasuperobject.biblemap.put("b1_24_22", new arasuperobject(1,24,22,"وحدث عندما فرغت الجمال من الشرب ان الرجل اخذ خزامة ذهب وزنها نصف شاقل وسوارين على يديها وزنهما عشرة شواقل ذهب"));
arasuperobject.biblemap.put("b1_24_23", new arasuperobject(1,24,23,"وقال بنت من انت. اخبريني. هل في بيت ابيك مكان لنا لنبيت"));
arasuperobject.biblemap.put("b1_24_24", new arasuperobject(1,24,24,"فقالت له انا بنت بتوئيل ابن ملكة الذي ولدته لناحور"));
arasuperobject.biblemap.put("b1_24_25", new arasuperobject(1,24,25,"وقالت له عندنا تبن وعلف كثير ومكان لتبيتوا ايضا"));
arasuperobject.biblemap.put("b1_24_26", new arasuperobject(1,24,26,"فخرّ الرجل وسجد للرب"));
arasuperobject.biblemap.put("b1_24_27", new arasuperobject(1,24,27,"وقال مبارك الرب اله سيدي ابراهيم الذي لم يمنع لطفه وحقه عن سيدي. اذ كنت انا في الطريق هداني الرب الى بيت اخوة سيدي"));
arasuperobject.biblemap.put("b1_24_28", new arasuperobject(1,24,28,"فركضت الفتاة واخبرت بيت امها بحسب هذه الامور"));
arasuperobject.biblemap.put("b1_24_29", new arasuperobject(1,24,29,"وكان لرفقة اخ اسمه لابان. فركض لابان الى الرجل خارجا الى العين"));
arasuperobject.biblemap.put("b1_24_30", new arasuperobject(1,24,30,"وحدث انه اذ رأى الخزامة والسوارين على يدي اخته واذ سمع كلام رفقة اخته قائلة هكذا كلمني الرجل جاء الى الرجل واذا هو واقف عند الجمال على العين"));
arasuperobject.biblemap.put("b1_24_31", new arasuperobject(1,24,31,"فقال ادخل يا مبارك الرب. لماذا تقف خارجا وانا قد هيّأت البيت ومكانا للجمال"));
arasuperobject.biblemap.put("b1_24_32", new arasuperobject(1,24,32,"فدخل الرجل الى البيت وحلّ عن الجمال. فاعطى تبنا وعلفا للجمال وماء لغسل رجليه وارجل الرجال الذين معه"));
arasuperobject.biblemap.put("b1_24_33", new arasuperobject(1,24,33,"ووضع قدامه ليأكل. فقال لا آكل حتى اتكلم كلامي. فقال تكلم"));
arasuperobject.biblemap.put("b1_24_34", new arasuperobject(1,24,34,"فقال انا عبد ابراهيم"));
arasuperobject.biblemap.put("b1_24_35", new arasuperobject(1,24,35,"والرب قد بارك مولاي جدا فصار عظيما. واعطاه غنما وبقرا وفضة وذهبا وعبيدا واماء وجمالا وحميرا"));
arasuperobject.biblemap.put("b1_24_36", new arasuperobject(1,24,36,"وولدت سارة امرأة سيدي ابنا لسيدي بعدما شاخت فقد اعطاه كل ما له"));
arasuperobject.biblemap.put("b1_24_37", new arasuperobject(1,24,37,"واستحلفني سيدي قائلا لا تأخذ زوجة لابني من بنات الكنعانيين الذين انا ساكن في ارضهم"));
arasuperobject.biblemap.put("b1_24_38", new arasuperobject(1,24,38,"بل الى بيت ابي تذهب والى عشيرتي وتاخذ زوجة لابني"));
arasuperobject.biblemap.put("b1_24_39", new arasuperobject(1,24,39,"فقلت لسيدي ربما لا تتبعني المرأة"));
arasuperobject.biblemap.put("b1_24_40", new arasuperobject(1,24,40,"فقال لي ان الرب الذي سرت امامه يرسل ملاكه معك وينجح طريقك. فتاخذ زوجة لابني من عشيرتي ومن بيت ابي"));
arasuperobject.biblemap.put("b1_24_41", new arasuperobject(1,24,41,"حينئذ تتبرأ من حلفي حينما تجيء الى عشيرتي. وان لم يعطوك فتكون بريئا من حلفي"));
arasuperobject.biblemap.put("b1_24_42", new arasuperobject(1,24,42,"فجئت اليوم الى العين وقلت ايها الرب اله سيدي ابراهيم ان كنت تنجح طريقي الذي انا سالك فيه"));
arasuperobject.biblemap.put("b1_24_43", new arasuperobject(1,24,43,"فها انا واقف على عين الماء وليكن ان الفتاة التي تخرج لتستقي واقول لها اسقيني قليل ماء من جرتك"));
arasuperobject.biblemap.put("b1_24_44", new arasuperobject(1,24,44,"فتقول لي اشرب انت وانا استقي لجمالك ايضا هي المرأة التي عيّنها الرب لابن سيدي"));
arasuperobject.biblemap.put("b1_24_45", new arasuperobject(1,24,45,"واذ كنت انا لم افرغ بعد من الكلام في قلبي اذا رفقة خارجة وجرتها على كتفها فنزلت الى العين واستقت. فقلت لها اسقيني"));
arasuperobject.biblemap.put("b1_24_46", new arasuperobject(1,24,46,"فاسرعت وانزلت جرتها عنها وقالت اشرب وانا اسقي جمالك ايضا. فشربت. وسقت الجمال ايضا"));
arasuperobject.biblemap.put("b1_24_47", new arasuperobject(1,24,47,"فسألتها وقلت بنت من انت. فقالت بنت بتوئيل بن ناحور الذي ولدته له ملكة. فوضعت الخزامة في انفها والسوارين على يديها"));
arasuperobject.biblemap.put("b1_24_48", new arasuperobject(1,24,48,"وخررت وسجدت للرب وباركت الرب اله سيدي ابراهيم الذي هداني في طريق امين لآخذ ابنة اخي سيدي لابنه"));
arasuperobject.biblemap.put("b1_24_49", new arasuperobject(1,24,49,"والآن ان كنتم تصنعون معروفا وامانة الى سيدي فاخبروني. وإلا فاخبروني لانصرف يمينا او شمالا"));
arasuperobject.biblemap.put("b1_24_50", new arasuperobject(1,24,50,"فاجاب لابان وبتوئيل وقالا من عند الرب خرج الامر. لا نقدر ان نكلمك بشر او خير"));
arasuperobject.biblemap.put("b1_24_51", new arasuperobject(1,24,51,"هوذا رفقة قدامك. خذها واذهب. فلتكن زوجة لابن سيدك كما تكلم الرب"));
arasuperobject.biblemap.put("b1_24_52", new arasuperobject(1,24,52,"وكان عندما سمع عبد ابراهيم كلامهم انه سجد للرب الى الارض"));
arasuperobject.biblemap.put("b1_24_53", new arasuperobject(1,24,53,"واخرج العبد آنية فضة وآنية ذهب وثيابا واعطاها لرفقة. واعطى تحفا لاخيها ولامها"));
arasuperobject.biblemap.put("b1_24_54", new arasuperobject(1,24,54,"فأكل وشرب هو والرجال الذين معه وباتوا. ثم قاموا صباحا فقال اصرفوني الى سيدي"));
arasuperobject.biblemap.put("b1_24_55", new arasuperobject(1,24,55,"فقال اخوها وامها لتمكث الفتاة عندنا اياما او عشرة. بعد ذلك تمضي"));
arasuperobject.biblemap.put("b1_24_56", new arasuperobject(1,24,56,"فقال لهم لا تعوّقوني والرب قد انجح طريقي. اصرفوني لاذهب الى سيدي"));
arasuperobject.biblemap.put("b1_24_57", new arasuperobject(1,24,57,"فقالوا ندعو الفتاة ونسألها شفاها"));
arasuperobject.biblemap.put("b1_24_58", new arasuperobject(1,24,58,"فدعوا رفقة وقالوا لها هل تذهبين مع هذا الرجل. فقالت اذهب"));
arasuperobject.biblemap.put("b1_24_59", new arasuperobject(1,24,59,"فصرفوا رفقة اختهم ومرضعتها وعبد ابراهيم ورجاله"));
arasuperobject.biblemap.put("b1_24_60", new arasuperobject(1,24,60,"وباركوا رفقة وقالوا لها انت اختنا. صيري الوف ربوات وليرث نسلك باب مبغضيه"));
arasuperobject.biblemap.put("b1_24_61", new arasuperobject(1,24,61,"فقامت رفقة وفتياتها وركبن على الجمال وتبعن الرجل. فأخذ العبد رفقة ومضى"));
arasuperobject.biblemap.put("b1_24_62", new arasuperobject(1,24,62,"وكان اسحق قد اتى من ورود بئر لحي رئي. اذ كان ساكنا في ارض الجنوب"));
arasuperobject.biblemap.put("b1_24_63", new arasuperobject(1,24,63,"وخرج اسحق ليتأمل في الحقل عند اقبال المساء. فرفع عينيه ونظر واذا جمال مقبلة"));
arasuperobject.biblemap.put("b1_24_64", new arasuperobject(1,24,64,"ورفعت رفقة عينيها فرأت اسحق فنزلت عن الجمل"));
arasuperobject.biblemap.put("b1_24_65", new arasuperobject(1,24,65,"وقالت للعبد من هذا الرجل الماشي في الحقل للقائنا. فقال العبد هو سيدي. فاخذت البرقع وتغطّت"));
arasuperobject.biblemap.put("b1_24_66", new arasuperobject(1,24,66,"ثم حدّث العبد اسحق بكل الامور التي صنع"));
arasuperobject.biblemap.put("b1_24_67", new arasuperobject(1,24,67,"فادخلها اسحق الى خباء سارة امه واخذ رفقة فصارت له زوجة واحبّها. فتعزّى اسحق بعد موت امه"));
arasuperobject.biblemap.put("b1_25_1", new arasuperobject(1,25,1,"وعاد ابراهيم فأخذ زوجة اسمها قطورة"));
arasuperobject.biblemap.put("b1_25_2", new arasuperobject(1,25,2,"فولدت له زمران ويقشان ومدان ومديان ويشباق وشوحا"));
arasuperobject.biblemap.put("b1_25_3", new arasuperobject(1,25,3,"وولد يقشان شبا وددان. وكان بنو ددان أشّوريم ولطوشيم ولأمّيم"));
arasuperobject.biblemap.put("b1_25_4", new arasuperobject(1,25,4,"وبنو مديان عيفة وعفر وحنوك وأبيداع وألدعة. جميع هؤلاء بنو قطورة"));
arasuperobject.biblemap.put("b1_25_5", new arasuperobject(1,25,5,"واعطى ابراهيم اسحق كل ما كان له"));
arasuperobject.biblemap.put("b1_25_6", new arasuperobject(1,25,6,"واما بنو السراري اللواتي كانت لابراهيم فاعطاهم ابراهيم عطايا وصرفهم عن اسحق ابنه شرقا الى ارض المشرق وهو بعد حيّ"));
arasuperobject.biblemap.put("b1_25_7", new arasuperobject(1,25,7,"وهذه ايام سني حياة ابراهيم التي عاشها. مئة وخمس وسبعون سنة"));
arasuperobject.biblemap.put("b1_25_8", new arasuperobject(1,25,8,"واسلم ابراهيم روحه ومات بشيبة صالحة شيخا وشبعان اياما وانضمّ الى قومه"));
arasuperobject.biblemap.put("b1_25_9", new arasuperobject(1,25,9,"ودفنه اسحق واسماعيل ابناه في مغارة المكفيلة في حقل عفرون بن صوحر الحثّي الذي امام ممرا"));
arasuperobject.biblemap.put("b1_25_10", new arasuperobject(1,25,10,"الحقل الذي اشتراه ابراهيم من بني حثّ. هناك دفن ابراهيم وسارة امرأته"));
arasuperobject.biblemap.put("b1_25_11", new arasuperobject(1,25,11,"وكان بعد موت ابراهيم ان الله بارك اسحق ابنه. وسكن اسحق عند بئر لحي رئي"));
arasuperobject.biblemap.put("b1_25_12", new arasuperobject(1,25,12,"وهذه مواليد اسماعيل بن ابراهيم الذي ولدته هاجر المصرية جارية سارة لابراهيم"));
arasuperobject.biblemap.put("b1_25_13", new arasuperobject(1,25,13,"وهذه اسماء بني اسماعيل باسمائهم حسب مواليدهم. نبايوت بكر اسماعيل وقيدار وأدبئيل ومبسام"));
arasuperobject.biblemap.put("b1_25_14", new arasuperobject(1,25,14,"ومشماع ودومة ومسّا"));
arasuperobject.biblemap.put("b1_25_15", new arasuperobject(1,25,15,"وحدار وتيما ويطور ونافيش وقدمة"));
arasuperobject.biblemap.put("b1_25_16", new arasuperobject(1,25,16,"هؤلاء هم بنو اسماعيل وهذه اسماؤهم بديارهم وحصونهم اثنا عشر رئيسا حسب قبائلهم"));
arasuperobject.biblemap.put("b1_25_17", new arasuperobject(1,25,17,"وهذه سنو حياة اسماعيل. مئة وسبع وثلاثون سنة. واسلم روحه ومات وانضمّ الى قومه"));
arasuperobject.biblemap.put("b1_25_18", new arasuperobject(1,25,18,"وسكنوا من حويلة الى شور التي امام مصر حينما تجيء نحو اشور. امام جميع اخوته نزل"));
arasuperobject.biblemap.put("b1_25_19", new arasuperobject(1,25,19,"وهذه مواليد اسحق بن ابراهيم. ولد ابراهيم اسحق"));
arasuperobject.biblemap.put("b1_25_20", new arasuperobject(1,25,20,"وكان اسحق ابن اربعين سنة لما اتخذ لنفسه زوجة رفقة بنت بتوئيل الارامي اخت لابان الارامي من فدّان ارام"));
arasuperobject.biblemap.put("b1_25_21", new arasuperobject(1,25,21,"وصلّى اسحق الى الرب لاجل امرأته لانها كانت عاقرا. فاستجاب له الرب فحبلت رفقة امرأته"));
arasuperobject.biblemap.put("b1_25_22", new arasuperobject(1,25,22,"وتزاحم الولدان في بطنها. فقالت ان كان هكذا فلماذا انا. فمضت لتسأل الرب"));
arasuperobject.biblemap.put("b1_25_23", new arasuperobject(1,25,23,"فقال لها الرب في بطنك امّتان. ومن احشائك يفترق شعبان. شعب يقوى على شعب. وكبير يستعبد لصغير"));
arasuperobject.biblemap.put("b1_25_24", new arasuperobject(1,25,24,"فلما كملت ايامها لتلد اذا في بطنها توأمان"));
arasuperobject.biblemap.put("b1_25_25", new arasuperobject(1,25,25,"فخرج الاول احمر. كله كفروة شعر. فدعوا اسمه عيسو"));
arasuperobject.biblemap.put("b1_25_26", new arasuperobject(1,25,26,"وبعد ذلك خرج اخوه ويده قابضة بعقب عيسو فدعي اسمه يعقوب. وكان اسحق ابن ستين سنة لما ولدتهما"));
arasuperobject.biblemap.put("b1_25_27", new arasuperobject(1,25,27,"فكبر الغلامان. وكان عيسو انسانا يعرف الصيد انسان البرية ويعقوب انسانا كاملا يسكن الخيام"));
arasuperobject.biblemap.put("b1_25_28", new arasuperobject(1,25,28,"فاحب اسحق عيسو لان في فمه صيدا. واما رفقة فكانت تحب يعقوب"));
arasuperobject.biblemap.put("b1_25_29", new arasuperobject(1,25,29,"وطبخ يعقوب طبيخا فاتى عيسو من الحقل وهو قد اعيا"));
arasuperobject.biblemap.put("b1_25_30", new arasuperobject(1,25,30,"فقال عيسو ليعقوب اطعمني من هذا الاحمر لاني قد اعييت. لذلك دعي اسمه ادوم"));
arasuperobject.biblemap.put("b1_25_31", new arasuperobject(1,25,31,"فقال يعقوب بعني اليوم بكوريتك"));
arasuperobject.biblemap.put("b1_25_32", new arasuperobject(1,25,32,"فقال عيسو ها انا ماض الى الموت. فلماذا لي بكورية"));
arasuperobject.biblemap.put("b1_25_33", new arasuperobject(1,25,33,"فقال يعقوب احلف لي اليوم. فحلف له. فباع بكوريته ليعقوب"));
arasuperobject.biblemap.put("b1_25_34", new arasuperobject(1,25,34,"فاعطى يعقوب عيسو خبزا وطبيخ عدس. فاكل وشرب وقام ومضى. فاحتقر عيسو البكورية"));
arasuperobject.biblemap.put("b1_26_1", new arasuperobject(1,26,1,"وكان في الارض جوع غير الجوع الاول الذي كان في ايام ابراهيم. فذهب اسحق الى ابيمالك ملك الفلسطينيين الى جرار"));
arasuperobject.biblemap.put("b1_26_2", new arasuperobject(1,26,2,"وظهر له الرب وقال لا تنزل الى مصر. اسكن في الارض التي اقول لك"));
arasuperobject.biblemap.put("b1_26_3", new arasuperobject(1,26,3,"تغرب في هذه الارض. فاكون معك واباركك. لاني لك ولنسلك اعطي جميع هذه البلاد وافي بالقسم الذي اقسمت لابراهيم ابيك"));
arasuperobject.biblemap.put("b1_26_4", new arasuperobject(1,26,4,"واكثر نسلك كنجوم السماء واعطي نسلك جميع هذه البلاد وتتبارك في نسلك جميع امم الارض"));
arasuperobject.biblemap.put("b1_26_5", new arasuperobject(1,26,5,"من اجل ان ابراهيم سمع لقولي وحفظ ما يحفظ لي اوامري وفرائضي وشرائعي"));
arasuperobject.biblemap.put("b1_26_6", new arasuperobject(1,26,6,"فاقام اسحق في جرار"));
arasuperobject.biblemap.put("b1_26_7", new arasuperobject(1,26,7,"وسأله اهل المكان عن امرأته. فقال هي اختي. لانه خاف ان يقول امرأتي لعل اهل المكان يقتلونني من اجل رفقة لانها كانت حسنة المنظر"));
arasuperobject.biblemap.put("b1_26_8", new arasuperobject(1,26,8,"وحدث اذ طالت له الايام هناك ان ابيمالك ملك الفلسطينيين اشرف من الكوّة ونظر واذا اسحق يلاعب رفقة امرأته"));
arasuperobject.biblemap.put("b1_26_9", new arasuperobject(1,26,9,"فدعا ابيمالك اسحق وقال انما هي امرأتك. فكيف قلت هي اختي. فقال له اسحق لاني قلت لعلي اموت بسببها"));
arasuperobject.biblemap.put("b1_26_10", new arasuperobject(1,26,10,"فقال ابيمالك ما هذا الذي صنعت بنا. لولا قليل لاضطجع احد الشعب مع امرأتك فجلبت علينا ذنبا"));
arasuperobject.biblemap.put("b1_26_11", new arasuperobject(1,26,11,"فاوصى ابيمالك جميع الشعب قائلا الذي يمسّ هذا الرجل او امرأته موتا يموت"));
arasuperobject.biblemap.put("b1_26_12", new arasuperobject(1,26,12,"وزرع اسحق في تلك الارض فاصاب في تلك السنة مئة ضعف وباركه الرب"));
arasuperobject.biblemap.put("b1_26_13", new arasuperobject(1,26,13,"فتعاظم الرجل وكان يتزايد في التعاظم حتى صار عظيما جدا"));
arasuperobject.biblemap.put("b1_26_14", new arasuperobject(1,26,14,"فكان له مواش من الغنم ومواش من البقر وعبيد كثيرون. فحسده الفلسطينيون"));
arasuperobject.biblemap.put("b1_26_15", new arasuperobject(1,26,15,"وجميع الآبار التي حفرها عبيد ابيه في ايام ابراهيم ابيه طمّها الفلسطينيون وملأوها ترابا"));
arasuperobject.biblemap.put("b1_26_16", new arasuperobject(1,26,16,"وقال أبيمالك لاسحق اذهب من عندنا لانك صرت اقوى منا جدا"));
arasuperobject.biblemap.put("b1_26_17", new arasuperobject(1,26,17,"فمضى اسحق من هناك ونزل في وادي جرار واقام هناك"));
arasuperobject.biblemap.put("b1_26_18", new arasuperobject(1,26,18,"فعاد اسحق ونبش آبار الماء التي حفروها في ايام ابراهيم ابيه وطمّها الفلسطينيون بعد موت ابيه. ودعاها باسماء كالاسماء التي دعاها بها ابوه"));
arasuperobject.biblemap.put("b1_26_19", new arasuperobject(1,26,19,"وحفر عبيد اسحق في الوادي فوجدوا هناك بئر ماء حيّ"));
arasuperobject.biblemap.put("b1_26_20", new arasuperobject(1,26,20,"فخاصم رعاة جرار رعاة اسحق قائلين لنا الماء. فدعا اسم البئر عسق لانهم نازعوه"));
arasuperobject.biblemap.put("b1_26_21", new arasuperobject(1,26,21,"ثم حفروا بئرا اخرى وتخاصموا عليها ايضا. فدعى اسمها سطنة"));
arasuperobject.biblemap.put("b1_26_22", new arasuperobject(1,26,22,"ثم نقل من هناك وحفر بئرا اخرى ولم يتخاصموا عليها. فدعا اسمها رحوبوت. وقال انه الآن قد ارحب لنا الرب واثمرنا في الارض"));
arasuperobject.biblemap.put("b1_26_23", new arasuperobject(1,26,23,"ثم صعد من هناك الى بئر سبع"));
arasuperobject.biblemap.put("b1_26_24", new arasuperobject(1,26,24,"فظهر له الرب في تلك الليلة وقال انا اله ابراهيم ابيك. لا تخف لاني معك واباركك واكثر نسلك من اجل ابراهيم عبدي"));
arasuperobject.biblemap.put("b1_26_25", new arasuperobject(1,26,25,"فبنى هناك مذبحا ودعا باسم الرب. ونصب هناك خيمته وحفر هناك عبيد اسحق بئرا"));
arasuperobject.biblemap.put("b1_26_26", new arasuperobject(1,26,26,"وذهب اليه من جرار ابيمالك وأحزّات من اصحابه وفيكول رئيس جيشه"));
arasuperobject.biblemap.put("b1_26_27", new arasuperobject(1,26,27,"فقال لهم اسحق ما بالكم أتيتم اليّ وانتم قد ابغضتموني وصرفتموني من عندكم"));
arasuperobject.biblemap.put("b1_26_28", new arasuperobject(1,26,28,"فقالوا اننا قد رأينا ان الرب كان معك. فقلنا ليكن بيننا حلف بيننا وبينك ونقطع معك عهدا"));
arasuperobject.biblemap.put("b1_26_29", new arasuperobject(1,26,29,"ان لا تصنع بنا شرا. كما لم نمسّك وكما لم نصنع بك الا خيرا وصرفناك بسلام. انت الآن مبارك الرب"));
arasuperobject.biblemap.put("b1_26_30", new arasuperobject(1,26,30,"فصنع لهم ضيافة. فأكلوا وشربوا"));
arasuperobject.biblemap.put("b1_26_31", new arasuperobject(1,26,31,"ثم بكروا في الغد وحلفوا بعضهم لبعض وصرفهم اسحق. فمضوا من عنده بسلام"));
arasuperobject.biblemap.put("b1_26_32", new arasuperobject(1,26,32,"وحدث في ذلك اليوم ان عبيد اسحق جاءوا واخبروه عن البئر التي حفروا وقالوا له قد وجدنا ماء"));
arasuperobject.biblemap.put("b1_26_33", new arasuperobject(1,26,33,"فدعاها شبعة. لذلك اسم المدينة بئر سبع الى هذا اليوم"));
arasuperobject.biblemap.put("b1_26_34", new arasuperobject(1,26,34,"ولما كان عيسو ابن اربعين سنة اتخذ زوجة يهوديت ابنة بيري الحثّي وبسمة ابنة ايلون الحثّي"));
arasuperobject.biblemap.put("b1_26_35", new arasuperobject(1,26,35,"فكانتا مرارة نفس لاسحق ورفقة"));
arasuperobject.biblemap.put("b1_27_1", new arasuperobject(1,27,1,"وحدث لما شاخ اسحق وكلّت عيناه عن النظر انه دعا عيسو ابنه الاكبر وقال له يا ابني. فقال له هانذا"));
arasuperobject.biblemap.put("b1_27_2", new arasuperobject(1,27,2,"فقال انني قد شخت ولست اعرف يوم وفاتي"));
arasuperobject.biblemap.put("b1_27_3", new arasuperobject(1,27,3,"فالآن خذ عدتك جعبتك وقوسك واخرج الى البرية وتصيّد لي صيدا"));
arasuperobject.biblemap.put("b1_27_4", new arasuperobject(1,27,4,"واصنع لي اطعمة كما احب وأتني بها لآكل حتى تباركك نفسي قبل ان اموت"));
arasuperobject.biblemap.put("b1_27_5", new arasuperobject(1,27,5,"وكانت رفقة سامعة اذ تكلم اسحق مع عيسو ابنه. فذهب عيسو الى البرية كي يصطاد صيدا ليأتي به"));
arasuperobject.biblemap.put("b1_27_6", new arasuperobject(1,27,6,"وأما رفقة فكلمت يعقوب ابنها قائلة اني قد سمعت اباك يكلم عيسو اخاك قائلا"));
arasuperobject.biblemap.put("b1_27_7", new arasuperobject(1,27,7,"ائتني بصيد واصنع لي اطعمة لآكل واباركك امام الرب قبل وفاتي"));
arasuperobject.biblemap.put("b1_27_8", new arasuperobject(1,27,8,"فالآن يا ابني اسمع لقولي في ما انا آمرك به"));
arasuperobject.biblemap.put("b1_27_9", new arasuperobject(1,27,9,"اذهب الى الغنم وخذ لي من هناك جديين جيّدين من المعزى. فأصنعهما اطعمة لابيك كما يحب"));
arasuperobject.biblemap.put("b1_27_10", new arasuperobject(1,27,10,"فتحضرها الى ابيك لياكل حتى يباركك قبل وفاته"));
arasuperobject.biblemap.put("b1_27_11", new arasuperobject(1,27,11,"فقال يعقوب لرفقة امه هوذا عيسو اخي رجل اشعر وانا رجل املس"));
arasuperobject.biblemap.put("b1_27_12", new arasuperobject(1,27,12,"ربما يجسّني ابي فاكون في عينيه كمتهاون واجلب على نفسي لعنة لا بركة"));
arasuperobject.biblemap.put("b1_27_13", new arasuperobject(1,27,13,"فقالت له امه لعنتك عليّ يا ابني. اسمع لقولي فقط واذهب خذ لي"));
arasuperobject.biblemap.put("b1_27_14", new arasuperobject(1,27,14,"فذهب واخذ واحضر لامه. فصنعت امه اطعمة كما كان ابوه يحب"));
arasuperobject.biblemap.put("b1_27_15", new arasuperobject(1,27,15,"واخذت رفقة ثياب عيسو ابنها الاكبر الفاخرة التي كانت عندها في البيت والبست يعقوب ابنها الاصغر"));
arasuperobject.biblemap.put("b1_27_16", new arasuperobject(1,27,16,"والبست يديه وملاسة عنقه جلود جديي المعزى"));
arasuperobject.biblemap.put("b1_27_17", new arasuperobject(1,27,17,"واعطت الاطعمة والخبز التي صنعت في يد يعقوب ابنها"));
arasuperobject.biblemap.put("b1_27_18", new arasuperobject(1,27,18,"فدخل الى ابيه وقال يا ابي. فقال هانذا. من انت يا ابني"));
arasuperobject.biblemap.put("b1_27_19", new arasuperobject(1,27,19,"فقال يعقوب لابيه انا عيسو بكرك. قد فعلت كما كلمتني. قم اجلس وكل من صيدي لكي تباركني نفسك"));
arasuperobject.biblemap.put("b1_27_20", new arasuperobject(1,27,20,"فقال اسحق لابنه ما هذا الذي اسرعت لتجد يا ابني. فقال ان الرب الهك قد يسّر لي"));
arasuperobject.biblemap.put("b1_27_21", new arasuperobject(1,27,21,"فقال اسحق ليعقوب تقدم لاجسّك يا ابني. أأنت هو ابني عيسو ام لا"));
arasuperobject.biblemap.put("b1_27_22", new arasuperobject(1,27,22,"فتقدم يعقوب الى اسحق ابيه. فجسّه وقال الصوت صوت يعقوب ولكن اليدين يدا عيسو"));
arasuperobject.biblemap.put("b1_27_23", new arasuperobject(1,27,23,"ولم يعرفه لان يديه كانتا مشعرتين كيدي عيسو اخيه. فباركه . ‎"));
arasuperobject.biblemap.put("b1_27_24", new arasuperobject(1,27,24,"وقال هل انت هو ابني عيسو. فقال انا هو"));
arasuperobject.biblemap.put("b1_27_25", new arasuperobject(1,27,25,"فقال قدم لي لآكل من صيد ابني حتى تباركك نفسي. فقدّم له فاكل. واحضر له خمرا فشرب"));
arasuperobject.biblemap.put("b1_27_26", new arasuperobject(1,27,26,"فقال له اسحق ابوه تقدم وقبّلني يا ابني"));
arasuperobject.biblemap.put("b1_27_27", new arasuperobject(1,27,27,"فتقدم وقبّله. فشم رائحة ثيابه وباركه. وقال انظر. رائحة ابني كرائحة حقل قد باركه الرب"));
arasuperobject.biblemap.put("b1_27_28", new arasuperobject(1,27,28,"فليعطك الله من ندى السماء. ومن دسم الارض. وكثرة حنطة وخمر"));
arasuperobject.biblemap.put("b1_27_29", new arasuperobject(1,27,29,"ليستعبد لك شعوب. وتسجد لك قبائل. كن سيدا لاخوتك. وليسجد لك بنو امك. ليكن لاعنوك ملعونين. ومباركوك مباركين"));
arasuperobject.biblemap.put("b1_27_30", new arasuperobject(1,27,30,"وحدث عندما فرغ اسحق من بركة يعقوب ويعقوب قد خرج من لدن اسحق ابيه ان عيسو اخاه أتى من صيده"));
arasuperobject.biblemap.put("b1_27_31", new arasuperobject(1,27,31,"فصنع هو ايضا اطعمة ودخل بها الى ابيه وقال لابيه ليقم ابي وياكل من صيد ابنه حتى تباركني نفسك"));
arasuperobject.biblemap.put("b1_27_32", new arasuperobject(1,27,32,"فقال له اسحق ابوه من انت. فقال انا ابنك بكرك عيسو"));
arasuperobject.biblemap.put("b1_27_33", new arasuperobject(1,27,33,"فارتعد اسحق ارتعادا عظيما جدا. وقال فمن هو الذي اصطاد صيدا وأتى به اليّ فاكلت من الكل قبل ان تجيء وباركته. نعم ويكون مباركا"));
arasuperobject.biblemap.put("b1_27_34", new arasuperobject(1,27,34,"فعندما سمع عيسو كلام ابيه صرخ صرخة عظيمة ومرة جدا. وقال لابيه باركني انا ايضا يا ابي"));
arasuperobject.biblemap.put("b1_27_35", new arasuperobject(1,27,35,"فقال قد جاء اخوك بمكر واخذ بركتك"));
arasuperobject.biblemap.put("b1_27_36", new arasuperobject(1,27,36,"فقال الا ان اسمه دعي يعقوب. فقد تعقبني الآن مرتين. اخذ بكوريتي وهوذا الآن قد اخذ بركتي. ثم قال أما ابقيت لي بركة"));
arasuperobject.biblemap.put("b1_27_37", new arasuperobject(1,27,37,"فاجاب اسحق وقال لعيسو اني قد جعلته سيدا لك ودفعت اليه جميع اخوته عبيدا وعضدته بحنطة وخمر. فماذا اصنع اليك يا ابني"));
arasuperobject.biblemap.put("b1_27_38", new arasuperobject(1,27,38,"فقال عيسو لابيه ألك بركة واحدة فقط يا ابي. باركني انا ايضا يا ابي. ورفع عيسو صوته وبكى"));
arasuperobject.biblemap.put("b1_27_39", new arasuperobject(1,27,39,"فاجاب اسحق ابوه وقال له هوذا بلا دسم الارض يكون مسكنك. وبلا ندى السماء من فوق"));
arasuperobject.biblemap.put("b1_27_40", new arasuperobject(1,27,40,"وبسيفك تعيش. ولاخيك تستعبد. ولكن يكون حينما تجمح انك تكسر نيره عن عنقك"));
arasuperobject.biblemap.put("b1_27_41", new arasuperobject(1,27,41,"فحقد عيسو على يعقوب من اجل البركة التي باركه بها ابوه. وقال عيسو في قلبه قربت ايام مناحة ابي. فاقتل يعقوب اخي"));
arasuperobject.biblemap.put("b1_27_42", new arasuperobject(1,27,42,"فأخبرت رفقة بكلام عيسو ابنها الاكبر. فارسلت ودعت يعقوب ابنها الاصغر وقالت له هوذا عيسو اخوك متسلّ من جهتك بانه يقتلك"));
arasuperobject.biblemap.put("b1_27_43", new arasuperobject(1,27,43,"فالآن يا ابني اسمع لقولي وقم اهرب الى اخي لابان الى حاران"));
arasuperobject.biblemap.put("b1_27_44", new arasuperobject(1,27,44,"وأقم عنده اياما قليلة حتى يرتد سخط اخيك"));
arasuperobject.biblemap.put("b1_27_45", new arasuperobject(1,27,45,"حتى يرتد غضب اخيك عنك وينسى ما صنعت به. ثم ارسل فآخذك من هناك. لماذا اعدم اثنيكما في يوم واحد"));
arasuperobject.biblemap.put("b1_27_46", new arasuperobject(1,27,46,"وقالت رفقة لاسحق مللت حياتي من اجل بنات حثّ. ان كان يعقوب يأخذ زوجة من بنات حثّ مثل هؤلاء من بنات الارض فلماذا لي حياة"));
arasuperobject.biblemap.put("b1_28_1", new arasuperobject(1,28,1,"فدعا اسحق يعقوب وباركه واوصاه وقال له لا تأخذ زوجة من بنات كنعان"));
arasuperobject.biblemap.put("b1_28_2", new arasuperobject(1,28,2,"قم اذهب الى فدّان ارام الى بيت بتوئيل ابي امك وخذ لنفسك زوجة من هناك من بنات لابان اخي امك"));
arasuperobject.biblemap.put("b1_28_3", new arasuperobject(1,28,3,"والله القدير يباركك ويجعلك مثمرا ويكثّرك فتكون جمهورا من الشعوب"));
arasuperobject.biblemap.put("b1_28_4", new arasuperobject(1,28,4,"ويعطيك بركة ابراهيم لك ولنسلك معك. لترث ارض غربتك التي اعطاها الله لابراهيم"));
arasuperobject.biblemap.put("b1_28_5", new arasuperobject(1,28,5,"فصرف اسحق يعقوب فذهب الى فدّان ارام الى لابان بن بتوئيل الارامي اخي رفقة ام يعقوب وعيسو"));
arasuperobject.biblemap.put("b1_28_6", new arasuperobject(1,28,6,"فلما رأى عيسو ان اسحق بارك يعقوب وارسله الى فدّان ارام ليأخذ لنفسه من هناك زوجة. اذ باركه واوصاه قائلا لا تأخذ زوجة من بنات كنعان"));
arasuperobject.biblemap.put("b1_28_7", new arasuperobject(1,28,7,"وان يعقوب سمع لابيه وامّه وذهب الى فدّان ارام"));
arasuperobject.biblemap.put("b1_28_8", new arasuperobject(1,28,8,"رأى عيسو ان بنات كنعان شريرات في عيني اسحق ابيه"));
arasuperobject.biblemap.put("b1_28_9", new arasuperobject(1,28,9,"فذهب عيسو الى اسماعيل واخذ محلة بنت اسماعيل بن ابراهيم اخت نبايوت زوجة له على نسائه"));
arasuperobject.biblemap.put("b1_28_10", new arasuperobject(1,28,10,"فخرج يعقوب من بئر سبع وذهب نحو حاران"));
arasuperobject.biblemap.put("b1_28_11", new arasuperobject(1,28,11,"وصادف مكانا وبات هناك لان الشمس كانت قد غابت. واخذ من حجارة المكان ووضعه تحت راسه فاضطجع في ذلك المكان"));
arasuperobject.biblemap.put("b1_28_12", new arasuperobject(1,28,12,"ورأى حلما واذا سلّم منصوبة على الارض وراسها يمسّ السماء. وهوذا ملائكة الله صاعدة ونازلة عليها"));
arasuperobject.biblemap.put("b1_28_13", new arasuperobject(1,28,13,"وهوذا الرب واقف عليها فقال انا الرب اله ابراهيم ابيك واله اسحق. الارض التي انت مضطجع عليها اعطيها لك ولنسلك"));
arasuperobject.biblemap.put("b1_28_14", new arasuperobject(1,28,14,"ويكون نسلك كتراب الارض وتمتدّ غربا وشرقا وشمالا وجنوبا. ويتبارك فيك وفي نسلك جميع قبائل الارض"));
arasuperobject.biblemap.put("b1_28_15", new arasuperobject(1,28,15,"وها انا معك واحفظك حيثما تذهب واردك الى هذه الارض. لاني لا اتركك حتى افعل ما كلمتك به"));
arasuperobject.biblemap.put("b1_28_16", new arasuperobject(1,28,16,"فاستيقظ يعقوب من نومه وقال حقا ان الرب في هذا المكان وانا لم اعلم"));
arasuperobject.biblemap.put("b1_28_17", new arasuperobject(1,28,17,"وخاف وقال ما ارهب هذا المكان. ما هذا الا بيت الله وهذا باب السماء"));
arasuperobject.biblemap.put("b1_28_18", new arasuperobject(1,28,18,"وبكّر يعقوب في الصباح واخذ الحجر الذي وضعه تحت راسه واقامه عمودا وصب زيتا على راسه"));
arasuperobject.biblemap.put("b1_28_19", new arasuperobject(1,28,19,"ودعا اسم ذلك المكان بيت ايل. ولكن اسم المدينة اولا كان لوز"));
arasuperobject.biblemap.put("b1_28_20", new arasuperobject(1,28,20,"ونذر يعقوب نذرا قائلا ان كان الله معي وحفظني في هذا الطريق الذي انا سائر فيه واعطاني خبزا لآكل وثيابا لالبس"));
arasuperobject.biblemap.put("b1_28_21", new arasuperobject(1,28,21,"ورجعت بسلام الى بيت ابي يكون الرب لي الها"));
arasuperobject.biblemap.put("b1_28_22", new arasuperobject(1,28,22,"وهذا الحجر الذي اقمته عمودا يكون بيت الله وكل ما تعطيني فاني اعشّره لك"));
arasuperobject.biblemap.put("b1_29_1", new arasuperobject(1,29,1,"ثم رفع يعقوب رجليه وذهب الى ارض بني المشرق"));
arasuperobject.biblemap.put("b1_29_2", new arasuperobject(1,29,2,"ونظر واذا في الحقل بئر وهناك ثلاثة قطعان غنم رابضة عندها. لانهم كانوا من تلك البئر يسقون القطعان. والحجر على فم البئر كان كبيرا"));
arasuperobject.biblemap.put("b1_29_3", new arasuperobject(1,29,3,"فكان يجتمع الى هناك جميع القطعان فيدحرجون الحجر عن فم البئر ويسقون الغنم. ثم يردون الحجر على فم البئر الى مكانه"));
arasuperobject.biblemap.put("b1_29_4", new arasuperobject(1,29,4,"فقال لهم يعقوب يا اخوتي من اين انتم. فقالوا نحن من حاران"));
arasuperobject.biblemap.put("b1_29_5", new arasuperobject(1,29,5,"فقال لهم هل تعرفون لابان ابن ناحور. فقالوا نعرفه"));
arasuperobject.biblemap.put("b1_29_6", new arasuperobject(1,29,6,"فقال لهم هل له سلامة. فقالوا له سلامة. وهوذا راحيل ابنته آتية مع الغنم"));
arasuperobject.biblemap.put("b1_29_7", new arasuperobject(1,29,7,"فقال هوذا النهار بعد طويل. ليس وقت اجتماع المواشي. اسقوا الغنم واذهبوا ارعوا"));
arasuperobject.biblemap.put("b1_29_8", new arasuperobject(1,29,8,"فقالوا لا نقدر حتى تجتمع جميع القطعان ويدحرجوا الحجر عن فم البئر. ثم نسقي الغنم"));
arasuperobject.biblemap.put("b1_29_9", new arasuperobject(1,29,9,"واذ هو بعد يتكلم معهم اتت راحيل مع غنم ابيها. لانها كانت ترعى"));
arasuperobject.biblemap.put("b1_29_10", new arasuperobject(1,29,10,"فكان لما ابصر يعقوب راحيل بنت لابان خاله وغنم لابان خاله ان يعقوب تقدم ودحرج الحجر عن فم البئر وسقى غنم لابان خاله"));
arasuperobject.biblemap.put("b1_29_11", new arasuperobject(1,29,11,"وقبّل يعقوب راحيل ورفع صوته وبكى"));
arasuperobject.biblemap.put("b1_29_12", new arasuperobject(1,29,12,"واخبر يعقوب راحيل انه اخو ابيها وانه ابن رفقة. فركضت واخبرت اباها"));
arasuperobject.biblemap.put("b1_29_13", new arasuperobject(1,29,13,"فكان حين سمع لابان خبر يعقوب ابن اخته انه ركض للقائه وعانقه وقبّله وأتى به الى بيته. فحدّث لابان بجميع هذه الامور"));
arasuperobject.biblemap.put("b1_29_14", new arasuperobject(1,29,14,"فقال له لابان انما انت عظمي ولحمي. فاقام عنده شهرا من الزمان"));
arasuperobject.biblemap.put("b1_29_15", new arasuperobject(1,29,15,"ثم قال لابان ليعقوب ألانك اخي تخدمني مجّانا. اخبرني ما اجرتك"));
arasuperobject.biblemap.put("b1_29_16", new arasuperobject(1,29,16,"وكان للابان ابنتان اسم الكبرى ليئة واسم الصغرى راحيل"));
arasuperobject.biblemap.put("b1_29_17", new arasuperobject(1,29,17,"وكانت عينا ليئة ضعيفتين. واما راحيل فكانت حسنة الصورة وحسنة المنظر"));
arasuperobject.biblemap.put("b1_29_18", new arasuperobject(1,29,18,"واحب يعقوب راحيل. فقال اخدمك سبع سنين براحيل ابنتك الصغرى"));
arasuperobject.biblemap.put("b1_29_19", new arasuperobject(1,29,19,"فقال لابان ان اعطيك اياها احسن من ان اعطيها لرجل آخر. أقم عندي"));
arasuperobject.biblemap.put("b1_29_20", new arasuperobject(1,29,20,"فخدم يعقوب براحيل سبع سنين. وكانت في عينيه كايام قليلة بسبب محبته لها"));
arasuperobject.biblemap.put("b1_29_21", new arasuperobject(1,29,21,"ثم قال يعقوب للابان اعطني امرأتي لان ايامي قد كملت فادخل عليها"));
arasuperobject.biblemap.put("b1_29_22", new arasuperobject(1,29,22,"فجمع لابان جميع اهل المكان وصنع وليمة"));
arasuperobject.biblemap.put("b1_29_23", new arasuperobject(1,29,23,"وكان في المساء انه اخذ ليئة ابنته واتى بها اليه. فدخل عليها"));
arasuperobject.biblemap.put("b1_29_24", new arasuperobject(1,29,24,"واعطى لابان زلفة جاريته لليئة ابنته جارية"));
arasuperobject.biblemap.put("b1_29_25", new arasuperobject(1,29,25,"وفي الصباح اذا هي ليئة. فقال للابان ما هذا الذي صنعت بي. أليس براحيل خدمت عندك. فلماذا خدعتني"));
arasuperobject.biblemap.put("b1_29_26", new arasuperobject(1,29,26,"فقال لابان لا يفعل هكذا في مكاننا ان تعطى الصغيرة قبل البكر"));
arasuperobject.biblemap.put("b1_29_27", new arasuperobject(1,29,27,"اكمل اسبوع هذه فنعطيك تلك ايضا بالخدمة التي تخدمني ايضا سبع سنين أخر"));
arasuperobject.biblemap.put("b1_29_28", new arasuperobject(1,29,28,"ففعل يعقوب هكذا. فاكمل اسبوع هذه. فاعطاه راحيل ابنته زوجة له"));
arasuperobject.biblemap.put("b1_29_29", new arasuperobject(1,29,29,"واعطى لابان راحيل ابنته بلهة جاريته جارية لها"));
arasuperobject.biblemap.put("b1_29_30", new arasuperobject(1,29,30,"فدخل على راحيل ايضا. واحب ايضا راحيل اكثر من ليئة. وعاد فخدم عنده سبع سنين أخر"));
arasuperobject.biblemap.put("b1_29_31", new arasuperobject(1,29,31,"ورأى الرب ان ليئة مكروهة ففتح رحمها. واما راحيل فكانت عاقرا"));
arasuperobject.biblemap.put("b1_29_32", new arasuperobject(1,29,32,"فحبلت ليئة وولدت ابنا ودعت اسمه رأوبين. لانها قالت ان الرب قد نظر الى مذّلتي. انه الآن يحبني رجلي"));
arasuperobject.biblemap.put("b1_29_33", new arasuperobject(1,29,33,"وحبلت ايضا وولدت ابنا وقالت ان الرب قد سمع اني مكروهة فاعطاني هذا ايضا. فدعت اسمه شمعون"));
arasuperobject.biblemap.put("b1_29_34", new arasuperobject(1,29,34,"وحبلت ايضا وولدت ابنا. وقالت الآن هذه المرة يقترن بي رجلي. لاني ولدت له ثلاثة بنين. لذلك دعي اسمه لاوي"));
arasuperobject.biblemap.put("b1_29_35", new arasuperobject(1,29,35,"وحبلت ايضا وولدت ابنا وقالت هذه المرة احمد الرب. لذلك دعت اسمه يهوذا. ثم توقفت عن الولادة"));
arasuperobject.biblemap.put("b1_30_1", new arasuperobject(1,30,1,"فلما رأت راحيل انها لم تلد ليعقوب غارت راحيل من اختها وقالت ليعقوب هب لي بنين. وإلا فانا اموت"));
arasuperobject.biblemap.put("b1_30_2", new arasuperobject(1,30,2,"فحمي غضب يعقوب على راحيل وقال ألعلي مكان الله الذي منع عنك ثمرة البطن"));
arasuperobject.biblemap.put("b1_30_3", new arasuperobject(1,30,3,"فقالت هوذا جاريتي بلهة. ادخل عليها فتلد على ركبتيّ وأرزق انا ايضا منها بنين"));
arasuperobject.biblemap.put("b1_30_4", new arasuperobject(1,30,4,". فاعطته بلهة جاريتها زوجة. فدخل عليها يعقوب"));
arasuperobject.biblemap.put("b1_30_5", new arasuperobject(1,30,5,". فحبلت بلهة وولدت ليعقوب ابنا"));
arasuperobject.biblemap.put("b1_30_6", new arasuperobject(1,30,6,"فقالت راحيل قد قضى لي الله وسمع ايضا لصوتي واعطاني ابنا. لذلك دعت اسمه دانا"));
arasuperobject.biblemap.put("b1_30_7", new arasuperobject(1,30,7,"وحبلت ايضا بلهة جارية راحيل وولدت ابنا ثانيا ليعقوب"));
arasuperobject.biblemap.put("b1_30_8", new arasuperobject(1,30,8,"فقالت راحيل مصارعات الله قد صارعت اختي وغلبت. فدعت اسمه نفتالي"));
arasuperobject.biblemap.put("b1_30_9", new arasuperobject(1,30,9,"ولما رأت ليئة انها توقّفت عن الولادة اخذت زلفة جاريتها واعطتها ليعقوب زوجة"));
arasuperobject.biblemap.put("b1_30_10", new arasuperobject(1,30,10,"فولدت زلفة جارية ليئة ليعقوب ابنا"));
arasuperobject.biblemap.put("b1_30_11", new arasuperobject(1,30,11,"فقالت ليئة بسعد. فدعت اسمه جادا"));
arasuperobject.biblemap.put("b1_30_12", new arasuperobject(1,30,12,"وولدت زلفة جارية ليئة ابنا ثانيا ليعقوب"));
arasuperobject.biblemap.put("b1_30_13", new arasuperobject(1,30,13,"فقالت ليئة بغبطتي لانه تغبطني بنات. فدعت اسمه اشير"));
arasuperobject.biblemap.put("b1_30_14", new arasuperobject(1,30,14,"ومضى رأوبين في ايام حصاد الحنطة فوجد لفّاحا في الحقل وجاء به الى ليئة امه. فقالت راحيل لليئة اعطني من لفّاح ابنك"));
arasuperobject.biblemap.put("b1_30_15", new arasuperobject(1,30,15,"فقالت لها أقليل انك اخذت رجلي فتاخذين لفّاح ابني ايضا. فقالت راحيل اذا يضطجع معك الليلة عوضا عن لفّاح ابنك"));
arasuperobject.biblemap.put("b1_30_16", new arasuperobject(1,30,16,"فلما اتى يعقوب من الحقل في المساء خرجت ليئة لملاقاته وقالت اليّ تجيء لاني قد استأجرتك بلفّاح ابني. فاضطجع معها تلك الليلة"));
arasuperobject.biblemap.put("b1_30_17", new arasuperobject(1,30,17,"وسمع الله لليئة فحبلت وولدت ليعقوب ابنا خامسا"));
arasuperobject.biblemap.put("b1_30_18", new arasuperobject(1,30,18,"فقالت ليئة قد اعطاني الله اجرتي لاني اعطيت جاريتي لرجلي. فدعت اسمه يسّاكر"));
arasuperobject.biblemap.put("b1_30_19", new arasuperobject(1,30,19,"وحبلت ايضا ليئة وولدت ابنا سادسا ليعقوب"));
arasuperobject.biblemap.put("b1_30_20", new arasuperobject(1,30,20,"فقالت ليئة قد وهبني الله هبة حسنة. الآن يساكنني رجلي لاني ولدت له ستة بنين. فدعت اسمه زبولون"));
arasuperobject.biblemap.put("b1_30_21", new arasuperobject(1,30,21,"ثم ولدت ابنة ودعت اسمها دينة"));
arasuperobject.biblemap.put("b1_30_22", new arasuperobject(1,30,22,"وذكر الله راحيل وسمع لها الله وفتح رحمها"));
arasuperobject.biblemap.put("b1_30_23", new arasuperobject(1,30,23,"فحبلت وولدت ابنا. فقالت قد نزع الله عاري"));
arasuperobject.biblemap.put("b1_30_24", new arasuperobject(1,30,24,"ودعت اسمه يوسف قائلة يزيدني الرب ابنا آخر"));
arasuperobject.biblemap.put("b1_30_25", new arasuperobject(1,30,25,"وحدث لما ولدت راحيل يوسف ان يعقوب قال للابان اصرفني لاذهب الى مكاني والى ارضي"));
arasuperobject.biblemap.put("b1_30_26", new arasuperobject(1,30,26,"اعطني نسائي واولادي الذين خدمتك بهم فاذهب. لانك انت تعلم خدمتي التي خدمتك"));
arasuperobject.biblemap.put("b1_30_27", new arasuperobject(1,30,27,"فقال له لابان ليتني اجد نعمة في عينيك. قد تفاءلت فباركني الرب بسببك"));
arasuperobject.biblemap.put("b1_30_28", new arasuperobject(1,30,28,"وقال عيّن لي اجرتك فاعطيك"));
arasuperobject.biblemap.put("b1_30_29", new arasuperobject(1,30,29,"فقال له انت تعلم ماذا خدمتك وماذا صارت مواشيك معي"));
arasuperobject.biblemap.put("b1_30_30", new arasuperobject(1,30,30,"لان ما كان لك قبلي قليل فقد اتّسع الى كثير وباركك الرب في اثري. والآن متى اعمل انا ايضا لبيتي"));
arasuperobject.biblemap.put("b1_30_31", new arasuperobject(1,30,31,"فقال ماذا اعطيك. فقال يعقوب لا تعطيني شيئا. ان صنعت لي هذا الامر اعود ارعى غنمك واحفظها"));
arasuperobject.biblemap.put("b1_30_32", new arasuperobject(1,30,32,"اجتاز بين غنمك كلها اليوم. وأعزل انت منها كل شاة رقطاء وبلقاء وكل شاة سوداء بين الخرفان وبلقاء ورقطاء بين المعزى. فيكون مثل ذلك اجرتي"));
arasuperobject.biblemap.put("b1_30_33", new arasuperobject(1,30,33,"ويشهد فيّ برّي يوم غد اذا جئت من اجل اجرتي قدامك. كل ما ليس ارقط او ابلق بين المعزى واسود بين الخرفان فهو مسروق عندي"));
arasuperobject.biblemap.put("b1_30_34", new arasuperobject(1,30,34,"فقال لابان هوذا ليكن بحسب كلامك"));
arasuperobject.biblemap.put("b1_30_35", new arasuperobject(1,30,35,"فعزل في ذلك اليوم التيوس المخطّطة والبلقاء وكل العناز الرقطاء والبلقاء. كل ما فيه بياض وكل اسود بين الخرفان. ودفعها الى ايدي بنيه"));
arasuperobject.biblemap.put("b1_30_36", new arasuperobject(1,30,36,"وجعل مسيرة ثلاثة ايام بينه وبين يعقوب. وكان يعقوب يرعى غنم لابان الباقية"));
arasuperobject.biblemap.put("b1_30_37", new arasuperobject(1,30,37,"فأخذ يعقوب لنفسه قضبانا خضرا من لبنى ولوز ودلب وقشر فيها خطوطا بيضا كاشطا عن البياض الذي على القضبان"));
arasuperobject.biblemap.put("b1_30_38", new arasuperobject(1,30,38,"واوقف القضبان التي قشرها في الاجران في مساقي الماء حيث كانت الغنم تجيء لتشرب. تجاه الغنم. لتتوحم عند مجيئها لتشرب"));
arasuperobject.biblemap.put("b1_30_39", new arasuperobject(1,30,39,"فتوحّمت الغنم عند القضبان وولدت الغنم مخطّطات ورقطا وبلقا"));
arasuperobject.biblemap.put("b1_30_40", new arasuperobject(1,30,40,"وافرز يعقوب الخرفان وجعل وجوه الغنم الى المخطّط وكل اسود بين غنم لابان. وجعل له قطعانا وحده ولم يجعلها مع غنم لابان"));
arasuperobject.biblemap.put("b1_30_41", new arasuperobject(1,30,41,"وحدث كلما توحّمت الغنم القوية ان يعقوب وضع القضبان امام عيون الغنم في الاجران. لتتوحّم بين القضبان"));
arasuperobject.biblemap.put("b1_30_42", new arasuperobject(1,30,42,"وحين استضعفت الغنم لم يضعها. فصارت الضعيفة للابان والقوية ليعقوب"));
arasuperobject.biblemap.put("b1_30_43", new arasuperobject(1,30,43,"فاتّسع الرجل كثيرا جدا. وكان له غنم كثير وجوار وعبيد وجمال وحمير"));
arasuperobject.biblemap.put("b1_31_1", new arasuperobject(1,31,1,"فسمع كلام بني لابان قائلين اخذ يعقوب كل ما كان لابينا. ومما لابينا صنع كل هذا المجد"));
arasuperobject.biblemap.put("b1_31_2", new arasuperobject(1,31,2,"ونظر يعقوب وجه لابان واذا هو ليس معه كامس واول من امس"));
arasuperobject.biblemap.put("b1_31_3", new arasuperobject(1,31,3,"وقال الرب ليعقوب ارجع الى ارض آبائك والى عشيرتك. فاكون معك"));
arasuperobject.biblemap.put("b1_31_4", new arasuperobject(1,31,4,"فارسل يعقوب ودعا راحيل وليئة الى الحقل الى غنمه"));
arasuperobject.biblemap.put("b1_31_5", new arasuperobject(1,31,5,"وقال لهما انا ارى وجه ابيكما انه ليس نحوي كامس واول من امس. ولكن اله ابي كان معي"));
arasuperobject.biblemap.put("b1_31_6", new arasuperobject(1,31,6,"وانتما تعلمان اني بكل قوتي خدمت اباكما"));
arasuperobject.biblemap.put("b1_31_7", new arasuperobject(1,31,7,"واما ابوكما فغدر بي وغيّر اجرتي عشر مرات. لكن الله لم يسمح له ان يصنع بي شرا"));
arasuperobject.biblemap.put("b1_31_8", new arasuperobject(1,31,8,"ان قال هكذا. الرقط تكون اجرتك ولدت كل الغنم رقطا. وان قال هكذا. المخطّطة تكون اجرتك ولدت كل الغنم مخطّطة"));
arasuperobject.biblemap.put("b1_31_9", new arasuperobject(1,31,9,"فقد سلب الله مواشي ابيكما واعطاني"));
arasuperobject.biblemap.put("b1_31_10", new arasuperobject(1,31,10,"وحدث في وقت توحّم الغنم اني رفعت عينيّ ونظرت في حلم واذا الفحول الصاعدة على الغنم مخططة ورقطاء ومنمّرة"));
arasuperobject.biblemap.put("b1_31_11", new arasuperobject(1,31,11,"وقال لي ملاك الله في الحلم يا يعقوب. فقلت هانذا"));
arasuperobject.biblemap.put("b1_31_12", new arasuperobject(1,31,12,"فقال ارفع عينيك وانظر. جميع الفحول الصاعدة على الغنم مخطّطة ورقطاء ومنمّرة. لاني قد رأيت كل ما يصنع بك لابان"));
arasuperobject.biblemap.put("b1_31_13", new arasuperobject(1,31,13,"انا اله بيت ايل حيث مسحت عمودا. حيث نذرت لي نذرا. الآن قم اخرج من هذه الارض وارجع الى ارض ميلادك"));
arasuperobject.biblemap.put("b1_31_14", new arasuperobject(1,31,14,"فاجابت راحيل وليئة وقالتا له ألنا ايضا نصيب وميراث في بيت ابينا"));
arasuperobject.biblemap.put("b1_31_15", new arasuperobject(1,31,15,"ألم نحسب منه اجنبيتين. لانه باعنا وقد اكل ايضا ثمننا"));
arasuperobject.biblemap.put("b1_31_16", new arasuperobject(1,31,16,"ان كل الغنى الذي سلبه الله من ابينا هو لنا ولاولادنا. فالآن كل ما قال لك الله افعل"));
arasuperobject.biblemap.put("b1_31_17", new arasuperobject(1,31,17,"فقام يعقوب وحمل اولاده ونساءه على الجمال"));
arasuperobject.biblemap.put("b1_31_18", new arasuperobject(1,31,18,"وساق كل مواشيه وجميع مقتناه الذي كان قد اقتنى. مواشي اقتنائه التي اقتنى في فدّان ارام. ليجيء الى اسحق ابيه الى ارض كنعان"));
arasuperobject.biblemap.put("b1_31_19", new arasuperobject(1,31,19,"واما لابان فكان قد مضى ليجزّ غنمه. فسرقت راحيل اصنام ابيها"));
arasuperobject.biblemap.put("b1_31_20", new arasuperobject(1,31,20,"وخدع يعقوب قلب لابان الارامي. اذ لم يخبره بانه هارب"));
arasuperobject.biblemap.put("b1_31_21", new arasuperobject(1,31,21,"فهرب هو وكل ما كان له وقام وعبر النهر وجعل وجهه نحو جبل جلعاد"));
arasuperobject.biblemap.put("b1_31_22", new arasuperobject(1,31,22,"فأخبر لابان في اليوم الثالث بان يعقوب قد هرب"));
arasuperobject.biblemap.put("b1_31_23", new arasuperobject(1,31,23,"فأخذ اخوته معه وسعى وراءه مسيرة سبعة ايام. فادركه في جبل جلعاد"));
arasuperobject.biblemap.put("b1_31_24", new arasuperobject(1,31,24,"وأتى الله الى لابان الارامي في حلم الليل. وقال له احترز من ان تكلم يعقوب بخير او شر"));
arasuperobject.biblemap.put("b1_31_25", new arasuperobject(1,31,25,"فلحق لابان يعقوب ويعقوب قد ضرب خيمته في الجبل. فضرب لابان مع اخوته في جبل جلعاد"));
arasuperobject.biblemap.put("b1_31_26", new arasuperobject(1,31,26,"وقال لابان ليعقوب ماذا فعلت وقد خدعت قلبي وسقت بناتي كسبايا السيف"));
arasuperobject.biblemap.put("b1_31_27", new arasuperobject(1,31,27,"لماذا هربت خفية وخدعتني ولم تخبرني حتى اشيعك بالفرح والاغاني بالدف والعود"));
arasuperobject.biblemap.put("b1_31_28", new arasuperobject(1,31,28,"ولم تدعني اقبّل بني وبناتي. الآن بغباوة فعلت"));
arasuperobject.biblemap.put("b1_31_29", new arasuperobject(1,31,29,"في قدرة يدي ان اصنع بكم شرا. ولكن اله ابيكم كلمني البارحة قائلا احترز من ان تكلم يعقوب بخير او شر"));
arasuperobject.biblemap.put("b1_31_30", new arasuperobject(1,31,30,"والآن انت ذهبت لانك قد اشتقت الى بيت ابيك. ولكن لماذا سرقت آلهتي"));
arasuperobject.biblemap.put("b1_31_31", new arasuperobject(1,31,31,"فاجاب يعقوب وقال للابان اني خفت لاني قلت لعلك تغتصب ابنتيك مني"));
arasuperobject.biblemap.put("b1_31_32", new arasuperobject(1,31,32,"الذي تجد آلهتك معه لا يعيش. قدام اخوتنا انظر ماذا معي وخذه لنفسك. ولم يكن يعقوب يعلم ان راحيل سرقتها"));
arasuperobject.biblemap.put("b1_31_33", new arasuperobject(1,31,33,"فدخل لابان خباء يعقوب وخباء ليئة وخباء الجاريتين ولم يجد. وخرج من خباء ليئة ودخل خباء راحيل"));
arasuperobject.biblemap.put("b1_31_34", new arasuperobject(1,31,34,"وكانت راحيل قد اخذت الاصنام ووضعتها في حداجة الجمل وجلست عليها. فجسّ لابان كل الخباء ولم يجد"));
arasuperobject.biblemap.put("b1_31_35", new arasuperobject(1,31,35,"وقالت لابيها لا يغتظ سيدي اني لا استطيع ان اقوم امامك لان عليّ عادة النساء. ففتّش ولم يجد الاصنام"));
arasuperobject.biblemap.put("b1_31_36", new arasuperobject(1,31,36,"فاغتاظ يعقوب وخاصم لابان واجاب يعقوب وقال للابان ما جرمي ما خطيتي حتى حميت ورائي"));
arasuperobject.biblemap.put("b1_31_37", new arasuperobject(1,31,37,"انك جسست جميع اثاثي. ماذا وجدت من جميع اثاث بيتك. ضعه ههنا قدام اخوتي واخوتك. فلينصفوا بيننا الاثنين"));
arasuperobject.biblemap.put("b1_31_38", new arasuperobject(1,31,38,"الآن عشرين سنة انا معك. نعاجك وعنازك لم تسقط. وكباش غنمك لم آكل"));
arasuperobject.biblemap.put("b1_31_39", new arasuperobject(1,31,39,"فريسة لم احضر اليك. انا كنت اخسرها. من يدي كنت تطلبها. مسروقة النهار او مسروقة الليل"));
arasuperobject.biblemap.put("b1_31_40", new arasuperobject(1,31,40,"كنت في النهار يأكلني الحرّ وفي الليل الجليد. وطار نومي من عينيّ"));
arasuperobject.biblemap.put("b1_31_41", new arasuperobject(1,31,41,"الآن لي عشرون سنة في بيتك. خدمتك اربع عشرة سنة بابنتيك وست سنين بغنمك. وقد غيّرت اجرتي عشر مرات"));
arasuperobject.biblemap.put("b1_31_42", new arasuperobject(1,31,42,"لولا ان اله ابي اله ابراهيم وهيبة اسحق كان معي لكنت الآن قد صرفتني فارغا. مشقّتي وتعب يديّ قد نظر الله فوبّخك البارحة"));
arasuperobject.biblemap.put("b1_31_43", new arasuperobject(1,31,43,"فاجاب لابان وقال ليعقوب البنات بناتي والبنون بنيّ والغنم غنمي وكل ما انت ترى فهو لي. فبناتي ماذا اصنع بهنّ اليوم او باولادهنّ الذين ولدن"));
arasuperobject.biblemap.put("b1_31_44", new arasuperobject(1,31,44,"فالآن هلم نقطع عهدا انا وانت. فيكون شاهدا بيني وبينك"));
arasuperobject.biblemap.put("b1_31_45", new arasuperobject(1,31,45,"فأخذ يعقوب حجرا واوقفه عمودا"));
arasuperobject.biblemap.put("b1_31_46", new arasuperobject(1,31,46,"وقال يعقوب لاخوته التقطوا حجارة. فاخذوا حجارة وعملوا رجمة واكلوا هناك على الرجمة"));
arasuperobject.biblemap.put("b1_31_47", new arasuperobject(1,31,47,"ودعاها لابان يجرسهدوثا. واما يعقوب فدعاها جلعيد"));
arasuperobject.biblemap.put("b1_31_48", new arasuperobject(1,31,48,"وقال لابان هذه الرجمة هي شاهدة بيني وبينك اليوم. لذلك دعي اسمها جلعيد"));
arasuperobject.biblemap.put("b1_31_49", new arasuperobject(1,31,49,"والمصفاة. لانه قال ليراقب الرب بيني وبينك حينما نتوارى بعضنا عن بعض"));
arasuperobject.biblemap.put("b1_31_50", new arasuperobject(1,31,50,"انك لا تذلّ بناتي ولا تأخذ نساء على بناتي. ليس انسان معنا. انظر. الله شاهد بيني وبينك"));
arasuperobject.biblemap.put("b1_31_51", new arasuperobject(1,31,51,"وقال لابان ليعقوب هوذا هذه الرجمة وهوذا العمود الذي وضعت بيني وبينك"));
arasuperobject.biblemap.put("b1_31_52", new arasuperobject(1,31,52,"شاهدة هذه الرجمة وشاهد العمود اني لا اتجاوز هذه الرجمة اليك وانك لا تتجاوز هذه الرجمة وهذا العمود اليّ للشرّ"));
arasuperobject.biblemap.put("b1_31_53", new arasuperobject(1,31,53,"اله ابراهيم وآلهة ناحور آلهة ابيهما يقضون بيننا. وحلف يعقوب بهيبة ابيه اسحق"));
arasuperobject.biblemap.put("b1_31_54", new arasuperobject(1,31,54,"وذبح يعقوب ذبيحة في الجبل ودعا اخوته ليأكلوا طعاما. فأكلوا طعاما وباتوا في الجبل"));
arasuperobject.biblemap.put("b1_31_55", new arasuperobject(1,31,55,"ثم بكّر لابان صباحا وقبّل بنيه وبناته وباركهم ومضى. ورجع لابان الى مكانه"));
arasuperobject.biblemap.put("b1_32_1", new arasuperobject(1,32,1,"واما يعقوب فمضى في طريقه ولاقاه ملائكة الله"));
arasuperobject.biblemap.put("b1_32_2", new arasuperobject(1,32,2,"وقال يعقوب اذ رآهم هذا جيش الله. فدعا اسم ذلك المكان محنايم"));
arasuperobject.biblemap.put("b1_32_3", new arasuperobject(1,32,3,"وارسل يعقوب رسلا قدامه الى عيسو اخيه الى ارض سعير بلاد ادوم"));
arasuperobject.biblemap.put("b1_32_4", new arasuperobject(1,32,4,"وامرهم قائلا هكذا تقولون لسيدي عيسو. هكذا قال عبدك يعقوب. تغربت عند لابان ولبثت الى الآن"));
arasuperobject.biblemap.put("b1_32_5", new arasuperobject(1,32,5,"وقد صار لي بقر وحمير وغنم وعبيد واماء. وارسلت لاخبر سيدي لكي اجد نعمة في عينيك"));
arasuperobject.biblemap.put("b1_32_6", new arasuperobject(1,32,6,"فرجع الرسل الى يعقوب قائلين أتينا الى اخيك الى عيسو. وهو ايضا قادم للقائك واربع مئة رجل معه"));
arasuperobject.biblemap.put("b1_32_7", new arasuperobject(1,32,7,"فخاف يعقوب جدا وضاق به الامر. فقسم القوم الذين معه والغنم والبقر والجمال الى جيشين"));
arasuperobject.biblemap.put("b1_32_8", new arasuperobject(1,32,8,"وقال ان جاء عيسو الى الجيش الواحد وضربه يكون الجيش الباقي ناجيا"));
arasuperobject.biblemap.put("b1_32_9", new arasuperobject(1,32,9,"وقال يعقوب يا اله ابي ابراهيم واله ابي اسحق الرب الذي قال لي ارجع الى ارضك والى عشيرتك فاحسن اليك"));
arasuperobject.biblemap.put("b1_32_10", new arasuperobject(1,32,10,"صغير انا عن جميع الطافك وجميع الامانة التي صنعت الى عبدك. فاني بعصاي عبرت هذا الاردن والآن قد صرت جيشين"));
arasuperobject.biblemap.put("b1_32_11", new arasuperobject(1,32,11,"نجّني من يد اخي من يد عيسو. لاني خائف منه ان يأتي ويضربني الام مع البنين"));
arasuperobject.biblemap.put("b1_32_12", new arasuperobject(1,32,12,"وانت قد قلت اني احسن اليك واجعل نسلك كرمل البحر الذي لا يعدّ للكثرة"));
arasuperobject.biblemap.put("b1_32_13", new arasuperobject(1,32,13,"وبات هناك تلك الليلة واخذ مما اتى بيده هدية لعيسو اخيه"));
arasuperobject.biblemap.put("b1_32_14", new arasuperobject(1,32,14,"مئتي عنز وعشرين تيسا مئتي نعجة وعشرين كبشا"));
arasuperobject.biblemap.put("b1_32_15", new arasuperobject(1,32,15,"ثلاثين ناقة مرضعة واولادها اربعين بقرة وعشرة ثيران عشرين اتانا وعشرة حمير"));
arasuperobject.biblemap.put("b1_32_16", new arasuperobject(1,32,16,"ودفعها الى يد عبيده قطيعا قطيعا على حدة. وقال لعبيده اجتازوا قدّامي واجعلوا فسحة بين قطيع وقطيع"));
arasuperobject.biblemap.put("b1_32_17", new arasuperobject(1,32,17,"وامر الاول قائلا اذا صادفك عيسو اخي وسألك قائلا لمن انت والى اين تذهب ولمن هذا الذي قدامك"));
arasuperobject.biblemap.put("b1_32_18", new arasuperobject(1,32,18,"تقول لعبدك يعقوب. هو هدية مرسلة لسيدي عيسو. وها هو ايضا وراءنا"));
arasuperobject.biblemap.put("b1_32_19", new arasuperobject(1,32,19,"وامر ايضا الثاني والثالث وجميع السائرين وراء القطعان قائلا بمثل هذا الكلام تكلّمون عيسو حينما تجدونه"));
arasuperobject.biblemap.put("b1_32_20", new arasuperobject(1,32,20,"وتقولون هوذا عبدك يعقوب ايضا وراءنا. لانه قال استعطف وجهه بالهدية السائرة امامي وبعد ذلك انظر وجهه. عسى ان يرفع وجهي"));
arasuperobject.biblemap.put("b1_32_21", new arasuperobject(1,32,21,"فاجتازت الهدية قدامه. واما هو فبات تلك الليلة في المحلّة"));
arasuperobject.biblemap.put("b1_32_22", new arasuperobject(1,32,22,"ثم قام في تلك الليلة واخذ امرأتيه وجاريتيه واولاده الاحد عشر وعبر مخاضة يبّوق"));
arasuperobject.biblemap.put("b1_32_23", new arasuperobject(1,32,23,"اخذهم واجازهم الوادي واجاز ما كان له"));
arasuperobject.biblemap.put("b1_32_24", new arasuperobject(1,32,24,"فبقي يعقوب وحده. وصارعه انسان حتى طلوع الفجر"));
arasuperobject.biblemap.put("b1_32_25", new arasuperobject(1,32,25,"ولما رأى انه لا يقدر عليه ضرب حقّ فخذه. فانخلع حقّ فخذ يعقوب في مصارعته معه"));
arasuperobject.biblemap.put("b1_32_26", new arasuperobject(1,32,26,"وقال اطلقني لانه قد طلع الفجر. فقال لا اطلقك ان لم تباركني"));
arasuperobject.biblemap.put("b1_32_27", new arasuperobject(1,32,27,"فقال له ما اسمك. فقال يعقوب"));
arasuperobject.biblemap.put("b1_32_28", new arasuperobject(1,32,28,"فقال لا يدعى اسمك في ما بعد يعقوب بل اسرائيل. لانك جاهدت مع الله والناس وقدرت"));
arasuperobject.biblemap.put("b1_32_29", new arasuperobject(1,32,29,"وسأل يعقوب وقال اخبرني باسمك. فقال لماذا تسأل عن اسمي. وباركه هناك"));
arasuperobject.biblemap.put("b1_32_30", new arasuperobject(1,32,30,"فدعا يعقوب اسم المكان فنيئيل. قائلا لاني نظرت الله وجها لوجه ونجّيت نفسي"));
arasuperobject.biblemap.put("b1_32_31", new arasuperobject(1,32,31,"واشرقت له الشمس اذ عبر فنوئيل وهو يخمع على فخذه"));
arasuperobject.biblemap.put("b1_32_32", new arasuperobject(1,32,32,"لذلك لا يأكل بنو اسرائيل عرق النّسا الذي على حقّ الفخذ الى هذا اليوم. لانه ضرب حقّ فخذ يعقوب على عرق النّسا"));
arasuperobject.biblemap.put("b1_33_1", new arasuperobject(1,33,1,"ورفع يعقوب عينيه ونظر واذا عيسو مقبل ومعه اربع مئة رجل. فقسم الاولاد على ليئة وعلى راحيل وعلى الجاريتين"));
arasuperobject.biblemap.put("b1_33_2", new arasuperobject(1,33,2,"ووضع الجاريتين واولادهما اولا وليئة واولادها وراءهم وراحيل ويوسف اخيرا"));
arasuperobject.biblemap.put("b1_33_3", new arasuperobject(1,33,3,"واما هو فاجتاز قدامهم وسجد الى الارض سبع مرات حتى اقترب الى اخيه"));
arasuperobject.biblemap.put("b1_33_4", new arasuperobject(1,33,4,"فركض عيسو للقائه وعانقه ووقع على عنقه وقبّله. وبكيا"));
arasuperobject.biblemap.put("b1_33_5", new arasuperobject(1,33,5,"ثم رفع عينيه وابصر النساء والاولاد وقال ما هؤلاء منك. فقال الاولاد الذين انعم الله بهم على عبدك"));
arasuperobject.biblemap.put("b1_33_6", new arasuperobject(1,33,6,"فاقتربت الجاريتان هما واولادهما وسجدتا"));
arasuperobject.biblemap.put("b1_33_7", new arasuperobject(1,33,7,"ثم اقتربت ليئة ايضا واولادها وسجدوا. وبعد ذلك اقترب يوسف وراحيل وسجدا"));
arasuperobject.biblemap.put("b1_33_8", new arasuperobject(1,33,8,"فقال ماذا منك كل هذا الجيش الذي صادفته. فقال لاجد نعمة في عيني سيدي"));
arasuperobject.biblemap.put("b1_33_9", new arasuperobject(1,33,9,"فقال عيسو لي كثير. يا اخي ليكن لك الذي لك"));
arasuperobject.biblemap.put("b1_33_10", new arasuperobject(1,33,10,"فقال يعقوب لا. ان وجدت نعمة في عينيك تاخذ هديتي من يدي. لاني رأيت وجهك كما يرى وجه الله فرضيت عليّ"));
arasuperobject.biblemap.put("b1_33_11", new arasuperobject(1,33,11,"خذ بركتي التي أتي بها اليك. لان الله قد انعم عليّ ولي كل شيء. والحّ عليه فاخذ"));
arasuperobject.biblemap.put("b1_33_12", new arasuperobject(1,33,12,"ثم قال لنرحل ونذهب واذهب انا قدامك"));
arasuperobject.biblemap.put("b1_33_13", new arasuperobject(1,33,13,"فقال له سيدي عالم ان الاولاد رخصة والغنم والبقر التي عندي مرضعة. فان استكدّوها يوما واحدا ماتت كل الغنم"));
arasuperobject.biblemap.put("b1_33_14", new arasuperobject(1,33,14,"ليجتز سيدي قدام عبده وانا استاق على مهلي في اثر الاملاك التي قدامي وفي اثر الاولاد حتى اجيء الى سيدي الى سعير"));
arasuperobject.biblemap.put("b1_33_15", new arasuperobject(1,33,15,"فقال عيسو اترك عندك من القوم الذين معي. فقال لماذا. دعني اجد نعمة في عيني سيدي"));
arasuperobject.biblemap.put("b1_33_16", new arasuperobject(1,33,16,"فرجع عيسو ذلك اليوم في طريقه الى سعير"));
arasuperobject.biblemap.put("b1_33_17", new arasuperobject(1,33,17,"واما يعقوب فارتحل الى سكّوت. وبنى لنفسه بيتا وصنع لمواشيه مظلات. لذلك دعا اسم المكان سكّوت"));
arasuperobject.biblemap.put("b1_33_18", new arasuperobject(1,33,18,"ثم اتى يعقوب سالما الى مدينة شكيم التي في ارض كنعان. حين جاء من فدّان ارام. ونزل امام المدينة"));
arasuperobject.biblemap.put("b1_33_19", new arasuperobject(1,33,19,"وابتاع قطعة الحقل التي نصب فيها خيمته من يد بني حمور ابي شكيم بمئة قسيطة"));
arasuperobject.biblemap.put("b1_33_20", new arasuperobject(1,33,20,"واقام هناك مذبحا ودعاه ايل اله اسرائيل"));
arasuperobject.biblemap.put("b1_34_1", new arasuperobject(1,34,1,"وخرجت دينة ابنة ليئة التي ولدتها ليعقوب لتنظر بنات الارض"));
arasuperobject.biblemap.put("b1_34_2", new arasuperobject(1,34,2,"فرآها شكيم ابن حمور الحوّي رئيس الارض واخذها واضطجع معها واذلّها"));
arasuperobject.biblemap.put("b1_34_3", new arasuperobject(1,34,3,"وتعلقت نفسه بدينة ابنة يعقوب واحب الفتاة ولاطف الفتاة"));
arasuperobject.biblemap.put("b1_34_4", new arasuperobject(1,34,4,"فكلم شكيم حمور اباه قائلا خذ لي هذه الصبية زوجة"));
arasuperobject.biblemap.put("b1_34_5", new arasuperobject(1,34,5,"وسمع يعقوب انه نجّس دينة ابنته. واما بنوه فكانوا مع مواشيه في الحقل. فسكت يعقوب حتى جاءوا"));
arasuperobject.biblemap.put("b1_34_6", new arasuperobject(1,34,6,"فخرج حمور ابو شكيم الى يعقوب ليتكلم معه"));
arasuperobject.biblemap.put("b1_34_7", new arasuperobject(1,34,7,"واتى بنو يعقوب من الحقل حين سمعوا. وغضب الرجال واغتاظوا جدا لانه صنع قباحة في اسرائيل بمضاجعة ابنة يعقوب. وهكذا لا يصنع"));
arasuperobject.biblemap.put("b1_34_8", new arasuperobject(1,34,8,"وتكلم حمور معهم قائلا شكيم ابني قد تعلّقت نفسه بابنتكم. اعطوه اياها زوجة"));
arasuperobject.biblemap.put("b1_34_9", new arasuperobject(1,34,9,"وصاهرونا. تعطوننا بناتكم وتأخذون لكم بناتنا"));
arasuperobject.biblemap.put("b1_34_10", new arasuperobject(1,34,10,"وتسكنون معنا وتكون الارض قدامكم. اسكنوا واتجروا فيها وتملّكوا بها"));
arasuperobject.biblemap.put("b1_34_11", new arasuperobject(1,34,11,"ثم قال شكيم لابيها ولاخوتها دعوني اجد نعمة في اعينكم. فالذي تقولون لي اعطي"));
arasuperobject.biblemap.put("b1_34_12", new arasuperobject(1,34,12,"كثّروا عليّ جدا مهّرا وعطية. فاعطي كما تقولون لي. واعطوني الفتاة زوجة"));
arasuperobject.biblemap.put("b1_34_13", new arasuperobject(1,34,13,"فاجاب بنو يعقوب شكيم وحمور اباه بمكر وتكلموا. لانه كان قد نجّس دينة اختهم"));
arasuperobject.biblemap.put("b1_34_14", new arasuperobject(1,34,14,"فقالوا لهما لا نستطيع ان نفعل هذا الامر ان نعطي اختنا لرجل اغلف. لانه عار لنا"));
arasuperobject.biblemap.put("b1_34_15", new arasuperobject(1,34,15,"غير اننا بهذا نواتيكم. ان صرتم مثلنا بختنكم كل ذكر"));
arasuperobject.biblemap.put("b1_34_16", new arasuperobject(1,34,16,"نعطيكم بناتنا ونأخذ لنا بناتكم ونسكن معكم ونصير شعبا واحدا"));
arasuperobject.biblemap.put("b1_34_17", new arasuperobject(1,34,17,"وان لم تسمعوا لنا ان تختتنوا نأخذ ابنتنا ونمضي"));
arasuperobject.biblemap.put("b1_34_18", new arasuperobject(1,34,18,"فحسن كلامهم في عيني حمور وفي عيني شكيم بن حمور"));
arasuperobject.biblemap.put("b1_34_19", new arasuperobject(1,34,19,"ولم يتأخر الغلام ان يفعل الامر. لانه كان مسرورا بابنة يعقوب. وكان اكرم جميع بيت ابيه"));
arasuperobject.biblemap.put("b1_34_20", new arasuperobject(1,34,20,"فاتى حمور وشكيم ابنه الى باب مدينتهما وكلما اهل مدينتهما قائلين"));
arasuperobject.biblemap.put("b1_34_21", new arasuperobject(1,34,21,"هؤلاء القوم مسالمون لنا. فليسكنوا في الارض ويتجروا فيها. وهوذا الارض واسعة الطرفين امامهم. نأخذ لنا بناتهم زوجات ونعطيهم بناتنا"));
arasuperobject.biblemap.put("b1_34_22", new arasuperobject(1,34,22,"غير انه بهذا فقط يواتينا القوم على السكن معنا لنصير شعبا واحدا. بختننا كل ذكر كما هم مختونون"));
arasuperobject.biblemap.put("b1_34_23", new arasuperobject(1,34,23,"ألا تكون مواشيهم ومقتناهم وكل بهائمهم لنا. نواتيهم فقط فيسكنون معنا"));
arasuperobject.biblemap.put("b1_34_24", new arasuperobject(1,34,24,"فسمع لحمور وشكيم ابنه جميع الخارجين من باب المدينة. واختتن كل ذكر. كل الخارجين من باب المدينة"));
arasuperobject.biblemap.put("b1_34_25", new arasuperobject(1,34,25,"فحدث في اليوم الثالث اذ كانوا متوجعين ان ابني يعقوب شمعون ولاوي اخوي دينة اخذا كل واحد سيفه وأتيا على المدينة بامن وقتلا كل ذكر"));
arasuperobject.biblemap.put("b1_34_26", new arasuperobject(1,34,26,"وقتلا حمور وشكيم ابنه بحد السيف. واخذا دينة من بيت شكيم وخرجا"));
arasuperobject.biblemap.put("b1_34_27", new arasuperobject(1,34,27,"ثم اتى بنو يعقوب على القتلى ونهبوا المدينة. لانهم نجّسوا اختهم"));
arasuperobject.biblemap.put("b1_34_28", new arasuperobject(1,34,28,"غنمهم وبقرهم وحميرهم وكل ما في المدينة وما في الحقل اخذوه"));
arasuperobject.biblemap.put("b1_34_29", new arasuperobject(1,34,29,"وسبوا ونهبوا كل ثروتهم وكل اطفالهم ونساءهم وكل ما في البيوت"));
arasuperobject.biblemap.put("b1_34_30", new arasuperobject(1,34,30,"فقال يعقوب لشمعون ولاوي كدّرتماني بتكريهكما اياي عند سكّان الارض الكنعانيين والفرزّيين وانا نفر قليل. فيجتمعون عليّ ويضربونني فأبيد انا وبيتي"));
arasuperobject.biblemap.put("b1_34_31", new arasuperobject(1,34,31,"فقالا أنظير زانية يفعل باختنا"));
arasuperobject.biblemap.put("b1_35_1", new arasuperobject(1,35,1,"ثم قال الله ليعقوب قم اصعد الى بيت ايل وأقم هناك واصنع هناك مذبحا لله الذي ظهر لك حين هربت من وجه عيسو اخيك"));
arasuperobject.biblemap.put("b1_35_2", new arasuperobject(1,35,2,"فقال يعقوب لبيته ولكل من كان معه اعزلوا الآلهة الغريبة التي بينكم وتطهروا وابدلوا ثيابكم"));
arasuperobject.biblemap.put("b1_35_3", new arasuperobject(1,35,3,"ولنقم ونصعد الى بيت ايل. فاصنع هناك مذبحا لله الذي استجاب لي في يوم ضيقتي وكان معي في الطريق الذي ذهبت فيه"));
arasuperobject.biblemap.put("b1_35_4", new arasuperobject(1,35,4,"فاعطوا يعقوب كل الآلهة الغريبة التي في ايديهم والاقراط التي في آذانهم. فطمرها يعقوب تحت البطمة التي عند شكيم"));
arasuperobject.biblemap.put("b1_35_5", new arasuperobject(1,35,5,"ثم رحلوا. وكان خوف الله على المدن التي حولهم. فلم يسعوا وراء بني يعقوب"));
arasuperobject.biblemap.put("b1_35_6", new arasuperobject(1,35,6,"فاتى يعقوب الى لوز التي في ارض كنعان وهي بيت ايل. هو وجميع القوم الذين معه"));
arasuperobject.biblemap.put("b1_35_7", new arasuperobject(1,35,7,"وبنى هناك مذبحا ودعا المكان ايل بيت ايل. لانه هناك ظهر له الله حين هرب من وجه اخيه"));
arasuperobject.biblemap.put("b1_35_8", new arasuperobject(1,35,8,"وماتت دبورة مرضعة رفقة ودفنت تحت بيت ايل تحت البلوطة. فدعا اسمها ألون باكوت"));
arasuperobject.biblemap.put("b1_35_9", new arasuperobject(1,35,9,"وظهر الله ليعقوب ايضا حين جاء من فدّان ارام وباركه"));
arasuperobject.biblemap.put("b1_35_10", new arasuperobject(1,35,10,"وقال له الله اسمك يعقوب. لا يدعى اسمك فيما بعد يعقوب بل يكون اسمك اسرائيل. فدعا اسمه اسرائيل"));
arasuperobject.biblemap.put("b1_35_11", new arasuperobject(1,35,11,"وقال له الله انا الله القدير. أثمر واكثر. امة وجماعة امم تكون منك. وملوك سيخرجون من صلبك"));
arasuperobject.biblemap.put("b1_35_12", new arasuperobject(1,35,12,"والارض التي اعطيت ابراهيم واسحق لك اعطيها. ولنسلك من بعدك اعطي الارض"));
arasuperobject.biblemap.put("b1_35_13", new arasuperobject(1,35,13,"ثم صعد الله عنه في المكان الذي فيه تكلم معه"));
arasuperobject.biblemap.put("b1_35_14", new arasuperobject(1,35,14,"فنصب يعقوب عمودا في المكان الذي فيه تكلم معه عمودا من حجر. وسكب عليه سكيبا وصب عليه زيتا"));
arasuperobject.biblemap.put("b1_35_15", new arasuperobject(1,35,15,"ودعا يعقوب اسم المكان الذي فيه تكلم الله معه بيت ايل"));
arasuperobject.biblemap.put("b1_35_16", new arasuperobject(1,35,16,"ثم رحلوا من بيت ايل. ولما كان مسافة من الارض بعد حتى يأتوا الى افراتة ولدت راحيل وتعسّرت ولادتها"));
arasuperobject.biblemap.put("b1_35_17", new arasuperobject(1,35,17,"وحدث حين تعسّرت ولادتها ان القابلة قالت لها لا تخافي لان هذا ايضا ابن لك"));
arasuperobject.biblemap.put("b1_35_18", new arasuperobject(1,35,18,"وكان عند خروج نفسها لانها ماتت انها دعت اسمه بن أوني. واما ابوه فدعاه بنيامين"));
arasuperobject.biblemap.put("b1_35_19", new arasuperobject(1,35,19,"فماتت راحيل ودفنت في طريق افراتة التي هي بيت لحم"));
arasuperobject.biblemap.put("b1_35_20", new arasuperobject(1,35,20,"فنصب يعقوب عمودا على قبرها. وهو عمود قبر راحيل الى اليوم"));
arasuperobject.biblemap.put("b1_35_21", new arasuperobject(1,35,21,"ثم رحل اسرائيل ونصب خيمته وراء مجدل عدر"));
arasuperobject.biblemap.put("b1_35_22", new arasuperobject(1,35,22,"وحدث اذ كان اسرائيل ساكنا في تلك الارض ان رأوبين ذهب واضطجع مع بلهة سرّية ابيه. وسمع اسرائيل وكان بنو يعقوب اثني عشر"));
arasuperobject.biblemap.put("b1_35_23", new arasuperobject(1,35,23,"بنو ليئة رأوبين بكر يعقوب وشمعون ولاوي ويهوذا ويسّاكر وزبولون"));
arasuperobject.biblemap.put("b1_35_24", new arasuperobject(1,35,24,"وابنا راحيل يوسف وبنيامين"));
arasuperobject.biblemap.put("b1_35_25", new arasuperobject(1,35,25,"وابنا بلهة جارية راحيل دان ونفتالي"));
arasuperobject.biblemap.put("b1_35_26", new arasuperobject(1,35,26,"وابنا زلفة جارية ليئة جاد واشير. هؤلاء بنو يعقوب الذين ولدوا له في فدّان ارام"));
arasuperobject.biblemap.put("b1_35_27", new arasuperobject(1,35,27,"وجاء يعقوب الى اسحق ابيه الى ممرا قرية اربع التي هي حبرون. حيث تغرب ابراهيم واسحق"));
arasuperobject.biblemap.put("b1_35_28", new arasuperobject(1,35,28,"وكانت ايام اسحق مئة وثمانين سنة"));
arasuperobject.biblemap.put("b1_35_29", new arasuperobject(1,35,29,"فاسلم اسحق روحه ومات وانضمّ الى قومه شيخا وشبعان اياما. ودفنه عيسو ويعقوب ابناه"));
arasuperobject.biblemap.put("b1_36_1", new arasuperobject(1,36,1,"وهذه مواليد عيسو الذي هو ادوم"));
arasuperobject.biblemap.put("b1_36_2", new arasuperobject(1,36,2,"اخذ عيسو نساءه من بنات كنعان. عدا بنت إيلون الحثّي وأهوليبامة بنت عنى بنت صبعون الحوّي"));
arasuperobject.biblemap.put("b1_36_3", new arasuperobject(1,36,3,"وبسمة بنت اسماعيل اخت نبايوت"));
arasuperobject.biblemap.put("b1_36_4", new arasuperobject(1,36,4,"فولدت عدا لعيسو أليفاز. وولدت بسمة رعوئيل"));
arasuperobject.biblemap.put("b1_36_5", new arasuperobject(1,36,5,"وولدت أهوليبامة يعوش ويعلام وقورح. هؤلاء بنو عيسو الذين ولدوا له في ارض كنعان"));
arasuperobject.biblemap.put("b1_36_6", new arasuperobject(1,36,6,"ثم اخذ عيسو نساءه وبنيه وبناته وجميع نفوس بيته ومواشيه وكل بهائمه وكل مقتناه الذي اقتنى في ارض كنعان ومضى الى ارض اخرى من وجه يعقوب اخيه"));
arasuperobject.biblemap.put("b1_36_7", new arasuperobject(1,36,7,"لان املاكهما كانت كثيرة على السكنى معا ولم تستطع ارض غربتهما ان تحملهما من اجل مواشيهما"));
arasuperobject.biblemap.put("b1_36_8", new arasuperobject(1,36,8,"فسكن عيسو في جبل سعير. وعيسو هو ادوم"));
arasuperobject.biblemap.put("b1_36_9", new arasuperobject(1,36,9,"وهذه مواليد عيسو ابي ادوم في جبل سعير"));
arasuperobject.biblemap.put("b1_36_10", new arasuperobject(1,36,10,"هذه اسماء بني عيسو. أليفاز ابن عدا امرأة عيسو ورعوئيل ابن بسمة امرأة عيسو"));
arasuperobject.biblemap.put("b1_36_11", new arasuperobject(1,36,11,"وكان بنو أليفاز تيمان واومار وصفوا وجعثام وقناز"));
arasuperobject.biblemap.put("b1_36_12", new arasuperobject(1,36,12,"وكانت تمناع سرية لاليفاز بن عيسو فولدت لاليفاز عماليق. هؤلاء بنو عدا امرأة عيسو"));
arasuperobject.biblemap.put("b1_36_13", new arasuperobject(1,36,13,"وهؤلاء بنو رعوئيل. نحث وزارح وشمّة ومزّة. هؤلاء كانوا بني بسمة امرأة عيسو"));
arasuperobject.biblemap.put("b1_36_14", new arasuperobject(1,36,14,"وهؤلاء كانوا بني أهوليبامة بنت عنى بنت صبعون امرأة عيسو. ولدت لعيسو يعوش ويعلام وقورح"));
arasuperobject.biblemap.put("b1_36_15", new arasuperobject(1,36,15,"هؤلاء امراء بني عيسو. بنو اليفاز بكر عيسو امير تيمان وامير اومار وامير صفو وامير قناز"));
arasuperobject.biblemap.put("b1_36_16", new arasuperobject(1,36,16,"وامير قورح وامير جعثام وامير عماليق. هؤلاء امراء أليفاز في ارض ادوم. هؤلاء بنو عدا"));
arasuperobject.biblemap.put("b1_36_17", new arasuperobject(1,36,17,"وهؤلاء بنو رعوئيل بن عيسو. امير نحث وامير زارح وامير شمّة وامير مزّة. هؤلاء امراء رعوئيل في ارض ادوم. هؤلاء بنو بسمة امرأة عيسو"));
arasuperobject.biblemap.put("b1_36_18", new arasuperobject(1,36,18,"وهؤلاء بنو أهوليبامة امرأة عيسو. امير يعوش وامير يعلام وامير قورح. هؤلاء امراء أهوليبامة بنت عنى امرأة عيسو"));
arasuperobject.biblemap.put("b1_36_19", new arasuperobject(1,36,19,"هؤلاء بنو عيسو الذي هو ادوم وهؤلاء امراؤهم"));
arasuperobject.biblemap.put("b1_36_20", new arasuperobject(1,36,20,"هؤلاء بنو سعير الحوريّ سكان الارض. لوطان وشوبان وصبعون وعنى"));
arasuperobject.biblemap.put("b1_36_21", new arasuperobject(1,36,21,"وديشون وإيصر وديشان. هؤلاء امراء الحوريين بنو سعير في ارض ادوم"));
arasuperobject.biblemap.put("b1_36_22", new arasuperobject(1,36,22,"وكان ابنا لوطان حوري وهيمام. وكانت تمناع اخت لوطان"));
arasuperobject.biblemap.put("b1_36_23", new arasuperobject(1,36,23,"وهؤلاء بنو شوبال علوان ومناحة وعيبال وشفو وأونام"));
arasuperobject.biblemap.put("b1_36_24", new arasuperobject(1,36,24,"وهذان ابنا صبعون أيّة وعنى. هذا هو عنى الذي وجد الحمائم في البرية اذ كان يرعى حمير صبعون ابيه"));
arasuperobject.biblemap.put("b1_36_25", new arasuperobject(1,36,25,"وهذا ابن عنى ديشون. وأهوليبامة هي بنت عنى"));
arasuperobject.biblemap.put("b1_36_26", new arasuperobject(1,36,26,"وهؤلاء بنو ديشان حمدان واشبان ويثران وكران"));
arasuperobject.biblemap.put("b1_36_27", new arasuperobject(1,36,27,"هؤلاء بنو إيصر بلهان وزعوان وعقان"));
arasuperobject.biblemap.put("b1_36_28", new arasuperobject(1,36,28,"هذان ابنا ديشان عوص وأران"));
arasuperobject.biblemap.put("b1_36_29", new arasuperobject(1,36,29,"هؤلاء امراء الحوريين. امير لوطان وامير شوبال وامير صبعون وامير عنى"));
arasuperobject.biblemap.put("b1_36_30", new arasuperobject(1,36,30,"وامير ديشون وامير إيصر وامير ديشان. هؤلاء امراء الحوريين بامرائهم في ارض سعير"));
arasuperobject.biblemap.put("b1_36_31", new arasuperobject(1,36,31,"وهؤلاء هم الملوك الذين ملكوا في ارض ادوم قبلما ملك ملك لبني اسرائيل"));
arasuperobject.biblemap.put("b1_36_32", new arasuperobject(1,36,32,"ملك في ادوم بالع بن بعور. وكان اسم مدينته دنهابة"));
arasuperobject.biblemap.put("b1_36_33", new arasuperobject(1,36,33,"ومات بالع فملك مكانه يوباب بن زارح من بصرة"));
arasuperobject.biblemap.put("b1_36_34", new arasuperobject(1,36,34,"ومات يوباب فملك مكانه حوشام من ارض التيماني"));
arasuperobject.biblemap.put("b1_36_35", new arasuperobject(1,36,35,"ومات حوشام فملك مكانه هداد بن بداد الذي كسر مديان في بلاد موآب. وكان اسم مدينته عويت"));
arasuperobject.biblemap.put("b1_36_36", new arasuperobject(1,36,36,"ومات هداد فملك مكانه سملة من مسريقة"));
arasuperobject.biblemap.put("b1_36_37", new arasuperobject(1,36,37,"ومات سملة فملك مكانه شاول من رحوبوت النهر"));
arasuperobject.biblemap.put("b1_36_38", new arasuperobject(1,36,38,"ومات شاول فملك مكانه بعل حانان بن عكبور"));
arasuperobject.biblemap.put("b1_36_39", new arasuperobject(1,36,39,"ومات بعل حانان بن عكبور فملك مكانه هدار. وكان اسم مدينته فاعو. واسم امرأته مهيطبئيل بنت مطرد بنت ماء ذهب"));
arasuperobject.biblemap.put("b1_36_40", new arasuperobject(1,36,40,"وهذه اسماء امراء عيسو حسب قبائلهم واماكنهم باسمائهم. امير تمناع وامير علوة وامير يتيت"));
arasuperobject.biblemap.put("b1_36_41", new arasuperobject(1,36,41,"وامير اهوليبامة وامير ايلة وامير فينون"));
arasuperobject.biblemap.put("b1_36_42", new arasuperobject(1,36,42,"وامير قناز وامير تيمان وامير مبصار"));
arasuperobject.biblemap.put("b1_36_43", new arasuperobject(1,36,43,"وامير مجديئيل وامير عيرام. هؤلاء امراء ادوم حسب مساكنهم في ارض ملكهم. هذا هو عيسو ابو ادوم"));
arasuperobject.biblemap.put("b1_37_1", new arasuperobject(1,37,1,"وسكن يعقوب في ارض غربة ابيه في ارض كنعان"));
arasuperobject.biblemap.put("b1_37_2", new arasuperobject(1,37,2,"هذه مواليد يعقوب. يوسف اذ كان ابن سبع عشرة سنة كان يرعى مع اخوته الغنم وهو غلام عند بني بلهة وبني زلفة امرأتي ابيه. واتى يوسف بنميمتهم الرديئة الى ابيهم"));
arasuperobject.biblemap.put("b1_37_3", new arasuperobject(1,37,3,"واما اسرائيل فاحب يوسف اكثر من سائر بنيه لانه ابن شيخوخته. فصنع له قميصا ملوّنا"));
arasuperobject.biblemap.put("b1_37_4", new arasuperobject(1,37,4,"فلما رأى اخوته ان اباهم احبه اكثر من جميع اخوته ابغضوه ولم يستطيعوا ان يكلموه بسلام"));
arasuperobject.biblemap.put("b1_37_5", new arasuperobject(1,37,5,"وحلم يوسف حلما واخبر اخوته. فازدادوا ايضا بغضا له"));
arasuperobject.biblemap.put("b1_37_6", new arasuperobject(1,37,6,"فقال لهم اسمعوا هذا الحلم الذي حلمت"));
arasuperobject.biblemap.put("b1_37_7", new arasuperobject(1,37,7,"فها نحن حازمون حزما في الحقل. واذا حزمتي قامت وانتصبت فاحتاطت حزمكم وسجدت لحزمتي"));
arasuperobject.biblemap.put("b1_37_8", new arasuperobject(1,37,8,"فقال له اخوته ألعلك تملك علينا ملكا ام تتسلط علينا تسلّطا. وازدادوا ايضا بغضا له من اجل احلامه ومن اجل كلامه"));
arasuperobject.biblemap.put("b1_37_9", new arasuperobject(1,37,9,"ثم حلم ايضا حلما آخر وقصّه على اخوته. فقال اني قد حلمت حلما ايضا واذا الشمس والقمر واحد عشر كوكبا ساجدة لي"));
arasuperobject.biblemap.put("b1_37_10", new arasuperobject(1,37,10,"وقصّه على ابيه وعلى اخوته. فانتهره ابوه وقال له ما هذا الحلم الذي حلمت. هل نأتي انا وامك واخوتك لنسجد لك الى الارض"));
arasuperobject.biblemap.put("b1_37_11", new arasuperobject(1,37,11,"فحسده اخوته. واما ابوه فحفظ الامر"));
arasuperobject.biblemap.put("b1_37_12", new arasuperobject(1,37,12,"ومضى اخوته ليرعوا غنم ابيهم عند شكيم"));
arasuperobject.biblemap.put("b1_37_13", new arasuperobject(1,37,13,"فقال اسرائيل ليوسف أليس اخوتك يرعون عند شكيم. تعال فارسلك اليهم. فقال له هانذا"));
arasuperobject.biblemap.put("b1_37_14", new arasuperobject(1,37,14,"فقال له اذهب انظر سلامة اخوتك وسلامة الغنم وردّ لي خبرا. فارسله من وطاء حبرون فاتى الى شكيم"));
arasuperobject.biblemap.put("b1_37_15", new arasuperobject(1,37,15,"فوجده رجل واذا هو ضال في الحقل. فساله الرجل قائلا ماذا تطلب"));
arasuperobject.biblemap.put("b1_37_16", new arasuperobject(1,37,16,"فقال انا طالب اخوتي. اخبرني اين يرعون"));
arasuperobject.biblemap.put("b1_37_17", new arasuperobject(1,37,17,"فقال الرجل قد ارتحلوا من هنا. لاني سمعتهم يقولون لنذهب الى دوثان. فذهب يوسف وراء اخوته فوجدهم في دوثان"));
arasuperobject.biblemap.put("b1_37_18", new arasuperobject(1,37,18,"فلما ابصروه من بعيد قبلما اقترب اليهم احتالوا له ليميتوه"));
arasuperobject.biblemap.put("b1_37_19", new arasuperobject(1,37,19,"فقال بعضهم لبعض هوذا هذا صاحب الاحلام قادم"));
arasuperobject.biblemap.put("b1_37_20", new arasuperobject(1,37,20,"فالآن هلم نقتله ونطرحه في احدى الآبار ونقول وحش رديء اكله. فنرى ماذا تكون احلامه"));
arasuperobject.biblemap.put("b1_37_21", new arasuperobject(1,37,21,"فسمع رأوبين وانقذه من ايديهم. وقال لا نقتله"));
arasuperobject.biblemap.put("b1_37_22", new arasuperobject(1,37,22,"وقال لهم رأوبين لا تسفكوا دما. اطرحوه في هذه البئر التي في البرية ولا تمدوا اليه يدا. لكي ينقذه من ايديهم ليرده الى ابيه"));
arasuperobject.biblemap.put("b1_37_23", new arasuperobject(1,37,23,"فكان لما جاء يوسف الى اخوته انهم خلعوا عن يوسف قميصه القميص الملوّن الذي عليه"));
arasuperobject.biblemap.put("b1_37_24", new arasuperobject(1,37,24,"واخذوه وطرحوه في البئر. واما البئر فكانت فارغة ليس فيها ماء"));
arasuperobject.biblemap.put("b1_37_25", new arasuperobject(1,37,25,"ثم جلسوا ليأكلوا طعاما. فرفعوا عيونهم ونظروا واذا قافلة اسمعيليين مقبلة من جلعاد وجمالهم حاملة كثيراء وبلسانا ولاذنا ذاهبين لينزلوا بها الى مصر"));
arasuperobject.biblemap.put("b1_37_26", new arasuperobject(1,37,26,"فقال يهوذا لاخوته ما الفائدة ان نقتل اخانا ونخفي دمه"));
arasuperobject.biblemap.put("b1_37_27", new arasuperobject(1,37,27,"تعالوا فنبيعه للاسمعيليين ولا تكن ايدينا عليه لانه اخونا ولحمنا. فسمع له اخوته"));
arasuperobject.biblemap.put("b1_37_28", new arasuperobject(1,37,28,"واجتاز رجال مديانيون تجار. فسحبوا يوسف واصعدوه من البئر وباعوا يوسف للاسمعيليين بعشرين من الفضة. فأتوا بيوسف الى مصر"));
arasuperobject.biblemap.put("b1_37_29", new arasuperobject(1,37,29,"ورجع رأوبين الى البئر واذا يوسف ليس في البئر. فمزّق ثيابه"));
arasuperobject.biblemap.put("b1_37_30", new arasuperobject(1,37,30,"ثم رجع الى اخوته وقال الولد ليس موجودا. وانا الى اين اذهب"));
arasuperobject.biblemap.put("b1_37_31", new arasuperobject(1,37,31,"فأخذوا قميص يوسف وذبحوا تيسا من المعزى وغمسوا القميص في الدم"));
arasuperobject.biblemap.put("b1_37_32", new arasuperobject(1,37,32,"وارسلوا القميص الملّون واحضروه الى ابيهم. وقالوا وجدنا هذا. حقق أقميص ابنك هو ام لا"));
arasuperobject.biblemap.put("b1_37_33", new arasuperobject(1,37,33,"فتحققه وقال قميص ابني. وحش رديء اكله. افترس يوسف افتراسا"));
arasuperobject.biblemap.put("b1_37_34", new arasuperobject(1,37,34,"فمزّق يعقوب ثيابه ووضع مسحا على حقويه وناح على ابنه اياما كثيرة"));
arasuperobject.biblemap.put("b1_37_35", new arasuperobject(1,37,35,"فقام جميع بنيه وجميع بناته ليعزوه. فابى ان يتعزى وقال اني انزل الى ابني نائحا الى الهاوية. وبكى عليه ابوه"));
arasuperobject.biblemap.put("b1_37_36", new arasuperobject(1,37,36,"واما المديانيون فباعوه في مصر لفوطيفار خصي فرعون رئيس الشّرط"));
arasuperobject.biblemap.put("b1_38_1", new arasuperobject(1,38,1,"وحدث في ذلك الزمان ان يهوذا نزل من عند اخوته ومال الى رجل عدلاميّ اسمه حيرة"));
arasuperobject.biblemap.put("b1_38_2", new arasuperobject(1,38,2,"ونظر يهوذا هناك ابنة رجل كنعاني اسمه شوع. فاخذها ودخل عليها"));
arasuperobject.biblemap.put("b1_38_3", new arasuperobject(1,38,3,"فحبلت وولدت ابنا ودعا اسمه عيرا"));
arasuperobject.biblemap.put("b1_38_4", new arasuperobject(1,38,4,"ثم حبلت ايضا وولدت ابنا ودعت اسمه أونان"));
arasuperobject.biblemap.put("b1_38_5", new arasuperobject(1,38,5,"ثم عادت فولدت ايضا ابنا ودعت اسمه شيلة. وكان في كزيب حين ولدته"));
arasuperobject.biblemap.put("b1_38_6", new arasuperobject(1,38,6,"واخذ يهوذا زوجة لعير بكره اسمها ثامار"));
arasuperobject.biblemap.put("b1_38_7", new arasuperobject(1,38,7,"وكان عير بكر يهوذا شريرا في عيني الرب. فأماته الرب"));
arasuperobject.biblemap.put("b1_38_8", new arasuperobject(1,38,8,"فقال يهوذا لأونان ادخل على امرأة اخيك وتزوج بها واقم نسلا لاخيك"));
arasuperobject.biblemap.put("b1_38_9", new arasuperobject(1,38,9,"فعلم أونان ان النسل لا يكون له. فكان اذ دخل على امرأة اخيه انه افسد على الارض لكيلا يعطي نسلا لاخيه"));
arasuperobject.biblemap.put("b1_38_10", new arasuperobject(1,38,10,"فقبح في عيني الرب ما فعله. فاماته ايضا"));
arasuperobject.biblemap.put("b1_38_11", new arasuperobject(1,38,11,"فقال يهوذا لثامار كنته اقعدي ارملة في بيت ابيك حتى يكبر شيلة ابني. لانه قال لعله يموت هو ايضا كاخويه. فمضت ثامار وقعدت في بيت ابيها"));
arasuperobject.biblemap.put("b1_38_12", new arasuperobject(1,38,12,"ولما طال الزمان ماتت ابنة شوع امرأة يهوذا. ثم تعزّى يهوذا فصعد الى جزاز غنمه الى تمنة هو وحيرة صاحبه العدلامي"));
arasuperobject.biblemap.put("b1_38_13", new arasuperobject(1,38,13,"فاخبرت ثامار وقيل لها هوذا حموك صاعد الى تمنة ليجزّ غنمه"));
arasuperobject.biblemap.put("b1_38_14", new arasuperobject(1,38,14,"فخلعت عنها ثياب ترملها وتغطت ببرقع وتلفّفت وجلست في مدخل عينايم التي على طريق تمنة. لانها رأت ان شيلة قد كبر وهي لم تعط له زوجة"));
arasuperobject.biblemap.put("b1_38_15", new arasuperobject(1,38,15,"فنظرها يهوذا وحسبها زانية. لانها كانت قد غطت وجهها"));
arasuperobject.biblemap.put("b1_38_16", new arasuperobject(1,38,16,"فمال اليها على الطريق وقال هاتي ادخل عليك. لانه لم يعلم انها كنته. فقالت ماذا تعطيني لكي تدخل عليّ"));
arasuperobject.biblemap.put("b1_38_17", new arasuperobject(1,38,17,"فقال اني ارسل جدي معزى من الغنم. فقالت هل تعطيني رهنا حتى ترسله"));
arasuperobject.biblemap.put("b1_38_18", new arasuperobject(1,38,18,"فقال ما الرهن الذي اعطيك. فقالت خاتمك وعصابتك وعصاك التي في يدك. فاعطاها ودخل عليها. فحبلت منه"));
arasuperobject.biblemap.put("b1_38_19", new arasuperobject(1,38,19,"ثم قامت ومضت وخلعت عنها برقعها ولبست ثياب ترملها"));
arasuperobject.biblemap.put("b1_38_20", new arasuperobject(1,38,20,"فارسل يهوذا جدي المعزى بيد صاحبه العدلامي ليأخذ الرهن من يد المرأة. فلم يجدها"));
arasuperobject.biblemap.put("b1_38_21", new arasuperobject(1,38,21,"فسأل اهل مكانها قائلا اين الزانية التي كانت في عينايم على الطريق. فقالوا لم تكن ههنا زانية"));
arasuperobject.biblemap.put("b1_38_22", new arasuperobject(1,38,22,"فرجع الى يهوذا وقال لم اجدها. واهل المكان ايضا قالوا لم تكن ههنا زانية"));
arasuperobject.biblemap.put("b1_38_23", new arasuperobject(1,38,23,"فقال يهوذا لتاخذ لنفسها لئلا نصير اهانة. اني قد ارسلت هذا الجدي وانت لم تجدها"));
arasuperobject.biblemap.put("b1_38_24", new arasuperobject(1,38,24,"ولما كان نحو ثلاثة اشهر أخبر يهوذا وقيل له قد زنت ثامار كنتك. وها هي حبلى ايضا من الزنى. فقال يهوذا اخرجوها فتحرق"));
arasuperobject.biblemap.put("b1_38_25", new arasuperobject(1,38,25,"اما هي فلما اخرجت ارسلت الى حميها قائلة من الرجل الذي هذه له انا حبلى. وقالت حقّق لمن الخاتم والعصابة والعصا هذه"));
arasuperobject.biblemap.put("b1_38_26", new arasuperobject(1,38,26,"فتحققها يهوذا وقال هي ابرّ مني لاني لم أعطها لشيلة ابني. فلم يعد يعرفها ايضا"));
arasuperobject.biblemap.put("b1_38_27", new arasuperobject(1,38,27,"وفي وقت ولادتها اذا في بطنها توأمان"));
arasuperobject.biblemap.put("b1_38_28", new arasuperobject(1,38,28,"وكان في ولادتها ان احدهما اخرج يدا فاخذت القابلة وربطت على يده قرمزا قائلة هذا خرج اولا"));
arasuperobject.biblemap.put("b1_38_29", new arasuperobject(1,38,29,"ولكن حين ردّ يده اذ اخوه قد خرج. فقالت لماذا اقتحمت. عليك اقتحام. فدعي اسمه فارص"));
arasuperobject.biblemap.put("b1_38_30", new arasuperobject(1,38,30,"وبعد ذلك خرج اخوه الذي على يده القرمز. فدعي اسمه زارح"));
arasuperobject.biblemap.put("b1_39_1", new arasuperobject(1,39,1,"واما يوسف فانزل الى مصر واشتراه فوطيفار خصي فرعون رئيس الشرط رجل مصري من يد الاسمعيليين الذين انزلوه الى هناك"));
arasuperobject.biblemap.put("b1_39_2", new arasuperobject(1,39,2,"وكان الرب مع يوسف فكان رجلا ناجحا. وكان في بيت سيده المصري"));
arasuperobject.biblemap.put("b1_39_3", new arasuperobject(1,39,3,"ورأى سيده ان الرب معه وان كل ما يصنع كان الرب ينحجه بيده"));
arasuperobject.biblemap.put("b1_39_4", new arasuperobject(1,39,4,"فوجد يوسف نعمة في عينيه وخدمه. فوكله على بيته ودفع الى يده كل ما كان له"));
arasuperobject.biblemap.put("b1_39_5", new arasuperobject(1,39,5,"وكان من حين وكله على بيته وعلى كل ما كان له ان الرب بارك بيت المصري بسبب يوسف. وكانت بركة الرب على كل ما كان له في البيت وفي الحقل"));
arasuperobject.biblemap.put("b1_39_6", new arasuperobject(1,39,6,"فترك كل ما كان له في يد يوسف. ولم يكن معه يعرف شيئا الا الخبز الذي يأكل. وكان يوسف حسن الصورة وحسن المنظر"));
arasuperobject.biblemap.put("b1_39_7", new arasuperobject(1,39,7,"وحدث بعد هذه الامور ان امرأة سيده رفعت عينيها الى يوسف وقالت اضطجع معي"));
arasuperobject.biblemap.put("b1_39_8", new arasuperobject(1,39,8,"فأبى وقال لامرأة سيده هوذا سيدي لا يعرف معي ما في البيت وكل ما له قد دفعه الى يدي"));
arasuperobject.biblemap.put("b1_39_9", new arasuperobject(1,39,9,"ليس هو في هذا البيت اعظم مني. ولم يمسك عني شيئا غيرك لانك امرأته. فكيف اصنع هذا الشر العظيم واخطئ الى الله"));
arasuperobject.biblemap.put("b1_39_10", new arasuperobject(1,39,10,"وكان اذ كلّمت يوسف يوما فيوما انه لم يسمع لها ان يضطجع بجانبها ليكون معها"));
arasuperobject.biblemap.put("b1_39_11", new arasuperobject(1,39,11,"ثم حدث نحو هذا الوقت انه دخل البيت ليعمل عمله ولم يكن انسان من اهل البيت هناك في البيت"));
arasuperobject.biblemap.put("b1_39_12", new arasuperobject(1,39,12,"فامسكته بثوبه قائلة اضطجع معي. فترك ثوبه في يدها وهرب وخرج الى خارج"));
arasuperobject.biblemap.put("b1_39_13", new arasuperobject(1,39,13,"وكان لما رأت انه ترك ثوبه في يدها وهرب الى خارج"));
arasuperobject.biblemap.put("b1_39_14", new arasuperobject(1,39,14,"انها نادت اهل بيتها وكلمتهم قائلة انظروا. قد جاء الينا برجل عبراني ليداعبنا. دخل اليّ ليضطجع معي فصرخت بصوت عظيم"));
arasuperobject.biblemap.put("b1_39_15", new arasuperobject(1,39,15,"وكان لما سمع اني رفعت صوتي وصرخت انه ترك ثوبه بجانبي وهرب وخرج الى خارج"));
arasuperobject.biblemap.put("b1_39_16", new arasuperobject(1,39,16,"فوضعت ثوبه بجانبها حتى جاء سيده الى بيته"));
arasuperobject.biblemap.put("b1_39_17", new arasuperobject(1,39,17,"فكلمته بمثل هذا الكلام قائلة دخل اليّ العبد العبراني الذي جئت به الينا ليداعبني"));
arasuperobject.biblemap.put("b1_39_18", new arasuperobject(1,39,18,"وكان لما رفعت صوتي وصرخت انه ترك ثوبه بجانبي وهرب الى خارج"));
arasuperobject.biblemap.put("b1_39_19", new arasuperobject(1,39,19,"فكان لما سمع سيده كلام امرأته الذي كلمته به قائلة بحسب هذا الكلام صنع بي عبدك ان غضبه حمي"));
arasuperobject.biblemap.put("b1_39_20", new arasuperobject(1,39,20,"فأخذ يوسف سيده ووضعه في بيت السجن المكان الذي كان اسرى الملك محبوسين فيه. وكان هناك في بيت السجن"));
arasuperobject.biblemap.put("b1_39_21", new arasuperobject(1,39,21,"ولكن الرب كان مع يوسف وبسط اليه لطفا وجعل نعمة له في عيني رئيس بيت السجن"));
arasuperobject.biblemap.put("b1_39_22", new arasuperobject(1,39,22,"فدفع رئيس بيت السجن الى يد يوسف جميع الاسرى الذين في بيت السجن. وكل ما كانوا يعملون هناك كان هو العامل"));
arasuperobject.biblemap.put("b1_39_23", new arasuperobject(1,39,23,"ولم يكن رئيس بيت السجن ينظر شيئا البتة مّما في يده. لان الرب كان معه ومهما صنع كان الرب ينجحه"));
arasuperobject.biblemap.put("b1_40_1", new arasuperobject(1,40,1,"وحدث بعد هذه الامور ان ساقي ملك مصر والخباز اذنبا الى سيدهما ملك مصر"));
arasuperobject.biblemap.put("b1_40_2", new arasuperobject(1,40,2,"فسخط فرعون على خصيّيه رئيس السقاة ورئيس الخبازين"));
arasuperobject.biblemap.put("b1_40_3", new arasuperobject(1,40,3,"فوضعهما في حبس بيت رئيس الشرط في بيت السجن المكان الذي كان يوسف محبوسا فيه"));
arasuperobject.biblemap.put("b1_40_4", new arasuperobject(1,40,4,"فاقام رئيس الشرط يوسف عندهما فخدمهما. وكانا اياما في الحبس"));
arasuperobject.biblemap.put("b1_40_5", new arasuperobject(1,40,5,"وحلما كلاهما حلما في ليلة واحدة كل واحد حلمه كل واحد بحسب تعبير حلمه. ساقي ملك مصر وخبازه المحبوسان في بيت السجن"));
arasuperobject.biblemap.put("b1_40_6", new arasuperobject(1,40,6,"فدخل يوسف اليهما في الصباح ونظرهما واذا هما مغتمّان"));
arasuperobject.biblemap.put("b1_40_7", new arasuperobject(1,40,7,"فسأل خصيّي فرعون اللذين معه في حبس بيت سيده قائلا لماذا وجهاكما مكمّدان اليوم"));
arasuperobject.biblemap.put("b1_40_8", new arasuperobject(1,40,8,"فقالا له حلمنا حلما وليس من يعبره. فقال لهما يوسف أليست لله التعابير. قصا عليّ"));
arasuperobject.biblemap.put("b1_40_9", new arasuperobject(1,40,9,"فقصّ رئيس السقاة حلمه على يوسف وقال له كنت في حلمي واذا كرمة امامي"));
arasuperobject.biblemap.put("b1_40_10", new arasuperobject(1,40,10,"وفي الكرمة ثلاثة قضبان. وهي اذ أفرخت طلع زهرها وانضجت عناقيدها عنبا"));
arasuperobject.biblemap.put("b1_40_11", new arasuperobject(1,40,11,"وكانت كاس فرعون في يدي. فاخذت العنب وعصرته في كاس فرعون واعطيت الكاس في يد فرعون"));
arasuperobject.biblemap.put("b1_40_12", new arasuperobject(1,40,12,"فقال له يوسف هذا تعبيره. الثلاثة القضبان هي ثلاثة ايام"));
arasuperobject.biblemap.put("b1_40_13", new arasuperobject(1,40,13,"في ثلاثة ايام ايضا يرفع فرعون راسك ويردك الى مقامك. فتعطي كاس فرعون في يده كالعادة الاولى حين كنت ساقيه"));
arasuperobject.biblemap.put("b1_40_14", new arasuperobject(1,40,14,"وانما اذا ذكرتني عندك حينما يصير لك خير تصنع اليّ احسانا وتذكرني لفرعون وتخرجني من هذا البيت"));
arasuperobject.biblemap.put("b1_40_15", new arasuperobject(1,40,15,"لاني قد سرقت من ارض العبرانيين. وهنا ايضا لم افعل شيئا حتى وضعوني في السجن"));
arasuperobject.biblemap.put("b1_40_16", new arasuperobject(1,40,16,"فلما رأى رئيس الخبازين انه عبّر جيدا قال ليوسف كنت انا ايضا في حلمي واذا ثلاثة سلال حوّارى على راسي"));
arasuperobject.biblemap.put("b1_40_17", new arasuperobject(1,40,17,"وفي السل الاعلى من جميع طعام فرعون من صنعة الخباز. والطيور تاكله من السل عن راسي"));
arasuperobject.biblemap.put("b1_40_18", new arasuperobject(1,40,18,"فاجاب يوسف وقال هذا تعبيره. الثلاثة السلال هي ثلاثة ايام"));
arasuperobject.biblemap.put("b1_40_19", new arasuperobject(1,40,19,"في ثلاثة ايام ايضا يرفع فرعون راسك عنك ويعلقك على خشبة وتأكل الطيور لحمك عنك"));
arasuperobject.biblemap.put("b1_40_20", new arasuperobject(1,40,20,"فحدث في اليوم الثالث يوم ميلاد فرعون انه صنع وليمة لجميع عبيده ورفع راس رئيس السقاة وراس رئيس الخبازين بين عبيده"));
arasuperobject.biblemap.put("b1_40_21", new arasuperobject(1,40,21,"ورد رئيس السقاة الى سقيه. فأعطى الكاس في يد فرعون"));
arasuperobject.biblemap.put("b1_40_22", new arasuperobject(1,40,22,"واما رئيس الخبازين فعلقه كما عبّر لهما يوسف"));
arasuperobject.biblemap.put("b1_40_23", new arasuperobject(1,40,23,"ولكن لم يذكر رئيس السقاة يوسف بل نسيه"));
arasuperobject.biblemap.put("b1_41_1", new arasuperobject(1,41,1,"وحدث من بعد سنتين من الزمان ان فرعون رأى حلما. واذا هو واقف عند النهر"));
arasuperobject.biblemap.put("b1_41_2", new arasuperobject(1,41,2,"وهوذا سبع بقرات طالعة من النهر حسنة المنظر وسمينة اللحم. فارتعت في روضة"));
arasuperobject.biblemap.put("b1_41_3", new arasuperobject(1,41,3,"ثم هوذا سبع بقرات اخرى طالعة وراءها من النهر قبيحة المنظر ورقيقة اللحم. فوقفت بجانب البقرات الاولى على شاطئ النهر"));
arasuperobject.biblemap.put("b1_41_4", new arasuperobject(1,41,4,"فاكلت البقرات القبيحة المنظر والرقيقة اللحم البقرات السبع الحسنة المنظر والسمينة. واستيقظ فرعون"));
arasuperobject.biblemap.put("b1_41_5", new arasuperobject(1,41,5,"ثم نام فحلم ثانية. وهوذا سبع سنابل طالعة في ساق واحد سمينة وحسنة"));
arasuperobject.biblemap.put("b1_41_6", new arasuperobject(1,41,6,"ثم هوذا سبع سنابل رقيقة وملفوحة بالريح الشرقية نابتة وراءها"));
arasuperobject.biblemap.put("b1_41_7", new arasuperobject(1,41,7,"فابتلعت السنابل الرقيقة السنابل السبع السمينة الممتلئة. واستيقظ فرعون واذا هو حلم"));
arasuperobject.biblemap.put("b1_41_8", new arasuperobject(1,41,8,"وكان في الصباح ان نفسه انزعجت. فارسل ودعا جميع سحرة مصر وجميع حكمائها وقصّ عليهم فرعون حلمه. فلم يكن من يعبّره لفرعون"));
arasuperobject.biblemap.put("b1_41_9", new arasuperobject(1,41,9,"ثم كلم رئيس السقاة فرعون قائلا انا اتذكر اليوم خطاياي"));
arasuperobject.biblemap.put("b1_41_10", new arasuperobject(1,41,10,"فرعون سخط على عبديه فجعلني في حبس بيت رئيس الشرّط انا ورئيس الخبازين"));
arasuperobject.biblemap.put("b1_41_11", new arasuperobject(1,41,11,"فحلمنا حلما في ليلة واحدة انا وهو. حلمنا كل واحد بحسب تعبير حلمه"));
arasuperobject.biblemap.put("b1_41_12", new arasuperobject(1,41,12,"وكان هناك معنا غلام عبراني عبد لرئيس الشرط فقصصنا عليه. فعبّر لنا حلمينا. عبّر لكل واحد بحسب حلمه"));
arasuperobject.biblemap.put("b1_41_13", new arasuperobject(1,41,13,"وكما عبّر لنا هكذا حدث. ردّني انا الى مقامي واما هو فعلّقه"));
arasuperobject.biblemap.put("b1_41_14", new arasuperobject(1,41,14,"فارسل فرعون ودعا يوسف. فاسرعوا به من السجن. فحلق وابدل ثيابه ودخل على فرعون"));
arasuperobject.biblemap.put("b1_41_15", new arasuperobject(1,41,15,"فقال فرعون ليوسف حلمت حلما وليس من يعبّره. وانا سمعت عنك قولا انك تسمع احلاما لتعبّرها"));
arasuperobject.biblemap.put("b1_41_16", new arasuperobject(1,41,16,"فاجاب يوسف فرعون قائلا ليس لي. الله يجيب بسلامة فرعون"));
arasuperobject.biblemap.put("b1_41_17", new arasuperobject(1,41,17,"فقال فرعون ليوسف اني كنت في حلمي واقفا على شاطئ النهر"));
arasuperobject.biblemap.put("b1_41_18", new arasuperobject(1,41,18,"وهوذا سبع بقرات طالعة من النهر سمينة اللحم وحسنة الصورة. فارتعت في روضة"));
arasuperobject.biblemap.put("b1_41_19", new arasuperobject(1,41,19,"ثم هوذا سبع بقرات اخرى طالعة وراءها مهزولة وقبيحة الصورة جدا ورقيقة اللحم. لم انظر في كل ارض مصر مثلها في القباحة"));
arasuperobject.biblemap.put("b1_41_20", new arasuperobject(1,41,20,"فأكلت البقرات الرقيقة والقبيحة البقرات السبع الاولى السمينة"));
arasuperobject.biblemap.put("b1_41_21", new arasuperobject(1,41,21,"فدخلت اجوافها ولم يعلم انها دخلت في اجوافها. فكان منظرها قبيحا كما في الاول. واستيقظت"));
arasuperobject.biblemap.put("b1_41_22", new arasuperobject(1,41,22,"ثم رأيت في حلمي وهوذا سبع سنابل طالعة في ساق واحد ممتلئة وحسنة"));
arasuperobject.biblemap.put("b1_41_23", new arasuperobject(1,41,23,"ثم هوذا سبع سنابل يابسة رقيقة ملفوحة بالريح الشرقية نابتة وراءها"));
arasuperobject.biblemap.put("b1_41_24", new arasuperobject(1,41,24,"فابتلعت السنابل الرقيقة السنابل السبع الحسنة. فقلت للسحرة ولم يكن من يخبرني"));
arasuperobject.biblemap.put("b1_41_25", new arasuperobject(1,41,25,"فقال يوسف لفرعون حلم فرعون واحد. قد اخبر الله فرعون بما هو صانع"));
arasuperobject.biblemap.put("b1_41_26", new arasuperobject(1,41,26,"البقرات السبع الحسنة هي سبع سنين. والسنابل السبع الحسنة هي سبع سنين. هو حلم واحد"));
arasuperobject.biblemap.put("b1_41_27", new arasuperobject(1,41,27,"والبقرات السبع الرقيقة القبيحة التي طلعت وراءها هي سبع سنين. والسنابل السبع الفارغة الملفوحة بالريح الشرقية تكون سبع سنين جوعا"));
arasuperobject.biblemap.put("b1_41_28", new arasuperobject(1,41,28,"هو الامر الذي كلمت به فرعون. قد اظهر الله لفرعون ما هو صانع"));
arasuperobject.biblemap.put("b1_41_29", new arasuperobject(1,41,29,"هوذا سبع سنين قادمة شبعا عظيما في كل ارض مصر"));
arasuperobject.biblemap.put("b1_41_30", new arasuperobject(1,41,30,"ثم تقوم بعدها سبع سنين جوعا. فينسى كل الشبع في ارض مصر ويتلف الجوع الارض"));
arasuperobject.biblemap.put("b1_41_31", new arasuperobject(1,41,31,"ولا يعرف الشبع في الارض من اجل ذلك الجوع بعده. لانه يكون شديدا جدا"));
arasuperobject.biblemap.put("b1_41_32", new arasuperobject(1,41,32,"واما عن تكرار الحلم على فرعون مرّتين فلأن الامر مقرّر من قبل الله والله مسرع ليصنعه"));
arasuperobject.biblemap.put("b1_41_33", new arasuperobject(1,41,33,"فالآن لينظر فرعون رجلا بصيرا وحكيما ويجعله على ارض مصر"));
arasuperobject.biblemap.put("b1_41_34", new arasuperobject(1,41,34,"يفعل فرعون فيوكّل نظّارا على الارض وياخذ خمس غلّة ارض مصر في سبع سني الشبع"));
arasuperobject.biblemap.put("b1_41_35", new arasuperobject(1,41,35,"فيجمعون جميع طعام هذه السنين الجيّدة القادمة ويخزنون قمحا تحت يد فرعون طعاما في المدن ويحفظونه"));
arasuperobject.biblemap.put("b1_41_36", new arasuperobject(1,41,36,"فيكون الطعام ذخيرة للارض لسبع سني الجوع التي تكون في ارض مصر. فلا تنقرض الارض بالجوع"));
arasuperobject.biblemap.put("b1_41_37", new arasuperobject(1,41,37,"فحسن الكلام في عيني فرعون وفي عيون جميع عبيده"));
arasuperobject.biblemap.put("b1_41_38", new arasuperobject(1,41,38,"فقال فرعون لعبيده هل نجد مثل هذا رجلا فيه روح الله"));
arasuperobject.biblemap.put("b1_41_39", new arasuperobject(1,41,39,"ثم قال فرعون ليوسف بعدما اعلمك الله كل هذا ليس بصير وحكيم مثلك"));
arasuperobject.biblemap.put("b1_41_40", new arasuperobject(1,41,40,"انت تكون على بيتي وعلى فمك يقبّل جميع شعبي. الا ان الكرسي اكون فيه اعظم منك"));
arasuperobject.biblemap.put("b1_41_41", new arasuperobject(1,41,41,"ثم قال فرعون ليوسف انظر. قد جعلتك على كل ارض مصر"));
arasuperobject.biblemap.put("b1_41_42", new arasuperobject(1,41,42,"وخلع فرعون خاتمه من يده وجعله في يد يوسف. والبسه ثياب بوص ووضع طوق ذهب في عنقه"));
arasuperobject.biblemap.put("b1_41_43", new arasuperobject(1,41,43,"واركبه في مركبته الثانية ونادوا امامه اركعوا. وجعله على كل ارض مصر"));
arasuperobject.biblemap.put("b1_41_44", new arasuperobject(1,41,44,"وقال فرعون ليوسف انا فرعون. فبدونك لا يرفع انسان يده ولا رجله في كل ارض مصر"));
arasuperobject.biblemap.put("b1_41_45", new arasuperobject(1,41,45,"ودعا فرعون اسم يوسف صفنات فعنيح. واعطاه أسنات بنت فوطي فارع كاهن أون زوجة. فخرج يوسف على ارض مصر"));
arasuperobject.biblemap.put("b1_41_46", new arasuperobject(1,41,46,"وكان يوسف ابن ثلاثين سنة لما وقف قدام فرعون ملك مصر. فخرج يوسف من لدن فرعون واجتاز في كل ارض مصر"));
arasuperobject.biblemap.put("b1_41_47", new arasuperobject(1,41,47,"واثمرت الارض في سبع سني الشبع بحزم"));
arasuperobject.biblemap.put("b1_41_48", new arasuperobject(1,41,48,"فجمع كل طعام السبع سنين التي كانت في ارض مصر وجعل طعاما في المدن. طعام حقل المدينة الذي حواليها جعله فيها"));
arasuperobject.biblemap.put("b1_41_49", new arasuperobject(1,41,49,"وخزن يوسف قمحا كرمل البحر كثيرا جدا حتى ترك العدد اذ لم يكن له عدد"));
arasuperobject.biblemap.put("b1_41_50", new arasuperobject(1,41,50,"وولد ليوسف ابنان قبل ان تأتي سنة الجوع. ولدتهما له اسنات بنت فوطي فارع كاهن أون"));
arasuperobject.biblemap.put("b1_41_51", new arasuperobject(1,41,51,"ودعا يوسف اسم البكر منسّى قائلا لان الله انساني كل تعبي وكل بيت ابي"));
arasuperobject.biblemap.put("b1_41_52", new arasuperobject(1,41,52,"ودعا اسم الثاني افرايم قائلا لان الله جعلني مثمرا في ارض مذلّتي"));
arasuperobject.biblemap.put("b1_41_53", new arasuperobject(1,41,53,"ثم كملت سبع سني الشبع الذي كان في ارض مصر"));
arasuperobject.biblemap.put("b1_41_54", new arasuperobject(1,41,54,"وابتدأت سبع سني الجوع تأتي كما قال يوسف. فكان جوع في جميع البلدان. واما جميع ارض مصر فكان فيها خبز"));
arasuperobject.biblemap.put("b1_41_55", new arasuperobject(1,41,55,"ولما جاعت جميع ارض مصر وصرخ الشعب الى فرعون لاجل الخبز قال فرعون لكل المصريين اذهبوا الى يوسف. والذي يقول لكم افعلوا"));
arasuperobject.biblemap.put("b1_41_56", new arasuperobject(1,41,56,"وكان الجوع على كل وجه الارض. وفتح يوسف جميع ما فيه طعام وباع للمصريين. واشتّد الجوع في ارض مصر"));
arasuperobject.biblemap.put("b1_41_57", new arasuperobject(1,41,57,"وجاءت كل الارض الى مصر الى يوسف لتشتري قمحا. لان الجوع كان شديدا في كل الارض"));
arasuperobject.biblemap.put("b1_42_1", new arasuperobject(1,42,1,"فلما رأى يعقوب انه يوجد قمح في مصر قال يعقوب لبنيه لماذا تنظرون بعضكم الى بعض"));
arasuperobject.biblemap.put("b1_42_2", new arasuperobject(1,42,2,"وقال اني قد سمعت انه يوجد قمح في مصر. انزلوا الى هناك واشتروا لنا من هناك لنحيا ولا نموت"));
arasuperobject.biblemap.put("b1_42_3", new arasuperobject(1,42,3,"فنزل عشرة من اخوة يوسف ليشتروا قمحا من مصر"));
arasuperobject.biblemap.put("b1_42_4", new arasuperobject(1,42,4,"واما بنيامين اخو يوسف فلم يرسله يعقوب مع اخوته. لانه قال لعلّه تصيبه اذيّة"));
arasuperobject.biblemap.put("b1_42_5", new arasuperobject(1,42,5,"فاتى بنو اسرائيل ليشتروا بين الذين اتوا. لان الجوع كان في ارض كنعان"));
arasuperobject.biblemap.put("b1_42_6", new arasuperobject(1,42,6,"وكان يوسف هو المسلّط على الارض وهو البائع لكل شعب الارض. فأتى اخوة يوسف وسجدوا له بوجوههم الى الارض"));
arasuperobject.biblemap.put("b1_42_7", new arasuperobject(1,42,7,"ولما نظر يوسف اخوته عرفهم. فتنكّر لهم وتكلم معهم بجفاء وقال لهم من اين جئتم. فقالوا من ارض كنعان لنشتري طعاما"));
arasuperobject.biblemap.put("b1_42_8", new arasuperobject(1,42,8,"وعرف يوسف اخوته. واما هم فلم يعرفوه"));
arasuperobject.biblemap.put("b1_42_9", new arasuperobject(1,42,9,"فتذكر يوسف الاحلام التي حلم عنهم وقال لهم جواسيس انتم. لتروا عورة الارض جئتم"));
arasuperobject.biblemap.put("b1_42_10", new arasuperobject(1,42,10,"فقالوا له لا يا سيدي. بل عبيدك جاءوا ليشتروا طعاما"));
arasuperobject.biblemap.put("b1_42_11", new arasuperobject(1,42,11,"نحن جميعنا بنو رجل واحد. نحن أمناء. ليس عبيدك جواسيس"));
arasuperobject.biblemap.put("b1_42_12", new arasuperobject(1,42,12,"فقال لهم كلا بل لتروا عورة الارض جئتم"));
arasuperobject.biblemap.put("b1_42_13", new arasuperobject(1,42,13,"فقالوا عبيدك اثنا عشر اخا. نحن بنو رجل واحد في ارض كنعان. وهوذا الصغير عند ابينا اليوم والواحد مفقود"));
arasuperobject.biblemap.put("b1_42_14", new arasuperobject(1,42,14,"فقال لهم يوسف ذلك ما كلمتكم به قائلا جواسيس انتم"));
arasuperobject.biblemap.put("b1_42_15", new arasuperobject(1,42,15,"بهذا تمتحنون. وحياة فرعون لا تخرجون من هنا الا بمجيء اخيكم الصغير الى هنا"));
arasuperobject.biblemap.put("b1_42_16", new arasuperobject(1,42,16,"ارسلوا منكم واحدا ليجيء باخيكم وانتم تحبسون. فيمتحن كلامكم هل عندكم صدق. وإلا فوحياة فرعون انكم لجواسيس"));
arasuperobject.biblemap.put("b1_42_17", new arasuperobject(1,42,17,"فجمعهم الى حبس ثلاثة ايام"));
arasuperobject.biblemap.put("b1_42_18", new arasuperobject(1,42,18,"ثم قال لهم يوسف في اليوم الثالث افعلوا هذا واحيوا. انا خائف الله"));
arasuperobject.biblemap.put("b1_42_19", new arasuperobject(1,42,19,"ان كنتم أمناء فليحبس اخ واحد منكم في بيت حبسكم وانطلقوا انتم وخذوا قمحا لمجاعة بيوتكم"));
arasuperobject.biblemap.put("b1_42_20", new arasuperobject(1,42,20,"واحضروا اخاكم الصغير اليّ. فيتحقّق كلامكم ولا تموتوا. ففعلوا هكذا"));
arasuperobject.biblemap.put("b1_42_21", new arasuperobject(1,42,21,"وقالوا بعضهم لبعض حقّا اننا مذنبون الى اخينا الذي رأينا ضيقة نفسه لما استرحمنا ولم نسمع. لذلك جاءت علينا هذه الضيقة"));
arasuperobject.biblemap.put("b1_42_22", new arasuperobject(1,42,22,"فاجابهم رأوبين قائلا ألم اكلمكم قائلا لا تأثموا بالولد وانتم لم تسمعوا. فهوذا دمه يطلب"));
arasuperobject.biblemap.put("b1_42_23", new arasuperobject(1,42,23,"وهم لم يعلموا ان يوسف فاهم. لان الترجمان كان بينهم"));
arasuperobject.biblemap.put("b1_42_24", new arasuperobject(1,42,24,"فتحول عنهم وبكى. ثم رجع اليهم وكلمهم. واخذ منهم شمعون وقيّده امام عيونهم"));
arasuperobject.biblemap.put("b1_42_25", new arasuperobject(1,42,25,"ثم امر يوسف ان تملأ اوعيتهم قمحا وتردّ فضة كل واحد الى عدله وان يعطوا زادا للطريق. ففعل لهم هكذا"));
arasuperobject.biblemap.put("b1_42_26", new arasuperobject(1,42,26,"فحملوا قمحهم على حميرهم ومضوا من هناك"));
arasuperobject.biblemap.put("b1_42_27", new arasuperobject(1,42,27,"فلما فتح احدهم عدله ليعطي عليقا لحماره في المنزل رأى فضته واذا هي في فم عدله"));
arasuperobject.biblemap.put("b1_42_28", new arasuperobject(1,42,28,"فقال لاخوته ردّت فضتي وها هي في عدلي. فطارت قلوبهم وارتعدوا بعضهم في بعض قائلين ما هذا الذي صنعه الله بنا"));
arasuperobject.biblemap.put("b1_42_29", new arasuperobject(1,42,29,"فجاءوا الى يعقوب ابيهم الى ارض كنعان واخبروه بكل ما اصابهم قائلين"));
arasuperobject.biblemap.put("b1_42_30", new arasuperobject(1,42,30,"تكلم معنا الرجل سيد الارض بجفاء وحسبنا جواسيس الارض"));
arasuperobject.biblemap.put("b1_42_31", new arasuperobject(1,42,31,"فقلنا له نحن أمناء. لسنا جواسيس"));
arasuperobject.biblemap.put("b1_42_32", new arasuperobject(1,42,32,"نحن اثنا عشر اخا بنو ابينا. الواحد مفقود والصغير اليوم عند ابينا في ارض كنعان"));
arasuperobject.biblemap.put("b1_42_33", new arasuperobject(1,42,33,"فقال لنا الرجل سيد الارض بهذا اعرف انكم أمناء. دعوا اخا واحدا منكم عندي وخذوا لمجاعة بيوتكم وانطلقوا"));
arasuperobject.biblemap.put("b1_42_34", new arasuperobject(1,42,34,"واحضروا اخاكم الصغير اليّ. فاعرف انكم لستم جواسيس بل انكم أمناء. فاعطيكم اخاكم وتتجرون في الارض"));
arasuperobject.biblemap.put("b1_42_35", new arasuperobject(1,42,35,"واذ كانوا يفرغون عدالهم اذا صرّة فضة كل واحد في عدله. فلما رأوا صرر فضتهم هم وابوهم خافوا"));
arasuperobject.biblemap.put("b1_42_36", new arasuperobject(1,42,36,"فقال لهم يعقوب اعدمتموني الاولاد. يوسف مفقود وشمعون مفقود وبنيامين تأخذونه. صار كل هذا عليّ"));
arasuperobject.biblemap.put("b1_42_37", new arasuperobject(1,42,37,"وكلّم رأوبين اباه قائلا اقتل ابنيّ ان لم اجيء به اليك. سلّمه بيدي وانا ارده اليك"));
arasuperobject.biblemap.put("b1_42_38", new arasuperobject(1,42,38,"فقال لا ينزل ابني معكم. لان اخاه قد مات وهو وحده باق. فان اصابته اذيّة في الطريق التي تذهبون فيها تنزلون شيبتي بحزن الى الهاوية"));
arasuperobject.biblemap.put("b1_43_1", new arasuperobject(1,43,1,"وكان الجوع شديدا في الارض"));
arasuperobject.biblemap.put("b1_43_2", new arasuperobject(1,43,2,"وحدث لما فرغوا من اكل القمح الذي جاءوا به من مصر ان اباهم قال لهم ارجعوا اشتروا لنا قليلا من الطعام"));
arasuperobject.biblemap.put("b1_43_3", new arasuperobject(1,43,3,"فكلمه يهوذا قائلا ان الرجل قد اشهد علينا قائلا لا ترون وجهي بدون ان يكون اخوكم معكم"));
arasuperobject.biblemap.put("b1_43_4", new arasuperobject(1,43,4,"ان كنت ترسل اخانا معنا ننزل ونشتري لك طعاما"));
arasuperobject.biblemap.put("b1_43_5", new arasuperobject(1,43,5,"ولكن ان كنت لا ترسله لا ننزل. لان الرجل قال لنا لا ترون وجهي بدون ان يكون اخوكم معكم"));
arasuperobject.biblemap.put("b1_43_6", new arasuperobject(1,43,6,"فقال اسرائيل لماذا اسأتم اليّ حتى اخبرتم الرجل ان لكم اخا ايضا"));
arasuperobject.biblemap.put("b1_43_7", new arasuperobject(1,43,7,"فقالوا ان الرجل قد سأل عنا وعن عشيرتنا قائلا هل ابوكم حيّ بعد. هل لكم اخ. فاخبرناه بحسب هذا الكلام. هل كنا نعلم انه يقول انزلوا باخيكم"));
arasuperobject.biblemap.put("b1_43_8", new arasuperobject(1,43,8,"وقال يهوذا لاسرائيل ابيه ارسل الغلام معي لنقوم ونذهب ونحيا ولا نموت نحن وانت واولادنا جميعا"));
arasuperobject.biblemap.put("b1_43_9", new arasuperobject(1,43,9,"انا اضمنه. من يدي تطلبه. ان لم اجئ به اليك واوقفه قدامك أصر مذنبا اليك كل الايام"));
arasuperobject.biblemap.put("b1_43_10", new arasuperobject(1,43,10,"لاننا لو لم نتوان لكنا قد رجعنا الآن مرتين"));
arasuperobject.biblemap.put("b1_43_11", new arasuperobject(1,43,11,"فقال لهم اسرائيل ابوهم ان كان هكذا فافعوا هذا. خذوا من افخر جنى الارض في اوعيتكم وأنزلوا للرجل هدية. قليلا من البلسان وقليلا من العسل وكثيراء ولاذنا وفستقا ولوزا"));
arasuperobject.biblemap.put("b1_43_12", new arasuperobject(1,43,12,"وخذوا فضة اخرى في اياديكم. والفضة المردودة في افواه عدالكم ردوها في اياديكم. لعله كان سهوا"));
arasuperobject.biblemap.put("b1_43_13", new arasuperobject(1,43,13,"وخذوا اخاكم وقوموا ارجعوا الى الرجل"));
arasuperobject.biblemap.put("b1_43_14", new arasuperobject(1,43,14,"والله القدير يعطيكم رحمة امام الرجل حتى يطلق لكم اخاكم الآخر وبنيامين. وانا اذا عدمت الاولاد عدمتهم"));
arasuperobject.biblemap.put("b1_43_15", new arasuperobject(1,43,15,"فاخذ الرجال هذه الهدية واخذوا ضعف الفضة في اياديهم وبنيامين وقاموا ونزلوا الى مصر ووقفوا امام يوسف"));
arasuperobject.biblemap.put("b1_43_16", new arasuperobject(1,43,16,"فلما رأى يوسف بنيامين معهم قال للذي على بيته أدخل الرجال الى البيت واذبح ذبيحة وهيئ. لان الرجال ياكلون معي عند الظهر"));
arasuperobject.biblemap.put("b1_43_17", new arasuperobject(1,43,17,"ففعل الرجل كما قال يوسف. وادخل الرجل الرجال الى بيت يوسف"));
arasuperobject.biblemap.put("b1_43_18", new arasuperobject(1,43,18,"فخاف الرجال اذ أدخلوا الى بيت يوسف. وقالوا لسبب الفضة التي رجعت اولا في عدالنا نحن قد ادخلنا ليهجم علينا ويقع بنا ويأخذنا عبيدا وحميرنا"));
arasuperobject.biblemap.put("b1_43_19", new arasuperobject(1,43,19,"فتقدموا الى الرجل الذي على بيت يوسف وكلموه في باب البيت"));
arasuperobject.biblemap.put("b1_43_20", new arasuperobject(1,43,20,"وقالوا استمع يا سيدي. اننا قد نزلنا اولا لنشتري طعاما"));
arasuperobject.biblemap.put("b1_43_21", new arasuperobject(1,43,21,"وكان لما أتينا الى المنزل اننا فتحنا عدالنا واذا فضة كل واحد في فم عدله فضتنا بوزنها. فقد رددناها في ايادينا"));
arasuperobject.biblemap.put("b1_43_22", new arasuperobject(1,43,22,"وانزلنا فضة اخرى في ايادينا لنشتري طعاما. لا نعلم من وضع فضتنا في عدالنا"));
arasuperobject.biblemap.put("b1_43_23", new arasuperobject(1,43,23,"فقال سلام لكم. لا تخافوا. الهكم واله ابيكم اعطاكم كنزا في عدالكم. فضتكم وصلت اليّ. ثم اخرج اليهم شمعون"));
arasuperobject.biblemap.put("b1_43_24", new arasuperobject(1,43,24,"وادخل الرجل الرجال الى بيت يوسف واعطاهم ماء ليغسلوا ارجلهم واعطى عليقا لحميرهم"));
arasuperobject.biblemap.put("b1_43_25", new arasuperobject(1,43,25,"وهيّأوا الهدية الى ان يجيء يوسف عند الظهر لانهم سمعوا انهم هناك ياكلون طعاما"));
arasuperobject.biblemap.put("b1_43_26", new arasuperobject(1,43,26,"فلما جاء يوسف الى البيت احضروا اليه الهدية التي في اياديهم الى البيت وسجدوا له الى الارض"));
arasuperobject.biblemap.put("b1_43_27", new arasuperobject(1,43,27,"فسأل عن سلامتهم وقال أسالم ابوكم الشيخ الذي قلتم عنه. أحيّ هو بعد"));
arasuperobject.biblemap.put("b1_43_28", new arasuperobject(1,43,28,"فقالوا عبدك ابونا سالم. هو حيّ بعد. وخروا وسجدوا"));
arasuperobject.biblemap.put("b1_43_29", new arasuperobject(1,43,29,"فرفع عينيه ونظر بنيامين اخاه ابن امه وقال أهذا اخوكم الصغير الذي قلتم لي عنه. ثم قال الله ينعم عليك يا ابني"));
arasuperobject.biblemap.put("b1_43_30", new arasuperobject(1,43,30,"واستعجل يوسف لان احشاءه حنّت الى اخيه وطلب مكانا ليبكي. فدخل المخدع وبكى هناك"));
arasuperobject.biblemap.put("b1_43_31", new arasuperobject(1,43,31,"ثم غسل وجهه وخرج وتجلد. وقال قدموا طعاما"));
arasuperobject.biblemap.put("b1_43_32", new arasuperobject(1,43,32,"فقدموا له وحده ولهم وحدهم وللمصريين الآكلين عنده وحدهم. لان المصريين لا يقدرون ان يأكلوا طعاما مع العبرانيين لانه رجس عند المصريين"));
arasuperobject.biblemap.put("b1_43_33", new arasuperobject(1,43,33,"فجلسوا قدامه البكر بحسب بكوريته والصغير بحسب صغره. فبهت الرجال بعضهم الى بعض"));
arasuperobject.biblemap.put("b1_43_34", new arasuperobject(1,43,34,"ورفع حصصا من قدامه اليهم. فكانت حصة بنيامين اكثر من حصص جميعهم خمسة اضعاف. وشربوا ورووا معه"));
arasuperobject.biblemap.put("b1_44_1", new arasuperobject(1,44,1,"ثم امر الذي على بيته قائلا املأ عدال الرجال طعاما حسب ما يطيقون حمله وضع فضة كل واحد في فم عدله"));
arasuperobject.biblemap.put("b1_44_2", new arasuperobject(1,44,2,"وطاسي طاس الفضة تضع في فم عدل الصغير وثمن قمحه. ففعل بحسب كلام يوسف الذي تكلّم به"));
arasuperobject.biblemap.put("b1_44_3", new arasuperobject(1,44,3,"فلما اضاء الصبح انصرف الرجال هم وحميرهم"));
arasuperobject.biblemap.put("b1_44_4", new arasuperobject(1,44,4,"ولما كانوا قد خرجوا من المدينة ولم يبتعدوا قال يوسف للذي على بيته قم اسع وراء الرجال ومتى ادركتهم فقل لهم لماذا جازيتم شرا عوضا عن خير"));
arasuperobject.biblemap.put("b1_44_5", new arasuperobject(1,44,5,"أليس هذا هو الذي يشرب سيدي فيه. وهو يتفاءل به. اسأتم في ما صنعتم"));
arasuperobject.biblemap.put("b1_44_6", new arasuperobject(1,44,6,"فادركهم وقال لهم هذا الكلام"));
arasuperobject.biblemap.put("b1_44_7", new arasuperobject(1,44,7,"فقالوا له لماذا يتكلم سيدي مثل هذا الكلام. حاشا لعبيدك ان يفعلوا مثل هذا الامر"));
arasuperobject.biblemap.put("b1_44_8", new arasuperobject(1,44,8,"هوذا الفضة التي وجدنا في افواه عدالنا رددناها اليك من ارض كنعان. فكيف نسرق من بيت سيدك فضة او ذهبا"));
arasuperobject.biblemap.put("b1_44_9", new arasuperobject(1,44,9,"الذي يوجد معه من عبيدك يموت. ونحن ايضا نكون عبيدا لسيدي"));
arasuperobject.biblemap.put("b1_44_10", new arasuperobject(1,44,10,"فقال نعم الآن بحسب كلامكم هكذا يكون. الذي يوجد معه يكون لي عبدا. واما انتم فتكونون ابرياء"));
arasuperobject.biblemap.put("b1_44_11", new arasuperobject(1,44,11,"فاستعجلوا وانزلوا كل واحد عدله الى الارض وفتحوا كل واحد عدله"));
arasuperobject.biblemap.put("b1_44_12", new arasuperobject(1,44,12,"ففتش مبتدأ من الكبير حتى انتهى الى الصغير. فوجد الطاس في عدل بنيامين"));
arasuperobject.biblemap.put("b1_44_13", new arasuperobject(1,44,13,"فمزقوا ثيابهم وحمّل كل واحد على حماره ورجعوا الى المدينة"));
arasuperobject.biblemap.put("b1_44_14", new arasuperobject(1,44,14,"فدخل يهوذا واخوته الى بيت يوسف وهو بعد هناك. ووقعوا امامه على الارض"));
arasuperobject.biblemap.put("b1_44_15", new arasuperobject(1,44,15,"فقال لهم يوسف ما هذا الفعل الذي فعلتم ألم تعلموا ان رجلا مثلي يتفاءل"));
arasuperobject.biblemap.put("b1_44_16", new arasuperobject(1,44,16,"فقال يهوذا ماذا نقول لسيدي. ماذا نتكلم وبماذا نتبرر. الله قد وجد اثم عبيدك. ها نحن عبيد لسيدي نحن والذي وجد الطاس في يده جميعا"));
arasuperobject.biblemap.put("b1_44_17", new arasuperobject(1,44,17,"فقال حاشا لي ان افعل هذا. الرجل الذي وجد الطاس في يده هو يكون لي عبدا. واما انتم فاصعدوا بسلام الى ابيكم"));
arasuperobject.biblemap.put("b1_44_18", new arasuperobject(1,44,18,"ثم تقدم اليه يهوذا وقال استمع يا سيدي. ليتكلم عبدك كلمة في أذني سيدي. ولا يحم غضبك على عبدك. لانك مثل فرعون"));
arasuperobject.biblemap.put("b1_44_19", new arasuperobject(1,44,19,"سيدي سأل عبيده قائلا هل لكم اب او اخ"));
arasuperobject.biblemap.put("b1_44_20", new arasuperobject(1,44,20,"فقلنا لسيدي لنا اب شيخ وابن شيخوخة صغير مات اخوه وبقي هو وحده لامه وابوه يحبه"));
arasuperobject.biblemap.put("b1_44_21", new arasuperobject(1,44,21,"فقلت لعبيدك انزلوا به اليّ فاجعل نظري عليه"));
arasuperobject.biblemap.put("b1_44_22", new arasuperobject(1,44,22,"فقلنا لسيدي لا يقدر الغلام ان يترك اباه. وان ترك اباه يموت"));
arasuperobject.biblemap.put("b1_44_23", new arasuperobject(1,44,23,"فقلت لعبيدك ان لم ينزل اخوكم الصغير معكم لا تعودوا تنظرون وجهي"));
arasuperobject.biblemap.put("b1_44_24", new arasuperobject(1,44,24,"فكان لما صعدنا الى عبدك ابي اننا اخبرناه بكلام سيدي"));
arasuperobject.biblemap.put("b1_44_25", new arasuperobject(1,44,25,"ثم قال ابونا ارجعوا اشتروا لنا قليلا من الطعام"));
arasuperobject.biblemap.put("b1_44_26", new arasuperobject(1,44,26,"فقلنا لا نقدر ان ننزل. وانما اذا كان اخونا الصغير معنا ننزل. لاننا لا نقدر ان ننظر وجه الرجل واخونا الصغير ليس معنا"));
arasuperobject.biblemap.put("b1_44_27", new arasuperobject(1,44,27,"فقال لنا عبدك ابي انتم تعلمون ان امرأتي ولدت لي اثنين"));
arasuperobject.biblemap.put("b1_44_28", new arasuperobject(1,44,28,"فخرج الواحد من عندي وقلت انما هو قد افترس افتراسا. ولم انظره الى الآن"));
arasuperobject.biblemap.put("b1_44_29", new arasuperobject(1,44,29,"فاذا اخذتم هذا ايضا من امام وجهي واصابته اذيّة تنزلون شيبتي بشر الى الهاوية"));
arasuperobject.biblemap.put("b1_44_30", new arasuperobject(1,44,30,"فالآن متى جئت الى عبدك ابي والغلام ليس معنا ونفسه مرتبطة بنفسه"));
arasuperobject.biblemap.put("b1_44_31", new arasuperobject(1,44,31,"يكون متى رأى ان الغلام مفقود انه يموت. فينزل عبيدك شيبة عبدك ابينا بحزن الى الهاوية"));
arasuperobject.biblemap.put("b1_44_32", new arasuperobject(1,44,32,"لان عبدك ضمن الغلام لابي قائلا ان لم اجئ به اليك أصر مذنبا الى ابي كل الايام"));
arasuperobject.biblemap.put("b1_44_33", new arasuperobject(1,44,33,"فالآن ليمكث عبدك عوضا عن الغلام عبدا لسيدي ويصعد الغلام مع اخوته"));
arasuperobject.biblemap.put("b1_44_34", new arasuperobject(1,44,34,"لاني كيف اصعد الى ابي والغلام ليس معي. لئلا انظر الشر الذي يصيب ابي"));
arasuperobject.biblemap.put("b1_45_1", new arasuperobject(1,45,1,"فلم يستطع يوسف ان يضبط نفسه لدى جميع الواقفين عنده فصرخ أخرجوا كل انسان عني. فلم يقف احد عنده حين عرّف يوسف اخوته بنفسه"));
arasuperobject.biblemap.put("b1_45_2", new arasuperobject(1,45,2,"فاطلق صوته بالبكاء. فسمع المصريون وسمع بيت فرعون"));
arasuperobject.biblemap.put("b1_45_3", new arasuperobject(1,45,3,"وقال يوسف لاخوته انا يوسف. أحيّ ابي بعد. فلم يستطع اخوته ان يجيبوه لانهم ارتاعوا منه"));
arasuperobject.biblemap.put("b1_45_4", new arasuperobject(1,45,4,"فقال يوسف لاخوته تقدموا اليّ. فتقدموا. فقال انا يوسف اخوكم الذي بعتموه الى مصر"));
arasuperobject.biblemap.put("b1_45_5", new arasuperobject(1,45,5,"والآن لا تتأسّفوا ولا تغتاظوا لانكم بعتموني الى هنا. لانه لاستبقاء حياة ارسلني الله قدّامكم"));
arasuperobject.biblemap.put("b1_45_6", new arasuperobject(1,45,6,"لان للجوع في الارض الآن سنتين. وخمس سنين ايضا لا تكون فيها فلاحة ولا حصاد"));
arasuperobject.biblemap.put("b1_45_7", new arasuperobject(1,45,7,"فقد ارسلني الله قدامكم ليجعل لكم بقية في الارض وليستبقي لكم نجاة عظيمة"));
arasuperobject.biblemap.put("b1_45_8", new arasuperobject(1,45,8,"فالآن ليس انتم ارسلتموني الى هنا بل الله. وهو قد جعلني ابا لفرعون وسيّدا لكل بيته ومتسلطا على كل ارض مصر"));
arasuperobject.biblemap.put("b1_45_9", new arasuperobject(1,45,9,"اسرعوا واصعدوا الى ابي وقولوا له هكذا يقول ابنك يوسف. قد جعلني الله سيدا لكل مصر. انزل اليّ. لا تقف"));
arasuperobject.biblemap.put("b1_45_10", new arasuperobject(1,45,10,"فتسكن في ارض جاسان وتكون قريبا مني انت وبنوك وبنو بنيك وغنمك وبقرك وكل مالك"));
arasuperobject.biblemap.put("b1_45_11", new arasuperobject(1,45,11,"واعولك هناك لانه يكون ايضا خمس سنين جوعا. لئلا تفتقر انت وبيتك وكل مالك"));
arasuperobject.biblemap.put("b1_45_12", new arasuperobject(1,45,12,"وهوذا عيونكم ترى وعينا اخي بنيامين ان فمي هو الذي يكلمكم"));
arasuperobject.biblemap.put("b1_45_13", new arasuperobject(1,45,13,"وتخبرون ابي بكل مجدي في مصر وبكل ما رأيتم وتستعجلون وتنزلون بابي الى هنا"));
arasuperobject.biblemap.put("b1_45_14", new arasuperobject(1,45,14,"ثم وقع على عنق بنيامين اخيه وبكى. وبكى بنيامين على عنقه"));
arasuperobject.biblemap.put("b1_45_15", new arasuperobject(1,45,15,"وقبّل جميع اخوته وبكى عليهم. وبعد ذلك تكلم اخوته معه"));
arasuperobject.biblemap.put("b1_45_16", new arasuperobject(1,45,16,"وسمع الخبر في بيت فرعون وقيل جاء اخوة يوسف. فحسن في عيني فرعون وفي عيون عبيده"));
arasuperobject.biblemap.put("b1_45_17", new arasuperobject(1,45,17,"فقال فرعون ليوسف قل لاخوتك افعلوا هذا. حمّلوا دوابكم وانطلقوا اذهبوا الى ارض كنعان"));
arasuperobject.biblemap.put("b1_45_18", new arasuperobject(1,45,18,"وخذوا اباكم وبيوتكم وتعالوا اليّ. فاعطيكم خيرات ارض مصر وتاكلوا دسم الارض"));
arasuperobject.biblemap.put("b1_45_19", new arasuperobject(1,45,19,"فانت قد أمرت. افعلوا هذا. خذوا لكم من ارض مصر عجلات لاولادكم ونسائكم واحملوا اباكم وتعالوا"));
arasuperobject.biblemap.put("b1_45_20", new arasuperobject(1,45,20,"ولا تحزن عيونكم على اثاثكم. لان خيرات جميع ارض مصر لكم"));
arasuperobject.biblemap.put("b1_45_21", new arasuperobject(1,45,21,"ففعل بنو اسرائيل هكذا. واعطاهم يوسف عجلات بحسب امر فرعون. واعطاهم زادا للطريق"));
arasuperobject.biblemap.put("b1_45_22", new arasuperobject(1,45,22,"واعطى كل واحد منهم حلل ثياب. واما بنيامين فاعطاه ثلاث مئة من الفضة وخمس حلل ثياب"));
arasuperobject.biblemap.put("b1_45_23", new arasuperobject(1,45,23,"وارسل لابيه هكذا. عشرة حمير حاملة من خيرات مصر وعشر أتن حاملة حنطة وخبزا وطعاما لابيه لاجل الطريق"));
arasuperobject.biblemap.put("b1_45_24", new arasuperobject(1,45,24,"ثم صرف اخوته فانطلقوا وقال لهم لا تتغاضبوا في الطريق"));
arasuperobject.biblemap.put("b1_45_25", new arasuperobject(1,45,25,"فصعدوا من مصر وجاءوا الى ارض كنعان الى يعقوب ابيهم"));
arasuperobject.biblemap.put("b1_45_26", new arasuperobject(1,45,26,"واخبروه قائلين يوسف حيّ بعد. وهو متسلط على كل ارض مصر. فجمد قلبه لانه لم يصدقهم"));
arasuperobject.biblemap.put("b1_45_27", new arasuperobject(1,45,27,"ثم كلموه بكل كلام يوسف الذي كلمهم به. وابصر العجلات التي ارسلها يوسف لتحمله. فعاشت روح يعقوب ابيهم"));
arasuperobject.biblemap.put("b1_45_28", new arasuperobject(1,45,28,"فقال اسرائيل كفى. يوسف ابني حيّ بعد. أذهب واراه قبل ان اموت"));
arasuperobject.biblemap.put("b1_46_1", new arasuperobject(1,46,1,"فارتحل اسرائيل وكل ما كان له وأتى الى بئر سبع. وذبح ذبائح لاله ابيه اسحق"));
arasuperobject.biblemap.put("b1_46_2", new arasuperobject(1,46,2,"فكلم الله اسرائيل في رؤى الليل وقال يعقوب يعقوب. فقال هانذا"));
arasuperobject.biblemap.put("b1_46_3", new arasuperobject(1,46,3,"فقال انا الله اله ابيك. لا تخف من النزول الى مصر. لاني اجعلك امة عظيمة هناك"));
arasuperobject.biblemap.put("b1_46_4", new arasuperobject(1,46,4,"انا انزل معك الى مصر وانا اصعدك ايضا. ويضع يوسف يده على عينيك"));
arasuperobject.biblemap.put("b1_46_5", new arasuperobject(1,46,5,"فقام يعقوب من بئر سبع. وحمل بنو اسرائيل يعقوب اباهم واولادهم ونساءهم في العجلات التي ارسل فرعون لحمله"));
arasuperobject.biblemap.put("b1_46_6", new arasuperobject(1,46,6,"واخذوا مواشيهم ومقتناهم الذي اقتنوا في ارض كنعان وجاءوا الى مصر. يعقوب وكل نسله معه"));
arasuperobject.biblemap.put("b1_46_7", new arasuperobject(1,46,7,"بنوه وبنو بنيه معه وبناته وبنات بنيه وكل نسله جاء بهم معه الى مصر"));
arasuperobject.biblemap.put("b1_46_8", new arasuperobject(1,46,8,"وهذه اسماء بني اسرائيل الذين جاءوا الى مصر. يعقوب وبنوه. بكر يعقوب رأوبين"));
arasuperobject.biblemap.put("b1_46_9", new arasuperobject(1,46,9,"وبنو رأوبين حنوك وفلّو وحصرون وكرمي"));
arasuperobject.biblemap.put("b1_46_10", new arasuperobject(1,46,10,"وبنو شمعون يموئيل ويامين وأوهد وياكين وصوحر وشأول ابن الكنعانية"));
arasuperobject.biblemap.put("b1_46_11", new arasuperobject(1,46,11,"وبنو لاوي جرشون وقهات ومراري"));
arasuperobject.biblemap.put("b1_46_12", new arasuperobject(1,46,12,"وبنو يهوذا عير وأونان وشيلة وفارص وزارح. واما عير وأونان فماتا في ارض كنعان. وكان ابنا فارص حصرون وحامول"));
arasuperobject.biblemap.put("b1_46_13", new arasuperobject(1,46,13,"وبنو يساكر تولاع وفوّة ويوب وشمرون"));
arasuperobject.biblemap.put("b1_46_14", new arasuperobject(1,46,14,"وبنو زبولون سارد وإيلون وياحلئيل"));
arasuperobject.biblemap.put("b1_46_15", new arasuperobject(1,46,15,"هؤلاء بنو ليئة الذين ولدتهم ليعقوب في فدّان ارام مع دينة ابنته. جميع نفوس بنيه وبناته ثلاث وثلاثون"));
arasuperobject.biblemap.put("b1_46_16", new arasuperobject(1,46,16,"وبنو جاد صفيون وحجي وشوني واصبون وعيري وارودي وأرئيلي"));
arasuperobject.biblemap.put("b1_46_17", new arasuperobject(1,46,17,"وبنو اشير يمنة ويشوة ويشوي وبريعة وسارح هي اختهم. وابنا بريعة حابر وملكيئيل"));
arasuperobject.biblemap.put("b1_46_18", new arasuperobject(1,46,18,"هؤلاء بنو زلفة التي اعطاها لابان لليئة ابنته. فولدت هؤلاء ليعقوب ست عشرة نفسا"));
arasuperobject.biblemap.put("b1_46_19", new arasuperobject(1,46,19,"ابنا راحيل امرأة يعقوب يوسف وبنيامين"));
arasuperobject.biblemap.put("b1_46_20", new arasuperobject(1,46,20,"وولد ليوسف في ارض مصر منسّى وافرايم اللذان ولدتهما له اسنات بنت فوطي فارع كاهن أون"));
arasuperobject.biblemap.put("b1_46_21", new arasuperobject(1,46,21,"وبنو بنيامين بالع وباكر وأشبيل وجيرا ونعمان وإيحي وروش ومفّيم وحفّيم وأرد"));
arasuperobject.biblemap.put("b1_46_22", new arasuperobject(1,46,22,"هؤلاء بنو راحيل الذين ولدوا ليعقوب. جميع النفوس اربع عشرة"));
arasuperobject.biblemap.put("b1_46_23", new arasuperobject(1,46,23,"وابن دان حوشيم"));
arasuperobject.biblemap.put("b1_46_24", new arasuperobject(1,46,24,"وبنو نفتالي ياحصئيل وجوني ويصر وشلّيم"));
arasuperobject.biblemap.put("b1_46_25", new arasuperobject(1,46,25,"هؤلاء بنو بلهة التي اعطاها لابان لراحيل ابنته. فولدت هؤلاء ليعقوب جميع الانفس سبع"));
arasuperobject.biblemap.put("b1_46_26", new arasuperobject(1,46,26,"جميع النفوس ليعقوب التي اتت الى مصر الخارجة من صلبه ما عدا نساء بني يعقوب جميع النفوس ست وستون نفسا"));
arasuperobject.biblemap.put("b1_46_27", new arasuperobject(1,46,27,"وابنا يوسف اللذان ولدا له في مصر نفسان. جميع نفوس بيت يعقوب التي جاءت الى مصر سبعون"));
arasuperobject.biblemap.put("b1_46_28", new arasuperobject(1,46,28,"فارسل يهوذا امامه الى يوسف ليري الطريق امامه الى جاسان. ثم جاءوا الى ارض جاسان"));
arasuperobject.biblemap.put("b1_46_29", new arasuperobject(1,46,29,"فشدّ يوسف مركبته وصعد لاستقبال اسرائيل ابيه الى جاسان. ولما ظهر له وقع على عنقه وبكى على عنقه زمانا"));
arasuperobject.biblemap.put("b1_46_30", new arasuperobject(1,46,30,"فقال اسرائيل ليوسف اموت الآن بعدما رأيت وجهك انك حيّ بعد"));
arasuperobject.biblemap.put("b1_46_31", new arasuperobject(1,46,31,"ثم قال يوسف لاخوته ولبيت ابيه أصعد وأخبر فرعون واقول له اخوتي وبيت ابي الذين في ارض كنعان جاءوا اليّ"));
arasuperobject.biblemap.put("b1_46_32", new arasuperobject(1,46,32,"والرجال رعاة غنم. فانهم كانوا اهل مواش وقد جاءوا بغنمهم وبقرهم وكل ما لهم"));
arasuperobject.biblemap.put("b1_46_33", new arasuperobject(1,46,33,"فيكون اذا دعاكم فرعون وقال ما صناعتكم"));
arasuperobject.biblemap.put("b1_46_34", new arasuperobject(1,46,34,"ان تقولوا عبيدك اهل مواش منذ صبانا الى الآن نحن وآباؤنا جميعا. لكي تسكنوا في ارض جاسان. لان كل راعي غنم رجس للمصريين"));
arasuperobject.biblemap.put("b1_47_1", new arasuperobject(1,47,1,"فاتى يوسف واخبر فرعون وقال ابي واخوتي وغنمهم وبقرهم وكل ما لهم جاءوا من ارض كنعان. وهوذا هم في ارض جاسان"));
arasuperobject.biblemap.put("b1_47_2", new arasuperobject(1,47,2,"واخذ من جملة اخوته خمسة رجال واوقفهم امام فرعون"));
arasuperobject.biblemap.put("b1_47_3", new arasuperobject(1,47,3,"فقال فرعون لاخوته ما صناعتكم. فقالوا لفرعون عبيدك رعاة غنم نحن وآباؤنا جميعا"));
arasuperobject.biblemap.put("b1_47_4", new arasuperobject(1,47,4,"وقالوا لفرعون جئنا لنتغرب في الارض. اذ ليس لغنم عبيدك مرعى. لان الجوع شديد في ارض كنعان. فالآن ليسكن عبيدك في ارض جاسان"));
arasuperobject.biblemap.put("b1_47_5", new arasuperobject(1,47,5,"فكلم فرعون يوسف قائلا ابوك واخوتك جاءوا اليك"));
arasuperobject.biblemap.put("b1_47_6", new arasuperobject(1,47,6,"ارض مصر قدامك. في افضل الارض اسكن اباك واخوتك. ليسكنوا في ارض جاسان. وان علمت انه يوجد بينهم ذوو قدرة فاجعلهم رؤساء مواش على التي لي"));
arasuperobject.biblemap.put("b1_47_7", new arasuperobject(1,47,7,"ثم ادخل يوسف يعقوب اباه واوقفه امام فرعون. وبارك يعقوب فرعون"));
arasuperobject.biblemap.put("b1_47_8", new arasuperobject(1,47,8,"فقال فرعون ليعقوب كم هي ايام سني حياتك"));
arasuperobject.biblemap.put("b1_47_9", new arasuperobject(1,47,9,"فقال يعقوب لفرعون ايام سني غربتي مئة وثلاثون سنة. قليلة وردية كانت ايام سني حياتي ولم تبلغ الى ايام سني حياة آبائي في ايام غربتهم"));
arasuperobject.biblemap.put("b1_47_10", new arasuperobject(1,47,10,"وبارك يعقوب فرعون وخرج من لدن فرعون"));
arasuperobject.biblemap.put("b1_47_11", new arasuperobject(1,47,11,"فاسكن يوسف اباه واخوته واعطاهم ملكا في ارض مصر في افضل الارض في ارض رعمسيس كما امر فرعون"));
arasuperobject.biblemap.put("b1_47_12", new arasuperobject(1,47,12,"وعال يوسف اباه واخوته وكل بيت ابيه بطعام على حسب الاولاد"));
arasuperobject.biblemap.put("b1_47_13", new arasuperobject(1,47,13,"ولم يكن خبز في كل الارض. لان الجوع كان شديدا جدا. فخوّرت ارض مصر وارض كنعان من اجل الجوع"));
arasuperobject.biblemap.put("b1_47_14", new arasuperobject(1,47,14,"فجمع يوسف كل الفضة الموجودة في ارض مصر وفي ارض كنعان بالقمح الذي اشتروا. وجاء يوسف بالفضة الى بيت فرعون"));
arasuperobject.biblemap.put("b1_47_15", new arasuperobject(1,47,15,"فلما فرغت الفضة من ارض مصر ومن ارض كنعان أتى جميع المصريين الى يوسف قائلين أعطنا خبزا. فلماذا نموت قدامك. لان ليس فضة ايضا"));
arasuperobject.biblemap.put("b1_47_16", new arasuperobject(1,47,16,"فقال يوسف هاتوا مواشيكم فاعطيكم بمواشيكم ان لم يكن فضة ايضا"));
arasuperobject.biblemap.put("b1_47_17", new arasuperobject(1,47,17,"فجاءوا بمواشيهم الى يوسف. فاعطاهم يوسف خبزا بالخيل وبمواشي الغنم والبقر وبالحمير. فقاتهم بالخبز تلك السنة بدل جميع مواشيهم"));
arasuperobject.biblemap.put("b1_47_18", new arasuperobject(1,47,18,"ولما تمت تلك السنة اتوا اليه في السنة الثانية وقالوا له لا نخفي عن سيدي انه اذ قد فرغت الفضة ومواشي البهائم عند سيدي لم يبق قدام سيدي الا اجسادنا وارضنا"));
arasuperobject.biblemap.put("b1_47_19", new arasuperobject(1,47,19,"لماذا نموت امام عينيك نحن وارضنا جميعا. اشترنا وارضنا بالخبز فنصير نحن وارضنا عبيدا لفرعون. واعط بذارا لنحيا ولا نموت ولا تصير ارضنا قفرا"));
arasuperobject.biblemap.put("b1_47_20", new arasuperobject(1,47,20,"فاشترى يوسف كل ارض مصر لفرعون. اذ باع المصريون كل واحد حقله. لان الجوع اشتد عليهم. فصارت الارض لفرعون"));
arasuperobject.biblemap.put("b1_47_21", new arasuperobject(1,47,21,"واما الشعب فنقلهم الى المدن من اقصى حد مصر الى اقصاه"));
arasuperobject.biblemap.put("b1_47_22", new arasuperobject(1,47,22,"الا ان ارض الكهنة لم يشترها. اذ كانت للكهنة فريضة من قبل فرعون. فاكلوا فريضتهم التي اعطاهم فرعون. لذلك لم يبيعوا ارضهم"));
arasuperobject.biblemap.put("b1_47_23", new arasuperobject(1,47,23,"فقال يوسف للشعب اني قد اشتريتكم اليوم وارضكم لفرعون. هوذا لكم بذار فتزرعون الارض"));
arasuperobject.biblemap.put("b1_47_24", new arasuperobject(1,47,24,"ويكون عند الغلّة انكم تعطون خمسا لفرعون. والاربعة الاجزاء تكون لكم بذارا للحقل وطعاما لكم ولمن في بيوتكم وطعاما لاولادكم"));
arasuperobject.biblemap.put("b1_47_25", new arasuperobject(1,47,25,"فقالوا احييتنا. ليتنا نجد نعمة في عيني سيدي فنكون عبيدا لفرعون"));
arasuperobject.biblemap.put("b1_47_26", new arasuperobject(1,47,26,"فجعلها يوسف فرضا على ارض مصر الى هذا اليوم لفرعون الخمس. الا ان ارض الكهنة وحدهم لم تصر لفرعون"));
arasuperobject.biblemap.put("b1_47_27", new arasuperobject(1,47,27,"وسكن اسرائيل في ارض مصر في ارض جاسان. وتملكوا فيها واثمروا وكثروا جدا"));
arasuperobject.biblemap.put("b1_47_28", new arasuperobject(1,47,28,"وعاش يعقوب في ارض مصر سبع عشرة سنة. فكانت ايام يعقوب سنو حياته مئة وسبعا واربعين سنة"));
arasuperobject.biblemap.put("b1_47_29", new arasuperobject(1,47,29,"ولما قربت ايام اسرائيل ان يموت دعا ابنه يوسف وقال له ان كنت قد وجدت نعمة في عينيك فضع يدك تحت فخذي واصنع معي معروفا وامانة. لا تدفني في مصر"));
arasuperobject.biblemap.put("b1_47_30", new arasuperobject(1,47,30,"بل أضطجع مع آبائي. فتحملني من مصر وتدفنني في مقبرتهم. فقال انا افعل بحسب قولك"));
arasuperobject.biblemap.put("b1_47_31", new arasuperobject(1,47,31,"فقال احلف لي. فحلف له. فسجد اسرائيل على راس السرير"));
arasuperobject.biblemap.put("b1_48_1", new arasuperobject(1,48,1,"وحدث بعد هذه الامور انه قيل ليوسف هوذا ابوك مريض. فأخذ معه ابنيه منسّى وافرايم"));
arasuperobject.biblemap.put("b1_48_2", new arasuperobject(1,48,2,"فاخبر يعقوب وقيل له هوذا ابنك يوسف قادم اليك. فتشدد اسرائيل وجلس على السرير"));
arasuperobject.biblemap.put("b1_48_3", new arasuperobject(1,48,3,"وقال يعقوب ليوسف الله القادر على كل شيء ظهر لي في لوز في ارض كنعان وباركني"));
arasuperobject.biblemap.put("b1_48_4", new arasuperobject(1,48,4,"وقال لي ها انا اجعلك مثمرا واكثرك واجعلك جمهورا من الامم واعطي نسلك هذه الارض من بعدك ملكا ابديا"));
arasuperobject.biblemap.put("b1_48_5", new arasuperobject(1,48,5,"والآن ابناك المولودان لك في ارض مصر قبلما أتيت اليك الى مصر هما لي. افرايم ومنسّى كرأوبين وشمعون يكونان لي"));
arasuperobject.biblemap.put("b1_48_6", new arasuperobject(1,48,6,"واما اولادك الذين تلد بعدهما فيكونون لك. على اسم اخويهم يسمون في نصيبهم"));
arasuperobject.biblemap.put("b1_48_7", new arasuperobject(1,48,7,"وانا حين جئت من فدّان ماتت عندي راحيل في ارض كنعان في الطريق اذ بقيت مسافة من الارض حتى آتي الى افراتة. فدفنتها هناك في طريق افراتة التي هي بيت لحم"));
arasuperobject.biblemap.put("b1_48_8", new arasuperobject(1,48,8,"ورأى اسرائيل ابني يوسف فقال من هذان"));
arasuperobject.biblemap.put("b1_48_9", new arasuperobject(1,48,9,"فقال يوسف لابيه هما ابناي اللذان اعطاني الله ههنا. فقال قدمهما اليّ لاباركهما"));
arasuperobject.biblemap.put("b1_48_10", new arasuperobject(1,48,10,"واما عينا اسرائيل فكانتا قد ثقلتا من الشيخوخة لا يقدر ان يبصر. فقربهما اليه فقبّلهما واحتضنهما"));
arasuperobject.biblemap.put("b1_48_11", new arasuperobject(1,48,11,"وقال اسرائيل ليوسف لم اكن اظن اني ارى وجهك وهوذا الله قد اراني نسلك ايضا"));
arasuperobject.biblemap.put("b1_48_12", new arasuperobject(1,48,12,"ثم اخرجهما يوسف من بين ركبتيه وسجد امام وجهه الى الارض"));
arasuperobject.biblemap.put("b1_48_13", new arasuperobject(1,48,13,"واخذ يوسف الاثنين افرايم بيمينه عن يسار اسرائيل ومنسّى بيساره عن يمين اسرائيل وقربهما اليه"));
arasuperobject.biblemap.put("b1_48_14", new arasuperobject(1,48,14,"فمدّ اسرائيل يمينه ووضعها على راس افرايم وهو الصغير ويساره على راس منسّى. وضع يديه بفطنة فان منسّى كان البكر"));
arasuperobject.biblemap.put("b1_48_15", new arasuperobject(1,48,15,"وبارك يوسف وقال الله الذي سار امامه ابواي ابراهيم واسحق. الله الذي رعاني منذ وجودي الى هذا اليوم"));
arasuperobject.biblemap.put("b1_48_16", new arasuperobject(1,48,16,"الملاك الذي خلصني من كل شر يبارك الغلامين. وليدع عليهما اسمي واسم ابوي ابراهيم واسحق. وليكثرا كثيرا في الارض"));
arasuperobject.biblemap.put("b1_48_17", new arasuperobject(1,48,17,"فلما رأى يوسف ان اباه وضع يده اليمنى على راس افرايم ساء ذلك في عينيه. فامسك بيد ابيه لينقلها عن راس افرايم الى راس منسّى"));
arasuperobject.biblemap.put("b1_48_18", new arasuperobject(1,48,18,"وقال يوسف لابيه ليس هكذا يا ابي لان هذا هو البكر. ضع يمينك على راسه"));
arasuperobject.biblemap.put("b1_48_19", new arasuperobject(1,48,19,"فابى ابوه وقال علمت يا ابني علمت. هو ايضا يكون شعبا وهو ايضا يصير كبيرا. ولكن اخاه الصغير يكون اكبر منه ونسله يكون جمهورا من الامم"));
arasuperobject.biblemap.put("b1_48_20", new arasuperobject(1,48,20,"وباركهما في ذلك اليوم قائلا بك يبارك اسرائيل قائلا يجعلك الله كافرايم وكمنسّى. فقدّم افرايم على منسّى"));
arasuperobject.biblemap.put("b1_48_21", new arasuperobject(1,48,21,"وقال اسرائيل ليوسف ها انا اموت ولكن الله سيكون معكم ويردكم الى ارض آبائكم"));
arasuperobject.biblemap.put("b1_48_22", new arasuperobject(1,48,22,"وانا قد وهبت لك سهما واحدا فوق اخوتك اخذته من يد الاموريين بسيفي وقوسي"));
arasuperobject.biblemap.put("b1_49_1", new arasuperobject(1,49,1,"ودعا يعقوب بنيه وقال اجتمعوا لانبئكم بما يصيبكم في آخر الايام"));
arasuperobject.biblemap.put("b1_49_2", new arasuperobject(1,49,2,"اجتمعوا واسمعوا يا بني يعقوب. واصغوا الى اسرائيل ابيكم"));
arasuperobject.biblemap.put("b1_49_3", new arasuperobject(1,49,3,"رأوبين انت بكري قوتي واول قدرتي فضل الرفعة وفضل العزّ"));
arasuperobject.biblemap.put("b1_49_4", new arasuperobject(1,49,4,"فائرا كالماء لا تتفضل. لانك صعدت على مضجع ابيك. حينئذ دنسته. على فراشي صعد"));
arasuperobject.biblemap.put("b1_49_5", new arasuperobject(1,49,5,"شمعون ولاوي أخوان. آلات ظلم سيوفهما"));
arasuperobject.biblemap.put("b1_49_6", new arasuperobject(1,49,6,"في مجلسهما لا تدخل نفسي. بمجمعهما لا تتحد كرامتي لانهما في غضبهما قتلا انسانا وفي رضاهما عرقبا ثورا"));
arasuperobject.biblemap.put("b1_49_7", new arasuperobject(1,49,7,"ملعون غضبهما فانه شديد وسخطهما فانه قاس. اقسمهما في يعقوب وافرقهما في اسرائيل"));
arasuperobject.biblemap.put("b1_49_8", new arasuperobject(1,49,8,"يهوذا اياك يحمد اخوتك. يدك على قفا اعدائك يسجد لك بنو ابيك"));
arasuperobject.biblemap.put("b1_49_9", new arasuperobject(1,49,9,"يهوذا جرو اسد. من فريسة صعدت يا ابني. جثا وربض كاسد وكلبوة. من ينهضه"));
arasuperobject.biblemap.put("b1_49_10", new arasuperobject(1,49,10,"لا يزول قضيب من يهوذا ومشترع من بين رجليه حتى ياتي شيلون وله يكون خضوع شعوب"));
arasuperobject.biblemap.put("b1_49_11", new arasuperobject(1,49,11,"رابطا بالكرمة جحشه وبالجفنة ابن اتانه غسل بالخمر لباسه وبدم العنب ثوبه"));
arasuperobject.biblemap.put("b1_49_12", new arasuperobject(1,49,12,"مسود العينين من الخمر ومبيض الاسنان من اللبن"));
arasuperobject.biblemap.put("b1_49_13", new arasuperobject(1,49,13,"زبولون عند ساحل البحر يسكن وهو عند ساحل السفن وجانبه عند صيدون"));
arasuperobject.biblemap.put("b1_49_14", new arasuperobject(1,49,14,"يساكر حمار جسيم رابض بين الحظائر"));
arasuperobject.biblemap.put("b1_49_15", new arasuperobject(1,49,15,"فرأى المحلّ انه حسن والارض انها نزهة. فاحنى كتفه للحمل وصار للجزية عبدا"));
arasuperobject.biblemap.put("b1_49_16", new arasuperobject(1,49,16,"دان يدين شعبه كاحد اسباط اسرائيل"));
arasuperobject.biblemap.put("b1_49_17", new arasuperobject(1,49,17,"يكون دان حية على الطريق افعوانا على السبيل يلسع عقبي الفرس فيسقط راكبه الى الوراء"));
arasuperobject.biblemap.put("b1_49_18", new arasuperobject(1,49,18,"لخلاصك انتظرت يا رب"));
arasuperobject.biblemap.put("b1_49_19", new arasuperobject(1,49,19,"جاد يزحمه جيش. ولكنه يزحم مؤخره"));
arasuperobject.biblemap.put("b1_49_20", new arasuperobject(1,49,20,"اشير خبزه سمين وهو يعطي لذّات ملوك"));
arasuperobject.biblemap.put("b1_49_21", new arasuperobject(1,49,21,"نفتالي ايّلة مسيّبة يعطي اقوالا حسنة"));
arasuperobject.biblemap.put("b1_49_22", new arasuperobject(1,49,22,"يوسف غصن شجرة مثمرة غصن شجرة مثمرة على عين. اغصان قد ارتفعت فوق حائط"));
arasuperobject.biblemap.put("b1_49_23", new arasuperobject(1,49,23,"فمرّرته ورمته واضطهدته ارباب السهام"));
arasuperobject.biblemap.put("b1_49_24", new arasuperobject(1,49,24,"ولكن ثبتت بمتانة قوسه وتشددت سواعد يديه. من يدي عزيز يعقوب من هناك من الراعي صخر اسرائيل"));
arasuperobject.biblemap.put("b1_49_25", new arasuperobject(1,49,25,"من اله ابيك الذي يعينك ومن القادر على كل شيء الذي يباركك تاتي بركات السماء من فوق وبركات الغمر الرابض تحت. بركات الثديين والرحم"));
arasuperobject.biblemap.put("b1_49_26", new arasuperobject(1,49,26,"بركات ابيك فاقت على بركات ابويّ. الى منية الآكام الدهرية تكون على راس يوسف وعلى قمة نذير اخوته"));
arasuperobject.biblemap.put("b1_49_27", new arasuperobject(1,49,27,"بنيامين ذئب يفترس. في الصباح يأكل غنيمة وعند المساء يقسم نهبا"));
arasuperobject.biblemap.put("b1_49_28", new arasuperobject(1,49,28,"جميع هؤلاء هم اسباط اسرائيل الاثنا عشر. وهذا ما كلمهم به ابوهم وباركهم. كل واحد بحسب بركته باركهم"));
arasuperobject.biblemap.put("b1_49_29", new arasuperobject(1,49,29,"واوصاهم وقال لهم انا انضم الى قومي. ادفنوني عند آبائي في المغارة التي في حقل عفرون الحثي"));
arasuperobject.biblemap.put("b1_49_30", new arasuperobject(1,49,30,"في المغارة التي في حقل المكفيلة التي امام ممرا في ارض كنعان التي اشتراها ابراهيم مع الحقل من عفرون الحثي ملك قبر"));
arasuperobject.biblemap.put("b1_49_31", new arasuperobject(1,49,31,"هناك دفنوا ابراهيم وسارة امرأته. هناك دفنوا اسحق ورفقة امرأته. وهناك دفنت ليئة"));
arasuperobject.biblemap.put("b1_49_32", new arasuperobject(1,49,32,"شراء الحقل والمغارة التي فيه كان من بني حثّ"));
arasuperobject.biblemap.put("b1_49_33", new arasuperobject(1,49,33,"ولما فرغ يعقوب من توصية بنيه ضم رجليه الى السرير واسلم الروح وانضمّ الى قومه"));
arasuperobject.biblemap.put("b1_50_1", new arasuperobject(1,50,1,"فوقع يوسف على وجه ابيه وبكى عليه وقبّله"));
arasuperobject.biblemap.put("b1_50_2", new arasuperobject(1,50,2,"وامر يوسف عبيده الاطباء ان يحنطوا اباه. فحنط الاطباء اسرائيل"));
arasuperobject.biblemap.put("b1_50_3", new arasuperobject(1,50,3,"وكمل له اربعون يوما. لانه هكذا تكمل ايام المحنطين وبكى عليه المصريون سبعين يوما"));
arasuperobject.biblemap.put("b1_50_4", new arasuperobject(1,50,4,"وبعدما مضت ايام بكائه كلم يوسف بيت فرعون قائلا ان كنت قد وجدت نعمة في عيونكم فتكلموا في مسامع فرعون قائلين"));
arasuperobject.biblemap.put("b1_50_5", new arasuperobject(1,50,5,"ابي استحلفني قائلا ها انا اموت. في قبري الذي حفرت لنفسي في ارض كنعان هناك تدفنني. فالآن أصعد لادفن ابي وارجع"));
arasuperobject.biblemap.put("b1_50_6", new arasuperobject(1,50,6,"فقال فرعون اصعد وادفن اباك كما استحلفك"));
arasuperobject.biblemap.put("b1_50_7", new arasuperobject(1,50,7,"فصعد يوسف ليدفن اباه. وصعد معه جميع عبيد فرعون شيوخ بيته وجميع شيوخ ارض مصر"));
arasuperobject.biblemap.put("b1_50_8", new arasuperobject(1,50,8,"وكل بيت يوسف واخوته وبيت ابيه. غير انهم تركوا اولادهم وغنمهم وبقرهم في ارض جاسان"));
arasuperobject.biblemap.put("b1_50_9", new arasuperobject(1,50,9,"وصعد معه مركبات وفرسان. فكان الجيش كثيرا جدا"));
arasuperobject.biblemap.put("b1_50_10", new arasuperobject(1,50,10,"فأتوا الى بيدر أطاد الذي في عبر الاردن وناحوا هناك نوحا عظيما وشديدا جدا. وصنع لابيه مناحة سبعة ايام"));
arasuperobject.biblemap.put("b1_50_11", new arasuperobject(1,50,11,"فلما رأى اهل البلاد الكنعانيون المناحة في بيدر أطاد قالوا هذه مناحة ثقيلة للمصريين. لذلك دعي اسمه آبل مصرايم. الذي في عبر الاردن"));
arasuperobject.biblemap.put("b1_50_12", new arasuperobject(1,50,12,"وفعل له بنوه هكذا كما اوصاهم"));
arasuperobject.biblemap.put("b1_50_13", new arasuperobject(1,50,13,"حمله بنوه الى ارض كنعان ودفنوه في مغارة حقل المكفيلة التي اشتراها ابراهيم مع الحقل ملك قبر من عفرون الحثي امام ممرا"));
arasuperobject.biblemap.put("b1_50_14", new arasuperobject(1,50,14,"ثم رجع يوسف الى مصر هو واخوته وجميع الذين صعدوا معه لدفن ابيه بعدما دفن اباه"));
arasuperobject.biblemap.put("b1_50_15", new arasuperobject(1,50,15,"ولما رأى اخوة يوسف ان اباهم قد مات قالوا لعل يوسف يضطهدنا ويرد علينا جميع الشر الذي صنعنا به"));
arasuperobject.biblemap.put("b1_50_16", new arasuperobject(1,50,16,"فاوصوا الى يوسف قائلين ابوك اوصى قبل موته قائلا"));
arasuperobject.biblemap.put("b1_50_17", new arasuperobject(1,50,17,"هكذا تقولون ليوسف آه اصفح عن ذنب اخوتك وخطيتهم فانهم صنعوا بك شرا. فالآن اصفح عن ذنب عبيد اله ابيك. فبكى يوسف حين كلموه"));
arasuperobject.biblemap.put("b1_50_18", new arasuperobject(1,50,18,"وأتى اخوته ايضا ووقعوا امامه وقالوا ها نحن عبيدك"));
arasuperobject.biblemap.put("b1_50_19", new arasuperobject(1,50,19,"فقال لهم يوسف لا تخافوا. لانه هل انا مكان الله"));
arasuperobject.biblemap.put("b1_50_20", new arasuperobject(1,50,20,"انتم قصدتم لي شرا. اما الله فقصد به خيرا لكي يفعل كما اليوم. ليحيي شعبا كثيرا"));
arasuperobject.biblemap.put("b1_50_21", new arasuperobject(1,50,21,"فالآن لا تخافوا. انا اعولكم واولادكم. فعزّاهم وطيب قلوبهم"));
arasuperobject.biblemap.put("b1_50_22", new arasuperobject(1,50,22,"وسكن يوسف في مصر هو وبيت ابيه. وعاش يوسف مئة وعشر سنين"));
arasuperobject.biblemap.put("b1_50_23", new arasuperobject(1,50,23,"ورأى يوسف لافرايم اولاد الجيل الثالث. واولاد ماكير بن منسّى ايضا ولدوا على ركبتيّ يوسف"));
arasuperobject.biblemap.put("b1_50_24", new arasuperobject(1,50,24,"وقال يوسف لاخوته انا اموت. ولكن الله سيفتقدكم ويصعدكم من هذه الارض الى الارض التي حلف لابراهيم واسحق ويعقوب"));
arasuperobject.biblemap.put("b1_50_25", new arasuperobject(1,50,25,"واستحلف يوسف بني اسرائيل قائلا الله سيفتقدكم. فتصعدون عظامي من هنا"));
arasuperobject.biblemap.put("b1_50_26", new arasuperobject(1,50,26,"ثم مات يوسف وهو ابن مئة وعشر سنين. فحنطوه ووضع في تابوت في مصر"));

