arasuperobject.biblemap.put("b10_3_27", new arasuperobject(10,3,27,"ولما رجع ابنير الى حبرون مال به يوآب الى وسط الباب ليكلمه سرّا وضربه هناك في بطنه فمات بدم عسائيل اخيه"));
arasuperobject.biblemap.put("b10_3_28", new arasuperobject(10,3,28,"فسمع داود بعد ذلك فقال اني بريء انا ومملكتي لدى الرب الى الابد من دم ابنير بن نير"));
arasuperobject.biblemap.put("b10_3_29", new arasuperobject(10,3,29,"فليحل على راس يوآب وعلى كل بيت ابيه ولا ينقطع من بيت يوآب ذو سيل وابرص وعاكز على العكازة وساقط بالسيف ومحتاج الخبز"));
arasuperobject.biblemap.put("b10_3_30", new arasuperobject(10,3,30,"فقتل يوآب وابيشاي اخوه ابنير لانه قتل عسائيل اخاهما في جبعون في الحرب"));
arasuperobject.biblemap.put("b10_3_31", new arasuperobject(10,3,31,"فقال داود ليوآب ولجميع الشعب الذين معه مزقوا ثيابكم وتنطّقوا بالمسوح والطموا امام ابنير. وكان داود الملك يمشي وراء النعش"));
arasuperobject.biblemap.put("b10_3_32", new arasuperobject(10,3,32,"ودفنوا ابنير في حبرون. ورفع الملك صوته وبكى على قبر ابنير وبكى جميع الشعب"));
arasuperobject.biblemap.put("b10_3_33", new arasuperobject(10,3,33,"ورثا الملك ابنير وقال هل كموت احمق يموت ابنير"));
arasuperobject.biblemap.put("b10_3_34", new arasuperobject(10,3,34,"يداك لم تكونا مربوطتين ورجلاك لم توضعا في سلاسل نحاس. كالسقوط امام بني الاثم سقطت. وعاد جميع الشعب يبكون عليه"));
arasuperobject.biblemap.put("b10_3_35", new arasuperobject(10,3,35,"وجاء جميع الشعب ليطعموا داود خبزا وكان بعد نهار فحلف داود قائلا هكذا يفعل لي الله وهكذا يزيد ان كنت اذوق خبزا او شيئا آخر قبل غروب الشمس"));
arasuperobject.biblemap.put("b10_3_36", new arasuperobject(10,3,36,"فعرف جميع الشعب وحسن في اعينهم كما ان كل ما صنع الملك كان حسنا في اعين جميع الشعب"));
arasuperobject.biblemap.put("b10_3_37", new arasuperobject(10,3,37,"وعلم كل الشعب وجميع اسرائيل في ذلك اليوم انه لم يكن من الملك قتل ابنير بن نير"));
arasuperobject.biblemap.put("b10_3_38", new arasuperobject(10,3,38,"وقال الملك لعبيده ألا تعلمون ان رئيسا وعظيما سقط اليوم في اسرائيل"));
arasuperobject.biblemap.put("b10_3_39", new arasuperobject(10,3,39,"وانا اليوم ضعيف وممسوح ملكا وهؤلاء الرجال بنو صروية اقوى مني. يجازي الرب فاعل الشرّ كشرّه"));
arasuperobject.biblemap.put("b10_4_1", new arasuperobject(10,4,1,"ولما سمع ابن شاول ان ابنير قد مات في حبرون ارتخت يداه وارتاع جميع اسرائيل"));
arasuperobject.biblemap.put("b10_4_2", new arasuperobject(10,4,2,"وكان لابن شاول رجلان رئيسا غزاة اسم الواحد بعنة واسم الآخر ركاب ابنا رمّون البئيروتي من بني بنيامين. لان بئيروت حسبت لبنيامين"));
arasuperobject.biblemap.put("b10_4_3", new arasuperobject(10,4,3,"وهرب البئيروتيون الى جتّايم وتغربوا هناك الى هذا اليوم"));
arasuperobject.biblemap.put("b10_4_4", new arasuperobject(10,4,4,"وكان ليوناثان بن شاول ابن مضروب الرجلين. كان ابن خمس سنين عند مجيء خبر شاول ويوناثان من يزرعيل فحملته مربيته وهربت ولما كانت مسرعة لتهرب وقع وصار اعرج واسمه مفيبوشث"));
arasuperobject.biblemap.put("b10_4_5", new arasuperobject(10,4,5,"وسار ابنا رمّون البئيروتي ركاب وبعنة ودخلا عند حر النهار الى بيت ايشبوشث وهو نائم نومة الظهيرة"));
arasuperobject.biblemap.put("b10_4_6", new arasuperobject(10,4,6,"فدخلا الى وسط البيت لياخذا حنطة وضرباه في بطنه ثم افلت ركاب وبعنة اخوه"));
arasuperobject.biblemap.put("b10_4_7", new arasuperobject(10,4,7,"فعند دخولهما البيت كان هو مضطجعا على سريره في مخدع نومه فضرباه وقتلاه وقطعا راسه واخذا راسه وسارا في طريق العربة الليل كله"));
arasuperobject.biblemap.put("b10_4_8", new arasuperobject(10,4,8,"وأتيا براس ايشبوشث الى داود الى حبرون وقالا للملك هوذا راس ايشبوشث بن شاول عدوك الذي كان يطلب نفسك. وقد اعطى الرب لسيدي الملك انتقاما في هذا اليوم من شاول ومن نسله"));
arasuperobject.biblemap.put("b10_4_9", new arasuperobject(10,4,9,"فاجاب داود ركاب وبعنة اخاه ابني رمّون البئيروتي وقال لهما. حيّ هو الرب الذي فدى نفسي من كل ضيق"));
arasuperobject.biblemap.put("b10_4_10", new arasuperobject(10,4,10,"ان الذي اخبرني قائلا هوذا قد مات شاول وكان في عيني نفسه كمبشر قبضت عليه وقتلته في صقلغ. ذلك اعطيته بشارة"));
arasuperobject.biblemap.put("b10_4_11", new arasuperobject(10,4,11,"فكم بالحري اذا كان رجلان باغيان يقتلان رجلا صدّيقا في بيته على سريره. فالآن اما اطلب دمه من ايديكما وانزعكما من الارض"));
arasuperobject.biblemap.put("b10_4_12", new arasuperobject(10,4,12,"وأمر داود الغلمان فقتلوهما وقطعوا ايديهما وارجلهما وعلقوهما على البركة في حبرون. واما راس ايشبوشث فأخذوه ودفنوه في قبر ابنير في حبرون"));
arasuperobject.biblemap.put("b10_5_1", new arasuperobject(10,5,1,"وجاء جميع اسباط اسرائيل الى داود الى حبرون وتكلموا قائلين هوذا عظمك ولحمك نحن"));
arasuperobject.biblemap.put("b10_5_2", new arasuperobject(10,5,2,"ومنذ امس وما قبله حين كان شاول ملكا علينا قد كنت انت تخرج وتدخل اسرائيل وقد قال لك الرب انت ترعى شعبي اسرائيل وانت تكون رئيسا على اسرائيل"));
arasuperobject.biblemap.put("b10_5_3", new arasuperobject(10,5,3,"وجاء جميع شيوخ اسرائيل الى الملك الى حبرون فقطع الملك داود معهم عهدا في حبرون امام الرب ومسحوا داود ملكا على اسرائيل"));
arasuperobject.biblemap.put("b10_5_4", new arasuperobject(10,5,4,"كان داود ابن ثلاثين سنة حين ملك وملك اربعين سنة"));
arasuperobject.biblemap.put("b10_5_5", new arasuperobject(10,5,5,"في حبرون ملك على يهوذا سبع سنين وستة اشهر. وفي اورشليم ملك ثلاثا وثلاثين سنة على جميع اسرائيل ويهوذا"));
arasuperobject.biblemap.put("b10_5_6", new arasuperobject(10,5,6,"وذهب الملك ورجاله الى اورشليم الى اليبوسيين سكان الارض. فكلموا داود قائلين لا تدخل الى هنا ما لم تنزع العميان والعرج. اي لا يدخل داود الى هنا"));
arasuperobject.biblemap.put("b10_5_7", new arasuperobject(10,5,7,"واخذ داود حصن صهيون. هي مدينة داود"));
arasuperobject.biblemap.put("b10_5_8", new arasuperobject(10,5,8,"وقال داود في ذلك اليوم ان الذي يضرب اليبوسيين ويبلغ الى القناة والعرج والعمي المبغضين من نفس داود*****لذلك يقولون لا يدخل البيت اعمى او اعرج"));
arasuperobject.biblemap.put("b10_5_9", new arasuperobject(10,5,9,"واقام داود في الحصن وسمّاه مدينة داود. وبنى داود مستديرا من القلعة فداخلا"));
arasuperobject.biblemap.put("b10_5_10", new arasuperobject(10,5,10,"وكان داود يتزايد متعظما والرب اله الجنود معه"));
arasuperobject.biblemap.put("b10_5_11", new arasuperobject(10,5,11,"وارسل حيرام ملك صور رسلا الى داود وخشب ارز ونجارين وبنّائين فبنوا لداود بيتا"));
arasuperobject.biblemap.put("b10_5_12", new arasuperobject(10,5,12,"وعلم داود ان الرب قد اثبته ملكا على اسرائيل وانه قد رفّع ملكه من اجل شعبه اسرائيل"));
arasuperobject.biblemap.put("b10_5_13", new arasuperobject(10,5,13,"واخذ داود ايضا سراري ونساء من اورشليم بعد مجيئه من حبرون فولد ايضا لداود بنون وبنات"));
arasuperobject.biblemap.put("b10_5_14", new arasuperobject(10,5,14,"وهذه اسماء الذين ولدوا له في اورشليم. شموع وشوباب وناثان وسليمان"));
arasuperobject.biblemap.put("b10_5_15", new arasuperobject(10,5,15,"ويبحار واليشوع ونافج ويافيع"));
arasuperobject.biblemap.put("b10_5_16", new arasuperobject(10,5,16,"واليشمع واليداع واليفلط"));
arasuperobject.biblemap.put("b10_5_17", new arasuperobject(10,5,17,"وسمع الفلسطينيون انهم قد مسحوا داود ملكا على اسرائيل فصعد جميع الفلسطينيين ليفتّشوا على داود. ولما سمع داود نزل الى الحصن"));
arasuperobject.biblemap.put("b10_5_18", new arasuperobject(10,5,18,"وجاء الفلسطينيون وانتشروا في وادي الرفائيين"));
arasuperobject.biblemap.put("b10_5_19", new arasuperobject(10,5,19,"وسأل داود من الرب قائلا أأصعد الى الفلسطينيين. أتدفعهم ليدي. فقال الرب لداود اصعد لاني دفعا ادفع الفلسطينيين ليدك"));
arasuperobject.biblemap.put("b10_5_20", new arasuperobject(10,5,20,"فجاء داود الى بعل فراصيم وضربهم داود هناك وقال قد اقتحم الرب اعدائي امامي كاقتحام المياه. لذلك دعا اسم ذلك الموضع بعل فراصيم"));
arasuperobject.biblemap.put("b10_5_21", new arasuperobject(10,5,21,"وتركوا هناك اصنامهم فنزعها داود ورجاله"));
arasuperobject.biblemap.put("b10_5_22", new arasuperobject(10,5,22,"ثم عاد الفلسطينيون فصعدوا ايضا وانتشروا في وادي الرفائيين"));
arasuperobject.biblemap.put("b10_5_23", new arasuperobject(10,5,23,"فسأل داود من الرب فقال لا تصعد بل در من ورائهم وهلم عليهم مقابل اشجار البكا"));
arasuperobject.biblemap.put("b10_5_24", new arasuperobject(10,5,24,"وعندما تسمع صوت خطوات في رؤوس اشجار البكا حينئذ احترص لانه اذ ذاك يخرج الرب امامك لضرب محلّة الفلسطينيين"));
arasuperobject.biblemap.put("b10_5_25", new arasuperobject(10,5,25,"ففعل داود كذلك كما امره الرب وضرب الفلسطينيين من جبع الى مدخل جازر"));
arasuperobject.biblemap.put("b10_6_1", new arasuperobject(10,6,1,"وجمع داود ايضا جميع المنتخبين في اسرائيل ثلاثين الفا"));
arasuperobject.biblemap.put("b10_6_2", new arasuperobject(10,6,2,"وقام داود وذهب هو وجميع الشعب الذي معه من بعلة يهوذا ليصعدوا من هناك تابوت الله الذي يدعى عليه بالاسم اسم رب الجنود الجالس على الكروبيم"));
arasuperobject.biblemap.put("b10_6_3", new arasuperobject(10,6,3,"فاركبوا تابوت الله على عجلة جديدة وحملوه من بيت ابيناداب الذي في الاكمة وكان عزّة واخيو ابنا ابيناداب يسوقان العجلة الجديدة"));
arasuperobject.biblemap.put("b10_6_4", new arasuperobject(10,6,4,"فاخذوها من بيت ابيناداب الذي في الاكمة مع تابوت الله. وكان اخيو يسير امام التابوت"));
arasuperobject.biblemap.put("b10_6_5", new arasuperobject(10,6,5,"وداود وكل بيت اسرائيل يلعبون امام الرب بكل انواع الآلات من خشب السرو بالعيدان وبالرباب وبالدفوف وبالجنوك وبالصنوج"));
arasuperobject.biblemap.put("b10_6_6", new arasuperobject(10,6,6,"ولما انتهوا الى بيدر ناخون مدّ عزّة يده الى تابوت الله وامسكه لان الثيران انشمصت"));
arasuperobject.biblemap.put("b10_6_7", new arasuperobject(10,6,7,"فحمي غضب الرب على عزّة وضربه الله هناك لاجل غفله فمات هناك لدى تابوت الله"));
arasuperobject.biblemap.put("b10_6_8", new arasuperobject(10,6,8,"فاغتاظ داود لان الرب اقتحم عزّة اقتحاما وسمّى ذلك الموضع فارص عزّة الى هذا اليوم"));
arasuperobject.biblemap.put("b10_6_9", new arasuperobject(10,6,9,"وخاف داود من الرب في ذلك اليوم وقال كيف ياتي اليّ تابوت الرب"));
arasuperobject.biblemap.put("b10_6_10", new arasuperobject(10,6,10,"ولم يشأ داود ان ينقل تابوت الرب اليه الى مدينة داود فمال به داود الى بيت عوبيد ادوم الجتّي"));
arasuperobject.biblemap.put("b10_6_11", new arasuperobject(10,6,11,"وبقي تابوت الرب في بيت عوبيد ادوم الجتّي ثلاثة اشهر. وبارك الرب عوبيد ادوم وكل بيته"));
arasuperobject.biblemap.put("b10_6_12", new arasuperobject(10,6,12,"فأخبر الملك داود وقيل له قد بارك الرب بيت عوبيد ادوم وكل ما له بسبب تابوت الله. فذهب داود واصعد تابوت الله من بيت عوبيد ادوم الى مدينة داود بفرح"));
arasuperobject.biblemap.put("b10_6_13", new arasuperobject(10,6,13,"وكان كلما خطا حاملوا تابوت الرب ست خطوات يذبح ثورا وعجلا معلوفا"));
arasuperobject.biblemap.put("b10_6_14", new arasuperobject(10,6,14,"وكان داود يرقص بكل قوته امام الرب. وكان داود متنطقا بافود من كتان"));
arasuperobject.biblemap.put("b10_6_15", new arasuperobject(10,6,15,"فاصعد داود وجميع بيت اسرائيل تابوت الرب بالهتاف وبصوت البوق"));
arasuperobject.biblemap.put("b10_6_16", new arasuperobject(10,6,16,"ولما دخل تابوت الرب مدينة داود اشرفت ميكال بنت شاول من الكوّة ورأت الملك داود يطفر ويرقص امام الرب فاحتقرته في قلبها"));
arasuperobject.biblemap.put("b10_6_17", new arasuperobject(10,6,17,"فادخلوا تابوت الرب واوقفوه في مكانه في وسط الخيمة التي نصبها له داود واصعد داود محرقات امام الرب وذبائح سلامة"));
arasuperobject.biblemap.put("b10_6_18", new arasuperobject(10,6,18,"ولما انتهى داود من اصعاد المحرقات وذبائح السلامة بارك الشعب باسم رب الجنود"));
arasuperobject.biblemap.put("b10_6_19", new arasuperobject(10,6,19,"وقسم على جميع الشعب على كل جمهور اسرائيل رجالا ونساء على كل واحد رغيف خبز وكاس خمر وقرص زبيب. ثم ذهب كل الشعب كل واحد الى بيته"));
arasuperobject.biblemap.put("b10_6_20", new arasuperobject(10,6,20,"ورجع داود ليبارك بيته فخرجت ميكال بنت شاول لاستقبال داود وقالت ما كان اكرم ملك اسرائيل اليوم حيث تكشّف اليوم في اعين إماء عبيده كما يتكشّف احد السفهاء"));
arasuperobject.biblemap.put("b10_6_21", new arasuperobject(10,6,21,"فقال داود لميكال انما امام الرب الذي اختارني دون ابيك ودون كل بيته ليقيمني رئيسا على شعب الرب اسرائيل. فلعبت امام الرب"));
arasuperobject.biblemap.put("b10_6_22", new arasuperobject(10,6,22,"واني اتصاغر دون ذلك واكون وضيعا في عيني نفسي واما عند الاماء التي ذكرت فاتمجد"));
arasuperobject.biblemap.put("b10_6_23", new arasuperobject(10,6,23,"ولم يكن لميكال بنت شاول ولد الى يوم موتها"));
arasuperobject.biblemap.put("b10_7_1", new arasuperobject(10,7,1,"وكان لما سكن الملك في بيته واراحه الرب من كل الجهات من جميع اعدائه"));
arasuperobject.biblemap.put("b10_7_2", new arasuperobject(10,7,2,"ان الملك قال لناثان النبي انظر. اني ساكن في بيت من ارز وتابوت الله ساكن داخل الشقق"));
arasuperobject.biblemap.put("b10_7_3", new arasuperobject(10,7,3,"فقال ناثان للملك اذهب افعل كل ما بقلبك لان الرب معك"));
arasuperobject.biblemap.put("b10_7_4", new arasuperobject(10,7,4,"وفي تلك الليلة كان كلام الرب الى ناثان قائلا"));
arasuperobject.biblemap.put("b10_7_5", new arasuperobject(10,7,5,"اذهب وقل لعبدي داود هكذا قال الرب. أأنت تبني لي بيتا لسكناي"));
arasuperobject.biblemap.put("b10_7_6", new arasuperobject(10,7,6,"لاني لم اسكن في بيت منذ يوم اصعدت بني اسرائيل من مصر الى هذا اليوم بل كنت اسير في خيمة وفي مسكن"));
arasuperobject.biblemap.put("b10_7_7", new arasuperobject(10,7,7,"في كل ما سرت مع جميع بني اسرائيل هل تكلمت بكلمة الى احد قضاة اسرائيل الذين امرتهم ان يرعوا شعبي اسرائيل قائلا لماذا لم تبنوا لي بيتا من الارز"));
arasuperobject.biblemap.put("b10_7_8", new arasuperobject(10,7,8,"والآن فهكذا تقول لعبدي داود. هكذا قال رب الجنود انا اخذتك من المربض من وراء الغنم لتكون رئيسا على شعبي اسرائيل"));
arasuperobject.biblemap.put("b10_7_9", new arasuperobject(10,7,9,"وكنت معك حيثما توجهت وقرضت جميع اعدائك من امامك وعملت لك اسما عظيما كاسم العظماء الذين في الارض"));
arasuperobject.biblemap.put("b10_7_10", new arasuperobject(10,7,10,"وعيّنت مكانا لشعبي اسرائيل وغرسته فسكن في مكانه ولا يضطرب بعد ولا يعود بنو الاثم يذللونه كما في الاول"));
arasuperobject.biblemap.put("b10_7_11", new arasuperobject(10,7,11,"ومنذ يوم اقمت فيه قضاة على شعبي اسرائيل. وقد ارحتك من جميع اعدائك. والرب يخبرك ان الرب يصنع لك بيتا"));
arasuperobject.biblemap.put("b10_7_12", new arasuperobject(10,7,12,"متى كملت ايامك واضطجعت مع آبائك اقيم بعدك نسلك الذي يخرج من احشائك واثبت مملكته"));
arasuperobject.biblemap.put("b10_7_13", new arasuperobject(10,7,13,"هو يبني بيتا لاسمي وانا اثبت كرسي مملكته الى الابد"));
arasuperobject.biblemap.put("b10_7_14", new arasuperobject(10,7,14,"انا اكون له ابا وهو يكون لي ابنا. ان تعوج أؤدبه بقضيب الناس وبضربات بني آدم"));
arasuperobject.biblemap.put("b10_7_15", new arasuperobject(10,7,15,"ولكن رحمتي لا تنزع منه كما نزعتها من شاول الذي ازلته من امامك"));
arasuperobject.biblemap.put("b10_7_16", new arasuperobject(10,7,16,"ويأمن بيتك ومملكتك الى الابد امامك. كرسيك يكون ثابتا الى الابد"));
arasuperobject.biblemap.put("b10_7_17", new arasuperobject(10,7,17,"فحسب جميع هذا الكلام وحسب كل هذه الرؤيا كذلك كلم ناثان داود"));
arasuperobject.biblemap.put("b10_7_18", new arasuperobject(10,7,18,"فدخل الملك داود وجلس امام الرب وقال. من انا يا سيدي الرب وما هو بيتي حتى اوصلتني الى ههنا"));
arasuperobject.biblemap.put("b10_7_19", new arasuperobject(10,7,19,"وقلّ هذا ايضا في عينيك يا سيدي الرب فتكلمت ايضا من جهة بيت عبدك الى زمان طويل. وهذه عادة الانسان يا سيدي الرب"));
arasuperobject.biblemap.put("b10_7_20", new arasuperobject(10,7,20,"وبماذا يعود داود يكلمك وانت قد عرفت عبدك يا سيدي الرب"));
arasuperobject.biblemap.put("b10_7_21", new arasuperobject(10,7,21,"فمن اجل كلمتك وحسب قلبك فعلت هذه العظائم كلها لتعرّف عبدك"));
arasuperobject.biblemap.put("b10_7_22", new arasuperobject(10,7,22,"لذلك قد عظمت ايها الرب الاله لانه ليس مثلك وليس اله غيرك حسب كل ما سمعناه بآذاننا"));
arasuperobject.biblemap.put("b10_7_23", new arasuperobject(10,7,23,"وايّة امة على الارض مثل شعبك اسرائيل الذي سار الله ليفتديه لنفسه شعبا ويجعل له اسما ويعمل لكم العظائم والتخاويف لارضك امام شعبك الذي افتديته لنفسك من مصر من الشعوب وآلهتهم"));
arasuperobject.biblemap.put("b10_7_24", new arasuperobject(10,7,24,"وثبّت لنفسك شعبك اسرائيل شعبا لنفسك الى الابد وانت يا رب صرت لهم الها"));
arasuperobject.biblemap.put("b10_7_25", new arasuperobject(10,7,25,"والآن ايها الرب الاله اقم الى الابد الكلام الذي تكلمت به عن عبدك وعن بيته وافعل كما نطقت"));
arasuperobject.biblemap.put("b10_7_26", new arasuperobject(10,7,26,"وليتعظم اسمك الى الابد فيقال رب الجنود اله على اسرائيل. وليكن بيت عبدك داود ثابتا امامك"));
arasuperobject.biblemap.put("b10_7_27", new arasuperobject(10,7,27,"لانك انت يا رب الجنود اله اسرائيل قد اعلنت لعبدك قائلا اني ابني لك بيتا. لذلك وجد عبدك في قلبه ان يصلي لك هذه الصلاة"));
arasuperobject.biblemap.put("b10_7_28", new arasuperobject(10,7,28,"والآن يا سيدي الرب انت هو الله وكلامك هو حق وقد كلمت عبدك بهذا الخير"));
arasuperobject.biblemap.put("b10_7_29", new arasuperobject(10,7,29,"فالآن ارتض وبارك بيت عبدك ليكون الى الابد امامك لانك انت يا سيدي الرب قد تكلمت فليبارك بيت عبدك ببركتك الى الابد"));
arasuperobject.biblemap.put("b10_8_1", new arasuperobject(10,8,1,"وبعد ذلك ضرب داود الفلسطينيين وذلّلهم واخذ داود زمام القصبة من يد الفلسطينيين"));
arasuperobject.biblemap.put("b10_8_2", new arasuperobject(10,8,2,"وضرب الموآبيين وقاسهم بالحبل. اضجعهم على الارض فقاس بحبلين للقتل وبحبل كامل للاستحياء. وصار الموآبيون عبيدا لداود يقدمون هدايا"));
arasuperobject.biblemap.put("b10_8_3", new arasuperobject(10,8,3,"وضرب داود هدد عزر بن رحوب ملك صوبة حين ذهب ليرد سلطته عند نهر الفرات"));
arasuperobject.biblemap.put("b10_8_4", new arasuperobject(10,8,4,"فاخذ داود منه الفا وسبع مئة فارس وعشرين الف راجل. وعرقب داود جميع خيل المركبات وابقى منها مئة مركبة"));
arasuperobject.biblemap.put("b10_8_5", new arasuperobject(10,8,5,"فجاء ارام دمشق لنجدة هدد عزر ملك صوبة فضرب داود من ارام اثنين وعشرين الف رجل"));
arasuperobject.biblemap.put("b10_8_6", new arasuperobject(10,8,6,"وجعل داود محافظين في ارام دمشق وصار الاراميون لداود عبيدا يقدمون هدايا. وكان الرب يخلّص داود حيثما توجه"));
arasuperobject.biblemap.put("b10_8_7", new arasuperobject(10,8,7,"واخذ داود اتراس الذهب التي كانت على عبيد هدد عزر وأتى بها الى اورشليم"));
arasuperobject.biblemap.put("b10_8_8", new arasuperobject(10,8,8,"ومن باطح ومن بيروثاي مدينتي هدد عزر اخذ الملك داود نحاسا كثيرا جدا"));
arasuperobject.biblemap.put("b10_8_9", new arasuperobject(10,8,9,"وسمع توعي ملك حماة ان داود قد ضرب كل جيش هدد عزر"));
arasuperobject.biblemap.put("b10_8_10", new arasuperobject(10,8,10,"فارسل توعي يورام ابنه الى الملك داود ليسأل عن سلامته ويباركه لانه حارب هدد عزر وضربه لان هدد عزر كانت له حروب مع توعي. وكان بيده آنية فضة وآنية ذهب وآنية نحاس"));
arasuperobject.biblemap.put("b10_8_11", new arasuperobject(10,8,11,"وهذه ايضا قدسها الملك داود للرب مع الفضة والذهب الذي قدسه من جميع الشعوب الذين اخضعهم"));
arasuperobject.biblemap.put("b10_8_12", new arasuperobject(10,8,12,"من ارام ومن موآب ومن بني عمون ومن الفلسطينيين ومن عماليق ومن غنيمة هدد عزر بن رحوب ملك صوبة"));
arasuperobject.biblemap.put("b10_8_13", new arasuperobject(10,8,13,"ونصب داود تذكارا عند رجوعه من ضربه ثمانية عشر الفا من ارام في وادي الملح"));
arasuperobject.biblemap.put("b10_8_14", new arasuperobject(10,8,14,"وجعل في ادوم محافظين. وضع محافظين في ادوم كلها وكان جميع الادوميين عبيدا لداود وكان الرب يخلص داود حيثما توجه"));
arasuperobject.biblemap.put("b10_8_15", new arasuperobject(10,8,15,"وملك داود على جميع اسرائيل وكان داود يجري قضاء وعدلا لكل شعبه"));
arasuperobject.biblemap.put("b10_8_16", new arasuperobject(10,8,16,"وكان يوآب ابن صروية على الجيش ويهوشافاط بن اخيلود مسجلا"));
arasuperobject.biblemap.put("b10_8_17", new arasuperobject(10,8,17,"وصادوق بن اخيطوب واخيمالك بن ابياثار كاهنين وسرايا كاتبا"));
arasuperobject.biblemap.put("b10_8_18", new arasuperobject(10,8,18,"وبناياهو بن يهوياداع على الجلادين والسعاة وبنو داود كانوا كهنة"));
arasuperobject.biblemap.put("b10_9_1", new arasuperobject(10,9,1,"وقال داود هل يوجد بعد احد قد بقي من بيت شاول فاصنع معه معروفا من اجل يوناثان"));
arasuperobject.biblemap.put("b10_9_2", new arasuperobject(10,9,2,"وكان لبيت شاول عبد اسمه صيبا فاستدعوه الى داود وقال له الملك أأنت صيبا. فقال عبدك"));
arasuperobject.biblemap.put("b10_9_3", new arasuperobject(10,9,3,"فقال الملك ألا يوجد بعد احد لبيت شاول فاصنع معه احسان الله. فقال صيبا للملك بعد ابن ليوناثان اعرج الرجلين"));
arasuperobject.biblemap.put("b10_9_4", new arasuperobject(10,9,4,"فقال له الملك اين هو. فقال صيبا للملك هوذا هو في بيت ماكير بن عميئيل في لودبار"));
arasuperobject.biblemap.put("b10_9_5", new arasuperobject(10,9,5,"فارسل الملك داود واخذه من بيت ماكير بن عميئيل من لودبار"));
arasuperobject.biblemap.put("b10_9_6", new arasuperobject(10,9,6,"فجاء مفيبوشث بن يوناثان بن شاول الى داود وخرّ على وجهه وسجد. فقال داود يا مفيبوشث. فقال هانذا عبدك"));
arasuperobject.biblemap.put("b10_9_7", new arasuperobject(10,9,7,"فقال له داود لا تخف. فاني لاعملنّ معك معروفا من اجل يوناثان ابيك وارد لك كل حقول شاول ابيك وانت تاكل خبزا على مائدتي دائما"));
arasuperobject.biblemap.put("b10_9_8", new arasuperobject(10,9,8,"فسجد وقال من هو عبدك حتى تلتفت الى كلب ميت مثلي"));
arasuperobject.biblemap.put("b10_9_9", new arasuperobject(10,9,9,"ودعا الملك صيبا غلام شاول وقال له. كل ما كان لشاول ولكل بيته قد دفعته لابن سيدك"));
arasuperobject.biblemap.put("b10_9_10", new arasuperobject(10,9,10,"فتشتغل له في الارض انت وبنوك وعبيدك وتستغل ليكون لابن سيدك خبز ليأكل. ومفيبوشث ابن سيدك يأكل دائما خبزا على مائدتي. وكان لصيبا خمسة عشر ابنا وعشرون عبدا"));
arasuperobject.biblemap.put("b10_9_11", new arasuperobject(10,9,11,"فقال صبيا للملك حسب كل ما يامر به سيدي الملك عبده كذلك يصنع عبدك. فياكل مفيبوشث على مائدتي كواحد من بني الملك"));
arasuperobject.biblemap.put("b10_9_12", new arasuperobject(10,9,12,"وكان لمفيبوشث ابن صغير اسمه ميخا. وكان جميع ساكني بيت صيبا عبيدا لمفيبوشث"));
arasuperobject.biblemap.put("b10_9_13", new arasuperobject(10,9,13,"فسكن مفيبوشث في اورشليم لانه كان ياكل دائما على مائدة الملك. وكان اعرج من رجليه كلتيهما"));
arasuperobject.biblemap.put("b10_10_1", new arasuperobject(10,10,1,"وكان بعد ذلك ان ملك بني عمون مات وملك حانون ابنه عوضا عنه"));
arasuperobject.biblemap.put("b10_10_2", new arasuperobject(10,10,2,"فقال داود اصنع معروفا مع حانون بن ناحاش كما صنع ابوه معي معروفا. فارسل داود بيد عبيده يعزيه عن ابيه. فجاء عبيد داود الى ارض بني عمون"));
arasuperobject.biblemap.put("b10_10_3", new arasuperobject(10,10,3,"فقال رؤساء بني عمون لحانون سيدهم. هل يكرم داود اباك في عينيك حتى ارسل اليك معزّين. أليس لاجل فحص المدينة وتجسّسها وقلبها ارسل داود عبيده اليك"));
arasuperobject.biblemap.put("b10_10_4", new arasuperobject(10,10,4,"فاخذ حانون عبيد داود وحلق انصاف لحاهم وقصّ ثيابهم من الوسط الى استاههم ثم اطلقهم"));
arasuperobject.biblemap.put("b10_10_5", new arasuperobject(10,10,5,"ولما اخبروا داود ارسل للقائهم لان الرجال كانوا خجلين جدا. وقال الملك اقيموا في اريحا حتى تنبت لحاكم ثم ارجعوا"));
arasuperobject.biblemap.put("b10_10_6", new arasuperobject(10,10,6,"ولما رأى بنو عمون انهم قد انتنوا عند داود ارسل بنو عمون واستاجروا ارام بيت رحوب وارام صوبا عشرين الف راجل ومن ملك معكة الف رجل ورجال طوب اثني عشر الف رجل"));
arasuperobject.biblemap.put("b10_10_7", new arasuperobject(10,10,7,"فلما سمع داود ارسل يوآب وكل جيش الجبابرة"));
arasuperobject.biblemap.put("b10_10_8", new arasuperobject(10,10,8,"وخرج بنو عمون واصطفوا للحرب عند مدخل الباب وكان ارام صوبا ورحوب ورجال طوب ومعكة وحدهم في الحقل"));
arasuperobject.biblemap.put("b10_10_9", new arasuperobject(10,10,9,"فلما رأى يوآب ان مقدمة الحرب كانت نحوه من قدام ومن وراء اختار من جميع منتخبي اسرائيل وصفهم للقاء ارام"));
arasuperobject.biblemap.put("b10_10_10", new arasuperobject(10,10,10,"وسلّم بقية الشعب ليد اخيه ابيشاي فصفهم للقاء بني عمون"));
arasuperobject.biblemap.put("b10_10_11", new arasuperobject(10,10,11,"وقال ان قوي ارام عليّ تكون لي منجدا. وان قوي عليك بنو عمون اذهب لنجدتك"));
arasuperobject.biblemap.put("b10_10_12", new arasuperobject(10,10,12,"تجلد ولنتشدد من اجل شعبنا ومن اجل مدن الهنا والرب يفعل ما يحسن في عينيه"));
arasuperobject.biblemap.put("b10_10_13", new arasuperobject(10,10,13,"فتقدم يوآب والشعب الذين معه لمحاربة ارام فهربوا من امامه"));
arasuperobject.biblemap.put("b10_10_14", new arasuperobject(10,10,14,"ولما رأى بنو عمون انه قد هرب ارام هربوا من امام ابيشاي ودخلوا المدينة. فرجع يوآب عن بني عمون وأتى الى اورشليم"));
arasuperobject.biblemap.put("b10_10_15", new arasuperobject(10,10,15,"ولما رأى ارام انهم قد انكسروا امام اسرائيل اجتمعوا معا"));
arasuperobject.biblemap.put("b10_10_16", new arasuperobject(10,10,16,"وارسل هدر عزر فابرز ارام الذي في عبر النهر فأتوا الى حيلام وامامهم شوبك رئيس جيش هدر عزر"));
arasuperobject.biblemap.put("b10_10_17", new arasuperobject(10,10,17,"ولما أخبر داود جمع كل اسرائيل وعبر الاردن وجاء الى حيلام فاصطف ارام للقاء داود وحاربوه"));
arasuperobject.biblemap.put("b10_10_18", new arasuperobject(10,10,18,"وهرب ارام من امام اسرائيل وقتل داود من ارام سبع مئة مركبة واربعين الف فارس وضرب شوبك رئيس جيشه فمات هناك"));
arasuperobject.biblemap.put("b10_10_19", new arasuperobject(10,10,19,"ولما رأى جميع الملوك عبيد هدر عزر انهم انكسروا امام اسرائيل صالحوا اسرائيل واستعبدوا لهم وخاف ارام ان ينجدوا بني عمون بعد"));
arasuperobject.biblemap.put("b10_11_1", new arasuperobject(10,11,1,"وكان عند تمام السنة في وقت خروج الملوك ان داود ارسل يوآب وعبيده معه وجميع اسرائيل فاخربوا بني عمون وحاصروا ربّة. واما داود فاقام في اورشليم"));
arasuperobject.biblemap.put("b10_11_2", new arasuperobject(10,11,2,"وكان في وقت المساء ان داود قام عن سريره وتمشى على سطح بيت الملك فرأى من على السطح امرأة تستحمّ. وكانت المرأة جميلة المنظر جدا"));
arasuperobject.biblemap.put("b10_11_3", new arasuperobject(10,11,3,"فارسل داود وسأل عن المرأة فقال واحد أليست هذه بثشبع بنت اليعام امرأة اوريا الحثّي"));
arasuperobject.biblemap.put("b10_11_4", new arasuperobject(10,11,4,"فارسل داود رسلا واخذها فدخلت اليه فاضطجع معها وهي مطهّرة من طمثها. ثم رجعت الى بيتها"));
arasuperobject.biblemap.put("b10_11_5", new arasuperobject(10,11,5,"وحبلت المرأة فارسلت واخبرت داود وقالت اني حبلى"));
arasuperobject.biblemap.put("b10_11_6", new arasuperobject(10,11,6,"فارسل داود الى يوآب يقول ارسل اليّ اوريا الحثي. فارسل يوآب اوريا الى داود"));
arasuperobject.biblemap.put("b10_11_7", new arasuperobject(10,11,7,"فأتى اوريا اليه فسأل داود عن سلامة يوآب وسلامة الشعب ونجاح الحرب"));
arasuperobject.biblemap.put("b10_11_8", new arasuperobject(10,11,8,"وقال داود لاوريا انزل الى بيتك واغسل رجليك. فخرج اوريا من بيت الملك وخرجت وراءه حصة من عند الملك"));
arasuperobject.biblemap.put("b10_11_9", new arasuperobject(10,11,9,"ونام اوريا على باب بيت الملك مع جميع عبيد سيده ولم ينزل الى بيته"));
arasuperobject.biblemap.put("b10_11_10", new arasuperobject(10,11,10,"فاخبروا داود قائلين لم ينزل اوريا الى بيته. فقال داود لاوريا اما جئت من السفر. فلماذا لم تنزل الى بيتك"));
arasuperobject.biblemap.put("b10_11_11", new arasuperobject(10,11,11,"فقال اوريا لداود ان التابوت واسرائيل ويهوذا ساكنون في الخيام وسيدي يوآب وعبيد سيدي نازلون على وجه الصحراء وانا آتي الى بيتي لآكل واشرب واضطجع مع امرأتي. وحياتك وحياة نفسك لا افعل هذا الامر"));
arasuperobject.biblemap.put("b10_11_12", new arasuperobject(10,11,12,"فقال داود لاوريا اقم هنا اليوم ايضا وغدا اطلقك. فاقام اوريا في اورشليم ذلك اليوم وغده"));
arasuperobject.biblemap.put("b10_11_13", new arasuperobject(10,11,13,"ودعاه داود فاكل امامه وشرب واسكره. وخرج عند المساء ليضطجع في مضجعه مع عبيد سيده والى بيته لم ينزل"));
arasuperobject.biblemap.put("b10_11_14", new arasuperobject(10,11,14,"وفي الصباح كتب داود مكتوبا الى يوآب وارسله بيد اوريا"));
arasuperobject.biblemap.put("b10_11_15", new arasuperobject(10,11,15,"وكتب في المكتوب يقول. اجعلوا اوريا في وجه الحرب الشديدة وارجعوا من ورائه فيضرب ويموت"));
arasuperobject.biblemap.put("b10_11_16", new arasuperobject(10,11,16,"وكان في محاصرة يوآب المدينة انه جعل اوريا في الموضع الذي علم ان رجال البأس فيه"));
arasuperobject.biblemap.put("b10_11_17", new arasuperobject(10,11,17,"فخرج رجال المدينة وحاربوا يوآب فسقط بعض الشعب من عبيد داود ومات اوريا الحثّي ايضا"));
arasuperobject.biblemap.put("b10_11_18", new arasuperobject(10,11,18,"فارسل يوآب واخبر داود بجميع امور الحرب"));
arasuperobject.biblemap.put("b10_11_19", new arasuperobject(10,11,19,"واوصى الرسول قائلا عندما تفرغ من الكلام مع الملك عن جميع امور الحرب"));
arasuperobject.biblemap.put("b10_11_20", new arasuperobject(10,11,20,"فان اشتعل غضب الملك وقال لك لماذا دنوتم من المدينة للقتال. اما علمتم انهم يرمون من على السور"));
arasuperobject.biblemap.put("b10_11_21", new arasuperobject(10,11,21,"من قتل ابيمالك بن يربوشث. ألم ترمه امرأة بقطعة رحى من على السور فمات في تاباص. لماذا دنوتم من السور. فقل قد مات عبدك اوريا الحثي ايضا"));
arasuperobject.biblemap.put("b10_11_22", new arasuperobject(10,11,22,"فذهب الرسول ودخل واخبر داود بكل ما ارسله فيه يوآب"));
arasuperobject.biblemap.put("b10_11_23", new arasuperobject(10,11,23,"وقال الرسول لداود قد تجبر علينا القوم وخرجوا الينا الى الحقل فكنا عليهم الى مدخل الباب"));
arasuperobject.biblemap.put("b10_11_24", new arasuperobject(10,11,24,"فرمى الرماة عبيدك من على السور فمات البعض من عبيد الملك ومات عبدك اوريا الحثي ايضا"));
arasuperobject.biblemap.put("b10_11_25", new arasuperobject(10,11,25,"فقال داود للرسول هكذا تقول ليوآب. لا يسوء في عينيك هذا الامر لان السيف ياكل هذا وذاك. شدد قتالك على المدينة واخربها. وشدّده"));
arasuperobject.biblemap.put("b10_11_26", new arasuperobject(10,11,26,"فلما سمعت امرأة اوريا انه قد مات اوريا رجلها ندبت بعلها"));
arasuperobject.biblemap.put("b10_11_27", new arasuperobject(10,11,27,"ولما مضت المناحة ارسل داود وضمها الى بيته وصارت له امرأة وولدت له ابنا. واما الامر الذي فعله داود فقبح في عيني الرب"));
arasuperobject.biblemap.put("b10_12_1", new arasuperobject(10,12,1,"فارسل الرب ناثان الى داود. فجاء اليه وقال له. كان رجلان في مدينة واحدة واحد منهما غني والآخر فقير"));
arasuperobject.biblemap.put("b10_12_2", new arasuperobject(10,12,2,"وكان للغني غنم وبقر كثيرة جدا"));
arasuperobject.biblemap.put("b10_12_3", new arasuperobject(10,12,3,"واما الفقير فلم يكن له شيء الا نعجة واحدة صغيرة قد اقتناها وربّاها وكبرت معه ومع بنيه جميعا. تاكل من لقمته وتشرب من كاسه وتنام في حضنه وكانت له كابنة"));
arasuperobject.biblemap.put("b10_12_4", new arasuperobject(10,12,4,"فجاء ضيف الى الرجل الغني فعفا ان ياخذ من غنمه ومن بقره ليهيّئ للضيف الذي جاء اليه فاخذ نعجة الرجل الفقير وهيّأ للرجل الذي جاء اليه"));
arasuperobject.biblemap.put("b10_12_5", new arasuperobject(10,12,5,"فحمي غضب داود على الرجل جدا وقال لناثان حيّ هو الرب انه يقتل الرجل الفاعل ذلك"));
arasuperobject.biblemap.put("b10_12_6", new arasuperobject(10,12,6,"ويرد النعجة اربعة اضعاف لانه فعل هذا الامر ولانه لم يشفق"));
arasuperobject.biblemap.put("b10_12_7", new arasuperobject(10,12,7,"فقال ناثان لداود انت هو الرجل. هكذا قال الرب اله اسرائيل. انا مسحتك ملكا على اسرائيل وانقذتك من يد شاول"));
arasuperobject.biblemap.put("b10_12_8", new arasuperobject(10,12,8,"واعطيتك بيت سيدك ونساء سيدك في حضنك واعطيتك بيت اسرائيل ويهوذا وان كان ذلك قليلا كنت ازيد لك كذا وكذا"));
arasuperobject.biblemap.put("b10_12_9", new arasuperobject(10,12,9,"لماذا احتقرت كلام الرب لتعمل الشر في عينيه. قد قتلت اوريا الحثّي بالسيف واخذت امرأته لك امرأة واياه قتلت بسيف بني عمون"));
arasuperobject.biblemap.put("b10_12_10", new arasuperobject(10,12,10,"والآن لا يفارق السيف بيتك الى الابد لانك احتقرتني واخذت امرأة اوريا الحثّي لتكون لك امرأة"));
arasuperobject.biblemap.put("b10_12_11", new arasuperobject(10,12,11,"هكذا قال الرب هانذا اقيم عليك الشر من بيتك وآخذ نساءك امام عينيك واعطيهنّ لقريبك فيضطجع مع نسائك في عين هذه الشمس"));
arasuperobject.biblemap.put("b10_12_12", new arasuperobject(10,12,12,"لانك انت فعلت بالسرّ وانا افعل هذا الأمر قدام جميع اسرائيل وقدام الشمس"));
arasuperobject.biblemap.put("b10_12_13", new arasuperobject(10,12,13,"فقال داود لناثان قد اخطأت الى الرب. فقال ناثان لداود. الرب ايضا قد نقل عنك خطيتك. لا تموت"));
arasuperobject.biblemap.put("b10_12_14", new arasuperobject(10,12,14,"غير انه من اجل انك قد جعلت بهذا الامر اعداء الرب يشمتون فالابن المولود لك يموت"));
arasuperobject.biblemap.put("b10_12_15", new arasuperobject(10,12,15,"وذهب ناثان الى بيته وضرب الرب الولد الذي ولدته امرأة اوريا لداود فثقل"));
arasuperobject.biblemap.put("b10_12_16", new arasuperobject(10,12,16,"فسأل داود الله من اجل الصبي وصام داود صوما ودخل وبات مضطجعا على الارض"));
arasuperobject.biblemap.put("b10_12_17", new arasuperobject(10,12,17,"فقام شيوخ بيته عليه ليقيموه عن الارض فلم يشأ ولم ياكل معهم خبزا"));
arasuperobject.biblemap.put("b10_12_18", new arasuperobject(10,12,18,"وكان في اليوم السابع ان الولد مات فخاف عبيد داود ان يخبروه بان الولد قد مات لانهم قالوا هوذا لما كان الولد حيّا كلمناه فلم يسمع لصوتنا. فكيف نقول له قد مات الولد. يعمل اشرّ"));
arasuperobject.biblemap.put("b10_12_19", new arasuperobject(10,12,19,"ورأى داود عبيده يتناجون ففطن داود ان الولد قد مات. فقال داود لعبيده هل مات الولد. فقالوا مات"));
arasuperobject.biblemap.put("b10_12_20", new arasuperobject(10,12,20,"فقام داود عن الارض واغتسل وادّهن وبدل ثيابه ودخل بيت الرب وسجد ثم جاء الى بيته وطلب فوضعوا له خبزا فأكل"));
arasuperobject.biblemap.put("b10_12_21", new arasuperobject(10,12,21,"فقال له عبيده ما هذا الأمر الذي فعلت. لما كان الولد حيّا صمت وبكيت ولما مات الولد قمت واكلت خبزا"));
arasuperobject.biblemap.put("b10_12_22", new arasuperobject(10,12,22,"فقال لما كان الولد حيّا صمت وبكيت لاني قلت من يعلم ربما يرحمني الرب ويحيا الولد"));
arasuperobject.biblemap.put("b10_12_23", new arasuperobject(10,12,23,"والآن قد مات فلماذا اصوم. هل اقدر ان ارده بعد. انا ذاهب اليه واما هو فلا يرجع اليّ"));
arasuperobject.biblemap.put("b10_12_24", new arasuperobject(10,12,24,"وعزّى داود بثشبع امرأته ودخل اليها واضطجع معها فولدت ابنا فدعا اسمه سليمان والرب احبه"));
arasuperobject.biblemap.put("b10_12_25", new arasuperobject(10,12,25,"وارسل بيد ناثان النبي ودعا اسمه يديديّا من اجل الرب"));
arasuperobject.biblemap.put("b10_12_26", new arasuperobject(10,12,26,"وحارب يوآب ربّة بني عمون واخذ مدينة المملكة"));
arasuperobject.biblemap.put("b10_12_27", new arasuperobject(10,12,27,"وارسل يوآب رسلا الى داود يقول قد حاربت ربّة واخذت ايضا مدينة المياه"));
arasuperobject.biblemap.put("b10_12_28", new arasuperobject(10,12,28,"فالآن اجمع بقية الشعب وانزل على المدينة وخذها لئلا آخذ انا المدينة فيدعى باسمي عليها"));
arasuperobject.biblemap.put("b10_12_29", new arasuperobject(10,12,29,"فجمع داود كل الشعب وذهب الى ربّة وحاربها واخذها"));
arasuperobject.biblemap.put("b10_12_30", new arasuperobject(10,12,30,"واخذ تاج ملكهم عن راسه ووزنه وزنة من الذهب مع حجر كريم وكان على راس داود. واخرج غنيمة المدينة كثيرة جدا"));
arasuperobject.biblemap.put("b10_12_31", new arasuperobject(10,12,31,"واخرج الشعب الذي فيها ووضعهم تحت مناشير ونوارج حديد وفؤوس حديد وامرّهم في اتون الآجرّ وهكذا صنع بجميع مدن بني عمون. ثم رجع داود وجميع الشعب الى اورشليم"));
arasuperobject.biblemap.put("b10_13_1", new arasuperobject(10,13,1,"وجرى بعد ذلك انه كان لابشالوم بن داود اخت جميلة اسمها ثامار فاحبها امنون بن داود"));
arasuperobject.biblemap.put("b10_13_2", new arasuperobject(10,13,2,"وأحصر امنون للسقم من اجل ثامار اخته لانها كانت عذراء وعسر في عيني امنون ان يفعل لها شيئا"));
arasuperobject.biblemap.put("b10_13_3", new arasuperobject(10,13,3,"وكان لامنون صاحب اسمه يوناداب بن شمعى اخي داود. وكان يوناداب رجلا حكيما جدا"));
arasuperobject.biblemap.put("b10_13_4", new arasuperobject(10,13,4,"فقال له لماذا يا ابن الملك انت ضعيف هكذا من صباح الى صباح. أما تخبرني. فقال له امنون اني احب ثامار اخت ابشالوم اخي"));
arasuperobject.biblemap.put("b10_13_5", new arasuperobject(10,13,5,"فقال يوناداب اضطجع على سريرك وتمارض. واذا جاء ابوك ليراك فقل له دع ثامار اختي فتأتي وتطعمني خبزا وتعمل امامي الطعام لارى فأكل من يدها"));
arasuperobject.biblemap.put("b10_13_6", new arasuperobject(10,13,6,"فاضطجع امنون وتمارض فجاء الملك ليراه. فقال امنون للملك دع ثامار اختي فتأتي وتصنع امامي كعكتين فآكل من يدها"));
arasuperobject.biblemap.put("b10_13_7", new arasuperobject(10,13,7,"فارسل داود الى ثامار الى البيت قائلا اذهبي الى بيت امنون اخيك واعملي له طعاما"));
arasuperobject.biblemap.put("b10_13_8", new arasuperobject(10,13,8,"فذهبت ثامار الى بيت امنون اخيها وهو مضطجع. واخذت العجين وعجنت وعملت كعكا امامه وخبزت الكعك"));
arasuperobject.biblemap.put("b10_13_9", new arasuperobject(10,13,9,"واخذت المقلاة وسكبت امامه فابى ان ياكل. وقال امنون اخرجوا كل انسان عني. فخرج كل انسان عنه"));
arasuperobject.biblemap.put("b10_13_10", new arasuperobject(10,13,10,"ثم قال امنون لثامار ايتي بالطعام الى المخدع فآكل من يدك. فاخذت ثامار الكعك الذي عملته وأتت به امنون اخاها الى المخدع"));
arasuperobject.biblemap.put("b10_13_11", new arasuperobject(10,13,11,"وقدمت له لياكل فامسكها وقال لها تعالي اضطجعي معي يا اختي"));
arasuperobject.biblemap.put("b10_13_12", new arasuperobject(10,13,12,"فقالت له لا يا اخي لا تذلني لانه لا يفعل هكذا في اسرائيل. لا تعمل هذه القباحة"));
arasuperobject.biblemap.put("b10_13_13", new arasuperobject(10,13,13,"اما انا فاين اذهب بعاري واما انت فتكون كواحد من السفهاء في اسرائيل. والآن كلم الملك لانه لا يمنعني منك"));
arasuperobject.biblemap.put("b10_13_14", new arasuperobject(10,13,14,"فلم يشأ ان يسمع لصوتها بل تمكن منها وقهرها واضطجع معها"));
arasuperobject.biblemap.put("b10_13_15", new arasuperobject(10,13,15,"ثم ابغضها امنون بغضة شديدة جدا حتى ان البغضة التي ابغضها اياها كانت اشد من المحبة التي احبها اياها. وقال لها امنون قومي انطلقي"));
arasuperobject.biblemap.put("b10_13_16", new arasuperobject(10,13,16,"فقالت له لا سبب. هذا الشر بطردك اياي هو اعظم من الآخر الذي عملته بي. فلم يشأ ان يسمع لها"));
arasuperobject.biblemap.put("b10_13_17", new arasuperobject(10,13,17,"بل دعا غلامه الذي كان يخدمه وقال اطرد هذه عني خارجا واقفل الباب وراءها"));
arasuperobject.biblemap.put("b10_13_18", new arasuperobject(10,13,18,"وكان عليها ثوب ملون لان بنات الملك العذارى كنّ يلبسن جبّات مثل هذه. فاخرجها خادمه الى الخارج واقفل الباب وراءها"));
arasuperobject.biblemap.put("b10_13_19", new arasuperobject(10,13,19,"فجعلت ثامار رمادا على راسها ومزقت الثوب الملون الذي عليها ووضعت يدها على راسها وكانت تذهب صارخة"));
arasuperobject.biblemap.put("b10_13_20", new arasuperobject(10,13,20,"فقال لها ابشالوم اخوها هل كان امنون اخوك معك. فالآن يا اختي اسكتي. اخوك هو. لا تضعي قلبك على هذا الامر. فاقامت ثامار مستوحشة في بيت ابشالوم اخيها"));
arasuperobject.biblemap.put("b10_13_21", new arasuperobject(10,13,21,"ولما سمع الملك داود بجميع هذه الامور اغتاظ جدا"));
arasuperobject.biblemap.put("b10_13_22", new arasuperobject(10,13,22,"ولم يكلم ابشالوم امنون بشر ولا بخير لان ابشالوم ابغض امنون من اجل انه اذل ثامار اخته"));
arasuperobject.biblemap.put("b10_13_23", new arasuperobject(10,13,23,"وكان بعد سنتين من الزمان انه كان لابشالوم جزّازون في بعل حاصور التي عند افرايم. فدعا ابشالوم جميع بني الملك"));
arasuperobject.biblemap.put("b10_13_24", new arasuperobject(10,13,24,"وجاء ابشالوم الى الملك وقال هوذا لعبدك جزّازون. فليذهب الملك وعبيده مع عبدك"));
arasuperobject.biblemap.put("b10_13_25", new arasuperobject(10,13,25,"فقال الملك لابشالوم لا يا ابني. لا نذهب كلنا لئلا نثقل عليك. فالحّ عليه. فلم يشأ ان يذهب بل باركه"));
arasuperobject.biblemap.put("b10_13_26", new arasuperobject(10,13,26,"فقال ابشالوم اذا دع اخي امنون يذهب معنا. فقال الملك لماذا يذهب معك"));
arasuperobject.biblemap.put("b10_13_27", new arasuperobject(10,13,27,"فالحّ عليه ابشالوم فارسل معه امنون وجميع بني الملك"));
arasuperobject.biblemap.put("b10_13_28", new arasuperobject(10,13,28,"فاوصى ابشالوم غلمانه قائلا انظروا. متى طاب قلب امنون بالخمر وقلت لكم اضربوا امنون فاقتلوه. لا تخافوا. أليس اني انا أمرتكم. فتشددوا وكونوا ذوي بأس"));
arasuperobject.biblemap.put("b10_13_29", new arasuperobject(10,13,29,"ففعل غلمان ابشالوم بامنون كما امر ابشالوم. فقام جميع بني الملك وركبوا كل واحد على بغله وهربوا"));
arasuperobject.biblemap.put("b10_13_30", new arasuperobject(10,13,30,"وفيما هم في الطريق وصل الخبر الى داود وقيل له قد قتل ابشالوم جميع بني الملك ولم يتبق منهم احد"));
arasuperobject.biblemap.put("b10_13_31", new arasuperobject(10,13,31,"فقام الملك ومزق ثيابه واضطجع على الارض وجميع عبيده واقفون وثيابهم ممزقة"));
arasuperobject.biblemap.put("b10_13_32", new arasuperobject(10,13,32,"فاجاب يوناداب بن شمعى اخي داود وقال لا يظن سيدي انهم قتلوا جميع الفتيان بني الملك. انما امنون وحده مات لان ذلك قد وضع عند ابشالوم منذ يوم اذل ثامار اخته"));
arasuperobject.biblemap.put("b10_13_33", new arasuperobject(10,13,33,"والآن لا يضعن سيدي الملك في قلبه شيئا قائلا ان جميع بني الملك قد ماتوا. انما امنون وحده مات"));
arasuperobject.biblemap.put("b10_13_34", new arasuperobject(10,13,34,"وهرب ابشالوم. ورفع الغلام الرقيب طرفه ونظر واذا بشعب كثير يسيرون على الطريق وراءه بجانب الجبل"));
arasuperobject.biblemap.put("b10_13_35", new arasuperobject(10,13,35,"فقال يوناداب للملك هوذا بنو الملك قد جاءوا. كما قال عبدك كذلك صار"));
arasuperobject.biblemap.put("b10_13_36", new arasuperobject(10,13,36,"ولما فرغ من الكلام اذا ببني الملك قد جاءوا ورفعوا اصواتهم وبكوا وكذلك بكى الملك وعبيده بكاء عظيما جدا"));
arasuperobject.biblemap.put("b10_13_37", new arasuperobject(10,13,37,"فهرب ابشالوم وذهب الى تلماي بن عميهود ملك جشور. وناح داود على ابنه الايام كلها"));
arasuperobject.biblemap.put("b10_13_38", new arasuperobject(10,13,38,"وهرب ابشالوم وذهب الى جشور وكان هناك ثلاث سنين"));
arasuperobject.biblemap.put("b10_13_39", new arasuperobject(10,13,39,"وكان داود يتوق الى الخروج الى ابشالوم لانه تعزّى عن امنون حيث انه مات"));
arasuperobject.biblemap.put("b10_14_1", new arasuperobject(10,14,1,"وعلم يوآب ابن صروية ان قلب الملك على ابشالوم"));
arasuperobject.biblemap.put("b10_14_2", new arasuperobject(10,14,2,"فارسل يوآب الى تقوع واخذ من هناك امرأة حكيمة. وقال لها تظاهري بالحزن والبسي ثياب الحزن ولا تدّهني بزيت بل كوني كامرأة لها ايام كثيرة وهي تنوح على ميت"));
arasuperobject.biblemap.put("b10_14_3", new arasuperobject(10,14,3,"وادخلي الى الملك وكلميه بهذا الكلام. وجعل يوآب الكلام في فمها"));
arasuperobject.biblemap.put("b10_14_4", new arasuperobject(10,14,4,"وكلمت المرأة التقوعية الملك وخرّت على وجهها الى الارض وسجدت وقالت اعن ايها الملك"));
arasuperobject.biblemap.put("b10_14_5", new arasuperobject(10,14,5,"فقال لها الملك ما بالك. فقالت اني امرأة ارملة. قد مات رجلي"));
arasuperobject.biblemap.put("b10_14_6", new arasuperobject(10,14,6,"ولجاريتك ابنان فتخاصما في الحقل وليس من يفصل بينهما فضرب احدهما الآخر وقتله"));
arasuperobject.biblemap.put("b10_14_7", new arasuperobject(10,14,7,"وهوذا العشيرة كلها قد قامت على جاريتك وقالوا سلمي ضارب اخيه لنقتله بنفس اخيه الذي قتله فنهلك الوارث ايضا. فيطفئون جمرتي التي بقيت ولا يتركون لرجلي اسما ولا بقية على وجه الارض"));
arasuperobject.biblemap.put("b10_14_8", new arasuperobject(10,14,8,"فقال الملك للمرأة اذهبي الى بيتك وانا اوصي فيك"));
arasuperobject.biblemap.put("b10_14_9", new arasuperobject(10,14,9,"فقالت المرأة التقوعية للملك عليّ الاثم يا سيدي الملك وعلى بيت ابي والملك وكرسيه نقيان"));
arasuperobject.biblemap.put("b10_14_10", new arasuperobject(10,14,10,"فقال الملك اذا كلمك احد فأتي به اليّ فلا يعود يمسك بعد"));
arasuperobject.biblemap.put("b10_14_11", new arasuperobject(10,14,11,"فقالت اذكر ايها الملك الرب الهك حتى لا يكثر ولي الدم القتل لئلا يهلكوا ابني. فقال حيّ هو الرب انه لا تسقط شعرة من شعر ابنك الى الارض"));
arasuperobject.biblemap.put("b10_14_12", new arasuperobject(10,14,12,"فقالت المرأة لتتكلم جاريتك كلمة الى سيدي الملك. فقال تكلمي"));
arasuperobject.biblemap.put("b10_14_13", new arasuperobject(10,14,13,"فقالت المرأة ولماذا افتكرت بمثل هذا الامر على شعب الله. ويتكلم الملك بهذا الكلام كمذنب بما ان الملك لا يرد منفيه"));
arasuperobject.biblemap.put("b10_14_14", new arasuperobject(10,14,14,"لانه لا بد ان نموت ونكون كالماء المهراق على الارض الذي لا يجمع ايضا. ولا ينزع الله نفسا بل يفكر افكارا حتى لا يطرد عنه منفيّه"));
arasuperobject.biblemap.put("b10_14_15", new arasuperobject(10,14,15,"والآن حيث اني جئت لاكلم الملك سيدي بهذا الأمر لان الشعب اخافني فقالت جاريتك اكلم الملك لعل الملك يفعل كقول امته"));
arasuperobject.biblemap.put("b10_14_16", new arasuperobject(10,14,16,"لان الملك يسمع لينقذ امته من يد الرجل الذي يريد ان يهلكني انا وابني معا من نصيب الله"));
arasuperobject.biblemap.put("b10_14_17", new arasuperobject(10,14,17,"فقالت جاريتك ليكن كلام سيدي الملك عزاء لانه سيدي الملك انما هو كملاك الله لفهم الخير والشر والرب الهك يكون معك"));
arasuperobject.biblemap.put("b10_14_18", new arasuperobject(10,14,18,"فاجاب الملك وقال للمرأة لا تكتمي عني امرا اسألك عنه. فقالت المرأة ليتكلم سيدي الملك"));
arasuperobject.biblemap.put("b10_14_19", new arasuperobject(10,14,19,"فقال الملك هل يد يوآب معك في هذا كله. فاجابت المرأة وقالت حيّة هي نفسك يا سيدي الملك لا يحاد يمينا او يسارا عن كل ما تكلم به سيدي الملك. لان عبدك يوآب هو اوصاني وهو وضع في فم جاريتك كل هذا الكلام"));
arasuperobject.biblemap.put("b10_14_20", new arasuperobject(10,14,20,"لاجل تحويل وجه الكلام فعل عبدك يوآب هذا الامر وسيدي حكيم كحكمة ملاك الله ليعلم كل ما في الارض"));
arasuperobject.biblemap.put("b10_14_21", new arasuperobject(10,14,21,"فقال الملك ليوآب هانذا قد فعلت هذا الامر فاذهب رد الفتى ابشالوم"));
arasuperobject.biblemap.put("b10_14_22", new arasuperobject(10,14,22,"فسقط يوآب على وجهه الى الارض وسجد وبارك الملك وقال يوآب اليوم علم عبدك اني قد وجدت نعمة في عينيك يا سيدي الملك اذ فعل الملك قول عبده"));
arasuperobject.biblemap.put("b10_14_23", new arasuperobject(10,14,23,"ثم قام يوآب وذهب الى جشور وأتى بابشالوم الى اورشليم"));
arasuperobject.biblemap.put("b10_14_24", new arasuperobject(10,14,24,"فقال الملك لينصرف الى بيته ولا ير وجهي. فانصرف ابشالوم الى بيته ولم ير وجه الملك"));
arasuperobject.biblemap.put("b10_14_25", new arasuperobject(10,14,25,"ولم يكن في كل اسرائيل رجل جميل وممدوح جدا كابشالوم من باطن قدمه حتى هامته لم يكن فيه عيب"));
arasuperobject.biblemap.put("b10_14_26", new arasuperobject(10,14,26,"وعند حلقه راسه اذ كان يحلقه في آخر كل سنة لانه كان يثقل عليه فيحلقه كان يزن شعر راسه مئتي شاقل بوزن الملك"));
arasuperobject.biblemap.put("b10_14_27", new arasuperobject(10,14,27,"وولد لابشالوم ثلاثة بنين وبنت واحدة اسمها ثامار وكانت امرأة جميلة المنظر"));
arasuperobject.biblemap.put("b10_14_28", new arasuperobject(10,14,28,"واقام ابشالوم في اورشليم سنتين ولم ير وجه الملك"));
arasuperobject.biblemap.put("b10_14_29", new arasuperobject(10,14,29,"فارسل ابشالوم الى يوآب ليرسله الى الملك فلم يشأ ان يأتي اليه. ثم ارسل ايضا ثانية فلم يشأ ان يأتي"));
arasuperobject.biblemap.put("b10_14_30", new arasuperobject(10,14,30,"فقال لعبيده انظروا. حقلة يوآب بجانبي وله هناك شعير. اذهبوا واحرقوه بالنار. فاحرق عبيد ابشالوم الحقلة بالنار"));
arasuperobject.biblemap.put("b10_14_31", new arasuperobject(10,14,31,"فقام يوآب وجاء الى ابشالوم الى البيت وقال له لماذا احرق عبيدك حقلتي بالنار"));
arasuperobject.biblemap.put("b10_14_32", new arasuperobject(10,14,32,"فقال ابشالوم ليوآب هانذا قد ارسلت اليك قائلا تعال الى هنا فارسلك الى الملك تقول لماذا جئت من جشور. خير لي لو كنت باقيا هناك. فالآن اني ارى وجه الملك وان وجد فيّ اثم فليقتلني"));
arasuperobject.biblemap.put("b10_14_33", new arasuperobject(10,14,33,"فجاء يوآب الى الملك واخبره. ودعا ابشالوم فأتى الى الملك وسجد على وجهه الى الارض قدام الملك فقبّل الملك ابشالوم"));
arasuperobject.biblemap.put("b10_15_1", new arasuperobject(10,15,1,"وكان بعد ذلك ان ابشالوم اتخذ مركبة وخيلا وخمسين رجلا يجرون قدامه"));
arasuperobject.biblemap.put("b10_15_2", new arasuperobject(10,15,2,"وكان ابشالوم يبكر ويقف بجانب طريق الباب وكل صاحب دعوى آت الى الملك لاجل الحكم كان ابشالوم يدعوه اليه ويقول من اية مدينة انت فيقول من احد اسباط اسرائيل عبدك"));
arasuperobject.biblemap.put("b10_15_3", new arasuperobject(10,15,3,"فيقول ابشالوم له. انظر امورك صالحة ومستقيمة ولكن ليس من يسمع لك من قبل الملك"));
arasuperobject.biblemap.put("b10_15_4", new arasuperobject(10,15,4,"ثم يقول ابشالوم من يجعلني قاضيا في الارض فيأتي اليّ كل انسان له خصومة ودعوى فانصفه"));
arasuperobject.biblemap.put("b10_15_5", new arasuperobject(10,15,5,"وكان اذا تقدم احد ليسجد له يمد يده ويمسكه ويقبله"));
arasuperobject.biblemap.put("b10_15_6", new arasuperobject(10,15,6,"وكان ابشالوم يفعل مثل هذا الامر لجميع اسرائيل الذين كانوا يأتون لاجل الحكم الى الملك فاسترق ابشالوم قلوب رجال اسرائيل"));
arasuperobject.biblemap.put("b10_15_7", new arasuperobject(10,15,7,"وفي نهاية اربعين سنة قال ابشالوم للملك دعني فاذهب واوفي نذري الذي نذرته للرب في حبرون"));
arasuperobject.biblemap.put("b10_15_8", new arasuperobject(10,15,8,"لان عبدك نذر نذرا عند سكناي في جشور في ارام قائلا ان ارجعني الرب الى اورشليم فاني اعبد الرب"));
arasuperobject.biblemap.put("b10_15_9", new arasuperobject(10,15,9,"فقال له الملك اذهب بسلام. فقام وذهب الى حبرون"));
arasuperobject.biblemap.put("b10_15_10", new arasuperobject(10,15,10,"وارسل ابشالوم جواسيس في جميع اسباط اسرائيل قائلا اذا سمعتم صوت البوق فقولوا قد ملك ابشالوم في حبرون"));
arasuperobject.biblemap.put("b10_15_11", new arasuperobject(10,15,11,"وانطلق مع ابشالوم مئتا رجل من اورشليم قد دعوا وذهبوا ببساطة ولم يكونوا يعلمون شيئا"));
arasuperobject.biblemap.put("b10_15_12", new arasuperobject(10,15,12,"وارسل ابشالوم الى اخيتوفل الجيلوني مشير داود من مدينته جيلوه اذ كان يذبح ذبائح. وكانت الفتنة شديدة وكان الشعب لا يزال يتزايد مع ابشالوم"));
arasuperobject.biblemap.put("b10_15_13", new arasuperobject(10,15,13,"فأتى مخبر الى داود قائلا ان قلوب رجال اسرائيل صارت وراء ابشالوم"));
arasuperobject.biblemap.put("b10_15_14", new arasuperobject(10,15,14,"فقال داود لجميع عبيده الذين معه في اورشليم قوموا بنا نهرب لانه ليس لنا نجاة من وجه ابشالوم. اسرعوا للذهاب لئلا يبادر ويدركنا وينزل بنا الشر ويضرب المدينة بحد السيف"));
arasuperobject.biblemap.put("b10_15_15", new arasuperobject(10,15,15,"فقال عبيد الملك للملك حسب كل ما يختاره سيدنا الملك نحن عبيده"));
arasuperobject.biblemap.put("b10_15_16", new arasuperobject(10,15,16,"فخرج الملك وجميع بيته وراءه. وترك الملك عشر نساء سراري لحفظ البيت"));
arasuperobject.biblemap.put("b10_15_17", new arasuperobject(10,15,17,"وخرج الملك وكل الشعب في اثره ووقفوا عند البيت الابعد"));
arasuperobject.biblemap.put("b10_15_18", new arasuperobject(10,15,18,"وجميع عبيده كانوا يعبرون بين يديه مع جميع الجلادين والسعاة وجميع الجتّيين ست مئة رجل اتوا وراءه من جتّ وكانوا يعبرون بين يدي الملك"));
arasuperobject.biblemap.put("b10_15_19", new arasuperobject(10,15,19,"فقال الملك لإتّاي الجتّي لماذا تذهب انت ايضا معنا. ارجع واقم مع الملك لانك غريب ومنفي ايضا من وطنك"));
arasuperobject.biblemap.put("b10_15_20", new arasuperobject(10,15,20,"امسا جئت واليوم اتيهك بالذهاب معنا وانا انطلق الى حيث انطلق. ارجع ورجع اخوتك. الرحمة والحق معك"));
arasuperobject.biblemap.put("b10_15_21", new arasuperobject(10,15,21,"فاجاب اتّاي الملك وقال حيّ هو الرب وحي سيدي الملك انه حيثما كان سيدي الملك ان كان للموت او للحياة فهناك يكون عبدك ايضا"));
arasuperobject.biblemap.put("b10_15_22", new arasuperobject(10,15,22,"فقال داود لإتّاي اذهب واعبر. فعبر اتّاي الجتّي وجميع رجاله وجميع الاطفال الذين معه"));
arasuperobject.biblemap.put("b10_15_23", new arasuperobject(10,15,23,"وكانت جميع الارض تبكي بصوت عظيم وجميع الشعب يعبرون وعبر الملك في وادي قدرون وعبر جميع الشعب نحو طريق البرية"));
arasuperobject.biblemap.put("b10_15_24", new arasuperobject(10,15,24,"واذا بصادوق ايضا وجميع اللاويين معه يحملون تابوت عهد الله. فوضعوا تابوت الله وصعد ابياثار حتى انتهى جميع الشعب من العبور من المدينة"));
arasuperobject.biblemap.put("b10_15_25", new arasuperobject(10,15,25,"فقال الملك لصادوق ارجع تابوت الله الى المدينة فان وجدت نعمة في عيني الرب فانه يرجعني ويريني اياه ومسكنه"));
arasuperobject.biblemap.put("b10_15_26", new arasuperobject(10,15,26,"وان قال هكذا اني لم اسرّ بك فهانذا فليفعل بي حسبما يحسن في عينيه"));
arasuperobject.biblemap.put("b10_15_27", new arasuperobject(10,15,27,"ثم قال الملك لصادوق الكاهن أأنت راء. فارجع الى المدينة بسلام انت واخميعص ابنك ويوناثان بن ابياثار. ابناكما كلاهما معكما"));
arasuperobject.biblemap.put("b10_15_28", new arasuperobject(10,15,28,"انظروا. اني اتوانى في سهول البرية حتى تأتي كلمة منكم لتخبيري"));
arasuperobject.biblemap.put("b10_15_29", new arasuperobject(10,15,29,"فارجع صادوق وابياثار تابوت الله الى اورشليم واقاما هناك"));
arasuperobject.biblemap.put("b10_15_30", new arasuperobject(10,15,30,"واما داود فصعد في مصعد جبل الزيتون كان يصعد باكيا وراسه مغطى ويمشي حافيا وجميع الشعب الذين معه غطوا كل واحد راسه وكانوا يصعدون وهم يبكون"));
arasuperobject.biblemap.put("b10_15_31", new arasuperobject(10,15,31,"وأخبر داود وقيل له ان اخيتوفل بين الفاتنين مع ابشالوم. فقال داود حمق يا رب مشورة اخيتوفل"));
arasuperobject.biblemap.put("b10_15_32", new arasuperobject(10,15,32,"ولما وصل داود الى القمة حيث سجد للّه اذا بحوشاي الاركي قد لقيه ممزق الثوب والتراب على راسه"));
arasuperobject.biblemap.put("b10_15_33", new arasuperobject(10,15,33,"فقال له داود اذا عبرت معي تكون عليّ حملا"));
arasuperobject.biblemap.put("b10_15_34", new arasuperobject(10,15,34,"ولكن اذا رجعت الى المدينة وقلت لابشالوم انا اكون عبدك ايها الملك. انا عبد ابيك منذ زمان والآن انا عبدك. فانك تبطل لي مشورة اخيتوفل"));
arasuperobject.biblemap.put("b10_15_35", new arasuperobject(10,15,35,"أليس معك هناك صادوق وابياثار الكاهنان. فكل ما تسمعه من بيت الملك فاخبر به صادوق وابياثار الكاهنين"));
arasuperobject.biblemap.put("b10_15_36", new arasuperobject(10,15,36,"هوذا هناك معهما ابناهما اخيمعص لصادوق ويوناثان لابياثار. فترسلون على ايديهما اليّ كل كلمة تسمعونها"));
arasuperobject.biblemap.put("b10_15_37", new arasuperobject(10,15,37,"فأتى حوشاي صاحب داود الى المدينة وابشالوم يدخل اورشليم"));
arasuperobject.biblemap.put("b10_16_1", new arasuperobject(10,16,1,"ولما عبر داود قليلا عن القمة اذا بصيبا غلام مفيبوشث قد لقيه بحمارين مشدودين عليهما مئتا رغيف خبز ومئة عنقود زبيب ومئة قرص تين وزق خمر"));
arasuperobject.biblemap.put("b10_16_2", new arasuperobject(10,16,2,"فقال الملك لصيبا ما لك وهذه. فقال صيبا الحماران لبيت الملك للركوب والخبز والتين للغلمان ليأكلوا والخمر ليشربه من اعيا في البرية"));
arasuperobject.biblemap.put("b10_16_3", new arasuperobject(10,16,3,"فقال الملك واين ابن سيدك. فقال صيبا للملك هوذا هو مقيم في اورشليم لانه قال اليوم يرد لي بيت اسرائيل مملكة ابي"));
arasuperobject.biblemap.put("b10_16_4", new arasuperobject(10,16,4,"فقال الملك لصيبا هوذا لك كل ما لمفيبوشث. فقال صيبا سجدت. ليتني اجد نعمة في عينيك يا سيدي الملك"));
arasuperobject.biblemap.put("b10_16_5", new arasuperobject(10,16,5,"ولما جاء الملك داود الى بحوريم اذا برجل خارج من هناك من عشيرة بيت شاول اسمه شمعي بن جيرا. يسب وهو يخرج"));
arasuperobject.biblemap.put("b10_16_6", new arasuperobject(10,16,6,"ويرشق بالحجارة داود وجميع عبيد الملك داود وجميع الشعب وجميع الجبابرة عن يمينه وعن يساره"));
arasuperobject.biblemap.put("b10_16_7", new arasuperobject(10,16,7,"وهكذا كان شمعي يقول في سبه اخرج اخرج يا رجل الدماء ورجل بليعال"));
arasuperobject.biblemap.put("b10_16_8", new arasuperobject(10,16,8,"قد رد الرب عليك كل دماء بيت شاول الذي ملكت عوضا عنه وقد دفع الرب المملكة ليد ابشالوم ابنك وها انت واقع بشرّك لانك رجل دماء"));
arasuperobject.biblemap.put("b10_16_9", new arasuperobject(10,16,9,"فقال ابيشاي ابن صروية للملك لماذا يسب هذا الكلب الميت سيدي الملك. دعني اعبر فاقطع راسه"));
arasuperobject.biblemap.put("b10_16_10", new arasuperobject(10,16,10,"فقال الملك ما لي ولكم يا بني صروية. دعوه يسب لان الرب قال له سبّ داود ومن يقول لماذا تفعل هكذا"));
arasuperobject.biblemap.put("b10_16_11", new arasuperobject(10,16,11,"وقال داود لابيشاي ولجميع عبيده هوذا ابني الذي خرج من احشائي يطلب نفسي فكم بالحري الآن بنياميني. دعوه يسب لان الرب قال له"));
arasuperobject.biblemap.put("b10_16_12", new arasuperobject(10,16,12,"لعل الرب ينظر الى مذلتي ويكافئني الرب خيرا عوض مسبته بهذا اليوم"));
arasuperobject.biblemap.put("b10_16_13", new arasuperobject(10,16,13,"واذ كان داود ورجاله يسيرون في الطريق كان شمعي يسير في جانب الجبل مقابله ويسب وهو سائر ويرشق بالحجارة مقابله ويذري التراب"));
arasuperobject.biblemap.put("b10_16_14", new arasuperobject(10,16,14,"وجاء الملك وكل الشعب الذين معه وقد اعيوا فاستراحوا هناك"));
arasuperobject.biblemap.put("b10_16_15", new arasuperobject(10,16,15,"واما ابشالوم وجميع الشعب رجال اسرائيل فاتوا الى اورشليم واخيتوفل معهم"));
arasuperobject.biblemap.put("b10_16_16", new arasuperobject(10,16,16,"ولما جاء حوشاي الاركي صاحب داود الى ابشالوم قال حوشاي لابشالوم ليحي الملك ليحي الملك"));
arasuperobject.biblemap.put("b10_16_17", new arasuperobject(10,16,17,"فقال ابشالوم لحوشاي أهذا معروفك مع صاحبك. لماذا لم تذهب مع صاحبك"));
arasuperobject.biblemap.put("b10_16_18", new arasuperobject(10,16,18,"فقال حوشاي لابشالوم كلا ولكن الذي اختاره الرب وهذا الشعب وكل رجال اسرائيل فله اكون ومعه اقيم"));
arasuperobject.biblemap.put("b10_16_19", new arasuperobject(10,16,19,"وثانيا من اخدم. أليس بين يدي ابنه. كما خدمت بين يدي ابيك كذلك اكون بين يديك"));
arasuperobject.biblemap.put("b10_16_20", new arasuperobject(10,16,20,"وقال ابشالوم لاخيتوفل اعطوا مشورة ماذا نفعل"));
arasuperobject.biblemap.put("b10_16_21", new arasuperobject(10,16,21,"فقال اخيتوفل لابشالوم ادخل الى سراري ابيك اللواتي تركهنّ لحفظ البيت فيسمع كل اسرائيل انك قد صرت مكروها من ابيك فتتشدد ايدي جميع الذين معك"));
arasuperobject.biblemap.put("b10_16_22", new arasuperobject(10,16,22,"فنصبوا لابشالوم الخيمة على السطح ودخل ابشالوم الى سراري ابيه امام جميع اسرائيل"));
arasuperobject.biblemap.put("b10_16_23", new arasuperobject(10,16,23,"وكانت مشورة اخيتوفل التي كان يشير بها في تلك الايام كمن يسأل بكلام الله. هكذا كل مشورة اخيتوفل على داود وعلى ابشالوم جميعا"));
arasuperobject.biblemap.put("b10_17_1", new arasuperobject(10,17,1,"وقال اخيتوفل لابشالوم دعني انتخب اثني عشر الف رجل واقوم واسعى وراء داود هذه الليلة"));
arasuperobject.biblemap.put("b10_17_2", new arasuperobject(10,17,2,"فآتي عليه وهو متعب ومرتخي اليدين فازعجه فيهرب كل الشعب الذي معه واضرب الملك وحده"));
arasuperobject.biblemap.put("b10_17_3", new arasuperobject(10,17,3,"وارد جميع الشعب اليك. كرجوع الجميع هو الرجل الذي تطلبه فيكون كل الشعب في سلام"));
arasuperobject.biblemap.put("b10_17_4", new arasuperobject(10,17,4,"فحسن الأمر في عيني ابشالوم واعين جميع شيوخ اسرائيل"));
arasuperobject.biblemap.put("b10_17_5", new arasuperobject(10,17,5,"فقال ابشالوم ادع ايضا حوشاي الاركي فنسمع ما يقول هو ايضا"));
arasuperobject.biblemap.put("b10_17_6", new arasuperobject(10,17,6,"فلما جاء حوشاي الى ابشالوم كلمه ابشالوم قائلا بمثل هذا الكلام تكلم اخيتوفل. أنعمل حسب كلامه ام لا. تكلم انت"));
arasuperobject.biblemap.put("b10_17_7", new arasuperobject(10,17,7,"فقال حوشاي لابشالوم ليست حسنة المشورة التي اشار بها اخيتوفل هذه المرّة"));
arasuperobject.biblemap.put("b10_17_8", new arasuperobject(10,17,8,"ثم قال حوشاي انت تعلم اباك ورجاله انهم جبابرة وان انفسهم مرّة كدبّة مثكل في الحقل. وابوك رجل قتال ولا يبيت مع الشعب"));
arasuperobject.biblemap.put("b10_17_9", new arasuperobject(10,17,9,"ها هو الآن مختبئ في احدى الحفر او احد الاماكن ويكون اذا سقط بعضهم في الابتداء ان السامع يسمع فيقول قد صارت كسرة في الشعب الذي وراء ابشالوم"));
arasuperobject.biblemap.put("b10_17_10", new arasuperobject(10,17,10,"ايضا ذو البأس الذي قلبه كقلب الاسد يذوب ذوبانا لان جميع اسرائيل يعلمون ان اباك جبار والذين معه ذوو بأس"));
arasuperobject.biblemap.put("b10_17_11", new arasuperobject(10,17,11,"لذلك اشير بان يجتمع اليك كل اسرائيل من دان الى بئر سبع كالرمل الذي على البحر في الكثرة وحضرتك سائر في الوسط"));
arasuperobject.biblemap.put("b10_17_12", new arasuperobject(10,17,12,"ونأتي اليه الى احد الاماكن حيث هو وننزل عليه نزول الطل على الارض ولا يبقى منه ولا من جميع الرجال الذين معه واحد"));
arasuperobject.biblemap.put("b10_17_13", new arasuperobject(10,17,13,"واذا انحاز الى مدينة يحمل جميع اسرائيل الى تلك المدينة حبالا فنجرّها الى الوادي حتى لا تبقى هناك ولا حصاة"));
arasuperobject.biblemap.put("b10_17_14", new arasuperobject(10,17,14,"فقال ابشالوم وكل رجال اسرائيل ان مشورة حوشاي الاركي احسن من مشورة اخيتوفل. فان الرب امر بابطال مشورة اخيتوفل الصالحة لكي ينزل الرب الشر بابشالوم"));
arasuperobject.biblemap.put("b10_17_15", new arasuperobject(10,17,15,"وقال حوشاي لصادوق وابياثار الكاهنين كذا وكذا اشار اخيتوفل على ابشالوم وعلى شيوخ اسرائيل وكذا وكذا اشرت انا"));
arasuperobject.biblemap.put("b10_17_16", new arasuperobject(10,17,16,"فالآن ارسلوا عاجلا واخبروا داود قائلين لا تبت هذه الليلة في سهول البرية بل اعبر لئلا يبتلع الملك وجميع الشعب الذي معه"));
arasuperobject.biblemap.put("b10_17_17", new arasuperobject(10,17,17,"وكان يوناثان واخيمعص واقفين عند عين روجل فانطلقت الجارية واخبرتهما وهما ذهبا واخبرا الملك داود. لانهما لم يقدرا ان يريا داخلين المدينة"));
arasuperobject.biblemap.put("b10_17_18", new arasuperobject(10,17,18,"فرآهما غلام واخبر ابشالوم. فذهبا كلاهما عاجلا ودخلا بيت رجل في بحوريم. وله بئر في داره فنزلا اليها"));
arasuperobject.biblemap.put("b10_17_19", new arasuperobject(10,17,19,"فاخذت المرأة وفرشت سجفا على فم البئر وسطحت عليه سميذا فلم يعلم الامر"));
arasuperobject.biblemap.put("b10_17_20", new arasuperobject(10,17,20,"فجاء عبيد ابشالوم الى المرأة الى البيت وقالوا اين اخيمعص ويوناثان. فقالت لهم المرأة قد عبرا قناة الماء. ولما فتشوا ولم يجدوهما رجعوا الى اورشليم"));
arasuperobject.biblemap.put("b10_17_21", new arasuperobject(10,17,21,"وبعد ذهابهم خرجا من البئر وذهبا واخبرا الملك داود وقالا لداود قوموا واعبروا سريعا الماء لان هكذا اشار عليكم اخيتوفل"));
arasuperobject.biblemap.put("b10_17_22", new arasuperobject(10,17,22,"فقام داود وجميع الشعب الذي معه وعبروا الاردن وعند ضوء الصباح لم يبق احد لم يعبر الاردن"));
arasuperobject.biblemap.put("b10_17_23", new arasuperobject(10,17,23,"واما اخيتوفل فلما رأى ان مشورته لم يعمل بها شدّ على الحمار وقام وانطلق الى بيته الى مدينته واوصى لبيته وخنق نفسه ومات ودفن في قبر ابيه"));
arasuperobject.biblemap.put("b10_17_24", new arasuperobject(10,17,24,"وجاء داود الى محنايم. وعبر ابشالوم الاردن هو وجميع رجال اسرائيل معه"));
arasuperobject.biblemap.put("b10_17_25", new arasuperobject(10,17,25,"واقام ابشالوم عماسا بدل يوآب على الجيش. وكان عماسا ابن رجل اسمه يثرا الاسرائيلي الذي دخل الى ابيجايل بنت ناحاش اخت صروية ام يوآب"));
arasuperobject.biblemap.put("b10_17_26", new arasuperobject(10,17,26,"ونزل اسرائيل وابشالوم في ارض جلعاد"));
arasuperobject.biblemap.put("b10_17_27", new arasuperobject(10,17,27,"وكان لما جاء داود الى محنايم ان شوبي بن ناحاش من ربّة بني عمون وماكير بن عميئيل من لودبار وبرزلاي الجلعادي من روجليم"));
arasuperobject.biblemap.put("b10_17_28", new arasuperobject(10,17,28,"قدموا فرشا وطسوسا وآنية خزف وحنطة وشعيرا ودقيقا وفريكا وفولا وعدسا وحمّصا مشويا"));
arasuperobject.biblemap.put("b10_17_29", new arasuperobject(10,17,29,"وعسلا وزبدة وضأنا وجبن بقر لداود وللشعب الذي معه لياكلوا. لانهم قالوا الشعب جوعان ومتعب وعطشان في البرية"));
arasuperobject.biblemap.put("b10_18_1", new arasuperobject(10,18,1,"واحصى داود الشعب الذي معه وجعل عليهم رؤساء الوف ورؤساء مئات"));
arasuperobject.biblemap.put("b10_18_2", new arasuperobject(10,18,2,"وارسل داود الشعب ثلثا بيد يوآب وثلثا بيد ابيشاي ابن صروية اخي يوآب وثلثا بيد إتاي الجتّي. وقال الملك للشعب اني انا ايضا اخرج معكم"));
arasuperobject.biblemap.put("b10_18_3", new arasuperobject(10,18,3,"فقال الشعب لا تخرج لاننا اذا هربنا لا يبالون بنا واذا مات نصفنا لا يبالون بنا. والآن انت كعشرة آلاف منا. والآن الاصلح ان تكون لنا نجدة من المدينة"));
arasuperobject.biblemap.put("b10_18_4", new arasuperobject(10,18,4,"فقال لهم الملك ما يحسن في اعينكم افعله. فوقف الملك بجانب الباب وخرج جميع الشعب مئات والوفا"));
arasuperobject.biblemap.put("b10_18_5", new arasuperobject(10,18,5,"واوصى الملك يوآب وابيشاي واتّاي قائلا ترفقوا لي بالفتى ابشالوم. وسمع جميع الشعب حين اوصى الملك جميع الرؤساء بابشالوم"));
arasuperobject.biblemap.put("b10_18_6", new arasuperobject(10,18,6,"وخرج الشعب الى الحقل للقاء اسرائيل. وكان القتال في وعر افرايم"));
arasuperobject.biblemap.put("b10_18_7", new arasuperobject(10,18,7,"فانكسر هناك شعب اسرائيل امام عبيد داود وكانت هناك مقتلة عظيمة في ذلك اليوم. قتل عشرون الفا"));
arasuperobject.biblemap.put("b10_18_8", new arasuperobject(10,18,8,"وكان القتال هناك منتشرا على وجه كل الارض وزاد الذين اكلهم الوعر من الشعب على الذين اكلهم السيف في ذلك اليوم"));
arasuperobject.biblemap.put("b10_18_9", new arasuperobject(10,18,9,"وصادف ابشالوم عبيد داود وكان ابشالوم راكبا على بغل فدخل البغل تحت اغصان البطمة العظيمة الملتفّة فتعلّق راسه بالبطمة وعلّق بين السماء والارض والبغل الذي تحته مرّ"));
arasuperobject.biblemap.put("b10_18_10", new arasuperobject(10,18,10,"فرأه رجل واخبر يوآب وقال اني قد رأيت ابشالوم معلقا بالبطمة"));
arasuperobject.biblemap.put("b10_18_11", new arasuperobject(10,18,11,"فقال يوآب للرجل الذي اخبره انك قد رأيته فلماذا لم تضربه هناك الى الارض وعليّ ان اعطيك عشرة من الفضة ومنطقة"));
arasuperobject.biblemap.put("b10_18_12", new arasuperobject(10,18,12,"فقال الرجل ليوآب فلو وزن في يدي الف من الفضة لما كنت امد يدي الى ابن الملك. لان الملك اوصاك في آذاننا انت وابيشاي واتّاي قائلا احترزوا ايّا كان منكم على الفتى ابشالوم"));
arasuperobject.biblemap.put("b10_18_13", new arasuperobject(10,18,13,"والا فكنت فعلت بنفسي زورا اذ لا يخفى عن الملك شيء وانت كنت وقفت ضدّي"));
arasuperobject.biblemap.put("b10_18_14", new arasuperobject(10,18,14,"فقال يوآب اني لا اصبر هكذا امامك. فاخذ ثلاثة سهام بيده ونشبها في قلب ابشالوم وهو بعد حيّ في قلب البطمة"));
arasuperobject.biblemap.put("b10_18_15", new arasuperobject(10,18,15,"واحاط بها عشرة غلمان حاملو سلاح يوآب وضربوا ابشالوم واماتوه"));
arasuperobject.biblemap.put("b10_18_16", new arasuperobject(10,18,16,"وضرب يوآب بالبوق فرجع الشعب عن اتّباع اسرائيل لان يوآب منع الشعب"));
arasuperobject.biblemap.put("b10_18_17", new arasuperobject(10,18,17,"واخذوا ابشالوم وطرحوه في الوعر في الجبّ العظيم واقاموا عليه رجمة عظيمة جدا من الحجارة. وهرب كل اسرائيل كل واحد الى خيمته"));
arasuperobject.biblemap.put("b10_18_18", new arasuperobject(10,18,18,"وكان ابشالوم قد اخذ واقام لنفسه وهو حيّ النصب الذي في وادي الملك لانه قال ليس لي ابن لاجل تذكير اسمي. ودعا النصب باسمه وهو يدعى يد ابشالوم الى هذا اليوم"));
arasuperobject.biblemap.put("b10_18_19", new arasuperobject(10,18,19,"وقال اخيمعص بن صادوق دعني اجر فابشر الملك لان الله قد انتقم له من اعدائه"));
arasuperobject.biblemap.put("b10_18_20", new arasuperobject(10,18,20,"فقال له يوآب ما انت صاحب بشارة في هذا اليوم. في يوم آخر تبشّر وهذا اليوم لا تبشّر من اجل ان ابن الملك قد مات"));
arasuperobject.biblemap.put("b10_18_21", new arasuperobject(10,18,21,"وقال يوآب لكوشي اذهب واخبر الملك بما رأيت. فسجد كوشي ليوآب وركض"));
arasuperobject.biblemap.put("b10_18_22", new arasuperobject(10,18,22,"وعاد ايضا اخيمعص بن صادوق فقال ليوآب مهما كان فدعني أجر انا ايضا وراء كوشي. فقال يوآب لماذا تجري انت يا ابني وليس لك بشارة تجازى"));
arasuperobject.biblemap.put("b10_18_23", new arasuperobject(10,18,23,"قال مهما كان اجري. فقال له اجر. فجرى اخيمعص في طريق الغور وسبق كوشي"));
arasuperobject.biblemap.put("b10_18_24", new arasuperobject(10,18,24,"وكان داود جالسا بين البابين وطلع الرقيب الى سطح الباب الى السور ورفع عينيه ونظر واذا برجل يجري وحده"));
arasuperobject.biblemap.put("b10_18_25", new arasuperobject(10,18,25,"فنادى الرقيب واخبر الملك. فقال الملك ان كان وحده ففي فمه بشارة. وكان يسعى ويقرب"));
arasuperobject.biblemap.put("b10_18_26", new arasuperobject(10,18,26,"ثم رأى الرقيب رجلا آخر يجري. فنادى الرقيب البواب وقال هوذا رجل يجري وحده. فقال الملك وهذا ايضا مبشّر"));
arasuperobject.biblemap.put("b10_18_27", new arasuperobject(10,18,27,"وقال الرقيب اني ارى جري الاول كجري اخيمعص بن صادوق. فقال الملك هذا رجل صالح ويأتي ببشارة صالحة"));
arasuperobject.biblemap.put("b10_18_28", new arasuperobject(10,18,28,"فنادى اخيمعص وقال للملك السلام وسجد للملك على وجهه الى الارض. وقال مبارك الرب الهك الذي دفع القوم الذين رفعوا ايديهم على سيدي الملك"));
arasuperobject.biblemap.put("b10_18_29", new arasuperobject(10,18,29,"فقال الملك أسلام للفتى ابشالوم. فقال اخيمعص قد رأيت جمهورا عظيما عند ارسال يوآب عبد الملك وعبدك ولم اعلم ماذا"));
arasuperobject.biblemap.put("b10_18_30", new arasuperobject(10,18,30,"فقال الملك در وقف ههنا. فدار ووقف"));
arasuperobject.biblemap.put("b10_18_31", new arasuperobject(10,18,31,"واذا بكوشي قد اتى وقال كوشي ليبشّر سيدي الملك لان الرب قد انتقم لك اليوم من جميع القائمين عليك"));
arasuperobject.biblemap.put("b10_18_32", new arasuperobject(10,18,32,"فقال الملك لكوشي أسلام للفتى ابشالوم. فقال كوشي ليكن كالفتى اعداء سيدي الملك وجميع الذين قاموا عليك للشرّ"));
arasuperobject.biblemap.put("b10_18_33", new arasuperobject(10,18,33,"فانزعج الملك وصعد الى علية الباب وكان يبكي ويقول هكذا وهو يتمشى يا ابني ابشالوم يا ابني يا ابني ابشالوم يا ليتني مت عوضا عنك يا ابشالوم ابني يا ابني"));
arasuperobject.biblemap.put("b10_19_1", new arasuperobject(10,19,1,"فأخبر يوآب هوذا الملك يبكي وينوح على ابشالوم"));
arasuperobject.biblemap.put("b10_19_2", new arasuperobject(10,19,2,"فصارت الغلبة في ذلك اليوم مناحة عند جميع الشعب لان الشعب سمعوا في ذلك اليوم من يقول ان الملك قد تأسف على ابنه"));
arasuperobject.biblemap.put("b10_19_3", new arasuperobject(10,19,3,"وتسلّل الشعب في ذلك اليوم للدخول الى المدينة كما يتسلّل القوم الخجلون عندما يهربون في القتال"));
arasuperobject.biblemap.put("b10_19_4", new arasuperobject(10,19,4,"وستر الملك وجهه وصرخ الملك بصوت عظيم يا ابني ابشالوم يا ابشالوم ابني يا ابني"));
arasuperobject.biblemap.put("b10_19_5", new arasuperobject(10,19,5,"فدخل يوآب الى الملك الى البيت وقال قد اخزيت اليوم وجوه جميع عبيدك منقذي نفسك اليوم وانفس بنيك وبناتك وانفس نسائك وانفس سراريك"));
arasuperobject.biblemap.put("b10_19_6", new arasuperobject(10,19,6,"بمحبتك لمبغضيك وبغضك لمحبيك. لانك اظهرت اليوم انه ليس لك رؤساء ولا عبيد لاني علمت اليوم انه لو كان ابشالوم حيّا وكلنا اليوم موتى لحسن حينئذ الامر في عينيك"));
arasuperobject.biblemap.put("b10_19_7", new arasuperobject(10,19,7,"فالآن قم واخرج وطيب قلوب عبيدك. لاني قد اقسمت بالرب انه ان لم تخرج لا يبيت احد معك هذه الليلة ويكون ذلك اشرّ عليك من كل شر اصابك منذ صباك الى الآن"));
arasuperobject.biblemap.put("b10_19_8", new arasuperobject(10,19,8,"فقام الملك وجلس في الباب. فاخبروا جميع الشعب قائلين هوذا الملك جالس في الباب. فأتى جميع الشعب امام الملك. واما اسرائيل فهربوا كل واحد الى خيمته"));
arasuperobject.biblemap.put("b10_19_9", new arasuperobject(10,19,9,"وكان جميع الشعب في خصام في جميع اسباط اسرائيل قائلين ان الملك قد انقذنا من يد اعدائنا وهو نجّانا من يد الفلسطينيين. والآن قد هرب من الارض لاجل ابشالوم"));
arasuperobject.biblemap.put("b10_19_10", new arasuperobject(10,19,10,"وابشالوم الذي مسحناه علينا قد مات في الحرب. فالآن لماذا انتم ساكتون عن ارجاع الملك"));
arasuperobject.biblemap.put("b10_19_11", new arasuperobject(10,19,11,"وارسل الملك داود الى صادوق وابياثار الكاهنين قائلا كلما شيوخ يهوذا قائلين لماذا تكونون آخرين في ارجاع الملك الى بيته وقد أتى كلام جميع اسرائيل الى الملك في بيته"));
arasuperobject.biblemap.put("b10_19_12", new arasuperobject(10,19,12,"انتم اخوتي انتم عظمي ولحمي. فلماذا تكونون آخرين في ارجاع الملك"));
arasuperobject.biblemap.put("b10_19_13", new arasuperobject(10,19,13,"وتقولان لعماسا. أما انت عظمي ولحمي. هكذا يفعل بي الله وهكذا يزيد ان كنت لا تصير رئيس جيش عندي كل الايام بدل يوآب"));
arasuperobject.biblemap.put("b10_19_14", new arasuperobject(10,19,14,"فاستمال بقلوب جميع رجال يهوذا كرجل واحد فارسلوا الى الملك قائلين ارجع انت وجميع عبيدك"));
arasuperobject.biblemap.put("b10_19_15", new arasuperobject(10,19,15,"فرجع الملك وأتى الى الاردن وأتى يهوذا الى الجلجال سائرا لملاقاة الملك ليعبّر الملك الاردن"));
arasuperobject.biblemap.put("b10_19_16", new arasuperobject(10,19,16,"فبادر شمعي بن جيرا البنياميني الذي من بحوريم ونزل مع رجال يهوذا للقاء الملك داود"));
arasuperobject.biblemap.put("b10_19_17", new arasuperobject(10,19,17,"ومعه الف رجل من بنيامين وصيبا غلام بيت شاول وبنوه الخمسة عشر وعبيده العشرون معه فخاضوا الاردن امام الملك"));
arasuperobject.biblemap.put("b10_19_18", new arasuperobject(10,19,18,"وعبر القارب لتعبير بيت الملك ولعمل ما يحسن في عينيه. وسقط شمعي بن جيرا امام الملك عندما عبر الاردن"));
arasuperobject.biblemap.put("b10_19_19", new arasuperobject(10,19,19,"وقال للملك. لا يحسب لي سيدي اثما ولا تذكر ما افترى به عبدك يوم خروج سيدي الملك من اورشليم حتى يضع الملك ذلك في قلبه"));
arasuperobject.biblemap.put("b10_19_20", new arasuperobject(10,19,20,"لان عبدك يعلم اني قد اخطأت وهانذا قد جئت اليوم اوّل كل بيت يوسف ونزلت للقاء سيدي الملك"));
arasuperobject.biblemap.put("b10_19_21", new arasuperobject(10,19,21,"فاجاب ابيشاي ابن صروية وقال ألا يقتل شمعي لاجل هذا لانه سبّ مسيح الرب"));
arasuperobject.biblemap.put("b10_19_22", new arasuperobject(10,19,22,"فقال داود ما لي ولكم يا بني صروية حتى تكونوا لي اليوم مقاومين. آليوم يقتل احد في اسرائيل. أفما علمت اني اليوم ملك على اسرائيل"));
arasuperobject.biblemap.put("b10_19_23", new arasuperobject(10,19,23,"ثم قال الملك لشمعي لا تموت. وحلف له الملك"));
arasuperobject.biblemap.put("b10_19_24", new arasuperobject(10,19,24,"ونزل مفيبوشث ابن شاول للقاء الملك ولم يعتن برجليه ولا اعتنى بلحيته ولا غسل ثيابه من اليوم الذي ذهب فيه الملك الى اليوم الذي اتى فيه بسلام"));
arasuperobject.biblemap.put("b10_19_25", new arasuperobject(10,19,25,"فلما جاء الى اورشليم للقاء الملك قال له الملك لماذا لم تذهب معي يا مفيبوشث"));
arasuperobject.biblemap.put("b10_19_26", new arasuperobject(10,19,26,"فقال يا سيدي الملك ان عبدي قد خدعني لان عبدك قال اشد لنفسي الحمار فاركب عليه واذهب مع الملك لان عبدك اعرج"));
arasuperobject.biblemap.put("b10_19_27", new arasuperobject(10,19,27,"ووشى بعبدك الى سيدي الملك وسيدي الملك كملاك الله فافعل ما يحسن في عينيك"));
arasuperobject.biblemap.put("b10_19_28", new arasuperobject(10,19,28,"لان كل بيت ابي لم يكن الا اناسا موتى لسيدي الملك وقد جعلت عبدك بين الآكلين على مائدتك فاي حق لي بعد حتى اصرخ ايضا الى الملك"));
arasuperobject.biblemap.put("b10_19_29", new arasuperobject(10,19,29,"فقال له الملك لماذا تتكلم بعد بامورك. قد قلت انك انت وصيبا تقسمان الحقل"));
arasuperobject.biblemap.put("b10_19_30", new arasuperobject(10,19,30,"فقال مفيبوشث للملك فليأخذ الكل ايضا بعد ان جاء سيدي الملك بسلام الى بيته"));
arasuperobject.biblemap.put("b10_19_31", new arasuperobject(10,19,31,"ونزل برزلاي الجلعادي من روجليم وعبر الاردن مع الملك ليشيعه عند الاردن"));
arasuperobject.biblemap.put("b10_19_32", new arasuperobject(10,19,32,"وكان برزلاي قد شاخ جدا. كان ابن ثمانين سنة. وهو عال الملك عند اقامته في محنايم لانه كان رجلا عظيما جدا"));
arasuperobject.biblemap.put("b10_19_33", new arasuperobject(10,19,33,"فقال الملك لبرزلاي اعبر انت معي وانا اعولك معي في اورشليم"));
arasuperobject.biblemap.put("b10_19_34", new arasuperobject(10,19,34,"فقال برزلاي للملك كم ايام سني حياتي حتى اصعد مع الملك الى اورشليم"));
arasuperobject.biblemap.put("b10_19_35", new arasuperobject(10,19,35,"انا اليوم ابن ثمانين سنة. هل اميّز بين الطيب والردي وهل يستطعم عبدك بما آكل وما اشرب وهل اسمع ايضا اصوات المغنين والمغنيات. فلماذا يكون عبدك ايضا ثقلا على سيدي الملك"));
arasuperobject.biblemap.put("b10_19_36", new arasuperobject(10,19,36,"يعبر عبدك قليلا الاردن مع الملك ولماذا يكافئني الملك بهذه المكافاة"));
arasuperobject.biblemap.put("b10_19_37", new arasuperobject(10,19,37,"دع عبدك يرجع فاموت في مدينتي عند قبر ابي وامي وهوذا عبدك كمهام يعبر مع سيدي الملك فافعل له ما يحسن في عينيك"));
arasuperobject.biblemap.put("b10_19_38", new arasuperobject(10,19,38,"فاجاب الملك ان كمهام يعبر معي فأفعل له ما يحسن في عينيك وكل ما تتمناه مني افعله لك"));
arasuperobject.biblemap.put("b10_19_39", new arasuperobject(10,19,39,"فعبر جميع الشعب الاردن والملك عبر. وقبّل الملك برزلاي وباركه فرجع الى مكانه"));
arasuperobject.biblemap.put("b10_19_40", new arasuperobject(10,19,40,"وعبر الملك الى الجلجال وعبر كمهام معه وكل شعب يهوذا عبّروا الملك وكذلك نصف شعب اسرائيل"));
arasuperobject.biblemap.put("b10_19_41", new arasuperobject(10,19,41,"واذا بجميع رجال اسرائيل جاءون الى الملك وقالوا للملك لماذا سرقك اخوتنا رجال يهوذا وعبروا الاردن بالملك وبيته وكل رجال داود معه"));
arasuperobject.biblemap.put("b10_19_42", new arasuperobject(10,19,42,"فاجاب كل رجال يهوذا رجال اسرائيل لان الملك قريب اليّ ولماذا تغتاظ من هذا الأمر. هل اكلنا شيئا من الملك او وهبنا هبة"));
arasuperobject.biblemap.put("b10_19_43", new arasuperobject(10,19,43,"فاجاب رجال اسرائيل رجال يهوذا وقالوا. لي عشرة اسهم في الملك وانا احق منك بداود. فلماذا استخففت بي ولم يكن كلامي اولا في ارجاع ملكي. وكان كلام رجال يهوذا اقسى من كلام رجال اسرائيل"));
arasuperobject.biblemap.put("b10_20_1", new arasuperobject(10,20,1,"واتفق هناك رجل لئيم اسمه شبع بن بكري رجل بنياميني فضرب بالبوق وقال ليس لنا قسم في داود ولا لنا نصيب في ابن يسّى. كل رجل الى خيمته يا اسرائيل"));
arasuperobject.biblemap.put("b10_20_2", new arasuperobject(10,20,2,"فصعد كل رجال اسرائيل من وراء داود الى وراء شبع بن بكري. واما رجال يهوذا فلازموا ملكهم من الاردن الى اورشليم"));
arasuperobject.biblemap.put("b10_20_3", new arasuperobject(10,20,3,"وجاء داود الى بيته في اورشليم. واخذ الملك النساء السراري العشر اللواتي تركهنّ لحفظ البيت وجعلهنّ تحت حجز وكان يعولهنّ ولكن لم يدخل اليهنّ بل كنّ محبوسات الى يوم موتهنّ في عيشة العزوبة"));
arasuperobject.biblemap.put("b10_20_4", new arasuperobject(10,20,4,"وقال الملك لعماسا اجمع لي رجال يهوذا في ثلاثة ايام واحضر انت هنا"));
arasuperobject.biblemap.put("b10_20_5", new arasuperobject(10,20,5,"فذهب عماسا ليجمع يهوذا ولكنه تأخر عن الميقات الذي عينه"));
arasuperobject.biblemap.put("b10_20_6", new arasuperobject(10,20,6,"فقال داود لابيشاي الآن يسيء الينا شبع بن بكري اكثر من ابشالوم. فخذ انت عبيد سيدك واتبعه لئلا يجد لنفسه مدنا حصينة وينفلت من امام اعيننا"));
arasuperobject.biblemap.put("b10_20_7", new arasuperobject(10,20,7,"فخرج وراءه رجال يوآب الجلادون والسعاة وجميع الابطال وخرجوا من اورشليم ليتبعوا شبع بن بكري"));
arasuperobject.biblemap.put("b10_20_8", new arasuperobject(10,20,8,"ولما كانوا عند الصخرة العظيمة التي في جبعون جاء عماسا قدامهم. وكان يوآب متنطقا على ثوبه الذي كان لابسه وفوقه منطقة سيف في غمده مشدودة على حقويه فلما خرج اندلق السيف"));
arasuperobject.biblemap.put("b10_20_9", new arasuperobject(10,20,9,"فقال يوآب لعماسا أسالم انت يا اخي. وامسكت يد يوآب اليمنى بلحية عماسا ليقبّله"));
arasuperobject.biblemap.put("b10_20_10", new arasuperobject(10,20,10,"واما عماسا فلم يحترز من السيف الذي بيد يوآب فضربه به في بطنه فدلق امعاءه الى الارض ولم يثن عليه فمات. واما يوآب وابيشاي اخوه فتبعا شبع بن بكري"));
arasuperobject.biblemap.put("b10_20_11", new arasuperobject(10,20,11,"ووقف عنده واحد من غلمان يوآب فقال من سرّ بيوآب ومن هو لداود فوراء يوآب"));
arasuperobject.biblemap.put("b10_20_12", new arasuperobject(10,20,12,"وكان عماسا يتمرغ في الدم في وسط السكة. ولما رأى الرجل ان كل الشعب يقفون نقل عماسا من السكة الى الحقل وطرح عليه ثوبا لما رأى ان كل من يصل اليه يقف"));
arasuperobject.biblemap.put("b10_20_13", new arasuperobject(10,20,13,"فلما نقل عن السكة عبر كل انسان وراء يوآب لاتباع شبع بن بكري"));
arasuperobject.biblemap.put("b10_20_14", new arasuperobject(10,20,14,"وعبر في جميع اسباط اسرائيل الى آبل وبيت معكة وجميع البيريين فاجتمعوا وخرجوا ايضا وراءه"));
arasuperobject.biblemap.put("b10_20_15", new arasuperobject(10,20,15,"وجاءوا وحاصروه في آبل بيت معكة واقاموا مترسة حول المدينة فاقامت في الحصار وجميع الشعب الذين مع يوآب كانوا يخربون لاجل اسقاط السور"));
arasuperobject.biblemap.put("b10_20_16", new arasuperobject(10,20,16,"فنادت امرأة حكيمة من المدينة. اسمعوا. اسمعوا. قولوا ليوآب تقدم الى ههنا فاكلمك"));
arasuperobject.biblemap.put("b10_20_17", new arasuperobject(10,20,17,"فتقدم اليها فقالت المرأة أأنت يوآب. فقال انا هو. فقالت له اسمع كلام امتك. فقال انا سامع"));
arasuperobject.biblemap.put("b10_20_18", new arasuperobject(10,20,18,"فتكلمت قائلة كانوا يتكلمون اولا قائلين سؤالا يسألون في آبل وهكذا كانوا انتهوا"));
arasuperobject.biblemap.put("b10_20_19", new arasuperobject(10,20,19,"انا مسالمة امينة في اسرائيل. انت طالب ان تميت مدينة وامّا في اسرائيل. لماذا تبلع نصيب الرب"));
arasuperobject.biblemap.put("b10_20_20", new arasuperobject(10,20,20,"فاجاب يوآب وقال. حاشاي حاشاي ان ابلع وان اهلك"));
arasuperobject.biblemap.put("b10_20_21", new arasuperobject(10,20,21,"الأمر ليس كذلك. لان رجلا من جبل افرايم اسمه شبع بن بكري رفع يده على الملك داود. سلموه وحده فأنصرف عن المدينة. فقالت المرأة ليوآب هوذا راسه يلقى اليك عن السور"));
arasuperobject.biblemap.put("b10_20_22", new arasuperobject(10,20,22,"فأتت المرأة الى جميع الشعب بحكمتها فقطعوا راس شبع بن بكري والقوه الى يوآب فضرب بالبوق فانصرفوا عن المدينة كل واحد الى خيمته. واما يوآب فرجع الى اورشليم الى الملك"));
arasuperobject.biblemap.put("b10_20_23", new arasuperobject(10,20,23,"وكان يوآب على جميع جيش اسرائيل وبنايا بن يهوياداع على الجلادين والسعاة"));
arasuperobject.biblemap.put("b10_20_24", new arasuperobject(10,20,24,"وادورام على الجزية ويهوشافاط بن اخيلود مسجلا"));
arasuperobject.biblemap.put("b10_20_25", new arasuperobject(10,20,25,"وشيوا كاتبا وصادوق وابياثار كاهنين"));
arasuperobject.biblemap.put("b10_20_26", new arasuperobject(10,20,26,"وعيرا اليائيري ايضا كان كاهنا لداود"));
arasuperobject.biblemap.put("b10_21_1", new arasuperobject(10,21,1,"وكان جوع في ايام داود ثلاث سنين سنة بعد سنة فطلب داود وجه الرب. فقال الرب هو لاجل شاول ولاجل بيت الدماء لانه قتل الجبعونيين"));
arasuperobject.biblemap.put("b10_21_2", new arasuperobject(10,21,2,"فدعا الملك الجبعونيين وقال لهم. والجبعونيون ليسوا من بني اسرائيل بل من بقايا الاموريين وقد حلف لهم بنو اسرائيل وطلب شاول ان يقتلهم لاجل غيرته على بني اسرائيل ويهوذا"));
arasuperobject.biblemap.put("b10_21_3", new arasuperobject(10,21,3,"قال داود للجبعونيين ماذا افعل لكم وبماذا اكفّر فتباركوا نصيب الرب"));
arasuperobject.biblemap.put("b10_21_4", new arasuperobject(10,21,4,"فقال له الجبعونيون ليس لنا فضة ولا ذهب عند شاول ولا عند بيته وليس لنا ان نميت احدا في اسرائيل. فقال مهما قلتم افعله لكم"));
arasuperobject.biblemap.put("b10_21_5", new arasuperobject(10,21,5,"فقالوا للملك الرجل الذي افنانا والذي تآمر علينا ليبيدنا لكي لا نقيم في كل تخوم اسرائيل"));
arasuperobject.biblemap.put("b10_21_6", new arasuperobject(10,21,6,"فلنعط سبعة رجال من بنيه فنصلبهم للرب في جبعة شاول مختار الرب. فقال الملك انا اعطي"));
arasuperobject.biblemap.put("b10_21_7", new arasuperobject(10,21,7,"واشفق الملك على مفيبوشث بن يوناثان بن شاول من اجل يمين الرب التي بينهما بين داود ويوناثان بن شاول"));
arasuperobject.biblemap.put("b10_21_8", new arasuperobject(10,21,8,"فاخذ الملك ابني رصفة ابنة ايّة اللذين ولدتهما لشاول ارموني ومفيبوشث وبني ميكال ابنة شاول الخمسة الذين ولدتهم لعدرئيل ابن برزلاي المحولي"));
arasuperobject.biblemap.put("b10_21_9", new arasuperobject(10,21,9,"وسلمهم الى يد الجبعونيين فصلبوهم على الجبل امام الرب فسقط السبعة معا وقتلوا في ايام الحصاد في اولها في ابتداء حصاد الشعير"));
arasuperobject.biblemap.put("b10_21_10", new arasuperobject(10,21,10,"فاخذت رصفة ابنة ايّة مسحا وفرشته لنفسها على الصخر من ابتداء الحصاد حتى انصب الماء عليهم من السماء ولم تدع طيور السماء تنزل عليهم نهارا ولا حيوانات الحقل ليلا"));
arasuperobject.biblemap.put("b10_21_11", new arasuperobject(10,21,11,"فاخبر داود بما فعلت رصفة ابنة ايّة سرية شاول"));
arasuperobject.biblemap.put("b10_21_12", new arasuperobject(10,21,12,"فذهب داود واخذ عظام شاول وعظام يوناثان ابنه من اهل يابيش جلعاد الذين سرقوها من شارع بيت شان حيث علقهما الفلسطينيون يوم ضرب الفلسطينيون شاول في جلبوع"));
arasuperobject.biblemap.put("b10_21_13", new arasuperobject(10,21,13,"فاصعد من هناك عظام شاول وعظام يوناثان ابنه وجمعوا عظام المصلوبين"));
arasuperobject.biblemap.put("b10_21_14", new arasuperobject(10,21,14,"ودفنوا عظام شاول ويوناثان ابنه في ارض بنيامين في صيلع في قبر قيس ابيه وعملوا كل ما امر به الملك وبعد ذلك استجاب الله من اجل الارض"));
arasuperobject.biblemap.put("b10_21_15", new arasuperobject(10,21,15,"وكانت ايضا حرب بين الفلسطينيين واسرائيل فانحدر داود وعبيده معه وحاربوا الفلسطينيين فاعيا داود"));
arasuperobject.biblemap.put("b10_21_16", new arasuperobject(10,21,16,"ويشبي بنوب الذي من اولاد رافا ووزن رمحه ثلاث مئة شاقل نحاس وقد تقلد جديدا افتكر ان يقتل داود"));
arasuperobject.biblemap.put("b10_21_17", new arasuperobject(10,21,17,"فانجده ابيشاي ابن صروية فضرب الفلسطيني وقتله. حينئذ حلف رجال داود له قائلين لا تخرج ايضا معنا الى الحرب ولا تطفئ سراج اسرائيل"));
arasuperobject.biblemap.put("b10_21_18", new arasuperobject(10,21,18,"ثم بعد ذلك كانت ايضا حرب في جوب مع الفلسطينيين. حينئذ سبكاي الحوشي قتل ساف الذي هو من اولاد رافا"));
arasuperobject.biblemap.put("b10_21_19", new arasuperobject(10,21,19,"ثم كانت ايضا حرب في جوب مع الفلسطينيين. فالحانان بن يعري ارجيم البيتلحمي قتل جليات الجتّي وكانت قناة رمحه كنول النسّاجين"));
arasuperobject.biblemap.put("b10_21_20", new arasuperobject(10,21,20,"وكانت ايضا حرب في جتّ وكان رجل طويل القامة اصابع كل من يديه ست واصابع كل من رجليه ست عددها اربع وعشرون هو ايضا ولد لرافا"));
arasuperobject.biblemap.put("b10_21_21", new arasuperobject(10,21,21,"ولما عيّر اسرائيل ضربه يوناثان بن شمعي اخي داود"));
arasuperobject.biblemap.put("b10_21_22", new arasuperobject(10,21,22,"هؤلاء الاربعة ولدوا لرافا في جتّ وسقطوا بيد داود وبيد عبيده"));
arasuperobject.biblemap.put("b10_22_1", new arasuperobject(10,22,1,"وكلم داود الرب بكلام هذا النشيد في اليوم الذي انقذه فيه الرب من ايدي كل اعدائه ومن يد شاول"));
arasuperobject.biblemap.put("b10_22_2", new arasuperobject(10,22,2,"فقال. الرب صخرتي وحصني ومنقذي"));
arasuperobject.biblemap.put("b10_22_3", new arasuperobject(10,22,3,"اله صخرتي به احتمي. ترسي وقرن خلاصي. ملجإي ومناصي. مخلّصي من الظلم تخلصني"));
arasuperobject.biblemap.put("b10_22_4", new arasuperobject(10,22,4,"ادعو الرب الحميد فاتخلّص من اعدائي"));
arasuperobject.biblemap.put("b10_22_5", new arasuperobject(10,22,5,"لان امواج الموت اكتنفتني. سيول الهلاك افزعتني"));
arasuperobject.biblemap.put("b10_22_6", new arasuperobject(10,22,6,"حبال الهاوية احاطت بي. شرك الموت اصابتني"));
arasuperobject.biblemap.put("b10_22_7", new arasuperobject(10,22,7,"في ضيقي دعوت الرب والى الهي صرخت فسمع من هيكله صوتي وصراخي دخل اذنيه"));
arasuperobject.biblemap.put("b10_22_8", new arasuperobject(10,22,8,"فارتجت الارض وارتعشت. أسس السموات ارتعدت وارتجت لانه غضب"));
arasuperobject.biblemap.put("b10_22_9", new arasuperobject(10,22,9,"صعد دخان من انفه ونار من فمه اكلت. جمر اشتعلت منه"));
arasuperobject.biblemap.put("b10_22_10", new arasuperobject(10,22,10,"طأطأ السموات ونزل وضباب تحت رجليه"));
arasuperobject.biblemap.put("b10_22_11", new arasuperobject(10,22,11,"ركب على كروب وطار ورئي على اجنحة الريح"));
arasuperobject.biblemap.put("b10_22_12", new arasuperobject(10,22,12,"جعل الظلمة حوله مظلات مياها حاشكة وظلام الغمام"));
arasuperobject.biblemap.put("b10_22_13", new arasuperobject(10,22,13,"من الشعاع قدامه اشتعلت جمر نار"));
arasuperobject.biblemap.put("b10_22_14", new arasuperobject(10,22,14,"ارعد الرب من السموات والعلي اعطى صوته"));
arasuperobject.biblemap.put("b10_22_15", new arasuperobject(10,22,15,"ارسل سهاما فشتتهم برقا فازعجهم"));
arasuperobject.biblemap.put("b10_22_16", new arasuperobject(10,22,16,"فظهرت اعماق البحر وانكشفت أسس المسكونة من زجر الرب من نسمة ريح انفه"));
arasuperobject.biblemap.put("b10_22_17", new arasuperobject(10,22,17,"ارسل من العلى فأخذني. نشلني من مياه كثيرة"));
arasuperobject.biblemap.put("b10_22_18", new arasuperobject(10,22,18,"انقذني من عدوي القوي. من مبغضيّ لانهم اقوى مني"));
arasuperobject.biblemap.put("b10_22_19", new arasuperobject(10,22,19,"اصابوني في يوم بليتي وكان الرب سندي"));
arasuperobject.biblemap.put("b10_22_20", new arasuperobject(10,22,20,"اخرجني الى الرحب. خلصني لانه سرّ بي"));
arasuperobject.biblemap.put("b10_22_21", new arasuperobject(10,22,21,"يكافئني الرب حسب بري. حسب طهارة يدي يرد عليّ"));
arasuperobject.biblemap.put("b10_22_22", new arasuperobject(10,22,22,"لاني حفظت طرق الرب ولم اعص الهي"));
arasuperobject.biblemap.put("b10_22_23", new arasuperobject(10,22,23,"لان جميع احكامه امامي وفرائضه لا احيد عنها"));
arasuperobject.biblemap.put("b10_22_24", new arasuperobject(10,22,24,"واكون كاملا لديه واتحفظ من اثمي"));
arasuperobject.biblemap.put("b10_22_25", new arasuperobject(10,22,25,"فيرد الرب عليّ كبرّي وكطهارتي امام عينيه"));
arasuperobject.biblemap.put("b10_22_26", new arasuperobject(10,22,26,"مع الرحيم تكون رحيما. مع الرجل الكامل تكون كاملا"));
arasuperobject.biblemap.put("b10_22_27", new arasuperobject(10,22,27,"مع الطاهر تكون طاهرا ومع الاعوج تكون ملتويا"));
arasuperobject.biblemap.put("b10_22_28", new arasuperobject(10,22,28,"وتخلص الشعب البائس وعيناك على المترفعين فتضعهم"));
arasuperobject.biblemap.put("b10_22_29", new arasuperobject(10,22,29,"لانك انت سراجي يا رب. والرب يضيء ظلمتي"));
arasuperobject.biblemap.put("b10_22_30", new arasuperobject(10,22,30,"لاني بك اقتحمت جيشا. بالهي تسورت اسوارا"));
arasuperobject.biblemap.put("b10_22_31", new arasuperobject(10,22,31,"الله طريقه كامل وقول الرب نقي. ترس هو لجميع المحتمين به"));
arasuperobject.biblemap.put("b10_22_32", new arasuperobject(10,22,32,"لانه من هو اله غير الرب ومن هو صخرة غير الهنا"));
arasuperobject.biblemap.put("b10_22_33", new arasuperobject(10,22,33,"الاله الذي يعزّزني بالقوة ويصير طريقي كاملا"));
arasuperobject.biblemap.put("b10_22_34", new arasuperobject(10,22,34,"الذي يجعل رجلي كالايل وعلى مرتفعاتي يقيمني"));
arasuperobject.biblemap.put("b10_22_35", new arasuperobject(10,22,35,"الذي يعلم يديّ القتال فتحنى بذراعي قوس من نحاس"));
arasuperobject.biblemap.put("b10_22_36", new arasuperobject(10,22,36,"وتجعل لي ترس خلاصك ولطفك يعظمني"));
arasuperobject.biblemap.put("b10_22_37", new arasuperobject(10,22,37,"توسع خطواتي تحتي فلم تتقلقل كعباي"));
arasuperobject.biblemap.put("b10_22_38", new arasuperobject(10,22,38,"الحق اعدائي فاهلكهم ولا ارجع حتى افنيهم"));
arasuperobject.biblemap.put("b10_22_39", new arasuperobject(10,22,39,"افنيهم واسحقهم فلا يقومون بل يسقطون تحت رجليّ"));
arasuperobject.biblemap.put("b10_22_40", new arasuperobject(10,22,40,"تنطقني قوة للقتال وتصرع القائمين عليّ تحتي"));
arasuperobject.biblemap.put("b10_22_41", new arasuperobject(10,22,41,"وتعطيني اقفية اعدائي ومبغضي فافنيهم"));
arasuperobject.biblemap.put("b10_22_42", new arasuperobject(10,22,42,"يتطلعون فليس مخلّص. الى الرب فلا يستجيبهم"));
arasuperobject.biblemap.put("b10_22_43", new arasuperobject(10,22,43,"فاسحقهم كغبار الارض. مثل طين الاسواق ادقهم وادوسهم"));
arasuperobject.biblemap.put("b10_22_44", new arasuperobject(10,22,44,"وتنقذني من مخاصمات شعبي وتحفظني راسا للامم. شعب لم اعرفه يتعبد لي"));
arasuperobject.biblemap.put("b10_22_45", new arasuperobject(10,22,45,"بنو الغرباء يتذللون لي. من سماع الاذن يسمعون لي"));
arasuperobject.biblemap.put("b10_22_46", new arasuperobject(10,22,46,"بنو الغرباء يبلون ويزحفون من حصونهم"));
arasuperobject.biblemap.put("b10_22_47", new arasuperobject(10,22,47,"حيّ هو الرب ومبارك صخرتي ومرتفع اله صخرة خلاصي"));
arasuperobject.biblemap.put("b10_22_48", new arasuperobject(10,22,48,"الاله المنتقم لي والمخضع شعوبا تحتي"));
arasuperobject.biblemap.put("b10_22_49", new arasuperobject(10,22,49,"والذي يخرجني من بين اعدائي ويرفعني فوق القائمين عليّ وينقذني من رجل الظلم"));
arasuperobject.biblemap.put("b10_22_50", new arasuperobject(10,22,50,"لذلك احمدك يا رب في الامم ولاسمك ارنم"));
arasuperobject.biblemap.put("b10_22_51", new arasuperobject(10,22,51,"برج خلاص لملكه والصانع رحمة لمسيحه لداود ونسله الى الابد"));
arasuperobject.biblemap.put("b10_23_1", new arasuperobject(10,23,1,"فهذه هي كلمات داود الاخيرة. وحي داود بن يسّى ووحي الرجل القائم في العلا مسيح اله يعقوب ومرنم اسرائيل الحلو"));
arasuperobject.biblemap.put("b10_23_2", new arasuperobject(10,23,2,"روح الرب تكلم بي وكلمته على لساني"));
arasuperobject.biblemap.put("b10_23_3", new arasuperobject(10,23,3,"قال اله اسرائيل اليّ تكلم صخرة اسرائيل. اذا تسلط على الناس بار يتسلط بخوف الله"));
arasuperobject.biblemap.put("b10_23_4", new arasuperobject(10,23,4,"وكنور الصباح اذا اشرقت الشمس. كعشب من الارض في صباح صحو مضيء غبّ المطر"));
arasuperobject.biblemap.put("b10_23_5", new arasuperobject(10,23,5,"أليس هكذا بيتي عند الله لانه وضع لي عهدا ابديا متقنا في كل شيء ومحفوظا. أفلا يثبت كل خلاصي وكل مسرتي"));
arasuperobject.biblemap.put("b10_23_6", new arasuperobject(10,23,6,"ولكن بني بليعال جميعهم كشوك مطروح لانهم لا يؤخذون بيد"));
arasuperobject.biblemap.put("b10_23_7", new arasuperobject(10,23,7,"والرجل الذي يمسهم يتسلح بحديد وعصا رمح. فيحترقون بالنار في مكانهم"));
arasuperobject.biblemap.put("b10_23_8", new arasuperobject(10,23,8,"هذه اسماء الابطال الذين لداود. يوشيب بشبث التحكموني رئيس الثلاثة. هو هزّ رمحه على ثمان مئة قتلهم دفعة واحدة"));
arasuperobject.biblemap.put("b10_23_9", new arasuperobject(10,23,9,"وبعده العازار بن دودو بن اخوخي احد الثلاثة الابطال الذين كانوا مع داود حينما عيّروا الفلسطينيين الذين اجتمعوا هناك للحرب وصعد رجال اسرائيل"));
arasuperobject.biblemap.put("b10_23_10", new arasuperobject(10,23,10,"اما هو فاقام وضرب الفلسطينيين حتى كلّت يده ولصقت يده بالسيف وصنع الرب خلاصا عظيما في ذلك اليوم ورجع الشعب وراءه للنهب فقط"));
arasuperobject.biblemap.put("b10_23_11", new arasuperobject(10,23,11,"وبعده شمّة بن احي الهراري. فاجتمع الفلسطينيون جيشا وكانت هناك قطعة حقل مملوءة عدسا فهرب الشعب من امام الفلسطينيين"));
arasuperobject.biblemap.put("b10_23_12", new arasuperobject(10,23,12,"فوقف في وسط القطعة وانقذها وضرب الفلسطينيين فصنع الرب خلاصا عظيما"));
arasuperobject.biblemap.put("b10_23_13", new arasuperobject(10,23,13,"ونزل الثلاثة من الثلاثين رئيسا وأتوا في الحصاد الى داود الى مغارة عدلام وجيش الفلسطينيين نازل في وادي الرفائيين"));
arasuperobject.biblemap.put("b10_23_14", new arasuperobject(10,23,14,"وكان داود حينئذ في الحصن وحفظة الفلسطينيين حينئذ في بيت لحم"));
arasuperobject.biblemap.put("b10_23_15", new arasuperobject(10,23,15,"فتأوّه داود وقال من يسقيني ماء من بئر بيت لحم التي عند الباب"));
arasuperobject.biblemap.put("b10_23_16", new arasuperobject(10,23,16,"فشقّ الابطال الثلاثة محلّة الفلسطينيين واستقوا ماء من بئر بيت لحم التي عند الباب وحملوه وأتوا به الى داود فلم يشأ ان يشربه بل سكبه للرب"));
arasuperobject.biblemap.put("b10_23_17", new arasuperobject(10,23,17,"وقال حاشا لي يا رب ان افعل ذلك. هذا دم الرجال الذين خاطروا بانفسهم. فلم يشأ ان يشربه. هذا ما فعله الثلاثة الابطال"));
arasuperobject.biblemap.put("b10_23_18", new arasuperobject(10,23,18,"وابيشاي اخو يوآب ابن صروية هو رئيس ثلاثة. هذا هزّ رمحه على ثلاث مئة قتلهم فكان له اسم بين الثلاثة"));
arasuperobject.biblemap.put("b10_23_19", new arasuperobject(10,23,19,"ألم يكرم على الثلاثة فكان لهم رئيسا الا انه لم يصل الى الثلاثة الاول"));
arasuperobject.biblemap.put("b10_23_20", new arasuperobject(10,23,20,"وبناياهو بن يهوياداع ابن ذي بأس كثير الافعال من قبصئيل هو الذي ضرب اسدي موآب وهو الذي نزل وضرب اسدا في وسط جب يوم الثلج"));
arasuperobject.biblemap.put("b10_23_21", new arasuperobject(10,23,21,"وهو ضرب رجلا مصريا ذا منظر. وكان بيد المصري رمح. فنزل اليه بعصا وخطف الرمح من يد المصري وقتله برمحه"));
arasuperobject.biblemap.put("b10_23_22", new arasuperobject(10,23,22,"هذا ما فعله بناياهو بن يهوياداع فكان له اسم بين الثلاثة الابطال"));
arasuperobject.biblemap.put("b10_23_23", new arasuperobject(10,23,23,"وأكرم على الثلاثين الا انه لم يصل الى الثلاثة. فجعله داود من اصحاب سرّه"));
arasuperobject.biblemap.put("b10_23_24", new arasuperobject(10,23,24,"وعسائيل اخو يوآب كان من الثلاثين والحانان بن دودو من بيت لحم"));
arasuperobject.biblemap.put("b10_23_25", new arasuperobject(10,23,25,"وشمّة الحرودي واليقا الحرودي"));
arasuperobject.biblemap.put("b10_23_26", new arasuperobject(10,23,26,"وحالص الفلطي وعيرا بن عقيش التقوعي"));
arasuperobject.biblemap.put("b10_23_27", new arasuperobject(10,23,27,"وابيعزر العناثوثي ومبوناي الحوشاتي"));
arasuperobject.biblemap.put("b10_23_28", new arasuperobject(10,23,28,"وصلمون الاخوخي ومهراي النطوفاتي"));
arasuperobject.biblemap.put("b10_23_29", new arasuperobject(10,23,29,"وخالب بن بعنة النطوفاتي واتّاي بن ريباي من جبعة بني بيامين"));
arasuperobject.biblemap.put("b10_23_30", new arasuperobject(10,23,30,"وبنايا الفرعتوني وهدّاي من اودية جاعش"));
arasuperobject.biblemap.put("b10_23_31", new arasuperobject(10,23,31,"وابو علبون العرباتي وعزموت البرحومي"));
arasuperobject.biblemap.put("b10_23_32", new arasuperobject(10,23,32,"واليحبا الشعلبوني ومن بني ياشن يوناثان"));
arasuperobject.biblemap.put("b10_23_33", new arasuperobject(10,23,33,"وشمّة الهراري واخيآم بن شارار الاراري"));
arasuperobject.biblemap.put("b10_23_34", new arasuperobject(10,23,34,"واليفلط بن احسباي ابن المعكي واليعام بن اخيتوفل الجيلوني"));
arasuperobject.biblemap.put("b10_23_35", new arasuperobject(10,23,35,"وحصراي الكرملي وفعراي الأربيّ"));
arasuperobject.biblemap.put("b10_23_36", new arasuperobject(10,23,36,"ويجآل بن ناثان من صوبة وباني الجادي"));
arasuperobject.biblemap.put("b10_23_37", new arasuperobject(10,23,37,"وصالق العموني ونحراي البئيروتي حامل سلاح يوآب بن صروية"));
arasuperobject.biblemap.put("b10_23_38", new arasuperobject(10,23,38,"وعيرا اليثري وجارب اليثري"));
arasuperobject.biblemap.put("b10_23_39", new arasuperobject(10,23,39,"واوريا الحثي. الجميع سبعة وثلاثون"));
arasuperobject.biblemap.put("b10_24_1", new arasuperobject(10,24,1,"وعاد فحمي غضب الرب على اسرائيل فاهاج عليهم داود قائلا امض واحص اسرائيل ويهوذا"));
arasuperobject.biblemap.put("b10_24_2", new arasuperobject(10,24,2,"فقال الملك ليوآب رئيس الجيش الذي عنده طف في جميع اسباط اسرائيل من دان الى بئر سبع وعدّوا الشعب فاعلم عدد الشعب"));
arasuperobject.biblemap.put("b10_24_3", new arasuperobject(10,24,3,"فقال يوآب للملك ليزد الرب الهك الشعب امثالهم مئة ضعف وعينا سيدي الملك ناظرتان. ولكن لماذا يسر سيدي الملك بهذا الأمر"));
arasuperobject.biblemap.put("b10_24_4", new arasuperobject(10,24,4,"فاشتدّ كلام الملك على يوآب وعلى رؤساء الجيش فخرج يوآب ورؤساء الجيش من عند الملك ليعدّوا الشعب اي اسرائيل"));
arasuperobject.biblemap.put("b10_24_5", new arasuperobject(10,24,5,"فعبروا الاردن ونزلوا في عروعير عن يمين المدينة التي في وسط وادي جاد وتجاه يعزير"));
arasuperobject.biblemap.put("b10_24_6", new arasuperobject(10,24,6,"وأتوا الى جلعاد والى ارض تحتيم الى حدشي ثم أتوا الى دان يعن واستداروا الى صيدون"));
arasuperobject.biblemap.put("b10_24_7", new arasuperobject(10,24,7,"ثم أتوا الى حصن صور وجميع مدن الحوّيين والكنعانيين ثم خرجوا الى جنوبي يهوذا الى بئر سبع"));
arasuperobject.biblemap.put("b10_24_8", new arasuperobject(10,24,8,"وطافوا كل الارض وجاءوا في نهاية تسعة اشهر وعشرين يوما الى اورشليم"));
arasuperobject.biblemap.put("b10_24_9", new arasuperobject(10,24,9,"فدفع يوآب جملة عدد الشعب الى الملك فكان اسرائيل ثمان مئة الف رجل ذي بأس مستل السيف ورجال يهوذا خمس مئة الف رجل"));
arasuperobject.biblemap.put("b10_24_10", new arasuperobject(10,24,10,"وضرب داود قلبه بعدما عدّ الشعب. فقال داود للرب لقد اخطأت جدا في ما فعلت والآن يا رب ازل اثم عبدك لاني انحمقت جدا"));
arasuperobject.biblemap.put("b10_24_11", new arasuperobject(10,24,11,"ولما قام داود صباحا كان كلام الرب الى جاد النبي رائي داود قائلا"));
arasuperobject.biblemap.put("b10_24_12", new arasuperobject(10,24,12,"اذهب وقل لداود هكذا قال الرب. ثلاثة انا عارض عليك فاختر لنفسك واحدا منها فافعله بك"));
arasuperobject.biblemap.put("b10_24_13", new arasuperobject(10,24,13,"فأتى جاد الى داود واخبره وقال له أتاتي عليك سبع سني جوع في ارضك ام تهرب ثلاثة اشهر امام اعدائك وهم يتبعونك ام يكون ثلاثة ايام وبأ في ارضك. فالآن اعرف وانظر ماذا ارد جوابا على مرسلي"));
arasuperobject.biblemap.put("b10_24_14", new arasuperobject(10,24,14,"فقال داود لجاد قد ضاق بي الأمر جدا. فلنسقط في يد الرب لان مراحمه كثيرة ولا اسقط في يد انسان"));
arasuperobject.biblemap.put("b10_24_15", new arasuperobject(10,24,15,"فجعل الرب وبأ في اسرائيل من الصباح الى الميعاد فمات من الشعب من دان الى بئر سبع سبعون الف رجل"));
arasuperobject.biblemap.put("b10_24_16", new arasuperobject(10,24,16,"وبسط الملاك يده على اورشليم ليهلكها فندم الرب عن الشر وقال للملاك المهلك الشعب كفى. الآن رد يدك. وكان ملاك الرب عند بيدر ارونة اليبوسي"));
arasuperobject.biblemap.put("b10_24_17", new arasuperobject(10,24,17,"فكلم داود الرب عندما رأى الملاك الضارب الشعب وقال ها انا اخطأت وانا اذنبت واما هؤلاء الخراف فماذا فعلوا. فلتكن يدك عليّ وعلى بيت ابي"));
arasuperobject.biblemap.put("b10_24_18", new arasuperobject(10,24,18,"فجاء جاد في ذلك اليوم الى داود وقال له اصعد واقم للرب مذبحا في بيدر ارونة اليبوسي"));
arasuperobject.biblemap.put("b10_24_19", new arasuperobject(10,24,19,"فصعد داود حسب كلام جاد كما امر الرب"));
arasuperobject.biblemap.put("b10_24_20", new arasuperobject(10,24,20,"فتطلع ارونة ورأى الملك وعبيده يقبلون اليه فخرج ارونة وسجد للملك على وجهه الى الارض"));
arasuperobject.biblemap.put("b10_24_21", new arasuperobject(10,24,21,"وقال ارونة لماذا جاء سيدي الملك الى عبده. فقال داود لاشتري منك البيدر لكي ابني مذبحا للرب فتكفّ الضربة عن الشعب"));
arasuperobject.biblemap.put("b10_24_22", new arasuperobject(10,24,22,"فقال ارونة لداود فلياخذه سيدي الملك ويصعد ما يحسن في عينيه. انظر. البقر للمحرقة والنوارج وادوات البقر حطبا"));
arasuperobject.biblemap.put("b10_24_23", new arasuperobject(10,24,23,"الكل دفعه ارونة المالك الى الملك. وقال ارونة للملك الرب الهك يرضى عنك"));
arasuperobject.biblemap.put("b10_24_24", new arasuperobject(10,24,24,"فقال الملك لارونة لا بل اشتري منك بثمن ولا اصعد للرب الهي محرقات مجانية. فاشترى داود البيدر والبقر بخمسين شاقلا من الفضة"));
arasuperobject.biblemap.put("b10_24_25", new arasuperobject(10,24,25,"وبنى داود هناك مذبحا للرب واصعد محرقات وذبائح سلامة واستجاب الرب من اجل الارض فكفت الضربة عن اسرائيل"));

