arasuperobject.biblemap.put("b11_2_5", new arasuperobject(11,2,5,"وانت ايضا تعلم ما فعل بي يوآب ابن صروية ما فعل لرئيسي جيوش اسرائيل ابنير بن نير وعماسا بن يثر اذ قتلهما وسفك دم الحرب في الصلح وجعل دم الحرب في منطقته التي على حقويه وفي نعليه اللتين برجليه"));
arasuperobject.biblemap.put("b11_2_6", new arasuperobject(11,2,6,"فافعل حسب حكمتك ولا تدع شيبته تنحدر بسلام الى الهاوية"));
arasuperobject.biblemap.put("b11_2_7", new arasuperobject(11,2,7,"وافعل معروفا لبني برزلاي الجلعادي فيكونوا بين الآكلين على مائدتك لانهم هكذا تقدموا اليّ عند هربي من وجه ابشالوم اخيك"));
arasuperobject.biblemap.put("b11_2_8", new arasuperobject(11,2,8,"وهوذا معك شمعي بن جيرا البنياميني من بحوريم. وهو لعنني لعنة شديدة يوم انطلقت الى محنايم وقد نزل للقائي الى الاردن فحلفت له بالرب قائلا اني لا اميتك بالسيف"));
arasuperobject.biblemap.put("b11_2_9", new arasuperobject(11,2,9,"والآن فلا تبرره لانك انت رجل حكيم فاعلم ما تفعل به واحدر شيبته بالدم الى الهاوية"));
arasuperobject.biblemap.put("b11_2_10", new arasuperobject(11,2,10,"واضطجع داود مع آبائه ودفن في مدينة داود"));
arasuperobject.biblemap.put("b11_2_11", new arasuperobject(11,2,11,"وكان الزمان الذي ملك فيه داود على اسرائيل اربعين سنة. في حبرون ملك سبع سنين وفي اورشليم ملك ثلاثا وثلاثين سنة"));
arasuperobject.biblemap.put("b11_2_12", new arasuperobject(11,2,12,"وجلس سليمان على كرسي داود ابيه وتثبّت ملكه جدا"));
arasuperobject.biblemap.put("b11_2_13", new arasuperobject(11,2,13,"ثم جاء ادونيا بن حجيث الى بثشبع ام سليمان. فقالت أللسلام جئت. فقال للسلام"));
