arasuperobject.biblemap.put("b11_2_15", new arasuperobject(11,2,15,"فقال انت تعلمين ان الملك كان لي وقد جعل جميع اسرائيل وجوههم نحوي لاملك فدار الملك وصار لاخي لانه من قبل الرب صار له"));
arasuperobject.biblemap.put("b11_2_16", new arasuperobject(11,2,16,"والآن اسألك سوالا واحدا فلا ترديني فيه. فقالت له تكلم"));
arasuperobject.biblemap.put("b11_2_17", new arasuperobject(11,2,17,"فقال قولي لسليمان الملك لانه لا يردك ان يعطيني ابيشج الشونمية امرأة"));
arasuperobject.biblemap.put("b11_2_18", new arasuperobject(11,2,18,"فقالت بثشبع حسنا. انا اتكلم عنك الى الملك"));
