arasuperobject.biblemap.put("b11_2_20", new arasuperobject(11,2,20,"وقالت انما اسألك سوالا واحدا صغيرا. لا تردني. فقال لها الملك اسألي يا امي لاني لا اردك"));
arasuperobject.biblemap.put("b11_2_21", new arasuperobject(11,2,21,"فقالت لتعط ابيشج الشونمية لادونيا اخيك امرأة"));
arasuperobject.biblemap.put("b11_2_22", new arasuperobject(11,2,22,"فاجاب الملك سليمان وقال لامه ولماذا انت تسألين ابيشج الشونمية لادونيا. فاسألي له الملك. لانه اخي الاكبر مني. له ولابياثار الكاهن وليوآب ابن صروية"));
arasuperobject.biblemap.put("b11_2_23", new arasuperobject(11,2,23,"وحلف سليمان الملك بالرب قائلا هكذا يفعل لي الله وهكذا يزيد انه قد تكلم ادونيا بهذا الكلام ضد نفسه"));
arasuperobject.biblemap.put("b11_2_24", new arasuperobject(11,2,24,"والآن حيّ هو الرب الذي ثبتني واجلسني على كرسي داود ابي والذي صنع لي بيتا كما تكلم انه اليوم يقتل ادونيا"));
arasuperobject.biblemap.put("b11_2_25", new arasuperobject(11,2,25,"فارسل الملك سليمان بيد بناياهو بن يهوياداع فبطش به فمات"));
arasuperobject.biblemap.put("b11_2_26", new arasuperobject(11,2,26,"وقال الملك لابياثار الكاهن اذهب الى عناثوث الى حقولك لانك مستوجب الموت ولست اقتلك في هذا اليوم لانك حملت تابوت سيدي الرب امام داود ابي ولانك تذللت بكل ما تذلل به ابي"));
arasuperobject.biblemap.put("b11_2_27", new arasuperobject(11,2,27,"وطرد سليمان ابياثار عن ان يكون كاهنا للرب لاتمام كلام الرب الذي تكلم به على بيت عالي في شيلوه"));
arasuperobject.biblemap.put("b11_2_28", new arasuperobject(11,2,28,"فأتى الخبر الى يوآب. لان يوآب مال وراء ادونيا ولم يمل وراء ابشالوم. فهرب يوآب الى خيمة الرب وتمسك بقرون المذبح"));
arasuperobject.biblemap.put("b11_2_29", new arasuperobject(11,2,29,"فأخبر الملك سليمان بان يوآب قد هرب الى خيمة الرب وها هو بجانب المذبح. فارسل سليمان بناياهو بن يهوياداع قائلا اذهب ابطش به"));
arasuperobject.biblemap.put("b11_2_30", new arasuperobject(11,2,30,"فدخل بناياهو الى خيمة الرب وقال له هكذا يقول الملك اخرج. فقال كلا ولكنني هنا اموت. فرد بناياهو الجواب على الملك قائلا هكذا تكلم يوآب وهكذا جاوبني"));
arasuperobject.biblemap.put("b11_2_31", new arasuperobject(11,2,31,"فقال له الملك افعل كما تكلم وابطش به وادفنه وازل عني وعن بيت ابي الدم الزكي الذي سفكه يوآب"));
arasuperobject.biblemap.put("b11_2_32", new arasuperobject(11,2,32,"فيرد الرب دمه على راسه لانه بطش برجلين بريئين وخير منه وقتلهما بالسيف وابي داود لا يعلم وهما ابنير بن نير رئيس جيش اسرائيل وعماسا بن يثر رئيس جيش يهوذا"));
arasuperobject.biblemap.put("b11_2_33", new arasuperobject(11,2,33,"فيرتدّ دمهما على راس يوآب وراس نسله الى الابد ويكون لداود ونسله وبيته وكرسيه سلام الى الابد من عند الرب"));
arasuperobject.biblemap.put("b11_2_34", new arasuperobject(11,2,34,"فصعد بناياهو بن يهويادع وبطش به وقتله فدفن في بيته في البرية"));
arasuperobject.biblemap.put("b11_2_35", new arasuperobject(11,2,35,"وجعل الملك بناياهو بن يهوياداع مكانه على الجيش وجعل الملك صادوق الكاهن مكان ابياثار"));
arasuperobject.biblemap.put("b11_2_36", new arasuperobject(11,2,36,"ثم ارسل الملك ودعا شمعي وقال له. ابن لنفسك بيتا في اورشليم واقم هناك ولا تخرج من هناك الى هنا او هنالك"));
arasuperobject.biblemap.put("b11_2_37", new arasuperobject(11,2,37,"فيوم تخرج وتعبر وادي قدرون اعلمنّ بانك موتا تموت ويكون دمك على راسك"));
arasuperobject.biblemap.put("b11_2_38", new arasuperobject(11,2,38,"فقال شمعي للملك حسن الامر كما تكلم سيدي الملك كذلك يصنع عبدك. فاقام شمعي في اورشليم اياما كثيرة"));
arasuperobject.biblemap.put("b11_2_39", new arasuperobject(11,2,39,"وفي نهاية ثلاث سنين هرب عبدان لشمعي الى اخيش بن معكة ملك جتّ. فاخبروا شمعي قائلين هوذا عبداك في جتّ"));
arasuperobject.biblemap.put("b11_2_40", new arasuperobject(11,2,40,"فقام شمعي وشدّ على حماره وذهب الى جتّ الى اخيش ليفتش على عبديه فانطلق شمعي وأتى بعبديه من جتّ"));
arasuperobject.biblemap.put("b11_2_41", new arasuperobject(11,2,41,"فأخبر سليمان بان شمعي قد انطلق من اورشليم الى جتّ ورجع"));
arasuperobject.biblemap.put("b11_2_42", new arasuperobject(11,2,42,"فارسل الملك ودعا شمعي وقال له أما استحلفتك بالرب واشهدت عليك قائلا انك يوم تخرج وتذهب الى هنا وهنالك اعلمنّ بانك موتا تموت فقلت لي حسن الأمر. قد سمعت"));
arasuperobject.biblemap.put("b11_2_43", new arasuperobject(11,2,43,"فلماذا لم تحفظ يمين الرب والوصية التي اوصيتك بها"));
arasuperobject.biblemap.put("b11_2_44", new arasuperobject(11,2,44,"ثم قال الملك لشمعي انت عرفت كل الشر الذي علمه قلبك الذي فعلته لداود ابي فليرد الرب شرك على راسك"));
arasuperobject.biblemap.put("b11_2_45", new arasuperobject(11,2,45,"والملك سليمان يبارك وكرسي داود يكون ثابتا امام الرب الى الابد"));
arasuperobject.biblemap.put("b11_2_46", new arasuperobject(11,2,46,"وأمر الملك بناياهو بن يهوياداع فخرج وبطش به فمات. وتثبت الملك بيد سليمان"));
arasuperobject.biblemap.put("b11_3_1", new arasuperobject(11,3,1,"وصاهر سليمان فرعون ملك مصر واخذ بنت فرعون واتى بها الى مدينة داود الى ان اكمل بناء بيته وبيت الرب وسور اورشليم حواليها"));
arasuperobject.biblemap.put("b11_3_2", new arasuperobject(11,3,2,"الا ان الشعب كانوا يذبحون في المرتفعات لانه لم يبن بيت لاسم الرب الى تلك الايام"));
arasuperobject.biblemap.put("b11_3_3", new arasuperobject(11,3,3,"واحب سليمان الرب سائرا في فرائض داود ابيه الا انه كان يذبح ويوقد في المرتفعات"));
arasuperobject.biblemap.put("b11_3_4", new arasuperobject(11,3,4,"وذهب الملك الى جبعون ليذبح هناك. لانها هي المرتفعة العظمى. واصعد سليمان الف محرقة على ذلك المذبح"));
arasuperobject.biblemap.put("b11_3_5", new arasuperobject(11,3,5,"في جبعون تراءى الرب لسليمان في حلم ليلا. وقال الله اسأل ماذا اعطيك"));
arasuperobject.biblemap.put("b11_3_6", new arasuperobject(11,3,6,"فقال سليمان انك قد فعلت مع عبدك داود ابي رحمة عظيمة حسبما سار امامك بامانة وبر واستقامة قلب معك فحفظت له هذه الرحمة العظيمة واعطيته ابنا يجلس على كرسيه كهذا اليوم"));
arasuperobject.biblemap.put("b11_3_7", new arasuperobject(11,3,7,"والآن ايها الرب الهي انت ملّكت عبدك مكان داود ابي وانا فتى صغير لا اعلم الخروج والدخول"));
arasuperobject.biblemap.put("b11_3_8", new arasuperobject(11,3,8,"وعبدك في وسط شعبك الذي اخترته شعب كثير لا يحصى ولا يعد من الكثرة"));
arasuperobject.biblemap.put("b11_3_9", new arasuperobject(11,3,9,"فاعط عبدك قلبا فهيما لاحكم على شعبك واميّز بين الخير والشر لانه من يقدر ان يحكم على شعبك العظيم هذا"));
arasuperobject.biblemap.put("b11_3_10", new arasuperobject(11,3,10,"فحسن الكلام في عيني الرب لان سليمان سأل هذا الأمر"));
arasuperobject.biblemap.put("b11_3_11", new arasuperobject(11,3,11,"فقال له الله من اجل انك قد سألت هذا الأمر ولم تسأل لنفسك اياما كثيرة ولا سألت لنفسك غنى ولا سألت انفس اعدائك بل سألت لنفسك تمييزا لتفهم الحكم"));
arasuperobject.biblemap.put("b11_3_12", new arasuperobject(11,3,12,"هوذا قد فعلت حسب كلامك. هوذا اعطيتك قلبا حكيما ومميزا حتى انه لم يكن مثلك قبلك ولا يقوم بعدك نظيرك"));
arasuperobject.biblemap.put("b11_3_13", new arasuperobject(11,3,13,"وقد اعطيتك ايضا ما لم تسأله غنى وكرامة حتى انه لا يكون رجل مثلك في الملوك كل ايامك"));
arasuperobject.biblemap.put("b11_3_14", new arasuperobject(11,3,14,"فان سلكت في طريقي وحفظت فرائضي ووصاياي كما سلك داود ابوك فاني اطيل ايامك"));
arasuperobject.biblemap.put("b11_3_15", new arasuperobject(11,3,15,"فاستيقظ سليمان واذ هو حلم. وجاء الى اورشليم ووقف امام تابوت عهد الرب واصعد محرقات وقرّب ذبائح سلامة وعمل وليمة لكل عبيده"));
arasuperobject.biblemap.put("b11_3_16", new arasuperobject(11,3,16,"حينئذ اتت امرأتان زانيتان الى الملك ووقفتا بين يديه"));
arasuperobject.biblemap.put("b11_3_17", new arasuperobject(11,3,17,"فقالت المرأة الواحدة استمع يا سيدي. اني انا وهذه المرأة ساكنتان في بيت واحد وقد ولدت معها في البيت"));
arasuperobject.biblemap.put("b11_3_18", new arasuperobject(11,3,18,"وفي اليوم الثالث بعد ولادتي ولدت هذه المرأة ايضا وكنا معا ولم يكن معنا غريب في البيت غيرنا نحن كلتينا في البيت"));
arasuperobject.biblemap.put("b11_3_19", new arasuperobject(11,3,19,"فمات ابن هذه في الليل لانها اضطجعت عليه"));
arasuperobject.biblemap.put("b11_3_20", new arasuperobject(11,3,20,"فقامت في وسط الليل واخذت ابني من جانبي وامتك نائمة واضجعته في حضنها واضجعت ابنها الميت في حضني"));
arasuperobject.biblemap.put("b11_3_21", new arasuperobject(11,3,21,"فلما قمت صباحا لارضع ابني اذا هو ميت. ولما تأملت فيه في الصباح اذا هو ليس ابني الذي ولدته"));
arasuperobject.biblemap.put("b11_3_22", new arasuperobject(11,3,22,"وكانت المرأة الاخرى تقول كلا بل ابني الحي وابنك الميت. وهذه تقول لا بل ابنك الميت وابني الحي. وتكلمتا امام الملك"));
arasuperobject.biblemap.put("b11_3_23", new arasuperobject(11,3,23,"فقال الملك هذه تقول هذا ابني الحي وابنك الميت وتلك تقول لا بل ابنك الميت وابني الحي"));
arasuperobject.biblemap.put("b11_3_24", new arasuperobject(11,3,24,"فقال الملك ايتوني بسيف. فاتوا بسيف بين يدي الملك"));
arasuperobject.biblemap.put("b11_3_25", new arasuperobject(11,3,25,"فقال الملك اشطروا الولد الحي اثنين واعطوا نصفا للواحدة ونصفا للاخرى"));
arasuperobject.biblemap.put("b11_3_26", new arasuperobject(11,3,26,"فتكلمت المرأة التي ابنها الحي الى الملك. لان احشاءها اضطرمت على ابنها. وقالت استمع يا سيدي. اعطوها الولد الحي ولا تميتوه. واما تلك فقالت لا يكون لي ولا لك. اشطروه"));
arasuperobject.biblemap.put("b11_3_27", new arasuperobject(11,3,27,"فاجاب الملك وقال اعطوها الولد الحي ولا تميتوه فانها امه"));
arasuperobject.biblemap.put("b11_3_28", new arasuperobject(11,3,28,"ولما سمع جميع اسرائيل بالحكم الذي حكم به الملك خافوا الملك لانهم رأوا حكمة الله فيه لاجراء الحكم"));
arasuperobject.biblemap.put("b11_4_1", new arasuperobject(11,4,1,"وكان الملك سليمان ملكا على جميع اسرائيل"));
arasuperobject.biblemap.put("b11_4_2", new arasuperobject(11,4,2,"وهؤلاء هم الرؤساء الذين له. عزرياهو بن صادوق الكاهن"));
arasuperobject.biblemap.put("b11_4_3", new arasuperobject(11,4,3,"واليحورف واخيّا ابنا شيشا كاتبان. ويهوشافاط بن اخيلود المسجل"));
arasuperobject.biblemap.put("b11_4_4", new arasuperobject(11,4,4,"وبناياهو بن يهوياداع على الجيش وصادوق وابياثار كاهنان"));
arasuperobject.biblemap.put("b11_4_5", new arasuperobject(11,4,5,"وعزرياهو بن ناثان على الوكلاء وزابود بن ناثان كاهن وصاحب الملك"));
arasuperobject.biblemap.put("b11_4_6", new arasuperobject(11,4,6,"واخيشار على البيت وادونيرام بن عبدا على التسخير"));
arasuperobject.biblemap.put("b11_4_7", new arasuperobject(11,4,7,"وكان لسليمان اثنا عشر وكيلا على جميع اسرائيل يمتارون للملك وبيته. كان على الواحد ان يمتار شهرا في السنة"));
arasuperobject.biblemap.put("b11_4_8", new arasuperobject(11,4,8,"وهذه اسماؤهم. ابن حور في جبل افرايم"));
arasuperobject.biblemap.put("b11_4_9", new arasuperobject(11,4,9,"ابن دقر في ماقص وشعلبيم وبيت شمس وايلون بيت حانان"));
arasuperobject.biblemap.put("b11_4_10", new arasuperobject(11,4,10,"ابن حسد في أربوت. كانت له سوكوه وكل ارض حافر"));
arasuperobject.biblemap.put("b11_4_11", new arasuperobject(11,4,11,"ابن ابيناداب في كل مرتفعات دور. كانت طافة بنت سليمان له امرأة"));
arasuperobject.biblemap.put("b11_4_12", new arasuperobject(11,4,12,"بعنا بن اخيلود في تعنك ومجدّو وكل بيت شان التي بجانب صرتان تحت يزرعيل من بيت شان الى آبل محولة الى معبر يقمعام"));
arasuperobject.biblemap.put("b11_4_13", new arasuperobject(11,4,13,"ابن جابر في راموت جلعاد. له حوّوت يائير ابن منسّى التي في جلعاد. وله كورة ارجوب التي في باشان. ستون مدينة عظيمة باسوار وعوارض من نحاس"));
arasuperobject.biblemap.put("b11_4_14", new arasuperobject(11,4,14,"اخيناداب بن عدّو في محنايم"));
arasuperobject.biblemap.put("b11_4_15", new arasuperobject(11,4,15,"اخيمعص في نفتالي. وهو ايضا اخذ باسمة بنت سليمان امرأة"));
arasuperobject.biblemap.put("b11_4_16", new arasuperobject(11,4,16,"بعنا بن حوشاي في اشير وبعلوت"));
arasuperobject.biblemap.put("b11_4_17", new arasuperobject(11,4,17,"يهوشافاط بن فاروح في يساكر"));
arasuperobject.biblemap.put("b11_4_18", new arasuperobject(11,4,18,"شمعي بن ايلا في بنيامين"));
arasuperobject.biblemap.put("b11_4_19", new arasuperobject(11,4,19,"جابر بن اوري في ارض جلعاد ارض سيحون ملك الاموريين وعوج ملك باشان. ووكيل واحد الذي في الارض"));
arasuperobject.biblemap.put("b11_4_20", new arasuperobject(11,4,20,"وكان يهوذا واسرائيل كثيرين كالرمل الذي على البحر في الكثرة. ياكلون ويشربون ويفرحون"));
arasuperobject.biblemap.put("b11_4_21", new arasuperobject(11,4,21,"وكان سليمان متسلطا على جميع الممالك من النهر الى ارض فلسطين والى تخوم مصر. كانوا يقدمون الهدايا ويخدمون سليمان كل ايام حياته"));
arasuperobject.biblemap.put("b11_4_22", new arasuperobject(11,4,22,"وكان طعام سليمان لليوم الواحد ثلاثين كر سميذ وستين كرّ دقيق"));
arasuperobject.biblemap.put("b11_4_23", new arasuperobject(11,4,23,"وعشرة ثيران مسمنة وعشرين ثورا من المراعي ومئة خروف ما عدا الأيائل والظباء واليحامير والاوزّ المسمن"));
arasuperobject.biblemap.put("b11_4_24", new arasuperobject(11,4,24,"لانه كان متسلطا على كل ما عبر النهر من تفسح الى غزّة على كل ملوك عبر النهر وكان له صلح من جميع جوانبه حواليه"));
arasuperobject.biblemap.put("b11_4_25", new arasuperobject(11,4,25,"وسكن يهوذا واسرائيل آمنين كل واحد تحت كرمته وتحت تينته من دان الى بئر سبع كل ايام سليمان"));
arasuperobject.biblemap.put("b11_4_26", new arasuperobject(11,4,26,"وكان لسليمان اربعون الف مذود لخيل مركباته واثنا عشر الف فارس"));
arasuperobject.biblemap.put("b11_4_27", new arasuperobject(11,4,27,"وهؤلاء الوكلاء كانوا يمتارون للملك سليمان ولكل من تقدم الى مائدة الملك سليمان كل واحد في شهره. لم يكونوا يحتاجون الى شيء"));
arasuperobject.biblemap.put("b11_4_28", new arasuperobject(11,4,28,"وكانوا يأتون بشعير وتبن للخيل والجياد الى الموضع الذي يكون فيه كل واحد حسب قضائه"));
arasuperobject.biblemap.put("b11_4_29", new arasuperobject(11,4,29,"واعطى الله سليمان حكمة وفهما كثيرا جدا ورحبة قلب كالرمل الذي على شاطئ البحر"));
arasuperobject.biblemap.put("b11_4_30", new arasuperobject(11,4,30,"وفاقت حكمة سليمان حكمة جميع بني المشرق وكل حكمة مصر"));
arasuperobject.biblemap.put("b11_4_31", new arasuperobject(11,4,31,"وكان احكم من جميع الناس من ايثان الازراحي وهيمان وكلكول ودردع بني ماحول. وكان صيته في جميع الامم حواليه"));
arasuperobject.biblemap.put("b11_4_32", new arasuperobject(11,4,32,"وتكلم بثلاثة آلاف مثل. وكانت نشائده الفا وخمسا"));
arasuperobject.biblemap.put("b11_4_33", new arasuperobject(11,4,33,"وتكلم عن الاشجار من الارز الذي في لبنان الى الزوفا النابت في الحائط. وتكلم عن البهائم وعن الطير وعن الدبيب وعن السمك"));
arasuperobject.biblemap.put("b11_4_34", new arasuperobject(11,4,34,"وكانوا يأتون من جميع الشعوب ليسمعوا حكمة سليمان من جميع ملوك الارض الذين سمعوا بحكمته"));
arasuperobject.biblemap.put("b11_5_1", new arasuperobject(11,5,1,"وارسل حيرام ملك صور عبيده الى سليمان لانه سمع انهم مسحوه ملكا مكان ابيه لان حيرام كان محبا لداود كل الايام"));
arasuperobject.biblemap.put("b11_5_2", new arasuperobject(11,5,2,"فارسل سليمان الى حيرام يقول"));
arasuperobject.biblemap.put("b11_5_3", new arasuperobject(11,5,3,"انت تعلم داود ابي انه لم يستطع ان يبني بيتا لاسم الرب الهه بسبب الحروب التي احاطت به حتى جعلهم الرب تحت بطن قدميه"));
arasuperobject.biblemap.put("b11_5_4", new arasuperobject(11,5,4,"والآن فقد اراحني الرب الهي من كل الجهات فلا يوجد خصم ولا حادثة شر"));
arasuperobject.biblemap.put("b11_5_5", new arasuperobject(11,5,5,"وهانذا قائل على بناء بيت لاسم الرب الهي كما كلم الرب داود ابي قائلا ان ابنك الذي اجعله مكانك على كرسيك هو يبني البيت لاسمي"));
arasuperobject.biblemap.put("b11_5_6", new arasuperobject(11,5,6,"والآن فأمر ان يقطعوا لي ارزا من لبنان ويكون عبيدي مع عبيدك واجرة عبيدك اعطيك اياها حسب كل ما تقول لانك تعلم انه ليس بيننا احد يعرف قطع الخشب مثل الصيدونيين"));
arasuperobject.biblemap.put("b11_5_7", new arasuperobject(11,5,7,"فلما سمع حيرام كلام سليمان فرح جدا وقال مبارك اليوم الرب الذي اعطى داود ابنا حكيما على هذا الشعب الكثير"));
arasuperobject.biblemap.put("b11_5_8", new arasuperobject(11,5,8,"وارسل حيرام الى سليمان قائلا. قد سمعت ما ارسلت به اليّ. انا افعل كل مسرتك في خشب الارز وخشب السرو"));
arasuperobject.biblemap.put("b11_5_9", new arasuperobject(11,5,9,"عبيدي ينزلون ذلك من لبنان الى البحر وانا اجعله ارماثا في البحر الى الموضع الذي تعرّفني عنه وانفضه هناك وانت تحمله وانت تعمل مرضاتي باعطائك طعاما لبيتي"));
arasuperobject.biblemap.put("b11_5_10", new arasuperobject(11,5,10,"فكان حيرام يعطي سليمان خشب ارز وخشب سرو حسب كل مسرّته"));
arasuperobject.biblemap.put("b11_5_11", new arasuperobject(11,5,11,"واعطى سليمان حيرام عشرين الف كرّ حنطة طعاما لبيته وعشرين كر زيت رض. هكذا كان سليمان يعطي حيرام سنة فسنة"));
arasuperobject.biblemap.put("b11_5_12", new arasuperobject(11,5,12,"والرب اعطى سليمان حكمة كما كلمه. وكان صلح بين حيرام وسليمان وقطعا كلاهما عهدا"));
arasuperobject.biblemap.put("b11_5_13", new arasuperobject(11,5,13,"وسخّر الملك سليمان من جميع اسرائيل وكانت السخر ثلاثين الف رجل"));
arasuperobject.biblemap.put("b11_5_14", new arasuperobject(11,5,14,"فارسلهم الى لبنان عشرة آلاف في الشهر بالنوبة. يكونون شهرا في لبنان وشهرين في بيوتهم. وكان ادونيرام على التسخير"));
arasuperobject.biblemap.put("b11_5_15", new arasuperobject(11,5,15,"وكان لسليمان سبعون الفا يحملون احمالا وثمانون الفا يقطعون في الجبل"));
arasuperobject.biblemap.put("b11_5_16", new arasuperobject(11,5,16,"ما عدا رؤساء الوكلاء لسليمان الذين على العمل ثلاثة آلاف وثلاث مئة المتسلطين على الشعب العاملين العمل"));
arasuperobject.biblemap.put("b11_5_17", new arasuperobject(11,5,17,"وامر الملك ان يقلعوا حجارة كبيرة حجارة كريمة لتأسيس البيت حجارة مربعة"));
arasuperobject.biblemap.put("b11_5_18", new arasuperobject(11,5,18,"فنحتها بنّاؤو سليمان وبنّاؤو حيرام والجبليون وهيّأوا الاخشاب والحجارة لبناء البيت"));
arasuperobject.biblemap.put("b11_6_1", new arasuperobject(11,6,1,"وكان في سنة الاربع مئة والثمانين لخروج بني اسرائيل من ارض مصر في السنة الرابعة لملك سليمان على اسرائيل في شهر زيو وهو الشهر الثاني انه بنى البيت للرب"));
arasuperobject.biblemap.put("b11_6_2", new arasuperobject(11,6,2,"والبيت الذي بناه الملك سليمان للرب طوله ستون ذراعا وعرضه عشرون ذراعا وسمكه ثلاثون ذراعا"));
arasuperobject.biblemap.put("b11_6_3", new arasuperobject(11,6,3,"والرواق قدام هيكل البيت طوله عشرون ذراعا حسب عرض البيت وعرضه عشر اذرع قدام البيت"));
arasuperobject.biblemap.put("b11_6_4", new arasuperobject(11,6,4,"وعمل للبيت كوى مسقوفة مشبّكة"));
arasuperobject.biblemap.put("b11_6_5", new arasuperobject(11,6,5,"وبنى مع حائط البيت طباقا حواليه مع حيطان البيت حول الهيكل والمحراب وعمل غرفات في مستديرها"));
arasuperobject.biblemap.put("b11_6_6", new arasuperobject(11,6,6,"فالطبقة السفلى عرضها خمس اذرع والوسطى عرضها ست اذرع والثالثة عرضها سبع اذرع لانه جعل للبيت حواليه من خارج اخصاما لئلا تتمكن الجوائز في حيطان البيت"));
arasuperobject.biblemap.put("b11_6_7", new arasuperobject(11,6,7,"والبيت في بنائه بني بحجارة صحيحة مقتلعة ولم يسمع في البيت عند بنائه منحت ولا معول ولا اداة من حديد"));
arasuperobject.biblemap.put("b11_6_8", new arasuperobject(11,6,8,"وكان باب الغرفة الوسطى في جانب البيت الايمن وكانوا يصعدون بدرج معطّف الى الوسطى ومن الوسطى الى الثالثة"));
arasuperobject.biblemap.put("b11_6_9", new arasuperobject(11,6,9,"فبنى البيت واكمله وسقف البيت بألواح وجوائز من الارز"));
arasuperobject.biblemap.put("b11_6_10", new arasuperobject(11,6,10,"وبنى الغرفات على البيت كله سمكها خمس اذرع وتمكنت في البيت بخشب ارز"));
arasuperobject.biblemap.put("b11_6_11", new arasuperobject(11,6,11,"وكان كلام الرب الى سليمان قائلا"));
arasuperobject.biblemap.put("b11_6_12", new arasuperobject(11,6,12,"هذا البيت الذي انت بانيه ان سلكت في فرائضي وعملت احكامي وحفظت كل وصاياي للسلوك بها فاني اقيم معك كلامي الذي تكلمت به الى داود ابيك"));
arasuperobject.biblemap.put("b11_6_13", new arasuperobject(11,6,13,"واسكن في وسط بني اسرائيل ولا اترك شعبي اسرائيل"));
arasuperobject.biblemap.put("b11_6_14", new arasuperobject(11,6,14,"فبنى سليمان البيت واكمله"));
arasuperobject.biblemap.put("b11_6_15", new arasuperobject(11,6,15,"وبنى حيطان البيت من داخل بأضلاع ارز من ارض البيت الى حيطان السقف وغشّاه من داخل بخشب وفرش ارض البيت باخشاب سرو"));
arasuperobject.biblemap.put("b11_6_16", new arasuperobject(11,6,16,"وبنى عشرين ذراعا من مؤخّر البيت باضلاع ارز من الارض الى الحيطان. وبنى داخله لاجل المحراب اي قدس الاقداس"));
arasuperobject.biblemap.put("b11_6_17", new arasuperobject(11,6,17,"واربعون ذراعا كانت البيت اي الهيكل الذي امامه"));
arasuperobject.biblemap.put("b11_6_18", new arasuperobject(11,6,18,"وارز البيت من داخل كان منقورا على شكل قثّاء وبراعم زهور. الجميع ارز. لم يكن يرى حجر"));
arasuperobject.biblemap.put("b11_6_19", new arasuperobject(11,6,19,"وهيّأ محرابا في وسط البيت من داخل ليضع هناك تابوت عهد الرب"));
arasuperobject.biblemap.put("b11_6_20", new arasuperobject(11,6,20,"ولاجل المحراب عشرون ذراعا طولا وعشرون ذراعا عرضا وعشرون ذراعا سمكا. وغشّاه بذهب خالص. وغشّى المذبح بأرز"));
arasuperobject.biblemap.put("b11_6_21", new arasuperobject(11,6,21,"وغشّى سليمان البيت من داخل بذهب خالص. وسدّ بسلاسل ذهب قدام المحراب. وغشّاه بذهب"));
arasuperobject.biblemap.put("b11_6_22", new arasuperobject(11,6,22,"وجميع البيت غشّاه بذهب الى تمام كل البيت وكل المذبح الذي للمحراب غشّاه بذهب"));
arasuperobject.biblemap.put("b11_6_23", new arasuperobject(11,6,23,"وعمل في المحراب كروبين من خشب الزيتون علو الواحد عشر اذرع"));
arasuperobject.biblemap.put("b11_6_24", new arasuperobject(11,6,24,"وخمس اذرع جناح الكروب الواحد وخمس اذرع جناح الكروب الآخر. عشر اذرع من طرف جناحيه الى طرف جناحه"));
arasuperobject.biblemap.put("b11_6_25", new arasuperobject(11,6,25,"وعشر اذرع الكروب الآخر. قياس واحد وشكل واحد للكروبين"));
arasuperobject.biblemap.put("b11_6_26", new arasuperobject(11,6,26,"علو الكروب الواحد عشر اذرع وكذا الكروب الآخر"));
arasuperobject.biblemap.put("b11_6_27", new arasuperobject(11,6,27,"وجعل الكروبين في وسط البيت الداخلي وبسطوا اجنحة الكروبين فمسّ جناح الواحد الحائط وجناح الكروب الآخر مسّ الحائط الآخر وكانت اجنحتهما في وسط البيت يمسّ احدهما الآخر"));
arasuperobject.biblemap.put("b11_6_28", new arasuperobject(11,6,28,"وغشّى الكروبين بذهب"));
arasuperobject.biblemap.put("b11_6_29", new arasuperobject(11,6,29,"وجميع حيطان البيت في مستديرها رسمها نقشا بنقر كروبيم ونخيل وبراعم زهور من داخل ومن خارج"));
arasuperobject.biblemap.put("b11_6_30", new arasuperobject(11,6,30,"وغشّى ارض البيت بذهب من داخل ومن خارج"));
arasuperobject.biblemap.put("b11_6_31", new arasuperobject(11,6,31,"وعمل لباب المحراب مصراعين من خشب الزيتون. الساكف والقائمتان مخمّسة"));
arasuperobject.biblemap.put("b11_6_32", new arasuperobject(11,6,32,"والمصراعان من خشب الزيتون. ورسم عليهما نقش كروبيم ونخيل وبراعم زهور وغشّاهما بذهب ورصّع الكروبيم والنخيل بذهب"));
arasuperobject.biblemap.put("b11_6_33", new arasuperobject(11,6,33,"وكذلك عمل لمدخل الهيكل قوائم من خشب الزيتون مربعة"));
arasuperobject.biblemap.put("b11_6_34", new arasuperobject(11,6,34,"ومصراعين من خشب السرو. المصراع الواحد دفّتان تنطويان والمصراع الآخر دفّتان تنطويان"));
arasuperobject.biblemap.put("b11_6_35", new arasuperobject(11,6,35,"ونحت كروبيم ونخيلا وبراعم زهور وغشّاها بذهب مطرّق على المنقوش"));
arasuperobject.biblemap.put("b11_6_36", new arasuperobject(11,6,36,"وبنى الدار الداخلية ثلاثة صفوف منحوتة وصفا من جوائز الارز"));
arasuperobject.biblemap.put("b11_6_37", new arasuperobject(11,6,37,"في السنة الرابعة أسس بيت الرب في شهر زيو"));
arasuperobject.biblemap.put("b11_6_38", new arasuperobject(11,6,38,"وفي السنة الحادية عشرة في شهر بول وهو الشهر الثامن اكمل البيت في جميع أموره واحكامه. فبناه في سبع سنين"));
arasuperobject.biblemap.put("b11_7_1", new arasuperobject(11,7,1,"واما بيته فبناه سليمان في ثلاث عشرة سنة واكمل كل بيته"));
arasuperobject.biblemap.put("b11_7_2", new arasuperobject(11,7,2,"وبنى بيت وعر لبنان طوله مئة ذراع وعرضه خمسون ذراعا وسمكه ثلاثون ذراعا على اربعة صفوف من اعمدة ارز وجوائز ارز على الاعمدة"));
arasuperobject.biblemap.put("b11_7_3", new arasuperobject(11,7,3,"وسقف بارز من فوق على الغرفات الخمس والاربعين التي على الاعمدة. كل صفّ خمس عشرة"));
arasuperobject.biblemap.put("b11_7_4", new arasuperobject(11,7,4,"والسقوف ثلاث طباق وكوّة مقابل كوّة ثلاث مرّات"));
arasuperobject.biblemap.put("b11_7_5", new arasuperobject(11,7,5,"وجميع الابواب والقوائم مربعة مسقوفة ووجه كوّة مقابل كوّة ثلاث مرات"));
arasuperobject.biblemap.put("b11_7_6", new arasuperobject(11,7,6,"وعمل رواق الاعمدة طوله خمسون ذراعا وعرضه ثلاثون ذراعا. ورواقا آخر قدامها واعمدة واسكفّة قدامها"));
arasuperobject.biblemap.put("b11_7_7", new arasuperobject(11,7,7,"وعمل رواق الكرسي حيث يقضي اي رواق القضاء وغشّي بارز من ارض الى سقف"));
arasuperobject.biblemap.put("b11_7_8", new arasuperobject(11,7,8,"وبيته الذي كان يسكنه في دار اخرى داخل الرواق كان كهذا العمل. وعمل بيتا لابنة فرعون التي اخذها سليمان كهذا الرواق"));
arasuperobject.biblemap.put("b11_7_9", new arasuperobject(11,7,9,"كل هذه من حجارة كريمة كقياس الحجارة المنحوتة منشورة بمنشار من داخل ومن خارج من الاساس الى الافريز ومن داخل الى الدار الكبيرة"));
arasuperobject.biblemap.put("b11_7_10", new arasuperobject(11,7,10,"وكان مؤسسا على حجارة كريمة حجارة عظيمة حجارة عشر اذرع وحجارة ثمان اذرع"));
arasuperobject.biblemap.put("b11_7_11", new arasuperobject(11,7,11,"ومن فوق حجارة كريمة كقياس المنحوتة وارز"));
arasuperobject.biblemap.put("b11_7_12", new arasuperobject(11,7,12,"وللدار الكبيرة في مستديرها ثلاثة صفوف منحوتة وصف من جوائز الارز. كذلك دار بيت الرب الداخلية ورواق البيت"));
arasuperobject.biblemap.put("b11_7_13", new arasuperobject(11,7,13,"وارسل الملك سليمان واخذ حيرام من صور"));
arasuperobject.biblemap.put("b11_7_14", new arasuperobject(11,7,14,"وهو ابن امرأة ارملة من سبط نفتالي وابوه رجل صوري نحّاس وكان ممتلئا حكمة وفهما ومعرفة لعمل كل عمل في النحاس. فأتى الى الملك سليمان وعمل كل عمله"));
arasuperobject.biblemap.put("b11_7_15", new arasuperobject(11,7,15,"وصوّر العمودين من نحاس طول العمود الواحد ثمانية عشر ذراعا. وخيط اثنتا عشرة ذراعا يحيط بالعمود الآخر"));
arasuperobject.biblemap.put("b11_7_16", new arasuperobject(11,7,16,"وعمل تاجين ليضعهما على راسي العمودين من نحاس مسبوك. طول التاج الواحد خمس اذرع وطول التاج الآخر خمس اذرع"));
arasuperobject.biblemap.put("b11_7_17", new arasuperobject(11,7,17,"وشباكا عملا مشبكا وضفائر كعمل السلاسل للتاجين اللذين على راسي العمودين سبعا للتاج الواحد وسبعا للتاج الآخر"));
arasuperobject.biblemap.put("b11_7_18", new arasuperobject(11,7,18,"وعمل للعمودين صفين من الرمان في مستديرهما على الشبكة الواحدة لتغطية التاج الذي على راس العمود وهكذا عمل للتاج الآخر"));
arasuperobject.biblemap.put("b11_7_19", new arasuperobject(11,7,19,"والتاجان اللذان على راسي العمودين من صيغة السوسن كما في الرواق هما اربع اذرع"));
arasuperobject.biblemap.put("b11_7_20", new arasuperobject(11,7,20,"وكذلك التاجان اللذان على العمودين من عند البطن الذي من جهة الشبكة صاعدا. والرمانات مئتان على صفوف مستديرة على التاج الثاني"));
arasuperobject.biblemap.put("b11_7_21", new arasuperobject(11,7,21,"واوقف العمودين في رواق الهيكل. فاوقف العمود الايمن ودعا اسمه ياكين. ثم اوقف العمود الايسر ودعا اسمه بوعز"));
arasuperobject.biblemap.put("b11_7_22", new arasuperobject(11,7,22,"وعلى راس العمودين صيغة السوسن. فكمل عمل العمودين"));
arasuperobject.biblemap.put("b11_7_23", new arasuperobject(11,7,23,"وعمل البحر مسبوكا. عشر اذرع من شفته الى شفته وكان مدورا مستديرا. ارتفاعه خمس اذرع وخيط ثلاثون ذراعا يحيط به بدائره"));
arasuperobject.biblemap.put("b11_7_24", new arasuperobject(11,7,24,"وتحت شفته قثّاء مستديرا تحيط به. عشر للذراع. محيطة بالبحر بمستديره صفّين. القثاء قد سبكت بسبكه"));
arasuperobject.biblemap.put("b11_7_25", new arasuperobject(11,7,25,"وكان قائما على اثني عشر ثورا ثلاثة متوجهة الى الشمال وثلاثة متوجهة الى الغرب وثلاثة متوجهة الى الجنوب وثلاثة متوجهة الى الشرق. والبحر عليها من فوق وجميع اعجازها الى داخل"));
arasuperobject.biblemap.put("b11_7_26", new arasuperobject(11,7,26,"وغلظه شبر وشفته كعمل شفة كاس بزهر سوسن. يسع الفي بث"));
arasuperobject.biblemap.put("b11_7_27", new arasuperobject(11,7,27,"وعمل القواعد العشر من نحاس طول القاعدة الواحدة اربع اذرع وعرضها اربع اذرع وارتفاعها ثلاث اذرع"));
arasuperobject.biblemap.put("b11_7_28", new arasuperobject(11,7,28,"وهذا عمل القواعد. لها اتراس والاتراس بين الحواجب"));
arasuperobject.biblemap.put("b11_7_29", new arasuperobject(11,7,29,"وعلى الاتراس التي بين الحواجب أسود وثيران وكروبيم وكذلك على الحواجب من فوق. ومن تحت الأسود والثيران قلائد زهور عمل مدلّى"));
arasuperobject.biblemap.put("b11_7_30", new arasuperobject(11,7,30,"ولكل قاعدة اربع بكر من نحاس وقطاب من نحاس ولقوائمها الاربع اكتاف والاكتاف مسبوكة تحت المرحضة بجانب كل قلادة"));
arasuperobject.biblemap.put("b11_7_31", new arasuperobject(11,7,31,"وفمها داخل الاكليل ومن فوق ذراع. وفمها مدور كعمل قاعدة ذراع ونصف ذراع. وايضا على فمها نقش. واتراسها مربعة لا مدورة"));
arasuperobject.biblemap.put("b11_7_32", new arasuperobject(11,7,32,"والبكر الاربع تحت الاتراس وخطاطيف البكر في القاعدة وارتفاع البكرة الواحدة ذراع ونصف ذراع"));
arasuperobject.biblemap.put("b11_7_33", new arasuperobject(11,7,33,"وعمل البكر كعمل بكرة مركبة. خطاطيفها وأطرها واصابعها وقبوبها كلها مسبوكة"));
arasuperobject.biblemap.put("b11_7_34", new arasuperobject(11,7,34,"واربع اكتاف على اربع زوايا القاعدة الواحدة واكتاف القاعدة منها"));
arasuperobject.biblemap.put("b11_7_35", new arasuperobject(11,7,35,"واعلى القاعدة مقبّب مستدير على ارتفاع نصف ذراع من اعلى القاعدة. اياديها واتراسها منها"));
arasuperobject.biblemap.put("b11_7_36", new arasuperobject(11,7,36,"ونقش على الواح اياديها وعلى اتراسها كروبيم واسودا ونخيلا كسعة كل واحدة وقلائد زهور مستديرة"));
arasuperobject.biblemap.put("b11_7_37", new arasuperobject(11,7,37,"هكذا عمل القواعد العشر. لجميعها سبك واحد وقياس واحد وشكل واحد"));
arasuperobject.biblemap.put("b11_7_38", new arasuperobject(11,7,38,"وعمل عشر مراحض من نحاس تسع كل مرحضة اربعين بثا. المرحضة الواحدة اربع اذرع. مرحضة واحدة على القاعدة الواحدة للعشر القواعد"));
arasuperobject.biblemap.put("b11_7_39", new arasuperobject(11,7,39,"وجعل القواعد خمسا على جانب البيت الايمن وخمسا على جانب البيت الايسر وجعل البحر على جانب البيت الايمن الى الشرق من جهة الجنوب"));
arasuperobject.biblemap.put("b11_7_40", new arasuperobject(11,7,40,"وعمل حيرام المراحض والرفوش والمناضح وانتهى حيرام من جميع العمل الذي عمله للملك سليمان لبيت الرب"));
