arasuperobject.biblemap.put("b11_7_42", new arasuperobject(11,7,42,"واربع مئة الرمانة التي للشبكتين صفّا رمان للشبكة الواحدة لاجل تغطية كرتي التاجين اللذين على العمودين"));
arasuperobject.biblemap.put("b11_7_43", new arasuperobject(11,7,43,"والقواعد العشر والمراحض العشر على القواعد"));
