arasuperobject.biblemap.put("b11_7_45", new arasuperobject(11,7,45,"والقدور والرفوش والمناضح. وجميع هذه الآنية التي عملها حيرام للملك سليمان لبيت الرب هي من نحاس مصقول"));
arasuperobject.biblemap.put("b11_7_46", new arasuperobject(11,7,46,"في غور الاردن سبكها الملك في ارض الخزف بين سكوت وصرتان"));
