arasuperobject.biblemap.put("b11_8_5", new arasuperobject(11,8,5,"والملك سليمان وكل جماعة اسرائيل المجتمعين اليه معه امام التابوت كانوا يذبحون من الغنم والبقر ما لا يحصى ولا يعد من الكثرة"));
arasuperobject.biblemap.put("b11_8_6", new arasuperobject(11,8,6,"وادخل الكهنة تابوت عهد الرب الى مكانه في محراب البيت في قدس الاقداس الى تحت جناحي الكروبين"));
arasuperobject.biblemap.put("b11_8_7", new arasuperobject(11,8,7,"لان الكروبين بسطا اجنحتهما على موضع التابوت وظلل الكروبان التابوت وعصّيه من فوق"));
arasuperobject.biblemap.put("b11_8_8", new arasuperobject(11,8,8,"وجذبوا العصي فتراءت رؤوس العصي من القدس امام المحراب ولم تر خارجا وهي هناك الى هذا اليوم"));
arasuperobject.biblemap.put("b11_8_9", new arasuperobject(11,8,9,"لم يكن في التابوت الا لوحا الحجر اللذان وضعهما موسى هناك في حوريب حين عاهد الرب بني اسرائيل عند خروجهم من ارض مصر"));
