arasuperobject.biblemap.put("b11_8_14", new arasuperobject(11,8,14,"وحوّل الملك وجهه وبارك كل جمهور اسرائيل. وكل جمهور اسرائيل واقف"));
arasuperobject.biblemap.put("b11_8_15", new arasuperobject(11,8,15,"وقال مبارك الرب اله اسرائيل الذي تكلم بفمه الى داود ابي واكمل بيده قائلا"));
arasuperobject.biblemap.put("b11_8_16", new arasuperobject(11,8,16,"منذ يوم اخرجت شعبي اسرائيل من مصر لم اختر مدينة من جميع اسباط اسرائيل لبناء بيت ليكون اسمي هناك بل انما اخترت داود ليكون على شعبي اسرائيل"));
arasuperobject.biblemap.put("b11_8_17", new arasuperobject(11,8,17,"وكان في قلب داود ابي ان يبني بيتا لاسم الرب اله اسرائيل"));
arasuperobject.biblemap.put("b11_8_18", new arasuperobject(11,8,18,"فقال الرب لداود ابي من اجل انه كان في قلبك ان تبني بيتا لاسمي قد احسنت بكونه في قلبك"));
arasuperobject.biblemap.put("b11_8_19", new arasuperobject(11,8,19,"الا انك انت لا تبني البيت بل ابنك الخارج من صلبك هو يبني البيت لاسمي"));
arasuperobject.biblemap.put("b11_8_20", new arasuperobject(11,8,20,"واقام الرب كلامه الذي تكلم به وقد قمت انا مكان داود ابي وجلست على كرسي اسرائيل كما تكلم الرب وبنيت البيت لاسم الرب اله اسرائيل"));
arasuperobject.biblemap.put("b11_8_21", new arasuperobject(11,8,21,"وجعلت هناك مكانا للتابوت الذي فيه عهد الرب الذي قطعه مع آبائنا عند اخراجه اياهم من ارض مصر"));
arasuperobject.biblemap.put("b11_8_22", new arasuperobject(11,8,22,"ووقف سليمان امام مذبح الرب تجاه كل جماعة اسرائيل وبسط يديه الى السماء"));
