arasuperobject.biblemap.put("b11_8_24", new arasuperobject(11,8,24,"الذي قد حفظت لعبدك داود ابي ما كلمته به فتكلمت بفمك واكملت بيدك كهذا اليوم"));
arasuperobject.biblemap.put("b11_8_25", new arasuperobject(11,8,25,"والآن ايها الرب اله اسرائيل احفظ لعبدك داود ابي ما كلمته به قائلا لا يعدم لك امامي رجل يجلس على كرسي اسرائيل ان كان بنوك انما يحفظون طرقهم حتى يسيروا امامي كما سرت انت امامي"));
arasuperobject.biblemap.put("b11_8_26", new arasuperobject(11,8,26,"والآن يا اله اسرائيل فليتحقق كلامك الذي كلمت به عبدك داود ابي"));
arasuperobject.biblemap.put("b11_8_27", new arasuperobject(11,8,27,"لانه هل يسكن الله حقا على الارض. هوذا السموات وسماء السموات لا تسعك فكم بالاقل هذا البيت الذي بنيت"));
arasuperobject.biblemap.put("b11_8_28", new arasuperobject(11,8,28,"فالتفت الى صلاة عبدك والى تضرعه ايها الرب الهي واسمع الصراخ والصلاة التي يصلّيها عبدك امامك اليوم"));
arasuperobject.biblemap.put("b11_8_29", new arasuperobject(11,8,29,"لتكون عيناك مفتوحتين على هذا البيت ليلا ونهارا على الموضع الذي قلت ان اسمي يكون فيه لتسمع الصلاة التي يصليها عبدك في هذا الموضع"));
arasuperobject.biblemap.put("b11_8_30", new arasuperobject(11,8,30,"واسمع تضرع عبدك وشعبك اسرائيل الذين يصلّون في هذا الموضع واسمع انت في موضع سكناك في السماء واذا سمعت فاغفر"));
arasuperobject.biblemap.put("b11_8_31", new arasuperobject(11,8,31,"اذا اخطأ احد الى صاحبه ووضع عليه حلفا ليحلفه وجاء الحلف امام مذبحك في هذا البيت"));
