arasuperobject.biblemap.put("b11_16_14", new arasuperobject(11,16,14,"وبقية امور ايلة وكل ما فعل أما هي مكتوبة في سفر اخبار الايام لملوك اسرائيل"));
arasuperobject.biblemap.put("b11_16_15", new arasuperobject(11,16,15,"في السنة السابعة والعشرين لآسا ملك يهوذا ملك زمري سبعة ايام في ترصة. وكان الشعب نازلا على جبّثون التي للفلسطينيين"));
arasuperobject.biblemap.put("b11_16_16", new arasuperobject(11,16,16,"فسمع الشعب النازلون من يقول قد فتن زمري وقتل ايضا الملك. فملّك كل اسرائيل عمري رئيس الجيش على اسرائيل في ذلك اليوم في المحلّة"));
arasuperobject.biblemap.put("b11_16_17", new arasuperobject(11,16,17,"وصعد عمري وكل اسرائيل معه من جبّثون وحاصروا ترصة"));
arasuperobject.biblemap.put("b11_16_18", new arasuperobject(11,16,18,"ولما رأى زمري ان المدينة قد أخذت دخل الى قصر بيت الملك واحرق على نفسه بيت الملك بالنار فمات"));
arasuperobject.biblemap.put("b11_16_19", new arasuperobject(11,16,19,"من اجل خطاياه التي اخطأ بها بعمله الشر في عيني الرب وسيره في طريق يربعام ومن اجل خطيته التي عمل بجعله اسرائيل يخطئ"));
arasuperobject.biblemap.put("b11_16_20", new arasuperobject(11,16,20,"وبقية امور زمري وفتنته التي فتنها أما هي مكتوبة في سفر اخبار الايام لملوك اسرائيل"));
arasuperobject.biblemap.put("b11_16_21", new arasuperobject(11,16,21,"حينئذ انقسم شعب اسرائيل نصفين فنصف الشعب كان وراء تبني بن جينة لتمليكه ونصفه وراء عمري"));
arasuperobject.biblemap.put("b11_16_22", new arasuperobject(11,16,22,"وقوي الشعب الذي وراء عمري على الشعب الذي وراء تبني بن جينة فمات تبني وملك عمري"));
arasuperobject.biblemap.put("b11_16_23", new arasuperobject(11,16,23,"في السنة الواحدة والثلاثين لآسا ملك يهوذا ملك عمري على اسرائيل اثنتي عشرة سنة. ملك في ترصة ست سنين"));
arasuperobject.biblemap.put("b11_16_24", new arasuperobject(11,16,24,"واشترى جبل السامرة من شامر بوزنتين من الفضة وبنى على الجبل ودعا اسم المدينة التي بناها باسم شامر صاحب الجبل السامرة"));
arasuperobject.biblemap.put("b11_16_25", new arasuperobject(11,16,25,"وعمل عمري الشر في عيني الرب واساء اكثر من جميع الذين قبله"));
arasuperobject.biblemap.put("b11_16_26", new arasuperobject(11,16,26,"وسار في جميع طريق يربعام بن نباط وفي خطيته التي جعل بها اسرائيل يخطئ لاغاظة الرب اله اسرائيل باباطيلهم"));
arasuperobject.biblemap.put("b11_16_27", new arasuperobject(11,16,27,"وبقية أمور عمري التي عمل وجبروته الذي ابدى أما هي مكتوبة في سفر اخبار الايام لملوك اسرائيل"));
arasuperobject.biblemap.put("b11_16_28", new arasuperobject(11,16,28,"واضطجع عمري مع آبائه ودفن في السامرة وملك اخآب ابنه عوضا عنه"));
arasuperobject.biblemap.put("b11_16_29", new arasuperobject(11,16,29,"واخآب بن عمري ملك على اسرائيل في السنة الثامنة والثلاثين لآسا ملك يهوذا وملك اخآب بن عمري على اسرائيل في السامرة اثنتين وعشرين سنة"));
arasuperobject.biblemap.put("b11_16_30", new arasuperobject(11,16,30,"وعمل اخآب بن عمري الشر في عيني الرب اكثر من جميع الذين قبله"));
arasuperobject.biblemap.put("b11_16_31", new arasuperobject(11,16,31,"وكأنه كان امرا زهيدا سلوكه في خطايا يربعام بن نباط حتى اتخذ ايزابل ابنة اثبعل ملك الصيدونيين امرأة وسار وعبد البعل وسجد له"));
arasuperobject.biblemap.put("b11_16_32", new arasuperobject(11,16,32,"واقام مذبحا للبعل في بيت البعل الذي بناه في السامرة"));
arasuperobject.biblemap.put("b11_16_33", new arasuperobject(11,16,33,"وعمل اخآب سواري وزاد اخآب في العمل لاغاظة الرب اله اسرائيل اكثر من جميع ملوك اسرائيل الذين كانوا قبله"));
arasuperobject.biblemap.put("b11_16_34", new arasuperobject(11,16,34,"في ايامه بنى حيئيل البيتئيلي اريحا. بابيرام بكره وضع اساسها وبسجوب صغيره نصب ابوابها حسب كلام الرب الذي تكلم به عن يد يشوع بن نون"));
arasuperobject.biblemap.put("b11_17_1", new arasuperobject(11,17,1,"وقال ايليا التشبي من مستوطني جلعاد لاخآب حيّ هو الرب اله اسرائيل الذي وقفت امامه انه لا يكون طل ولا مطر في هذه السنين الا عند قولي"));
arasuperobject.biblemap.put("b11_17_2", new arasuperobject(11,17,2,"وكان كلام الرب له قائلا"));
arasuperobject.biblemap.put("b11_17_3", new arasuperobject(11,17,3,"انطلق من هنا واتجه نحو المشرق واختبئ عند نهر كريث الذي هو مقابل الاردن"));
arasuperobject.biblemap.put("b11_17_4", new arasuperobject(11,17,4,"فتشرب من النهر وقد أمرت الغربان ان تعولك هناك"));
arasuperobject.biblemap.put("b11_17_5", new arasuperobject(11,17,5,"فانطلق وعمل حسب كلام الرب وذهب فاقام عند نهر كريث الذي هو مقابل الاردن"));
arasuperobject.biblemap.put("b11_17_6", new arasuperobject(11,17,6,"وكانت الغربان تاتي اليه بخبز ولحم صباحا وبخبز ولحم مساء وكان يشرب من النهر"));
arasuperobject.biblemap.put("b11_17_7", new arasuperobject(11,17,7,"وكان بعد مدة من الزمان ان النهر يبس لانه لم يكن مطر في الارض"));
arasuperobject.biblemap.put("b11_17_8", new arasuperobject(11,17,8,"وكان له كلام الرب قائلا"));
arasuperobject.biblemap.put("b11_17_9", new arasuperobject(11,17,9,"قم اذهب الى صرفة التي لصيدون واقم هناك. هوذا قد امرت هناك امرأة ارملة ان تعولك"));
arasuperobject.biblemap.put("b11_17_10", new arasuperobject(11,17,10,"فقام وذهب الى صرفة. وجاء الى باب المدينة واذا بامرأة ارملة هناك تقش عيدانا فناداها وقال هاتي لي قليل ماء في اناء فاشرب"));
arasuperobject.biblemap.put("b11_17_11", new arasuperobject(11,17,11,"وفيما هي ذاهبة لتأتي به ناداها وقال هاتي لي كسرة خبز في يدك"));
arasuperobject.biblemap.put("b11_17_12", new arasuperobject(11,17,12,"فقالت حيّ هو الرب الهك انه ليست عندي كعكة ولكن ملء كف من الدقيق في الكوار وقليل من الزيت في الكوز وهانذا اقش عودين لآتي واعمله لي ولابني لناكله ثم نموت"));
arasuperobject.biblemap.put("b11_17_13", new arasuperobject(11,17,13,"فقال لها ايليا لا تخافي ادخلي واعملي كقولك ولكن اعملي لي منها كعكة صغيرة اولا واخرجي بها اليّ ثم اعملي لك ولابنك اخيرا"));
arasuperobject.biblemap.put("b11_17_14", new arasuperobject(11,17,14,"لانه هكذا قال الرب اله اسرائيل ان كوار الدقيق لا يفرغ وكوز الزيت لا ينقص الى اليوم الذي فيه يعطي الرب مطرا على وجه الارض"));
arasuperobject.biblemap.put("b11_17_15", new arasuperobject(11,17,15,"فذهبت وفعلت حسب قول ايليا واكلت هي وهو وبيتها اياما"));
arasuperobject.biblemap.put("b11_17_16", new arasuperobject(11,17,16,"كوار الدقيق لم يفرغ وكوز الزيت لم ينقص حسب قول الرب الذي تكلم به عن يد ايليا"));
arasuperobject.biblemap.put("b11_17_17", new arasuperobject(11,17,17,"وبعد هذه الأمور مرض ابن المرأة صاحبة البيت واشتدّ مرضه جدا حتى لم تبق فيه نسمة"));
arasuperobject.biblemap.put("b11_17_18", new arasuperobject(11,17,18,"فقالت لايليا ما لي ولك يا رجل الله. هل جئت اليّ لتذكير اثمي واماتة ابني"));
arasuperobject.biblemap.put("b11_17_19", new arasuperobject(11,17,19,"فقال لها اعطيني ابنك. واخذه من حضنها وصعد به الى العلية التي كان مقيما بها واضجعه على سريره"));
arasuperobject.biblemap.put("b11_17_20", new arasuperobject(11,17,20,"وصرخ الى الرب وقال ايها الرب الهي أايضا الى الارملة التي انا نازل عندها قد اسأت باماتتك ابنها"));
arasuperobject.biblemap.put("b11_17_21", new arasuperobject(11,17,21,"فتمدد على الولد ثلاث مرات وصرخ الى الرب وقال يا رب الهي لترجع نفس هذا الولد الى جوفه"));
arasuperobject.biblemap.put("b11_17_22", new arasuperobject(11,17,22,"فسمع الرب لصوت ايليا فرجعت نفس الولد الى جوفه فعاش"));
arasuperobject.biblemap.put("b11_17_23", new arasuperobject(11,17,23,"فاخذ ايليا الولد ونزل به من العلية الى البيت ودفعه لامه. وقال ايليا انظري. ابنك حيّ"));
arasuperobject.biblemap.put("b11_17_24", new arasuperobject(11,17,24,"فقالت المرأة لايليا هذا الوقت علمت انك رجل الله وان كلام الرب في فمك حق"));
arasuperobject.biblemap.put("b11_18_1", new arasuperobject(11,18,1,"وبعد ايام كثيرة كان كلام الرب الى ايليا في السنة الثالثة قائلا اذهب وتراءى لاخآب فاعطي مطرا على وجه الارض"));
arasuperobject.biblemap.put("b11_18_2", new arasuperobject(11,18,2,"فذهب ايليا ليتراءى لاخآب. وكان الجوع شديدا في السامرة"));
arasuperobject.biblemap.put("b11_18_3", new arasuperobject(11,18,3,"فدعا اخآب عوبديا الذي على البيت. وكان عوبديا يخشى الرب جدا"));
arasuperobject.biblemap.put("b11_18_4", new arasuperobject(11,18,4,"وكان حينما قطعت ايزابل انبياء الرب ان عوبديا اخذ مئة نبي وخبأهم خمسين رجلا في مغارة وعالهم بخبز وماء"));
arasuperobject.biblemap.put("b11_18_5", new arasuperobject(11,18,5,"وقال اخآب لعوبديا اذهب في الارض الى جميع عيون الماء والى جميع الاودية لعلنا نجد عشبا فنحيي الخيل والبغال ولا نعدم البهائم كلها"));
arasuperobject.biblemap.put("b11_18_6", new arasuperobject(11,18,6,"فقسما بينهما الارض ليعبرا بها. فذهب اخآب في طريق واحد وحده وذهب عوبديا في طريق آخر وحده"));
arasuperobject.biblemap.put("b11_18_7", new arasuperobject(11,18,7,"وفيما كان عوبديا في الطريق واذا بايليا قد لقيه. فعرفه وخرّ على وجهه وقال أأنت هو سيدي ايليا"));
arasuperobject.biblemap.put("b11_18_8", new arasuperobject(11,18,8,"فقال له انا هو. اذهب وقل لسيدك هوذا ايليا"));
arasuperobject.biblemap.put("b11_18_9", new arasuperobject(11,18,9,"فقال ما هي خطيتي حتى انك تدفع عبدك ليد اخآب ليميتني"));
arasuperobject.biblemap.put("b11_18_10", new arasuperobject(11,18,10,"حيّ هو الرب الهك انه لا توجد امة ولا مملكة لم يرسل سيدي اليها ليفتش عليك وكانوا يقولون انه لا يوجد وكان يستحلف المملكة والامة انهم لم يجدوك"));
arasuperobject.biblemap.put("b11_18_11", new arasuperobject(11,18,11,"والآن انت تقول اذهب قل لسيدك هوذا ايليا"));
arasuperobject.biblemap.put("b11_18_12", new arasuperobject(11,18,12,"ويكون اذا انطلقت من عندك ان روح الرب يحملك الى حيث لا اعلم فاذا أتيت واخبرت اخآب ولم يجدك فانه يقتلني. وانا عبدك اخشى الرب منذ صباي"));
arasuperobject.biblemap.put("b11_18_13", new arasuperobject(11,18,13,"ألم يخبر سيدي بما فعلت حين قتلت ايزابل انبياء الرب اذ خبأت من انبياء الرب مئة رجل خمسين خمسين رجلا في مغارة وعلتهم بخبز وماء"));
arasuperobject.biblemap.put("b11_18_14", new arasuperobject(11,18,14,"وانت الآن تقول اذهب قل لسيدك هوذا ايليا. فيقتلني"));
arasuperobject.biblemap.put("b11_18_15", new arasuperobject(11,18,15,"فقال ايليا حيّ هو رب الجنود الذي انا واقف امامه اني اليوم اتراءى له"));
arasuperobject.biblemap.put("b11_18_16", new arasuperobject(11,18,16,"فذهب عوبديا للقاء اخآب واخبره فسار اخآب للقاء ايليا"));
arasuperobject.biblemap.put("b11_18_17", new arasuperobject(11,18,17,"ولما رأى اخآب ايليا قال له اخآب أانت هو مكدر اسرائيل"));
arasuperobject.biblemap.put("b11_18_18", new arasuperobject(11,18,18,"فقال لم اكدر اسرائيل بل انت وبيت ابيك بترككم وصايا الرب وبسيرك وراء البعليم"));
arasuperobject.biblemap.put("b11_18_19", new arasuperobject(11,18,19,"فالآن ارسل واجمع اليّ كل اسرائيل الى جبل الكرمل وانبياء البعل اربع المئة والخمسين وانبياء السواري اربع المئة الذين يأكلون على مائدة ايزابل"));
arasuperobject.biblemap.put("b11_18_20", new arasuperobject(11,18,20,"فارسل اخآب الى جميع بني اسرائيل وجمع الانبياء الى جبل الكرمل"));
arasuperobject.biblemap.put("b11_18_21", new arasuperobject(11,18,21,"فتقدم ايليا الى جميع الشعب وقال حتى متى تعرجون بين الفرقتين. ان كان الرب هو الله فاتبعوه وان كان البعل فاتبعوه. فلم يجبه الشعب بكلمة"));
arasuperobject.biblemap.put("b11_18_22", new arasuperobject(11,18,22,"ثم قال ايليا للشعب انا بقيت نبيا للرب وحدي وانبياء البعل اربع مئة وخمسون رجلا"));
arasuperobject.biblemap.put("b11_18_23", new arasuperobject(11,18,23,"فليعطونا ثورين فيختاروا لانفسهم ثورا واحدا ويقطعوه ويضعوه على الحطب ولكن لا يضعوا نارا وانا اقرب الثور الآخر واجعله على الحطب ولكن لا اضع نارا"));
arasuperobject.biblemap.put("b11_18_24", new arasuperobject(11,18,24,"ثم تدعون باسم آلهتكم وانا ادعو باسم الرب. والاله الذي يجيب بنار فهو الله. فاجاب جميع الشعب وقالوا الكلام حسن"));
arasuperobject.biblemap.put("b11_18_25", new arasuperobject(11,18,25,"فقال ايليا لانبياء البعل اختاروا لانفسكم ثورا واحد وقرّبوا اولا لانكم انتم الاكثر وادعوا باسم آلهتكم ولكن لا تضعوا نارا"));
arasuperobject.biblemap.put("b11_18_26", new arasuperobject(11,18,26,"فاخذوا الثور الذي اعطي لهم وقربوه ودعوا باسم البعل من الصباح الى الظهر قائلين يا بعل اجبنا. فلم يكن صوت ولا مجيب. وكانوا يرقصون حول المذبح الذي عمل"));
arasuperobject.biblemap.put("b11_18_27", new arasuperobject(11,18,27,"وعند الظهر سخر بهم ايليا وقال ادعوا بصوت عال لانه اله. لعله مستغرق او في خلوة او في سفر او لعله نائم فيتنبّه"));
arasuperobject.biblemap.put("b11_18_28", new arasuperobject(11,18,28,"فصرخوا بصوت عال وتقطّعوا حسب عادتهم بالسيوف والرماح حتى سال منهم الدم"));
arasuperobject.biblemap.put("b11_18_29", new arasuperobject(11,18,29,"ولما جاز الظهر وتنبأوا الى حين اصعاد التقدمة ولم يكن صوت ولا مجيب ولا مصغ"));
arasuperobject.biblemap.put("b11_18_30", new arasuperobject(11,18,30,"قال ايليا لجميع الشعب تقدّموا اليّ. فتقدم جميع الشعب اليه. فرمم مذبح الرب المنهدم"));
arasuperobject.biblemap.put("b11_18_31", new arasuperobject(11,18,31,"ثم اخذ ايليا اثني عشر حجرا بعدد اسباط بني يعقوب الذي كان كلام الرب اليه قائلا اسرائيل يكون اسمك"));
arasuperobject.biblemap.put("b11_18_32", new arasuperobject(11,18,32,"وبنى الحجارة مذبحا باسم الرب وعمل قناة حول المذبح تسع كيلتين من البزر"));
arasuperobject.biblemap.put("b11_18_33", new arasuperobject(11,18,33,"ثم رتب الحطب وقطع الثور ووضعه على الحطب وقال املأوا اربع جرات ماء وصبوا على المحرقة وعلى الحطب"));
arasuperobject.biblemap.put("b11_18_34", new arasuperobject(11,18,34,"ثم قال ثنوا فثنوا وقال ثلثوا فثلثوا"));
arasuperobject.biblemap.put("b11_18_35", new arasuperobject(11,18,35,"فجرى الماء حول المذبح وامتلأت القناة ايضا ماء"));
arasuperobject.biblemap.put("b11_18_36", new arasuperobject(11,18,36,"وكان عند اصعاد التقدمة ان ايليا النبي تقدم وقال ايها الرب اله ابراهيم واسحق واسرائيل ليعلم اليوم انك انت الله في اسرائيل واني انا عبدك وبامرك قد فعلت كل هذه الامور"));
arasuperobject.biblemap.put("b11_18_37", new arasuperobject(11,18,37,"استجبني يا رب استجبني ليعلم هذا الشعب انك انت الرب الاله وانك انت حولت قلوبهم رجوعا"));
arasuperobject.biblemap.put("b11_18_38", new arasuperobject(11,18,38,"فسقطت نار الرب واكلت المحرقة والحطب والحجارة والتراب ولحست المياه التي في القناة"));
arasuperobject.biblemap.put("b11_18_39", new arasuperobject(11,18,39,"فلما رأى جميع الشعب ذلك سقطوا على وجوههم وقالوا الرب هو الله الرب هو الله"));
arasuperobject.biblemap.put("b11_18_40", new arasuperobject(11,18,40,"فقال لهم ايليا امسكوا انبياء البعل ولا يفلت منهم رجل. فامسكوهم فنزل بهم ايليا الى نهر قيشون وذبحهم هناك"));
arasuperobject.biblemap.put("b11_18_41", new arasuperobject(11,18,41,"وقال ايليا لاخآب اصعد كل واشرب لانه حس دوي مطر"));
arasuperobject.biblemap.put("b11_18_42", new arasuperobject(11,18,42,"فصعد اخآب لياكل ويشرب واما ايليا فصعد الى راس الكرمل وخرّ الى الارض وجعل وجهه بين ركبتيه"));
arasuperobject.biblemap.put("b11_18_43", new arasuperobject(11,18,43,"وقال لغلامه اصعد تطلع نحو البحر. فصعد وتطلع وقال ليس شيء. فقال ارجع سبع مرات"));
arasuperobject.biblemap.put("b11_18_44", new arasuperobject(11,18,44,"وفي المرة السابعة قال هوذا غيمة صغيرة قدر كف انسان صاعدة من البحر. فقال اصعد قل لاخآب اشدد وانزل لئلا يمنعك المطر"));
arasuperobject.biblemap.put("b11_18_45", new arasuperobject(11,18,45,"وكان من هنا الى هنا ان السماء اسودت من الغيم والريح وكان مطر عظيم. فركب اخآب ومضى الى يزرعيل"));
arasuperobject.biblemap.put("b11_18_46", new arasuperobject(11,18,46,"وكانت يد الرب على ايليا فشدّ حقويه وركض امام اخآب حتى تجيء الى يزرعيل"));
arasuperobject.biblemap.put("b11_19_1", new arasuperobject(11,19,1,"واخبر اخآب ايزابل بكل ما عمل ايليا وكيف انه قتل جميع الانبياء بالسيف"));
arasuperobject.biblemap.put("b11_19_2", new arasuperobject(11,19,2,"فارسلت ايزابل رسولا الى ايليا تقول هكذا تفعل الآلهة وهكذا تزيد ان لم اجعل نفسك كنفس واحد منهم في نحو هذا الوقت غدا"));
arasuperobject.biblemap.put("b11_19_3", new arasuperobject(11,19,3,"فلما رأى ذلك قام ومضى لاجل نفسه وأتى الى بئر سبع التي ليهوذا وترك غلامه هناك"));
arasuperobject.biblemap.put("b11_19_4", new arasuperobject(11,19,4,"ثم سار في البرية مسيرة يوم حتى أتى وجلس تحت رتمة وطلب الموت لنفسه وقال قد كفى الآن يا رب خذ نفسي لانني لست خيرا من آبائي"));
arasuperobject.biblemap.put("b11_19_5", new arasuperobject(11,19,5,"واضطجع ونام تحت الرتمة واذا بملاك قد مسّه وقال قم وكل"));
arasuperobject.biblemap.put("b11_19_6", new arasuperobject(11,19,6,"فتطلع واذا كعكة رضف وكوز ماء عند راسه فأكل وشرب ثم رجع فاضطجع"));
arasuperobject.biblemap.put("b11_19_7", new arasuperobject(11,19,7,"ثم عاد ملاك الرب ثانية فمسّه وقال قم وكل لان المسافة كثيرة عليك"));
arasuperobject.biblemap.put("b11_19_8", new arasuperobject(11,19,8,"فقام وأكل وشرب وسار بقوة تلك الأكلة اربعين نهارا واربعين ليلة الى جبل الله حوريب"));
arasuperobject.biblemap.put("b11_19_9", new arasuperobject(11,19,9,"ودخل هناك المغارة وبات فيها وكان كلام الرب اليه يقول ما لك ههنا يا ايليا"));
arasuperobject.biblemap.put("b11_19_10", new arasuperobject(11,19,10,"فقال قد غرت غيرة للرب اله الجنود لان بني اسرائيل قد تركوا عهدك ونقضوا مذابحك وقتلوا انبياءك بالسيف فبقيت انا وحدي وهم يطلبون نفسي ليأخذوها"));
arasuperobject.biblemap.put("b11_19_11", new arasuperobject(11,19,11,"فقال اخرج وقف على الجبل امام الرب. واذا بالرب عابر وريح عظيمة وشديدة قد شقت الجبال وكسرت الصخور امام الرب ولم يكن الرب في الريح. وبعد الريح زلزلة ولم يكن الرب في الزلزلة"));
arasuperobject.biblemap.put("b11_19_12", new arasuperobject(11,19,12,"وبعد الزلزلة نار ولم يكن الرب في النار. وبعد النار صوت منخفض خفيف"));
arasuperobject.biblemap.put("b11_19_13", new arasuperobject(11,19,13,"فلما سمع ايليا لف وجهه بردائه وخرج ووقف في باب المغارة. واذا بصوت اليه يقول ما لك ههنا يا ايليا"));
arasuperobject.biblemap.put("b11_19_14", new arasuperobject(11,19,14,"فقال غرت غيرة للرب اله الجنود لان بني اسرائيل قد تركوا عهدك ونقضوا مذابحك وقتلوا انبياءك بالسيف فبقيت انا وحدي وهم يطلبون نفسي ليأخذوها"));
arasuperobject.biblemap.put("b11_19_15", new arasuperobject(11,19,15,"فقال له الرب اذهب راجعا في طريقك الى برية دمشق وادخل وامسح حزائيل ملكا على ارام"));
arasuperobject.biblemap.put("b11_19_16", new arasuperobject(11,19,16,"وامسح ياهو بن نمشي ملكا على اسرائيل وامسح اليشع بن شافاط من آبل محولة نبيا عوضا عنك"));
arasuperobject.biblemap.put("b11_19_17", new arasuperobject(11,19,17,"فالذي ينجو من سيف حزائيل يقتله ياهو والذي ينجو من سيف ياهو يقتله اليشع"));
arasuperobject.biblemap.put("b11_19_18", new arasuperobject(11,19,18,"وقد ابقيت في اسرائيل سبعة آلاف كل الركب التي لم تجث للبعل وكل فم لم يقبّله"));
arasuperobject.biblemap.put("b11_19_19", new arasuperobject(11,19,19,"فذهب من هناك ووجد اليشع بن شافاط يحرث واثنا عشر فدان بقر قدامه وهو مع الثاني عشر فمرّ ايليا به وطرح رداءه عليه"));
arasuperobject.biblemap.put("b11_19_20", new arasuperobject(11,19,20,"فترك البقر وركض وراء ايليا وقال دعني اقبل ابي وامي واسير وراءك. فقال له اذهب راجعا لاني ماذا فعلت لك"));
arasuperobject.biblemap.put("b11_19_21", new arasuperobject(11,19,21,"فرجع من ورائه واخذ فدان بقر وذبحهما وسلق اللحم بأدوات البقر واعطى الشعب فأكلوا. ثم قام ومضى وراء ايليا وكان يخدمه"));
arasuperobject.biblemap.put("b11_20_1", new arasuperobject(11,20,1,"وجمع بنهدد ملك ارام كل جيشه واثنين وثلاثين ملكا معه وخيلا ومركبات وصعد وحاصر السامرة وحاربها"));
arasuperobject.biblemap.put("b11_20_2", new arasuperobject(11,20,2,"وارسل رسلا الى اخآب ملك اسرائيل الى المدينة وقال له هكذا يقول بنهدد"));
arasuperobject.biblemap.put("b11_20_3", new arasuperobject(11,20,3,"لي فضتك وذهبك ولي نساؤك وبنوك الحسان"));
arasuperobject.biblemap.put("b11_20_4", new arasuperobject(11,20,4,"فاجاب ملك اسرائيل وقال حسب قولك يا سيدي الملك انا وجميع مالي لك"));
arasuperobject.biblemap.put("b11_20_5", new arasuperobject(11,20,5,"فرجع الرسل وقالوا هكذا تكلم بن هدد قائلا اني قد ارسلت اليك قائلا ان فضتك وذهبك ونساءك وبنيك تعطيني اياهم"));
arasuperobject.biblemap.put("b11_20_6", new arasuperobject(11,20,6,"فاني في نحو هذا الوقت غدا ارسل عبيدي اليك فيفتشون بيتك وبيوت عبيدك وكل ما هو شهي في عينيك يضعونه في ايديهم وياخذونه"));
arasuperobject.biblemap.put("b11_20_7", new arasuperobject(11,20,7,"فدعا ملك اسرائيل جميع شيوخ الارض وقال اعلموا وانظروا ان هذا يطلب الشر لانه ارسل اليّ بطلب نسائي وبنيّ وفضتي وذهبي ولم امنعها عنه"));
arasuperobject.biblemap.put("b11_20_8", new arasuperobject(11,20,8,"فقال له كل الشيوخ وكل الشعب لا تسمع له ولا تقبل"));
arasuperobject.biblemap.put("b11_20_9", new arasuperobject(11,20,9,"فقال لرسل بنهدد قولوا لسيدي الملك ان كل ما ارسلت فيه الى عبدك اولا افعله. واما هذا الامر فلا استطيع ان افعله. فرجع الرسل وردّوا عليه الجواب"));
arasuperobject.biblemap.put("b11_20_10", new arasuperobject(11,20,10,"فارسل اليه بنهدد وقال هكذا تفعل بي الآلهة وهكذا تزيدني ان كان تراب السامرة يكفي قبضات لكل الشعب الذي يتبعني"));
arasuperobject.biblemap.put("b11_20_11", new arasuperobject(11,20,11,"فاجاب ملك اسرائيل وقال قولوا لا يفتخرنّ من يشد كمن يحل"));
arasuperobject.biblemap.put("b11_20_12", new arasuperobject(11,20,12,"فلما سمع هذا الكلام وهو يشرب مع الملوك في الخيام قال لعبيده اصطفوا فاصطفوا على المدينة"));
arasuperobject.biblemap.put("b11_20_13", new arasuperobject(11,20,13,"واذا بنبي تقدم الى اخآب ملك اسرائيل وقال هكذا قال الرب هل رايت كل هذا الجمهور العظيم. هانذا ادفعه ليدك اليوم فتعلم اني انا الرب"));
arasuperobject.biblemap.put("b11_20_14", new arasuperobject(11,20,14,"فقال اخآب بمن فقال هكذا قال الرب بغلمان رؤساء المقاطعات. فقال من يبتدئ بالحرب. فقال انت"));
arasuperobject.biblemap.put("b11_20_15", new arasuperobject(11,20,15,"فعدّ غلمان رؤساء المقاطعات فبلغوا مئتين واثنين وثلاثين. وعدّ بعدهم كل الشعب كل بني اسرائيل سبعة آلاف"));
arasuperobject.biblemap.put("b11_20_16", new arasuperobject(11,20,16,"وخرجوا عند الظهر وبنهدد يشرب ويسكر في الخيام هو والملوك الاثنان والثلاثون الذين ساعدوه"));
arasuperobject.biblemap.put("b11_20_17", new arasuperobject(11,20,17,"فخرج غلمان رؤساء المقاطعات اولا. وارسل بنهدد فاخبروه قائلين قد خرج رجال من السامرة"));
arasuperobject.biblemap.put("b11_20_18", new arasuperobject(11,20,18,"فقال ان كانوا قد خرجوا للسلام فامسكوهم احياء وان كانوا قد خرجوا للقتال فامسكوهم احياء"));
arasuperobject.biblemap.put("b11_20_19", new arasuperobject(11,20,19,"فخرج غلمان رؤساء المقاطعات هؤلاء من المدينة هم والجيش الذي وراءهم"));
arasuperobject.biblemap.put("b11_20_20", new arasuperobject(11,20,20,"وضرب كل رجل رجله فهرب الاراميون وطاردهم اسرائيل ونجا بنهدد ملك ارام على فرس مع الفرسان"));
arasuperobject.biblemap.put("b11_20_21", new arasuperobject(11,20,21,"وخرج ملك اسرائيل فضرب الخيل والمركبات وضرب ارام ضربة عظيمة"));
arasuperobject.biblemap.put("b11_20_22", new arasuperobject(11,20,22,"فتقدم النبي الى ملك اسرائيل وقال له اذهب تشدد واعلم وانظر ما تفعل لانه عند تمام السنة يصعد عليك ملك ارام"));
arasuperobject.biblemap.put("b11_20_23", new arasuperobject(11,20,23,"واما عبيد ملك ارام فقالوا له ان آلهتهم آلهة جبال لذلك قووا علينا. ولكن اذا حاربناهم في السهل فاننا نقوى عليهم"));
arasuperobject.biblemap.put("b11_20_24", new arasuperobject(11,20,24,"وافعل هذا الامر. اعزل الملوك كل واحد من مكانه وضع قوادا مكانهم"));
arasuperobject.biblemap.put("b11_20_25", new arasuperobject(11,20,25,"واحص لنفسك جيشا كالجيش الذي سقط منك فرسا بفرس ومركبة بمركبة فنحاربهم في السهل ونقوى عليهم. فسمع لقولهم وفعل كذلك"));
arasuperobject.biblemap.put("b11_20_26", new arasuperobject(11,20,26,"وعند تمام السنة عدّ بنهدد الاراميين وصعد الى افيق ليحارب اسرائيل"));
arasuperobject.biblemap.put("b11_20_27", new arasuperobject(11,20,27,"واحصي بنو اسرائيل وتزوّدوا وساروا للقائهم فنزل بنو اسرائيل مقابلهم نظير قطيعين صغيرين من المعزى. واما الاراميون فملأوا الارض"));
arasuperobject.biblemap.put("b11_20_28", new arasuperobject(11,20,28,"فتقدم رجل الله وكلم ملك اسرائيل وقال هكذا قال الرب من اجل ان الاراميين قالوا ان الرب انما هو اله جبال وليس هو اله اودية ادفع كل هذا الجمهور العظيم ليدك فتعلمون اني انا الرب"));
arasuperobject.biblemap.put("b11_20_29", new arasuperobject(11,20,29,"فنزل هؤلاء مقابل اولئك سبعة ايام. وفي اليوم السابع اشتبكت الحرب فضرب بنو اسرائيل من الاراميين مئة الف راجل في يوم واحد"));
arasuperobject.biblemap.put("b11_20_30", new arasuperobject(11,20,30,"وهرب الباقون الى افيق الى المدينة وسقط السور على السبعة والعشرين الف رجل الباقين. وهرب بنهدد ودخل المدينة من مخدع الى مخدع"));
arasuperobject.biblemap.put("b11_20_31", new arasuperobject(11,20,31,"فقال له عبيده اننا قد سمعنا ان ملوك بيت اسرائيل هم ملوك حليمون فلنضع مسوحا على احقائنا وحبالا على رؤوسنا ونخرج الى ملك اسرائيل لعله يحيي نفسك"));
arasuperobject.biblemap.put("b11_20_32", new arasuperobject(11,20,32,"فشدّوا مسوحا على احقائهم وحبالا على رؤوسهم واتوا الى ملك اسرائيل وقالوا يقول عبدك بنهدد لتحي نفسي. فقال أهو حيّ بعد. هو اخي"));
arasuperobject.biblemap.put("b11_20_33", new arasuperobject(11,20,33,"فتفاءل الرجال واسرعوا ولجّوا هل هو منه. وقالوا اخوك بنهدد. فقال ادخلوا خذوه فخرج اليه بنهدد فاصعده الى المركبة"));
arasuperobject.biblemap.put("b11_20_34", new arasuperobject(11,20,34,"وقال له اني ارد المدن التي اخذها ابي من ابيك وتجعل لنفسك اسواقا في دمشق كما جعل ابي في السامرة. فقال وانا اطلقك بهذا العهد. فقطع له عهدا واطلقه"));
arasuperobject.biblemap.put("b11_20_35", new arasuperobject(11,20,35,"وان رجلا من بني الانبياء قال لصاحبه. عن امر الرب اضربني. فابى الرجل ان يضربه"));
arasuperobject.biblemap.put("b11_20_36", new arasuperobject(11,20,36,"فقال له من اجل انك لم تسمع لقول الرب فحينما تذهب من عندي يقتلك اسد. ولما ذهب من عنده لقيه اسد. وقتله"));
arasuperobject.biblemap.put("b11_20_37", new arasuperobject(11,20,37,"ثم صادف رجلا آخر فقال اضربني. فضربه الرجل ضربة فجرحه"));
arasuperobject.biblemap.put("b11_20_38", new arasuperobject(11,20,38,"فذهب النبي وانتظر الملك على الطريق وتنكر بعصابة على عينيه"));
arasuperobject.biblemap.put("b11_20_39", new arasuperobject(11,20,39,"ولما عبر الملك نادى الملك وقال خرج عبدك الى وسط القتال واذا برجل مال واتى اليّ برجل وقال احفظ هذا الرجل. وان فقد تكون نفسك بدل نفسه او تدفع وزنة من الفضة"));
arasuperobject.biblemap.put("b11_20_40", new arasuperobject(11,20,40,"وفيما عبدك مشتغل هنا وهناك اذ هو مفقود. فقال له ملك اسرائيل هكذا حكمك. انت قضيت"));
arasuperobject.biblemap.put("b11_20_41", new arasuperobject(11,20,41,"فبادر ورفع العصابة عن عينيه فعرفه ملك اسرائيل انه من الانبياء"));
arasuperobject.biblemap.put("b11_20_42", new arasuperobject(11,20,42,"فقال له هكذا قال الرب لانك افلت من يدك رجلا قد حرمته تكون نفسك بدل نفسه وشعبك بدل شعبه"));
arasuperobject.biblemap.put("b11_20_43", new arasuperobject(11,20,43,"فمضى ملك اسرائيل الى بيته مكتئبا مغموما وجاء الى السامرة"));
arasuperobject.biblemap.put("b11_21_1", new arasuperobject(11,21,1,"وحدث بعد هذه الامور انه كان لنابوت اليزرعيلي كرم في يزرعيل بجانب قصر اخآب ملك السامرة"));
arasuperobject.biblemap.put("b11_21_2", new arasuperobject(11,21,2,"فكلم اخآب نابوت قائلا اعطني كرمك فيكون لي بستان بقول لانه قريب بجانب بيتي فاعطيك عوضه كرما احسن منه او اذا حسن في عينيك اعطيتك ثمنه فضة"));
arasuperobject.biblemap.put("b11_21_3", new arasuperobject(11,21,3,"فقال نابوت لاخآب حاشا لي من قبل الرب ان اعطيك ميراث آبائي"));
arasuperobject.biblemap.put("b11_21_4", new arasuperobject(11,21,4,"فدخل اخآب بيته مكتئبا مغموما من اجل الكلام الذي كلمه به نابوت اليزرعيلي قائلا لا اعطيك ميراث آبائي. واضطجع على سريره وحوّل وجهه ولم ياكل خبزا"));
arasuperobject.biblemap.put("b11_21_5", new arasuperobject(11,21,5,"فدخلت اليه ايزابل امرأته وقالت له لماذا روحك مكتئبة ولا تأكل خبزا"));
arasuperobject.biblemap.put("b11_21_6", new arasuperobject(11,21,6,"فقال لها لاني كلمت نابوت اليزرعيلي وقلت له اعطيني كرمك بفضة واذا شئت اعطيتك كرما عوضه فقال لا اعطيك كرمي"));
arasuperobject.biblemap.put("b11_21_7", new arasuperobject(11,21,7,"فقالت له ايزابل أأنت الآن تحكم على اسرائيل. قم كل خبزا وليطب قلبك. انا اعطيك كرم نابوت اليزرعيلي"));
arasuperobject.biblemap.put("b11_21_8", new arasuperobject(11,21,8,"ثم كتبت رسائل باسم اخآب وختمتها بخاتمه وارسلت الرسائل الى الشيوخ والاشراف الذين في مدينته الساكنين مع نابوت"));
arasuperobject.biblemap.put("b11_21_9", new arasuperobject(11,21,9,"وكتبت في الرسائل تقول. نادوا بصوم واجلسوا نابوت في راس الشعب"));
arasuperobject.biblemap.put("b11_21_10", new arasuperobject(11,21,10,"واجلسوا رجلين من بني بليعال تجاهه ليشهدا قائلين قد جدّفت على الله وعلى الملك. ثم اخرجوه وارجموه فيموت"));
arasuperobject.biblemap.put("b11_21_11", new arasuperobject(11,21,11,"ففعل رجال مدينته الشيوخ والاشراف الساكنون في مدينته كما ارسلت اليهم ايزابل كما هو مكتوب في الرسائل التي ارسلتها اليهم"));
arasuperobject.biblemap.put("b11_21_12", new arasuperobject(11,21,12,"فنادوا بصوم واجلسوا نابوت في راس الشعب"));
arasuperobject.biblemap.put("b11_21_13", new arasuperobject(11,21,13,"وأتى رجلان من بني بليعال وجلسا تجاهه وشهد رجلا بليعال على نابوت امام الشعب قائلين قد جدف نابوت على الله وعلى الملك. فاخرجوه خارج المدينة ورجموه بحجارة فمات"));
arasuperobject.biblemap.put("b11_21_14", new arasuperobject(11,21,14,"وارسلوا الى ايزابل يقولون قد رجم نابوت ومات"));
arasuperobject.biblemap.put("b11_21_15", new arasuperobject(11,21,15,"ولما سمعت ايزابل ان نابوت قد رجم ومات قالت ايزابل لاخآب قم رث كرم نابوت اليزرعيلي الذي ابى ان يعطيك اياه بفضة لان نابوت ليس حيّا بل هو ميت"));
arasuperobject.biblemap.put("b11_21_16", new arasuperobject(11,21,16,"ولما سمع اخآب ان نابوت قد مات قام اخآب لينزل الى كرم نابوت اليزرعيلي ليرثه"));
arasuperobject.biblemap.put("b11_21_17", new arasuperobject(11,21,17,"فكان كلام الرب الى ايليا التشبي قائلا"));
arasuperobject.biblemap.put("b11_21_18", new arasuperobject(11,21,18,"قم انزل للقاء اخآب ملك اسرائيل الذي في السامرة. هوذا هو في كرم نابوت الذي نزل اليه ليرثه"));
arasuperobject.biblemap.put("b11_21_19", new arasuperobject(11,21,19,"وكلمه قائلا هكذا قال الرب هل قتلت وورثت ايضا. ثم كلمه قائلا هكذا قال الرب. في المكان الذي لحست فيه الكلاب دم نابوت تلحس الكلاب دمك انت ايضا"));
arasuperobject.biblemap.put("b11_21_20", new arasuperobject(11,21,20,"فقال اخآب لايليا هل وجدتني يا عدوي. فقال قد وجدتك لانك قد بعت نفسك لعمل الشر في عيني الرب"));
arasuperobject.biblemap.put("b11_21_21", new arasuperobject(11,21,21,"هانذا اجلب عليك شرا وابيد نسلك واقطع لاخآب كل بائل بحائط ومحجوز ومطلق في اسرائيل"));
arasuperobject.biblemap.put("b11_21_22", new arasuperobject(11,21,22,"واجعل بيتك كبيت يربعام بن نباط وكبيت بعشا بن اخيا لاجل الاغاظة التي اغظتني ولجعلك اسرائيل يخطئ"));
arasuperobject.biblemap.put("b11_21_23", new arasuperobject(11,21,23,"وتكلم الرب عن ايزابل ايضا قائلا ان الكلاب تاكل ايزابل عند مترسة يزرعيل"));
arasuperobject.biblemap.put("b11_21_24", new arasuperobject(11,21,24,"من مات لاخآب في المدينة تاكله الكلاب ومن مات في الحقل تاكله طيور السماء"));
arasuperobject.biblemap.put("b11_21_25", new arasuperobject(11,21,25,"ولم يكن كاخآب الذي باع نفسه لعمل الشر في عيني الرب الذي اغوته ايزابل امرأته"));
arasuperobject.biblemap.put("b11_21_26", new arasuperobject(11,21,26,"ورجس جدا بذهابه وراء الاصنام حسب كل ما فعل الاموريون الذين طردهم الرب من امام بني اسرائيل"));
arasuperobject.biblemap.put("b11_21_27", new arasuperobject(11,21,27,"ولما سمع اخآب هذا الكلام شقّ ثيابه وجعل مسحا على جسده وصام واضطجع بالمسح ومشى بسكوت"));
arasuperobject.biblemap.put("b11_21_28", new arasuperobject(11,21,28,"فكان كلام الرب الى ايليا التشبي قائلا"));
arasuperobject.biblemap.put("b11_21_29", new arasuperobject(11,21,29,"هل رأيت كيف اتضع اخآب امامي. فمن اجل انه قد اتضع امامي لا اجلب الشر في ايامه بل في ايام ابنه اجلب الشر على بيته"));
arasuperobject.biblemap.put("b11_22_1", new arasuperobject(11,22,1,"واقاموا ثلاث سنين بدون حرب بين ارام واسرائيل"));
arasuperobject.biblemap.put("b11_22_2", new arasuperobject(11,22,2,"وفي السنة الثالثة نزل يهوشافاط ملك يهوذا الى ملك اسرائيل"));
arasuperobject.biblemap.put("b11_22_3", new arasuperobject(11,22,3,"فقال ملك اسرائيل لعبيده أتعلمون ان راموت جلعاد لنا ونحن ساكتون عن اخذها من يد ملك ارام"));
arasuperobject.biblemap.put("b11_22_4", new arasuperobject(11,22,4,"وقال ليهوشافاط أتذهب معي للحرب الى راموت جلعاد. فقال يهوشافاط لملك اسرائيل مثلي مثلك. شعبي كشعبك وخيلي كخيلك"));
arasuperobject.biblemap.put("b11_22_5", new arasuperobject(11,22,5,"ثم قال يهوشافاط لملك اسرائيل اسأل اليوم عن كلام الرب"));
arasuperobject.biblemap.put("b11_22_6", new arasuperobject(11,22,6,"فجمع ملك اسرائيل الانبياء نحو اربع مئة رجل وقال لهم. أأذهب الى راموت جلعاد للقتال ام امتنع. فقالوا اصعد فيدفعها السيد ليد الملك"));
arasuperobject.biblemap.put("b11_22_7", new arasuperobject(11,22,7,"فقال يهوشافاط أما يوجد هنا بعد نبي للرب فنسأل منه"));
arasuperobject.biblemap.put("b11_22_8", new arasuperobject(11,22,8,"فقال ملك اسرائيل ليهوشافاط انه يوجد بعد رجل واحد لسؤال الرب به ولكني ابغضه لانه لا يتنبأ عليّ خيرا بل شرا وهو ميخا بن يملة. فقال يهوشافاط لا يقل الملك هكذا"));
arasuperobject.biblemap.put("b11_22_9", new arasuperobject(11,22,9,"فدعا ملك اسرائيل خصيا وقال اسرع اليّ بميخا بن يملة"));
arasuperobject.biblemap.put("b11_22_10", new arasuperobject(11,22,10,"وكان ملك اسرائيل ويهوشافاط ملك يهوذا جالسين كل واحد على كرسيه لابسين ثيابهما في ساحة عند مدخل باب السامرة وجميع الانبياء يتنبأون امامهما"));
arasuperobject.biblemap.put("b11_22_11", new arasuperobject(11,22,11,"وعمل صدقيا بن كنعنة لنفسه قرني حديد وقال هكذا قال الرب بهذه تنطح الاراميين حتى يفنوا"));
arasuperobject.biblemap.put("b11_22_12", new arasuperobject(11,22,12,"وتنبأ جميع الانبياء هكذا قائلين اصعد الى راموت جلعاد وافلح فيدفعها الرب ليد الملك"));
arasuperobject.biblemap.put("b11_22_13", new arasuperobject(11,22,13,"واما الرسول الذي ذهب ليدعو ميخا فكلمه قائلا هوذا كلام جميع الانبياء بفم واحد خير للملك. فليكن كلامك مثل كلام واحد منهم وتكلم بخير"));
arasuperobject.biblemap.put("b11_22_14", new arasuperobject(11,22,14,"فقال ميخا حيّ هو الرب ان ما يقوله لي الرب به اتكلم"));
arasuperobject.biblemap.put("b11_22_15", new arasuperobject(11,22,15,"ولما اتى الى الملك قال له الملك يا ميخا أنصعد الى راموت جلعاد للقتال ام نمتنع. فقال له اصعد وافلح فيدفعها الرب ليد الملك"));
arasuperobject.biblemap.put("b11_22_16", new arasuperobject(11,22,16,"فقال له الملك كم مرة استحلفتك ان لا تقول لي الا الحق باسم الرب"));
arasuperobject.biblemap.put("b11_22_17", new arasuperobject(11,22,17,"فقال رأيت كل اسرائيل مشتّتين على الجبال كخراف لا راعي لها. فقال الرب ليس لهؤلاء اصحاب فليرجعوا كل واحد الى بيته بسلام"));
arasuperobject.biblemap.put("b11_22_18", new arasuperobject(11,22,18,"فقال ملك اسرائيل ليهوشافاط أما قلت لك انه لا يتنبأ عليّ خيرا بل شرا"));
arasuperobject.biblemap.put("b11_22_19", new arasuperobject(11,22,19,"وقال فاسمع اذا كلام الرب. قد رأيت الرب جالسا على كرسيه وكل جند السماء وقوف لديه عن يمينه وعن يساره"));
arasuperobject.biblemap.put("b11_22_20", new arasuperobject(11,22,20,"فقال الرب من يغوي اخآب فيصعد ويسقط في راموت جلعاد. فقال هذا هكذا وقال ذاك هكذا"));
arasuperobject.biblemap.put("b11_22_21", new arasuperobject(11,22,21,"ثم خرج الروح ووقف امام الرب وقال انا اغويه. وقال له الرب بماذا"));
arasuperobject.biblemap.put("b11_22_22", new arasuperobject(11,22,22,"فقال اخرج واكون روح كذب في افواه جميع انبيائه. فقال انك تغويه وتقتدر. فاخرج وافعل هكذا"));
arasuperobject.biblemap.put("b11_22_23", new arasuperobject(11,22,23,"والآن هوذا قد جعل الرب روح كذب في افواه جميع انبيائك هؤلاء والرب تكلم عليك بشر"));
arasuperobject.biblemap.put("b11_22_24", new arasuperobject(11,22,24,"فتقدم صدقيا بن كنعنة وضرب ميخا على الفكّ وقال من اين عبر روح الرب مني ليكلمك"));
arasuperobject.biblemap.put("b11_22_25", new arasuperobject(11,22,25,"فقال ميخا انك سترى في ذلك اليوم الذي تدخل فيه من مخدع الى مخدع لتختبئ"));
arasuperobject.biblemap.put("b11_22_26", new arasuperobject(11,22,26,"فقال ملك اسرائيل خذ ميخا وردّه الى آمون رئيس المدينة والى يوآش ابن الملك"));
arasuperobject.biblemap.put("b11_22_27", new arasuperobject(11,22,27,"وقل هكذا قال الملك ضعوا هذا في السجن واطعموه خبز الضيق وماء الضيق حتى آتي بسلام"));
arasuperobject.biblemap.put("b11_22_28", new arasuperobject(11,22,28,"فقال ميخا ان رجعت بسلام فلم يتكلم الرب بي. وقال اسمعوا ايها الشعب اجمعون"));
arasuperobject.biblemap.put("b11_22_29", new arasuperobject(11,22,29,"فصعد ملك اسرائيل ويهوشافاط ملك يهوذا الى راموت جلعاد"));
arasuperobject.biblemap.put("b11_22_30", new arasuperobject(11,22,30,"فقال ملك اسرائيل ليهوشافاط اني اتنكّر وادخل الحرب. واما انت فالبس ثيابك. فتنكّر ملك اسرائيل ودخل الحرب"));
arasuperobject.biblemap.put("b11_22_31", new arasuperobject(11,22,31,"وامر ملك ارام رؤساء المركبات التي له الاثنين والثلاثين وقال لا تحاربوا صغيرا ولا كبيرا الا ملك اسرائيل وحده"));
arasuperobject.biblemap.put("b11_22_32", new arasuperobject(11,22,32,"فلما رأى رؤساء المركبات يهوشافاط قالوا انه ملك اسرائيل فمالوا عليه ليقاتلوه فصرخ يهوشافاط"));
arasuperobject.biblemap.put("b11_22_33", new arasuperobject(11,22,33,"فلما رأى رؤساء المركبات انه ليس ملك اسرائيل رجعوا عنه"));
arasuperobject.biblemap.put("b11_22_34", new arasuperobject(11,22,34,"وان رجلا نزع في قوسه غير متعمد وضرب ملك اسرائيل بين اوصال الدرع. فقال لمدير مركبته رد يدك واخرجني من الجيش لاني قد جرحت"));
arasuperobject.biblemap.put("b11_22_35", new arasuperobject(11,22,35,"واشتدّ القتال في ذلك اليوم وأوقف الملك في مركبته مقابل ارام ومات عند المساء وجرى دم الجرح الى حضن المركبة"));
arasuperobject.biblemap.put("b11_22_36", new arasuperobject(11,22,36,"وعبرت الرنّة في الجند عند غروب الشمس قائلا كل رجل الى مدينته وكل رجل الى ارضه"));
arasuperobject.biblemap.put("b11_22_37", new arasuperobject(11,22,37,"فمات الملك وأدخل السامرة فدفنوا الملك في السامرة"));
arasuperobject.biblemap.put("b11_22_38", new arasuperobject(11,22,38,"وغسلت المركبة في بركة السامرة فلحست الكلاب دمه. وغسلوا سلاحه. حسب كلام الرب الذي تكلم به"));
arasuperobject.biblemap.put("b11_22_39", new arasuperobject(11,22,39,"وبقية أمور اخآب وكل ما فعل وبيت العاج الذي بناه وكل المدن التي بناها أما هي مكتوبة في سفر اخبار الايام لملوك اسرائيل"));
arasuperobject.biblemap.put("b11_22_40", new arasuperobject(11,22,40,"فاضطجع اخآب مع آبائه وملك أخزيا ابنه عوضا عنه"));
arasuperobject.biblemap.put("b11_22_41", new arasuperobject(11,22,41,"وملك يهوشافاط ابن آسا على يهوذا في السنة الرابعة لاخآب ملك اسرائيل"));
arasuperobject.biblemap.put("b11_22_42", new arasuperobject(11,22,42,"وكان يهوشافاط ابن خمس وثلاثين سنة حين ملك وملك خمسا وعشرين سنة في اورشليم واسم امه عزوبة بنت شلحي"));
arasuperobject.biblemap.put("b11_22_43", new arasuperobject(11,22,43,"وسار في كل طريق آسا ابيه. لم يحد عنها. اذ عمل المستقيم في عيني الرب. الا ان المرتفعات لم تنتزع بل كان الشعب لا يزال يذبح ويوقد على المرتفعات"));
arasuperobject.biblemap.put("b11_22_44", new arasuperobject(11,22,44,"وصالح يهوشافاط ملك اسرائيل"));
arasuperobject.biblemap.put("b11_22_45", new arasuperobject(11,22,45,"وبقية امور يهوشافاط وجبروته الذي اظهره وكيف حارب أما هي مكتوبة في سفر اخبار الايام لملوك يهوذا"));
arasuperobject.biblemap.put("b11_22_46", new arasuperobject(11,22,46,"وبقية المأبونين الذين بقوا في ايام آسا ابيه ابادهم من الارض"));
arasuperobject.biblemap.put("b11_22_47", new arasuperobject(11,22,47,"ولم يكن في ادوم ملك. ملك وكيل"));
arasuperobject.biblemap.put("b11_22_48", new arasuperobject(11,22,48,"وعمل يهوشافاط سفن ترشيش لكي تذهب الى اوفير لاجل الذهب فلم تذهب لان السفن تكسرت في عصيون جابر"));
arasuperobject.biblemap.put("b11_22_49", new arasuperobject(11,22,49,"حينئذ قال اخزيا بن اخآب ليهوشافاط ليذهب عبيدي مع عبيدك في السفن. فلم يشأ يهوشافاط"));
arasuperobject.biblemap.put("b11_22_50", new arasuperobject(11,22,50,"واضطجع يهوشافاط مع آبائه ودفن مع آبائه في مدينة داود ابيه فملك يهورام ابنه عوضا عنه"));
arasuperobject.biblemap.put("b11_22_51", new arasuperobject(11,22,51,"اخزيا ابن اخآب ملك على اسرائيل في السامرة في السنة السابعة عشرة ليهوشافاط ملك يهوذا. ملك على اسرائيل سنتين"));
arasuperobject.biblemap.put("b11_22_52", new arasuperobject(11,22,52,"وعمل الشر في عيني الرب وسار في طريق ابيه وطريق امه وطريق يربعام بن نباط الذي جعل اسرائيل يخطئ"));
arasuperobject.biblemap.put("b11_22_53", new arasuperobject(11,22,53,"وعبد البعل وسجد له واغاظ الرب اله اسرائيل حسب كل ما فعل ابوه"));

