arasuperobject.biblemap.put("b13_3_14", new arasuperobject(13,3,14,"وابنه آمون وابنه يوشيا"));
arasuperobject.biblemap.put("b13_3_15", new arasuperobject(13,3,15,"وبنو يوشيا البكر يوحانان الثاني يهوياقيم الثالث صدقيا الرابع شلّوم"));
arasuperobject.biblemap.put("b13_3_16", new arasuperobject(13,3,16,"وابنا يهوياقيم يكنيا ابنه وصدقيا ابنه"));
arasuperobject.biblemap.put("b13_3_17", new arasuperobject(13,3,17,"وابنا يكنيا اسّير وشألتيئيل ابنه"));
arasuperobject.biblemap.put("b13_3_18", new arasuperobject(13,3,18,"وملكيرام وفدايا وشنأصّر ويقميا وهوشاماع وندبيا"));
arasuperobject.biblemap.put("b13_3_19", new arasuperobject(13,3,19,"وابنا فدايا زربابل وشمعي وبنو زربابل مشلام وحننيا وشلومية اختهم"));
arasuperobject.biblemap.put("b13_3_20", new arasuperobject(13,3,20,"وحشوبة واوهل وبرخيا وحسديا ويوشب حسد. خمسة"));
arasuperobject.biblemap.put("b13_3_21", new arasuperobject(13,3,21,"وبنو حننيا فلطيا ويشعيا وبنو رفايا وبنو ارنان وبنو عوبديا وبنو شكنيا"));
arasuperobject.biblemap.put("b13_3_22", new arasuperobject(13,3,22,"وبنو شكنيا شمعيا وبنو شمعيا حطّوش ويجآل وباريح ونعريا وشافاط. ستة"));
arasuperobject.biblemap.put("b13_3_23", new arasuperobject(13,3,23,"وبنو نعريا إليوعيني وحزقيا وعزريقام. ثلاثة"));
arasuperobject.biblemap.put("b13_3_24", new arasuperobject(13,3,24,"وبنو اليوعيني هوداياهو والياشيب وفلايا وعقّوب ويوحانان ودلايا وعناني. سبعة"));
arasuperobject.biblemap.put("b13_4_1", new arasuperobject(13,4,1,"بنو يهوذا فارص وحصرون وكرمي وحور وشوبال"));
arasuperobject.biblemap.put("b13_4_2", new arasuperobject(13,4,2,"ورآيا بن شوبال ولد يحث ويحث ولد اخوماي ولاهد. هذه عشائر الصرعيين"));
arasuperobject.biblemap.put("b13_4_3", new arasuperobject(13,4,3,"وهؤلاء لابي عيطم. يزرعيل ويشما ويدباش واسم اختهم هصّللفوني"));
arasuperobject.biblemap.put("b13_4_4", new arasuperobject(13,4,4,"وفنوئيل ابو جدور وعازر ابو حوشة. هؤلاء بنو حور بكر افراتة ابي بيت لحم"));
arasuperobject.biblemap.put("b13_4_5", new arasuperobject(13,4,5,"وكان لاشحور ابي تقوع امرأتان حلاة ونعرة"));
arasuperobject.biblemap.put("b13_4_6", new arasuperobject(13,4,6,"وولدت له نعرة اخزّام وحافر والتيماني والاخشتاري. هؤلاء بنو نعرة"));
arasuperobject.biblemap.put("b13_4_7", new arasuperobject(13,4,7,"وبنو حلاة صرث وصوحر واثنان"));
arasuperobject.biblemap.put("b13_4_8", new arasuperobject(13,4,8,"وقوص ولد عانوب وهصوبيبة وعشائر اخرحيل بن هارم"));
arasuperobject.biblemap.put("b13_4_9", new arasuperobject(13,4,9,"وكان يعبيص اشرف من اخوته. وسمتّه امه يعبيص قائلة لاني ولدته بحزن"));
arasuperobject.biblemap.put("b13_4_10", new arasuperobject(13,4,10,"ودعا يعبيص اله اسرائيل قائلا ليتك تباركني وتوسّع تخومي وتكون يدك معي وتحفظني من الشر حتى لا يتعبني. فآتاه الله بما سأل"));
arasuperobject.biblemap.put("b13_4_11", new arasuperobject(13,4,11,"وكلوب اخو شوحة ولد محير. هو ابو اشتون"));
arasuperobject.biblemap.put("b13_4_12", new arasuperobject(13,4,12,"واشتون ولد بيت رافا وفاسح وتحنّة ابا مدينة ناحاش. هؤلاء اهل ريكة"));
arasuperobject.biblemap.put("b13_4_13", new arasuperobject(13,4,13,"وابنا قناز عثنيئيل وسرايا وابن عثنيئيل حثاث"));
arasuperobject.biblemap.put("b13_4_14", new arasuperobject(13,4,14,"ومعونوثاي ولد عفرة وسرايا ولد يوآب ابا وادي الصناع لانهم كانوا صنّاعا"));
arasuperobject.biblemap.put("b13_4_15", new arasuperobject(13,4,15,"وبنو كالب بن يفنة عيرو وايلة وناعم. وابن ايلة قناز"));
arasuperobject.biblemap.put("b13_4_16", new arasuperobject(13,4,16,"وبنو يهللئيل زيف وزيفة وتيريا واسرئيل"));
arasuperobject.biblemap.put("b13_4_17", new arasuperobject(13,4,17,"وبنو عزرة يثر ومرد وعافر ويالون******وحبلت بمريم وشماي ويشبح ابي اشتموع"));
arasuperobject.biblemap.put("b13_4_18", new arasuperobject(13,4,18,"وامراته اليهودية ولدت يارد ابا جدور وحابر ابا سوكو ويقوثيئيل ابا زانوح وهؤلاء بنو بثية بنت فرعون التي اخذها مرد"));
arasuperobject.biblemap.put("b13_4_19", new arasuperobject(13,4,19,"وبنو امرأته اليهودية اخت نحم ابي قعيلة الجرمي واشتموع المعكي"));
arasuperobject.biblemap.put("b13_4_20", new arasuperobject(13,4,20,"وبنو شيمون امنون ورنّة بن حانان وتيلون. وابنا يشعي زوحيت وبنزوحيت"));
arasuperobject.biblemap.put("b13_4_21", new arasuperobject(13,4,21,"بنو شيلة بن يهوذا عير ابو ليكة ولعدة ابو مريشة وعشائر بيت عاملي البزّ من بيت اشبيع"));
arasuperobject.biblemap.put("b13_4_22", new arasuperobject(13,4,22,"ويوقيم واهل كزيبا ويوآش وساراف الذين هم اصحاب موآب ويشوبي لحم وهذه الامور قديمة"));
arasuperobject.biblemap.put("b13_4_23", new arasuperobject(13,4,23,"هؤلاء هم الخزافون وسكان نتاعيم وجديرة. اقاموا هناك مع الملك لشغله"));
arasuperobject.biblemap.put("b13_4_24", new arasuperobject(13,4,24,"بنو شمعون نموئيل ويامين ويريب وزارح وشاول"));
