arasuperobject.biblemap.put("b13_4_27", new arasuperobject(13,4,27,"وكان لشمعي ستة عشر ابنا وست بنات. واما اخوته فلم يكن لهم بنون كثيرون وكل عشائرهم لم يكثروا مثل بني يهوذا"));
arasuperobject.biblemap.put("b13_4_28", new arasuperobject(13,4,28,"واقاموا في بئر سبع ومولادة وحصر شوعال"));
