arasuperobject.biblemap.put("b13_4_30", new arasuperobject(13,4,30,"وفي بتوئيل وحرمة وصقلغ"));
arasuperobject.biblemap.put("b13_4_31", new arasuperobject(13,4,31,"وفي بيت مركبوت وحصر سوسيم وبيت برئي وشعرايم. هذه مدنهم الى حينما ملك داود"));
arasuperobject.biblemap.put("b13_4_32", new arasuperobject(13,4,32,"وقراهم عيطم وعين ورومون وتوكن وعاشان خمس مدن"));
