arasuperobject.biblemap.put("b13_4_34", new arasuperobject(13,4,34,"ومشوباب ويمليك ويوشا بن امصيا"));
arasuperobject.biblemap.put("b13_4_35", new arasuperobject(13,4,35,"ويوئيل وياهو بن يوشبيا بن سرايا بن عسيئيل"));
arasuperobject.biblemap.put("b13_4_36", new arasuperobject(13,4,36,"وأليوعيناي ويعقوبا ويشوحايا وعسايا وعديئيل ويسيميئيل وبنايا"));
arasuperobject.biblemap.put("b13_4_37", new arasuperobject(13,4,37,"وزيزا بن شفعي بن الون بن يدايا بن شمري بن شمعيا"));
arasuperobject.biblemap.put("b13_4_38", new arasuperobject(13,4,38,"هؤلاء الواردون باسمائهم رؤساء في عشائرهم وبيوت آبائهم امتدوا كثيرا"));
arasuperobject.biblemap.put("b13_4_39", new arasuperobject(13,4,39,"وساروا الى مدخل جدور الى شرقي الوادي ليفتشوا على مرعى لماشيتهم"));
