arasuperobject.biblemap.put("b13_4_41", new arasuperobject(13,4,41,"وجاء هؤلاء المكتوبة اسماؤهم في ايام حزقيا ملك يهوذا وضربوا خيمهم والمعونيين الذين وجدوا هناك وحرّموهم الى هذا اليوم وسكنوا مكانهم لان هناك مرعى لماشيتهم"));
arasuperobject.biblemap.put("b13_4_42", new arasuperobject(13,4,42,"ومنهم من بني شمعون ذهب الى جبل سعير خمس مئة رجل وقدامهم فلطيا ونعريا ورافايا وعزّيئيل بنو يشعي"));
arasuperobject.biblemap.put("b13_4_43", new arasuperobject(13,4,43,"وضربوا بقية المنفلتين من عماليق وسكنوا هناك الى هذا اليوم"));
arasuperobject.biblemap.put("b13_5_1", new arasuperobject(13,5,1,"وبنو راوبين بكر اسرائيل. لانه هو البكر ولاجل تدنيسه فراش ابيه أعطيت بكوريته لبني يوسف بن اسرائيل فلم ينسب بكرا"));
arasuperobject.biblemap.put("b13_5_2", new arasuperobject(13,5,2,"لان يهوذا اعتزّ على اخوته ومنه الرئيس واما البكورية فليوسف"));
arasuperobject.biblemap.put("b13_5_3", new arasuperobject(13,5,3,"بنو رأوبين بكر اسرائيل حنوك وفلّو وحصرون وكرمي"));
arasuperobject.biblemap.put("b13_5_4", new arasuperobject(13,5,4,"بنو يوئيل ابنه شمعيا وابنه جوج وابنه شمعي"));
arasuperobject.biblemap.put("b13_5_5", new arasuperobject(13,5,5,"وابنه ميخا وابنه رآيا وابنه بعل"));
arasuperobject.biblemap.put("b13_5_6", new arasuperobject(13,5,6,"وابنه بئيرة الذي سباه تغلث فلناسر ملك اشور. هو رئيس الرأوبينيين"));
arasuperobject.biblemap.put("b13_5_7", new arasuperobject(13,5,7,"واخوته حسب عشائرهم في الانتساب حسب مواليدهم الرئيس يعيئيل وزكريا"));
arasuperobject.biblemap.put("b13_5_8", new arasuperobject(13,5,8,"وبالع بن عزاز بن شامع بن يوئيل الذي سكن في عروعير حتى الى نبو وبعل معون"));
arasuperobject.biblemap.put("b13_5_9", new arasuperobject(13,5,9,"وسكن شرقا الى مدخل البرية من نهر الفرات لان ماشيتهم كثرت في ارض جلعاد"));
arasuperobject.biblemap.put("b13_5_10", new arasuperobject(13,5,10,"وفي ايام شاول عملوا حربا مع الهاجريين فسقطوا بايديهم وسكنوا في خيامهم في جميع جهات شرق جلعاد"));
arasuperobject.biblemap.put("b13_5_11", new arasuperobject(13,5,11,"وبنو جاد سكنوا مقابلهم في ارض باشان حتى الى سلخة"));
arasuperobject.biblemap.put("b13_5_12", new arasuperobject(13,5,12,"يوئيل الراس وشافاط ثانيه ويعناي وشافاط في باشان"));
arasuperobject.biblemap.put("b13_5_13", new arasuperobject(13,5,13,"واخوتهم حسب بيوت آبائهم ميخائيل ومشلام وشبع ويوراي ويعكان وزيع وعابر. سبعة"));
arasuperobject.biblemap.put("b13_5_14", new arasuperobject(13,5,14,"هؤلاء بنو ابيحايل بن حوري بن ياروح بن جلعاد بن ميخائيل بن يشيشاي بن يحدو بن بوز"));
arasuperobject.biblemap.put("b13_5_15", new arasuperobject(13,5,15,"واخي بن عبديئيل بن جوني رئيس بيت آبائهم"));
arasuperobject.biblemap.put("b13_5_16", new arasuperobject(13,5,16,"وسكنوا في جلعاد في باشان وقراها وفي جميع مسارح شارون عند مخارجها"));
arasuperobject.biblemap.put("b13_5_17", new arasuperobject(13,5,17,"جميعهم انتسبوا في ايام يوثام ملك يهوذا وفي ايام يربعام ملك اسرائيل"));
arasuperobject.biblemap.put("b13_5_18", new arasuperobject(13,5,18,"بنو رأوبين والجاديون ونصف سبط منسّى من بني البأس رجال يحملون الترس والسيف ويشدّون القوس ومتعلمون القتال اربعة واربعون الفا وسبع مئة وستون من الخارجين في الجيش"));
arasuperobject.biblemap.put("b13_5_19", new arasuperobject(13,5,19,"وعملوا حربا مع الهاجريين ويطور ونافيش ونوداب"));
arasuperobject.biblemap.put("b13_5_20", new arasuperobject(13,5,20,"فانتصروا عليهم فدفع ليدهم الهاجريون وكل من معهم لانهم صرخوا الى الله في القتال فاستجاب لهم لانهم اتكلوا عليه"));
arasuperobject.biblemap.put("b13_5_21", new arasuperobject(13,5,21,"ونهبوا ماشيتهم جمالهم خمسين الفا وغنما مئتين وخمسين الفا وحميرا الفين وسبوا اناسا مئة الف"));
arasuperobject.biblemap.put("b13_5_22", new arasuperobject(13,5,22,"لانه سقط قتلى كثيرون لان القتال انما كان من الله. وسكنوا مكانهم الى السبي"));
arasuperobject.biblemap.put("b13_5_23", new arasuperobject(13,5,23,"وبنو نصف سبط منسّى سكنوا في الارض وامتدّوا من باشان الى بعل حرمون وسنير وجبل حرمون"));
arasuperobject.biblemap.put("b13_5_24", new arasuperobject(13,5,24,"وهؤلاء رؤوس بيوت آبائهم. عافر ويشعي واليئيل وعزريئيل ويرميا وهودوبا ويحديئيل رجال جبابرة بأس وذوو اسم ورؤوس لبيوت آبائهم"));
arasuperobject.biblemap.put("b13_5_25", new arasuperobject(13,5,25,"وخانوا اله آبائهم وزنوا وراء آلهة شعوب الارض الذين طردهم الرب من امامهم"));
arasuperobject.biblemap.put("b13_5_26", new arasuperobject(13,5,26,"فنبّه اله اسرائيل روح فول ملك اشور وروح تلغث فلناسر ملك اشور فسباهم الرأوبينيين والجاديين ونصف سبط منسّى واتى بهم الى حلح وخابور وهارا ونهر جوزان الى هذا اليوم"));
arasuperobject.biblemap.put("b13_6_1", new arasuperobject(13,6,1,"بنو لاوي جرشون وقهات ومراري"));
arasuperobject.biblemap.put("b13_6_2", new arasuperobject(13,6,2,"وبنو قهات عمرام ويصهار وحبرون وعزيئيل"));
arasuperobject.biblemap.put("b13_6_3", new arasuperobject(13,6,3,"وبنو عمرام هرون وموسى ومريم. وبنو هرون ناداب وابيهو والعازار وايثامار"));
arasuperobject.biblemap.put("b13_6_4", new arasuperobject(13,6,4,"العازار ولد فينحاس وفينحاس ولد ابيشوع"));
arasuperobject.biblemap.put("b13_6_5", new arasuperobject(13,6,5,"وابيشوع ولد بقّي وبقّي ولد عزّي"));
arasuperobject.biblemap.put("b13_6_6", new arasuperobject(13,6,6,"وعزّي ولد زرحيا وزرحيا ولد مرايوث"));
arasuperobject.biblemap.put("b13_6_7", new arasuperobject(13,6,7,"ومرايوث ولد امريا وامريا ولد اخيطوب"));
arasuperobject.biblemap.put("b13_6_8", new arasuperobject(13,6,8,"واخيطوب ولد صادوق وصادوق ولد اخيمعص"));
arasuperobject.biblemap.put("b13_6_9", new arasuperobject(13,6,9,"واخيمعص ولد عزريا وعزريا ولد يوحانان"));
arasuperobject.biblemap.put("b13_6_10", new arasuperobject(13,6,10,"ويوحانان ولد عزريا وهو الذي كهن في البيت الذي بناه سليمان في اورشليم"));
arasuperobject.biblemap.put("b13_6_11", new arasuperobject(13,6,11,"وعزريا ولد امريا وامريا ولد اخيطوب"));
arasuperobject.biblemap.put("b13_6_12", new arasuperobject(13,6,12,"واخيطوب ولد صادوق وصادوق ولد شلوم"));
arasuperobject.biblemap.put("b13_6_13", new arasuperobject(13,6,13,"وشلوم ولد حلقيا وحلقيا ولد عزريا"));
arasuperobject.biblemap.put("b13_6_14", new arasuperobject(13,6,14,"وعزريا ولد سرايا وسرايا ولد يهوصاداق"));
arasuperobject.biblemap.put("b13_6_15", new arasuperobject(13,6,15,"ويهوصاداق سار في سبي الرب يهوذا واورشليم بيد نبوخذناصّر"));
arasuperobject.biblemap.put("b13_6_16", new arasuperobject(13,6,16,"بنو لاوي جرشوم وقهات ومراري"));
arasuperobject.biblemap.put("b13_6_17", new arasuperobject(13,6,17,"وهذان اسما ابني جرشوم لبني وشمعي"));
arasuperobject.biblemap.put("b13_6_18", new arasuperobject(13,6,18,"وبنو قهات عمرام ويصهار وحبرون وعزيئيل"));
arasuperobject.biblemap.put("b13_6_19", new arasuperobject(13,6,19,"وابنا مراري محلي وموشي. فهذه عشائر اللاويين حسب آبائهم"));
arasuperobject.biblemap.put("b13_6_20", new arasuperobject(13,6,20,"لجرشوم لبني ابنه ويحث ابنه وزمّة ابنه"));
arasuperobject.biblemap.put("b13_6_21", new arasuperobject(13,6,21,"ويوآخ ابنه وعدّو ابنه وزارح ابنه ويأثراي ابنه"));
arasuperobject.biblemap.put("b13_6_22", new arasuperobject(13,6,22,"بنو قهات عميناداب ابنه وقورح ابنه واسّير ابنه"));
