arasuperobject.biblemap.put("b13_8_15", new arasuperobject(13,8,15,"وزبديا وعراد وعادر"));
arasuperobject.biblemap.put("b13_8_16", new arasuperobject(13,8,16,"وميخائيل ويشفة ويوخا ابناء بريعة"));
arasuperobject.biblemap.put("b13_8_17", new arasuperobject(13,8,17,"وزبديا ومشلام وحزقي وحابر"));
arasuperobject.biblemap.put("b13_8_18", new arasuperobject(13,8,18,"ويشمراي ويزلياه ويوباب ابناء ألفعل"));
arasuperobject.biblemap.put("b13_8_19", new arasuperobject(13,8,19,"وياقيم وزكري وزبدي"));
arasuperobject.biblemap.put("b13_8_20", new arasuperobject(13,8,20,"واليعيناي وصلّتاي وايليئيل"));
arasuperobject.biblemap.put("b13_8_21", new arasuperobject(13,8,21,"وعدايا وبرايا وشمرة ابناء شمعي"));
arasuperobject.biblemap.put("b13_8_22", new arasuperobject(13,8,22,"ويشفان وعابر وايليئيل"));
arasuperobject.biblemap.put("b13_8_23", new arasuperobject(13,8,23,"وعبدون وزكري وحانان"));
arasuperobject.biblemap.put("b13_8_24", new arasuperobject(13,8,24,"وحننيا وعيلام وعنثوثيا"));
arasuperobject.biblemap.put("b13_8_25", new arasuperobject(13,8,25,"ويفدايا وفنوئيل ابناء شاشق"));
arasuperobject.biblemap.put("b13_8_26", new arasuperobject(13,8,26,"وشمشراي وشحريا وعثليا"));
arasuperobject.biblemap.put("b13_8_27", new arasuperobject(13,8,27,"ويعرشيا وايليا وزكري ابناء يروحام"));
arasuperobject.biblemap.put("b13_8_28", new arasuperobject(13,8,28,"هؤلاء رؤوس آباء. حسب مواليدهم رؤوس. هؤلاء سكنوا في اورشليم"));
arasuperobject.biblemap.put("b13_8_29", new arasuperobject(13,8,29,"وفي جبعون سكن ابو جبعون واسم امرأته معكة"));
arasuperobject.biblemap.put("b13_8_30", new arasuperobject(13,8,30,"وابنه البكر عبدون ثم صور وقيس وبعل ناداب"));
arasuperobject.biblemap.put("b13_8_31", new arasuperobject(13,8,31,"وجدور واخيو وزاكر"));
arasuperobject.biblemap.put("b13_8_32", new arasuperobject(13,8,32,"ومقلوث ولد شماة. وهم ايضا مع اخوتهم سكنوا في اورشليم مقابل اخوتهم"));
arasuperobject.biblemap.put("b13_8_33", new arasuperobject(13,8,33,"ونير ولد قيس وقيس ولد شاول وشاول ولد يهوناثان وملكيشوع وابيناداب واشبعل"));
arasuperobject.biblemap.put("b13_8_34", new arasuperobject(13,8,34,"وابن يهوناثان مريببعل ومريببعل ولد ميخا"));
arasuperobject.biblemap.put("b13_8_35", new arasuperobject(13,8,35,"وبنو ميخا فيثون ومالك وتاريع وآحاز"));
arasuperobject.biblemap.put("b13_8_36", new arasuperobject(13,8,36,"وآحاز ولد يهوعدّة ويهوعدة ولد علمث وعزموت وزمري. وزمري ولد موصا"));
arasuperobject.biblemap.put("b13_8_37", new arasuperobject(13,8,37,"وموصا ولد بنعة ورافة ابنه والعاسة ابنه وآصيل ابنه"));
arasuperobject.biblemap.put("b13_8_38", new arasuperobject(13,8,38,"ولآصيل ستة بنين وهذه اسماؤهم. عزريقام وبكرو واسماعيل وشعريا وعوبديا وحانان. كل هؤلاء بنو آصيل"));
arasuperobject.biblemap.put("b13_8_39", new arasuperobject(13,8,39,"وبنو عاشق اخيه اولام بكره ويعوش الثاني واليفلط الثالث"));
arasuperobject.biblemap.put("b13_8_40", new arasuperobject(13,8,40,"وكان بنو اولام رجالا جبابرة بأس يغرقون في القسي كثيري البنين وبني البنين مئة وخمسين. كل هؤلاء من بني بنيامين"));
arasuperobject.biblemap.put("b13_9_1", new arasuperobject(13,9,1,"وانتسب كل اسرائيل وها هم مكتوبون في سفر ملوك اسرائيل. وسبي يهوذا الى بابل لاجل خيانتهم"));
arasuperobject.biblemap.put("b13_9_2", new arasuperobject(13,9,2,"والسكان الاولون في ملكهم ومدنهم هم اسرائيل الكهنة واللاويون والنثينيم"));
arasuperobject.biblemap.put("b13_9_3", new arasuperobject(13,9,3,"وسكن في اورشليم من بني يهوذا وبني بنيامين وبني افرايم ومنسّى"));
arasuperobject.biblemap.put("b13_9_4", new arasuperobject(13,9,4,"عوثاي بن عميهود بن عمري بن إمري بن باني من بني فارص بن يهوذا"));
arasuperobject.biblemap.put("b13_9_5", new arasuperobject(13,9,5,"ومن الشيلونيين عسايا البكر وبنوه"));
arasuperobject.biblemap.put("b13_9_6", new arasuperobject(13,9,6,"ومن بني زارح يعوئيل واخوتهم ست مئة وتسعون"));
arasuperobject.biblemap.put("b13_9_7", new arasuperobject(13,9,7,"ومن بني بنيامين سلّو بن مشلام بن هودويا بن هسنواة"));
arasuperobject.biblemap.put("b13_9_8", new arasuperobject(13,9,8,"ويبنيا بن يروحام وايلة بن عزي بن مكري ومشلام بن شفطيا بن رعوئيل بن يبنيا"));
arasuperobject.biblemap.put("b13_9_9", new arasuperobject(13,9,9,"واخوتهم حسب مواليدهم تسع مئة وستة وخمسون. كل هؤلاء الرجال رؤوس آباء لبيوت آبائهم"));
arasuperobject.biblemap.put("b13_9_10", new arasuperobject(13,9,10,"ومن الكهنة يدعيا ويهوياريب وياكين"));
arasuperobject.biblemap.put("b13_9_11", new arasuperobject(13,9,11,"وعزريا بن حلقيا بن مشلام بن صادوق بن مرايوث بن اخيطوب رئيس بيت الله"));
arasuperobject.biblemap.put("b13_9_12", new arasuperobject(13,9,12,"وعدايا بن يروحام بن فشحور بن ملكيا ومعساي بن عديئيل بن يحزيره بن مشلام بن مشلّيميت بن إمّير"));
arasuperobject.biblemap.put("b13_9_13", new arasuperobject(13,9,13,"واخوتهم رؤوس بيوت آبائهم الف وسبع مئة وستون جبابرة بأس لعمل خدمة بيت الله"));
arasuperobject.biblemap.put("b13_9_14", new arasuperobject(13,9,14,"ومن اللاويين شمعيا بن حشّوب بن عزريقام بن حشبيا من بني مراري"));
arasuperobject.biblemap.put("b13_9_15", new arasuperobject(13,9,15,"وبقبقّر وحرش وجلال ومتنيا بن ميخا بن زكري بن آساف"));
arasuperobject.biblemap.put("b13_9_16", new arasuperobject(13,9,16,"وعوبديا بن شمعيا بن جلال بن يدوثون وبرخيا بن آسا بن القانة الساكن في قرى النطوفاتيين"));
arasuperobject.biblemap.put("b13_9_17", new arasuperobject(13,9,17,"والبوابون شلّوم وعقّوب وطلمون واخيمان واخوتهم. شلوم الراس"));
arasuperobject.biblemap.put("b13_9_18", new arasuperobject(13,9,18,"وحتى الآن هم في باب الملك الى الشرق. هم البوابون لفرق بني لاوي"));
arasuperobject.biblemap.put("b13_9_19", new arasuperobject(13,9,19,"وشلوم بن قوري بن ابياساف بن قورح واخوته لبيوت آبائه. القورحيون على عمل الخدمة حراس ابواب الخيمة وآباؤهم على محلّة الرب حراس المدخل"));
arasuperobject.biblemap.put("b13_9_20", new arasuperobject(13,9,20,"وفينحاس بن العازار كان رئيسا عليهم سابقا والرب معه"));
arasuperobject.biblemap.put("b13_9_21", new arasuperobject(13,9,21,"وزكريا بن مشلميا كان بواب باب خيمة الاجتماع"));
arasuperobject.biblemap.put("b13_9_22", new arasuperobject(13,9,22,"جميع هؤلاء المنتخبين بوابين للابواب مئتان واثنا عشر وقد انتسبوا حسب قراهم. اقامهم داود وصموئيل الرائي على وظائفهم"));
arasuperobject.biblemap.put("b13_9_23", new arasuperobject(13,9,23,"وكانوا هم وبنوهم على ابواب بيت الرب بيت الخيمة للحراسة"));
arasuperobject.biblemap.put("b13_9_24", new arasuperobject(13,9,24,"في الجهات الاربع كان البوابون في الشرق والغرب والشمال والجنوب"));
arasuperobject.biblemap.put("b13_9_25", new arasuperobject(13,9,25,"وكان اخوتهم في قراهم للمجيء معهم في السبعة الايام حينا بعد حين"));
arasuperobject.biblemap.put("b13_9_26", new arasuperobject(13,9,26,"لانه بالوظيفة رؤساء البوابين هؤلاء الاربعة هم لاويون وكانوا على المخادع وعلى خزائن بيت الله"));
arasuperobject.biblemap.put("b13_9_27", new arasuperobject(13,9,27,"ونزلوا حول بيت الله لان عليهم الحراسة وعليهم الفتح كل صباح"));
arasuperobject.biblemap.put("b13_9_28", new arasuperobject(13,9,28,"وبعضهم على آنية الخدمة لانهم كانوا يدخلونها بعدد ويخرجونها بعدد"));
arasuperobject.biblemap.put("b13_9_29", new arasuperobject(13,9,29,"وبعضهم اؤتمنوا على الآنية وعلى كل امتعة القدس وعلى الدقيق والخمر والزيت واللبان والاطياب"));
arasuperobject.biblemap.put("b13_9_30", new arasuperobject(13,9,30,"والبعض من بني الكهنة كانوا يركّبون دهون الاطياب"));
arasuperobject.biblemap.put("b13_9_31", new arasuperobject(13,9,31,"ومتثيا واحد من اللاويين وهو بكر شلوم القورحي بالوظيفة على عمل المطبوخات"));
arasuperobject.biblemap.put("b13_9_32", new arasuperobject(13,9,32,"والبعض من بني القهاتيين من اخوتهم على خبز الوجوه ليهيئوه في كل سبت"));
arasuperobject.biblemap.put("b13_9_33", new arasuperobject(13,9,33,"فهؤلاء هم المغنون رؤوس آباء اللاويين في المخادع وهم معفون لانه نهارا وليلا عليهم العمل"));
arasuperobject.biblemap.put("b13_9_34", new arasuperobject(13,9,34,"هؤلاء رؤوس آباء اللاويين. حسب مواليدهم رؤوس. هؤلاء سكنوا في اورشليم"));
arasuperobject.biblemap.put("b13_9_35", new arasuperobject(13,9,35,"وفي جبعون سكن ابو جبعون يعوئيل واسم امرأته معكة"));
arasuperobject.biblemap.put("b13_9_36", new arasuperobject(13,9,36,"وابنه البكر عبدون ثم صور وقيس وبعل ونير وناداب"));
arasuperobject.biblemap.put("b13_9_37", new arasuperobject(13,9,37,"وحدور واخيو وزكريا ومقلوث"));
arasuperobject.biblemap.put("b13_9_38", new arasuperobject(13,9,38,"ومقلوث ولد شمآم. وهم ايضا سكنوا مقابل اخوتهم في اورشليم مع اخوتهم"));
arasuperobject.biblemap.put("b13_9_39", new arasuperobject(13,9,39,"ونير ولد قيس وقيس ولد شاول وشاول ولد يهوناثان وملكيشوع وابيناداب واشبعل"));
arasuperobject.biblemap.put("b13_9_40", new arasuperobject(13,9,40,"وابن يهوناثان مريببعل ومريببعل ولد ميخا"));
arasuperobject.biblemap.put("b13_9_41", new arasuperobject(13,9,41,"وبنو ميخا فيثون ومالك وتحريع وآحاز"));
arasuperobject.biblemap.put("b13_9_42", new arasuperobject(13,9,42,"وآحاز ولد يعرة ويعرة ولد علمث وعزموت وزمري. وزمري ولد موصا"));
arasuperobject.biblemap.put("b13_9_43", new arasuperobject(13,9,43,"وموصا ولد بنعا ورفايا ابنه والعسة ابنه وآصيل ابنه"));
arasuperobject.biblemap.put("b13_9_44", new arasuperobject(13,9,44,"وكان لآصيل ستة بنين وهذه اسماؤهم. عزريقام وبكرو ثم اسماعيل وشعريا وعوبديا وحانان. هؤلاء بنو آصيل"));
arasuperobject.biblemap.put("b13_10_1", new arasuperobject(13,10,1,"وحارب الفلسطينيون اسرائيل فهرب رجال اسرائيل من امام الفلسطينيين وسقطوا قتلى في جبل جلبوع"));
arasuperobject.biblemap.put("b13_10_2", new arasuperobject(13,10,2,"وشدّ الفلسطينيون وراء شاول ووراء بنيه وضرب الفلسطينيون يوناثان وابيناداب وملكيشوع ابناء شاول"));
arasuperobject.biblemap.put("b13_10_3", new arasuperobject(13,10,3,"واشتدّت الحرب على شاول فاصابته رماة القسي فانجرح من الرماة"));
arasuperobject.biblemap.put("b13_10_4", new arasuperobject(13,10,4,"فقال شاول لحامل سلاحه استلّ سيفك واطعنّي به لئلا ياتي هؤلاء الغلف ويطعنوني ويقبحوني. فلم يشأ حامل سلاحه لانه خاف جدا. فاخذ شاول السيف وسقط عليه"));
arasuperobject.biblemap.put("b13_10_5", new arasuperobject(13,10,5,"فلما رأى حامل سلاحه انه قد مات شاول سقط هو ايضا على السيف ومات"));
arasuperobject.biblemap.put("b13_10_6", new arasuperobject(13,10,6,"فمات شاول وبنوه الثلاثة وكل بيته ماتوا معا"));
arasuperobject.biblemap.put("b13_10_7", new arasuperobject(13,10,7,"ولما رأى جميع رجال اسرائيل الذين في الوادي انهم قد هربوا وان شاول وبنيه قد ماتوا تركوا مدنهم وهربوا فأتى الفلسطينيون وسكنوا بها"));
arasuperobject.biblemap.put("b13_10_8", new arasuperobject(13,10,8,"وفي الغد لما جاء الفلسطينيون ليعرّوا القتلى وجدوا شاول وبنيه ساقطين في جبل جلبوع"));
arasuperobject.biblemap.put("b13_10_9", new arasuperobject(13,10,9,"فعرّوه واخذوا راسه وسلاحه وارسلوا الى ارض الفلسطينيين في كل ناحية لاجل تبشير اصنامهم والشعب"));
arasuperobject.biblemap.put("b13_10_10", new arasuperobject(13,10,10,"ووضعوا سلاحه في بيت آلهتهم وسمّروا راسه في بيت داجون"));
arasuperobject.biblemap.put("b13_10_11", new arasuperobject(13,10,11,"ولما سمع كل يابيش جلعاد بكل ما فعل الفلسطينيون بشاول"));
arasuperobject.biblemap.put("b13_10_12", new arasuperobject(13,10,12,"قام كل ذي بأس وأخذوا جثة شاول وجثث بنيه وجاءوا بها الى يابيش ودفنوا عظامهم تحت البطمة في يابيش وصاموا سبعة ايام"));
arasuperobject.biblemap.put("b13_10_13", new arasuperobject(13,10,13,"فمات شاول بخيانته التي بها خان الرب من اجل كلام الرب الذي لم يحفظه. وايضا لاجل طلبه الى الجان للسؤال"));
arasuperobject.biblemap.put("b13_10_14", new arasuperobject(13,10,14,"ولم يسأل من الرب فاماته وحوّل المملكة الى داود بن يسّى"));
arasuperobject.biblemap.put("b13_11_1", new arasuperobject(13,11,1,"واجتمع كل اسرائيل الى داود في حبرون قائلين هوذا عظمك ولحمك نحن"));
arasuperobject.biblemap.put("b13_11_2", new arasuperobject(13,11,2,"ومنذ امس وما قبله حين كان شاول ملكا كنت انت تخرج وتدخل اسرائيل وقد قال لك الرب الهك انت ترعى شعبي اسرائيل وانت تكون رئيسا لشعبي اسرائيل"));
arasuperobject.biblemap.put("b13_11_3", new arasuperobject(13,11,3,"وجاء جميع شيوخ اسرائيل الى الملك الى حبرون فقطع داود معهم عهدا في حبرون امام الرب ومسحوا داود ملكا على اسرائيل حسب كلام الرب عن يد صموئيل"));
arasuperobject.biblemap.put("b13_11_4", new arasuperobject(13,11,4,"وذهب داود وكل اسرائيل الى اورشليم اي يبوس. وهناك اليبوسيون سكان الارض"));
arasuperobject.biblemap.put("b13_11_5", new arasuperobject(13,11,5,"وقال سكان يبوس لداود لا تدخل الى هنا. فاخذ داود حصن صهيون. هي مدينة داود"));
arasuperobject.biblemap.put("b13_11_6", new arasuperobject(13,11,6,"وقال داود ان الذي يضرب اليبوسيين اولا يكون راسا وقائدا. فصعد اولا يوآب ابن صروية فصار راسا"));
arasuperobject.biblemap.put("b13_11_7", new arasuperobject(13,11,7,"واقام داود في الحصن لذلك دعوه مدينة داود"));
arasuperobject.biblemap.put("b13_11_8", new arasuperobject(13,11,8,"وبنى المدينة حواليها من القلعة الى ما حولها. ويوآب جدّد سائر المدينة"));
arasuperobject.biblemap.put("b13_11_9", new arasuperobject(13,11,9,"وكان داود يتزايد متعظما ورب الجنود معه"));
arasuperobject.biblemap.put("b13_11_10", new arasuperobject(13,11,10,"وهؤلاء رؤساء الابطال الذين لداود الذين تشددوا معه في ملكه مع كل اسرائيل لتمليكه حسب كلام الرب من جهة اسرائيل"));
arasuperobject.biblemap.put("b13_11_11", new arasuperobject(13,11,11,"وهذا هو عدد الابطال الذين لداود. يشبعام بن حكموني رئيس الثوالث. هو هزّ رمحه على ثلاث مئة قتلهم دفعة واحدة"));
arasuperobject.biblemap.put("b13_11_12", new arasuperobject(13,11,12,"وبعده العازار بن دودو الاخوخي. هو من الابطال الثلاثة"));
arasuperobject.biblemap.put("b13_11_13", new arasuperobject(13,11,13,"هو كان مع داود في فسّ دمّيم وقد اجتمع هناك الفلسطينيون للحرب. وكانت قطعة الحقل مملوءة شعيرا فهرب الشعب من امام الفلسطينيين"));
arasuperobject.biblemap.put("b13_11_14", new arasuperobject(13,11,14,"ووقفوا في وسط القطعة وانقذوها وضربوا الفلسطينيين وخلّص الرب خلاصا عظيما"));
arasuperobject.biblemap.put("b13_11_15", new arasuperobject(13,11,15,"ونزل ثلاثة من الثلاثين رئيسا الى الصخر الى داود الى مغارة عدلام وجيش الفلسطينيين نازل في وادي الرفائيين"));
arasuperobject.biblemap.put("b13_11_16", new arasuperobject(13,11,16,"وكان داود حينئذ في الحصن. وحفظة الفلسطينيين حينئذ في بيت لحم"));
arasuperobject.biblemap.put("b13_11_17", new arasuperobject(13,11,17,"فتأوه داود وقال من يسقيني ماء من بئر بيت لحم التي عند الباب"));
arasuperobject.biblemap.put("b13_11_18", new arasuperobject(13,11,18,"فشقّ الثلاثة محلّة الفلسطينيين واستقوا ماء من بئر بيت لحم التي عند الباب وحملوه وأتوا به الى داود فلم يشأ داود ان يشربه بل سكبه للرب"));
arasuperobject.biblemap.put("b13_11_19", new arasuperobject(13,11,19,"وقال. حاشا لي من قبل الهي ان افعل ذلك. أأشرب دم هؤلاء الرجال بانفسهم. لانهم انما أتوا به بانفسهم. ولم يشأ ان يشربه. هذا ما فعله الابطال الثلاثة"));
arasuperobject.biblemap.put("b13_11_20", new arasuperobject(13,11,20,"وابشاي اخو يوآب كان رئيس ثلاثة. وهو قد هزّ رمحه على ثلاث مئة فقتلهم فكان له اسم بين الثلاثة"));
arasuperobject.biblemap.put("b13_11_21", new arasuperobject(13,11,21,"من الثلاثة اكرم على الاثنين وكان لهما رئيسا الا انه لم يصل الى الثلاثة الأول"));
arasuperobject.biblemap.put("b13_11_22", new arasuperobject(13,11,22,"بنايا بن يهوياداع ابن ذي بأس كثير الافعال من قبصئيل. هو الذي ضرب اسدي موآب وهو الذي نزل وضرب اسدا في وسط جب يوم الثلج"));
arasuperobject.biblemap.put("b13_11_23", new arasuperobject(13,11,23,"وهو ضرب الرجل المصري الذي قامته خمس اذرع. وفي يد المصري رمح كنول النسّاجين. فنزل اليه بعصا وخطف الرمح من يد المصري وقتله برمحه"));
arasuperobject.biblemap.put("b13_11_24", new arasuperobject(13,11,24,"هذا ما فعله بنايا بن يهوياداع فكان له اسم بين الثلاثة الابطال"));
arasuperobject.biblemap.put("b13_11_25", new arasuperobject(13,11,25,"هوذا اكرم على الثلاثين الا انه لم يصل الى الثلاثة. فجعله داود من اصحاب سرّه"));
arasuperobject.biblemap.put("b13_11_26", new arasuperobject(13,11,26,"وابطال الجيش هم عسائيل اخو يوآب والحانان بن دودو من بيت لحم"));
arasuperobject.biblemap.put("b13_11_27", new arasuperobject(13,11,27,"شموت الهروري حالص الفلوني"));
arasuperobject.biblemap.put("b13_11_28", new arasuperobject(13,11,28,"عيرا بن عقيش التقوعي ابيعزر العناثوثي"));
arasuperobject.biblemap.put("b13_11_29", new arasuperobject(13,11,29,"سبكاي الحوشاتي عيلاي الاخوخي"));
arasuperobject.biblemap.put("b13_11_30", new arasuperobject(13,11,30,"مهراي النطوفاتي خالد بن بعنة النطوفاتي"));
arasuperobject.biblemap.put("b13_11_31", new arasuperobject(13,11,31,"إتّاي بن ريباي من جبعة بني بنيامين بنايا الفرعتوني"));
arasuperobject.biblemap.put("b13_11_32", new arasuperobject(13,11,32,"حوراي من اودية جاعش ابيئيل العرباتي"));
arasuperobject.biblemap.put("b13_11_33", new arasuperobject(13,11,33,"عزموت البحرومي إليحبا الشعلبوني"));
arasuperobject.biblemap.put("b13_11_34", new arasuperobject(13,11,34,"بنو هاشم الجزوني يوناثان بن شاجاي الهراري"));
arasuperobject.biblemap.put("b13_11_35", new arasuperobject(13,11,35,"اخيآم بن ساكار الهراري اليفال بن اور"));
arasuperobject.biblemap.put("b13_11_36", new arasuperobject(13,11,36,"حافر المكيراتي واخيّا الفلوني"));
arasuperobject.biblemap.put("b13_11_37", new arasuperobject(13,11,37,"حصرو الكرملي نعراي بن ازباي"));
arasuperobject.biblemap.put("b13_11_38", new arasuperobject(13,11,38,"يوئيل اخو ناثان مبحار بن هجري"));
arasuperobject.biblemap.put("b13_11_39", new arasuperobject(13,11,39,"صالق العموني نحراي البئيروتي حامل سلاح يوآب ابن صروية"));
arasuperobject.biblemap.put("b13_11_40", new arasuperobject(13,11,40,"عيرا اليثري جارب اليثري"));
arasuperobject.biblemap.put("b13_11_41", new arasuperobject(13,11,41,"اوريا الحثي زاباد بن احلاي"));
arasuperobject.biblemap.put("b13_11_42", new arasuperobject(13,11,42,"عدينا بن شيزا الرأوبيني راس الرأوبينيين ومعه ثلاثون"));
arasuperobject.biblemap.put("b13_11_43", new arasuperobject(13,11,43,"حانان ابن معكة يوشافاط المثني"));
arasuperobject.biblemap.put("b13_11_44", new arasuperobject(13,11,44,"عزيا العشتروتي شاماع ويعوئيل ابنا حوثام العروعيري"));
arasuperobject.biblemap.put("b13_11_45", new arasuperobject(13,11,45,"يديعئيل بن شمري ويوحا اخوه التيصي"));
arasuperobject.biblemap.put("b13_11_46", new arasuperobject(13,11,46,"ايليئيل من محويم ويريباي ويوشويا ابنا النعم ويثمة الموآبي"));
arasuperobject.biblemap.put("b13_11_47", new arasuperobject(13,11,47,"ايليئيل وعوبيد ويعسيئيل من مصوبايا"));
arasuperobject.biblemap.put("b13_12_1", new arasuperobject(13,12,1,"وهؤلاء هم الذين جاءوا الى داود الى صقلغ وهو بعد محجوز عن وجه شاول بن قيس وهم من الابطال مساعدون في الحرب"));
arasuperobject.biblemap.put("b13_12_2", new arasuperobject(13,12,2,"نازعون في القسي يرمون الحجارة والسهام من القسي باليمين واليسار من اخوة شاول من بنيامين"));
arasuperobject.biblemap.put("b13_12_3", new arasuperobject(13,12,3,"الراس اخيعزر ثم يوآش ابنا شماعة الجبعي ويزوئيل وفالط ابنا عزموت وبراخة وياهو العناثوثي"));
arasuperobject.biblemap.put("b13_12_4", new arasuperobject(13,12,4,"ويشمعيا الجبعوني البطل بين الثلاثين وعلى الثلاثين ويرميا ويحزيئيل ويوحانان ويوزاباد الجديري"));
arasuperobject.biblemap.put("b13_12_5", new arasuperobject(13,12,5,"وإلعوزاي ويريموث وبعليا وشمريا وشفطيا الحروفي"));
arasuperobject.biblemap.put("b13_12_6", new arasuperobject(13,12,6,"والقانة ويشيا وعزرئيل ويوعزر ويشبعام القورحيون"));
arasuperobject.biblemap.put("b13_12_7", new arasuperobject(13,12,7,"ويوعيلة وزبديا ابنا يروحام من جدور"));
arasuperobject.biblemap.put("b13_12_8", new arasuperobject(13,12,8,"ومن الجاديين انفصل الى داود الى الحصن في البرية جبابرة البأس رجال جيش للحرب صافّو اتراس ورماح وجوههم كوجوه الأسود وهم كالظبي على الجبال في السرعة"));
arasuperobject.biblemap.put("b13_12_9", new arasuperobject(13,12,9,"عازر الراس وعوبديا الثاني واليآب الثالث"));
arasuperobject.biblemap.put("b13_12_10", new arasuperobject(13,12,10,"ومشمنّة الرابع ويرميا الخامس"));
arasuperobject.biblemap.put("b13_12_11", new arasuperobject(13,12,11,"وعتّاي السادس وايليئيل السابع"));
arasuperobject.biblemap.put("b13_12_12", new arasuperobject(13,12,12,"ويوحانان الثامن والزاباد التاسع"));
arasuperobject.biblemap.put("b13_12_13", new arasuperobject(13,12,13,"ويرميا العاشر ومخبنّاي الحادي عشر"));
arasuperobject.biblemap.put("b13_12_14", new arasuperobject(13,12,14,"هؤلاء من بني جاد رؤوس الجيش. صغيرهم لمئة والكبير لالف"));
arasuperobject.biblemap.put("b13_12_15", new arasuperobject(13,12,15,"هؤلاء هم الذين عبروا الاردن في الشهر الاول وهو ممتلئ الى جميع شطوطه وهزموا كل اهل الاودية شرقا وغربا"));
arasuperobject.biblemap.put("b13_12_16", new arasuperobject(13,12,16,"وجاء قوم من بني بنيامين ويهوذا الى الحصن الى داود"));
arasuperobject.biblemap.put("b13_12_17", new arasuperobject(13,12,17,"فخرج داود لاستقبالهم واجاب وقال لهم ان كنتم قد جئتم بسلام اليّ لتساعدوني يكون لي معكم قلب واحد. وان كان لكي تدفعوني لعدوّي ولا ظلم في يدي فلينظر اله آبائنا وينصف"));
arasuperobject.biblemap.put("b13_12_18", new arasuperobject(13,12,18,"فحلّ الروح على عماساي راس الثوالث فقال لك نحن يا داود ومعك نحن يا ابن يسّى. سلام سلام لك وسلام لمساعديك. لان الهك معينك. فقبلهم داود وجعلهم رؤوس الجيوش"));
arasuperobject.biblemap.put("b13_12_19", new arasuperobject(13,12,19,"وسقط الى داود بعض من منسّى حين جاء مع الفلسطينيين ضد شاول للقتال ولم يساعدوهم. لان اقطاب الفلسطينيين ارسلوه بمشورة قائلين انما برؤوسنا يسقط الى سيده شاول"));
arasuperobject.biblemap.put("b13_12_20", new arasuperobject(13,12,20,"حين انطلق الى صقلغ سقط اليه من منسّى عدناح ويوزاباد ويديعيئيل وميخائيل ويوزاباد واليهو وصلتاي رؤوس الوف منسّى"));
arasuperobject.biblemap.put("b13_12_21", new arasuperobject(13,12,21,"وهم ساعدوا داود على الغزاة لانهم جميعا جبابرة بأس وكانوا رؤساء في الجيش"));
arasuperobject.biblemap.put("b13_12_22", new arasuperobject(13,12,22,"لانه وقتئذ اتى اناس الى داود يوما فيوما لمساعدته حتى صاروا جيشا عظيما كجيش الله"));
arasuperobject.biblemap.put("b13_12_23", new arasuperobject(13,12,23,"وهذا عدد رؤوس المتجردين للقتال الذين جاءوا الى داود الى حبرون ليحوّلوا مملكة شاول اليه حسب قول الرب"));
arasuperobject.biblemap.put("b13_12_24", new arasuperobject(13,12,24,"بنو يهوذا حاملو الاتراس والرماح ستة آلاف وثمان مئة متجرد للقتال"));
arasuperobject.biblemap.put("b13_12_25", new arasuperobject(13,12,25,"من بني شمعون جبابرة بأس في الحرب سبعة آلاف ومئة"));
arasuperobject.biblemap.put("b13_12_26", new arasuperobject(13,12,26,"من بني لاوي اربعة آلاف وست مئة"));
arasuperobject.biblemap.put("b13_12_27", new arasuperobject(13,12,27,"ويهوياداع رئيس الهرونيين ومعه ثلاثة آلاف وسبع مئة"));
arasuperobject.biblemap.put("b13_12_28", new arasuperobject(13,12,28,"وصادوق غلام جبار بأس وبيت ابيه اثنان وعشرون قائدا"));
arasuperobject.biblemap.put("b13_12_29", new arasuperobject(13,12,29,"ومن بني بنيامين اخوة شاول ثلاثة آلاف والى هنا كان اكثرهم يحرسون حراسة بيت شاول"));
arasuperobject.biblemap.put("b13_12_30", new arasuperobject(13,12,30,"ومن بني افرايم عشرون الفا وثمان مئة جبابرة بأس وذوو اسم في بيوت آبائهم"));
arasuperobject.biblemap.put("b13_12_31", new arasuperobject(13,12,31,"ومن نصف سبط منسّى ثمانية عشر الفا قد تعيّنوا باسمائهم لكي ياتوا ويملّكوا داود"));
arasuperobject.biblemap.put("b13_12_32", new arasuperobject(13,12,32,"ومن بني يساكر الخبيرين بالاوقات لمعرفة ما يعمل اسرائيل رؤوسهم مئتان وكل اخوتهم تحت امرهم"));
arasuperobject.biblemap.put("b13_12_33", new arasuperobject(13,12,33,"من زبولون الخارجون للقتال المصطفون للحرب بجميع ادوات الحرب خمسون الفا وللاصطفاف من دون خلاف"));
arasuperobject.biblemap.put("b13_12_34", new arasuperobject(13,12,34,"ومن نفتالي الف رئيس ومعهم سبعة وثلاثون الفا بالاتراس والرماح"));
arasuperobject.biblemap.put("b13_12_35", new arasuperobject(13,12,35,"ومن الدانيين مصطفون للحرب ثمانية وعشرون الفا وست مئة"));
arasuperobject.biblemap.put("b13_12_36", new arasuperobject(13,12,36,"ومن اشير الخارجون للجيش لاجل الاصطفاف للحرب اربعون الفا"));
arasuperobject.biblemap.put("b13_12_37", new arasuperobject(13,12,37,"ومن عبر الاردن من الرأوبينيين والجاديين ونصف سبط منسّى بجميع ادوات جيش الحرب مئة وعشرون الفا"));
arasuperobject.biblemap.put("b13_12_38", new arasuperobject(13,12,38,"كل هؤلاء رجال حرب يصطفون صفوفا اتوا بقلب تام الى حبرون ليملّكوا داود على كل اسرائيل. وكذلك كل بقية اسرائيل بقلب واحد لتمليك داود"));
arasuperobject.biblemap.put("b13_12_39", new arasuperobject(13,12,39,"وكانوا هناك مع داود ثلاثة ايام يأكلون ويشربون لان اخوتهم اعدّوا لهم"));
arasuperobject.biblemap.put("b13_12_40", new arasuperobject(13,12,40,"وكذلك القريبون منهم حتى يساكر وزبولون ونفتالي كانوا ياتون بخبز على الحمير والجمال والبغال والبقر وبطعام من دقيق وتين وزبيب وخمر وزيت وبقر وغنم بكثرة لانه كان فرح في اسرائيل"));
arasuperobject.biblemap.put("b13_13_1", new arasuperobject(13,13,1,"وشاور داود قواد الالوف والمئات وكل رئيس"));
arasuperobject.biblemap.put("b13_13_2", new arasuperobject(13,13,2,"وقال داود لكل جماعة اسرائيل ان حسن عندكم وكان ذلك من الرب الهنا فلنرسل الى كل جهة الى اخوتنا الباقين في كل اراضي اسرائيل ومعهم الكهنة واللاويون في مدن مسارحهم ليجتمعوا الينا"));
arasuperobject.biblemap.put("b13_13_3", new arasuperobject(13,13,3,"فنرجع تابوت الهنا الينا لاننا لم نسأل به في ايام شاول"));
arasuperobject.biblemap.put("b13_13_4", new arasuperobject(13,13,4,"فقال كل الجماعة بان يفعلوا ذلك لان الأمر حسن في اعين جميع الشعب"));
arasuperobject.biblemap.put("b13_13_5", new arasuperobject(13,13,5,"وجمع داود كل اسرائيل من شيحور مصر الى مدخل حماة ليأتوا بتابوت الله من قرية يعاريم"));
arasuperobject.biblemap.put("b13_13_6", new arasuperobject(13,13,6,"وصعد داود وكل اسرائيل الى بعلة الى قرية يعاريم التي ليهوذا ليصعدوا من هناك تابوت الله الرب الجالس على الكروبيم الذي دعي بالاسم"));
arasuperobject.biblemap.put("b13_13_7", new arasuperobject(13,13,7,"واركبوا تابوت الله على عجلة جديدة من بيت ابيناداب وكان عزّا واخيو يسوقان العجلة"));
arasuperobject.biblemap.put("b13_13_8", new arasuperobject(13,13,8,"وداود وكل اسرائيل يلعبون امام الله بكل عزّ وباغاني وعيدان ورباب ودفوف وصنوج وابواق"));
arasuperobject.biblemap.put("b13_13_9", new arasuperobject(13,13,9,"ولما انتهوا الى بيدر كيدون مدّ عزّا يده ليمسك التابوت لان الثيران انشمصت"));
arasuperobject.biblemap.put("b13_13_10", new arasuperobject(13,13,10,"فحمي غضب الرب على عزّا وضربه من اجل انه مدّ يده الى التابوت فمات هناك امام الله"));
arasuperobject.biblemap.put("b13_13_11", new arasuperobject(13,13,11,"فاغتاظ داود لان الرب اقتحم عزّا اقتحاما وسمّى ذلك الموضع فارص عزّا الى هذا اليوم"));
arasuperobject.biblemap.put("b13_13_12", new arasuperobject(13,13,12,"وخاف داود الله في ذلك اليوم قائلا كيف آتي بتابوت الله اليّ"));
arasuperobject.biblemap.put("b13_13_13", new arasuperobject(13,13,13,"ولم ينقل داود التابوت اليه الى مدينة داود بل مال به الى بيت عوبيد ادوم الجتّي"));
arasuperobject.biblemap.put("b13_13_14", new arasuperobject(13,13,14,"وبقي تابوت الله عند بيت عوبيد ادوم في بيته ثلاثة اشهر وبارك الرب بيت عوبيد ادوم وكل ما له"));
arasuperobject.biblemap.put("b13_14_1", new arasuperobject(13,14,1,"وارسل حيرام ملك صور رسلا الى داود وخشب ارز وبنائين ونجارين ليبنوا له بيتا"));
arasuperobject.biblemap.put("b13_14_2", new arasuperobject(13,14,2,"وعلم داود ان الرب قد اثبته ملكا على اسرائيل لان مملكته ارتفعت متصاعدة من اجل شعبه اسرائيل"));
arasuperobject.biblemap.put("b13_14_3", new arasuperobject(13,14,3,"واخذ داود نساء ايضا في اورشليم وولد ايضا داود بنين وبنات"));
arasuperobject.biblemap.put("b13_14_4", new arasuperobject(13,14,4,"وهذه اسماء الاولاد الذين كانوا له في اورشليم. شمّوع وشوباب وناثان وسليمان"));
arasuperobject.biblemap.put("b13_14_5", new arasuperobject(13,14,5,"ويبحار واليشوع والفالط"));
arasuperobject.biblemap.put("b13_14_6", new arasuperobject(13,14,6,"ونوجه ونافج ويافيع"));
arasuperobject.biblemap.put("b13_14_7", new arasuperobject(13,14,7,"واليشمع وبعلياداع واليفلط"));
arasuperobject.biblemap.put("b13_14_8", new arasuperobject(13,14,8,"وسمع الفلسطينيون ان داود قد مسح ملكا على كل اسرائيل فصعد كل الفلسطينيين ليفتشوا على داود. ولما سمع داود خرج لاستقبالهم"));
arasuperobject.biblemap.put("b13_14_9", new arasuperobject(13,14,9,"فجاء الفلسطينيون وانتشروا في وادي الرفائيين"));
arasuperobject.biblemap.put("b13_14_10", new arasuperobject(13,14,10,"فسأل داود من الله قائلا أأصعد على الفلسطينيين فتدفعهم ليدي. فقال له الرب اصعد فادفعهم ليدك"));
arasuperobject.biblemap.put("b13_14_11", new arasuperobject(13,14,11,"فصعدوا الى بعل فراصيم وضربهم داود هناك. وقال داود قد اقتحم الله اعدائي بيدي كاقتحام المياه. لذلك دعوا اسم ذلك الموضع بعل فراصيم"));
arasuperobject.biblemap.put("b13_14_12", new arasuperobject(13,14,12,"وتركوا هناك آلهتهم فامر داود فأحرقت بالنار"));
arasuperobject.biblemap.put("b13_14_13", new arasuperobject(13,14,13,"ثم عاد الفلسطينيون ايضا وانتشروا في الوادي"));
arasuperobject.biblemap.put("b13_14_14", new arasuperobject(13,14,14,"فسأل ايضا داود من الله فقال له الله لا تصعد وراءهم تحوّل عنهم وهلم عليهم مقابل اشجار البكا"));
arasuperobject.biblemap.put("b13_14_15", new arasuperobject(13,14,15,"وعندما تسمع صوت خطوات في رؤوس اشجار البكا فاخرج حينئذ للحرب لان الله يخرج امامك لضرب محلّة الفلسطينيين"));
arasuperobject.biblemap.put("b13_14_16", new arasuperobject(13,14,16,"ففعل داود كما امره الله وضربوا محلّة الفلسطينيين من جبعون الى جازر"));
arasuperobject.biblemap.put("b13_14_17", new arasuperobject(13,14,17,"وخرج اسم داود الى جميع الاراضي وجعل الرب هيبته على جميع الامم"));
arasuperobject.biblemap.put("b13_15_1", new arasuperobject(13,15,1,"وعمل داود لنفسه بيوتا في مدينة داود واعدّ مكانا لتابوت الله ونصب له خيمة"));
arasuperobject.biblemap.put("b13_15_2", new arasuperobject(13,15,2,"حينئذ قال داود ليس لاحد ان يحمل تابوت الله الا للاويين لان الرب انما اختارهم لحمل تابوت الله ولخدمته الى الابد"));
arasuperobject.biblemap.put("b13_15_3", new arasuperobject(13,15,3,"وجمع داود كل اسرائيل الى اورشليم لاجل اصعاد تابوت الرب الى مكانه الذي اعدّه له"));
arasuperobject.biblemap.put("b13_15_4", new arasuperobject(13,15,4,"فجمع داود بني هرون واللاويين"));
arasuperobject.biblemap.put("b13_15_5", new arasuperobject(13,15,5,"من بني قهات اوريئيل الرئيس واخوته مئة وعشرين"));
arasuperobject.biblemap.put("b13_15_6", new arasuperobject(13,15,6,"من بني مراري عسايا الرئيس واخوته مئتين وعشرين"));
arasuperobject.biblemap.put("b13_15_7", new arasuperobject(13,15,7,"من بني جرشوم يوئيل الرئيس واخوته مئة وثلاثين"));
arasuperobject.biblemap.put("b13_15_8", new arasuperobject(13,15,8,"من بني اليصافان شمعيا الرئيس واخوته مئتين"));
arasuperobject.biblemap.put("b13_15_9", new arasuperobject(13,15,9,"من بني حبرون ايليئيل الرئيس واخوته ثمانين"));
arasuperobject.biblemap.put("b13_15_10", new arasuperobject(13,15,10,"من بني عزيئيل عميناداب الرئيس واخوته مئة واثني عشر"));
arasuperobject.biblemap.put("b13_15_11", new arasuperobject(13,15,11,"ودعا داود صادوق وابياثار الكاهنين واللاويين اوريئيل وعسايا ويوئيل وشمعيا وايليئيل وعميناداب"));
arasuperobject.biblemap.put("b13_15_12", new arasuperobject(13,15,12,"وقال لهم انتم رؤوس آباء اللاويين فتقدسوا انتم واخوتكم واصعدوا تابوت الرب اله اسرائيل الى حيث اعددت له"));
arasuperobject.biblemap.put("b13_15_13", new arasuperobject(13,15,13,"لانه اذ لم تكونوا في المرة الاولى اقتحمنا الرب الهنا لاننا لم نسأله حسب المرسوم"));
arasuperobject.biblemap.put("b13_15_14", new arasuperobject(13,15,14,"فتقدس الكهنة واللاويون ليصعدوا تابوت الرب اله اسرائيل"));
arasuperobject.biblemap.put("b13_15_15", new arasuperobject(13,15,15,"وحمل بنو اللاويين تابوت الله كما أمر موسى حسب كلام الرب بالعصي على اكتافهم"));
arasuperobject.biblemap.put("b13_15_16", new arasuperobject(13,15,16,"وامر داود رؤساء اللاويين ان يوقفوا اخوتهم المغنين بآلات غناء بعيدان ورباب وصنوج مسمّعين برفع الصوت بفرح"));
arasuperobject.biblemap.put("b13_15_17", new arasuperobject(13,15,17,"فاوقف اللاويون هيمان بن يوئيل ومن اخوته آساف بن برخيا ومن بني مراري اخوتهم ايثان بن قوشيا"));
arasuperobject.biblemap.put("b13_15_18", new arasuperobject(13,15,18,"ومعهم اخوتهم الثواني زكريا وبين ويعزيئيل وشميراموث ويحيئيل وعنّي واليآب وبنايا ومعسيا ومتثايا واليفليا ومقنيا وعوبيد ادوم ويعيئيل البوابين"));
arasuperobject.biblemap.put("b13_15_19", new arasuperobject(13,15,19,"والمغنون هيمان وآساف وايثان بصنوج نحاس للتسميع"));
arasuperobject.biblemap.put("b13_15_20", new arasuperobject(13,15,20,"وزكريا وعزيئيل وشميراموث ويحيئيل وعنّي واليآب ومعسيا وبنايا بالرباب على الجواب"));
arasuperobject.biblemap.put("b13_15_21", new arasuperobject(13,15,21,"ومتّثيا واليفليا ومقنيا وعوبيد ادوم ويعيئيل وعززيا بالعيدان على القرار للامامة"));
arasuperobject.biblemap.put("b13_15_22", new arasuperobject(13,15,22,"وكننيا رئيس اللاويين على الحمل مرشدا في الحمل لانه كان خبيرا"));
arasuperobject.biblemap.put("b13_15_23", new arasuperobject(13,15,23,"وبرخيا والقانة بوابان للتابوت"));
arasuperobject.biblemap.put("b13_15_24", new arasuperobject(13,15,24,"وشبنيا ويوشافاط ونثنئيل وعماساي وزكريا وبنايا واليعزر الكهنة ينفخون بالابواق امام تابوت الله وعوبيد ادوم ويحيّى بوابان للتابوت"));
arasuperobject.biblemap.put("b13_15_25", new arasuperobject(13,15,25,"وكان داود وشيوخ اسرائيل ورؤساء الالوف هم الذين ذهبوا لاصعاد تابوت عهد الرب من بيت عوبيد ادوم بفرح"));
arasuperobject.biblemap.put("b13_15_26", new arasuperobject(13,15,26,"ولما اعان الله اللاويين حاملي تابوت عهد الرب ذبحوا سبعة عجول وسبعة كباش"));
arasuperobject.biblemap.put("b13_15_27", new arasuperobject(13,15,27,"وكان داود لابسا جبّة من كتّان وجميع اللاويين حاملين التابوت والمغنون وكننيا رئيس الحمل مع المغنين. وكان على داود افود من كتان"));
arasuperobject.biblemap.put("b13_15_28", new arasuperobject(13,15,28,"فكان جميع اسرائيل يصعدون تابوت عهد الرب بهتاف وبصوت الاصوار والابواق والصنوج يصّوتون بالرباب والعيدان"));
arasuperobject.biblemap.put("b13_15_29", new arasuperobject(13,15,29,"ولما دخل تابوت عهد الرب مدينة داود اشرفت ميكال بنت شاول من الكوّة فرأت الملك داود يرقص ويلعب فاحتقرته في قلبها"));
arasuperobject.biblemap.put("b13_16_1", new arasuperobject(13,16,1,"وادخلوا تابوت الله واثبتوه في وسط الخيمة التي نصبها له داود وقربوا محرقات وذبائح سلامة امام الله"));
arasuperobject.biblemap.put("b13_16_2", new arasuperobject(13,16,2,"ولما انتهى داود من اصعاد المحرقات وذبائح السلامة بارك الشعب باسم الرب"));
arasuperobject.biblemap.put("b13_16_3", new arasuperobject(13,16,3,"وقسم على كل آل اسرائيل من الرجال والنساء على كل انسان رغيف خبز وكاس خمر وقرص زبيب"));
arasuperobject.biblemap.put("b13_16_4", new arasuperobject(13,16,4,"وجعل امام تابوت الرب من اللاويين خداما ولاجل التذكير والشكر وتسبيح الرب اله اسرائيل"));
arasuperobject.biblemap.put("b13_16_5", new arasuperobject(13,16,5,"آساف الراس وزكريا ثانيه ويعيئيل وشميراموث ويحيئيل ومتثايا واليآب وبنايا وعوبيد ادوم ويعيئيل بآلات رباب وعيدان. وكان آساف يصوّت بالصنوج"));
arasuperobject.biblemap.put("b13_16_6", new arasuperobject(13,16,6,"وبنايا ويحزيئيل الكاهنان بالابواق دائما امام تابوت عهد الله"));
arasuperobject.biblemap.put("b13_16_7", new arasuperobject(13,16,7,"حينئذ في ذلك اليوم اولا جعل داود يحمد الرب بيد آساف واخوته"));
arasuperobject.biblemap.put("b13_16_8", new arasuperobject(13,16,8,"احمدوا الرب. ادعوا باسمه. اخبروا في الشعوب باعماله"));
arasuperobject.biblemap.put("b13_16_9", new arasuperobject(13,16,9,"غنوا له. ترنموا له. تحادثوا بكل عجائبه"));
arasuperobject.biblemap.put("b13_16_10", new arasuperobject(13,16,10,"افتخروا باسم قدسه. تفرح قلوب الذين يلتمسون الرب"));
arasuperobject.biblemap.put("b13_16_11", new arasuperobject(13,16,11,"اطلبوا الرب وعزّه. التمسوا وجهه دائما"));
arasuperobject.biblemap.put("b13_16_12", new arasuperobject(13,16,12,"اذكروا عجائبه التي صنع. آياته واحكام فمه"));
arasuperobject.biblemap.put("b13_16_13", new arasuperobject(13,16,13,"يا ذرية اسرائيل عبده وبني يعقوب مختاريه"));
arasuperobject.biblemap.put("b13_16_14", new arasuperobject(13,16,14,"هو الرب الهنا. في كل الارض احكامه"));
arasuperobject.biblemap.put("b13_16_15", new arasuperobject(13,16,15,"اذكروا الى الابد عهده. الكلمة التي اوصى بها الى الف جيل"));
arasuperobject.biblemap.put("b13_16_16", new arasuperobject(13,16,16,"الذي قطعه مع ابراهيم. وقسمه لاسحق"));
arasuperobject.biblemap.put("b13_16_17", new arasuperobject(13,16,17,"وقد اقامه ليعقوب فريضة ولاسرائيل عهدا ابديا"));
arasuperobject.biblemap.put("b13_16_18", new arasuperobject(13,16,18,"قائلا لك اعطي ارض كنعان حبل ميراثكم"));
arasuperobject.biblemap.put("b13_16_19", new arasuperobject(13,16,19,"حين كنتم عددا قليلا قليلين جدا وغرباء فيها"));
arasuperobject.biblemap.put("b13_16_20", new arasuperobject(13,16,20,"وذهبوا من امة الى امة ومن مملكة الى شعب آخر"));
arasuperobject.biblemap.put("b13_16_21", new arasuperobject(13,16,21,"لم يدع احدا يظلمهم بل وبّخ من اجلهم ملوكا"));
arasuperobject.biblemap.put("b13_16_22", new arasuperobject(13,16,22,"لا تمسّوا مسحائي ولا تؤذوا انبيائي"));
arasuperobject.biblemap.put("b13_16_23", new arasuperobject(13,16,23,"غنّوا للرب يا كل الارض. بشّروا من يوم الى يوم بخلاصه"));
arasuperobject.biblemap.put("b13_16_24", new arasuperobject(13,16,24,"حدّثوا في الامم بمجده وفي كل الشعوب بعجائبه"));
arasuperobject.biblemap.put("b13_16_25", new arasuperobject(13,16,25,"لان الرب عظيم ومفتخر جدا. وهو مرهوب فوق جميع الآلهة"));
arasuperobject.biblemap.put("b13_16_26", new arasuperobject(13,16,26,"لان كل آلهة الامم اصنام واما الرب فقد صنع السموات"));
arasuperobject.biblemap.put("b13_16_27", new arasuperobject(13,16,27,"الجلال والبهاء امامه. العزّة والبهجة في مكانه"));
arasuperobject.biblemap.put("b13_16_28", new arasuperobject(13,16,28,"هبوا الرب يا عشائر الشعوب هبوا الرب مجدا وعزّة"));
arasuperobject.biblemap.put("b13_16_29", new arasuperobject(13,16,29,"هبوا الرب مجد اسمه. احملوا هدايا وتعالوا الى امامه. اسجدوا للرب في زينة مقدسة"));
arasuperobject.biblemap.put("b13_16_30", new arasuperobject(13,16,30,"ارتعدوا امامه يا جميع الارض. تثبّتت المسكونة ايضا لا تتزعزع"));
arasuperobject.biblemap.put("b13_16_31", new arasuperobject(13,16,31,"لتفرح السموات وتبتهج الارض ويقولوا في الامم الرب قد ملك"));
arasuperobject.biblemap.put("b13_16_32", new arasuperobject(13,16,32,"ليعج البحر وملؤه ولتبتهج البرية وكل ما فيها"));
arasuperobject.biblemap.put("b13_16_33", new arasuperobject(13,16,33,"حينئذ تترنم اشجار الوعر امام الرب لانه جاء ليدين الارض"));
arasuperobject.biblemap.put("b13_16_34", new arasuperobject(13,16,34,"احمدوا الرب لانه صالح لان الى الابد رحمته"));
arasuperobject.biblemap.put("b13_16_35", new arasuperobject(13,16,35,"وقولوا خلّصنا يا اله خلاصنا واجمعنا وانقذنا من الامم لنحمد اسم قدسك ونتفاخر بتسبيحك"));
arasuperobject.biblemap.put("b13_16_36", new arasuperobject(13,16,36,"مبارك الرب اله اسرائيل من الازل والى الابد. فقال كل الشعب آمين وسبحوا الرب"));
arasuperobject.biblemap.put("b13_16_37", new arasuperobject(13,16,37,"وترك هناك امام تابوت عهد الرب آساف واخوته ليخدموا امام التابوت دائما خدمة كل يوم بيومها"));
arasuperobject.biblemap.put("b13_16_38", new arasuperobject(13,16,38,"وعوبيد ادوم واخوتهم ثمانية وستين وعوبيد ادوم بن يديثون وحوسة بوابين"));
arasuperobject.biblemap.put("b13_16_39", new arasuperobject(13,16,39,"وصادوق الكاهن واخوته الكهنة امام مسكن الرب في المرتفعة التي في جبعون"));
arasuperobject.biblemap.put("b13_16_40", new arasuperobject(13,16,40,"ليصعدوا محرقات للرب على مذبح المحرقة دائما صباحا ومساء وحسب كل ما هو مكتوب في شريعة الرب التي أمر بها اسرائيل"));
arasuperobject.biblemap.put("b13_16_41", new arasuperobject(13,16,41,"ومعهم هيمان ويدوثون وباقي المنتخبين الذين ذكرت اسماؤهم ليحمدوا الرب لان الى الابد رحمته"));
arasuperobject.biblemap.put("b13_16_42", new arasuperobject(13,16,42,"ومعهم هيمان ويدوثون بابواق وصنوج للمصوّتين وآلات غناء لله وبنو يدوثون بوابون"));
arasuperobject.biblemap.put("b13_16_43", new arasuperobject(13,16,43,"ثم انطلق كل الشعب كل واحد الى بيته ورجع داود ليبارك بيته"));
arasuperobject.biblemap.put("b13_17_1", new arasuperobject(13,17,1,"وكان لما سكن داود في بيته قال داود لناثان النبي. هانذا ساكن في بيت من ارز وتابوت عهد الرب تحت شقق"));
arasuperobject.biblemap.put("b13_17_2", new arasuperobject(13,17,2,"فقال ناثان لداود افعل كل ما في قلبك لان الله معك"));
arasuperobject.biblemap.put("b13_17_3", new arasuperobject(13,17,3,"وفي تلك الليلة كان كلام الله الى ناثان قائلا"));
arasuperobject.biblemap.put("b13_17_4", new arasuperobject(13,17,4,"اذهب وقل لداود عبدي هكذا قال الرب. انت لا تبني لي بيتا للسكنى"));
arasuperobject.biblemap.put("b13_17_5", new arasuperobject(13,17,5,"لاني لم اسكن في بيت منذ يوم اصعدت اسرائيل الى هذا اليوم بل سرت من خيمة الى خيمة ومن مسكن الى مسكن"));
arasuperobject.biblemap.put("b13_17_6", new arasuperobject(13,17,6,"في كل ما سرت مع جميع اسرائيل هل تكلمت بكلمة مع احد قضاة اسرائيل الذين امرتهم ان يرعوا شعبي اسرائيل قائلا لماذا لم تبنوا لي بيتا من ارز"));
arasuperobject.biblemap.put("b13_17_7", new arasuperobject(13,17,7,"والآن فهكذا تقول لعبدي داود. هكذا قال رب الجنود انا اخذتك من المربض من وراء الغنم لتكون رئيسا على شعبي اسرائيل"));
arasuperobject.biblemap.put("b13_17_8", new arasuperobject(13,17,8,"وكنت معك حيثما توجهت وقرضت جميع اعدائك من امامك وعملت لك اسما كاسم العظماء الذين في الارض"));
arasuperobject.biblemap.put("b13_17_9", new arasuperobject(13,17,9,"وعيّنت مكانا لشعبي اسرائيل وغرسته فسكن في مكانه ولا يضطرب بعد ولا يعود بنو الاثم يبلونه كما في الاول"));
arasuperobject.biblemap.put("b13_17_10", new arasuperobject(13,17,10,"ومنذ الايام التي فيها اقمت قضاة على شعبي اسرائيل. واذللت جميع اعدائك. واخبرك ان الرب يبني لك بيتا"));
arasuperobject.biblemap.put("b13_17_11", new arasuperobject(13,17,11,"ويكون متى كملت ايامك لتذهب مع آبائك اني اقيم بعدك نسلك الذي يكون من بنيك واثبت مملكته"));
arasuperobject.biblemap.put("b13_17_12", new arasuperobject(13,17,12,"هو يبني لي بيتا وانا اثبت كرسيه الى الابد"));
arasuperobject.biblemap.put("b13_17_13", new arasuperobject(13,17,13,"انا اكون له ابا وهو يكون لي ابنا ولا انزع رحمتي عنه كما نزعتها عن الذي كان قبلك"));
arasuperobject.biblemap.put("b13_17_14", new arasuperobject(13,17,14,"واقيمه في بيتي وملكوتي الى الابد ويكون كرسيه ثابتا الى الابد"));
arasuperobject.biblemap.put("b13_17_15", new arasuperobject(13,17,15,"فحسب جميع هذا الكلام وحسب كل هذه الرؤيا كذلك كلم ناثان داود"));
arasuperobject.biblemap.put("b13_17_16", new arasuperobject(13,17,16,"فدخل الملك داود وجلس امام الرب وقال. من انا ايها الرب الاله وماذا بيتي حتى اوصلتني الى هنا"));
arasuperobject.biblemap.put("b13_17_17", new arasuperobject(13,17,17,"وقلّ هذا في عينيك يا الله فتكلمت عن بيت عبدك الى زمان طويل ونظرت اليّ من العلاء كعادة الانسان ايها الرب الاله"));
arasuperobject.biblemap.put("b13_17_18", new arasuperobject(13,17,18,"فماذا يزيد داود بعد لك لاجل اكرام عبدك وانت قد عرفت عبدك"));
arasuperobject.biblemap.put("b13_17_19", new arasuperobject(13,17,19,"يا رب من اجل عبدك وحسب قلبك قد فعلت كل هذه العظائم لتظهر جميع العظائم"));
arasuperobject.biblemap.put("b13_17_20", new arasuperobject(13,17,20,"يا رب ليس مثلك ولا اله غيرك حسب كل ما سمعناه بآذاننا"));
arasuperobject.biblemap.put("b13_17_21", new arasuperobject(13,17,21,"وايّة امة على الارض مثل شعبك اسرائيل الذي سار الله ليفتديه لنفسه شعبا لتجعل لك اسم عظائم ومخاوف بطردك امما من امام شعبك الذي افتديته من مصر"));
arasuperobject.biblemap.put("b13_17_22", new arasuperobject(13,17,22,"وقد جعلت شعبك اسرائيل لنفسك شعبا الى الابد وانت ايها الرب صرت لهم الها"));
arasuperobject.biblemap.put("b13_17_23", new arasuperobject(13,17,23,"والآن ايها الرب ليثبت الى الابد الكلام الذي تكلمت به عن عبدك وعن بيته وافعل كما نطقت"));
arasuperobject.biblemap.put("b13_17_24", new arasuperobject(13,17,24,"وليثبت ويتعظم اسمك الى الابد فيقال رب الجنود اله اسرائيل هو الله لاسرائيل وليثبت بيت داود عبدك امامك"));
arasuperobject.biblemap.put("b13_17_25", new arasuperobject(13,17,25,"لانك يا الهي قد اعلنت لعبدك انك تبني له بيتا لذلك وجد عبدك ان يصلّي امامك"));
arasuperobject.biblemap.put("b13_17_26", new arasuperobject(13,17,26,"والآن ايها الرب انت هو الله وقد وعدت عبدك بهذا الخير"));
arasuperobject.biblemap.put("b13_17_27", new arasuperobject(13,17,27,"والآن قد ارتضيت بان تبارك بيت عبدك ليكون الى الابد امامك لانك انت يا رب قد باركت وهو مبارك الى الابد"));
arasuperobject.biblemap.put("b13_18_1", new arasuperobject(13,18,1,"وبعد ذلك ضرب داود الفلسطينيين وذلّلهم واخذ جتّ وقراها من يد الفلسطينيين"));
arasuperobject.biblemap.put("b13_18_2", new arasuperobject(13,18,2,"وضرب موآب فصار الموآبيون عبيدا لداود يقدمون هدايا"));
arasuperobject.biblemap.put("b13_18_3", new arasuperobject(13,18,3,"وضرب داود هدرعزر ملك صوبة في حماة حين ذهب ليقيم سلطته عند نهر الفرات"));
arasuperobject.biblemap.put("b13_18_4", new arasuperobject(13,18,4,"واخذ داود منه الف مركبة وسبعة آلاف فارس وعشرين الف راجل وعرقب داود كل خيل المركبات وابقى منها مئة مركبة"));
arasuperobject.biblemap.put("b13_18_5", new arasuperobject(13,18,5,"فجاء ارام دمشق لنجدة هدرعزر ملك صوبة فضرب داود من ارام اثنين وعشرين الف رجل"));
arasuperobject.biblemap.put("b13_18_6", new arasuperobject(13,18,6,"وجعل داود محافظين في ارام دمشق وصار الاراميون لداود عبيدا يقدمون هدايا. وكان الرب يخلص داود حيثما توجّه"));
arasuperobject.biblemap.put("b13_18_7", new arasuperobject(13,18,7,"واخذ داود اتراس الذهب التي كانت على عبيد هدرعزر وأتى بها الى اورشليم"));
arasuperobject.biblemap.put("b13_18_8", new arasuperobject(13,18,8,"ومن طبحة وخون مدينتي هدرعزر اخذ داود نحاسا كثيرا جدا صنع منه سليمان بحر النحاس والاعمدة وآنية النحاس"));
arasuperobject.biblemap.put("b13_18_9", new arasuperobject(13,18,9,"وسمع توعو ملك حماة ان داود قد ضرب كل جيش هدرعزر ملك صوبة"));
arasuperobject.biblemap.put("b13_18_10", new arasuperobject(13,18,10,"فارسل هدورام ابنه الى الملك داود ليسأل عن سلامته ويباركه لانه حارب هدرعزر وضربه. لان هدرعزر كانت له حروب مع توعو. وبيده جميع آنية الذهب والفضة والنحاس"));
arasuperobject.biblemap.put("b13_18_11", new arasuperobject(13,18,11,"هذه ايضا قدسها الملك داود للرب مع الفضة والذهب الذي اخذه من كل الامم من ادوم ومن موآب ومن بني عمون ومن الفلسطينيين ومن عماليق"));
arasuperobject.biblemap.put("b13_18_12", new arasuperobject(13,18,12,"وابشاي ابن صروية ضرب من ادوم في وادي الملح ثمانية عشر الفا"));
arasuperobject.biblemap.put("b13_18_13", new arasuperobject(13,18,13,"وجعل في ادوم محافظين فصار جميع الادوميين عبيدا لداود وكان الرب يخلص داود حيثما توجّه"));
arasuperobject.biblemap.put("b13_18_14", new arasuperobject(13,18,14,"وملك داود على جميع اسرائيل وكان يجري قضاء وعدلا لكل شعبه"));
arasuperobject.biblemap.put("b13_18_15", new arasuperobject(13,18,15,"وكان يوآب ابن صروية على الجيش ويهوشافاط بن اخيلود مسجّلا"));
arasuperobject.biblemap.put("b13_18_16", new arasuperobject(13,18,16,"وصادوق بن اخيطوب وابيمالك بن ابياثار كاهنين وشوشا كاتبا"));
arasuperobject.biblemap.put("b13_18_17", new arasuperobject(13,18,17,"وبنايا بن يهوياداع على الجلادين والسعاة وبنو داود الاولين بين يدي الملك"));
arasuperobject.biblemap.put("b13_19_1", new arasuperobject(13,19,1,"وكان بعد ذلك ان ناحاش ملك بني عمون مات فملك ابنه عوضا عنه"));
arasuperobject.biblemap.put("b13_19_2", new arasuperobject(13,19,2,"فقال داود اصنع معروفا مع حانون بن ناحاش لان اباه صنع معي معروفا. فارسل داود رسلا ليعزيه بابيه. فجاء عبيد داود الى ارض بني عمون الى حانون ليعزّوه"));
arasuperobject.biblemap.put("b13_19_3", new arasuperobject(13,19,3,"فقال رؤساء بني عمون لحانون هل يكرم داود اباك في عينيك حتى ارسل اليك معزّين. أليس انما لاجل الفحص والقلب وتجسس الارض جاء عبيده اليك"));
arasuperobject.biblemap.put("b13_19_4", new arasuperobject(13,19,4,"فاخذ حانون عبيد داود وحلق لحاهم وقصّ ثيابهم من الوسط عند السوءة ثم اطلقهم"));
arasuperobject.biblemap.put("b13_19_5", new arasuperobject(13,19,5,"فذهب اناس واخبروا داود عن الرجال. فارسل للقائهم لان الرجال كانوا خجلين جدا. وقال الملك اقيموا في اريحا حتى تنبت لحاكم ثم ارجعوا"));
arasuperobject.biblemap.put("b13_19_6", new arasuperobject(13,19,6,"ولما رأى بنو عمون انهم قد انتنوا عند داود ارسل حانون وبنو عمون الف وزنة من الفضة لكي يستأجروا لانفسهم من ارام النهرين ومن ارام معكة ومن صوبة مركبات وفرسانا"));
arasuperobject.biblemap.put("b13_19_7", new arasuperobject(13,19,7,"فاستأجروا لانفسهم اثنين وثلاثين الف مركبة وملك معكة وشعبه فجاءوا ونزلوا مقابل ميدبا. واجتمع بنو عمون من مدنهم وأتوا للحرب"));
arasuperobject.biblemap.put("b13_19_8", new arasuperobject(13,19,8,"ولما سمع داود ارسل يوآب وكل جيش الجبابرة"));
arasuperobject.biblemap.put("b13_19_9", new arasuperobject(13,19,9,"فخرج بنو عمون واصطفوا للحرب عند باب المدينة والملوك الذين جاءوا كانوا وحدهم في الحقل"));
arasuperobject.biblemap.put("b13_19_10", new arasuperobject(13,19,10,"ولما رأى يوآب ان مقدمة الحرب كانت نحوه من قدام ومن وراء اختار من جميع منتخبي اسرائيل وصفّهم للقاء ارام"));
arasuperobject.biblemap.put("b13_19_11", new arasuperobject(13,19,11,"وسلّم بقية الشعب ليد ابشاي اخيه فاصطفوا للقاء بني عمون"));
arasuperobject.biblemap.put("b13_19_12", new arasuperobject(13,19,12,"وقال ان قوي ارام عليّ تكون لي نجدة وان قوي بنو عمون عليك انجدتك"));
arasuperobject.biblemap.put("b13_19_13", new arasuperobject(13,19,13,"تجلّد ولنتشدد لاجل شعبنا ولاجل مدن الهنا وما يحسن في عيني الرب يفعل"));
arasuperobject.biblemap.put("b13_19_14", new arasuperobject(13,19,14,"وتقدم يوآب والشعب الذين معه نحو ارام للمحاربة فهربوا من امامه"));
arasuperobject.biblemap.put("b13_19_15", new arasuperobject(13,19,15,"ولما رأى بنو عمون انه قد هرب ارام هربوا هم ايضا من امام ابشاي اخيه ودخلوا الى المدينة. وجاء يوآب الى اورشليم"));
arasuperobject.biblemap.put("b13_19_16", new arasuperobject(13,19,16,"ولما رأى ارام انهم قد انكسروا امام اسرائيل ارسلوا رسلا وابرزوا ارام الذين في عبر النهر وامامهم شوبك رئيس جيش هدرعزر"));
arasuperobject.biblemap.put("b13_19_17", new arasuperobject(13,19,17,"ولما أخبر داود جمع كل اسرائيل وعبر الاردن وجاء اليهم واصطف ضدّهم. اصطف داود للقاء ارام في الحرب فحاربوه"));
arasuperobject.biblemap.put("b13_19_18", new arasuperobject(13,19,18,"وهرب ارام من امام اسرائيل وقتل داود من ارام سبعة آلاف مركبة واربعين الف راجل وقتل شوبك رئيس الجيش"));
arasuperobject.biblemap.put("b13_19_19", new arasuperobject(13,19,19,"ولما رأى عبيد هدرعزر انهم قد انكسروا امام اسرائيل صالحوا داود وخدموه. ولم يشأ ارام ان ينجدوا بني عمون بعد"));
arasuperobject.biblemap.put("b13_20_1", new arasuperobject(13,20,1,"وكان عند تمام السنة في وقت خروج الملوك اقتاد يوآب قوة الجيش واخرب ارض بني عمون واتى وحاصر ربّة. وكان داود مقيما في اورشليم. فضرب يوآب ربّة وهدمها"));
arasuperobject.biblemap.put("b13_20_2", new arasuperobject(13,20,2,"واخذ داود تاج ملكهم عن راسه فوجد وزنه وزنة من الذهب وفيه حجر كريم فكان على راس داود. واخرج غنيمة المدينة وكانت كثيرة جدا"));
arasuperobject.biblemap.put("b13_20_3", new arasuperobject(13,20,3,"واخرج الشعب الذين بها ونشرهم بمناشير ونوارج حديد وفؤوس. وهكذا صنع داود لكل مدن بني عمون ثم رجع داود وكل الشعب الى اورشليم"));
arasuperobject.biblemap.put("b13_20_4", new arasuperobject(13,20,4,"ثم بعد ذلك قامت حرب في جازر مع الفلسطينيين. حينئذ سبكاي الحوشي قتل سفّاي من اولاد رافا فذلّوا"));
arasuperobject.biblemap.put("b13_20_5", new arasuperobject(13,20,5,"وكانت ايضا حرب مع الفلسطينيين فقتل الحانان بن ياعور لحمي اخا جليات الجتّي. وكانت قناة رمحه كنول النسّاجين"));
arasuperobject.biblemap.put("b13_20_6", new arasuperobject(13,20,6,"ثم كانت ايضا حرب في جتّ وكان رجل طويل القامة أعنش اصابعه اربع وعشرون وهو ايضا ولد لرافا"));
arasuperobject.biblemap.put("b13_20_7", new arasuperobject(13,20,7,"ولما عيّر اسرائيل ضربه يهوناثان بن شمعا اخي داود"));
arasuperobject.biblemap.put("b13_20_8", new arasuperobject(13,20,8,"هؤلاء ولدوا لرافا في جت وسقطوا بيد داود وبيد عبيده"));
arasuperobject.biblemap.put("b13_21_1", new arasuperobject(13,21,1,"ووقف الشيطان ضد اسرائيل واغوى داود ليحصي اسرائيل"));
arasuperobject.biblemap.put("b13_21_2", new arasuperobject(13,21,2,"فقال داود ليوآب ولرؤساء الشعب اذهبوا عدّوا اسرائيل من بئر سبع الى دان وأتوا اليّ فاعلم عددهم"));
arasuperobject.biblemap.put("b13_21_3", new arasuperobject(13,21,3,"فقال يوآب ليزد الرب على شعبه امثالهم مئة ضعف. أليسوا جميعا يا سيدي الملك عبيدا لسيدي. لماذا يطلب هذا سيدي. لماذا يكون سبب اثم لاسرائيل"));
arasuperobject.biblemap.put("b13_21_4", new arasuperobject(13,21,4,"فاشتدّ كلام الملك على يوآب. فخرج يوآب وطاف في كل اسرائيل ثم جاء الى اورشليم"));
arasuperobject.biblemap.put("b13_21_5", new arasuperobject(13,21,5,"فدفع يوآب جملة عدد الشعب الى داود فكان كل اسرائيل الف الف ومئة الف رجل مستلّي السيف ويهوذا اربع مئة وسبعين الف رجل مستلّي السيف"));
arasuperobject.biblemap.put("b13_21_6", new arasuperobject(13,21,6,"واما لاوي وبنيامين فلم يعدّهم معهم لان كلام الملك كان مكروها لدى يوآب"));
arasuperobject.biblemap.put("b13_21_7", new arasuperobject(13,21,7,"وقبح في عيني الله هذا الامر فضرب اسرائيل"));
arasuperobject.biblemap.put("b13_21_8", new arasuperobject(13,21,8,"فقال داود لله لقد اخطأت جدا حيث عملت هذا الامر والآن أزل اثم عبدك لاني سفهت جدا"));
arasuperobject.biblemap.put("b13_21_9", new arasuperobject(13,21,9,"فكلم الرب جاد رائي داود وقال"));
arasuperobject.biblemap.put("b13_21_10", new arasuperobject(13,21,10,"اذهب وكلم داود قائلا هكذا قال الرب ثلاثة انا عارض عليك فاختر لنفسك واحدا منها فافعله بك"));
arasuperobject.biblemap.put("b13_21_11", new arasuperobject(13,21,11,"فجاء جاد الى داود وقال له هكذا قال الرب اقبل لنفسك"));
arasuperobject.biblemap.put("b13_21_12", new arasuperobject(13,21,12,"اما ثلاث سنين جوع او ثلاثة اشهر هلاك امام مضايقيك وسيف اعدائك يدركك او ثلاثة ايام يكون فيها سيف الرب ووبأ في الارض وملاك الرب يعثو في كل تخوم اسرائيل. فانظر الآن ماذا ارد جوابا لمرسلي"));
arasuperobject.biblemap.put("b13_21_13", new arasuperobject(13,21,13,"فقال داود لجاد قد ضاق بي الأمر جدا. دعني اسقط في يد الرب لان مراحمه كثيرة ولا اسقط في يد انسان"));
arasuperobject.biblemap.put("b13_21_14", new arasuperobject(13,21,14,"فجعل الرب وبأ في اسرائيل فسقط من اسرائيل سبعون الف رجل"));
arasuperobject.biblemap.put("b13_21_15", new arasuperobject(13,21,15,"وارسل الله ملاكا على اورشليم لاهلاكها وفيما هو يهلك رأى الرب فندم على الشر وقال للملاك المهلك كفى الآن رد يدك. وكان ملاك الرب واقفا عند بيدر أرنان اليبوسي"));
arasuperobject.biblemap.put("b13_21_16", new arasuperobject(13,21,16,"ورفع داود عينيه فرأى ملاك الرب واقفا بين الارض والسماء وسيفه مسلول بيده وممدود على اورشليم فسقط داود والشيوخ على وجوههم مكتسين بالمسوح"));
arasuperobject.biblemap.put("b13_21_17", new arasuperobject(13,21,17,"وقال داود لله ألست انا هو الذي امر باحصاء الشعب. وانا هو الذي اخطأ وأساء واما هؤلاء الخراف فماذا عملوا. فأيها الرب الهي لتكن يدك عليّ وعلى بيت ابي لا على شعبك لضربهم"));
arasuperobject.biblemap.put("b13_21_18", new arasuperobject(13,21,18,"فكلم ملاك الرب جاد ان يقول لداود ان يصعد داود ليقيم مذبحا للرب في بيدر أرنان اليبوسي"));
arasuperobject.biblemap.put("b13_21_19", new arasuperobject(13,21,19,"فصعد داود حسب كلام جاد الذي تكلم به باسم الرب"));
arasuperobject.biblemap.put("b13_21_20", new arasuperobject(13,21,20,"فالتفت أرنان فرأى الملاك. وبنوه الاربعة معه اختبأوا. وكان ارنان يدرس حنطة"));
arasuperobject.biblemap.put("b13_21_21", new arasuperobject(13,21,21,"وجاء داود الى ارنان وتطلّع ارنان فرأى داود وخرج من البيدر وسجد لداود على وجهه الى الارض"));
arasuperobject.biblemap.put("b13_21_22", new arasuperobject(13,21,22,"فقال داود لأرنان اعطني مكان البيدر فابني فيه مذبحا للرب. بفضة كاملة اعطني اياه فتكفّ الضربة عن الشعب"));
arasuperobject.biblemap.put("b13_21_23", new arasuperobject(13,21,23,"فقال ارنان لداود خذه لنفسك وليفعل سيدي الملك ما يحسن في عينيه. انظر قد اعطيت البقر للمحرقة والنوارج للوقود والحنطة للتقدمة. الجميع اعطيت"));
arasuperobject.biblemap.put("b13_21_24", new arasuperobject(13,21,24,"فقال الملك داود لأرنان لا. بل شراء اشتريه بفضة كاملة لاني لا آخذ مالك للرب فاصعد محرقة مجّانيّة"));
arasuperobject.biblemap.put("b13_21_25", new arasuperobject(13,21,25,"ودفع داود لأرنان عن المكان ذهبا وزنه ست مئة شاقل"));
arasuperobject.biblemap.put("b13_21_26", new arasuperobject(13,21,26,"وبنى داود هناك مذبحا للرب واصعد محرقات وذبائح سلامة ودعا الرب فاجابه بنار من السماء على مذبح المحرقة"));
arasuperobject.biblemap.put("b13_21_27", new arasuperobject(13,21,27,"وأمر الرب الملاك فرد سيفه الى غمده"));
arasuperobject.biblemap.put("b13_21_28", new arasuperobject(13,21,28,"في ذلك الوقت لما رأى داود ان الرب قد اجابه في بيدر ارنان اليبوسي ذبح هناك"));
arasuperobject.biblemap.put("b13_21_29", new arasuperobject(13,21,29,"ومسكن الرب الذي عمله موسى في البرية ومذبح المحرقة كانا في ذلك الوقت في المرتفعة في جبعون"));
arasuperobject.biblemap.put("b13_21_30", new arasuperobject(13,21,30,"ولم يستطع داود ان يذهب الى امامه ليسأل الله لانه خاف من جهة سيف ملاك الرب"));
arasuperobject.biblemap.put("b13_22_1", new arasuperobject(13,22,1,"فقال داود هذا هو بيت الرب الاله وهذا هو مذبح المحرقة لاسرائيل"));
arasuperobject.biblemap.put("b13_22_2", new arasuperobject(13,22,2,"وأمر داود بجمع الاجنبيين الذين في ارض اسرائيل واقام نحّاتين لنحت حجارة مربعة لبناء بيت الله"));
arasuperobject.biblemap.put("b13_22_3", new arasuperobject(13,22,3,"وهيّأ داود حديدا كثيرا للمسامير لمصاريع الابواب وللوصل ونحاسا كثيرا بلا وزن"));
arasuperobject.biblemap.put("b13_22_4", new arasuperobject(13,22,4,"وخشب ارز لم يكن له عدد لان الصيدونيين والصوريين أتوا بخشب ارز كثير الى داود"));
arasuperobject.biblemap.put("b13_22_5", new arasuperobject(13,22,5,"وقال داود ان سليمان ابني صغير وغضّ والبيت الذي يبنى للرب يكون عظيما جدا في الاسم والمجد في جميع الاراضي فانا اهيئ له. فهيّأ داود كثيرا قبل وفاته"));
arasuperobject.biblemap.put("b13_22_6", new arasuperobject(13,22,6,"ودعا سليمان ابنه واوصاه ان يبني بيتا للرب اله اسرائيل"));
arasuperobject.biblemap.put("b13_22_7", new arasuperobject(13,22,7,"وقال داود لسليمان يا ابني قد كان في قلبي ان ابني بيتا لاسم الرب الهي"));
arasuperobject.biblemap.put("b13_22_8", new arasuperobject(13,22,8,"فكان اليّ كلام الرب قائلا قد سفكت دما كثيرا وعملت حروبا عظيمة فلا تبني بيتا لاسمي لانك سفكت دماء كثيرة على الارض امامي"));
arasuperobject.biblemap.put("b13_22_9", new arasuperobject(13,22,9,"هوذا يولد لك ابن يكون صاحب راحة واريحه من جميع اعدائه حواليه لان اسمه يكون سليمان. فاجعل سلاما وسكينة في اسرائيل في ايامه"));
arasuperobject.biblemap.put("b13_22_10", new arasuperobject(13,22,10,"هو يبني بيتا لاسمي وهو يكون لي ابنا وانا له ابا واثبت كرسي ملكه على اسرائيل الى الابد"));
arasuperobject.biblemap.put("b13_22_11", new arasuperobject(13,22,11,"الآن يا ابني ليكن الرب معك فتفلح وتبني بيت الرب الهك كما تكلم عنك"));
arasuperobject.biblemap.put("b13_22_12", new arasuperobject(13,22,12,"انما يعطيك الرب فطنة وفهما ويوصيك باسرائيل لحفظ شريعة الرب الهك"));
arasuperobject.biblemap.put("b13_22_13", new arasuperobject(13,22,13,"حينئذ تفلح اذا تحفظت لعمل الفرائض والاحكام التي أمر بها الرب موسى لاجل اسرائيل. تشدد وتشجع لا تخف ولا ترتعب"));
arasuperobject.biblemap.put("b13_22_14", new arasuperobject(13,22,14,"هانذا في مذلتي هيّأت لبيت الرب ذهبا مئة الف وزنة وفضة الف الف وزنة ونحاسا وحديدا بلا وزن لانه كثير. وقد هيّأت خشبا وحجارة فتزيد عليها"));
arasuperobject.biblemap.put("b13_22_15", new arasuperobject(13,22,15,"وعندك كثيرون من عاملي الشغل نحاتين وبنائين ونجارين وكل حكيم في كل عمل"));
arasuperobject.biblemap.put("b13_22_16", new arasuperobject(13,22,16,"الذهب والفضة والنحاس والحديد ليس لها عدد. قم واعمل وليكن الرب معك"));
arasuperobject.biblemap.put("b13_22_17", new arasuperobject(13,22,17,"وأمر داود جميع رؤساء اسرائيل ان يساعدوا سليمان ابنه"));
arasuperobject.biblemap.put("b13_22_18", new arasuperobject(13,22,18,"اليس الرب الهكم معكم وقد اراحكم من كل ناحية لانه دفع ليدي سكان الارض فخضعت الارض امام الرب وامام شعبه"));
arasuperobject.biblemap.put("b13_22_19", new arasuperobject(13,22,19,"فالآن اجعلوا قلوبكم وانفسكم لطلب الرب الهكم وقوموا وابنوا مقدس الرب الاله ليؤتى بتابوت عهد الرب وبآنية قدس الله الى البيت الذي يبنى لاسم الرب"));
arasuperobject.biblemap.put("b13_23_1", new arasuperobject(13,23,1,"لما شاخ داود وشبع اياما ملّك سليمان ابنه على اسرائيل"));
arasuperobject.biblemap.put("b13_23_2", new arasuperobject(13,23,2,"وجمع كل رؤساء اسرائيل والكهنة واللاويين"));
arasuperobject.biblemap.put("b13_23_3", new arasuperobject(13,23,3,"فعدّ اللاويون من ابن ثلاثين سنة فما فوق فكان عددهم حسب رؤوسهم من الرجال ثمانية وثلاثين الفا"));
arasuperobject.biblemap.put("b13_23_4", new arasuperobject(13,23,4,"من هؤلاء للمناظرة على عمل بيت الرب اربعة وعشرون الفا. وستة آلاف عرفاء وقضاة"));
arasuperobject.biblemap.put("b13_23_5", new arasuperobject(13,23,5,"واربعة آلاف بوابون واربعة آلاف مسبحون للرب بالآلات التي عملت للتسبيح"));
arasuperobject.biblemap.put("b13_23_6", new arasuperobject(13,23,6,"وقسمهم داود فرقا لبني لاوي لجرشون وقهات ومراري"));
arasuperobject.biblemap.put("b13_23_7", new arasuperobject(13,23,7,"من الجرشونيين لعدان وشمعي"));
arasuperobject.biblemap.put("b13_23_8", new arasuperobject(13,23,8,"بنو لعدان الراس يحيئيل ثم زيثام ويوئيل ثلاثة"));
arasuperobject.biblemap.put("b13_23_9", new arasuperobject(13,23,9,"بنو شمعي شلوميث وحزيئيل وهاران ثلاثة. هؤلاء رؤوس آباء للعدان"));
arasuperobject.biblemap.put("b13_23_10", new arasuperobject(13,23,10,"وبنو شمعي يحث وزينا ويعوش وبريعة. هؤلاء بنو شمعي اربعة"));
arasuperobject.biblemap.put("b13_23_11", new arasuperobject(13,23,11,"وكان يحث الراس وزيزة الثاني. اما يعوش وبريعة فلم يكثرا الاولاد فكانوا في الاحصاء لبيت اب واحد"));
arasuperobject.biblemap.put("b13_23_12", new arasuperobject(13,23,12,"بنو قهات عمرام ويصهار وحبرون وعزيئيل اربعة"));
arasuperobject.biblemap.put("b13_23_13", new arasuperobject(13,23,13,"ابنا عمرام هرون وموسى. وأفرز هرون لتقديسه قدس اقداس هو وبنوه الى الابد ليوقد امام الرب ويخدمه ويبارك باسمه الى الابد"));
arasuperobject.biblemap.put("b13_23_14", new arasuperobject(13,23,14,"واما موسى رجل الله فدعي بنوه مع سبط لاوي"));
arasuperobject.biblemap.put("b13_23_15", new arasuperobject(13,23,15,"ابنا موسى جرشوم واليعزر"));
arasuperobject.biblemap.put("b13_23_16", new arasuperobject(13,23,16,"بنو جرشوم شبوئيل الراس"));
arasuperobject.biblemap.put("b13_23_17", new arasuperobject(13,23,17,"وكان ابن اليعزر رحبيا الراس ولم يكن لاليعزر بنون آخرون. واما بنو رحبيا فكانوا كثيرين جدا"));
arasuperobject.biblemap.put("b13_23_18", new arasuperobject(13,23,18,"بنو يصهار شلوميث الراس"));
arasuperobject.biblemap.put("b13_23_19", new arasuperobject(13,23,19,"بنو حبرون يريا الراس وامريا الثاني ويحزيئيل الثالث ويقمعام الرابع"));
arasuperobject.biblemap.put("b13_23_20", new arasuperobject(13,23,20,"ابنا عزيئيل ميخا الراس ويشّيّا الثاني"));
arasuperobject.biblemap.put("b13_23_21", new arasuperobject(13,23,21,"ابنا مراري محلي وموشى. ابنا محلي العازار وقيس"));
arasuperobject.biblemap.put("b13_23_22", new arasuperobject(13,23,22,"ومات العازار ولم يكن له بنون بل بنات فأخذهنّ بنو قيس اخوتهنّ"));
arasuperobject.biblemap.put("b13_23_23", new arasuperobject(13,23,23,"بنو موشي محلي وعادر ويريموث ثلاثة"));
arasuperobject.biblemap.put("b13_23_24", new arasuperobject(13,23,24,"هؤلاء بنو لاوي حسب بيوت آبائهم رؤوس الآباء حسب احصائهم في عدد الاسماء حسب رؤوسهم عاملو العمل لخدمة بيت الرب من ابن عشرين سنة فما فوق"));
arasuperobject.biblemap.put("b13_23_25", new arasuperobject(13,23,25,"لان داود قال قد اراح الرب اله اسرائيل شعبه فسكن في اورشليم الى الابد"));
arasuperobject.biblemap.put("b13_23_26", new arasuperobject(13,23,26,"وليس للاويين بعد ان يحملوا المسكن وكل آنيته لخدمته"));
arasuperobject.biblemap.put("b13_23_27", new arasuperobject(13,23,27,"لانه حسب كلام داود الاخير عدّ بنو لاوي من ابن عشرين سنة فما فوق"));
arasuperobject.biblemap.put("b13_23_28", new arasuperobject(13,23,28,"لانهم كانوا يقفون بين يدي بني هرون على خدمة بيت الرب في الدور والمخادع وعلى تطهير كل قدس وعمل خدمة بيت الله"));
arasuperobject.biblemap.put("b13_23_29", new arasuperobject(13,23,29,"وعلى خبز الوجوه ودقيق التقدمة ورقاق الفطير وما يعمل على الصاج والمربوكات وعلى كل كيل وقياس"));
arasuperobject.biblemap.put("b13_23_30", new arasuperobject(13,23,30,"ولاجل الوقوف كل صباح لحمد الرب وتسبيحه وكذلك في المساء"));
arasuperobject.biblemap.put("b13_23_31", new arasuperobject(13,23,31,"ولكل اصعاد محرقات للرب في السبوت والاهلّة والمواسم بالعدد حسب المرسوم عليهم دائما امام الرب"));
arasuperobject.biblemap.put("b13_23_32", new arasuperobject(13,23,32,"وليحرسوا حراسة خيمة الاجتماع وحراسة القدس وحراسة بني هرون اخوتهم في خدمة بيت الرب"));
arasuperobject.biblemap.put("b13_24_1", new arasuperobject(13,24,1,"وهذه فرق بني هرون. بنو هرون ناداب وابيهو العازار وايثامار"));
arasuperobject.biblemap.put("b13_24_2", new arasuperobject(13,24,2,"ومات ناداب وابيهو قبل ابيهما ولم يكن لهما بنون فكهن العازار وايثامار"));
arasuperobject.biblemap.put("b13_24_3", new arasuperobject(13,24,3,"وقسمهم داود وصادوق من بني العازار واخيمالك من بني ايثامار حسب وكالتهم في خدمتهم"));
arasuperobject.biblemap.put("b13_24_4", new arasuperobject(13,24,4,"ووجد لبني العازار رؤوس رجال اكثر من بني ايثامار فانقسموا لبني العازار رؤوسا لبيت آبائهم ستة عشر ولبني ايثامار لبيت آبائهم ثمانية"));
arasuperobject.biblemap.put("b13_24_5", new arasuperobject(13,24,5,"وانقسموا بالقرعة هؤلاء مع هؤلاء لان رؤساء القدس ورؤساء بيت الله كانوا من بني العازار ومن بني ايثامار"));
arasuperobject.biblemap.put("b13_24_6", new arasuperobject(13,24,6,"وكتبهم شمعيا بن نثنئيل الكاتب من اللاويين امام الملك والرؤساء وصادوق الكاهن واخيمالك بن ابياثار ورؤوس الآباء للكهنة واللاويين فاخذ بيت اب واحد لالعازار وأخذ واحد لايثامار"));
arasuperobject.biblemap.put("b13_24_7", new arasuperobject(13,24,7,"فخرجت القرعة الاولى ليهوياريب. الثانية ليدعيا"));
arasuperobject.biblemap.put("b13_24_8", new arasuperobject(13,24,8,"الثالثة لحاريم. الرابعة لسعوريم"));
arasuperobject.biblemap.put("b13_24_9", new arasuperobject(13,24,9,"الخامسة لملكيا. السادسة لميّامين"));
arasuperobject.biblemap.put("b13_24_10", new arasuperobject(13,24,10,"السابعة لهقّوص. الثامنة لابيّا"));
arasuperobject.biblemap.put("b13_24_11", new arasuperobject(13,24,11,"التاسعة ليشوع. العاشرة لشكنيا"));
arasuperobject.biblemap.put("b13_24_12", new arasuperobject(13,24,12,"الحادية عشرة لالياشيب. الثانية عشرة لياقيم"));
