arasuperobject.biblemap.put("b13_24_18", new arasuperobject(13,24,18,"الثالثة والعشرون لدلايا. الرابعة والعشرون لمعزيا"));
arasuperobject.biblemap.put("b13_24_19", new arasuperobject(13,24,19,"فهذه وكالتهم وخدمتهم للدخول الى بيت الرب حسب حكمهم عن يد هرون ابيهم كما أمره الرب اله اسرائيل"));
arasuperobject.biblemap.put("b13_24_20", new arasuperobject(13,24,20,"واما بنو لاوي الباقون فمن بني عمرام شوبائيل ومن بني شوبائيل يحديا"));
