arasuperobject.biblemap.put("b13_24_23", new arasuperobject(13,24,23,"ومن بني حبرون يريا وامريا الثاني ويحزيئيل الثالث ويقمعام الرابع"));
arasuperobject.biblemap.put("b13_24_24", new arasuperobject(13,24,24,"من بني عزّيئيل ميخا. من بني ميخا شامور"));
arasuperobject.biblemap.put("b13_24_25", new arasuperobject(13,24,25,"اخو ميخا يشّيّا ومن بني يشّيّا زكريا"));
arasuperobject.biblemap.put("b13_24_26", new arasuperobject(13,24,26,"ابنا مراري محلي وموشي. ابن يعزيا بنو"));
arasuperobject.biblemap.put("b13_24_27", new arasuperobject(13,24,27,"من بني مراري ليعزيا بنو وشوهم وزوكور وعبري"));
