arasuperobject.biblemap.put("b13_24_29", new arasuperobject(13,24,29,"واما قيس فابن قيس يرحمئيل"));
arasuperobject.biblemap.put("b13_24_30", new arasuperobject(13,24,30,"وبنو موشي محلي وعادر ويريموث. هؤلاء بنو اللاويين حسب بيوت آبائهم"));
arasuperobject.biblemap.put("b13_24_31", new arasuperobject(13,24,31,"والقوا هم ايضا قرعا مقابل اخوتهم بني هرون امام داود الملك وصادوق واخيمالك ورؤؤس آباء الكهنة واللاويين. الآباء الرؤوس كما اخوتهم الاصاغر"));
arasuperobject.biblemap.put("b13_25_1", new arasuperobject(13,25,1,"وافرز داود ورؤساء الجيش للخدمة بني آساف وهيمان ويدوثون المتنبئين بالعيدان والرباب والصنوج. وكان عددهم من رجال العمل حسب خدمتهم"));
arasuperobject.biblemap.put("b13_25_2", new arasuperobject(13,25,2,"من بني آساف زكور ويوسف ونثنيا واشرئيلة بنو آساف تحت يد آساف المتنبّئ بين يدي الملك"));
arasuperobject.biblemap.put("b13_25_3", new arasuperobject(13,25,3,"من يدوثون بنو يدوثون جدليا وصري ويشعيا وحشبيا ومتثيا ستة تحت يد ابيهم يدوثون المتنبّئ بالعود لاجل الحمد والتسبيح للرب"));
arasuperobject.biblemap.put("b13_25_4", new arasuperobject(13,25,4,"من هيمان بنو هيمان بقّيّا ومتّنيا وعزيئيل وشبوئيل ويريموت وحننيا وحناني وايلياثة وجدّلتي وروممتي عزر ويشبقاشة وملوثي وهوثير ومحزيوث"));
arasuperobject.biblemap.put("b13_25_5", new arasuperobject(13,25,5,"جميع هؤلاء بنو هيمان رائي الملك بكلام الله لرفع القرن. ورزق الرب هيمان اربعة عشر ابنا وثلاث بنات"));
arasuperobject.biblemap.put("b13_25_6", new arasuperobject(13,25,6,"كل هؤلاء تحت يد ابيهم لاجل غناء بيت الرب بالصنوج والرباب والعيدان لخدمة بيت الله تحت يد الملك وآساف ويدوثون وهيمان"));
arasuperobject.biblemap.put("b13_25_7", new arasuperobject(13,25,7,"وكان عددهم مع اخوتهم المتعلمين الغناء للرب كل الخبيرين مئتين وثمانية وثمانين"));
arasuperobject.biblemap.put("b13_25_8", new arasuperobject(13,25,8,"والقوا قرع الحراسة الصغير كما الكبير المعلم مع التلميذ"));
arasuperobject.biblemap.put("b13_25_9", new arasuperobject(13,25,9,"فخرجت القرعة الاولى التي هي لآساف ليوسف. الثانية لجدليا. هو واخوته وبنوه اثنا عشر"));
