arasuperobject.biblemap.put("b13_25_27", new arasuperobject(13,25,27,"العشرون لإيليآثة. بنوه واخوته اثنا عشر"));
arasuperobject.biblemap.put("b13_25_28", new arasuperobject(13,25,28,"الحادية والعشرون لهوثير. بنوه واخوته اثنا عشر"));
arasuperobject.biblemap.put("b13_25_29", new arasuperobject(13,25,29,"الثانية والعشرون لجدلتي. بنوه واخوته اثنا عشر"));
arasuperobject.biblemap.put("b13_25_30", new arasuperobject(13,25,30,"الثالثة والعشرون لمحزيوث. بنوه واخوته اثنا عشر"));
arasuperobject.biblemap.put("b13_25_31", new arasuperobject(13,25,31,"الرابعة والعشرون لروممتي عزر. بنوه واخوته اثنا عشر"));
arasuperobject.biblemap.put("b13_26_1", new arasuperobject(13,26,1,"واما اقسام البوابين فمن القورحيين مشلميا بن قوري من بني آساف"));
arasuperobject.biblemap.put("b13_26_2", new arasuperobject(13,26,2,"وكان لمشلميا بنون زكريا البكر ويديعئيل الثاني وزبديا الثالث ويثنئيل الرابع"));
arasuperobject.biblemap.put("b13_26_3", new arasuperobject(13,26,3,"وعيلام الخامس ويهو حانان السادس واليهو عيناي السابع"));
arasuperobject.biblemap.put("b13_26_4", new arasuperobject(13,26,4,"وكان لعوبيد ادوم بنون شمعيا البكر ويهوزاباد الثاني ويوآخ الثالث وساكار الرابع ونثنئيل الخامس"));
arasuperobject.biblemap.put("b13_26_5", new arasuperobject(13,26,5,"وعمّيئيل السادس ويساكر السابع وفعلتاي الثامن. لان الله باركه"));
arasuperobject.biblemap.put("b13_26_6", new arasuperobject(13,26,6,"ولشمعيا ابنه ولد بنون تسلطوا في بيت آبائهم لانهم جبابرة بأس"));
arasuperobject.biblemap.put("b13_26_7", new arasuperobject(13,26,7,"بنو شمعيا عثني ورفائيل وعوبيد والزاباد اخوته اصحاب بأس. اليهو وسمكيا"));
arasuperobject.biblemap.put("b13_26_8", new arasuperobject(13,26,8,"كل هؤلاء من بني عوبيد ادوم هم وبنوهم واخوتهم اصحاب بأس بقوة في الخدمة اثنان وستون لعوبيد ادوم"));
arasuperobject.biblemap.put("b13_26_9", new arasuperobject(13,26,9,"وكان لمشلميا بنون واخوة اصحاب بأس ثمانية عشر"));
arasuperobject.biblemap.put("b13_26_10", new arasuperobject(13,26,10,"وكان لحوسة من بني مراري بنون شمري الراس. مع انه لم يكن بكرا جعله ابوه راسا"));
arasuperobject.biblemap.put("b13_26_11", new arasuperobject(13,26,11,"حلقيا الثاني وطبليا الثالث وزكريا الرابع كل بني حوسة واخوته ثلاثة عشر"));
arasuperobject.biblemap.put("b13_26_12", new arasuperobject(13,26,12,"لفرق البوابين هؤلاء حسب رؤوس الجبابرة حراسة كما لاخوتهم للخدمة في بيت الرب"));
arasuperobject.biblemap.put("b13_26_13", new arasuperobject(13,26,13,"والقوا قرعا الصغير كالكبير حسب بيوت آبائهم لكل باب"));
arasuperobject.biblemap.put("b13_26_14", new arasuperobject(13,26,14,"فاصابت القرعة من جهة الشرق شلميا. ولزكريا ابنه المشير بفطنة القوا قرعا فخرجت القرعة له الى الشمال"));
arasuperobject.biblemap.put("b13_26_15", new arasuperobject(13,26,15,"لعوبيد ادوم الى الجنوب ولبنيه المخازن"));
arasuperobject.biblemap.put("b13_26_16", new arasuperobject(13,26,16,"لشفّيم وحوسة الى الغرب من باب شلكة في مصعد الدرج محرس مقابل محرس"));
arasuperobject.biblemap.put("b13_26_17", new arasuperobject(13,26,17,"من جهة الشرق كان اللاويون ستة. من جهة الشمال اربعة لليوم من جهة الجنوب اربعة لليوم ومن جهة المخازن اثنين اثنين"));
arasuperobject.biblemap.put("b13_26_18", new arasuperobject(13,26,18,"من جهة الرواق الى الغرب اربعة في المصعد واثنين في الرواق"));
arasuperobject.biblemap.put("b13_26_19", new arasuperobject(13,26,19,"هذه اقسام البوابين من بني القورحيين ومن بني مراري"));
arasuperobject.biblemap.put("b13_26_20", new arasuperobject(13,26,20,"واما اللاويون فاخيا على خزائن بيت الله وعلى خزائن الاقداس"));
arasuperobject.biblemap.put("b13_26_21", new arasuperobject(13,26,21,"وما بنو لعدان فبنو لعدان الجرشوني رؤس بيت الآباء للعدان الجرشوني يحيئيلي"));
arasuperobject.biblemap.put("b13_26_22", new arasuperobject(13,26,22,"بنو يحيئيل زيثام ويوئيل اخوه على خزائن بيت الرب"));
arasuperobject.biblemap.put("b13_26_23", new arasuperobject(13,26,23,"من العمراميين واليصهاريين والحبرونيين والعزيئيليين"));
arasuperobject.biblemap.put("b13_26_24", new arasuperobject(13,26,24,"كان شبوئيل بن جرشوم بن موسى وكان رئيسا على الخزائن"));
arasuperobject.biblemap.put("b13_26_25", new arasuperobject(13,26,25,"واخوته من أليعزر رحبيا ابنه ويشعيا ابنه ويورام ابنه وزكري ابنه وشلوميث ابنه"));
arasuperobject.biblemap.put("b13_26_26", new arasuperobject(13,26,26,"شلوميث هذا واخوته كانوا على جميع خزائن الاقداس التي قدسها داود الملك ورؤوس الآباء ورؤساء الالوف والمئات ورؤساء الجيش"));
arasuperobject.biblemap.put("b13_26_27", new arasuperobject(13,26,27,"من الحروب ومن الغنائم قدسوا لتشديد بيت الرب"));
arasuperobject.biblemap.put("b13_26_28", new arasuperobject(13,26,28,"وكل ما قدسه صموئيل الرائي وشاول بن قيس وابنير بن نير ويوآب ابن صروية كل مقدس كان تحت يد شلوميث واخوته"));
arasuperobject.biblemap.put("b13_26_29", new arasuperobject(13,26,29,"ومن اليصهاريين كننيا وبنوه للعمل الخارجي على اسرائيل عرفاء وقضاة"));
arasuperobject.biblemap.put("b13_26_30", new arasuperobject(13,26,30,"من الحبرونيين حشبيا واخوته ذوو بأس الف وسبع مئة موكلين على اسرائيل في عبر الاردن غربا في كل عمل الرب وفي خدمة الملك"));
arasuperobject.biblemap.put("b13_26_31", new arasuperobject(13,26,31,"من الحبرونيين يريا راس الحبرونيين حسب مواليد آبائه. في السنة الرابعة لملك داود طلبوا فوجد فيهم جبابرة بأس في يعزير جلعاد"));
arasuperobject.biblemap.put("b13_26_32", new arasuperobject(13,26,32,"واخوته ذوو بأس الفان وسبع مئة رؤوس آباء. ووكلهم داود الملك على الرأوبينيين والجاديين ونصف سبط منسّى في كل أمور الله وامور الملك"));
arasuperobject.biblemap.put("b13_27_1", new arasuperobject(13,27,1,"وبنو اسرائيل حسب عددهم من رؤوس الآباء ورؤساء الالوف والمئات وعرفائهم الذين يخدمون الملك في كل امور الفرق الداخلين والخارجين شهرا فشهرا لكل شهور السنة كل فرقة كانت اربعة وعشرين الفا"));
arasuperobject.biblemap.put("b13_27_2", new arasuperobject(13,27,2,"على الفرقة الاولى للشهر الاول يشبعام بن زبديئيل وفي فرقته اربعة وعشرون الفا"));
arasuperobject.biblemap.put("b13_27_3", new arasuperobject(13,27,3,"من بني فارص كان راس جميع رؤساء الجيوش للشهر الاول"));
arasuperobject.biblemap.put("b13_27_4", new arasuperobject(13,27,4,"وعلى فرقة الشهر الثاني دوداي الاخوخي ومن فرقته مقلوث الرئيس. وفي فرقته اربعة وعشرون الفا"));
arasuperobject.biblemap.put("b13_27_5", new arasuperobject(13,27,5,"رئيس الجيش الثالث للشهر الثالث بنايا بن يهوياداع الكاهن الراس وفي فرقته اربعة وعشرون الفا"));
arasuperobject.biblemap.put("b13_27_6", new arasuperobject(13,27,6,"هو بنايا جبار الثلاثين وعلى الثلاثين ومن فرقته عميزاباد ابنه"));
arasuperobject.biblemap.put("b13_27_7", new arasuperobject(13,27,7,"الرابع للشهر الرابع عسائيل اخو يوآب وزبديا ابنه بعده وفي فرقته اربعة وعشرون الفا"));
arasuperobject.biblemap.put("b13_27_8", new arasuperobject(13,27,8,"الخامس للشهر الخامس الرئيس شمحوث اليزراحي وفي فرقته اربعة وعشرون الفا"));
arasuperobject.biblemap.put("b13_27_9", new arasuperobject(13,27,9,"السادس للشهر السادس عيرا بن عقّيش التقوعي وفي فرقته اربعة وعشرون الفا"));
arasuperobject.biblemap.put("b13_27_10", new arasuperobject(13,27,10,"السابع للشهر السابع حالص الفلوني من بني افرايم وفي فرقته اربعة وعشرون الفا"));
arasuperobject.biblemap.put("b13_27_11", new arasuperobject(13,27,11,"الثامن للشهر الثامن سبكاي الحوشاتي من الزارحيين وفي فرقته اربعة وعشرون الفا"));
arasuperobject.biblemap.put("b13_27_12", new arasuperobject(13,27,12,"التاسع للشهر التاسع ابيعزر العناثوثي من بنيامين وفي فرقته اربعة وعشرون الفا"));
arasuperobject.biblemap.put("b13_27_13", new arasuperobject(13,27,13,"العاشر للشهر العاشر مهراي النطوفاتي من الزارحيين وفي فرقته اربعة وعشرون الفا"));
arasuperobject.biblemap.put("b13_27_14", new arasuperobject(13,27,14,"الحادي عشر للشهر الحادي عشر بنايا الفرعتوني من بني افرايم وفي فرقته اربعة وعشرون الفا"));
arasuperobject.biblemap.put("b13_27_15", new arasuperobject(13,27,15,"الثاني عشر للشهر الثاني عشر خلداي النطوفاتي من عثنيئيل وفي فرقته اربعة وعشرون الفا"));
arasuperobject.biblemap.put("b13_27_16", new arasuperobject(13,27,16,"وعلى اسباط اسرائيل. للرأوبينيين الرئيس أليعزر بن زكري. للشمعونيين شفطيا بن معكة"));
arasuperobject.biblemap.put("b13_27_17", new arasuperobject(13,27,17,"للاويين حشبيا بن قموئيل. لهرون صادوق"));
arasuperobject.biblemap.put("b13_27_18", new arasuperobject(13,27,18,"ليهوذا اليهو من اخوة داود. ليساكر عمري بن ميخائيل"));
arasuperobject.biblemap.put("b13_27_19", new arasuperobject(13,27,19,"لزبولون يشمعيا بن عوبديا. لنفتالي يريموث بن عزرئيل"));
arasuperobject.biblemap.put("b13_27_20", new arasuperobject(13,27,20,"لبني افرايم هوشع بن عزريا. لنصف سبط منسّى يوئيل بن فدايا"));
arasuperobject.biblemap.put("b13_27_21", new arasuperobject(13,27,21,"لنصف سبط منسّى في جلعاد يدّو بن زكريا. لبنيامين يعسيئيل بن ابنير"));
arasuperobject.biblemap.put("b13_27_22", new arasuperobject(13,27,22,"لدان عزرئيل بن يروحام. هؤلاء رؤساء اسباط اسرائيل"));
arasuperobject.biblemap.put("b13_27_23", new arasuperobject(13,27,23,"ولم ياخذ داود عددهم من ابن عشرين سنة فما دون. لان الرب قال انه يكثر اسرائيل كنجوم السماء"));
arasuperobject.biblemap.put("b13_27_24", new arasuperobject(13,27,24,"يوآب بن صروية ابتدأ يحصي ولم يكمل لانه كان من جرى ذلك سخط على اسرائيل ولم يدوّن العدد في سفر اخبار الايام للملك داود"));
arasuperobject.biblemap.put("b13_27_25", new arasuperobject(13,27,25,"وعلى خزائن الملك عزموت بن عديئيل. وعلى الخزائن في الحقل في المدن والقرى والحصون يهوناثان بن عزيا"));
arasuperobject.biblemap.put("b13_27_26", new arasuperobject(13,27,26,"وعلى الفعلة في الحقل لشغل الارض عزري بن كلوب"));
arasuperobject.biblemap.put("b13_27_27", new arasuperobject(13,27,27,"وعلى الكروم شمعي الرامي. وعلى ما في الكروم من خزائن الخمر زبدي الشفمي"));
arasuperobject.biblemap.put("b13_27_28", new arasuperobject(13,27,28,"وعلى الزيتون والجميز اللذين في السهل بعل حانان الجديري وعلى خزائن الزيت يوعاش"));
arasuperobject.biblemap.put("b13_27_29", new arasuperobject(13,27,29,"وعلى البقر السائم في شارون شطراي الشاروني. وعلى البقر الذي في الاودية شافاط بن عدلاي"));
arasuperobject.biblemap.put("b13_27_30", new arasuperobject(13,27,30,"وعلى الجمال اوبيل الاسمعيلي. وعلى الحمير يحديا الميرونوثي"));
arasuperobject.biblemap.put("b13_27_31", new arasuperobject(13,27,31,"وعلى الغنم يازيز الهاجري. كل هؤلاء رؤساء الاملاك التي للملك داود"));
arasuperobject.biblemap.put("b13_27_32", new arasuperobject(13,27,32,"ويهوناثان عم داود كان مشيرا ورجلا مختبرا وفقيها. ويحيئيل بن حكموني كان مع بني الملك"));
arasuperobject.biblemap.put("b13_27_33", new arasuperobject(13,27,33,"وكان اخيتوفل مشيرا للملك وحوشاي الاركي صاحب الملك"));
arasuperobject.biblemap.put("b13_27_34", new arasuperobject(13,27,34,"وبعد اخيتوفيل يهوياداع بن بنايا وابياثار. وكان رئيس جيش الملك يوآب"));
arasuperobject.biblemap.put("b13_28_1", new arasuperobject(13,28,1,"وجمع داود كل رؤساء اسرائيل رؤساء الاسباط ورؤساء الفرق الخادمين الملك ورؤساء الالوف ورؤساء المئات ورؤساء كل الاموال والاملاك التي للملك ولبنيه مع الخصيان والابطال وكل جبابرة البأس الى اورشليم"));
arasuperobject.biblemap.put("b13_28_2", new arasuperobject(13,28,2,"ووقف داود الملك على رجليه وقال اسمعوني يا اخوتي وشعبي. كان في قلبي ان ابني بيت قرار لتابوت عهد الرب ولموطئ قدمي الهنا وقد هيّأت للبناء"));
arasuperobject.biblemap.put("b13_28_3", new arasuperobject(13,28,3,"ولكن الله قال لي لا تبني بيتا لاسمي لانك انت رجل حروب وقد سفكت دما"));
arasuperobject.biblemap.put("b13_28_4", new arasuperobject(13,28,4,"وقد اختارني الرب اله اسرائيل من كل بيت ابي لاكون ملكا على اسرائيل الى الابد لانه انما اختار يهوذا رئيسا ومن بيت يهوذا بيت ابي ومن بني ابي سرّ بي ليملّكني على كل اسرائيل"));
arasuperobject.biblemap.put("b13_28_5", new arasuperobject(13,28,5,"ومن كل بنيّ لان الرب اعطاني بنين كثيرين انما اختار سليمان ابني ليجلس على كرسي مملكة الرب على اسرائيل"));
arasuperobject.biblemap.put("b13_28_6", new arasuperobject(13,28,6,"وقال لي ان سليمان ابنك هو يبني بيتي ودياري لاني اخترته لي ابنا وانا اكون له ابا"));
arasuperobject.biblemap.put("b13_28_7", new arasuperobject(13,28,7,"واثبت مملكته الى الابد اذا تشدد للعمل حسب وصاياي واحكامي كهذا اليوم"));
arasuperobject.biblemap.put("b13_28_8", new arasuperobject(13,28,8,"والآن في اعين كل اسرائيل محفل الرب وفي سماع الهنا احفظوا واطلبوا جميع وصايا الرب الهكم لكي ترثوا الارض الجيدة وتورثوها لاولادكم بعدكم الى الابد"));
arasuperobject.biblemap.put("b13_28_9", new arasuperobject(13,28,9,"وانت يا سليمان ابني اعرف اله ابيك واعبده بقلب كامل ونفس راغبة لان الرب يفحص جميع القلوب ويفهم كل تصورات الافكار. فاذا طلبته يوجد منك واذا تركته يرفضك الى الابد"));
arasuperobject.biblemap.put("b13_28_10", new arasuperobject(13,28,10,"انظر الآن لان الرب قد اختارك لتبني بيتا للمقدس فتشدد واعمل"));
arasuperobject.biblemap.put("b13_28_11", new arasuperobject(13,28,11,"واعطى داود سليمان ابنه مثال الرواق وبيوته وخزائنه وعلاليه ومخادعه الداخلية وبيت الغطاء"));
arasuperobject.biblemap.put("b13_28_12", new arasuperobject(13,28,12,"ومثال كل ما كان عنده بالروح لديار بيت الرب ولجميع المخادع حواليه ولخزائن بيت الله وخزائن الاقداس"));
arasuperobject.biblemap.put("b13_28_13", new arasuperobject(13,28,13,"ولفرق الكهنة واللاويين ولكل عمل خدمة بيت الرب ولكل آنية خدمة بيت الرب"));
arasuperobject.biblemap.put("b13_28_14", new arasuperobject(13,28,14,"فمن الذهب بالوزن لما هو من ذهب لكل آنية خدمة فخدمة ولجميع آنية الفضة فضة بالوزن لكل آنية خدمة فخدمة"));
arasuperobject.biblemap.put("b13_28_15", new arasuperobject(13,28,15,"وبالوزن لمنائر الذهب وسرجها من ذهب بالوزن لكل منارة فمنارة وسرجها ولمنائر الفضة بالوزن لكل منارة وسرجها حسب خدمة منارة فمنارة"));
arasuperobject.biblemap.put("b13_28_16", new arasuperobject(13,28,16,"وذهبا بالوزن لموائد خبز الوجوه لكل مائدة فمائدة وفضة لموائد الفضة"));
arasuperobject.biblemap.put("b13_28_17", new arasuperobject(13,28,17,"وذهبا خالصا للمناشل والمناضح والكؤوس. ولاقداح الذهب بالوزن لقدح فقدح ولاقداح الفضة بالوزن لقدح فقدح"));
arasuperobject.biblemap.put("b13_28_18", new arasuperobject(13,28,18,"ولمذبح البخور ذهبا مصفّى بالوزن وذهبا لمثال مركبة الكروبيم الباسطة اجنحتها المظللة تابوت عهد الرب"));
arasuperobject.biblemap.put("b13_28_19", new arasuperobject(13,28,19,"قد افهمني الرب كل ذلك بالكتابة بيده عليّ اي كل اشغال المثال"));
arasuperobject.biblemap.put("b13_28_20", new arasuperobject(13,28,20,"وقال داود لسليمان ابنه تشدد وتشجع واعمل لا تخف ولا ترتعب لان الرب الاله الهي معك. لا يخذلك ولا يتركك حتى تكمل كل عمل خدمة بيت الرب"));
arasuperobject.biblemap.put("b13_28_21", new arasuperobject(13,28,21,"وهوذا فرق الكهنة واللاويين لكل خدمة بيت الله. ومعك في كل عمل كل نبيه بحكمة لكل خدمة والرؤساء وكل الشعب تحت كل اوامرك"));
arasuperobject.biblemap.put("b13_29_1", new arasuperobject(13,29,1,"وقال داود الملك لكل المجمع ان سليمان ابني الذي وحده اختاره الله انما هو صغير وغضّ والعمل عظيم لان الهيكل ليس لانسان بل للرب الاله"));
arasuperobject.biblemap.put("b13_29_2", new arasuperobject(13,29,2,"وانا بكل قوتي هيّأت لبيت الهي الذهب لما هو من ذهب والفضة لما هو من فضة والنحاس لما هو من نحاس والحديد لما هو من حديد والخشب لما هو من خشب وحجارة الجزع وحجارة للترصيع وحجارة كحلاء ورقماء وكل حجارة كريمة وحجارة الرخام بكثرة"));
arasuperobject.biblemap.put("b13_29_3", new arasuperobject(13,29,3,"وايضا لاني قد سررت ببيت الهي لي خاصّة من ذهب وفضة قد دفعتها لبيت الهي فوق جميع ما هيّأته لبيت القدس"));
arasuperobject.biblemap.put("b13_29_4", new arasuperobject(13,29,4,"ثلاثة آلاف وزنة ذهب من ذهب اوفير وسبعة آلاف وزنة فضة مصفّاة لاجل تغشية حيطان البيوت"));
arasuperobject.biblemap.put("b13_29_5", new arasuperobject(13,29,5,"الذهب للذهب والفضة للفضة ولكل عمل بيد ارباب الصنائع. فمن ينتدب اليوم لملء يده للرب"));
arasuperobject.biblemap.put("b13_29_6", new arasuperobject(13,29,6,"فانتدب رؤساء الآباء ورؤساء اسباط اسرائيل ورؤساء الالوف والمئات ورؤساء اشغال الملك"));
arasuperobject.biblemap.put("b13_29_7", new arasuperobject(13,29,7,"واعطوا لخدمة بيت الله خمسة آلاف وزنة وعشرة آلاف درهم من الذهب وعشرة آلاف وزنة من الفضة وثمانية عشر الف وزنة من النحاس ومئة الف وزنة من الحديد"));
arasuperobject.biblemap.put("b13_29_8", new arasuperobject(13,29,8,"ومن وجد عنده حجارة اعطاها لخزينة بيت الرب عن يد يحيئيل الجرشوني"));
arasuperobject.biblemap.put("b13_29_9", new arasuperobject(13,29,9,"وفرح الشعب بانتدابهم لانهم بقلب كامل انتدبوا للرب. وداود الملك ايضا فرح فرحا عظيما"));
arasuperobject.biblemap.put("b13_29_10", new arasuperobject(13,29,10,"وبارك داود الرب امام كل الجماعة وقال داود مبارك انت ايها الرب اله اسرائيل ابينا من الازل والى الابد"));
arasuperobject.biblemap.put("b13_29_11", new arasuperobject(13,29,11,"لك يا رب العظمة والجبروت والجلال والبهاء والمجد لان لك كل ما في السماء والارض. لك يا رب الملك وقد ارتفعت راسا على الجميع"));
arasuperobject.biblemap.put("b13_29_12", new arasuperobject(13,29,12,"والغنى والكرامة من لدنك وانت تتسلط على الجميع وبيدك القوة والجبروت وبيدك تعظيم وتشديد الجميع"));
arasuperobject.biblemap.put("b13_29_13", new arasuperobject(13,29,13,"والآن يا الهنا نحمدك ونسبّح اسمك الجليل"));
arasuperobject.biblemap.put("b13_29_14", new arasuperobject(13,29,14,"ولكن من انا ومن هو شعبي حتى نستطيع ان ننتدب هكذا. لان منك الجميع ومن يدك اعطيناك"));
arasuperobject.biblemap.put("b13_29_15", new arasuperobject(13,29,15,"لاننا نحن غرباء امامك ونزلاء مثل كل آبائنا. ايامنا كالظل على الارض وليس رجاء"));
arasuperobject.biblemap.put("b13_29_16", new arasuperobject(13,29,16,"ايها الرب الهنا كل هذه الثروة التي هيأناها لنبني لك بيتا لاسم قدسك انما هي من يدك ولك الكل"));
arasuperobject.biblemap.put("b13_29_17", new arasuperobject(13,29,17,"وقد علمت يا الهي انك انت تمتحن القلوب وتسرّ بالاستقامة. انا باستقامة قلبي انتدبت بكل هذه والآن شعبك الموجود هنا رأيته بفرح ينتدب لك"));
arasuperobject.biblemap.put("b13_29_18", new arasuperobject(13,29,18,"يا رب اله ابراهيم واسحق واسرائيل آبائنا احفظ هذه الى الابد في تصوّر افكار قلوب شعبك واعدّ قلوبهم نحوك"));
arasuperobject.biblemap.put("b13_29_19", new arasuperobject(13,29,19,"واما سليمان ابني فاعطه قلبا كاملا ليحفظ وصاياك شهاداتك وفرائضك وليعمل الجميع وليبني الهيكل الذي هيّأت له"));
arasuperobject.biblemap.put("b13_29_20", new arasuperobject(13,29,20,"ثم قال داود لكل الجماعة باركوا الرب الهكم. فبارك كل الجماعة الرب اله آبائهم وخرّوا وسجدوا للرب وللملك"));
arasuperobject.biblemap.put("b13_29_21", new arasuperobject(13,29,21,"وذبحوا للرب ذبائح واصعدوا محرقات للرب في غد ذلك اليوم الف ثور والف كبش والف خروف مع سكائبها وذبائح كثيرة لكل اسرائيل"));
arasuperobject.biblemap.put("b13_29_22", new arasuperobject(13,29,22,"واكلوا وشربوا امام الرب في ذلك اليوم بفرح عظيم. وملّكوا ثانية سليمان بن داود ومسحوه للرب رئيسا وصادوق كاهنا"));
arasuperobject.biblemap.put("b13_29_23", new arasuperobject(13,29,23,"وجلس سليمان على كرسي الرب ملكا مكان داود ابيه ونجح واطاعه كل اسرائيل"));
arasuperobject.biblemap.put("b13_29_24", new arasuperobject(13,29,24,"وجميع الرؤساء والابطال وجميع اولاد الملك داود ايضا خضعوا لسليمان الملك"));
arasuperobject.biblemap.put("b13_29_25", new arasuperobject(13,29,25,"وعظم الرب سليمان جدا في اعين جميع اسرائيل وجعل عليه جلالا ملكيا لم يكن على ملك قبله في اسرائيل"));
arasuperobject.biblemap.put("b13_29_26", new arasuperobject(13,29,26,"وداود بن يسّى ملك على كل اسرائيل"));
arasuperobject.biblemap.put("b13_29_27", new arasuperobject(13,29,27,"والزمان الذي ملك فيه على اسرائيل اربعون سنة. ملك سبع سنين في حبرون وملك ثلاثا وثلاثين سنة في اورشليم"));
arasuperobject.biblemap.put("b13_29_28", new arasuperobject(13,29,28,"ومات بشيبة صالحة وقد شبع اياما وغنى وكرامة. وملك سليمان ابنه مكانه"));
arasuperobject.biblemap.put("b13_29_29", new arasuperobject(13,29,29,"وأمور داود الملك الاولى والاخيرة هي مكتوبة في اخبار صموئيل الرائي واخبار ناثان النبي واخبار جاد الرائي"));
arasuperobject.biblemap.put("b13_29_30", new arasuperobject(13,29,30,"مع كل ملكه وجبروته والاوقات التي عبرت عليه وعلى اسرائيل وعلى كل ممالك الاروض"));

