arasuperobject.biblemap.put("b14_8_13", new arasuperobject(14,8,13,"أمر كل يوم بيومه من المحرقات حسب وصية موسى في السبوت والاهلّة والمواسم ثلاث مرات في السنة في عيد الفطير وعيد الاسابيع وعيد المظال"));
arasuperobject.biblemap.put("b14_8_14", new arasuperobject(14,8,14,"واوقف حسب قضاء داود ابيه فرق الكهنة على خدمتهم واللاويين على حراساتهم للتسبيح والخدمة امام الكهنة عمل كل يوم بيومه والبوابين حسب فرقهم على كل باب. لانه هكذا هي وصية داود رجل الله"));
arasuperobject.biblemap.put("b14_8_15", new arasuperobject(14,8,15,"ولم يحيدوا عن وصية الملك على الكهنة واللاويين في كل أمر وفي الخزائن"));
arasuperobject.biblemap.put("b14_8_16", new arasuperobject(14,8,16,"فتهيّأ كل عمل سليمان الى يوم تأسيس بيت الرب والى نهايته. فكمل بيت الرب"));
arasuperobject.biblemap.put("b14_8_17", new arasuperobject(14,8,17,"حينئذ ذهب سليمان الى عصيون جابر والى ايلة على شاطئ البحر في ارض ادوم"));
arasuperobject.biblemap.put("b14_8_18", new arasuperobject(14,8,18,"وارسل له حورام بيد عبيده سفنا وعبيدا يعرفون البحر فأتوا مع عبيد سليمان الى اوفير واخذوا من هناك اربع مئة وخمسين وزنة ذهب واتوا بها الى الملك سليمان"));
arasuperobject.biblemap.put("b14_9_1", new arasuperobject(14,9,1,"وسمعت ملكة سبأ بخبر سليمان فاتت لتمتحن سليمان بمسائل الى اورشليم بموكب عظيم جدا وجمال حاملة اطيابا وذهبا بكثرة وحجارة كريمة فاتت الى سليمان وكلمته عن كل ما في قلبها"));
arasuperobject.biblemap.put("b14_9_2", new arasuperobject(14,9,2,"فاخبرها سليمان بكل كلامها. ولم يخف عن سليمان أمر الا واخبرها به"));
