arasuperobject.biblemap.put("b14_9_4", new arasuperobject(14,9,4,"وطعام مائدته ومجلس عبيده وموقف خدامه وملابسهم وسقاته وملابسهم ومحرقاته التي كان يصعدها في بيت الرب لم تبق فيها روح بعد"));
arasuperobject.biblemap.put("b14_9_5", new arasuperobject(14,9,5,"فقالت للملك صحيح الخبر الذي سمعته في ارضي عن امورك وعن حكمتك"));
arasuperobject.biblemap.put("b14_9_6", new arasuperobject(14,9,6,"ولم اصدق كلامهم حتى جئت وأبصرت عيناي فهوذا لم أخبر بنصف كثرة حكمتك. زدت على الخبر الذي سمعته"));
arasuperobject.biblemap.put("b14_9_7", new arasuperobject(14,9,7,"فطوبى لرجالك وطوبى لعبيدك هؤلاء الواقفين امامك دائما والسامعين حكمتك"));
arasuperobject.biblemap.put("b14_9_8", new arasuperobject(14,9,8,"ليكن مباركا الرب الهك الذي سرّ بك وجعلك على كرسيه ملكا للرب الهك. لان الهك احب اسرائيل ليثبته الى الابد قد جعلك عليهم ملكا لتجري حكما وعدلا"));
arasuperobject.biblemap.put("b14_9_9", new arasuperobject(14,9,9,"واهدت للملك مئة وعشرين وزنة ذهب واطيابا كثيرة جدا وحجارة كريمة. ولم يكن مثل ذلك الطيب الذي اهدته ملكة سبا للملك سليمان"));
arasuperobject.biblemap.put("b14_9_10", new arasuperobject(14,9,10,"وكذا عبيد حورام وعبيد سليمان الذين جلبوا ذهبا من اوفير أتوا بخشب الصندل وحجارة كريمة"));
