arasuperobject.biblemap.put("b14_9_15", new arasuperobject(14,9,15,"وعمل الملك سليمان مئتي ترس من ذهب مطرّق. خصّ الترس الواحد ست مئة شاقل من الذهب المطرّق"));
arasuperobject.biblemap.put("b14_9_16", new arasuperobject(14,9,16,"وثلاث مئة مجنّ من ذهب مطرّق. خصّ المجن الواحد ثلاث مئة شاقل من الذهب. وجعلها الملك في بيت وعر لبنان"));
arasuperobject.biblemap.put("b14_9_17", new arasuperobject(14,9,17,"وعمل الملك كرسيا عظيما من عاج وغشّاه بذهب خالص"));
