arasuperobject.biblemap.put("b14_9_24", new arasuperobject(14,9,24,"وكانوا ياتون كل واحد بهديته بآنية فضة وآنية ذهب وحلل وسلاح واطياب وخيل وبغال سنة فسنة"));
arasuperobject.biblemap.put("b14_9_25", new arasuperobject(14,9,25,"وكان لسليمان اربعة آلاف مذود خيل ومركبات واثنا عشر الف فارس فجعلها في مدن المركبات ومع الملك في اورشليم"));
arasuperobject.biblemap.put("b14_9_26", new arasuperobject(14,9,26,"وكان متسلطا على جميع الملوك من النهر الى ارض الفلسطينيين والى تخوم مصر"));
