arasuperobject.biblemap.put("b14_10_9", new arasuperobject(14,10,9,"وقال لهم بماذا تشيرون انتم فنردّ جوابا على هذا الشعب الذين كلموني قائلين خفّف من النير الذي جعله علينا ابوك"));
arasuperobject.biblemap.put("b14_10_10", new arasuperobject(14,10,10,"فكلمه الاحداث الذين نشأوا معه قائلين هكذا تقول للشعب الذين كلموك قائلين ان اباك ثقّل نيرنا واما انت فخفّف عنا هكذا تقول لهم ان خنصري اغلظ من متني ابي"));
arasuperobject.biblemap.put("b14_10_11", new arasuperobject(14,10,11,"والآن ابي حمّلكم نيرا ثقيلا وانا ازيد على نيركم. ابي ادّبكم بالسياط واما انا فبالعقارب"));
arasuperobject.biblemap.put("b14_10_12", new arasuperobject(14,10,12,"فجاء يربعام وجميع الشعب الى رحبعام في اليوم الثالث كما تكلم الملك قائلا ارجعوا اليّ في اليوم الثالث"));
arasuperobject.biblemap.put("b14_10_13", new arasuperobject(14,10,13,"فاجابهم الملك بقساوة وترك الملك رحبعام مشورة الشيوخ"));
arasuperobject.biblemap.put("b14_10_14", new arasuperobject(14,10,14,"وكلمهم حسب مشورة الاحداث قائلا. ابي ثقل نيركم وانا ازيد عليه. ابي ادّبكم بالسياط واما انا فبالعقارب"));
arasuperobject.biblemap.put("b14_10_15", new arasuperobject(14,10,15,"ولم يسمع الملك للشعب لان السبب كان من قبل الله لكي يقيم الرب كلامه الذي تكلم به عن يد اخيّا الشيلوني الى يربعام بن نباط"));
arasuperobject.biblemap.put("b14_10_16", new arasuperobject(14,10,16,"فلما رأى كل اسرائيل ان الملك لم يسمع لهم جاوب الشعب الملك قائلين اي قسم لنا في داود ولا نصيب لنا في ابن يسّى. كل واحد الى خيمته يا اسرائيل. الآن انظر الى بيتك يا داود. وذهب كل اسرائيل الى خيامهم"));
arasuperobject.biblemap.put("b14_10_17", new arasuperobject(14,10,17,"واما بنو اسرائيل الساكنون في مدن يهوذا فملك عليهم رحبعام"));
arasuperobject.biblemap.put("b14_10_18", new arasuperobject(14,10,18,"ثم ارسل الملك رحبعام هدورام الذي على التسخير فرجمه بنو اسرائيل بالحجارة فمات. فبادر الملك رحبعام وصعد الى المركبة ليهرب الى اورشليم"));
arasuperobject.biblemap.put("b14_10_19", new arasuperobject(14,10,19,"فعصى اسرائيل بيت داود الى هذا اليوم"));
arasuperobject.biblemap.put("b14_11_1", new arasuperobject(14,11,1,"ولما جاء رحبعام الى اورشليم جمع من بيت يهوذا وبنيامين مئة وثمانين الف مختار محارب ليحارب اسرائيل ليرد الملك الى رحبعام"));
arasuperobject.biblemap.put("b14_11_2", new arasuperobject(14,11,2,"وكان كلام الرب الى شمعيا رجل الله قائلا"));
arasuperobject.biblemap.put("b14_11_3", new arasuperobject(14,11,3,"كلم رحبعام بن سليمان ملك يهوذا وكل اسرائيل في يهوذا وبنيامين قائلا"));
arasuperobject.biblemap.put("b14_11_4", new arasuperobject(14,11,4,"هكذا قال الرب لا تصعدوا ولا تحاربوا اخوتكم. ارجعوا كل واحد الى بيته لانه من قبلي صار هذا الامر. فسمعوا لكلام الرب ورجعوا عن الذهاب ضد يربعام"));
arasuperobject.biblemap.put("b14_11_5", new arasuperobject(14,11,5,"واقام رحبعام في اورشليم وبنى مدنا للحصار في يهوذا"));
arasuperobject.biblemap.put("b14_11_6", new arasuperobject(14,11,6,"فبنى بيت لحم وعيطام وتقوع"));
arasuperobject.biblemap.put("b14_11_7", new arasuperobject(14,11,7,"وبيت صور وسوكو وعدلام"));
arasuperobject.biblemap.put("b14_11_8", new arasuperobject(14,11,8,"وجتّ ومريشة وزيف"));
arasuperobject.biblemap.put("b14_11_9", new arasuperobject(14,11,9,"وادورايم ولخيش وعزيقة"));
arasuperobject.biblemap.put("b14_11_10", new arasuperobject(14,11,10,"وصرعة وايلون وحبرون التي في يهوذا وبنيامين مدنا حصينة"));
arasuperobject.biblemap.put("b14_11_11", new arasuperobject(14,11,11,"وشدد الحصون وجعل فيها قوادا وخزائن مأكل وزيت وخمر"));
arasuperobject.biblemap.put("b14_11_12", new arasuperobject(14,11,12,"واتراسا في كل مدينة ورماحا وشددها كثيرا جدا وكان له يهوذا وبنيامين"));
arasuperobject.biblemap.put("b14_11_13", new arasuperobject(14,11,13,"والكهنة واللاويون الذين في كل اسرائيل مثلوا بين يديه من جميع تخومهم"));
arasuperobject.biblemap.put("b14_11_14", new arasuperobject(14,11,14,"لان اللاويين تركوا مسارحهم واملاكهم وانطلقوا الى يهوذا واورشليم لان يربعام وبنيه رفضوهم من ان يكهنوا للرب"));
arasuperobject.biblemap.put("b14_11_15", new arasuperobject(14,11,15,"واقام لنفسه كهنة للمرتفعات وللتيوس وللعجول التي عمل"));
arasuperobject.biblemap.put("b14_11_16", new arasuperobject(14,11,16,"وبعدهم جاء الى اورشليم من جميع اسباط اسرائيل الذين وجهوا قلوبهم الى طلب الرب اله اسرائيل ليذبحوا للرب اله آبائهم"));
arasuperobject.biblemap.put("b14_11_17", new arasuperobject(14,11,17,"وشددوا مملكة يهوذا وقووا رحبعام بن سليمان ثلاث سنين لانهم ساروا في طريق داود وسليمان ثلاث سنين"));
arasuperobject.biblemap.put("b14_11_18", new arasuperobject(14,11,18,"واتخذ رحبعام لنفسه امرأة محلة بنت يريموث بن داود وابيجايل بنت الياب بن يسّى"));
arasuperobject.biblemap.put("b14_11_19", new arasuperobject(14,11,19,"فولدت له بنين يعوش وشمريا وزاهم"));
arasuperobject.biblemap.put("b14_11_20", new arasuperobject(14,11,20,"ثم بعدها اخذ معكة بنت ابشالوم فولدت له ابيا وعتّاي وزيزا وشلوميث"));
arasuperobject.biblemap.put("b14_11_21", new arasuperobject(14,11,21,"واحب رحبعام معكة بنت ابشالوم اكثر من جميع نسائه وسراريه لانه اتخذ ثمانية عشر امرأة وستين سرية وولد ثمانية وعشرين ابنا وستين ابنة"));
arasuperobject.biblemap.put("b14_11_22", new arasuperobject(14,11,22,"واقام رحبعام ابيا ابن معكة راسا وقائدا بين اخوته لكي يملّكه"));
