arasuperobject.biblemap.put("b14_16_9", new arasuperobject(14,16,9,"لان عيني الرب تجولان في كل الارض ليتشدد مع الذين قلوبهم كاملة نحوه. فقد حمقت في هذا حتى انه من الآن تكون عليك حروب"));
arasuperobject.biblemap.put("b14_16_10", new arasuperobject(14,16,10,"فغضب آسا على الرائي ووضعه في السجن لانه اغتاظ منه من اجل هذا. وضايق آسا بعضا من الشعب في ذلك الوقت"));
arasuperobject.biblemap.put("b14_16_11", new arasuperobject(14,16,11,"وأمور آسا الاولى والاخيرة ها هي مكتوبة في سفر الملوك ليهوذا واسرائيل"));
arasuperobject.biblemap.put("b14_16_12", new arasuperobject(14,16,12,"ومرض آسا في السنة التاسعة والثلاثين من ملكه في رجليه حتى اشتد مرضه وفي مرضه ايضا لم يطلب الرب بل الاطباء"));
arasuperobject.biblemap.put("b14_16_13", new arasuperobject(14,16,13,"ثم اضطجع آسا مع آبائه ومات في السنة الحادية والاربعين لملكه"));
arasuperobject.biblemap.put("b14_16_14", new arasuperobject(14,16,14,"فدفنوه في قبوره التي حفرها لنفسه في مدينة داود واضجعوه في سرير كان مملوّا اطيابا واصنافا عطرة حسب صناعة العطارة واحرقوا له حريقة عظيمة جدا"));
arasuperobject.biblemap.put("b14_17_1", new arasuperobject(14,17,1,"وملك يهوشافاط ابنه عوضا عنه وتشدد على اسرائيل"));
arasuperobject.biblemap.put("b14_17_2", new arasuperobject(14,17,2,"وجعل جيشا في جميع مدن يهوذا الحصينة وجعل وكلاء في ارض يهوذا وفي مدن افرايم التي اخذها آسا ابوه"));
arasuperobject.biblemap.put("b14_17_3", new arasuperobject(14,17,3,"وكان الرب مع يهوشافاط لانه سار في طرق داود ابيه الاولى ولم يطلب البعليم"));
arasuperobject.biblemap.put("b14_17_4", new arasuperobject(14,17,4,"ولكنه طلب اله ابيه وسار في وصاياه لا حسب اعمال اسرائيل"));
arasuperobject.biblemap.put("b14_17_5", new arasuperobject(14,17,5,"فثبّت الرب المملكة في يده وقدم كل يهوذا هدايا ليهوشافاط وكان له غنى وكرامة بكثرة"));
arasuperobject.biblemap.put("b14_17_6", new arasuperobject(14,17,6,"وتقوى قلبه في طرق الرب ونزع ايضا المرتفعات والسواري من يهوذا"));
arasuperobject.biblemap.put("b14_17_7", new arasuperobject(14,17,7,"وفي السنة الثالثة لملكه ارسل الى رؤسائه الى بنحائل وعوبديا وزكريا ونثنيئل وميخايا ان يعلّموا في مدن يهوذا"));
arasuperobject.biblemap.put("b14_17_8", new arasuperobject(14,17,8,"ومعهم اللاويون شمعيا ونثنيا وزبديا وعسائيل وشميراموث ويهوناثان وادونيا وطوبيا وطوب ادونيا اللاويون ومعهم اليشمع ويهورام الكاهنان"));
arasuperobject.biblemap.put("b14_17_9", new arasuperobject(14,17,9,"فعلّموا في يهوذا ومعهم سفر شريعة الرب وجالوا في جميع مدن يهوذا وعلّموا الشعب"));
arasuperobject.biblemap.put("b14_17_10", new arasuperobject(14,17,10,"وكانت هيبة الرب على جميع ممالك الاراضي التي حول يهوذا فلم يحاربوا يهوشافاط"));
arasuperobject.biblemap.put("b14_17_11", new arasuperobject(14,17,11,"وبعض الفلسطينيين اتوا يهوشافاط بهدايا وحمل فضة والعربان ايضا أتوه بغنم من الكباش سبعة آلاف وسبع مئة ومن التيوس سبعة آلاف وسبع مئة"));
arasuperobject.biblemap.put("b14_17_12", new arasuperobject(14,17,12,"وكان يهوشافاط يتعظم جدا وبنى في يهوذا حصونا ومدن مخازن"));
arasuperobject.biblemap.put("b14_17_13", new arasuperobject(14,17,13,"وكان له شغل كثير في مدن يهوذا ورجال حرب جبابرة بأس في اورشليم"));
arasuperobject.biblemap.put("b14_17_14", new arasuperobject(14,17,14,"وهذا عددهم حسب بيوت آبائهم من يهوذا رؤساء الوف. عدنة الرئيس ومعه جبابرة بأس ثلاث مئة الف"));
arasuperobject.biblemap.put("b14_17_15", new arasuperobject(14,17,15,"وبجانبه يهوناثان الرئيس ومعه مئتان وثمانون الفا"));
arasuperobject.biblemap.put("b14_17_16", new arasuperobject(14,17,16,"وبجانبه عمسيا بن زكري المنتدب للرب ومعه مئتا الف جبار باس"));
arasuperobject.biblemap.put("b14_17_17", new arasuperobject(14,17,17,"ومن بنيامين الياداع جبار بأس ومعه من المتسلحين بالقسي والاتراس مئتا الف"));
arasuperobject.biblemap.put("b14_17_18", new arasuperobject(14,17,18,"وبجانبه يهوزاباد ومعه مئة وثمانون الفا متجردون للحرب"));
arasuperobject.biblemap.put("b14_17_19", new arasuperobject(14,17,19,"هؤلاء خدام الملك فضلا عن الذين جعلهم الملك في المدن الحصينة في كل يهوذا"));
arasuperobject.biblemap.put("b14_18_1", new arasuperobject(14,18,1,"وكان ليهوشافاط غنى وكرامة بكثرة. وصاهر اخآب"));
arasuperobject.biblemap.put("b14_18_2", new arasuperobject(14,18,2,"ونزل بعد سنين الى اخآب الى السامرة. فذبح اخآب غنما وبقرا بكثرة له وللشعب الذي معه واغواه ان يصعد الى راموت جلعاد"));
arasuperobject.biblemap.put("b14_18_3", new arasuperobject(14,18,3,"وقال اخآب ملك اسرائيل ليهوشافاط ملك يهوذا أتذهب معي الى راموت جلعاد. وقال له مثلي مثلك وشعبي كشعبك ومعك في القتال"));
arasuperobject.biblemap.put("b14_18_4", new arasuperobject(14,18,4,"ثم قال يهوشافاط لملك اسرائيل اسأل اليوم عن كلام الرب"));
arasuperobject.biblemap.put("b14_18_5", new arasuperobject(14,18,5,"فجمع ملك اسرائيل الانبياء اربع مئة رجل وقال لهم أنذهب الى راموت جلعاد للقتال ام امتنع. فقالوا اصعد فيدفعها الله ليد الملك"));
arasuperobject.biblemap.put("b14_18_6", new arasuperobject(14,18,6,"فقال يهوشافاط أليس هنا ايضا نبي للرب فنسأل منه"));
arasuperobject.biblemap.put("b14_18_7", new arasuperobject(14,18,7,"فقال ملك اسرائيل ليهوشافاط بعد رجل واحد لسؤال الرب به ولكنني ابغضه لانه لا يتنبأ علي خيرا بل شرا كل ايامه. وهو ميخا بن يملة. فقال يهوشافاط لا يقل الملك هكذا"));
arasuperobject.biblemap.put("b14_18_8", new arasuperobject(14,18,8,"فدعا ملك اسرائيل خصيا وقال اسرع بميخا بن يملة"));
arasuperobject.biblemap.put("b14_18_9", new arasuperobject(14,18,9,"وكان ملك اسرائيل ويهوشافاط ملك يهوذا جالسين كل واحد على كرسيه لابسين ثيابهما وجالسين في ساحة عند مدخل باب السامرة وجميع الانبياء يتنبأون امامهما"));
arasuperobject.biblemap.put("b14_18_10", new arasuperobject(14,18,10,"وعمل صدقيا بن كنعنة لنفسه قرون حديد وقال هكذا قال الرب بهذه تنطح الاراميين حتى يفنوا"));
arasuperobject.biblemap.put("b14_18_11", new arasuperobject(14,18,11,"وتنبأ جميع الانبياء هكذا قائلين اصعد الى راموت جلعاد وافلح فيدفعها الرب ليد الملك"));
arasuperobject.biblemap.put("b14_18_12", new arasuperobject(14,18,12,"واما الرسول الذي ذهب ليدعو ميخا فكلّمه قائلا. هوذا كلام جميع الانبياء بفم واحد خير للملك. فليكن كلامك كواحد منهم وتكلم بخير"));
arasuperobject.biblemap.put("b14_18_13", new arasuperobject(14,18,13,"فقال ميخا حيّ هو الرب ان ما يقوله الهي فبه اتكلم"));
arasuperobject.biblemap.put("b14_18_14", new arasuperobject(14,18,14,"ولما جاء الى الملك قال له الملك يا ميخا أنذهب الى راموت جلعاد للقتال ام امتنع. فقال اصعدوا وافلحوا فيدفعوا ليدكم"));
arasuperobject.biblemap.put("b14_18_15", new arasuperobject(14,18,15,"فقال له الملك كم مرة استحلفك ان لا تقول لي الا الحق باسم الرب"));
arasuperobject.biblemap.put("b14_18_16", new arasuperobject(14,18,16,"فقال رأيت كل اسرائيل مشتّتين على الجبال كخراف لا راعي لها. فقال الرب ليس لهؤلاء اصحاب فليرجعوا كل واحد الى بيته بسلام"));
arasuperobject.biblemap.put("b14_18_17", new arasuperobject(14,18,17,"فقال ملك اسرائيل ليهوشافاط أما قلت لك انه لا يتنبأ عليّ خيرا بل شرا"));
arasuperobject.biblemap.put("b14_18_18", new arasuperobject(14,18,18,"وقال فاسمع اذا كلام الرب. قد رأيت الرب جالسا على كرسيه وكل جند السماء وقوف عن يمينه وعن يساره"));
arasuperobject.biblemap.put("b14_18_19", new arasuperobject(14,18,19,"فقال الرب من يغوي اخآب ملك اسرائيل فيصعد ويسقط في راموت جلعاد. فقال هذا هكذا وقال ذاك هكذا"));
arasuperobject.biblemap.put("b14_18_20", new arasuperobject(14,18,20,"ثم خرج الروح ووقف امام الرب وقال انا اغويه. فقال له الرب بماذا"));
arasuperobject.biblemap.put("b14_18_21", new arasuperobject(14,18,21,"فقال اخرج واكون لروح كذب في افواه جميع انبيائه. فقال انك تغويه وتقتدر. فاخرج وافعل هكذا"));
arasuperobject.biblemap.put("b14_18_22", new arasuperobject(14,18,22,"والآن هوذا قد جعل الرب روح كذب في افواه انبيائك هؤلاء والرب تكلم عليك بشر"));
arasuperobject.biblemap.put("b14_18_23", new arasuperobject(14,18,23,"فتقدم صدقيا بن كنعنة وضرب ميخا على الفك وقال من اي طريق عبر روح الرب مني ليكلمك"));
arasuperobject.biblemap.put("b14_18_24", new arasuperobject(14,18,24,"فقال ميخا انك سترى في ذلك اليوم الذي تدخل فيه من مخدع الى مخدع لتختبئ"));
arasuperobject.biblemap.put("b14_18_25", new arasuperobject(14,18,25,"فقال ملك اسرائيل خذوا ميخا وردّوه الى امون رئيس المدينة والى يوآش ابن الملك"));
arasuperobject.biblemap.put("b14_18_26", new arasuperobject(14,18,26,"وقولوا هكذا يقول الملك ضعوا هذا في السجن واطعموه خبز الضيق وماء الضيق حتى ارجع بسلام"));
arasuperobject.biblemap.put("b14_18_27", new arasuperobject(14,18,27,"فقال ميخا ان رجعت رجوعا بسلام فلم يتكلم الرب بي. وقال اسمعوا ايها الشعوب اجمعون"));
arasuperobject.biblemap.put("b14_18_28", new arasuperobject(14,18,28,"فصعد ملك اسرائيل ويهوشافاط ملك يهوذا الى راموت جلعاد"));
arasuperobject.biblemap.put("b14_18_29", new arasuperobject(14,18,29,"وقال ملك اسرائيل ليهوشافاط اني اتنكر وادخل الحرب. واما انت فالبس ثيابك. فتنكّر ملك اسرائيل ودخلا الحرب"));
arasuperobject.biblemap.put("b14_18_30", new arasuperobject(14,18,30,"وأمر ملك ارام رؤساء المركبات التي له قائلا لا تحاربوا صغيرا ولا كبيرا الا ملك اسرائيل وحده"));
arasuperobject.biblemap.put("b14_18_31", new arasuperobject(14,18,31,"فلما رأى رؤساء المركبات يهوشافاط قالوا انه ملك اسرائيل فحاوطوه للقتال فصرخ يهوشافاط وساعده الرب وحوّلهم الله عنه"));
arasuperobject.biblemap.put("b14_18_32", new arasuperobject(14,18,32,"فلما رأى رؤساء المركبات انه ليس ملك اسرائيل رجعوا عنه"));
arasuperobject.biblemap.put("b14_18_33", new arasuperobject(14,18,33,"وان رجلا نزع في قوسه غير متعمد وضرب ملك اسرائيل بين اوصال الدرع فقال لمدير المركبة رد يدك واخرجني من الجيش لاني قد جرحت"));
arasuperobject.biblemap.put("b14_18_34", new arasuperobject(14,18,34,"واشتدّ القتال في ذلك اليوم وأوقف ملك اسرائيل في المركبة مقابل ارام الى المساء ومات عند غروب الشمس"));
arasuperobject.biblemap.put("b14_19_1", new arasuperobject(14,19,1,"ورجع يهوشافاط ملك يهوذا الى بيته بسلام الى اورشليم"));
arasuperobject.biblemap.put("b14_19_2", new arasuperobject(14,19,2,"وخرج للقائه ياهو بن حناني الرائي وقال للملك يهوشافاط أتساعد الشرير وتحب مبغضي الرب. فلذلك الغضب عليك من قبل الرب"));
arasuperobject.biblemap.put("b14_19_3", new arasuperobject(14,19,3,"غير انه وجد فيك أمور صالحة لانك نزعت السواري من الارض وهيّأت قلبك لطلب الله"));
arasuperobject.biblemap.put("b14_19_4", new arasuperobject(14,19,4,"واقام يهوشافاط في اورشليم ثم رجع وخرج ايضا بين الشعب من بئر سبع الى جبل افرايم وردّهم الى الرب اله آبائهم"));
arasuperobject.biblemap.put("b14_19_5", new arasuperobject(14,19,5,"واقام قضاة في الارض في كل مدن يهوذا المحصّنة في كل مدينة فمدينة"));
arasuperobject.biblemap.put("b14_19_6", new arasuperobject(14,19,6,"وقال للقضاة انظروا ما انتم فاعلون لانكم لا تقضون للانسان بل للرب وهو معكم في امر القضاء"));
arasuperobject.biblemap.put("b14_19_7", new arasuperobject(14,19,7,"والآن لتكن هيبة الرب عليكم. احذروا وافعلوا. لانه ليس عند الرب الهنا ظلم ولا محاباة ولا ارتشاء"));
arasuperobject.biblemap.put("b14_19_8", new arasuperobject(14,19,8,"وكذا في اورشليم اقام يهوشافاط من اللاويين والكهنة ومن رؤوس آباء اسرائيل لقضاء الرب والدعاوي. ورجعوا الى اورشليم"));
arasuperobject.biblemap.put("b14_19_9", new arasuperobject(14,19,9,"وأمرهم قائلا هكذا تفعلون بتقوى الرب بامانة وقلب كامل"));
arasuperobject.biblemap.put("b14_19_10", new arasuperobject(14,19,10,"وفي كل دعوى تاتي اليكم من اخوتكم الساكنين في مدنهم بين دم ودم بين شريعة ووصية من جهة فرائض او احكام حذّروهم فلا ياثموا الى الرب فيكون غضب عليكم وعلى اخوتكم. هكذا افعلوا فلا تاثموا"));
arasuperobject.biblemap.put("b14_19_11", new arasuperobject(14,19,11,"وهوذا امريا الكاهن الراس عليكم في كل امور الرب وزبديا بن يشمعئيل الرئيس على بيت يهوذا في كل امور الملك والعرفاء اللاويين امامكم. تشددوا وافعلوا وليكن الرب مع الصالح"));
arasuperobject.biblemap.put("b14_20_1", new arasuperobject(14,20,1,"ثم بعد ذلك اتى بنو موآب وبنو عمون ومعهم العمونيون على يهوشافاط للمحاربة"));
arasuperobject.biblemap.put("b14_20_2", new arasuperobject(14,20,2,"فجاء اناس واخبروا يهوشافاط قائلين قد جاء عليك جمهور كثير من عبر البحر من ارام وها هم في حصون تامار. هي عين جدي"));
arasuperobject.biblemap.put("b14_20_3", new arasuperobject(14,20,3,"فخاف يهوشافاط وجعل وجهه ليطلب الرب ونادى بصوم في كل يهوذا"));
arasuperobject.biblemap.put("b14_20_4", new arasuperobject(14,20,4,"واجتمع يهوذا ليسألوا الرب. جاءوا ايضا من كل مدن يهوذا ليسألوا الرب"));
arasuperobject.biblemap.put("b14_20_5", new arasuperobject(14,20,5,"فوقف يهوشافاط في جماعة يهوذا واورشليم في بيت الرب امام الدار الجديدة"));
arasuperobject.biblemap.put("b14_20_6", new arasuperobject(14,20,6,"وقال. يا رب اله آبائنا أما انت هو الله في السماء وانت المتسلط على جميع ممالك الامم وبيدك قوة وجبروت وليس من يقف معك"));
arasuperobject.biblemap.put("b14_20_7", new arasuperobject(14,20,7,"ألست انت الهنا الذي طردت سكان هذه الارض من امام شعبك اسرائيل واعطيتها لنسل ابراهيم خليلك الى الابد"));
arasuperobject.biblemap.put("b14_20_8", new arasuperobject(14,20,8,"فسكنوا فيها وبنو لك فيها مقدسا لاسمك قائلين"));
arasuperobject.biblemap.put("b14_20_9", new arasuperobject(14,20,9,"اذا جاء علينا شر سيف قضاء او وبأ او جوع ووقفنا امام هذا البيت وامامك لان اسمك في هذا البيت وصرخنا اليك من ضيقنا فانك تسمع وتخلص"));
arasuperobject.biblemap.put("b14_20_10", new arasuperobject(14,20,10,"والآن هوذا بنو عمون وموآب وجبل ساعير الذين لم تدع اسرائيل يدخلون اليهم حين جاءوا من ارض مصر بل مالوا عنهم ولم يهلكوهم"));
arasuperobject.biblemap.put("b14_20_11", new arasuperobject(14,20,11,"فهوذا هم يكافئوننا بمجيئهم لطردنا من ملكك الذي ملكتنا اياه"));
arasuperobject.biblemap.put("b14_20_12", new arasuperobject(14,20,12,"يا الهنا أما تقضي عليهم لانه ليس فينا قوة امام هذا الجمهور الكثير الآتي علينا ونحن لا نعلم ماذا نعمل ولكن نحوك اعيننا"));
arasuperobject.biblemap.put("b14_20_13", new arasuperobject(14,20,13,"وكان كل يهوذا واقفين امام الرب مع اطفالهم ونسائهم وبنيهم"));
arasuperobject.biblemap.put("b14_20_14", new arasuperobject(14,20,14,"وان يحزيئيل بن زكريا بن بنايا بن يعيئيل بن متّنيا اللاوي من بني آساف كان عليه روح الرب في وسط الجماعة"));
arasuperobject.biblemap.put("b14_20_15", new arasuperobject(14,20,15,"فقال اصغوا يا جميع يهوذا وسكان اورشليم وايها الملك يهوشافاط. هكذا قال الرب لكم لا تخافوا ولا ترتاعوا بسبب هذا الجمهور الكثير لان الحرب ليست لكم بل لله"));
arasuperobject.biblemap.put("b14_20_16", new arasuperobject(14,20,16,"غدا انزلوا عليهم. هوذا هم صاعدون في عقبة صيص فتجدوهم في اقصى الوادي امام برية يروئيل"));
arasuperobject.biblemap.put("b14_20_17", new arasuperobject(14,20,17,"ليس عليكم ان تحاربوا في هذه. قفوا اثبتوا وانظروا خلاص الرب معكم يا يهوذا واورشليم لا تخافوا ولا ترتاعوا. غدا اخرجوا للقائهم والرب معكم"));
arasuperobject.biblemap.put("b14_20_18", new arasuperobject(14,20,18,"فخرّ يهوشافاط لوجهه على الارض وكل يهوذا وسكان اورشليم سقطوا امام الرب سجودا للرب"));
arasuperobject.biblemap.put("b14_20_19", new arasuperobject(14,20,19,"فقام اللاويون من بني القهاتيين ومن بني القورحيين ليسبحوا الرب اله اسرائيل بصوت عظيم جدا"));
arasuperobject.biblemap.put("b14_20_20", new arasuperobject(14,20,20,"وبكّروا صباحا وخرجوا الى برية تقوع وعند خروجهم وقف يهوشافاط وقال اسمعوا يا يهوذا وسكان اورشليم آمنوا بالرب الهكم فتأمنوا. آمنوا بانبيائه فتفلحوا"));
arasuperobject.biblemap.put("b14_20_21", new arasuperobject(14,20,21,"ولما استشار الشعب اقام مغنين للرب ومسبحين في زينة مقدسة عند خروجهم امام المتجردين وقائلين احمدوا الرب لان الى الابد رحمته"));
arasuperobject.biblemap.put("b14_20_22", new arasuperobject(14,20,22,"ولما ابتدأوا في الغناء والتسبيح جعل الرب اكمنة على بني عمون وموآب وجبل ساعير الآتين على يهوذا فانكسروا"));
arasuperobject.biblemap.put("b14_20_23", new arasuperobject(14,20,23,"وقام بنو عمون وموآب على سكان جبل ساعير ليحرّموهم ويهلكوهم. ولما فرغوا من سكان ساعير ساعد بعضهم على اهلاك بعض"));
arasuperobject.biblemap.put("b14_20_24", new arasuperobject(14,20,24,"ولما جاء يهوذا الى المرقب في البرية تطلعوا نحو الجمهور واذا هم جثث ساقطة على الارض ولم ينفلت احد"));
arasuperobject.biblemap.put("b14_20_25", new arasuperobject(14,20,25,"فأتى يهوشافاط وشعبه لنهب اموالهم فوجدوا بينهم اموالا وجثثا وامتعة ثمينة بكثرة فاخذوها لانفسهم حتى لم يقدروا ان يحملوها وكانوا ثلاثة ايام ينهبون الغنيمة لانها كانت كثيرة"));
arasuperobject.biblemap.put("b14_20_26", new arasuperobject(14,20,26,"وفي اليوم الرابع اجتمعوا في وادي بركة لانهم هناك باركوا الرب لذلك دعوا اسم ذلك المكان وادي بركة الى اليوم"));
arasuperobject.biblemap.put("b14_20_27", new arasuperobject(14,20,27,"ثم ارتد كل رجال يهوذا واورشليم ويهوشافاط براسهم ليرجعوا الى اورشليم بفرح لان الرب فرحهم على اعدائهم"));
arasuperobject.biblemap.put("b14_20_28", new arasuperobject(14,20,28,"ودخلوا اورشليم بالرباب والعيدان والابواق الى بيت الرب"));
arasuperobject.biblemap.put("b14_20_29", new arasuperobject(14,20,29,"وكانت هيبة الله على كل ممالك الاراضي حين سمعوا ان الرب حارب اعداء اسرائيل"));
arasuperobject.biblemap.put("b14_20_30", new arasuperobject(14,20,30,"واستراحت مملكة يهوشافاط واراحه الهه من كل جهة"));
arasuperobject.biblemap.put("b14_20_31", new arasuperobject(14,20,31,"وملك يهوشافاط على يهوذا. كان ابن خمس وثلاثين سنة حين ملك وملك خمس وعشرين سنة في اورشليم واسم امه عزوبة بنت شلحي"));
arasuperobject.biblemap.put("b14_20_32", new arasuperobject(14,20,32,"وسار في طريق ابيه آسا ولم يحد عنها اذ عمل المستقيم في عيني الرب"));
arasuperobject.biblemap.put("b14_20_33", new arasuperobject(14,20,33,"الا ان المرتفعات لم تنتزع بل كان الشعب لم يعدّوا بعد قلوبهم لإله آبائهم"));
arasuperobject.biblemap.put("b14_20_34", new arasuperobject(14,20,34,"وبقية امور يهوشافاط الاولى والاخيرة ها هي مكتوبة في اخبار ياهو بن حناني المذكور في سفر ملوك اسرائيل"));
arasuperobject.biblemap.put("b14_20_35", new arasuperobject(14,20,35,"ثم بعد ذلك اتحد يهوشافاط ملك يهوذا مع اخزيا ملك اسرائيل الذي اساء في عمله"));
arasuperobject.biblemap.put("b14_20_36", new arasuperobject(14,20,36,"فاتحد معه في عمل سفن تسير الى ترشيش فعملا السفن في عصيون جابر"));
arasuperobject.biblemap.put("b14_20_37", new arasuperobject(14,20,37,"وتنبأ اليعزر بن دوداواهو من مريشة على يهوشافاط قائلا لانك اتحدت مع اخزيا قد اقتحم الرب اعمالك. فتكسرت السفن ولم تستطع السير الى ترشيش"));
arasuperobject.biblemap.put("b14_21_1", new arasuperobject(14,21,1,"واضطجع يهوشافاط مع آبائه فدفن مع آبائه في مدينة داود وملك يهورام ابنه عوضا عنه"));
arasuperobject.biblemap.put("b14_21_2", new arasuperobject(14,21,2,"وكان له اخوة بنو يهوشافاط عزريا ويحيئيل وزكريا وعزرياهو وميخائيل وشفطيا كل هؤلاء بنو يهوشافاط ملك اسرائيل"));
arasuperobject.biblemap.put("b14_21_3", new arasuperobject(14,21,3,"واعطاهم ابوهم عطايا كثيرة من فضة وذهب وتحف مع مدن حصينة في يهوذا. واما المملكة فاعطاها ليهورام لانه البكر"));
arasuperobject.biblemap.put("b14_21_4", new arasuperobject(14,21,4,"فقام يهورام على مملكة ابيه وتشدد وقتل جميع اخوته بالسيف وايضا بعضا من رؤساء اسرائيل"));
arasuperobject.biblemap.put("b14_21_5", new arasuperobject(14,21,5,"كان يهورام ابن اثنتين وثلاثين سنة حين ملك وملك ثماني سنين في اورشليم"));
arasuperobject.biblemap.put("b14_21_6", new arasuperobject(14,21,6,"وسار في طريق ملوك اسرائيل كما فعل بيت اخآب لان بنت اخآب كانت له امرأة. وعمل الشر في عيني الرب"));
arasuperobject.biblemap.put("b14_21_7", new arasuperobject(14,21,7,"ولم يشإ الرب ان يبيد بيت داود لاجل العهد الذي قطعه مع داود ولانه قال انه يعطيه وبنيه سراجا كل الايام"));
arasuperobject.biblemap.put("b14_21_8", new arasuperobject(14,21,8,"في ايامه عصى ادوم من تحت يد يهوذا وملكوا على انفسهم ملكا"));
arasuperobject.biblemap.put("b14_21_9", new arasuperobject(14,21,9,"وعبر يهورام مع رؤسائه وجميع المركبات معه وقام ليلا وضرب ادوم المحيط به ورؤساء المركبات"));
arasuperobject.biblemap.put("b14_21_10", new arasuperobject(14,21,10,"فعصى ادوم من تحت يد يهوذا الى هذا اليوم. حينئذ عصت لبنة في ذلك الوقت من تحت يده لانه ترك الرب اله آبائه"));
arasuperobject.biblemap.put("b14_21_11", new arasuperobject(14,21,11,"وهو ايضا عمل مرتفعات في جبال يهوذا وجعل سكان اورشليم يزنون وطوّح يهوذا"));
arasuperobject.biblemap.put("b14_21_12", new arasuperobject(14,21,12,"وأتت كتابة من ايليا النبي تقول. هكذا قال الرب اله داود ابيك من اجل انك لم تسلك في طرق يهوشافاط ابيك وطرق آسا ملك يهوذا"));
arasuperobject.biblemap.put("b14_21_13", new arasuperobject(14,21,13,"بل سلكت في طرق ملوك اسرائيل وجعلت يهوذا وسكان اورشليم يزنون كزنى بيت اخآب وقتلت ايضا اخوتك من بيت ابيك الذين هم افضل منك"));
arasuperobject.biblemap.put("b14_21_14", new arasuperobject(14,21,14,"هوذا يضرب الرب شعبك وبنيك ونسائك وكل مالك ضربة عظيمة"));
arasuperobject.biblemap.put("b14_21_15", new arasuperobject(14,21,15,"واياك بامراض كثيرة بداء امعائك حتى تخرج امعاؤك بسبب المرض يوما فيوم"));
arasuperobject.biblemap.put("b14_21_16", new arasuperobject(14,21,16,"واهاج الرب على يهورام روح الفلسطينيين والعرب الذين بجانب الكوشيين"));
arasuperobject.biblemap.put("b14_21_17", new arasuperobject(14,21,17,"فصعدوا الى يهوذا وافتتحوها وسبوا كل الاموال الموجودة في بيت الملك مع بنيه ونسائه ايضا ولم يبقى له ابن الا يهوآحاز اصغر بنيه"));
arasuperobject.biblemap.put("b14_21_18", new arasuperobject(14,21,18,"وبعد هذا كله ضربه الرب في امعائه بمرض ليس له شفاء"));
arasuperobject.biblemap.put("b14_21_19", new arasuperobject(14,21,19,"وكان من يوم الى يوم وحسب ذهاب المدة عند نهاية سنتين ان امعائه خرجت بسبب مرضه فمات بامراض ردية ولم يعمل له شعبه حريقة كحريقة آبائه"));
arasuperobject.biblemap.put("b14_21_20", new arasuperobject(14,21,20,"كان ابن اثنتين وثلاثين سنة حين ملك وملك ثماني سنين في اورشليم وذهب غير مأسوف عليه ودفنوه في مدينة داود ولكن ليس في قبور الملوك"));
arasuperobject.biblemap.put("b14_22_1", new arasuperobject(14,22,1,"وملك سكان اورشليم اخزيا ابنه الاصغر عوضا عنه لان جميع الاولين قتلهم الغزاة الذين جاءوا مع العرب الى المحلّة. فملك اخزيا بن يهورام ملك يهوذا"));
arasuperobject.biblemap.put("b14_22_2", new arasuperobject(14,22,2,"كان اخزيا ابن اثنتين واربعين سنة حين ملك وملك سنة واحدة في اورشليم واسم امه عثليا بنت عمري"));
arasuperobject.biblemap.put("b14_22_3", new arasuperobject(14,22,3,"وهو ايضا سلك في طرق بيت اخآب لان امه كانت تشير عليه بفعل الشر"));
arasuperobject.biblemap.put("b14_22_4", new arasuperobject(14,22,4,"فعمل الشر في عيني الرب مثل بيت اخآب لانهم كانوا له مشيرين بعد وفاة ابيه لابادته"));
arasuperobject.biblemap.put("b14_22_5", new arasuperobject(14,22,5,"فسلك بمشورتهم وذهب مع يهورام بن اخآب ملك اسرائيل لمحاربة حزائيل ملك ارام في راموت جلعاد. وضرب الاراميون يورام"));
arasuperobject.biblemap.put("b14_22_6", new arasuperobject(14,22,6,"فرجع ليبرأ في يزرعيل بسبب الضربات التي ضربوه اياها في الرامة عند محاربته حزائيل ملك ارام. ونزل عزريا بن يهورام ملك يهوذا لعيادة يهورام بن اخآب في يزرعيل لانه كان مريضا"));
arasuperobject.biblemap.put("b14_22_7", new arasuperobject(14,22,7,"فمن قبل الله كان هلاك اخزيا بمجيئه الى يورام. فانه حين جاء خرج مع يهورام الى ياهو بن نمشي الذي مسحه الرب لقطع بيت اخآب"));
arasuperobject.biblemap.put("b14_22_8", new arasuperobject(14,22,8,"واذ كان ياهو يقضي على بيت اخآب وجد رؤساء يهوذا وبني اخوة اخزيا اللذن كانوا يخدمون اخزيا فقتلهم"));
arasuperobject.biblemap.put("b14_22_9", new arasuperobject(14,22,9,"وطلب اخزيا فامسكوه وهو مختبئ في السامرة وأتوا به الى ياهو وقتلوه ودفنوه لانهم قالوا انه ابن يهوشافاط الذي طلب الرب بكل قلبه. فلم يكن لبيت اخزيا من يقوى على المملكة"));
arasuperobject.biblemap.put("b14_22_10", new arasuperobject(14,22,10,"ولما رأت عثليا ام اخزيا ان ابنها قد مات قامت وابادت جميع النسل الملكي من بيت يهوذا"));
arasuperobject.biblemap.put("b14_22_11", new arasuperobject(14,22,11,"اما يهوشبعه بنت الملك فاخذت يواش بن اخزيا وسرقته من وسط بني الملك الذين قتلوا وجعلته هو ومرضعته في مخدع السرير وخبأته يهوشبعه بنت الملك يهورام امرأة يهوياداع الكاهن. لانها كانت اخت اخزيا. من وجه عثليا فلم تقتله"));
arasuperobject.biblemap.put("b14_22_12", new arasuperobject(14,22,12,"وكان معهم في بيت الله مختبئا ست سنين وعثليا مالكة على الارض"));
arasuperobject.biblemap.put("b14_23_1", new arasuperobject(14,23,1,"وفي السنة السابعة تشدد يهوياداع واخذ معه في العهد رؤساء المئات عزريا بن يروحام واسماعيل بن يهوحانان وعزريا بن عوبيد ومعسيا بن عدايا واليشافاط بن زكري"));
arasuperobject.biblemap.put("b14_23_2", new arasuperobject(14,23,2,"وجالوا في يهوذا وجمعوا اللاويين من جميع مدن يهوذا ورؤوس آباء اسرائيل وجاءوا الى اورشليم"));
arasuperobject.biblemap.put("b14_23_3", new arasuperobject(14,23,3,"وقطع كل المجمع عهدا في بيت الله مع الملك وقال لهم هوذا ابن الملك يملك كما تكلم الرب عن بني داود"));
arasuperobject.biblemap.put("b14_23_4", new arasuperobject(14,23,4,"هذا هو الأمر الذي تعملونه. الثلث منكم الذين يدخلون في السبت من الكهنة واللاويين يكونون بوابين للابواب"));
arasuperobject.biblemap.put("b14_23_5", new arasuperobject(14,23,5,"والثلث في بيت الملك والثلث في باب الاساس وجميع الشعب في ديار بيت الرب"));
arasuperobject.biblemap.put("b14_23_6", new arasuperobject(14,23,6,"ولا يدخل بيت الرب الا الكهنة والذين يخدمون من اللاويين فهم يدخلون لانهم مقدسون وكل الشعب يحرسون حراسة الرب"));
arasuperobject.biblemap.put("b14_23_7", new arasuperobject(14,23,7,"ويحيط اللاويون بالملك مستديرين كل واحد سلاحه بيده والذي يدخل البيت يقتل وكونوا مع الملك في دخوله وفي خروجه"));
arasuperobject.biblemap.put("b14_23_8", new arasuperobject(14,23,8,"فعمل اللاويون وكل يهوذا حسب كل ما أمر به يهوياداع الكاهن. واخذوا كل واحد رجاله الداخلين في السبت مع الخارجين في السبت لان يهوياداع الكاهن لم يصرف الفرق"));
arasuperobject.biblemap.put("b14_23_9", new arasuperobject(14,23,9,"واعطى يهوياداع الكاهن رؤساء المئات الحراب والمجان والاتراس التي للملك داود التي في بيت الله"));
arasuperobject.biblemap.put("b14_23_10", new arasuperobject(14,23,10,"واوقف جميع الشعب وكل واحد سلاحه بيده من جانب البيت الايمن الى جانب البيت الايسر حول المذبح والبيت حول الملك مستديرين"));
arasuperobject.biblemap.put("b14_23_11", new arasuperobject(14,23,11,"ثم اخرجوا ابن الملك ووضعوا عليه التاج واعطوه الشهادة وملّكوه ومسحه يهوياداع وبنوه وقالوا ليحيا الملك"));
arasuperobject.biblemap.put("b14_23_12", new arasuperobject(14,23,12,"ولما سمعت عثليا صوت الشعب يركضون ويمدحون الملك دخلت الى الشعب في بيت الرب"));
arasuperobject.biblemap.put("b14_23_13", new arasuperobject(14,23,13,"ونظرت واذا الملك واقف على منبره في المدخل والرؤساء والابواق عند الملك وكل شعب الارض يفرحون وينفخون بالابواق والمغنون بآلات الغناء والمعلمون التسبيح فشقّت عثليا ثيابها وقالت خيانة خيانة"));
arasuperobject.biblemap.put("b14_23_14", new arasuperobject(14,23,14,"فاخرج يهوياداع الكاهن رؤساء المئات الموكلين على الجيش وقال لهم اخرجوها الى خارج الصفوف والذي يتبعها يقتل بالسيف. لان الكاهن قال لا تقتلوها في بيت الرب"));
arasuperobject.biblemap.put("b14_23_15", new arasuperobject(14,23,15,"فالقوا عليها الايادي ولما أتت الى مدخل باب الخيل الى بيت الملك قتلوها هناك"));
arasuperobject.biblemap.put("b14_23_16", new arasuperobject(14,23,16,"فقطع يهوياداع عهدا بينه وبين كل الشعب وبين الملك ان يكونوا شعبا للرب"));
arasuperobject.biblemap.put("b14_23_17", new arasuperobject(14,23,17,"ودخل جميع الشعب الى بيت البعل وهدموه وكسروا مذابحه وتماثيله وقتلوا متّان كاهن البعل امام المذبح"));
arasuperobject.biblemap.put("b14_23_18", new arasuperobject(14,23,18,"وجعل يهوياداع مناظرين على بيت الرب عن يد الكهنة اللاويين الذين قسمهم داود على بيت الرب لاجل اصعاد محرقات الرب كما هو مكتوب في شريعة موسى بالفرح والغناء حسب امر داود"));
arasuperobject.biblemap.put("b14_23_19", new arasuperobject(14,23,19,"واوقف البوابين على ابواب بيت الرب لئلا يدخل نجسا في أمر ما"));
arasuperobject.biblemap.put("b14_23_20", new arasuperobject(14,23,20,"واخذ رؤساء المئات والعظماء والمتسلطين على الشعب وكل شعب الارض وانزل الملك من بيت الرب ودخلوا من وسط الباب الاعلى الى بيت الملك واجلسوا الملك على كرسي المملكة"));
arasuperobject.biblemap.put("b14_23_21", new arasuperobject(14,23,21,"ففرح كل شعب الارض واستراحت المدينة وقتلوا عثليا بالسيف"));
arasuperobject.biblemap.put("b14_24_1", new arasuperobject(14,24,1,"كان يوآش ابن سبع سنين حين ملك وملك اربعين سنة في اورشليم واسم امه ظبية من بئر سبع"));
arasuperobject.biblemap.put("b14_24_2", new arasuperobject(14,24,2,"وعمل يوآش المستقيم في عيني الرب كل ايام يهوياداع الكاهن"));
arasuperobject.biblemap.put("b14_24_3", new arasuperobject(14,24,3,"واتخذ يهوياداع له امرأتين فولد بنين وبنات"));
arasuperobject.biblemap.put("b14_24_4", new arasuperobject(14,24,4,"وحدث بعد ذلك انه كان في قلب يوآش ان يجدد بيت الرب"));
arasuperobject.biblemap.put("b14_24_5", new arasuperobject(14,24,5,"فجمع الكهنة واللاويون وقال لهم. اخرجوا الى مدن يهوذا واجمعوا من جميع اسرائيل فضة لاجل ترميم بيت الهكم من سنة الى سنة وبادروا انتم الى هذا الأمر. فلم يبادر اللاويون"));
arasuperobject.biblemap.put("b14_24_6", new arasuperobject(14,24,6,"فدعا الملك يهوياداع الراس وقال له لماذا لم تطلب من اللاويين ان ياتوا من يهوذا واورشليم بجزية موسى عبد الرب وجماعة اسرائيل لخيمة الشهادة"));
arasuperobject.biblemap.put("b14_24_7", new arasuperobject(14,24,7,"لان بني عثليا الخبيثة قد هدموا بيت الله وصيّروا كل اقداس بيت الرب للبعليم"));
arasuperobject.biblemap.put("b14_24_8", new arasuperobject(14,24,8,"وأمر الملك فعملوا صندوقا وجعلوه في باب بيت الرب خارجا"));
arasuperobject.biblemap.put("b14_24_9", new arasuperobject(14,24,9,"ونادوا في يهوذا واورشليم بان يأتوا الى الرب بجزية موسى عبد الرب المفروضة على اسرائيل في البرية"));
arasuperobject.biblemap.put("b14_24_10", new arasuperobject(14,24,10,"ففرح كل الرؤساء وكل الشعب وادخلوا والقوا في الصندوق حتى امتلأ"));
arasuperobject.biblemap.put("b14_24_11", new arasuperobject(14,24,11,"وحينما كان يؤتى بالصندوق الى وكالة الملك ليد اللاويين عندما يرون ان الفضة قد كثرت كان ياتي كاتب الملك ووكيل الكاهن الراس ويفرغان الصندوق ثم يحملانه ويردانه الى مكانه هكذا كانوا يفعلون يوما فيوم حتى جمعوا فضة بكثرة"));
arasuperobject.biblemap.put("b14_24_12", new arasuperobject(14,24,12,"ودفعها الملك ويهوياداع لعاملي شغل خدمة بيت الرب وكانوا يستأجرون نحاتين ونجارين لتجديد بيت الرب وللعاملين في الحديد والنحاس ايضا لترميم بيت الرب"));
arasuperobject.biblemap.put("b14_24_13", new arasuperobject(14,24,13,"فعمل عاملوا الشغل ونجح العمل بايديهم واقاموا بيت الله على رسمه وثبتوه"));
arasuperobject.biblemap.put("b14_24_14", new arasuperobject(14,24,14,"ولما اكملوا أتوا الى ما بين يدي الملك ويهوياداع ببقية الفضة وعملوها آنية لبيت الرب آنية خدمة واصعاد وصحونا وآنية ذهب وفضة. وكانوا يصعدون محرقات في بيت الرب دائما كل ايام يهوياداع"));
arasuperobject.biblemap.put("b14_24_15", new arasuperobject(14,24,15,"وشاخ يهوياداع وشبع من الايام ومات. كان ابن مئة وثلاثين سنة عند وفاته"));
arasuperobject.biblemap.put("b14_24_16", new arasuperobject(14,24,16,"فدفنوه في مدينة داود مع الملوك لانه عمل خيرا في اسرائيل ومع الله وبيته"));
arasuperobject.biblemap.put("b14_24_17", new arasuperobject(14,24,17,"وبعد موت يهوياداع جاء رؤساء يهوذا وسجدوا للملك. حينئذ سمع الملك لهم"));
arasuperobject.biblemap.put("b14_24_18", new arasuperobject(14,24,18,"وتركوا بيت الرب اله آبائهم وعبدوا السواري والاصنام فكان غضب على يهوذا واورشليم لاجل اثمهم هذا"));
arasuperobject.biblemap.put("b14_24_19", new arasuperobject(14,24,19,"وارسل اليهم انبياء لارجاعهم الى الرب واشهدوا عليهم فلم يصغوا"));
arasuperobject.biblemap.put("b14_24_20", new arasuperobject(14,24,20,"ولبس روح الله زكريا بن يهوياداع الكاهن فوقف فوق الشعب وقال لهم هكذا يقول الله لماذا تتعدون وصايا الرب فلا تفلحون. لانكم تركتم الرب قد ترككم"));
arasuperobject.biblemap.put("b14_24_21", new arasuperobject(14,24,21,"ففتنوا عليه ورجموه بحجارة بامر الملك في دار بيت الرب"));
arasuperobject.biblemap.put("b14_24_22", new arasuperobject(14,24,22,"ولم يذكر يواش الملك المعروف الذي عمله يهوياداع ابوه معه بل قتل ابنه. وعند موته قال الرب ينظر ويطالب"));
arasuperobject.biblemap.put("b14_24_23", new arasuperobject(14,24,23,"وفي مدار السنة صعد عليه جيش ارام واتوا الى يهوذا واورشليم واهلكوا كل رؤساء الشعب من الشعب وجميع غنيمتهم ارسلوها الى ملك دمشق"));
arasuperobject.biblemap.put("b14_24_24", new arasuperobject(14,24,24,"لان جيش ارام جاء بشرذمة قليلة ودفع الرب ليدهم جيشا كثيرا جدا لانهم تركوا الرب اله آبائهم. فاجروا قضاء على يواش"));
arasuperobject.biblemap.put("b14_24_25", new arasuperobject(14,24,25,"وعند ذهابهم عنه. لانهم تركوه بامراض كثيرة. فتن عليه عبيده من اجل دماء بني يهوياداع الكاهن وقتلوه على سريره فمات فدفنوه في مدينة داود ولم يدفنوه في قبور الملوك"));
arasuperobject.biblemap.put("b14_24_26", new arasuperobject(14,24,26,"وهذان هما الفاتنان عليه زاباد ابن شمعة العمونية ويهوزاباد ابن شمريت الموآبية"));
arasuperobject.biblemap.put("b14_24_27", new arasuperobject(14,24,27,"واما بنوه وكثرة ما حمل عليه ومرمّة بيت الله ها هي مكتوبة في مدرس سفر الملوك. وملك امصيا ابنه عوضا عنه"));
arasuperobject.biblemap.put("b14_25_1", new arasuperobject(14,25,1,"ملك امصيا وهو ابن خمس وعشرين سنة وملك تسعا وعشرين سنة في اورشليم واسم امه يهوعدّان من اورشليم"));
arasuperobject.biblemap.put("b14_25_2", new arasuperobject(14,25,2,"وعمل المستقيم في عيني الرب ولكن ليس بقلب كامل"));
arasuperobject.biblemap.put("b14_25_3", new arasuperobject(14,25,3,"ولما تثبتت المملكة عليه قتل عبيده الذين قتلوا الملك اباه"));
arasuperobject.biblemap.put("b14_25_4", new arasuperobject(14,25,4,"واما بنوهم فلم يقتلهم بل كما هو مكتوب في الشريعة في سفر موسى حيث أمر الرب قائلا لا تموت الآباء لاجل البنين ولا البنون يموتون لاجل الآباء. بل كل واحد يموت لاجل خطيته"));
arasuperobject.biblemap.put("b14_25_5", new arasuperobject(14,25,5,"وجمع امصيا يهوذا واقامهم حسب بيوت الآباء رؤساء الوف ورؤساء مئات في كل يهوذا وبنيامين واحصاهم من ابن عشرين سنة فما فوق فوجدهم ثلاث مئة الف مختار خارج للحرب حامل رمح وترس"));
arasuperobject.biblemap.put("b14_25_6", new arasuperobject(14,25,6,"واستأجر من اسرائيل مئة الف جبار بأس بمئة وزنة من الفضة"));
arasuperobject.biblemap.put("b14_25_7", new arasuperobject(14,25,7,"وجاء اليه رجل الله قائلا ايها الملك لا يأتي معك جيش اسرائيل لان الرب ليس مع اسرائيل مع كل بني افرايم"));
arasuperobject.biblemap.put("b14_25_8", new arasuperobject(14,25,8,"وان ذهبت انت فاعمل وتشدد للقتال لان الله يسقطك امام العدو لان عند الله قوة للمساعدة وللاسقاط"));
arasuperobject.biblemap.put("b14_25_9", new arasuperobject(14,25,9,"فقال امصيا لرجل الله فماذا يعمل لاجل المئة الوزنة التي اعطيتها لغزاة اسرائيل. فقال رجل الله ان الرب قادر ان يعطيك اكثر من هذه"));
arasuperobject.biblemap.put("b14_25_10", new arasuperobject(14,25,10,"فأفرز امصيا الغزاة الذين جاءوا اليه من افرايم لكي ينطلقوا الى مكانهم فحمي غضبهم جدا على يهوذا ورجعوا الى مكانهم بحمو الغضب"));
arasuperobject.biblemap.put("b14_25_11", new arasuperobject(14,25,11,"واما امصيا فتشدد واقتاد شعبه وذهب الى وادي الملح وضرب من بني ساعير عشرة آلاف"));
arasuperobject.biblemap.put("b14_25_12", new arasuperobject(14,25,12,"وعشرة آلاف احياء سباهم بنو يهوذا وأتوا بهم الى راس سالع وطرحوهم عن راس سالع فتكسروا اجمعون"));
arasuperobject.biblemap.put("b14_25_13", new arasuperobject(14,25,13,"واما الرجال الغزاة الذين ارجعهم امصيا عن الذهاب معه الى القتال فاقتحموا مدن يهوذا من السامرة الى بيت حورون وضربوا منهم ثلاث آلاف ونهبوا نهبا كثيرا"));
arasuperobject.biblemap.put("b14_25_14", new arasuperobject(14,25,14,"ثم بعد مجيء امصيا من ضرب الادوميين أتى بآلهة بني ساعير واقامهم له آلهة وسجد امامهم واوقد لهم"));
arasuperobject.biblemap.put("b14_25_15", new arasuperobject(14,25,15,"فحمي غضب الرب على امصيا وارسل اليه نبيا فقال له لماذا طلبت آلهة الشعب الذي لم ينقذوا شعبهم من يدك"));
arasuperobject.biblemap.put("b14_25_16", new arasuperobject(14,25,16,"وفيما هو يكلمه قال له هل جعلوك مشيرا للملك. كف. لماذا يقتلونك. فكف النبي وقال قد علمت ان الله قد قضى بهلاكك لانك عملت هذا ولم تسمع لمشورتي"));
arasuperobject.biblemap.put("b14_25_17", new arasuperobject(14,25,17,"فاستشار امصيا ملك يهوذا وارسل الى يواش بن يهوآحاز بن ياهو ملك اسرائيل قائلا هلم نتراء مواجهة"));
arasuperobject.biblemap.put("b14_25_18", new arasuperobject(14,25,18,"فارسل يواش ملك اسرائيل الى امصيا ملك يهوذا قائلا. العوسج الذي في لبنان ارسل الى الارز الذي في لبنان يقول اعطي ابنتك لابني امرأة. فعبر حيوان بري كان في لبنان وداس العوسج"));
arasuperobject.biblemap.put("b14_25_19", new arasuperobject(14,25,19,"تقول هانذا قد ضربت ادوم فرفعك قلبك للتمجد. فالآن اقم في بيتك. لماذا تهجم على الشر فتسقط انت ويهوذا معك"));
arasuperobject.biblemap.put("b14_25_20", new arasuperobject(14,25,20,"فلم يسمع امصيا لانه كان من قبل الله ان يسلّمهم لانهم طلبوا آلهة ادوم"));
arasuperobject.biblemap.put("b14_25_21", new arasuperobject(14,25,21,"وصعد يواش ملك اسرائيل فتراءيا مواجهة هو وامصيا ملك يهوذا في بيت شمس التي ليهوذا"));
arasuperobject.biblemap.put("b14_25_22", new arasuperobject(14,25,22,"فانهزم يهوذا امام اسرائيل وهربوا كل واحد الى خيمته"));
arasuperobject.biblemap.put("b14_25_23", new arasuperobject(14,25,23,"واما امصيا ملك يهوذا ابن يواش بن يهوآحاز فامسكه يوآش ملك اسرائيل في بيت شمس وجاء به الى اورشليم وهدم سور اورشليم من باب افرايم الى باب الزاوية اربع مئة ذراع"));
arasuperobject.biblemap.put("b14_25_24", new arasuperobject(14,25,24,"واخذ كل الذهب والفضة وكل الآنية الموجودة في بيت الله مع عبيد ادوم وخزائن بيت الملك والرهناء ورجع الى السامرة"));
arasuperobject.biblemap.put("b14_25_25", new arasuperobject(14,25,25,"وعاش امصيا بن يوآش ملك يهوذا بعد موت يوآش بن يهوآحاز ملك اسرائيل خمس عشرة سنة"));
arasuperobject.biblemap.put("b14_25_26", new arasuperobject(14,25,26,"وبقية امور امصيا الاولى والاخيرة أما هي مكتوبة في سفر ملوك يهوذا واسرائيل"));
arasuperobject.biblemap.put("b14_25_27", new arasuperobject(14,25,27,"ومن حين حاد امصيا من وراء الرب فتنوا عليه في اورشليم فهرب الى لخيش فارسلوا وراءه الى لخيش وقتلوه هناك"));
arasuperobject.biblemap.put("b14_25_28", new arasuperobject(14,25,28,"وحملوه على الخيل ودفنوه مع آبائه في مدينة يهوذا"));
arasuperobject.biblemap.put("b14_26_1", new arasuperobject(14,26,1,"واخذ كل شعب يهوذا عزّيا وهو ابن ست عشرة سنة وملكوه عوضا عن ابيه امصيا"));
arasuperobject.biblemap.put("b14_26_2", new arasuperobject(14,26,2,"هو بنى ايلة وردها ليهوذا بعد اضطجاع الملك مع آبائه"));
arasuperobject.biblemap.put("b14_26_3", new arasuperobject(14,26,3,"كان عزّيا ابن ست عشرة سنة حين ملك وملك اثنتين وخمسين سنة في اورشليم. واسم امه يكليا من اورشليم"));
arasuperobject.biblemap.put("b14_26_4", new arasuperobject(14,26,4,"وعمل المستقيم في عيني الرب حسب كل ما عمل امصيا ابوه"));
arasuperobject.biblemap.put("b14_26_5", new arasuperobject(14,26,5,"وكان يطلب الله في ايام زكريا الفاهم بمناظر الله وفي ايام طلبه الرب انجحه الله"));
arasuperobject.biblemap.put("b14_26_6", new arasuperobject(14,26,6,"وخرج وحارب الفسطينيين وهدم سور جتّ وسور يبنة وسور اشدود وبنى مدنا في ارض اشدود والفلسطينيين"));
arasuperobject.biblemap.put("b14_26_7", new arasuperobject(14,26,7,"وساعده الله على الفلسطينيين وعلى العرب الساكنين في جور بعل والمعونيين"));
arasuperobject.biblemap.put("b14_26_8", new arasuperobject(14,26,8,"واعطى العمونيون عزّيا هدايا وامتد اسمه الى مدخل مصر لانه تشدد جدا"));
arasuperobject.biblemap.put("b14_26_9", new arasuperobject(14,26,9,"وبنى عزيا ابراجا في اورشليم عند باب الزاوية وعند باب الوادي وعند الزاوية وحصّنها"));
arasuperobject.biblemap.put("b14_26_10", new arasuperobject(14,26,10,"وبنى ابراجا في البرية وحفر آبارا كثيرة لانه كان له ماشية كثيرة في الساحل والسهل وفلاحون وكرامون في الجبال وفي الكرمل لانه كان يحب الفلاحة"));
arasuperobject.biblemap.put("b14_26_11", new arasuperobject(14,26,11,"وكان لعزيا جيش من المقاتلين يخرجون للحرب احزابا حسب عدد احصائهم عن يد يعيئيل الكاتب ومعسيا العريف تحت يد حننيا واحد من رؤساء الملك"));
arasuperobject.biblemap.put("b14_26_12", new arasuperobject(14,26,12,"كل عدد رؤوس الآباء من جبابرة البأس الفان وست مئة"));
arasuperobject.biblemap.put("b14_26_13", new arasuperobject(14,26,13,"وتحت يدهم جيش جنود ثلاث مئة الف وسبعة آلاف وخمس مئة من المقاتلين بقوة شديدة لمساعدة الملك على العدو"));
arasuperobject.biblemap.put("b14_26_14", new arasuperobject(14,26,14,"وهيّأ لهم عزيا لكل الجيش اتراسا ورماحا وخوذا ودروعا وقسيا وحجارة مقاليع"));
arasuperobject.biblemap.put("b14_26_15", new arasuperobject(14,26,15,"وعمل في اورشليم منجنيقات اختراع مخترعين لتكون على الابراج وعلى الزوايا لترمى بها السهام والحجارة العظيمة وامتد اسمه الى بعيد اذ عجبت مساعدته حتى تشدد"));
arasuperobject.biblemap.put("b14_26_16", new arasuperobject(14,26,16,"ولما تشدد ارتفع قلبه الى الهلاك وخان الرب الهه ودخل هيكل الرب ليوقد على مذبح البخور"));
arasuperobject.biblemap.put("b14_26_17", new arasuperobject(14,26,17,"ودخل وراءه عزريا الكاهن ومعه ثمانون من كهنة الرب بني البأس"));
arasuperobject.biblemap.put("b14_26_18", new arasuperobject(14,26,18,"وقاوموا عزيا الملك وقالوا له ليس لك يا عزيا ان توقد للرب بل للكهنة بني هرون المقدسين للايقاد. اخرج من المقدس لانك خنت وليس لك من كرامة من عند الرب الاله"));
arasuperobject.biblemap.put("b14_26_19", new arasuperobject(14,26,19,"فحنق عزيا وكان في يده مجمرة للايقاد وعند حنقه على الكهنة خرج برص في جبهته امام الكهنة في بيت الرب بجانب مذبح البخور"));
arasuperobject.biblemap.put("b14_26_20", new arasuperobject(14,26,20,"فالتفت نحوه عزرياهو الكاهن الراس وكل الكهنة واذا هو ابرص في جبهته فطردوه من هناك حتى انه هو نفسه بادر الى الخروج لان الرب ضربه"));
arasuperobject.biblemap.put("b14_26_21", new arasuperobject(14,26,21,"وكان عزيا الملك ابرص الى يوم وفاته واقام في بيت المرض ابرص لانه قطع من بيت الرب وكان يوثام ابنه على بيت الملك يحكم على شعب الارض"));
arasuperobject.biblemap.put("b14_26_22", new arasuperobject(14,26,22,"وبقية امور عزيا الاولى والاخيرة كتبها اشعياء بن آموص النبي"));
arasuperobject.biblemap.put("b14_26_23", new arasuperobject(14,26,23,"ثم اضطجع عزيا مع آبائه ودفنوه مع آبائه في حقل المقبرة التي للملوك لانهم قالوا انه ابرص. وملك يوثام ابنه عوضا عنه"));
arasuperobject.biblemap.put("b14_27_1", new arasuperobject(14,27,1,"كان يوثام ابن خمس وعشرين سنة حين ملك وملك ست عشرة سنة في اورشليم واسم امه يروشة بنت صادوق"));
arasuperobject.biblemap.put("b14_27_2", new arasuperobject(14,27,2,"وعمل المستقيم في عيني الرب حسب كل ما عمل عزيا ابوه الا انه لم يدخل هيكل الرب وكان الشعب يفسدون بعد"));
arasuperobject.biblemap.put("b14_27_3", new arasuperobject(14,27,3,"هو بنى الباب الاعلى لبيت الرب وبنى كثيرا على سور الاكمة"));
arasuperobject.biblemap.put("b14_27_4", new arasuperobject(14,27,4,"وبنى مدنا في جبل يهوذا وبنى في الغابات قلعا وابراجا"));
arasuperobject.biblemap.put("b14_27_5", new arasuperobject(14,27,5,"وهو حارب ملك بني عمون وقوي عليهم فاعطاه بنو عمون في تلك السنة مئة وزنة من الفضة وعشرة آلاف كرّ قمح وعشرة آلاف من الشعير. هذا ما ادّاه له بنو عمون وكذلك في السنة الثانية والثالثة"));
arasuperobject.biblemap.put("b14_27_6", new arasuperobject(14,27,6,"وتشدد يوثام لانه هيّأ طرقه امام الرب الهه"));
arasuperobject.biblemap.put("b14_27_7", new arasuperobject(14,27,7,"وبقية أمور يوثام وكل حروبه وطرقه ها هي مكتوبة في سفر ملوك اسرائيل ويهوذا"));
arasuperobject.biblemap.put("b14_27_8", new arasuperobject(14,27,8,"كان ابن خمسة وعشرين سنة حين ملك وملك ست عشرة سنة في اورشليم"));
arasuperobject.biblemap.put("b14_27_9", new arasuperobject(14,27,9,"ثم اضطجع يوثام مع آبائه فدفنوه في مدينة داود وملك آحاز ابنه عوضا عنه"));
arasuperobject.biblemap.put("b14_28_1", new arasuperobject(14,28,1,"كان آحاز ابن عشرين سنة حين ملك وملك ست عشرة سنة في اورشليم ولم يفعل المستقيم في عيني الرب كداود ابيه"));
arasuperobject.biblemap.put("b14_28_2", new arasuperobject(14,28,2,"بل سار في طرق ملوك اسرائيل وعمل ايضا تماثيل مسبوكة للبعليم"));
arasuperobject.biblemap.put("b14_28_3", new arasuperobject(14,28,3,"وهو اوقد في وادي ابن هنوم واحرق بنيه بالنار حسب رجاسات الامم الذين طردهم الرب من امام بني اسرائيل"));
arasuperobject.biblemap.put("b14_28_4", new arasuperobject(14,28,4,"وذبح واوقد على المرتفعات وعلى التلال وتحت كل شجرة خضراء"));
arasuperobject.biblemap.put("b14_28_5", new arasuperobject(14,28,5,"فدفعه الرب الهه ليد ملك ارام. فضربوه وسبوا منه سبيا عظيما وأتوا بهم الى دمشق. ودفع ايضا ليد ملك اسرائيل فضربه ضربة عظيمة"));
arasuperobject.biblemap.put("b14_28_6", new arasuperobject(14,28,6,"وقتل فقح بن رمليا في يهوذا مئة وعشرين الفا في يوم واحد. الجميع بنو بأس. لانهم تركوا الرب اله آبائهم"));
arasuperobject.biblemap.put("b14_28_7", new arasuperobject(14,28,7,"وقتل زكري جبار افرايم معسيا ابن الملك وعزريقام رئيس البيت والقانة ثاني الملك"));
arasuperobject.biblemap.put("b14_28_8", new arasuperobject(14,28,8,"وسبى بني اسرائيل من اخوتهم مئتي الف من النساء والبنين والبنات ونهبوا ايضا منهم غنيمة وافرة واتوا بالغنيمة الى السامرة"));
arasuperobject.biblemap.put("b14_28_9", new arasuperobject(14,28,9,"وكان هناك نبي للرب اسمه عوديد. فخرج للقاء الجيش الآتي الى السامرة وقال لهم. هوذا من اجل غضب الرب اله آبائكم على يهوذا قد دفعهم ليدكم وقد قتلتموهم بغضب بلغ السماء"));
arasuperobject.biblemap.put("b14_28_10", new arasuperobject(14,28,10,"والآن انتم عازمون على اخضاع بني يهوذا واورشليم عبيدا واماء لكم. أما عندكم انتم آثام للرب الهكم"));
arasuperobject.biblemap.put("b14_28_11", new arasuperobject(14,28,11,"والآن اسمعوا لي وردوا السبي الذي سبيتموه من اخوتكم لان حمو غضب الرب عليكم"));
arasuperobject.biblemap.put("b14_28_12", new arasuperobject(14,28,12,"ثم قام رجال من رؤوس بني افرايم عزريا بن يهوحانان وبرخيا بن مشلّيموت ويحزقيا بن شلوم وعماسا بن حدلاي على المقبلين من الجيش"));
arasuperobject.biblemap.put("b14_28_13", new arasuperobject(14,28,13,"وقالوا لهم لا تدخلون بالسبي الى هنا لان علينا اثما للرب وانتم عازمون ان تزيدوا على خطايانا وعلى اثمنا لان لنا اثما كثيرا وعلى اسرائيل حمو غضب"));
arasuperobject.biblemap.put("b14_28_14", new arasuperobject(14,28,14,"فترك المتجردون السبي والنهب امام الرؤساء وكل الجماعة"));
arasuperobject.biblemap.put("b14_28_15", new arasuperobject(14,28,15,"وقام الرجال المعيّنة اسماؤهم واخذوا المسبيين والبسوا كل عراتهم من الغنيمة وكسوهم وحذوهم واطعموهم واسقوهم ودهّنوهم وحملوا على حمير جميع المعيين منهم وأتوا بهم الى اريحا مدينة النخل الى اخوتهم ثم رجعوا الى السامرة"));
arasuperobject.biblemap.put("b14_28_16", new arasuperobject(14,28,16,"في ذلك الوقت ارسل الملك آحاز الى ملوك اشور لكي يساعدوه"));
arasuperobject.biblemap.put("b14_28_17", new arasuperobject(14,28,17,"فان الادوميين اتوا ايضا وضربوا يهوذا وسبوا سبيا"));
arasuperobject.biblemap.put("b14_28_18", new arasuperobject(14,28,18,"واقتحم الفلسطينيون مدن الساحل وجنوبي يهوذا واخذوا بيت شمس وايلون وجديروت وسوكو وقراها وتمنة وقراها وجمزو وقراها وسكنوا هناك"));
arasuperobject.biblemap.put("b14_28_19", new arasuperobject(14,28,19,"لان الرب ذلّل يهوذا بسبب آحاز ملك اسرائيل لانه اجمح يهوذا وخان الرب خيانة"));
arasuperobject.biblemap.put("b14_28_20", new arasuperobject(14,28,20,"فجاء عليه تلغث فلناسر ملك اشور وضايقه ولم يشدّده"));
arasuperobject.biblemap.put("b14_28_21", new arasuperobject(14,28,21,"لان آحاز اخذ قسما من بيت الرب ومن بيت الملك ومن الرؤساء واعطاه لملك اشور ولكنه لم يساعده"));
arasuperobject.biblemap.put("b14_28_22", new arasuperobject(14,28,22,"وفي ضيقه زاد خيانة بالرب الملك آحاز هذا"));
arasuperobject.biblemap.put("b14_28_23", new arasuperobject(14,28,23,"وذبح لآلهة دمشق الذين ضاربوه وقال لان آلهة ملوك ارام تساعدهم انا اذبح فيساعدونني. واما هم فكانوا سبب سقوط له ولكل اسرائيل"));
arasuperobject.biblemap.put("b14_28_24", new arasuperobject(14,28,24,"وجمع آحاز آنية بيت الله وقطّع آنية بيت الله واغلق ابواب بيت الرب وعمل لنفسه مذابح في كل زاوية في اورشليم"));
arasuperobject.biblemap.put("b14_28_25", new arasuperobject(14,28,25,"وفي كل مدينة فمدينة من يهوذا عمل مرتفعات للايقاد لآلهة اخرى واسخط الرب اله آبائه"));
arasuperobject.biblemap.put("b14_28_26", new arasuperobject(14,28,26,"وبقية اموره وكل طرقه الاولى والاخيرة ها هي مكتوبة في سفر ملوك يهوذا واسرائيل"));
arasuperobject.biblemap.put("b14_28_27", new arasuperobject(14,28,27,"ثم اضطجع آحاز مع آبائه فدفنوه في المدينة في اورشليم لانهم لم يأتوا به الى قبور ملوك اسرائيل. وملك حزقيا ابنه عوضا عنه"));
arasuperobject.biblemap.put("b14_29_1", new arasuperobject(14,29,1,"ملك حزقيا وهو ابن خمس وعشرين سنة وملك تسعا وعشرين سنة في اورشليم واسم امه ابيّة بنت زكريا"));
arasuperobject.biblemap.put("b14_29_2", new arasuperobject(14,29,2,"وعمل المستقيم في عيني الرب حسب كل ما عمل داود ابوه"));
arasuperobject.biblemap.put("b14_29_3", new arasuperobject(14,29,3,"هو في السنة الاولى من ملكه في الشهر الاول فتح ابواب بيت الرب ورمّمها"));
arasuperobject.biblemap.put("b14_29_4", new arasuperobject(14,29,4,"وادخل الكهنة واللاويين وجمعهم الى الساحة الشرقية"));
arasuperobject.biblemap.put("b14_29_5", new arasuperobject(14,29,5,"وقال لهم اسمعوا لي ايها اللاويون. وتقدسوا الآن وقدسوا بيت الرب اله آبائكم واخرجوا النجاسة من القدس"));
arasuperobject.biblemap.put("b14_29_6", new arasuperobject(14,29,6,"لان آبائنا خانوا وعملوا الشر في عيني الرب الهنا وتركوه وحوّلوا وجوههم عن مسكن الرب واعطوا قفا"));
arasuperobject.biblemap.put("b14_29_7", new arasuperobject(14,29,7,"واغلقوا ايضا ابواب الرواق واطفأوا السرج ولم يوقدوا بخورا ولم يصعدوا محرقة في القدس لاله اسرائيل"));
arasuperobject.biblemap.put("b14_29_8", new arasuperobject(14,29,8,"فكان غضب الرب على يهوذا واورشليم واسلمهم للقلق والدهش والصفير كما انتم راؤون باعينكم"));
arasuperobject.biblemap.put("b14_29_9", new arasuperobject(14,29,9,"وهوذا قد سقط آباؤنا بالسيف وبنونا وبناتنا ونساؤنا في السبي لاجل هذا"));
arasuperobject.biblemap.put("b14_29_10", new arasuperobject(14,29,10,"فالآن في قلبي ان اقطع عهدا مع الرب اله اسرائيل فيرد عنا حمو غضبه"));
arasuperobject.biblemap.put("b14_29_11", new arasuperobject(14,29,11,"يا بنيّ لا تضلّوا الآن لان الرب اختاركم لكي تقفوا امامه وتخدموه وتكونوا خادمين وموقدين له"));
arasuperobject.biblemap.put("b14_29_12", new arasuperobject(14,29,12,"فقام اللاويون محث بن عماساي ويوئيل بن عزريا من بني القهاتيين ومن بني مراري قيس بن عبدي وعزريا بن يهللئيل ومن الجرشونيين يوآخ بن زمّة وعيدن بن يوآخ"));
arasuperobject.biblemap.put("b14_29_13", new arasuperobject(14,29,13,"ومن بني اليصافان شمري ويعيئيل ومن بني آساف زكريا ومتّنيا"));
arasuperobject.biblemap.put("b14_29_14", new arasuperobject(14,29,14,"ومن بني هيمان يحيئيل وشمعي ومن بني يدوثون شمعيا وعزيئيل"));
arasuperobject.biblemap.put("b14_29_15", new arasuperobject(14,29,15,"وجمعوا اخوتهم وتقدسوا وأتوا حسب امر الملك بكلام الرب ليطهروا بيت الرب"));
arasuperobject.biblemap.put("b14_29_16", new arasuperobject(14,29,16,"ودخل الكهنة الى داخل بيت الرب ليطهروه واخرجوا كل النجاسة التي وجدوها في هيكل الرب الى دار بيت الرب وتناولها اللاويون ليخرجوها الى الخارج الى وادي قدرون"));
arasuperobject.biblemap.put("b14_29_17", new arasuperobject(14,29,17,"وشرعوا في التقديس في اول الشهر الاول وفي اليوم الثامن من الشهر انتهوا الى رواق الرب وقدسوا بيت الرب في ثمانية ايام وفي اليوم السادس عشر من الشهر الاول انتهوا"));
arasuperobject.biblemap.put("b14_29_18", new arasuperobject(14,29,18,"ودخلوا الى داخل الى حزقيا الملك وقالوا قد طهرنا كل بيت الرب ومذبح المحرقة وكل آنيته ومائدة خبز الوجوه وكل آنيتها"));
arasuperobject.biblemap.put("b14_29_19", new arasuperobject(14,29,19,"وجميع الآنية التي طرحها الملك آحاز في ملكه بخيانته قد هيّأناها وقدسناها وها هي امام مذبح الرب"));
arasuperobject.biblemap.put("b14_29_20", new arasuperobject(14,29,20,"وبكر حزقيا الملك وجمع رؤساء المدينة وصعد الى بيت الرب"));
arasuperobject.biblemap.put("b14_29_21", new arasuperobject(14,29,21,"فأتوا بسبعة ثيران وسبعة كباش وسبعة خرفان وسبعة تيوس معزى ذبيحة خطية عن المملكة وعن المقدس وعن يهوذا. وقال لبني هرون الكهنة ان يصعدوها على مذبح الرب"));
arasuperobject.biblemap.put("b14_29_22", new arasuperobject(14,29,22,"فذبحوا الثيران وتناول الكهنة الدم ورشوه على المذبح ثم ذبحوا الكباش ورشوا الدم على المذبح ثم ذبحوا الخرفان ورشوا الدم على المذبح"));
arasuperobject.biblemap.put("b14_29_23", new arasuperobject(14,29,23,"ثم تقدموا بتيوس ذبيحة الخطية امام الملك والجماعة ووضعوا ايديهم عليها"));
arasuperobject.biblemap.put("b14_29_24", new arasuperobject(14,29,24,"وذبحها الكهنة وكفّروا بدمها على المذبح تكفيرا عن جميع اسرائيل لان الملك قال ان المحرقة وذبيحة الخطية هما عن كل اسرائيل"));
arasuperobject.biblemap.put("b14_29_25", new arasuperobject(14,29,25,"واوقف اللاويين في بيت الرب بصنوج ورباب وعيدان حسب امر داود وجاد رائي الملك وناثان النبي لان من قبل الرب الوصية عن يد انبيائه"));
arasuperobject.biblemap.put("b14_29_26", new arasuperobject(14,29,26,"فوقف اللاويون بآلات داود والكهنة بالابواق"));
arasuperobject.biblemap.put("b14_29_27", new arasuperobject(14,29,27,"وأمر حزقيا باصعاد المحرقة على المذبح. وعند ابتداء المحرقة ابتدأ نشيد الرب والابواق بواسطة آلات داود ملك اسرائيل"));
arasuperobject.biblemap.put("b14_29_28", new arasuperobject(14,29,28,"وكان كل الجماعة يسجدون والمغنون يغنون والمبوقون يبوقون. الجميع الى ان انتهت المحرقة"));
arasuperobject.biblemap.put("b14_29_29", new arasuperobject(14,29,29,"وعند انتهاء المحرقة خرّ الملك وكل الموجودين معه وسجدوا"));
arasuperobject.biblemap.put("b14_29_30", new arasuperobject(14,29,30,"وقال حزقيا الملك والرؤساء للاويين ان يسبحوا الرب بكلام داود وآساف الرائي. فسبحوا بابتهاج وخرّوا وسجدوا"));
arasuperobject.biblemap.put("b14_29_31", new arasuperobject(14,29,31,"ثم اجاب حزقيا وقال الآن ملأتم ايديكم للرب. تقدموا وأتوا بذبائح وقرابين شكر لبيت الرب. فأتت الجماعة بذبائح وقرابين شكر وكل سموح القلب اتى بمحرقات"));
arasuperobject.biblemap.put("b14_29_32", new arasuperobject(14,29,32,"وكان عدد المحرقات التي اتى بها الجماعة سبعين ثورا ومئة كبش ومئتي خروف. كل هذه محرقة للرب"));
arasuperobject.biblemap.put("b14_29_33", new arasuperobject(14,29,33,"والاقداس ست مئة من البقر وثلاثة آلاف من الضأن"));
arasuperobject.biblemap.put("b14_29_34", new arasuperobject(14,29,34,"الا ان الكهنة كانوا قليلين فلم يقدروا ان يسلخوا كل المحرقات فساعدهم اخوتهم اللاويون حتى كمل العمل وحتى تقدس الكهنة. لان اللاويين كانوا اكثر استقامة قلب من الكهنة في التقدس"));
arasuperobject.biblemap.put("b14_29_35", new arasuperobject(14,29,35,"وايضا كانت المحرقات كثيرة بشحم ذبائح السلامة وسكائب المحرقات. فاستقامت خدمة بيت الرب"));
arasuperobject.biblemap.put("b14_29_36", new arasuperobject(14,29,36,"وفرح حزقيا وكل الشعب من اجل ان الله اعدّ الشعب لان الأمر كان بغتة"));
arasuperobject.biblemap.put("b14_30_1", new arasuperobject(14,30,1,"وارسل حزقيا الى جميع اسرائيل ويهوذا وكتب ايضا رسائل الى افرايم ومنسّى ان يأتوا الى بيت الرب في اورشليم ليعملوا فصحا للرب اله اسرائيل"));
arasuperobject.biblemap.put("b14_30_2", new arasuperobject(14,30,2,"فتشاور الملك ورؤساؤه وكل الجماعة في اورشليم ان يعملوا الفصح في الشهر الثاني"));
arasuperobject.biblemap.put("b14_30_3", new arasuperobject(14,30,3,"لانهم لم يقدروا ان يعملوه في ذلك الوقت لان الكهنة لم يتقدسوا بالكفاية والشعب لم يجتمعوا الى اورشليم"));
arasuperobject.biblemap.put("b14_30_4", new arasuperobject(14,30,4,"فحسن الأمر في عيني الملك وعيون كل الجماعة"));
arasuperobject.biblemap.put("b14_30_5", new arasuperobject(14,30,5,"فاعتمدوا على اطلاق النداء في جميع اسرائيل من بئر سبع الى دان ان يأتوا لعمل الفصح للرب اله اسرائيل في اورشليم لانهم لم يعملوه كما هو مكتوب منذ زمان كثير"));
arasuperobject.biblemap.put("b14_30_6", new arasuperobject(14,30,6,"فذهب السعاة بالرسائل من يد الملك ورؤسائه في جميع اسرائيل ويهوذا وحسب وصية الملك كانوا يقولون يا بني اسرائيل ارجعوا الى الرب اله ابراهيم واسحق واسرائيل فيرجع الى الناجين الباقين لكم من يد ملوك اشور"));
arasuperobject.biblemap.put("b14_30_7", new arasuperobject(14,30,7,"ولا تكونوا كآبائكم وكاخوتكم الذين خانوا الرب اله آبائهم فجعلهم دهشة كما انتم ترون"));
arasuperobject.biblemap.put("b14_30_8", new arasuperobject(14,30,8,"الآن لا تصلّبوا رقابكم كآبائكم بل اخضعوا للرب وادخلوا مقدسه الذي قدسه الى الابد واعبدوا الرب الهكم فيرتدّ عنكم حمو غضبه"));
arasuperobject.biblemap.put("b14_30_9", new arasuperobject(14,30,9,"لانه برجوعكم الى الرب يجد اخوتكم وبنوكم رحمة امام الذين يسبونهم فيرجعون الى هذه الارض لان الرب الهكم حنّان ورحيم ولا يحوّل وجهه عنكم اذا رجعتم اليه"));
arasuperobject.biblemap.put("b14_30_10", new arasuperobject(14,30,10,"فكان السعاة يعبرون من مدينة الى مدينة في ارض افرايم ومنسّى حتى زبولون فكانوا يضحكون عليهم ويهزأون بهم"));
arasuperobject.biblemap.put("b14_30_11", new arasuperobject(14,30,11,"الا ان قوم من اشير ومنسّى وزبولون تواضعوا وأتوا الى اورشليم"));
arasuperobject.biblemap.put("b14_30_12", new arasuperobject(14,30,12,"وكانت يد الله في يهوذا ايضا فاعطاهم قلبا واحدا ليعملوا بأمر الملك والرؤساء حسب قول الرب"));
arasuperobject.biblemap.put("b14_30_13", new arasuperobject(14,30,13,"فاجتمع الى اورشليم شعب كثير لعمل عيد الفطير في الشهر الثاني جماعة كثيرة جدا"));
arasuperobject.biblemap.put("b14_30_14", new arasuperobject(14,30,14,"وقاموا وازالوا المذابح التي في اورشليم وازالوا كل مذابح التبخير وطرحوها الى وادي قدرون"));
arasuperobject.biblemap.put("b14_30_15", new arasuperobject(14,30,15,"وذبحوا الفصح في الرابع عشر من الشهر الثاني والكهنة واللاويون خجلوا وتقدسوا وادخلوا المحرقات الى بيت الرب"));
arasuperobject.biblemap.put("b14_30_16", new arasuperobject(14,30,16,"واقاموا على مقامهم حسب حكمهم كناموس موسى رجل الله. كان الكهنة يرشون الدم من يد اللاويين"));
arasuperobject.biblemap.put("b14_30_17", new arasuperobject(14,30,17,"لانه كان كثيرون في الجماعة لم يتقدسوا فكان اللاويون على ذبح الفصح عن كل من ليس بطاهر لتقديسهم للرب"));
arasuperobject.biblemap.put("b14_30_18", new arasuperobject(14,30,18,"لان كثيرين من الشعب كثيرين من افرايم ومنسّى ويساكر وزبولون لم يتطهروا بل اكلوا الفصح ليس كما هو مكتوب. الا ان حزقيا صلّى عنهم قائلا الرب الصالح يكفّر عن"));
arasuperobject.biblemap.put("b14_30_19", new arasuperobject(14,30,19,"كل من هيّأ قلبه لطلب الله الرب اله آبائه وليس كطهارة القدس"));
arasuperobject.biblemap.put("b14_30_20", new arasuperobject(14,30,20,"فسمع الرب لحزقيا وشفى الشعب"));
arasuperobject.biblemap.put("b14_30_21", new arasuperobject(14,30,21,"وعمل بنو اسرائيل الموجودون في اورشليم عيد الفطير سبعة ايام بفرح عظيم وكان اللاويون والكهنة يسبّحون الرب يوما فيوما بآلات حمد للرب"));
arasuperobject.biblemap.put("b14_30_22", new arasuperobject(14,30,22,"وطيب حزقيا قلوب جميع اللاويين الفطنين فطنة صالحة للرب وأكلوا الموسم سبعة ايام يذبحون ذبائح سلامة ويحمدون الرب اله آبائهم"));
arasuperobject.biblemap.put("b14_30_23", new arasuperobject(14,30,23,"وتشاور كل الجماعة ان يعملوا سبعة ايام اخرى فعملوا سبعة ايام بفرح"));
arasuperobject.biblemap.put("b14_30_24", new arasuperobject(14,30,24,"لان حزقيا ملك يهوذا قدم للجماعة الف ثور وسبعة آلاف من الضأن والرؤساء قدموا للجماعة الف ثور وعشرة آلاف من الضأن وتقدس كثيرون من الكهنة"));
arasuperobject.biblemap.put("b14_30_25", new arasuperobject(14,30,25,"وفرح كل جماعة يهوذا والكهنة واللاويون وكل الجماعة الآتين من اسرائيل والغرباء الآتون من ارض اسرائيل والساكنون في يهوذا"));
arasuperobject.biblemap.put("b14_30_26", new arasuperobject(14,30,26,"وكان فرح عظيم في اورشليم لانه من ايام سليمان بن داود ملك اسرائيل لم يكن كهذا في اورشليم"));
arasuperobject.biblemap.put("b14_30_27", new arasuperobject(14,30,27,"وقام الكهنة اللاويون وباركوا الشعب فسمع صوتهم ودخلت صلاتهم الى مسكن قدسه الى السماء"));
arasuperobject.biblemap.put("b14_31_1", new arasuperobject(14,31,1,"ولما كمل هذا خرج كل اسرائيل الحاضرين الى مدن يهوذا وكسروا الانصاب وقطعوا السواري وهدموا المرتفعات والمذابح من كل يهوذا وبنيامين ومن افرايم ومنسّى حتى افنوها ثم رجع كل اسرائيل كل واحد الى ملكه الى مدنهم"));
arasuperobject.biblemap.put("b14_31_2", new arasuperobject(14,31,2,"واقام حزقيا فرق الكهنة واللاويين حسب اقسامهم كل واحد حسب خدمته الكهنة واللاويين للمحرقات وذبائح السلامة للخدمة والحمد والتسبيح في ابواب محلات الرب"));
arasuperobject.biblemap.put("b14_31_3", new arasuperobject(14,31,3,"واعطى الملك حصة من ماله للمحرقات محرقات الصباح والمساء والمحرقات للسبوت والاشهر والمواسم كما هو مكتوب في شريعة الرب"));
arasuperobject.biblemap.put("b14_31_4", new arasuperobject(14,31,4,"وقال للشعب سكان اورشليم ان يعطوا حصة الكهنة واللاويين لكي يتمسكوا بشريعة الرب"));
arasuperobject.biblemap.put("b14_31_5", new arasuperobject(14,31,5,"ولما شاع الأمر كثر بنو اسرائيل من اوائل الحنطة والمسطار والزيت والعسل ومن كل غلة الحقل وأتوا بعشر الجميع بكثرة"));
arasuperobject.biblemap.put("b14_31_6", new arasuperobject(14,31,6,"وبنو اسرائيل ويهوذا الساكنون في مدن يهوذا أتوا هم ايضا بعشر البقر والضأن وعشر الاقداس المقدسة للرب الههم وجعلوها صبرا صبرا"));
arasuperobject.biblemap.put("b14_31_7", new arasuperobject(14,31,7,"في الشهر الثالث ابتدأوا بتأسيس الصّبر وفي الشهر السابع اكملوا"));
arasuperobject.biblemap.put("b14_31_8", new arasuperobject(14,31,8,"وجاء حزقيا والرؤساء ورأوا الصّبر فباركوا الرب وشعبه اسرائيل"));
arasuperobject.biblemap.put("b14_31_9", new arasuperobject(14,31,9,"وسأل حزقيا الكهنة واللاويين عن الصّبر"));
arasuperobject.biblemap.put("b14_31_10", new arasuperobject(14,31,10,"فكلّمه عزريا الكاهن الراس لبيت صادوق وقال منذ ابتدأ بجلب التقدمة الى بيت الرب اكلنا وشبعنا وفضل عنا بكثرة لان الرب بارك شعبه والذي فضل هو هذه الكثرة"));
arasuperobject.biblemap.put("b14_31_11", new arasuperobject(14,31,11,"وأمر حزقيا باعداد مخادع في بيت الرب فاعدّوا"));
arasuperobject.biblemap.put("b14_31_12", new arasuperobject(14,31,12,"وأتوا بالتقدمة والعشر والاقداس بامانة وكان رئيسا عليهم كوننيا اللاوي وشمعي اخوه الثاني"));
arasuperobject.biblemap.put("b14_31_13", new arasuperobject(14,31,13,"ويحيئيل وعزريا ونحث وعسائيل ويريموث ويوزاباد وايليئيل ويسمخيا ومحث وبنايا وكلاء تحت يد كوننيا وشمعي اخيه حسب تعيين حزقيا الملك وعزريا رئيس بيت الله"));
arasuperobject.biblemap.put("b14_31_14", new arasuperobject(14,31,14,"وقوري بن يمنة اللاوي البواب نحو الشرق كان على المتبرّع به لله لاعطاء تقدمة الرب واقداس الاقداس"));
arasuperobject.biblemap.put("b14_31_15", new arasuperobject(14,31,15,"وتحت يده عدن ومنيامين ويشوع وشمعيا وامريا وشكنيا في مدن الكهنة بامانة ليعطوا لاخوتهم حسب الفرق الكبير كالصغير"));
arasuperobject.biblemap.put("b14_31_16", new arasuperobject(14,31,16,"فضلا عن انتساب ذكورهم من ابن ثلاث سنين فما فوق من كل داخل بيت الرب أمر كل يوم بيومه حسب خدمتهم في حراستهم حسب اقسامهم"));
arasuperobject.biblemap.put("b14_31_17", new arasuperobject(14,31,17,"وانتساب الكهنة حسب بيوت آبائهم واللاويين من ابن عشرين سنة فما فوق حسب حراساتهم واقسامهم"));
arasuperobject.biblemap.put("b14_31_18", new arasuperobject(14,31,18,"وانتساب جميع اطفالهم ونسائهم وبنيهم وبناتهم في كل الجماعة لانهم بامانتهم تقدسوا تقدسا"));
arasuperobject.biblemap.put("b14_31_19", new arasuperobject(14,31,19,"ومن بني هرون الكهنة في حقول مسارح مدنهم في كل مدينة فمدينة الرجال المعيّنة اسماؤهم لاعطاء حصص لكل ذكر من الكهنة ولكل من انتسب من اللاويين"));
arasuperobject.biblemap.put("b14_31_20", new arasuperobject(14,31,20,"هكذا عمل حزقيا في كل يهوذا وعمل ما هو صالح ومستقيم وحق امام الرب الهه"));
arasuperobject.biblemap.put("b14_31_21", new arasuperobject(14,31,21,"وكل عمل ابتدأ به في خدمة بيت الله وفي الشريعة والوصية ليطلب الهه انما عمله بكل قلبه وافلح"));
arasuperobject.biblemap.put("b14_32_1", new arasuperobject(14,32,1,"وبعد هذه الأمور وهذه الامانة اتى سنحاريب ملك اشور ودخل يهوذا ونزل على المدن الحصينة وطمع باخضاعها لنفسه"));
arasuperobject.biblemap.put("b14_32_2", new arasuperobject(14,32,2,"ولما رأى حزقيا ان سنحاريب قد اتى ووجهه على محاربة اورشليم"));
arasuperobject.biblemap.put("b14_32_3", new arasuperobject(14,32,3,"تشاور هو ورؤساؤه وجبابرته على طمّ مياه العيون التي هي خارج المدينة فساعدوه"));
arasuperobject.biblemap.put("b14_32_4", new arasuperobject(14,32,4,"فتجمع شعب كثير وطمّوا جميع الينابيع والنهر الجاري في وسط الارض قائلين لماذا يأتي ملوك اشور ويجدون مياها غزيرة"));
arasuperobject.biblemap.put("b14_32_5", new arasuperobject(14,32,5,"وتشدد وبنى كل السور المنهدم واعلاه الى الابراج وسورا آخر خارجا وحصّن القلعة مدينة داود وعمل سلاحا بكثرة واتراسا"));
arasuperobject.biblemap.put("b14_32_6", new arasuperobject(14,32,6,"وجعل رؤساء قتال على الشعب وجمعهم اليه الى ساحة باب المدينة وطيب قلوبهم قائلا"));
arasuperobject.biblemap.put("b14_32_7", new arasuperobject(14,32,7,"تشددوا وتشجعوا. لا تخافوا ولا ترتاعوا من ملك اشور ومن كل الجمهور الذي معه لان معنا اكثر مما معه"));
arasuperobject.biblemap.put("b14_32_8", new arasuperobject(14,32,8,"معه ذراع بشر ومعنا الرب الهنا ليساعدنا ويحارب حروبنا. فاستند الشعب على كلام حزقيا ملك يهوذا"));
arasuperobject.biblemap.put("b14_32_9", new arasuperobject(14,32,9,"بعد هذا ارسل سنحاريب ملك اشور عبيده الى اورشليم. وهو على لخيش وكل سلطنته معه. الى حزقيا ملك يهوذا والى كل يهوذا الذين في اورشليم يقولون"));
arasuperobject.biblemap.put("b14_32_10", new arasuperobject(14,32,10,"هكذا يقول سنحاريب ملك اشور. على ماذا تتكلون وتقيمون في الحصار في اورشليم"));
arasuperobject.biblemap.put("b14_32_11", new arasuperobject(14,32,11,"أليس حزقيا يغويكم ليدفعكم للموت بالجوع والعطش قائلا الرب الهنا ينقذنا من يد ملك اشور"));
arasuperobject.biblemap.put("b14_32_12", new arasuperobject(14,32,12,"أليس حزقيا هو الذي ازال مرتفعاته ومذابحه وكلم يهوذا واورشليم قائلا امام مذبح واحد تسجدون وعليه توقدون"));
arasuperobject.biblemap.put("b14_32_13", new arasuperobject(14,32,13,"أما تعلمون ما فعلته انا وآبائي بجميع شعوب الاراضي. فهل قدرت آلهة امم الاراضي ان تنقذ ارضها من يدي"));
arasuperobject.biblemap.put("b14_32_14", new arasuperobject(14,32,14,"من من جميع آلهة هؤلاء الامم الذين حرمهم آبائي استطاع ان ينقذ شعبه من يدي حتى يستطيع الهكم ان ينقذكم من يدي"));
arasuperobject.biblemap.put("b14_32_15", new arasuperobject(14,32,15,"والآن لا يخدعنّكم حزقيا ولا يغوينّكم هكذا ولا تصدقوه. لانه لم يقدر اله امة او مملكة ان ينقذ شعبه من يدي ويد آبائي. فكم بالحري الهكم لا ينقذكم من يدي"));
arasuperobject.biblemap.put("b14_32_16", new arasuperobject(14,32,16,"وتكلم عبيده اكثر ضد الرب الاله وضد حزقيا عبده"));
arasuperobject.biblemap.put("b14_32_17", new arasuperobject(14,32,17,"وكتب رسائل لتعيير الرب اله اسرائيل وللتكلم ضده قائلا كما ان آلهة امم الاراضي لم تنقذ شعوبها من يدي كذلك لا ينقذ اله حزقيا شعبه من يدي"));
arasuperobject.biblemap.put("b14_32_18", new arasuperobject(14,32,18,"وصرخوا بصوت عظيم باليهودي الى شعب اورشليم الذين على السور لتخويفهم وترويعهم لكي ياخذوا المدينة"));
arasuperobject.biblemap.put("b14_32_19", new arasuperobject(14,32,19,"وتكلموا على اله اورشليم كما على آلهة شعوب الارض صنعة ايدي الناس"));
arasuperobject.biblemap.put("b14_32_20", new arasuperobject(14,32,20,"فصلّى حزقيا الملك واشعياء بن آموص النبي لذلك وصرخا الى السماء"));
arasuperobject.biblemap.put("b14_32_21", new arasuperobject(14,32,21,"فارسل الرب ملاكا فاباد كل جبار بأس ورئيس وقائد في محلّة ملك اشور. فرجع بخزي الوجه الى ارضه. ولما دخل بيت الهه قتله هناك بالسيف الذين خرجوا من احشائه"));
arasuperobject.biblemap.put("b14_32_22", new arasuperobject(14,32,22,"وخلص الرب حزقيا وسكان اورشليم من سنحاريب ملك اشور ومن يد الجميع وحماهم من كل ناحية"));
arasuperobject.biblemap.put("b14_32_23", new arasuperobject(14,32,23,"وكان كثيرون ياتون بتقدمات الرب الى اورشليم وتحف لحزقيا ملك يهوذا واعتبر في اعين جميع الامم بعد ذلك"));
arasuperobject.biblemap.put("b14_32_24", new arasuperobject(14,32,24,"في تلك الايام مرض حزقيا الى حد الموت وصلّى الى الرب فكلمه واعطاه علامة"));
arasuperobject.biblemap.put("b14_32_25", new arasuperobject(14,32,25,"ولكن لم يردّ حزقيا حسبما أنعم عليه لان قلبه ارتفع فكان غضب عليه وعلى يهوذا واورشليم"));
arasuperobject.biblemap.put("b14_32_26", new arasuperobject(14,32,26,"ثم تواضع حزقيا بسبب ارتفاع قلبه هو وسكان اورشليم فلم يأت عليهم غضب الرب في ايام حزقيا"));
arasuperobject.biblemap.put("b14_32_27", new arasuperobject(14,32,27,"وكان لحزقيا غنى وكرامة كثيرة جدا وعمل لنفسه خزائن للفضة والذهب والحجارة الكريمة والاطياب والاتراس وكل آنية ثمينة"));
arasuperobject.biblemap.put("b14_32_28", new arasuperobject(14,32,28,"ومخازن لغلة الحنطة والمسطار والزيت واواري لكل انواع البهائم وللقطعان اواري"));
arasuperobject.biblemap.put("b14_32_29", new arasuperobject(14,32,29,"وعمل لنفسه ابراجا ومواشي غنم وبقر بكثرة لان الله اعطاه اموالا كثيرة جدا"));
arasuperobject.biblemap.put("b14_32_30", new arasuperobject(14,32,30,"وحزقيا هذا سد مخرج مياه جيحون الاعلى واجراها تحت الارض الى الجهة الغربية من مدينة داود. وافلح حزقيا في كل عمله"));
arasuperobject.biblemap.put("b14_32_31", new arasuperobject(14,32,31,"وهكذا في أمر تراجم رؤساء بابل الذين ارسلوا اليه ليسألوا عن الاعجوبة التي كانت في الارض تركه الله ليجربه ليعلم كل ما في قلبه"));
arasuperobject.biblemap.put("b14_32_32", new arasuperobject(14,32,32,"وبقيت امور حزقيا ومراحمه ها هي مكتوبة في رؤيا اشعياء بن آموص النبي في سفر ملوك يهوذا واسرائيل"));
arasuperobject.biblemap.put("b14_32_33", new arasuperobject(14,32,33,"ثم اضطجع حزقيا مع آبائه فدفنوه في عقبة قبور بني داود وعمل له اكراما عند موته كل يهوذا وسكان اورشليم. وملك منسّى ابنه عوضا عنه"));
arasuperobject.biblemap.put("b14_33_1", new arasuperobject(14,33,1,"كان منسّى ابن اثنتي عشرة سنة حين ملك وملك خمسا وخمسين سنة في اورشليم"));
arasuperobject.biblemap.put("b14_33_2", new arasuperobject(14,33,2,"وعمل الشر في عيني الرب حسب رجاسات الامم الذين طردهم الرب من امام بني اسرئيل"));
arasuperobject.biblemap.put("b14_33_3", new arasuperobject(14,33,3,"وعاد فبنى المرتفعات التي هدمها حزقيا ابوه واقام مذابح للبعليم وعمل سواري وسجد لكل جند السماء وعبدها"));
arasuperobject.biblemap.put("b14_33_4", new arasuperobject(14,33,4,"وبنى مذابح في بيت الرب الذي قال عنه الرب في اورشليم يكون اسمي الى الابد"));
arasuperobject.biblemap.put("b14_33_5", new arasuperobject(14,33,5,"وبنى مذابح لكل جند السماء في داري بيت الرب"));
arasuperobject.biblemap.put("b14_33_6", new arasuperobject(14,33,6,"وعبّر بنيه في النار في وادي ابن هنّوم وعاف وتفائل وسحر واستخدم جانا وتابعة واكثر عمل الشرّ في عيني الرب لاغاظته"));
arasuperobject.biblemap.put("b14_33_7", new arasuperobject(14,33,7,"ووضع تمثال الشكل الذي عمله في بيت الله الذي قال الله عنه لداود ولسليمان ابنه في هذا البيت وفي اورشليم التي اخترت من جميع اسباط اسرائيل اضع اسمي الى الابد"));
arasuperobject.biblemap.put("b14_33_8", new arasuperobject(14,33,8,"ولا اعود ازحزح رجل اسرائيل عن الارض التي عيّنت لآبائهم وذلك اذا حفظوا وعملوا كل ما اوصيتهم به كل الشريعة والفرائض والاحكام عن يد موسى"));
arasuperobject.biblemap.put("b14_33_9", new arasuperobject(14,33,9,"ولكن منسّى اضلّ يهوذا وسكان اورشليم ليعملوا اشر من الامم الذين طردهم الرب من امام بني اسرائيل"));
arasuperobject.biblemap.put("b14_33_10", new arasuperobject(14,33,10,"وكلّم الرب منسّى وشعبه فلم يصغوا"));
arasuperobject.biblemap.put("b14_33_11", new arasuperobject(14,33,11,"فجلب الرب عليهم رؤساء الجند الذين لملك اشور فأخذوا منسّى بخزامة وقيدوه بسلاسل نحاس وذهبوا به الى بابل"));
arasuperobject.biblemap.put("b14_33_12", new arasuperobject(14,33,12,"ولما تضايق طلب وجه الرب الهه وتواضع جدا امام اله آبائه"));
arasuperobject.biblemap.put("b14_33_13", new arasuperobject(14,33,13,"وصلّى اليه فاستجاب له وسمع تضرعه ورده الى اورشليم الى مملكته. فعلم منسّى ان الرب هو الله"));
arasuperobject.biblemap.put("b14_33_14", new arasuperobject(14,33,14,"وبعد ذلك بنى سورا خارج مدينة داود غربا الى جيحون في الوادي والى مدخل باب السمك وحوّط الاكمة بسور وعلاه جدا. ووضع رؤساء جيوش في جميع المدن الحصينة في يهوذا"));
arasuperobject.biblemap.put("b14_33_15", new arasuperobject(14,33,15,"وازال الآلهة الغريبة والاشباه من بيت الرب وجميع المذابح التي بناها في جبل بيت الرب وفي اورشليم وطرحها خارج المدينة"));
arasuperobject.biblemap.put("b14_33_16", new arasuperobject(14,33,16,"ورمم مذبح الرب وذبح عليه ذبائح سلامة وشكر وأمر يهوذا ان يعبدوا الرب اله اسرائيل"));
arasuperobject.biblemap.put("b14_33_17", new arasuperobject(14,33,17,"الا ان الشعب كانوا بعد يذبحون على المرتفعات انما للرب الههم"));
arasuperobject.biblemap.put("b14_33_18", new arasuperobject(14,33,18,"وبقية امور منسّى وصلاته الى الهه وكلام الرائين الذين كلموه باسم الرب اله اسرائيل ها هي في اخبار ملوك اسرائيل"));
arasuperobject.biblemap.put("b14_33_19", new arasuperobject(14,33,19,"وصلاته والاستجابة له وكل خطاياه وخيانته والاماكن التي بنى فيها مرتفعات واقام سواري وتماثيل قبل تواضعه ها هي مكتوبة في اخبار الرائين"));
arasuperobject.biblemap.put("b14_33_20", new arasuperobject(14,33,20,"ثم اضطجع منسّى مع آبائه فدفنوه في بيته وملك آمون ابنه عوضا عنه"));
arasuperobject.biblemap.put("b14_33_21", new arasuperobject(14,33,21,"كان آمون ابن اثنتين وعشرين سنة حين ملك وملك سنتين في اورشليم"));
arasuperobject.biblemap.put("b14_33_22", new arasuperobject(14,33,22,"وعمل الشر في عيني الرب كما عمل منسّى ابوه. وذبح آمون لجميع التماثيل التي عمل منسّى ابوه وعبدها"));
arasuperobject.biblemap.put("b14_33_23", new arasuperobject(14,33,23,"ولم يتواضع امام الرب كما تواضع منسّى ابوه بل ازداد آمون اثما"));
arasuperobject.biblemap.put("b14_33_24", new arasuperobject(14,33,24,"وفتن عليه عبيده وقتلوه في بيته"));
arasuperobject.biblemap.put("b14_33_25", new arasuperobject(14,33,25,"وقتل شعب الارض جميع الفاتنين على الملك آمون وملّك شعب الارض يوشيا ابنه عوض عنه"));
arasuperobject.biblemap.put("b14_34_1", new arasuperobject(14,34,1,"كان يوشيا ابن ثماني سنين حين ملك وملك احدى وثلاثين سنة في اورشليم"));
arasuperobject.biblemap.put("b14_34_2", new arasuperobject(14,34,2,"وعمل المستقيم في عيني الرب وسار في طرق داود ابيه ولم يحد يمينا ولا شمالا"));
arasuperobject.biblemap.put("b14_34_3", new arasuperobject(14,34,3,"وفي السنة الثامنة من ملكه اذ كان بعد فتى ابتدأ يطلب اله داود ابيه. وفي السنة الثانية عشرة ابتدأ يطهر يهوذا واورشليم من المرتفعات والسواري والتماثيل والمسبوكات"));
arasuperobject.biblemap.put("b14_34_4", new arasuperobject(14,34,4,"وهدموا امامه مذابح البعليم وتماثيل الشمس التي عليها من فوق قطعها وكسر السواري والتماثيل والمسبوكات ودقّها ورشّها على قبور الذين ذبحوا لها"));
arasuperobject.biblemap.put("b14_34_5", new arasuperobject(14,34,5,"واحرق عظام الكهنة على مذابحهم وطهر يهوذا واورشليم"));
arasuperobject.biblemap.put("b14_34_6", new arasuperobject(14,34,6,"وفي مدن منسّى وافرايم وشمعون حتى ونفتالي مع خرائبها حولها"));
arasuperobject.biblemap.put("b14_34_7", new arasuperobject(14,34,7,"هدم المذابح والسواري ودقّ التماثيل ناعما وقطع جميع تماثيل الشمس في كل ارض اسرائيل ثم رجع الى اورشليم"));
arasuperobject.biblemap.put("b14_34_8", new arasuperobject(14,34,8,"وفي السنة الثامنة عشرة من ملكه بعد ان طهر الارض والبيت ارسل شافان بن اصليا ومعسيا رئيس المدينة ويوآخ بن يوآحاز المسجّل لاجل ترميم بيت الرب الهه"));
arasuperobject.biblemap.put("b14_34_9", new arasuperobject(14,34,9,"فجاءوا الى حلقيا الكاهن العظيم واعطوه الفضة المدخلة الى بيت الله التي جمعها اللاويون حارسوا الباب من منسّى وافرايم ومن كل بقية اسرائيل ومن كل يهوذا وبنيامين ثم رجعوا الى اورشليم"));
arasuperobject.biblemap.put("b14_34_10", new arasuperobject(14,34,10,"ودفعوها لايدي عاملي الشغل الموكلين في بيت الرب فدفعوها لعاملي الشغل الذين كانوا يعملون في بيت الرب لاجل اصلاح البيت وترميمه"));
arasuperobject.biblemap.put("b14_34_11", new arasuperobject(14,34,11,"واعطوها للنجارين والبنائين ليشتروا حجارة منحوتة واخشابا للوصل ولاجل تسقيف البيوت التي اخربها ملوك يهوذا"));
arasuperobject.biblemap.put("b14_34_12", new arasuperobject(14,34,12,"وكان الرجال يعملون العمل بامانة وعليهم وكلاء يحث وعوبديا اللاويان من بني مراري وزكريا ومشلام من بني القهاتيين لاجل المناظرة ومن اللاويين كل ماهر بآلات الغناء"));
arasuperobject.biblemap.put("b14_34_13", new arasuperobject(14,34,13,"وكانوا على الحمّال ووكلاء كل عامل شغل في خدمة فخدمة. وكان من اللاويين كتاب وعرفاء وبوابون"));
arasuperobject.biblemap.put("b14_34_14", new arasuperobject(14,34,14,"وعند اخراجهم الفضة المدخلة الى بيت الرب وجد حلقيا الكاهن سفر شريعة الرب بيد موسى"));
arasuperobject.biblemap.put("b14_34_15", new arasuperobject(14,34,15,"فاجاب حلقيا وقال لشافان الكاتب قد وجدت سفر الشريعة في بيت الرب. وسلّم حلقيا السفر الى شافان"));
arasuperobject.biblemap.put("b14_34_16", new arasuperobject(14,34,16,"فجاء شافان بالسفر الى الملك ورد الى الملك جوابا قائلا كل ما اسلم ليد عبيدك هم يفعلونه"));
arasuperobject.biblemap.put("b14_34_17", new arasuperobject(14,34,17,"وقد افرغوا الفضة الموجودة في بيت الرب ودفعوها ليد الوكلاء ويد عاملي الشغل"));
arasuperobject.biblemap.put("b14_34_18", new arasuperobject(14,34,18,"واخبر شافان الكاتب الملك قائلا قد اعطاني حلقيا الكاهن سفرا. وقرأ فيه شافان امام الملك"));
arasuperobject.biblemap.put("b14_34_19", new arasuperobject(14,34,19,"فلما سمع الملك كلام الشريعة مزّق ثيابه"));
arasuperobject.biblemap.put("b14_34_20", new arasuperobject(14,34,20,"وامر الملك حلقيا واخيقام بن شافان وعبدون بن ميخا وشافان الكاتب وعسايا عبد الملك قائلا"));
arasuperobject.biblemap.put("b14_34_21", new arasuperobject(14,34,21,"اذهبوا اسألوا الرب من اجلي ومن اجل من بقي من اسرائيل ويهوذا عن كلام السفر الذي وجد لانه عظيم غضب الرب الذي انسكب علينا من اجل ان آبائنا لم يحفظوا كلام الرب ليعملوا حسب كل ما هو مكتوب في هذا السفر"));
arasuperobject.biblemap.put("b14_34_22", new arasuperobject(14,34,22,"فذهب حلقيا والذين امرهم الملك الى خلدة النبية امرأة شلّوم بن توقهة بن حسرة حارس الثياب. وهي ساكنة في اورشليم في القسم الثاني. وكلّموها هكذا"));
arasuperobject.biblemap.put("b14_34_23", new arasuperobject(14,34,23,"فقالت لهم هكذا قال الرب اله اسرائيل قولوا للرجل الذي ارسلكم اليّ"));
arasuperobject.biblemap.put("b14_34_24", new arasuperobject(14,34,24,"هكذا قال الرب هانذا جالب شرا على هذا الموضع وعلى سكانه جميع اللعنات المكتوبة في السفر الذي قرأوه امام ملك يهوذا"));
arasuperobject.biblemap.put("b14_34_25", new arasuperobject(14,34,25,"من اجل انهم تركوني واوقدوا لآلهة اخرى لكي يغيظوني بكل اعمال ايديهم وينسكب غضبي على هذا الموضع ولا ينطفئ"));
arasuperobject.biblemap.put("b14_34_26", new arasuperobject(14,34,26,"واما ملك يهوذا الذي ارسلكم لتسألوا من الرب فهكذا تقولون له. هكذا قال الرب اله اسرائيل من جهة الكلام الذي سمعت"));
arasuperobject.biblemap.put("b14_34_27", new arasuperobject(14,34,27,"من اجل انه قد رقّ قلبك وتواضعت امام الله حين سمعت كلامه على هذا الموضع وعلى سكانه وتواضعت امامي ومزقت ثيابك وبكيت امامي يقول الرب قد سمعت انا ايضا"));
arasuperobject.biblemap.put("b14_34_28", new arasuperobject(14,34,28,"هانذا اضمّك الى آبائك فتضمّ الى قبرك بسلام وكل الشر الذي اجلبه على هذا الموضع وعلى سكانه لا ترى عيناك. فردّوا على الملك الجواب"));
arasuperobject.biblemap.put("b14_34_29", new arasuperobject(14,34,29,"وارسل الملك وجمع كل شيوخ يهوذا واورشليم"));
arasuperobject.biblemap.put("b14_34_30", new arasuperobject(14,34,30,"وصعد الملك الى بيت الرب مع كل رجال يهوذا وسكان اورشليم والكهنة واللاويين وكل الشعب من الكبير الى الصغير وقرأ في آذانهم كل كلام سفر العهد الذي وجد في بيت الرب"));
arasuperobject.biblemap.put("b14_34_31", new arasuperobject(14,34,31,"ووقف الملك على منبره وقطع عهدا امام الرب للذهاب وراء الرب ولحفظ وصاياه وشهاداته وفرائضه بكل قلبه وكل نفسه ليعمل كلام العهد المكتوب في هذا السفر"));
arasuperobject.biblemap.put("b14_34_32", new arasuperobject(14,34,32,"واوقف كل الموجودين في اورشليم وبنيامين فعمل سكان اورشليم حسب عهد الله اله آبائهم"));
arasuperobject.biblemap.put("b14_34_33", new arasuperobject(14,34,33,"وازال يوشيا جميع الرجاسات من كل الاراضي التي لبني اسرائيل وجعل جميع الموجودين في اورشليم يعبدون الرب الههم. كل ايامه لم يحيدوا من وراء الرب اله آبائهم"));
arasuperobject.biblemap.put("b14_35_1", new arasuperobject(14,35,1,"وعمل يوشيا في اورشليم فصحا للرب وذبحوا الفصح في الرابع عشر من الشهر الاول"));
arasuperobject.biblemap.put("b14_35_2", new arasuperobject(14,35,2,"واقام الكهنة على حراساتهم وشدّدهم لخدمة بيت الرب"));
arasuperobject.biblemap.put("b14_35_3", new arasuperobject(14,35,3,"وقال للاويين الذين كانوا يعلمون كل اسرائيل الذين كانوا مقدسين للرب اجعلوا تابوت القدس في البيت الذي بناه سليمان بن داود ملك اسرائيل. ليس لكم ان تحملوا على الاكتاف. الآن اخدموا الرب الهكم وشعبه اسرائيل"));
arasuperobject.biblemap.put("b14_35_4", new arasuperobject(14,35,4,"واعدّوا بيوت آبائكم حسب فرقكم حسب كتابة داود ملك اسرائيل وحسب كتابة سليمان ابنه"));
arasuperobject.biblemap.put("b14_35_5", new arasuperobject(14,35,5,"وقفوا في القدس حسب اقسام بيوت آباء اخوتكم بني الشعب وفرق بيوت آباء اللاويين"));
arasuperobject.biblemap.put("b14_35_6", new arasuperobject(14,35,6,"واذبحوا الفصح وتقدسوا واعدّوا اخوتكم ليعملوا حسب كلام الرب عن يد موسى"));
arasuperobject.biblemap.put("b14_35_7", new arasuperobject(14,35,7,"واعطى يوشيا لبني الشعب غنما حملانا وجداء جميع ذلك للفصح لكل الموجودين الى عدد ثلاثين الفا وثلاثة آلاف من البقر. هذه من مال الملك"));
arasuperobject.biblemap.put("b14_35_8", new arasuperobject(14,35,8,"ورؤساؤه قدموا تبرعا للشعب والكهنة واللاويين حلقيا وزكريا ويحيئيل رؤساء بيت الله. اعطوا الكهنة للفصح الفين وست مئة ومن البقر ثلاث مئة"));
arasuperobject.biblemap.put("b14_35_9", new arasuperobject(14,35,9,"وكوننيا وشمعيا ونثنئيل اخواه وحشبيا ويعيئيل ويوزاباد رؤساء اللاويين قدموا للاويين للفصح خمسة آلاف ومن البقر خمس مئة"));
arasuperobject.biblemap.put("b14_35_10", new arasuperobject(14,35,10,"فتهيّأت الخدمة وقام الكهنة في مقامهم واللاويين في فرقهم حسب امر الملك"));
arasuperobject.biblemap.put("b14_35_11", new arasuperobject(14,35,11,"وذبحوا الفصح ورشّ الكهنة من ايديهم. واما اللاويون فكانوا يسلخون"));
arasuperobject.biblemap.put("b14_35_12", new arasuperobject(14,35,12,"ورفعوا المحرقة ليعطوا حسب اقسام بيوت الآباء لبني الشعب ليقربوا للرب كما هو مكتوب في سفر موسى. وهكذا بالبقر"));
arasuperobject.biblemap.put("b14_35_13", new arasuperobject(14,35,13,"وشووا الفصح بالنار كالمرسوم. واما الاقداس فطبخوها في القدور والمراجل والصحاف وبادروا بها الى جميع بني الشعب"));
arasuperobject.biblemap.put("b14_35_14", new arasuperobject(14,35,14,"وبعد اعدّوا لانفسهم وللكهنة لان الكهنة بني هرون كانوا على اصعاد المحرقة والشحم الى الليل فاعدّ اللاويون لانفسهم وللكهنة بني هرون"));
arasuperobject.biblemap.put("b14_35_15", new arasuperobject(14,35,15,"والمغنون بنو آساف كانوا في مقامهم حسب امر داود وآساف وهيمان ويدوثون رائي الملك. والبوابون على باب فباب لم يكن لهم ان يحيدوا عن خدمتهم لان اخوتهم اللاويين اعدّوا لهم"));
arasuperobject.biblemap.put("b14_35_16", new arasuperobject(14,35,16,"فتهيّأ كل خدمة الرب في ذلك اليوم لعمل الفصح واصعاد المحرقات على مذبح الرب حسب امر الملك يوشيا"));
arasuperobject.biblemap.put("b14_35_17", new arasuperobject(14,35,17,"وعمل بنو اسرائيل الموجودون الفصح في ذلك اليوم وعيد الفطير سبعة ايام"));
arasuperobject.biblemap.put("b14_35_18", new arasuperobject(14,35,18,"ولم يعمل فصح مثله في اسرائيل من ايام صموئيل النبي. وكل ملوك اسرائيل لم يعملوا كالفصح الذي عمله يوشيا والكهنة واللاويون وكل يهوذا واسرائيل الموجودين وسكان اورشليم"));
arasuperobject.biblemap.put("b14_35_19", new arasuperobject(14,35,19,"في السنة الثامنة عشر للملك يوشيا عمل هذا الفصح"));
arasuperobject.biblemap.put("b14_35_20", new arasuperobject(14,35,20,"بعد كل هذا حين هيّأ يوشيا البيت صعد نخو ملك مصر الى كركميش ليحارب عند الفرات. فخرج يوشيا للقائه"));
arasuperobject.biblemap.put("b14_35_21", new arasuperobject(14,35,21,"فارسل اليه رسلا يقول ما لي ولك يا ملك يهوذا. لست عليك انت اليوم ولكن على بيت حربي والله امر باسراعي. فكف عن الله الذي معي فلا يهلكك"));
arasuperobject.biblemap.put("b14_35_22", new arasuperobject(14,35,22,"ولم يحوّل يوشيا وجهه عنه بل تنكّر لمقاتلته ولم يسمع لكلام نخو من فم الله بل جاء ليحارب في بقعة مجدو"));
arasuperobject.biblemap.put("b14_35_23", new arasuperobject(14,35,23,"واصاب الرماة الملك يوشيا فقال الملك لعبيده انقلوني لاني جرحت جدا"));
arasuperobject.biblemap.put("b14_35_24", new arasuperobject(14,35,24,"فنقله عبيده من المركبة واركبوه على المركبة الثانية التي له وساروا به الى اورشليم فمات ودفن في قبور آبائه. وكان كل يهوذا واورشليم ينوحون على يوشيا"));
arasuperobject.biblemap.put("b14_35_25", new arasuperobject(14,35,25,"ورثى ارميا يوشيا. وكان جميع المغنين والمغنيات يندبون يوشيا في مراثيهم الى اليوم. وجعلوها فريضة على اسرائيل. وها هي مكتوبة في المراثي"));
arasuperobject.biblemap.put("b14_35_26", new arasuperobject(14,35,26,"وبقية امور يوشيا ومراحمه حسبما هو مكتوب في ناموس الرب"));
arasuperobject.biblemap.put("b14_35_27", new arasuperobject(14,35,27,"واموره الاولى والاخيرة ها هي مكتوبة في سفر ملوك اسرائيل ويهوذا"));
arasuperobject.biblemap.put("b14_36_1", new arasuperobject(14,36,1,"واخذ شعب الارض يهوآحاز بن يوشيا وملّكوه عوضا عن ابيه في اورشليم"));
arasuperobject.biblemap.put("b14_36_2", new arasuperobject(14,36,2,"وكان يوآحاز ابن ثلاث وعشرين سنة حين ملك وملك ثلاثة اشهر في اورشليم"));
arasuperobject.biblemap.put("b14_36_3", new arasuperobject(14,36,3,"وعزله ملك مصر في اورشليم وغرّم الارض بمئة وزنة من الفضة وبوزنة من الذهب"));
arasuperobject.biblemap.put("b14_36_4", new arasuperobject(14,36,4,"وملك ملك مصر الياقيم اخاه على يهوذا واورشليم وغيّر اسمه الى يهوياقيم. واما يوآحاز اخوه فاخذه نخو وأتى به الى مصر"));
arasuperobject.biblemap.put("b14_36_5", new arasuperobject(14,36,5,"كان يهوياقيم ابن خمس وعشرين سنة حين ملك وملك احدى عشر سنة في اورشليم وعمل الشر في عيني الرب الهه"));
arasuperobject.biblemap.put("b14_36_6", new arasuperobject(14,36,6,"عليه صعد نبوخذناصّر ملك بابل وقيّده بسلاسل نحاس ليذهب به الى بابل"));
arasuperobject.biblemap.put("b14_36_7", new arasuperobject(14,36,7,"وأتى نبوخذناصّر ببعض آنية بيت الرب الى بابل وجعلها في هيكله في بابل"));
arasuperobject.biblemap.put("b14_36_8", new arasuperobject(14,36,8,"وبقية امور يهوياقيم ورجاساته التي عمل وما وجد فيه ها هي مكتوبة في سفر ملوك اسرائيل ويهوذا. وملك يهوياكين ابنه عوضا عنه"));
arasuperobject.biblemap.put("b14_36_9", new arasuperobject(14,36,9,"كان يهوياكين ابن ثماني سنين حين ملك وملك ثلاثة اشهر وعشرة ايام في اورشليم. وعمل الشر في عيني الرب"));
arasuperobject.biblemap.put("b14_36_10", new arasuperobject(14,36,10,"وعند رجوع السنة ارسل الملك نبوخذناصّر فأتى به الى بابل مع آنية بيت الرب الثمينة وملك صدقيا اخاه على يهوذا واورشليم"));
arasuperobject.biblemap.put("b14_36_11", new arasuperobject(14,36,11,"كان صدقيا ابن احدى وعشرين سنة حين ملك وملك احدى عشرة سنة في اورشليم"));
arasuperobject.biblemap.put("b14_36_12", new arasuperobject(14,36,12,"وعمل الشر في عيني الرب الهه ولم يتواضع امام ارميا النبي من فم الرب"));
arasuperobject.biblemap.put("b14_36_13", new arasuperobject(14,36,13,"وتمرّد ايضا على الملك نبوخذناصّر الذي حلّفه بالله وصلّب عنقه وقوّى قلبه عن الرجوع الى الرب اله اسرائيل"));
arasuperobject.biblemap.put("b14_36_14", new arasuperobject(14,36,14,"حتى ان جميع رؤساء الكهنة والشعب اكثروا الخيانة حسب كل رجاسات الامم ونجسوا بيت الرب الذي قدسه في اورشليم"));
arasuperobject.biblemap.put("b14_36_15", new arasuperobject(14,36,15,"فارسل الرب اله آبائهم اليهم عن يد رسله مبكرا ومرسلا لانه شفق على شعبه وعلى مسكنه"));
arasuperobject.biblemap.put("b14_36_16", new arasuperobject(14,36,16,"فكانوا يهزأون برسل الله ورذلوا كلامه وتهاونوا بانبيائه حتى ثار غضب الرب على شعبه حتى لم يكن شفاء"));
arasuperobject.biblemap.put("b14_36_17", new arasuperobject(14,36,17,"فاصعد عليهم ملك الكلدانيين فقتل مختاريهم بالسيف في بيت مقدسهم. ولم يشفق على فتى او عذراء ولا على شيخ او اشيب بل دفع الجميع ليده"));
arasuperobject.biblemap.put("b14_36_18", new arasuperobject(14,36,18,"وجميع آنية بيت الله الكبيرة والصغيرة وخزائن بيت الرب وخزائن الملك ورؤسائه اتى بها جميعا الى بابل"));
arasuperobject.biblemap.put("b14_36_19", new arasuperobject(14,36,19,"واحرقوا بيت الله وهدموا سور اورشليم واحرقوا جميع قصورها بالنار واهلكوا جميع آنيتها الثمينة"));
arasuperobject.biblemap.put("b14_36_20", new arasuperobject(14,36,20,"وسبى الذين بقوا من السيف الى بابل فكانوا له ولبنيه عبيدا الى ان ملكت مملكة فارس"));
arasuperobject.biblemap.put("b14_36_21", new arasuperobject(14,36,21,"لاكمال كلام الرب بفم ارميا حتى استوفت الارض سبوتها لانها سبتت في كل ايام خرابها لاكمال سبعين سنة"));
arasuperobject.biblemap.put("b14_36_22", new arasuperobject(14,36,22,"وفي السنة الاولى لكورش ملك فارس لاجل تكميل كلام الرب بفم ارميا نبّه الرب روح كورش ملك فارس فاطلق نداء في كل مملكته وكذا بالكتابة قائلا"));
arasuperobject.biblemap.put("b14_36_23", new arasuperobject(14,36,23,"هكذا قال كورش ملك فارس ان الرب اله السماء قد اعطاني جميع ممالك الارض وهو اوصاني ان ابني له بيتا في اورشليم التي في يهوذا. من منكم من جميع شعبه الرب الهه معه وليصعد"));

