arasuperobject.biblemap.put("b16_4_20", new arasuperobject(16,4,20,"فالمكان الذي تسمعون منه صوت البوق هناك تجتمعون الينا. الهنا يحارب عنا"));
arasuperobject.biblemap.put("b16_4_21", new arasuperobject(16,4,21,"فكنا نحن نعمل العمل وكان نصفهم يمسكون الرماح من طلوع الفجر الى ظهور النجوم"));
arasuperobject.biblemap.put("b16_4_22", new arasuperobject(16,4,22,"وقلت في ذلك الوقت ايضا للشعب ليبت كل واحد مع غلامه في وسط اورشليم ليكونوا لنا حراسا في الليل وللعمل في النهار"));
arasuperobject.biblemap.put("b16_4_23", new arasuperobject(16,4,23,"ولم اكن انا ولا اخوتي ولا غلماني ولا الحراس الذين ورائي نخلع ثيابنا. كان كل واحد يذهب بسلاحه الى الماء"));
arasuperobject.biblemap.put("b16_5_1", new arasuperobject(16,5,1,"وكان صراخ الشعب ونسائهم عظيما على اخوتهم اليهود"));
arasuperobject.biblemap.put("b16_5_2", new arasuperobject(16,5,2,"وكان من يقول بنونا وبناتنا نحن كثيرون. دعنا نأخذ قمحا فنأكل ونحيا"));
arasuperobject.biblemap.put("b16_5_3", new arasuperobject(16,5,3,"وكان من يقول حقولنا وكرومنا وبيوتنا نحن راهنوها حتى نأخذ قمحا في الجوع"));
arasuperobject.biblemap.put("b16_5_4", new arasuperobject(16,5,4,"وكان من يقول قد استقرضنا فضة لخراج الملك على حقولنا وكرومنا"));
arasuperobject.biblemap.put("b16_5_5", new arasuperobject(16,5,5,"والآن لحمنا كلحم اخوتنا وبنونا كبنيهم وها نحن نخضع بنينا وبناتنا عبيدا ويوجد من بناتنا مستعبدات وليس شيء في طاقة يدنا وحقولنا وكرومنا للآخرين"));
arasuperobject.biblemap.put("b16_5_6", new arasuperobject(16,5,6,"فغضبت جدا حين سمعت صراخهم وهذا الكلام"));
arasuperobject.biblemap.put("b16_5_7", new arasuperobject(16,5,7,"فشاورت قلبي فيّ وبكّت العظماء والولاة وقلت لهم انكم تاخذون الربا كل واحد من اخيه واقمت عليهم جماعة عظيمة"));
arasuperobject.biblemap.put("b16_5_8", new arasuperobject(16,5,8,"وقلت لهم نحن اشترينا اخوتنا اليهود الذين بيعوا للامم حسب طاقتنا. وانتم ايضا تبيعون اخوتكم فيباعون لنا. فسكتوا ولم يجدوا جوابا"));
arasuperobject.biblemap.put("b16_5_9", new arasuperobject(16,5,9,"وقلت ليس حسنا الامر الذي تعملونه. أما تسيرون بخوف الهنا بسبب تعيير الامم اعدائنا"));
arasuperobject.biblemap.put("b16_5_10", new arasuperobject(16,5,10,"وانا ايضا واخوتي وغلماني اقرضناهم فضة وقمحا. فلنترك هذا الربا"));
arasuperobject.biblemap.put("b16_5_11", new arasuperobject(16,5,11,"ردّوا لهم هذا اليوم حقولهم وكرومهم وزيتونهم وبيوتهم والجزء من مئة الفضة والقمح والخمر والزيت الذي تاخذونه منهم ربا"));
arasuperobject.biblemap.put("b16_5_12", new arasuperobject(16,5,12,"فقالوا نرد ولا نطلب منهم. هكذا نفعل كما تقول. فدعوت الكهنة واستحلفتهم ان يعملوا حسب هذا الكلام"));
arasuperobject.biblemap.put("b16_5_13", new arasuperobject(16,5,13,"ثم نفضت حجري وقلت هكذا ينفض الله كل انسان لا يقيم هذا الكلام من بيته ومن تعبه وهكذا يكون منفوضا وفارغا. فقال كل الجماعة آمين وسبحوا الرب. وعمل الشعب حسب هذا الكلام"));
arasuperobject.biblemap.put("b16_5_14", new arasuperobject(16,5,14,"وايضا من اليوم الذي اوصيت فيه ان اكون واليهم في ارض يهوذا من السنة العشرين الى السنة الثانية والثلاثين لارتحشستا الملك اثنتي عشرة سنة لم آكل انا ولا اخوتي خبز الوالي"));
arasuperobject.biblemap.put("b16_5_15", new arasuperobject(16,5,15,"ولكن الولاة الاولون الذين قبلي ثقلوا على الشعب واخذوا منهم خبزا وخمرا فضلا عن اربعين شاقلا من الفضة حتى ان غلمانهم تسلطوا على الشعب. واما انا فلم افعل هكذا من اجل خوف الله"));
arasuperobject.biblemap.put("b16_5_16", new arasuperobject(16,5,16,"وتمسكت ايضا بشغل هذا السور. ولم اشتر حقلا. وكان جميع غلماني مجتمعين هناك على العمل"));
arasuperobject.biblemap.put("b16_5_17", new arasuperobject(16,5,17,"وكان على مائدتي من اليهود والولاة مئة وخمسون رجلا فضلا عن الآتين الينا من الامم الذين حولنا"));
arasuperobject.biblemap.put("b16_5_18", new arasuperobject(16,5,18,"وكان ما يعمل ليوم واحد ثورا وستة خراف مختارة. وكان يعمل لي طيور وفي كل عشرة ايام كل نوع من الخمر بكثرة. ومع هذا لم اطلب خبز الوالي لان العبودية كانت ثقيلة على هذا الشعب"));
arasuperobject.biblemap.put("b16_5_19", new arasuperobject(16,5,19,"اذكر لي يا الهي للخير كل ما عملت لهذا الشعب"));
arasuperobject.biblemap.put("b16_6_1", new arasuperobject(16,6,1,"ولما سمع سنبلّط وطوبيا وجشم العربي وبقية اعدائنا اني قد بنيت السور ولم تبق فيه ثغرة. على اني لم اكن الى ذلك الوقت قد اقمت مصاريع للابواب"));
arasuperobject.biblemap.put("b16_6_2", new arasuperobject(16,6,2,"ارسل سنبلط وجشم اليّ قائلين هلم نجتمع معا في القرى في بقعة اونو. وكانا يفكران ان يعملا بي شرا"));
arasuperobject.biblemap.put("b16_6_3", new arasuperobject(16,6,3,"فارسلت اليهما رسلا قائلا اني انا عامل عملا عظيما فلا اقدر ان انزل. لماذا يبطل العمل بينما اتركه وانزل اليكما"));
arasuperobject.biblemap.put("b16_6_4", new arasuperobject(16,6,4,"وارسلا اليّ بمثل هذا الكلام اربع مرات وجاوبتهما بمثل هذا الجواب"));
arasuperobject.biblemap.put("b16_6_5", new arasuperobject(16,6,5,"فارسل اليّ سنبلط بمثل هذا الكلام مرة خامسة مع غلامه برسالة منشورة بيده مكتوب فيها"));
arasuperobject.biblemap.put("b16_6_6", new arasuperobject(16,6,6,"قد سمع بين الامم وجشم يقول انك انت واليهود تفكرون ان تتمردوا لذلك انت تبني السور لتكون لهم ملكا حسب هذه الامور"));
arasuperobject.biblemap.put("b16_6_7", new arasuperobject(16,6,7,"وقد اقمت ايضا انبياء لينادوا بك في اورشليم قائلين في يهوذا ملك. والآن يخبر الملك بهذا الكلام. فهلم الآن نتشاور معا"));
arasuperobject.biblemap.put("b16_6_8", new arasuperobject(16,6,8,"فارسلت اليه قائلا لا يكون مثل هذا الكلام الذي تقوله بل انما انت مختلقه من قلبك"));
arasuperobject.biblemap.put("b16_6_9", new arasuperobject(16,6,9,"لانهم كانوا جميعا يخيفوننا قائلين قد ارتخت ايديهم عن العمل فلا يعمل. فالآن يا الهي شدّد يديّ"));
arasuperobject.biblemap.put("b16_6_10", new arasuperobject(16,6,10,"ودخلت بيت شمعيا بن دلايا بن مهيطبئيل وهو مغلق فقال لنجتمع الى بيت الله الى وسط الهيكل ونقفل ابواب الهيكل لانهم يأتون ليقتلوك. في الليل يأتون ليقتلوك"));
arasuperobject.biblemap.put("b16_6_11", new arasuperobject(16,6,11,"فقلت أرجل مثلي يهرب ومن مثلي يدخل الهيكل فيحيا. لا ادخل"));
arasuperobject.biblemap.put("b16_6_12", new arasuperobject(16,6,12,"فتحققت وهوذا لم يرسله الله لانه تكلم بالنبوة عليّ وطوبيا وسنبلّط قد استأجراه"));
arasuperobject.biblemap.put("b16_6_13", new arasuperobject(16,6,13,"لاجل هذا قد استؤجر لكي اخاف وافعل هكذا واخطئ فيكون لهما خبر رديء لكي يعيراني"));
arasuperobject.biblemap.put("b16_6_14", new arasuperobject(16,6,14,"اذكر يا الهي طوبيا وسنبلّط حسب اعمالهما هذه ونوعدية النبية وباقي الانبياء الذين يخيفونني"));
arasuperobject.biblemap.put("b16_6_15", new arasuperobject(16,6,15,"وكمل السور في الخامس والعشرين من ايلول في اثنين وخمسين يوما"));
