arasuperobject.biblemap.put("b16_7_59", new arasuperobject(16,7,59,"بنو شفطيا بنو حطّيل بنو فوخرة الظباء بنو آمون"));
arasuperobject.biblemap.put("b16_7_60", new arasuperobject(16,7,60,"كل النثينيم وبني عبيد سليمان ثلاث مئة واثنان وتسعون"));
arasuperobject.biblemap.put("b16_7_61", new arasuperobject(16,7,61,"وهؤلاء هم الذين صعدوا من تل ملح وتل حرشا كروب وادون وامّير. ولم يستطيعوا ان يبيّنوا بيوت آبائهم ونسلهم هل هم من اسرائيل"));
arasuperobject.biblemap.put("b16_7_62", new arasuperobject(16,7,62,"بنو دلايا بنو طوبيا بنو نقودا ست مئة واثنان واربعون"));
arasuperobject.biblemap.put("b16_7_63", new arasuperobject(16,7,63,"ومن الكهنة بنو حبابا بنو هقوص بنو برزلاي الذي اخذ امرأة من بنات برزلاي الجلعادي وتسمى باسمهم"));
arasuperobject.biblemap.put("b16_7_64", new arasuperobject(16,7,64,"هؤلاء فحصوا عن كتابة انسابهم فلم توجد فرذلوا من الكهنوت"));
arasuperobject.biblemap.put("b16_7_65", new arasuperobject(16,7,65,"وقال لهم الترشاثا ان لا يأكلوا من قدس الاقداس حتى يقوم كاهن للاوريم والتمّيم"));
arasuperobject.biblemap.put("b16_7_66", new arasuperobject(16,7,66,"كل الجمهور معا اربع ربوات والفان وثلاث مئة وستون"));
arasuperobject.biblemap.put("b16_7_67", new arasuperobject(16,7,67,"فضلا عن عبيدهم وامائهم الذين كانوا سبعة آلاف وثلاث مئة وسبعة وثلاثين. ولهم من المغنّين والمغنيات مئتان وخمسة واربعون"));
arasuperobject.biblemap.put("b16_7_68", new arasuperobject(16,7,68,"وخيلهم سبع مئة وستة وثلاثون وبغالهم مئتان وخمسة واربعون"));
arasuperobject.biblemap.put("b16_7_69", new arasuperobject(16,7,69,"والجمال اربع مئة وخمسة وثلاثون والحمير ستة آلاف وسبع مئة وعشرون"));
arasuperobject.biblemap.put("b16_7_70", new arasuperobject(16,7,70,"والبعض من رؤوس الآباء اعطوا للعمل. الترشاثا اعطى للخزينة الف درهم من الذهب وخمسين منضحة وخمس مئة وثلاثين قميصا للكهنة"));
arasuperobject.biblemap.put("b16_7_71", new arasuperobject(16,7,71,"والبعض من رؤوس الآباء اعطوا لخزينة العمل ربوتين من الذهب والفين ومئتي منا من الفضة"));
arasuperobject.biblemap.put("b16_7_72", new arasuperobject(16,7,72,"وما اعطاه بقية الشعب ست ربوات من الذهب والفي منا من الفضة وسبعة وستون قميصا للكهنة"));
arasuperobject.biblemap.put("b16_7_73", new arasuperobject(16,7,73,"واقام الكهنة واللاويون والبوابون والمغنون وبعض الشعب والنثينيم وكل اسرائيل في مدنهم ولما استهل الشهر السابع وبنو اسرائيل في مدنهم"));
arasuperobject.biblemap.put("b16_8_1", new arasuperobject(16,8,1,"اجتمع كل الشعب كرجل واحد الى الساحة التي امام باب الماء وقالوا لعزرا الكاتب ان يأتي بسفر شريعة موسى التي أمر بها الرب اسرائيل"));
arasuperobject.biblemap.put("b16_8_2", new arasuperobject(16,8,2,"فأتى عزرا الكاتب بالشريعة امام الجماعة من الرجال والنساء وكل فاهم ما يسمع في اليوم الاول من الشهر السابع"));
arasuperobject.biblemap.put("b16_8_3", new arasuperobject(16,8,3,"وقرأ فيها امام الساحة التي امام باب الماء من الصباح الى نصف النهار امام الرجال والنساء والفاهمين وكانت آذان كل الشعب نحو سفر الشريعة"));
arasuperobject.biblemap.put("b16_8_4", new arasuperobject(16,8,4,"ووقف عزرا الكاتب على منبر الخشب الذي عملوه لهذا الأمر ووقف بجانبه متثيا وشمع وعنايا واوريا وحلقيا ومعسيا عن يمينه وعن يساره فدايا وميشائيل وملكيا وحشوم وحشبدانة وزكريا ومشلام"));
arasuperobject.biblemap.put("b16_8_5", new arasuperobject(16,8,5,"وفتح عزرا السفر امام كل الشعب لانه كان فوق كل الشعب وعندما فتحه وقف كل الشعب"));
arasuperobject.biblemap.put("b16_8_6", new arasuperobject(16,8,6,"وبارك عزرا الرب الاله العظيم. واجاب جميع الشعب آمين آمين رافعين ايديهم وخرّوا وسجدوا للرب على وجوههم الى الارض"));
arasuperobject.biblemap.put("b16_8_7", new arasuperobject(16,8,7,"ويشوع وباني وشربيا ويامين وعقوب وشبتاي وهوديا ومعسيا وقليطا وعزريا ويوزاباد وحنان وفلايا واللاويون افهموا الشعب الشريعة والشعب في اماكنهم"));
arasuperobject.biblemap.put("b16_8_8", new arasuperobject(16,8,8,"وقرأوا في السفر في شريعة الله ببيان وفسروا المعنى وافهموهم القراءة"));
arasuperobject.biblemap.put("b16_8_9", new arasuperobject(16,8,9,"ونحميا اي الترشاثا وعزرا الكاهن الكاتب واللاويون المفهمون الشعب قالوا لجميع الشعب هذا اليوم مقدس للرب الهكم لا تنوحوا ولا تبكوا. لان جميع الشعب بكوا حين سمعوا كلام الشريعة"));
arasuperobject.biblemap.put("b16_8_10", new arasuperobject(16,8,10,"فقال لهم اذهبوا كلوا السمين واشربوا الحلو وابعثوا انصبة لمن لم يعدّ له لان اليوم انما هو مقدس لسيدنا ولا تحزنوا لان فرح الرب هو قوتكم"));
arasuperobject.biblemap.put("b16_8_11", new arasuperobject(16,8,11,"وكان اللاويون يسكتون كل الشعب قائلين اسكتوا لان اليوم مقدس فلا تحزنوا"));
arasuperobject.biblemap.put("b16_8_12", new arasuperobject(16,8,12,"فذهب كل الشعب ليأكلوا ويشربوا ويبعثوا انصبة ويعملوا فرحا عظيما لانهم فهموا الكلام الذي علموهم اياه"));
arasuperobject.biblemap.put("b16_8_13", new arasuperobject(16,8,13,"وفي اليوم الثاني اجتمع رؤوس آباء جميع الشعب والكهنة واللاويون الى عزرا الكاتب ليفهمهم كلام الشريعة"));
arasuperobject.biblemap.put("b16_8_14", new arasuperobject(16,8,14,"فوجدوا مكتوبا في الشريعة التي امر بها الرب عن يد موسى ان بني اسرائيل يسكنون في مظال في العيد في الشهر السابع"));
arasuperobject.biblemap.put("b16_8_15", new arasuperobject(16,8,15,"وان يسمعوا وينادوا في كل مدنهم وفي اورشليم قائلين اخرجوا الى الجبل وأتوا باغصان زيتون واغصان زيتون برّي واغصان آس واغصان نخل واغصان اشجار غبياء لعمل مظال كما هو مكتوب"));
arasuperobject.biblemap.put("b16_8_16", new arasuperobject(16,8,16,"فخرج الشعب وجلبوا وعملوا لانفسهم مظال كل واحد على سطحه وفي دورهم ودور بيت الله وفي ساحة باب الماء وفي ساحة باب افرايم"));
arasuperobject.biblemap.put("b16_8_17", new arasuperobject(16,8,17,"وعمل كل الجماعة الراجعين من السبي مظال وسكنوا في المظال لانه لم يعمل بنو اسرائيل هكذا من ايام يشوع بن نون الى ذلك اليوم وكان فرح عظيم جدا"));
arasuperobject.biblemap.put("b16_8_18", new arasuperobject(16,8,18,"وكان يقرأ في سفر شريعة الله يوما فيوما من اليوم الاول الى اليوم الاخير وعملوا عيدا سبعة ايام وفي اليوم الثامن اعتكاف حسب المرسوم"));
arasuperobject.biblemap.put("b16_9_1", new arasuperobject(16,9,1,"وفي اليوم الرابع والعشرين من هذا الشهر اجتمع بنو اسرائيل بالصوم وعليهم مسوح وتراب"));
arasuperobject.biblemap.put("b16_9_2", new arasuperobject(16,9,2,"وانفصل نسل اسرائيل من جميع بني الغرباء ووقفوا واعترفوا بخطاياهم وذنوب آبائهم"));
arasuperobject.biblemap.put("b16_9_3", new arasuperobject(16,9,3,"واقاموا في مكانهم وقرأوا في سفر شريعة الرب الههم ربع النهار وفي الربع الآخر كانوا يحمدون ويسجدون للرب الههم"));
arasuperobject.biblemap.put("b16_9_4", new arasuperobject(16,9,4,"ووقف على درج اللاويين يشوع وباني وقدميئيل وشبنيا وبنيّ وشربيا وباني وكناني وصرخوا بصوت عظيم الى الرب الههم"));
arasuperobject.biblemap.put("b16_9_5", new arasuperobject(16,9,5,"وقال اللاويون يشوع وقدميئيل وباني وحشبنيا وشربيا وهوديا وشبنيا وفتحيا قوموا باركوا الرب الهكم من الازل الى الابد وليتبارك اسم جلالك المتعالي على كل بركة وتسبيح"));
arasuperobject.biblemap.put("b16_9_6", new arasuperobject(16,9,6,"انت هو الرب وحدك. انت صنعت السموات وسماء السموات وكل جندها والارض وكل ما عليها والبحار وكل ما فيها وانت تحييها كلها وجند السماء لك يسجد"));
arasuperobject.biblemap.put("b16_9_7", new arasuperobject(16,9,7,"انت هو الرب الاله الذي اخترت ابرام واخرجته من اور الكلدانيين وجعلت اسمه ابراهيم"));
arasuperobject.biblemap.put("b16_9_8", new arasuperobject(16,9,8,"ووجدت قلبه امينا امامك وقطعت معه العهد ان تعطيه ارض الكنعانيين والحثّيين والاموريين والفرزّيين واليبوسيين والجرجاشيين وتعطيها لنسله. وقد انجزت وعدك لانك صادق"));
arasuperobject.biblemap.put("b16_9_9", new arasuperobject(16,9,9,"ورأيت ذلّ آبائنا في مصر وسمعت صراخهم عند بحر سوف"));
arasuperobject.biblemap.put("b16_9_10", new arasuperobject(16,9,10,"واظهرت آيات وعجائب على فرعون وعلى جميع عبيده وعلى كل شعب ارضه لانك علمت انهم بغوا عليهم وعملت لنفسك اسما كهذا اليوم"));
arasuperobject.biblemap.put("b16_9_11", new arasuperobject(16,9,11,"وفلقت اليمّ امامهم وعبروا في وسط البحر على اليابسة وطرحت مطارديهم في الاعماق كحجر في مياه قوية"));
arasuperobject.biblemap.put("b16_9_12", new arasuperobject(16,9,12,"وهديتهم بعمود سحاب نهارا وبعمود نار ليلا لتضيء لهم في الطريق التي يسيرون فيها"));
arasuperobject.biblemap.put("b16_9_13", new arasuperobject(16,9,13,"ونزلت على جبل سينا وكلمتهم من السماء واعطيتهم احكاما مستقيمة وشرائع صادقة فرائض ووصايا صالحة"));
arasuperobject.biblemap.put("b16_9_14", new arasuperobject(16,9,14,"وعرّفتهم سبتك المقدس وامرتهم بوصايا وفرائض وشرائع عن يد موسى عبدك"));
arasuperobject.biblemap.put("b16_9_15", new arasuperobject(16,9,15,"واعطيتهم خبزا من السماء لجوعهم واخرجت لهم ماء من الصخرة لعطشهم وقلت لهم ان يدخلوا ويرثوا الارض التي رفعت يدك ان تعطيهم اياها"));
arasuperobject.biblemap.put("b16_9_16", new arasuperobject(16,9,16,"ولكنهم بغوا هم وآباؤنا وصلّبوا رقابهم ولم يسمعوا لوصاياك"));
arasuperobject.biblemap.put("b16_9_17", new arasuperobject(16,9,17,"وأبوا الاستماع ولم يذكروا عجائبك التي صنعت معهم وصلّبوا رقابهم وعند تمردهم اقاموا رئيسا ليرجعوا الى عبوديتهم. وانت اله غفور وحنان ورحيم طويل الروح وكثير الرحمة فلم تتركهم"));
arasuperobject.biblemap.put("b16_9_18", new arasuperobject(16,9,18,"مع انهم عملوا لانفسهم عجلا مسبوكا وقالوا هذا الهك الذي اخرجك من مصر وعملوا اهانة عظيمة"));
arasuperobject.biblemap.put("b16_9_19", new arasuperobject(16,9,19,"انت برحمتك الكثيرة لم تتركهم في البرية ولم يزل عنهم عمود السحاب نهارا لهدايتهم في الطريق ولا عمود النار ليلا ليضيء لهم في الطريق التي يسيرون فيها"));
arasuperobject.biblemap.put("b16_9_20", new arasuperobject(16,9,20,"واعطيتهم روحك الصالح لتعليمهم ولم تمنع منّك عن افواههم واعطيتهم ماء لعطشهم"));
arasuperobject.biblemap.put("b16_9_21", new arasuperobject(16,9,21,"وعلتهم اربعين سنة في البرية فلم يحتاجوا. لم تبل ثيابهم ولم تتورّم ارجلهم"));
arasuperobject.biblemap.put("b16_9_22", new arasuperobject(16,9,22,"واعطيتهم ممالك وشعوبا وفرقتهم الى جهات فامتلكوا ارض سيحون وارض ملك حشبون وارض عوج ملك باشان"));
arasuperobject.biblemap.put("b16_9_23", new arasuperobject(16,9,23,"واكثرت بنيهم كنجوم السماء واتيت بهم الى الارض التي قلت لآبائهم ان يدخوا ويرثوها"));
arasuperobject.biblemap.put("b16_9_24", new arasuperobject(16,9,24,"فدخل البنون وورثوا الارض واخضعت لهم سكان ارض الكنعانيين ودفعتهم ليدهم مع ملوكهم وشعوب الارض ليعملوا بهم حسب ارادتهم"));
arasuperobject.biblemap.put("b16_9_25", new arasuperobject(16,9,25,"وأخذوا مدنا حصينة وارضا سمينة وورثوا بيوتا ملآنة كل خير وآبارا محفورة وكروما وزيتونا واشجارا مثمرة بكثرة فأكلوا وشبعوا وسمنوا وتلذذوا بخيرك العظيم"));
arasuperobject.biblemap.put("b16_9_26", new arasuperobject(16,9,26,"وعصوا وتمردوا عليك وطرحوا شريعتك وراء ظهورهم وقتلوا انبياءك الذين اشهدوا عليهم ليردوهم اليك وعملوا اهانة عظيمة"));
arasuperobject.biblemap.put("b16_9_27", new arasuperobject(16,9,27,"فدفعتهم ليد مضايقيهم فضايقوهم وفي وقت ضيقهم صرخوا اليك وانت من السماء سمعت وحسب مراحمك الكثيرة اعطيتهم مخلصين خلّصوهم من يد مضايقيهم"));
arasuperobject.biblemap.put("b16_9_28", new arasuperobject(16,9,28,"ولكن لما استراحوا رجعوا الى عمل الشر قدامك فتركتهم بيد اعدائهم فتسلطوا عليهم ثم رجعوا وصرخوا اليك وانت من السماء سمعت وانقذتهم حسب مراحمك الكثيرة احيانا كثيرة"));
arasuperobject.biblemap.put("b16_9_29", new arasuperobject(16,9,29,"واشهدت عليهم لتردهم الى شريعتك. واما هم فبغوا ولم يسمعوا لوصاياك واخطأوا ضد احكامك التي اذا عملها انسان يحيا بها. واعطوا كتفا معاندة وصلّبوا رقابهم ولم يسمعوا"));
arasuperobject.biblemap.put("b16_9_30", new arasuperobject(16,9,30,"فاحتملتهم سنين كثيرة واشهدت عليهم بروحك عن يد انبيائك فلم يصغوا فدفعتهم ليد شعوب الاراضي"));
arasuperobject.biblemap.put("b16_9_31", new arasuperobject(16,9,31,"ولكن لاجل مراحمك الكثيرة لم تفنهم ولم تتركهم لانك اله حنّان ورحيم"));
arasuperobject.biblemap.put("b16_9_32", new arasuperobject(16,9,32,"والآن يا الهنا الاله العظيم الجبار المخوف حافظ العهد والرحمة لا تصغر لديك كل المشقات التي اصابتنا نحن وملوكنا ورؤساءنا وكهنتنا وانبياءنا وآباءنا وكل شعبك من ايام ملوك اشور الى هذا اليوم"));
arasuperobject.biblemap.put("b16_9_33", new arasuperobject(16,9,33,"وانت بار في كل ما اتى علينا لانك عملت بالحق ونحن اذنبنا"));
arasuperobject.biblemap.put("b16_9_34", new arasuperobject(16,9,34,"وملوكنا ورؤساؤنا وكهنتنا وآباؤنا لم يعملوا شريعتك ولا اصغوا الى وصاياك وشهاداتك التي اشهدتها عليهم"));
arasuperobject.biblemap.put("b16_9_35", new arasuperobject(16,9,35,"وهم لم يعبدوك في مملكتهم وفي خيرك الكثير الذي اعطيتهم وفي الارض الواسعة السمينة التي جعلتها امامهم ولم يرجعوا عن اعمالهم الرديّة"));
arasuperobject.biblemap.put("b16_9_36", new arasuperobject(16,9,36,"ها نحن اليوم عبيد والارض التي اعطيت لآبائنا ليأكلوا اثمارها وخيرها ها نحن عبيد فيها"));
arasuperobject.biblemap.put("b16_9_37", new arasuperobject(16,9,37,"وغلاتها كثيرة للملوك الذين جعلتهم علينا لاجل خطايانا وهم يتسلطون على اجسادنا وعلى بهائمنا حسب ارادتهم ونحن في كرب عظيم"));
arasuperobject.biblemap.put("b16_9_38", new arasuperobject(16,9,38,"ومن اجل كل ذلك نحن نقطع ميثاقا ونكتبه. ورؤساؤنا ولاويّونا وكهنتنا يختمون"));
arasuperobject.biblemap.put("b16_10_1", new arasuperobject(16,10,1,"والذين ختموا هم نحميا الترشاثا ابن حكليا وصدقيا"));
arasuperobject.biblemap.put("b16_10_2", new arasuperobject(16,10,2,"وسرايا وعزريا ويرميا"));
arasuperobject.biblemap.put("b16_10_3", new arasuperobject(16,10,3,"وفشحور وامريا وملكيا"));
arasuperobject.biblemap.put("b16_10_4", new arasuperobject(16,10,4,"وحطوش وشبنيا وملّوخ"));
arasuperobject.biblemap.put("b16_10_5", new arasuperobject(16,10,5,"وحاريم ومريموث وعوبديا"));
arasuperobject.biblemap.put("b16_10_6", new arasuperobject(16,10,6,"ودانيال وجنثون وباروخ"));
arasuperobject.biblemap.put("b16_10_7", new arasuperobject(16,10,7,"ومشلام وابيا وميامين"));
arasuperobject.biblemap.put("b16_10_8", new arasuperobject(16,10,8,"ومعزيا وبلجاي وشمعيا. هؤلاء هم الكهنة"));
