arasuperobject.biblemap.put("b16_10_28", new arasuperobject(16,10,28,"وباقي الشعب والكهنة واللاويين والبوابين والمغنين والنثينيم وكل الذين انفصلوا من شعوب الاراضي الى شريعة الله ونسائهم وبنيهم وبناتهم كل اصحاب المعرفة والفهم"));
arasuperobject.biblemap.put("b16_10_29", new arasuperobject(16,10,29,"لصقوا باخوتهم وعظمائهم ودخلوا في قسم وحلف ان يسيروا في شريعة الله التي أعطيت عن يد موسى عبد الله وان يحفظوا ويعملوا جميع وصايا الرب سيدنا واحكامه وفرائضه"));
arasuperobject.biblemap.put("b16_10_30", new arasuperobject(16,10,30,"وان لا نعطي بناتنا لشعوب الارض ولا ناخذ بناتهم لبنينا"));
arasuperobject.biblemap.put("b16_10_31", new arasuperobject(16,10,31,"وشعوب الارض الذين يأتون بالبضائع وكل طعام يوم السبت للبيع لا نأخذ منهم في سبت ولا في يوم مقدس وان نترك السنة السابعة والمطالبة بكل دين"));
arasuperobject.biblemap.put("b16_10_32", new arasuperobject(16,10,32,"واقمنا على انفسنا فرائض ان نجعل على انفسنا ثلث شاقل كل سنة لخدمة بيت الهنا"));
arasuperobject.biblemap.put("b16_10_33", new arasuperobject(16,10,33,"لخبز الوجوه والتقدمة الدائمة والمحرقة الدائمة والسبوت والاهلّة والمواسم والاقداس وذبائح الخطية للتكفير عن اسرائيل ولكل عمل بيت الهنا"));
arasuperobject.biblemap.put("b16_10_34", new arasuperobject(16,10,34,"والقينا قرعا على قربان الحطب بين الكهنة واللاويين والشعب لادخاله الى بيت الهنا حسب بيوت آبائنا في اوقات معينة سنة فسنة لاجل احراقه على مذبح الرب الهنا كما هو مكتوب في الشريعة"));
arasuperobject.biblemap.put("b16_10_35", new arasuperobject(16,10,35,"ولادخال باكورات ارضنا وباكورات ثمر كل شجرة سنة فسنة الى بيت الرب"));
arasuperobject.biblemap.put("b16_10_36", new arasuperobject(16,10,36,"وابكار بنينا وبهائمنا كما هو مكتوب في الشريعة وابكار بقرنا وغنمنا لاحضارها الى بيت الهنا الى الكهنة الخادمين في بيت الهنا"));
arasuperobject.biblemap.put("b16_10_37", new arasuperobject(16,10,37,"وان نأتي باوائل عجيننا ورفائعنا واثمار كل شجرة من الخمر والزيت الى الكهنة الى مخادع بيت الهنا وبعشر ارضنا الى اللاويين واللاويون هم الذين يعشرون في جميع مدن فلاحتنا"));
arasuperobject.biblemap.put("b16_10_38", new arasuperobject(16,10,38,"ويكون الكاهن ابن هرون مع اللاويين حين يعشر اللاويون ويصعد اللاويون عشر الاعشار الى بيت الهنا الى المخادع الى بيت الخزينة"));
arasuperobject.biblemap.put("b16_10_39", new arasuperobject(16,10,39,"لان بني اسرائيل وبني لاوي ياتون برفيعة القمح والخمر والزيت الى المخادع وهناك آنية القدس والكهنة الخادمون والبوابون والمغنون ولا نترك بيت الهنا"));
arasuperobject.biblemap.put("b16_11_1", new arasuperobject(16,11,1,"وسكن رؤساء الشعب في اورشليم. والقى سائر الشعب قرعا لياتوا بواحد من عشرة للسكنى في اورشليم مدينة القدس والتسعة الاقسام في المدن"));
arasuperobject.biblemap.put("b16_11_2", new arasuperobject(16,11,2,"وبارك الشعب جميع القوم الذين انتدبوا للسكنى في اورشليم"));
arasuperobject.biblemap.put("b16_11_3", new arasuperobject(16,11,3,"وهؤلاء هم رؤوس البلاد الذين سكنوا في اورشليم وفي مدن يهوذا. سكن كل واحد في ملكه في مدنهم من اسرائيل الكهنة واللاويون والنثينيم وبنو عبيد سليمان"));
arasuperobject.biblemap.put("b16_11_4", new arasuperobject(16,11,4,"وسكن في اورشليم من بني يهوذا ومن بني بنيامين. فمن بني يهوذا عثايا بن عزّيا بن زكريا بن امريا بن شفطيا بن مهللئيل من بني فارص"));
arasuperobject.biblemap.put("b16_11_5", new arasuperobject(16,11,5,"ومعسيا ابن باروخ بن كلحوزة بن حزايا بن عدايا بن يوياريب بن زكريا بن الشيلوني"));
arasuperobject.biblemap.put("b16_11_6", new arasuperobject(16,11,6,"جميع بني فارص الساكنين في اورشليم اربع مئة وثمانية وستون من رجال البأس"));
arasuperobject.biblemap.put("b16_11_7", new arasuperobject(16,11,7,"وهؤلاء بنو بنيامين. سلّو بن مشلام بن يوعيل بن فدايا بن قولايا بن معسيا بن ايثيئيل بن يشعيا"));
arasuperobject.biblemap.put("b16_11_8", new arasuperobject(16,11,8,"وبعده جبّاي سلاي. تسع مئة وثمانية وعشرون"));
arasuperobject.biblemap.put("b16_11_9", new arasuperobject(16,11,9,"وكان يوئيل بن زكري وكيلا عليهم ويهوذا بن هسنوآة ثانيا على المدينة"));
arasuperobject.biblemap.put("b16_11_10", new arasuperobject(16,11,10,"من الكهنة يدعيا بن يوياريب وياكين"));
arasuperobject.biblemap.put("b16_11_11", new arasuperobject(16,11,11,"وسرايا بن حلقيا بن مشلام بن صادوق بن مرايوث بن اخيطوب رئيس بيت الله"));
arasuperobject.biblemap.put("b16_11_12", new arasuperobject(16,11,12,"واخوتهم عاملو العمل للبيت ثمان مئة واثنان وعشرون. وعدايا بن يروحام بن فلليا بن امصي بن زكريا بن فشحور بن ملكيا"));
arasuperobject.biblemap.put("b16_11_13", new arasuperobject(16,11,13,"واخوته رؤوس الآباء مئتان واثنان واربعون. وعمشساي بن عزرئيل بن اخزاي بن مشليموث بن امّير"));
arasuperobject.biblemap.put("b16_11_14", new arasuperobject(16,11,14,"واخوتهم جبابرة بأس مئة وثمانية وعشرون. والوكيل عليهم زبديئيل بن هجدوليم"));
arasuperobject.biblemap.put("b16_11_15", new arasuperobject(16,11,15,"ومن اللاويين شمعيا بن حشوب بن عزريقام بن حشبيا بن بوني"));
