arasuperobject.biblemap.put("b16_11_17", new arasuperobject(16,11,17,"ومتنيا بن ميخا بن زبدي بن آساف رئيس التسبيح يحمد في الصلاة وبقبقيا الثاني بين اخوته وعبدا بن شموع بن جلال بن يدوثون"));
arasuperobject.biblemap.put("b16_11_18", new arasuperobject(16,11,18,"جميع اللاويين في المدينة المقدسة مئتان وثمانية واربعون"));
arasuperobject.biblemap.put("b16_11_19", new arasuperobject(16,11,19,"والبوابون عقوب وطلمون واخوتهما حارسو الابواب مئة واثنان وسبعون"));
arasuperobject.biblemap.put("b16_11_20", new arasuperobject(16,11,20,"وكان سائر اسرائيل من الكهنة واللاويين في جميع مدن يهوذا كل واحد في ميراثه"));
arasuperobject.biblemap.put("b16_11_21", new arasuperobject(16,11,21,"واما النثينيم فسكنوا في الاكمة. وكان صيحا وجشفا على النثينيم"));
arasuperobject.biblemap.put("b16_11_22", new arasuperobject(16,11,22,"وكان وكيل اللاويين في اورشليم على عمل بيت الله عزي بن باني بن حشبيا بن متنيا بن ميخا من بني آساف المغنين"));
arasuperobject.biblemap.put("b16_11_23", new arasuperobject(16,11,23,"لان وصية الملك من جهتهم كانت ان للمرنمين فريضة امر كل يوم فيوم"));
arasuperobject.biblemap.put("b16_11_24", new arasuperobject(16,11,24,"وفتحيا بن مشيزبئيل من بني زارح بن يهوذا كان تحت يد الملك في كل امور الشعب"));
arasuperobject.biblemap.put("b16_11_25", new arasuperobject(16,11,25,"وفي الضياع من حقولها سكن من بني يهوذا في قرية اربع وقراها وديبون وقراها وفي يقبصئيل وضياعها"));
