arasuperobject.biblemap.put("b16_11_31", new arasuperobject(16,11,31,"وبنو بنيامين سكنوا من جبع الى مخماس وعيّا وبيت ايل وقراها"));
arasuperobject.biblemap.put("b16_11_32", new arasuperobject(16,11,32,"وعناثوث ونوب وعننية"));
arasuperobject.biblemap.put("b16_11_33", new arasuperobject(16,11,33,"وحاصور ورامة وجتايم"));
arasuperobject.biblemap.put("b16_11_34", new arasuperobject(16,11,34,"وحاديد وصبوعيم ونبّلاط"));
