arasuperobject.biblemap.put("b16_12_1", new arasuperobject(16,12,1,"وهؤلاء هم الكهنة واللاويون الذين صعدوا مع زربابل بن شألتيئيل ويشوع. سرايا ويرميا وعزرا"));
arasuperobject.biblemap.put("b16_12_2", new arasuperobject(16,12,2,"وامريا وملوخ وحطوش"));
arasuperobject.biblemap.put("b16_12_3", new arasuperobject(16,12,3,"وشكنيا ورحوم ومريموث"));
arasuperobject.biblemap.put("b16_12_4", new arasuperobject(16,12,4,"وعدو وجنتوي وابيا"));
arasuperobject.biblemap.put("b16_12_5", new arasuperobject(16,12,5,"وميامين ومعديا وبلجة"));
arasuperobject.biblemap.put("b16_12_6", new arasuperobject(16,12,6,"وشمعيا ويوياريب ويدعيا"));
arasuperobject.biblemap.put("b16_12_7", new arasuperobject(16,12,7,"وسلو وعاموق وحلقيا ويدعيا. هؤلاء هم رؤوس الكهنة واخوتهم في ايام يشوع"));
arasuperobject.biblemap.put("b16_12_8", new arasuperobject(16,12,8,"واللاويون يشوع وبنوي وقدميئيل وشربيا ويهوذا ومتنيا الذي على التحميد هو واخوته"));
arasuperobject.biblemap.put("b16_12_9", new arasuperobject(16,12,9,"وبقبقيا وعنّي اخواهم مقابلهم في الحراسات"));
arasuperobject.biblemap.put("b16_12_10", new arasuperobject(16,12,10,"ويشوع ولد يوياقيم ويوياقيم ولد الياشيب والياشيب ولد يوياداع"));
arasuperobject.biblemap.put("b16_12_11", new arasuperobject(16,12,11,"ويوياداع ولد يوناثان ويوناثان ولد يدوع"));
arasuperobject.biblemap.put("b16_12_12", new arasuperobject(16,12,12,"وفي ايام يوياقيم كان الكهنة رؤوس الآباء لسرايا مرايا وليرميا حننيا"));
arasuperobject.biblemap.put("b16_12_13", new arasuperobject(16,12,13,"ولعزرا مشلام ولامريا يهوحانان"));
arasuperobject.biblemap.put("b16_12_14", new arasuperobject(16,12,14,"ولمليكو يوناثان ولشبنيا يوسف"));
arasuperobject.biblemap.put("b16_12_15", new arasuperobject(16,12,15,"ولحريم عدنا ولمرايوث حلقاي"));
arasuperobject.biblemap.put("b16_12_16", new arasuperobject(16,12,16,"ولعدو زكريا ولجنثون مشلام"));
arasuperobject.biblemap.put("b16_12_17", new arasuperobject(16,12,17,"ولابيا زكري ولمنيامين لموعديا فلطاي"));
arasuperobject.biblemap.put("b16_12_18", new arasuperobject(16,12,18,"ولبلجة شموع ولشمعيا يهوناثان"));
arasuperobject.biblemap.put("b16_12_19", new arasuperobject(16,12,19,"وليوياريب متناي وليدعيا عزي"));
arasuperobject.biblemap.put("b16_12_20", new arasuperobject(16,12,20,"ولسلاي قلاي ولعاموق عابر"));
arasuperobject.biblemap.put("b16_12_21", new arasuperobject(16,12,21,"ولحلقيا حشبيا وليدعيا نثنئيل"));
arasuperobject.biblemap.put("b16_12_22", new arasuperobject(16,12,22,"وكان اللاويون في ايام الياشيب ويوياداع ويوحانان ويدوع مكتوبين رؤوس آباء والكهنة ايضا في ملك داريوس الفارسي"));
arasuperobject.biblemap.put("b16_12_23", new arasuperobject(16,12,23,"وكان بنو لاوي رؤوس الآباء مكتوبين في سفر اخبار الايام الى ايام يوحانان بن الياشيب"));
arasuperobject.biblemap.put("b16_12_24", new arasuperobject(16,12,24,"ورؤوس اللاويين حشبيا وشربيا ويشوع بن قدميئيل واخوتهم مقابلهم للتسبيح والتحميد حسب وصية داود رجل الله نوبة مقابل نوبة"));
arasuperobject.biblemap.put("b16_12_25", new arasuperobject(16,12,25,"وكان متنيا وبقبقيا وعوبديا ومشلام وطلمون وعقوب بوابين حارسين الحراسة عند مخازن الابواب"));
arasuperobject.biblemap.put("b16_12_26", new arasuperobject(16,12,26,"كان هؤلاء في ايام يوياقيم بن يشوع بن يوصاداق وفي ايام نحميا الوالي وعزرا الكاهن الكاتب"));
arasuperobject.biblemap.put("b16_12_27", new arasuperobject(16,12,27,"وعند تدشين سور اورشليم طلبوا اللاويين من جميع اماكنهم لياتوا بهم الى اورشليم لكي يدشنوا بفرح وبحمد وغناء بالصنوج والرباب والعيدان"));
arasuperobject.biblemap.put("b16_12_28", new arasuperobject(16,12,28,"فاجتمع بنو المغنين من الدائرة حول اورشليم ومن ضياع النطوفاتي"));
arasuperobject.biblemap.put("b16_12_29", new arasuperobject(16,12,29,"ومن بيت الجلجال ومن حقول جبع وعزموت لان المغنين بنوا لانفسهم ضياعا حول اورشليم"));
arasuperobject.biblemap.put("b16_12_30", new arasuperobject(16,12,30,"وتطهر الكهنة واللاويون وطهروا الشعب والابواب والسور"));
arasuperobject.biblemap.put("b16_12_31", new arasuperobject(16,12,31,"واصعدت رؤساء يهوذا على السور واقمت فرقتين عظيمتين من الحمادين ووكبت الواحدة يمينا على السور نحو باب الدمن"));
arasuperobject.biblemap.put("b16_12_32", new arasuperobject(16,12,32,"وسار وراءهم هوشعيا ونصف رؤساء يهوذا"));
arasuperobject.biblemap.put("b16_12_33", new arasuperobject(16,12,33,"وعزريا وعزرا ومشلام"));
arasuperobject.biblemap.put("b16_12_34", new arasuperobject(16,12,34,"ويهوذا وبنيامين وشمعيا ويرميا"));
arasuperobject.biblemap.put("b16_12_35", new arasuperobject(16,12,35,"ومن بني الكهنة بالابواق زكريا بن يوناثان بن شمعيا بن متنيا بن ميخايا بن زكور بن آساف"));
arasuperobject.biblemap.put("b16_12_36", new arasuperobject(16,12,36,"واخوته شمعيا وعزرئيل ومللاي وجللاي وماعاي ونثنئيل ويهوذا وحناني بآلات غناء داود رجل الله وعزرا الكاتب امامهم"));
arasuperobject.biblemap.put("b16_12_37", new arasuperobject(16,12,37,"وعند باب العين الذي مقابلهم صعدوا على درج مدينة داود عند مصعد السور فوق بيت داود الى باب الماء شرقا"));
arasuperobject.biblemap.put("b16_12_38", new arasuperobject(16,12,38,"والفرقة الثانية من الحمّادين وكبت مقابلهم وانا وراءها ونصف الشعب على السور من عند برج التنانير الى السور العريض"));
arasuperobject.biblemap.put("b16_12_39", new arasuperobject(16,12,39,"ومن فوق باب افرايم وفوق الباب العتيق وفوق باب السمك وبرج حننئيل وبرج المئة الى باب الضأن ووقفوا في باب السجن"));
arasuperobject.biblemap.put("b16_12_40", new arasuperobject(16,12,40,"فوقف الفرقتان من الحمادين في بيت الله وانا ونصف الولاة معي"));
arasuperobject.biblemap.put("b16_12_41", new arasuperobject(16,12,41,"والكهنة الياقيم ومعسيا ومنيامين وميخايا واليوعيناي وزكريا وحننيا بالابواق"));
arasuperobject.biblemap.put("b16_12_42", new arasuperobject(16,12,42,"ومعسيا وشمعيا والعازار وعزي ويهوحانان وملكيا وعيلام وعازر وغنّى المغنون ويزرحيا الوكيل"));
arasuperobject.biblemap.put("b16_12_43", new arasuperobject(16,12,43,"وذبحوا في ذلك اليوم ذبائح عظيمة وفرحوا لان الله افرحهم فرحا عظيما وفرح الاولاد والنساء ايضا وسمع فرح اورشليم عن بعد"));
arasuperobject.biblemap.put("b16_12_44", new arasuperobject(16,12,44,"وتوكل في ذلك اليوم اناس على المخادع للخزائن والرفائع والاوائل الاعشار ليجمعوا فيها من حقول المدن انصبة الشريعة للكهنة واللاويين لان يهوذا فرح بالكهنة واللاويين الواقفين"));
arasuperobject.biblemap.put("b16_12_45", new arasuperobject(16,12,45,"حارسين حراسة الههم وحراسة التطهير وكان المغنون والبوابون حسب وصية داود وسليمان ابنه"));
arasuperobject.biblemap.put("b16_12_46", new arasuperobject(16,12,46,"لانه في ايام داود وآساف منذ القديم كان رؤوس مغنين وغناء تسبيح وتحميد لله"));
arasuperobject.biblemap.put("b16_12_47", new arasuperobject(16,12,47,"وكان كل اسرائيل في ايام زربابل وايام نحميا يؤدون انصبة المغنين والبوابين امر كل يوم في يومه وكانوا يقدسون للاويين وكان اللاويون يقدسون لبني هرون"));
arasuperobject.biblemap.put("b16_13_1", new arasuperobject(16,13,1,"في ذلك اليوم قرئ في سفر موسى في آذان الشعب ووجد مكتوبا فيه ان عمونيا وموآبيا لا يدخل في جماعة الله الى الابد"));
arasuperobject.biblemap.put("b16_13_2", new arasuperobject(16,13,2,"لانهم لم يلاقوا بني اسرائيل بالخبز والماء بل استأجروا عليهم بلعام لكي يلعنهم وحول الهنا اللعنة الى بركة"));
arasuperobject.biblemap.put("b16_13_3", new arasuperobject(16,13,3,"ولما سمعوا الشريعة فرزوا كل اللفيف من اسرائيل"));
arasuperobject.biblemap.put("b16_13_4", new arasuperobject(16,13,4,"وقبل هذا كان الياشيب الكاهن المقام على مخدع بيت الهنا قرابة طوبيا"));
arasuperobject.biblemap.put("b16_13_5", new arasuperobject(16,13,5,"قد هيأ له مخدعا عظيما حيث كانوا سابقا يضعون التقدمات والبخور والآنية وعشر القمح والخمر والزيت فريضة اللاويين والمغنين والبوابين ورفيعة الكهنة"));
arasuperobject.biblemap.put("b16_13_6", new arasuperobject(16,13,6,"وفي كل هذا لم اكن في اورشليم لاني في السنة الاثنتين والثلاثين لارتحشستا ملك بابل دخلت الى الملك وبعد ايام استأذنت من الملك"));
arasuperobject.biblemap.put("b16_13_7", new arasuperobject(16,13,7,"وأتيت الى اورشليم وفهمت الشر الذي عمله الياشيب لاجل طوبيا بعمله له مخدعا في ديار بيت الله"));
arasuperobject.biblemap.put("b16_13_8", new arasuperobject(16,13,8,"وساءني الامر جدا وطرحت جميع آنية بيت طوبيا خارج المخدع"));
arasuperobject.biblemap.put("b16_13_9", new arasuperobject(16,13,9,"وأمرت فطهروا المخادع ورددت اليها آنية بيت الله مع التقدمة والبخور"));
arasuperobject.biblemap.put("b16_13_10", new arasuperobject(16,13,10,"وعلمت ان انصبة اللاويين لم تعط بل هرب اللاويون والمغنون عاملو العمل كل واحد الى حقله"));
arasuperobject.biblemap.put("b16_13_11", new arasuperobject(16,13,11,"فخاصمت الولاة وقلت لماذا ترك بيت الله. فجمعتهم واوقفتهم في اماكنهم"));
arasuperobject.biblemap.put("b16_13_12", new arasuperobject(16,13,12,"واتى كل يهوذا بعشر القمح والخمر والزيت الى المخازن"));
arasuperobject.biblemap.put("b16_13_13", new arasuperobject(16,13,13,"واقمت خزنة على الخزائن شلميا الكاهن وصادوق الكاتب وفدايا من اللاويين وبجانبهم حانان بن زكور بن متنيا لانهم حسبوا امناء وكان عليهم ان يقسموا على اخوتهم"));
arasuperobject.biblemap.put("b16_13_14", new arasuperobject(16,13,14,"اذكرني يا الهي من اجل هذا ولا تمح حسناتي التي عملتها نحو بيت الهي ونحو شعائره"));
arasuperobject.biblemap.put("b16_13_15", new arasuperobject(16,13,15,"في تلك الايام رأيت في يهوذا قوما يدوسون معاصر في السبت ويأتون بحزم ويحملون حميرا وايضا يدخلون اورشليم في يوم السبت بخمر وعنب وتين وكل ما يحمل فاشهدت عليهم يوم بيعهم الطعام"));
arasuperobject.biblemap.put("b16_13_16", new arasuperobject(16,13,16,"والصوريون الساكنون بها كانوا ياتون بسمك وكل بضاعة ويبيعون في السبت لبني يهوذا وفي اورشليم"));
arasuperobject.biblemap.put("b16_13_17", new arasuperobject(16,13,17,"فخاصمت عظماء يهوذا وقلت لهم ما هذا الأمر القبيح الذي تعملونه وتدنسون يوم السبت"));
arasuperobject.biblemap.put("b16_13_18", new arasuperobject(16,13,18,"ألم يفعل آباؤكم هكذا فجلب الهنا علينا كل هذا الشر وعلى هذه المدينة وانتم تزيدون غضبا على اسرائيل اذ تدنسون السبت"));
arasuperobject.biblemap.put("b16_13_19", new arasuperobject(16,13,19,"وكان لما اظلمت ابواب اورشليم قبل السبت اني أمرت بان تغلق الابواب وقلت ان لا يفتحوها الى ما بعد السبت واقمت من غلماني على الابواب حتى لا يدخل حمل في يوم السبت"));
arasuperobject.biblemap.put("b16_13_20", new arasuperobject(16,13,20,"فبات التجار وبائعو كل بضاعة خارج اورشليم مرة واثنتين"));
arasuperobject.biblemap.put("b16_13_21", new arasuperobject(16,13,21,"فاشهدت عليهم وقلت لهم لماذا انتم بائتون بجانب السور. ان عدتم فاني ألقي يدا عليكم. ومن ذلك الوقت لم يأتوا في السبت"));
arasuperobject.biblemap.put("b16_13_22", new arasuperobject(16,13,22,"وقلت للاويين ان يتطهروا ويأتوا ويحرسوا الابواب لاجل تقديس يوم السبت. بهذا ايضا اذكرني يا الهي وتراءف عليّ حسب كثرة رحمتك"));
arasuperobject.biblemap.put("b16_13_23", new arasuperobject(16,13,23,"في تلك الايام ايضا رأيت اليهود الذين ساكنوا نساء اشدوديات وعمونيات وموآبيات"));
arasuperobject.biblemap.put("b16_13_24", new arasuperobject(16,13,24,"ونصف كلام بنيهم باللسان الاشدودي ولم يكونوا يحسنون التكلم باللسان اليهودي بل بلسان شعب وشعب"));
arasuperobject.biblemap.put("b16_13_25", new arasuperobject(16,13,25,"فخاصمتهم ولعنتهم وضربت منهم اناسا ونتفت شعورهم واستحلفتهم بالله قائلا لا تعطوا بناتكم لبنيهم ولا تأخذوا من بناتهم لبنيكم ولا لانفسكم"));
arasuperobject.biblemap.put("b16_13_26", new arasuperobject(16,13,26,"أليس من اجل هؤلاء اخطأ سليمان ملك اسرائيل ولم يكن في الامم الكثيرة ملك مثله وكان محبوبا الى الهه فجعله الله ملكا على كل اسرائيل. هو ايضا جعلته النساء الاجنبيات يخطئ"));
arasuperobject.biblemap.put("b16_13_27", new arasuperobject(16,13,27,"فهل نسكت لكم ان تعملوا كل هذا الشر العظيم بالخيانة ضد الهنا بمساكنة نساء اجنبيات"));
arasuperobject.biblemap.put("b16_13_28", new arasuperobject(16,13,28,"وكان واحد من بني يوياداع بن الياشيب الكاهن العظيم صهرا لسنبلّط الحوروني فطردته من عندي"));
arasuperobject.biblemap.put("b16_13_29", new arasuperobject(16,13,29,"اذكرهم يا الهي لانهم نجسوا الكهنوت وعهد الكهنوت واللاويين"));
arasuperobject.biblemap.put("b16_13_30", new arasuperobject(16,13,30,"فطهرتهم من كل غريب واقمت حراسات الكهنة واللاويين كل واحد على عمله"));
arasuperobject.biblemap.put("b16_13_31", new arasuperobject(16,13,31,"ولاجل قربان الحطب في ازمنة معينة وللباكورات. فاذكرني يا الهي بالخير"));

