arasuperobject.biblemap.put("b17_1_2", new arasuperobject(17,1,2,"انه في تلك الايام حين جلس الملك احشويروش على كرسي ملكه الذي في شوشن القصر"));
arasuperobject.biblemap.put("b17_1_3", new arasuperobject(17,1,3,"في السنة الثالثة من ملكه عمل وليمة لجميع رؤسائه وعبيده جيش فارس ومادي وامامه شرفاء البلدان ورؤساؤها"));
arasuperobject.biblemap.put("b17_1_4", new arasuperobject(17,1,4,"حين اظهر غنى مجد ملكه ووقار جلال عظمته اياما كثيرة مئة وثمانين يوما"));
arasuperobject.biblemap.put("b17_1_5", new arasuperobject(17,1,5,"وعند انقضاء هذه الايام عمل الملك لجميع الشعب الموجودين في شوشن القصر من الكبير الى الصغير وليمة سبعة ايام في دار جنة قصر الملك"));
arasuperobject.biblemap.put("b17_1_6", new arasuperobject(17,1,6,"بانسجة بيضاء وخضراء واسمانجونية معلّقة بحبال من بزّ وارجوان في حلقات من فضة واعمدة من رخام واسرّة من ذهب وفضة على مجزّع من بهت ومرمر ودر ورخام اسود"));
arasuperobject.biblemap.put("b17_1_7", new arasuperobject(17,1,7,"وكان السقاء من ذهب والآنية مختلفة الاشكال والخمر الملكي بكثرة حسب كرم الملك"));
arasuperobject.biblemap.put("b17_1_8", new arasuperobject(17,1,8,"وكان الشرب حسب الأمر. لم يكن غاصب لانه هكذا رسم الملك على كل عظيم في بيته ان يعملوا حسب رضا كل واحد"));
arasuperobject.biblemap.put("b17_1_9", new arasuperobject(17,1,9,"ووشتي الملكة عملت ايضا وليمة للنساء في بيت الملك الذي للملك احشويروش"));
arasuperobject.biblemap.put("b17_1_10", new arasuperobject(17,1,10,"في اليوم السابع لما طاب قلب الملك بالخمر قال لمهومان وبزثا وحربونا وبغثا وابغثا وزيثار وكركس الخصيان السبعة الذين كانوا يخدمون بين يدي الملك احشويروش"));
arasuperobject.biblemap.put("b17_1_11", new arasuperobject(17,1,11,"ان يأتوا بوشتي الملكة الى امام الملك بتاج الملك ليري الشعوب والرؤساء جمالها لانها كانت حسنة المنظر"));
arasuperobject.biblemap.put("b17_1_12", new arasuperobject(17,1,12,"فأبت الملكة وشتي ان تأتي حسب أمر الملك عن يد الخصيان فاغتاظ الملك جدا واشتعل غضبه فيه"));
arasuperobject.biblemap.put("b17_1_13", new arasuperobject(17,1,13,"وقال الملك للحكماء والعارفين بالازمنة. لانه هكذا كان أمر الملك نحو جميع العارفين بالسنّة والقضاء"));
arasuperobject.biblemap.put("b17_1_14", new arasuperobject(17,1,14,"وكان المقربون اليه كرشنا وشيثار وادماثا وترشيش ومرس ومرسنا ومموكان سبعة رؤساء فارس ومادي الذين يرون وجه الملك ويجلسون اولا في الملك"));
arasuperobject.biblemap.put("b17_1_15", new arasuperobject(17,1,15,"حسب السنّة ماذا يعمل بالملكة وشتي لانها لم تعمل كقول الملك احشويروش عن يد الخصيان"));
arasuperobject.biblemap.put("b17_1_16", new arasuperobject(17,1,16,"فقال مموكان امام الملك والرؤساء ليس الى الملك وحده اذنبت وشتي الملكة بل الى جميع الرؤساء وجميع الشعوب الذين في كل بلدان الملك احشويروش"));
arasuperobject.biblemap.put("b17_1_17", new arasuperobject(17,1,17,"لانه سوف يبلغ خبر الملكة الى جميع النساء حتى يحتقر ازواجهنّ في اعينهنّ عندما يقال ان الملك احشويروش امر ان يؤتى بوشتي الملكة الى امامه فلم تأت"));
arasuperobject.biblemap.put("b17_1_18", new arasuperobject(17,1,18,"وفي هذا اليوم تقول رئيسات فارس ومادي اللواتي سمعن خبر الملكة لجميع رؤساء الملك. ومثل ذلك احتقار وغضب"));
arasuperobject.biblemap.put("b17_1_19", new arasuperobject(17,1,19,"فاذا حسن عند الملك فليخرج امر ملكي من عنده وليكتب في سنن فارس ومادي فلا يتغيّر ان لا تات وشتي الى امام الملك احشويروش وليعط الملك ملكها لمن هي احسن منها"));
arasuperobject.biblemap.put("b17_1_20", new arasuperobject(17,1,20,"فيسمع أمر الملك الذي يخرجه في كل مملكته لانها عظيمة فتعطي جميع النساء الوقار لازواجهنّ من الكبير الى الصغير"));
arasuperobject.biblemap.put("b17_1_21", new arasuperobject(17,1,21,"فحسن الكلام في اعين الملك والرؤساء وعمل الملك حسب قول مموكان"));
arasuperobject.biblemap.put("b17_1_22", new arasuperobject(17,1,22,"وارسل كتبا الى كل بلدان الملك والى كل بلاد حسب كتابتها والى كل شعب حسب لسانه ليكون كل رجل متسلط في بيته ويتكلم بذلك بلسان شعبه"));
arasuperobject.biblemap.put("b17_2_1", new arasuperobject(17,2,1,"بعد هذه الأمور لما خمد غضب الملك احشويروش ذكر وشتي وما عملته وما حتم به عليها"));
arasuperobject.biblemap.put("b17_2_2", new arasuperobject(17,2,2,"فقال غلمان الملك الذين يخدمونه ليطلب للملك فتيات عذارى حسنات المنظر"));
arasuperobject.biblemap.put("b17_2_3", new arasuperobject(17,2,3,"وليوكل الملك وكلاء في كل بلاد مملكته ليجمعوا كل الفتيات العذارى الحسنات المنظر الى شوشن القصر الى بيت النساء الى يد هيجاي خصي الملك حارس النساء وليعطين ادهان عطرهنّ‎"));
arasuperobject.biblemap.put("b17_2_4", new arasuperobject(17,2,4,"والفتاة التي تحسن في عيني الملك فلتملك مكان وشتي. فحسن الكلام في عيني الملك فعمل هكذا"));
arasuperobject.biblemap.put("b17_2_5", new arasuperobject(17,2,5,"كان في شوشن القصر رجل يهودي اسمه مردخاي ابن يائير بن شمعي بن قيس رجل يميني"));
arasuperobject.biblemap.put("b17_2_6", new arasuperobject(17,2,6,"قد سبي من اورشليم مع السبي الذي سبي مع يكنيا ملك يهوذا الذي سباه نبوخذنصّر ملك بابل"));
arasuperobject.biblemap.put("b17_2_7", new arasuperobject(17,2,7,"وكان مربّيا لهدسّة اي استير بنت عمه لانه لم يكن لها اب ولا ام. وكانت الفتاة جميلة الصورة وحسنة المنظر وعند موت ابيها وامها اتخذها مردخاي لنفسه ابنة"));
arasuperobject.biblemap.put("b17_2_8", new arasuperobject(17,2,8,"فلما سمع كلام الملك وامره وجمعت فتايات كثيرات الى شوشن القصر الى يد هيجاي أخذت استير الى بيت الملك الى يد هيجاي حارس النساء"));
arasuperobject.biblemap.put("b17_2_9", new arasuperobject(17,2,9,"وحسنت الفتاة في عينيه ونالت نعمة بين يديه فبادر بادهان عطرها وانصبتها ليعطيها اياها مع السبع الفتيات المختارات لتعطى لها من بيت الملك ونقلها مع فتاياتها الى احسن مكان في بيت النساء"));
arasuperobject.biblemap.put("b17_2_10", new arasuperobject(17,2,10,"ولم تخبر استير عن شعبها وجنسها لان مردخاي اوصاها ان لا تخبر"));
arasuperobject.biblemap.put("b17_2_11", new arasuperobject(17,2,11,"وكان مردخاي يتمشى يوما فيوما امام دار بيت النساء ليستعلم عن سلامة استير وعما يصنع بها"));
