arasuperobject.biblemap.put("b18_5_27", new arasuperobject(18,5,27,"ها ان ذا قد بحثنا عنه. كذا هو. فاسمعه واعلم انت لنفسك"));
arasuperobject.biblemap.put("b18_6_1", new arasuperobject(18,6,1,"فاجاب ايوب وقال"));
arasuperobject.biblemap.put("b18_6_2", new arasuperobject(18,6,2,"ليت كربي وزن ومصيبتي رفعت في الموازين جميعها"));
arasuperobject.biblemap.put("b18_6_3", new arasuperobject(18,6,3,"لانها الآن اثقل من رمل البحر. من اجل ذلك لغا كلامي"));
arasuperobject.biblemap.put("b18_6_4", new arasuperobject(18,6,4,"لان سهام القدير فيّ وحمتها شاربة روحي. اهوال الله مصطفة ضدي"));
arasuperobject.biblemap.put("b18_6_5", new arasuperobject(18,6,5,"هل ينهق الفراء على العشب او يخور الثور على علفه"));
arasuperobject.biblemap.put("b18_6_6", new arasuperobject(18,6,6,"هل يؤكل المسيخ بلا ملح او يوجد طعم في مرق البقلة"));
arasuperobject.biblemap.put("b18_6_7", new arasuperobject(18,6,7,"ما عافت نفسي ان تمسّها هذه صارت مثل خبزي الكريه"));
arasuperobject.biblemap.put("b18_6_8", new arasuperobject(18,6,8,"يا ليت طلبتي تاتي ويعطيني الله رجائي"));
arasuperobject.biblemap.put("b18_6_9", new arasuperobject(18,6,9,"ان يرضى الله بان يسحقني ويطلق يده فيقطعني"));
arasuperobject.biblemap.put("b18_6_10", new arasuperobject(18,6,10,"فلا تزال تعزيتي وابتهاجي في عذاب لا يشفق اني لم اجحد كلام القدوس"));
arasuperobject.biblemap.put("b18_6_11", new arasuperobject(18,6,11,"ما هي قوتي حتى انتظر وما هي نهايتي حتى اصبّر نفسي"));
arasuperobject.biblemap.put("b18_6_12", new arasuperobject(18,6,12,"هل قوتي قوة الحجارة. هل لحمي نحاس"));
arasuperobject.biblemap.put("b18_6_13", new arasuperobject(18,6,13,"ألا انه ليست فيّ معونتي والمساعدة مطرودة عني"));
arasuperobject.biblemap.put("b18_6_14", new arasuperobject(18,6,14,"حق المحزون معروف من صاحبه وان ترك خشية القدير"));
arasuperobject.biblemap.put("b18_6_15", new arasuperobject(18,6,15,"اما اخواني فقد غدروا مثل الغدير. مثل ساقية الوديان يعبرون"));
arasuperobject.biblemap.put("b18_6_16", new arasuperobject(18,6,16,"التي هي عكرة من البرد ويختفي فيها الجليد"));
arasuperobject.biblemap.put("b18_6_17", new arasuperobject(18,6,17,"اذا جرت انقطعت. اذا حميت جفت من مكانها"));
arasuperobject.biblemap.put("b18_6_18", new arasuperobject(18,6,18,"يعرّج السّفر عن طريقهم يدخلون التيه فيهلكون"));
arasuperobject.biblemap.put("b18_6_19", new arasuperobject(18,6,19,"نظرت قوافل تيماء. سيارة سبأ رجوها"));
arasuperobject.biblemap.put("b18_6_20", new arasuperobject(18,6,20,"خزوا في ما كانوا مطمئنين. جاءوا اليها فخجلوا"));
arasuperobject.biblemap.put("b18_6_21", new arasuperobject(18,6,21,"فالآن قد صرتم مثلها. رايتم ضربة ففزعتم"));
arasuperobject.biblemap.put("b18_6_22", new arasuperobject(18,6,22,"هل قلت اعطوني شيئا او من مالكم ارشوا من اجلي"));
arasuperobject.biblemap.put("b18_6_23", new arasuperobject(18,6,23,"او نجوني من يد الخصم او من يد العتاة افدوني"));
arasuperobject.biblemap.put("b18_6_24", new arasuperobject(18,6,24,"علموني فانا اسكت. وفهموني في اي شيء ضللت"));
arasuperobject.biblemap.put("b18_6_25", new arasuperobject(18,6,25,"ما اشد الكلام المستقيم واما التوبيخ منكم فعلى ماذا يبرهن"));
arasuperobject.biblemap.put("b18_6_26", new arasuperobject(18,6,26,"هل تحسبون ان توبخوا كلمات. وكلام اليائس للريح"));
arasuperobject.biblemap.put("b18_6_27", new arasuperobject(18,6,27,"بل تلقون على اليتيم وتحفرون حفرة لصاحبكم"));
arasuperobject.biblemap.put("b18_6_28", new arasuperobject(18,6,28,"والآن تفرسوا فيّ. فاني على وجوهكم لا اكذب"));
arasuperobject.biblemap.put("b18_6_29", new arasuperobject(18,6,29,"ارجعوا. لا يكوننّ ظلم. ارجعوا ايضا. فيه حقي"));
arasuperobject.biblemap.put("b18_6_30", new arasuperobject(18,6,30,"هل في لساني ظلم ام حنكي لا يميّز فسادا"));
arasuperobject.biblemap.put("b18_7_1", new arasuperobject(18,7,1,"أليس جهاد للانسان على الارض وكايام الاجير ايامه"));
arasuperobject.biblemap.put("b18_7_2", new arasuperobject(18,7,2,"كما يتشوّق العبد الى الظل وكما يترجّى الاجير اجرته"));
arasuperobject.biblemap.put("b18_7_3", new arasuperobject(18,7,3,"هكذا تعين لي اشهر سوء وليالي شقاء قسمت لي"));
arasuperobject.biblemap.put("b18_7_4", new arasuperobject(18,7,4,"اذا اضطجعت اقول متى اقوم. الليل يطول واشبع قلقا حتى الصبح"));
arasuperobject.biblemap.put("b18_7_5", new arasuperobject(18,7,5,"لبس لحمي الدود مع مدر التراب. جلدي كرش وساخ"));
arasuperobject.biblemap.put("b18_7_6", new arasuperobject(18,7,6,"ايامي اسرع من الوشيعة وتنتهي بغير رجاء"));
arasuperobject.biblemap.put("b18_7_7", new arasuperobject(18,7,7,"اذكر ان حياتي انما هي ريح وعيني لا تعود ترى خيرا"));
arasuperobject.biblemap.put("b18_7_8", new arasuperobject(18,7,8,"لا تراني عين ناظري. عيناك عليّ ولست انا"));
arasuperobject.biblemap.put("b18_7_9", new arasuperobject(18,7,9,"السحاب يضمحل ويزول. هكذا الذي ينزل الى الهاوية لا يصعد"));
arasuperobject.biblemap.put("b18_7_10", new arasuperobject(18,7,10,"لا يرجع بعد الى بيته ولا يعرفه مكانه بعد"));
arasuperobject.biblemap.put("b18_7_11", new arasuperobject(18,7,11,"انا ايضا لا امنع فمي. اتكلم بضيق روحي. اشكو بمرارة نفسي"));
arasuperobject.biblemap.put("b18_7_12", new arasuperobject(18,7,12,"أبحر انا ام تنين حتى جعلت عليّ حارسا"));
arasuperobject.biblemap.put("b18_7_13", new arasuperobject(18,7,13,"ان قلت فراشي يعزيني مضجعي ينزع كربتي"));
arasuperobject.biblemap.put("b18_7_14", new arasuperobject(18,7,14,"تريعني بالاحلام وترهبني برؤى"));
arasuperobject.biblemap.put("b18_7_15", new arasuperobject(18,7,15,"فاختارت نفسي الخنق الموت على عظامي هذه"));
arasuperobject.biblemap.put("b18_7_16", new arasuperobject(18,7,16,"قد ذبت. لا الى الابد احيا. كف عني لان ايامي نفخة"));
arasuperobject.biblemap.put("b18_7_17", new arasuperobject(18,7,17,"ما هو الانسان حتى تعتبره وحتى تضع عليه قلبك"));
arasuperobject.biblemap.put("b18_7_18", new arasuperobject(18,7,18,"وتتعهّده كل صباح وكل لحظة تمتحنه"));
arasuperobject.biblemap.put("b18_7_19", new arasuperobject(18,7,19,"حتى متى لا تلتفت عني ولا ترخيني ريثما ابلع ريقي"));
arasuperobject.biblemap.put("b18_7_20", new arasuperobject(18,7,20,"أأخطأت. ماذا افعل لك يا رقيب الناس. لماذا جعلتني عاثورا لنفسك حتى اكون على نفسي حملا"));
arasuperobject.biblemap.put("b18_7_21", new arasuperobject(18,7,21,"ولماذا لا تغفر ذنبي ولا تزيل اثمي لاني الآن اضطجع في التراب. تطلبني فلا اكون"));
arasuperobject.biblemap.put("b18_8_1", new arasuperobject(18,8,1,"فاجاب بلدد الشوحي وقال"));
arasuperobject.biblemap.put("b18_8_2", new arasuperobject(18,8,2,"الى متى تقول هذا وتكون اقوال فيك ريحا شديدة"));
arasuperobject.biblemap.put("b18_8_3", new arasuperobject(18,8,3,"هل الله يعوّج القضاء او القدير يعكس الحق"));
arasuperobject.biblemap.put("b18_8_4", new arasuperobject(18,8,4,"اذ اخطأ اليه بنوك دفعهم الى يد معصيتهم"));
arasuperobject.biblemap.put("b18_8_5", new arasuperobject(18,8,5,"فان بكرت انت الى الله وتضرعت الى القدير"));
arasuperobject.biblemap.put("b18_8_6", new arasuperobject(18,8,6,"ان كنت انت زكيا مستقيما فانه الآن يتنبه لك ويسلم مسكن برك"));
arasuperobject.biblemap.put("b18_8_7", new arasuperobject(18,8,7,"وان تكن اولاك صغيرة فآخرتك تكثر جدا"));
arasuperobject.biblemap.put("b18_8_8", new arasuperobject(18,8,8,"اسأل القرون الاولى وتأكد مباحث آبائهم"));
arasuperobject.biblemap.put("b18_8_9", new arasuperobject(18,8,9,"لاننا نحن من امس ولا نعلم لان ايامنا على الارض ظل"));
arasuperobject.biblemap.put("b18_8_10", new arasuperobject(18,8,10,"فهلا يعلمونك. يقولون لك ومن قلوبهم يخرجون اقوالا قائلين"));
arasuperobject.biblemap.put("b18_8_11", new arasuperobject(18,8,11,"هل ينمي البردي في غير الغمقة او تنبت الحلفاء بلا ماء"));
arasuperobject.biblemap.put("b18_8_12", new arasuperobject(18,8,12,"وهو بعد في نضارته لم يقطع ييبس قبل كل العشب"));
arasuperobject.biblemap.put("b18_8_13", new arasuperobject(18,8,13,"هكذا سبل كل الناسين الله ورجاء الفاجر يخيب"));
arasuperobject.biblemap.put("b18_8_14", new arasuperobject(18,8,14,"فينقطع اعتماده ومتكله بيت العنكبوت"));
arasuperobject.biblemap.put("b18_8_15", new arasuperobject(18,8,15,"يستند الى بيته فلا يثبت. يتمسك به فلا يقوم"));
arasuperobject.biblemap.put("b18_8_16", new arasuperobject(18,8,16,"هو رطب تجاه الشمس وعلى جنته تنبت خراعيبه"));
arasuperobject.biblemap.put("b18_8_17", new arasuperobject(18,8,17,"واصوله مشتبكة في الرجمة فترى محل الحجارة"));
arasuperobject.biblemap.put("b18_8_18", new arasuperobject(18,8,18,"ان اقتلعه من مكانه يجحده قائلا ما رايتك"));
arasuperobject.biblemap.put("b18_8_19", new arasuperobject(18,8,19,"هذا هو فرح طريقه ومن التراب ينبت آخر"));
arasuperobject.biblemap.put("b18_8_20", new arasuperobject(18,8,20,"هوذا الله لا يرفض الكامل ولا يأخذ بيد فاعلي الشر"));
arasuperobject.biblemap.put("b18_8_21", new arasuperobject(18,8,21,"عندما يملأ فاك ضحكا وشفتيك هتافا"));
arasuperobject.biblemap.put("b18_8_22", new arasuperobject(18,8,22,"يلبس مبغضوك خزيا. اما خيمة الاشرار فلا تكون"));
arasuperobject.biblemap.put("b18_9_1", new arasuperobject(18,9,1,"فاجاب ايوب وقال"));
arasuperobject.biblemap.put("b18_9_2", new arasuperobject(18,9,2,"صحيح. قد علمت انه كذا. فكيف يتبرر الانسان عند الله"));
arasuperobject.biblemap.put("b18_9_3", new arasuperobject(18,9,3,"ان شاء ان يحاجه لا يجيبه عن واحد من الف"));
arasuperobject.biblemap.put("b18_9_4", new arasuperobject(18,9,4,"هو حكيم القلب وشديد القوة. من تصلب عليه فسلم"));
arasuperobject.biblemap.put("b18_9_5", new arasuperobject(18,9,5,"المزحزح الجبال ولا تعلم. الذي يقلبها في غضبه"));
arasuperobject.biblemap.put("b18_9_6", new arasuperobject(18,9,6,"المزعزع الارض من مقرها فتتزلزل اعمدتها"));
arasuperobject.biblemap.put("b18_9_7", new arasuperobject(18,9,7,"الآمر الشمس فلا تشرق ويختم على النجوم"));
arasuperobject.biblemap.put("b18_9_8", new arasuperobject(18,9,8,"الباسط السموات وحده والماشي على اعالي البحر"));
arasuperobject.biblemap.put("b18_9_9", new arasuperobject(18,9,9,"صانع النعش والجبار والثريا ومخادع الجنوب"));
arasuperobject.biblemap.put("b18_9_10", new arasuperobject(18,9,10,"فاعل عظائم لا تفحص وعجائب لا تعد"));
arasuperobject.biblemap.put("b18_9_11", new arasuperobject(18,9,11,"هوذا يمرّ عليّ ولا اراه ويجتاز فلا اشعر به"));
arasuperobject.biblemap.put("b18_9_12", new arasuperobject(18,9,12,"اذا خطف فمن يرده ومن يقول له ماذا تفعل"));
arasuperobject.biblemap.put("b18_9_13", new arasuperobject(18,9,13,"الله لا يرد غضبه. ينحني تحته اعوان رهب"));
arasuperobject.biblemap.put("b18_9_14", new arasuperobject(18,9,14,"كم بالاقل انا اجاوبه واختار كلامي معه"));
arasuperobject.biblemap.put("b18_9_15", new arasuperobject(18,9,15,"لاني وان تبررت لا اجاوب بل استرحم ديّاني"));
arasuperobject.biblemap.put("b18_9_16", new arasuperobject(18,9,16,"لو دعوت فاستجاب لي لما آمنت بانه سمع صوتي"));
arasuperobject.biblemap.put("b18_9_17", new arasuperobject(18,9,17,"ذاك الذي يسحقني بالعاصفة ويكثر جروحي بلا سبب"));
arasuperobject.biblemap.put("b18_9_18", new arasuperobject(18,9,18,"لا يدعني آخذ نفسي ولكن يشبعني مرائر"));
arasuperobject.biblemap.put("b18_9_19", new arasuperobject(18,9,19,"ان كان من جهة قوة القوي يقول هانذا. وان كان من جهة القضاء يقول من يحاكمني"));
arasuperobject.biblemap.put("b18_9_20", new arasuperobject(18,9,20,"ان تبررت يحكم عليّ فمي. وان كنت كاملا يستذنبني"));
arasuperobject.biblemap.put("b18_9_21", new arasuperobject(18,9,21,"كامل انا. لا ابالي بنفسي. رذلت حياتي"));
arasuperobject.biblemap.put("b18_9_22", new arasuperobject(18,9,22,"هي واحدة. لذلك قلت ان الكامل والشرير هو يفنيهما"));
arasuperobject.biblemap.put("b18_9_23", new arasuperobject(18,9,23,"اذا قتل السوط بغتة يستهزئ بتجربة الابرياء"));
arasuperobject.biblemap.put("b18_9_24", new arasuperobject(18,9,24,"الارض مسلّمة ليد الشرير. يغشي وجوه قضاتها. وان لم يكن هو فاذا من"));
arasuperobject.biblemap.put("b18_9_25", new arasuperobject(18,9,25,"ايامي اسرع من عدّاء. تفر ولا ترى خيرا"));
arasuperobject.biblemap.put("b18_9_26", new arasuperobject(18,9,26,"تمرّ مع سفن البردي. كنسر ينقض الى قنصه"));
arasuperobject.biblemap.put("b18_9_27", new arasuperobject(18,9,27,"ان قلت انسى كربتي اطلق وجهي واتبلج"));
arasuperobject.biblemap.put("b18_9_28", new arasuperobject(18,9,28,"اخاف من كل اوجاعي عالما انك لا تبرئني"));
arasuperobject.biblemap.put("b18_9_29", new arasuperobject(18,9,29,"انا مستذنب فلماذا اتعب عبثا"));
arasuperobject.biblemap.put("b18_9_30", new arasuperobject(18,9,30,"ولو اغتسلت في الثلج ونظفت يدي بالاشنان"));
arasuperobject.biblemap.put("b18_9_31", new arasuperobject(18,9,31,"فانك في النقع تغمسني حتى تكرهني ثيابي"));
arasuperobject.biblemap.put("b18_9_32", new arasuperobject(18,9,32,"لانه ليس هو انسانا مثلي فاجاوبه فنأتي جميعا الى المحاكمة"));
arasuperobject.biblemap.put("b18_9_33", new arasuperobject(18,9,33,"ليس بيننا مصالح يضع يده على كلينا"));
arasuperobject.biblemap.put("b18_9_34", new arasuperobject(18,9,34,"ليرفع عني عصاه ولا يبغتني رعبه"));
arasuperobject.biblemap.put("b18_9_35", new arasuperobject(18,9,35,"اذا اتكلم ولا اخافه. لاني لست هكذا عند نفسي"));
arasuperobject.biblemap.put("b18_10_1", new arasuperobject(18,10,1,"قد كرهت نفسي حياتي. اسيب شكواي. اتكلم في مرارة نفسي"));
arasuperobject.biblemap.put("b18_10_2", new arasuperobject(18,10,2,"قائلا لله لا تستذنبني. فهمني لماذا تخاصمني"));
arasuperobject.biblemap.put("b18_10_3", new arasuperobject(18,10,3,"احسن عندك ان تظلم ان ترذل عمل يديك وتشرق على مشورة الاشرار"));
arasuperobject.biblemap.put("b18_10_4", new arasuperobject(18,10,4,"ألك عينا بشر ام كنظر الانسان تنظر"));
arasuperobject.biblemap.put("b18_10_5", new arasuperobject(18,10,5,"أأيامك كايام الانسان ام سنوك كايام الرجل"));
arasuperobject.biblemap.put("b18_10_6", new arasuperobject(18,10,6,"حتى تبحث عن اثمي وتفتش على خطيتي"));
arasuperobject.biblemap.put("b18_10_7", new arasuperobject(18,10,7,"في علمك اني لست مذنبا ولا منقذ من يدك"));
arasuperobject.biblemap.put("b18_10_8", new arasuperobject(18,10,8,"يداك كوّنتاني وصنعتاني كلي جميعا. أفتبتلعني"));
arasuperobject.biblemap.put("b18_10_9", new arasuperobject(18,10,9,"اذكر انك جبلتني كالطين. أفتعيدني الى التراب"));
arasuperobject.biblemap.put("b18_10_10", new arasuperobject(18,10,10,"ألم تصبّني كاللبن وخثّرتني كالجبن"));
arasuperobject.biblemap.put("b18_10_11", new arasuperobject(18,10,11,"كسوتني جلدا ولحما فنسجتني بعظام وعصب"));
arasuperobject.biblemap.put("b18_10_12", new arasuperobject(18,10,12,"منحتني حياة ورحمة وحفظت عنايتك روحي"));
arasuperobject.biblemap.put("b18_10_13", new arasuperobject(18,10,13,"لكنك كتمت هذه في قلبك. علمت ان هذا عندك"));
arasuperobject.biblemap.put("b18_10_14", new arasuperobject(18,10,14,"ان اخطأت تلاحظني ولا تبرئني من اثمي"));
arasuperobject.biblemap.put("b18_10_15", new arasuperobject(18,10,15,"ان اذنبت فويل لي. وان تبررت لا ارفع راسي. اني شبعان هوانا وناظر مذلتي"));
arasuperobject.biblemap.put("b18_10_16", new arasuperobject(18,10,16,"وان ارتفع تصطادني كاسد ثم تعود وتتجبر عليّ"));
arasuperobject.biblemap.put("b18_10_17", new arasuperobject(18,10,17,"تجدد شهودك تجاهي وتزيد غضبك عليّ. نوب وجيش ضدي"));
arasuperobject.biblemap.put("b18_10_18", new arasuperobject(18,10,18,"فلماذا اخرجتني من الرحم. كنت قد اسلمت الروح ولم ترني عين"));
arasuperobject.biblemap.put("b18_10_19", new arasuperobject(18,10,19,"فكنت كاني لم اكن فأقاد من الرحم الى القبر"));
arasuperobject.biblemap.put("b18_10_20", new arasuperobject(18,10,20,"أليست ايامي قليلة. اترك. كف عني فاتبلج قليلا"));
arasuperobject.biblemap.put("b18_10_21", new arasuperobject(18,10,21,"قبل ان اذهب ولا اعود. الى ارض ظلمة وظل الموت"));
arasuperobject.biblemap.put("b18_10_22", new arasuperobject(18,10,22,"ارض ظلام مثل دجى ظل الموت وبلا ترتيب واشراقها كالدجى"));
arasuperobject.biblemap.put("b18_11_1", new arasuperobject(18,11,1,"فاجاب صوفر النعماتي وقال"));
arasuperobject.biblemap.put("b18_11_2", new arasuperobject(18,11,2,"أكثرة الكلام لا يجاوب ام رجل مهذار يتبرر"));
arasuperobject.biblemap.put("b18_11_3", new arasuperobject(18,11,3,"أصلفك يفحم الناس ام تلخ وليس من يخزيك"));
arasuperobject.biblemap.put("b18_11_4", new arasuperobject(18,11,4,"اذ تقول تعليمي زكي وانا بار في عينيك"));
arasuperobject.biblemap.put("b18_11_5", new arasuperobject(18,11,5,"ولكن يا ليت الله يتكلم ويفتح شفتيه معك"));
arasuperobject.biblemap.put("b18_11_6", new arasuperobject(18,11,6,"ويعلن لك خفيّات الحكمة انها مضاعفة الفهم فتعلم ان الله يغرمك باقل من اثمك"));
arasuperobject.biblemap.put("b18_11_7", new arasuperobject(18,11,7,"أإلى عمق الله تتصل ام الى نهاية القدير تنتهي"));
arasuperobject.biblemap.put("b18_11_8", new arasuperobject(18,11,8,"هو اعلى من السموات فماذا عساك ان تفعل. اعمق من الهاوية فماذا تدري"));
arasuperobject.biblemap.put("b18_11_9", new arasuperobject(18,11,9,"اطول من الارض طوله واعرض من البحر"));
arasuperobject.biblemap.put("b18_11_10", new arasuperobject(18,11,10,"ان بطش او اغلق او جمع فمن يرده"));
arasuperobject.biblemap.put("b18_11_11", new arasuperobject(18,11,11,"لانه هو يعلم اناس السوء ويبصر الاثم فهل لا ينتبه"));
arasuperobject.biblemap.put("b18_11_12", new arasuperobject(18,11,12,"اما الرجل ففارغ عديم الفهم وكجحش الفراء يولد الانسان"));
arasuperobject.biblemap.put("b18_11_13", new arasuperobject(18,11,13,"ان اعددت انت قلبك وبسطت اليه يديك"));
arasuperobject.biblemap.put("b18_11_14", new arasuperobject(18,11,14,"ان ابعدت الاثم الذي في يدك ولا يسكن الظلم في خيمتك"));
arasuperobject.biblemap.put("b18_11_15", new arasuperobject(18,11,15,"حينئذ ترفع وجهك بلا عيب وتكون ثابتا ولا تخاف"));
arasuperobject.biblemap.put("b18_11_16", new arasuperobject(18,11,16,"لانك تنسى المشقة. كمياه عبرت تذكرها"));
arasuperobject.biblemap.put("b18_11_17", new arasuperobject(18,11,17,"وفوق الظهيرة يقوم حظك. الظلام يتحول صباحا"));
arasuperobject.biblemap.put("b18_11_18", new arasuperobject(18,11,18,"وتطمئن لانه يوجد رجاء. تتجسس حولك وتضطجع آمنا"));
arasuperobject.biblemap.put("b18_11_19", new arasuperobject(18,11,19,"وتربض وليس من يزعج ويتضرع الى وجهك كثيرون"));
arasuperobject.biblemap.put("b18_11_20", new arasuperobject(18,11,20,"اما عيون الاشرار فتتلف ومناصهم يبيد ورجاؤهم تسليم النفس"));
arasuperobject.biblemap.put("b18_12_1", new arasuperobject(18,12,1,"فاجاب ايوب وقال"));
arasuperobject.biblemap.put("b18_12_2", new arasuperobject(18,12,2,"صحيح انكم انتم شعب ومعكم تموت الحكمة"));
arasuperobject.biblemap.put("b18_12_3", new arasuperobject(18,12,3,"غير انه لي فهم مثلكم. لست انا دونكم. ومن ليس عنده مثل هذه"));
arasuperobject.biblemap.put("b18_12_4", new arasuperobject(18,12,4,"رجلا سخرة لصاحبه صرت. دعا الله فاستجابه. سخرة هو الصديق الكامل"));
arasuperobject.biblemap.put("b18_12_5", new arasuperobject(18,12,5,"للمبتلي هوان في افكار المطمئن مهيّا لمن زلت قدمه"));
arasuperobject.biblemap.put("b18_12_6", new arasuperobject(18,12,6,"خيام المخربين مستريحة والذين يغيظون الله مطمئنون الذين ياتون بالههم في يدهم"));
arasuperobject.biblemap.put("b18_12_7", new arasuperobject(18,12,7,"فاسأل البهائم فتعلمك وطيور السماء فتخبرك"));
arasuperobject.biblemap.put("b18_12_8", new arasuperobject(18,12,8,"او كلم الارض فتعلمك ويحدثك سمك البحر"));
arasuperobject.biblemap.put("b18_12_9", new arasuperobject(18,12,9,"من لا يعلم من كل هؤلاء ان يد الرب صنعت هذا"));
arasuperobject.biblemap.put("b18_12_10", new arasuperobject(18,12,10,"الذي بيده نفس كل حيّ وروح كل البشر"));
arasuperobject.biblemap.put("b18_12_11", new arasuperobject(18,12,11,"أفليست الاذن تمتحن الاقوال كما ان الحنك يستطعم طعامه"));
arasuperobject.biblemap.put("b18_12_12", new arasuperobject(18,12,12,"عند الشيب حكمة وطول الايام فهم"));
arasuperobject.biblemap.put("b18_12_13", new arasuperobject(18,12,13,"عنده الحكمة والقدرة. له المشورة والفطنة"));
arasuperobject.biblemap.put("b18_12_14", new arasuperobject(18,12,14,"هوذا يهدم فلا يبنى. يغلق على انسان فلا يفتح"));
arasuperobject.biblemap.put("b18_12_15", new arasuperobject(18,12,15,"يمنع المياه فتيبس . ‎ يطلقها فتقلب الارض"));
arasuperobject.biblemap.put("b18_12_16", new arasuperobject(18,12,16,"عنده العزّ والفهم . ‎ له المضل والمضل"));
arasuperobject.biblemap.put("b18_12_17", new arasuperobject(18,12,17,"يذهب بالمشيرين اسرى ويحمق القضاة"));
arasuperobject.biblemap.put("b18_12_18", new arasuperobject(18,12,18,"يحل مناطق الملوك ويشد احقاءهم بوثاق"));
arasuperobject.biblemap.put("b18_12_19", new arasuperobject(18,12,19,"يذهب بالكهنة اسرى ويقلب الاقوياء"));
arasuperobject.biblemap.put("b18_12_20", new arasuperobject(18,12,20,"يقطع كلام الامناء وينزع ذوق الشيوخ"));
arasuperobject.biblemap.put("b18_12_21", new arasuperobject(18,12,21,"يلقي هوانا على الشرفاء ويرخي منطقة الاشداء"));
arasuperobject.biblemap.put("b18_12_22", new arasuperobject(18,12,22,"يكشف العمائق من الظلام ويخرج ظل الموت الى النور"));
arasuperobject.biblemap.put("b18_12_23", new arasuperobject(18,12,23,"يكثر الامم ثم يبيدها. يوسع للامم ثم يجليها"));
arasuperobject.biblemap.put("b18_12_24", new arasuperobject(18,12,24,"ينزع عقول رؤساء شعب الارض ويضلهم في تيه بلا طريق"));
arasuperobject.biblemap.put("b18_12_25", new arasuperobject(18,12,25,"يتلمسون في الظلام وليس نور ويرنحهم مثل السكران"));
arasuperobject.biblemap.put("b18_13_1", new arasuperobject(18,13,1,"هذا كله رأته عيني. سمعته اذني وفطنت به"));
arasuperobject.biblemap.put("b18_13_2", new arasuperobject(18,13,2,"ما تعرفونه عرفته انا ايضا. لست دونكم"));
arasuperobject.biblemap.put("b18_13_3", new arasuperobject(18,13,3,"ولكني اريد ان اكلم القدير وان أحاكم الى الله"));
arasuperobject.biblemap.put("b18_13_4", new arasuperobject(18,13,4,"اما انتم فملفقو كذب. اطباء بطالون كلكم"));
arasuperobject.biblemap.put("b18_13_5", new arasuperobject(18,13,5,"ليتكم تصمتون صمتا. يكون ذلك لكم حكمة"));
arasuperobject.biblemap.put("b18_13_6", new arasuperobject(18,13,6,"اسمعوا الآن حجتي واصغوا الى دعاوي شفتيّ"));
arasuperobject.biblemap.put("b18_13_7", new arasuperobject(18,13,7,"أتقولون لاجل الله ظلما وتتكلمون بغش لاجله"));
arasuperobject.biblemap.put("b18_13_8", new arasuperobject(18,13,8,"أتحابون وجهه ام عن الله تخاصمون"));
arasuperobject.biblemap.put("b18_13_9", new arasuperobject(18,13,9,"أخير لكم ان يفحصكم ام تخاتلونه كما يخاتل الانسان"));
arasuperobject.biblemap.put("b18_13_10", new arasuperobject(18,13,10,"توبيخا يوبخكم ان حابيتم الوجوه خفية"));
arasuperobject.biblemap.put("b18_13_11", new arasuperobject(18,13,11,"فهلا يرهبكم جلاله ويسقط عليكم رعبه"));
arasuperobject.biblemap.put("b18_13_12", new arasuperobject(18,13,12,"خطبكم امثال رماد وحصونكم حصون من طين"));
arasuperobject.biblemap.put("b18_13_13", new arasuperobject(18,13,13,"اسكتوا عني فاتكلم انا وليصبني مهما اصاب"));
arasuperobject.biblemap.put("b18_13_14", new arasuperobject(18,13,14,"لماذا آخذ لحمي باسناني واضع نفسي في كفي"));
arasuperobject.biblemap.put("b18_13_15", new arasuperobject(18,13,15,"هوذا يقتلني. لا انتظر شيئا. فقط ازكي طريقي قدامه"));
arasuperobject.biblemap.put("b18_13_16", new arasuperobject(18,13,16,"فهذا يعود الى خلاصي ان الفاجر لا يأتي قدامه"));
arasuperobject.biblemap.put("b18_13_17", new arasuperobject(18,13,17,"سمعا اسمعوا اقوالي وتصريحي بمسامعكم"));
arasuperobject.biblemap.put("b18_13_18", new arasuperobject(18,13,18,"هانذا قد احسنت الدعوى. اعلم اني اتبرر"));
arasuperobject.biblemap.put("b18_13_19", new arasuperobject(18,13,19,"من هو الذي يخاصمني حتى اصمت الآن واسلم الروح"));
arasuperobject.biblemap.put("b18_13_20", new arasuperobject(18,13,20,"انما أمرين لا تفعل بي فحينئذ لا اختفي من حضرتك"));
arasuperobject.biblemap.put("b18_13_21", new arasuperobject(18,13,21,"ابعد يديك عني ولا تدع هيبتك ترعبني"));
arasuperobject.biblemap.put("b18_13_22", new arasuperobject(18,13,22,"ثم ادع فانا اجيب او اتكلم فتجاوبني"));
arasuperobject.biblemap.put("b18_13_23", new arasuperobject(18,13,23,"كم لي من الآثام والخطايا. اعلمني ذنبي وخطيتي"));
arasuperobject.biblemap.put("b18_13_24", new arasuperobject(18,13,24,"لماذا تحجب وجهك وتحسبني عدوا لك"));
arasuperobject.biblemap.put("b18_13_25", new arasuperobject(18,13,25,"اترعب ورقة مندفعة وتطارد قشا يابسا"));
arasuperobject.biblemap.put("b18_13_26", new arasuperobject(18,13,26,"لانك كتبت عليّ أمورا مرّة وورثتني اثام صباي"));
arasuperobject.biblemap.put("b18_13_27", new arasuperobject(18,13,27,"فجعلت رجليّ في المقطرة ولاحظت جميع مسالكي وعلى اصول رجليّ نبشت"));
arasuperobject.biblemap.put("b18_13_28", new arasuperobject(18,13,28,"وانا كمتسوس يبلى كثوب اكله العث"));
arasuperobject.biblemap.put("b18_14_1", new arasuperobject(18,14,1,"الانسان مولود المرأة قليل الايام وشبعان تعبا"));
arasuperobject.biblemap.put("b18_14_2", new arasuperobject(18,14,2,"يخرج كالزهر ثم ينحسم ويبرح كالظل ولا يقف"));
arasuperobject.biblemap.put("b18_14_3", new arasuperobject(18,14,3,"فعلى مثل هذا حدقت عينيك واياي احضرت الى المحاكمة معك"));
arasuperobject.biblemap.put("b18_14_4", new arasuperobject(18,14,4,"من يخرج الطاهر من النجس. لا احد"));
arasuperobject.biblemap.put("b18_14_5", new arasuperobject(18,14,5,"ان كانت ايامه محدودة وعدد اشهره عندك وقد عينت اجله فلا يتجاوزه"));
arasuperobject.biblemap.put("b18_14_6", new arasuperobject(18,14,6,"فاقصر عنه ليسترح الى ان يسرّ كالاجير بانتهاء يومه"));
arasuperobject.biblemap.put("b18_14_7", new arasuperobject(18,14,7,"لان للشجرة رجاء. ان قطعت تخلف ايضا ولا تعدم خراعيبها"));
arasuperobject.biblemap.put("b18_14_8", new arasuperobject(18,14,8,"ولو قدم في الارض اصلها ومات في التراب جذعها"));
arasuperobject.biblemap.put("b18_14_9", new arasuperobject(18,14,9,"فمن رائحة الماء تفرخ وتنبت فروعا كالغرس"));
arasuperobject.biblemap.put("b18_14_10", new arasuperobject(18,14,10,"اما الرجل فيموت ويبلى. الانسان يسلم الروح فاين هو"));
arasuperobject.biblemap.put("b18_14_11", new arasuperobject(18,14,11,"قد تنفد المياه من البحرة والنهر ينشف ويجف"));
arasuperobject.biblemap.put("b18_14_12", new arasuperobject(18,14,12,"والانسان يضطجع ولا يقوم. لا يستيقظون حتى لا تبقى السموات ولا ينتبهون من نومهم"));
arasuperobject.biblemap.put("b18_14_13", new arasuperobject(18,14,13,"ليتك تواريني في الهاوية وتخفيني الى ان ينصرف غضبك وتعيّن لي اجلا فتذكرني"));
arasuperobject.biblemap.put("b18_14_14", new arasuperobject(18,14,14,"ان مات رجل أفيحيا. كل ايام جهادي اصبر الى ان يأتي بدلي"));
arasuperobject.biblemap.put("b18_14_15", new arasuperobject(18,14,15,"تدعو فانا اجيبك. تشتاق الى عمل يدك"));
arasuperobject.biblemap.put("b18_14_16", new arasuperobject(18,14,16,"اما الآن فتحصي خطواتي. ألا تحافظ على خطيتي"));
arasuperobject.biblemap.put("b18_14_17", new arasuperobject(18,14,17,"معصيتي مختوم عليها في صرّة وتلفّق عليّ فوق اثمي"));
arasuperobject.biblemap.put("b18_14_18", new arasuperobject(18,14,18,"ان الجبل الساقط ينتثر والصخر يزحزح من مكانه"));
arasuperobject.biblemap.put("b18_14_19", new arasuperobject(18,14,19,"الحجارة تبليها المياه وتجرف سيولها تراب الارض. وكذلك انت تبيد رجاء الانسان"));
arasuperobject.biblemap.put("b18_14_20", new arasuperobject(18,14,20,"تتجبر عليه ابدا فيذهب. تغير وجهه وتطرده"));
arasuperobject.biblemap.put("b18_14_21", new arasuperobject(18,14,21,"يكرم بنوه ولا يعلم او يصغرون ولا يفهم بهم"));
arasuperobject.biblemap.put("b18_14_22", new arasuperobject(18,14,22,"انما على ذاته يتوجع لحمه وعلى ذاتها تنوح نفسه"));
arasuperobject.biblemap.put("b18_15_1", new arasuperobject(18,15,1,"فاجاب اليفاز التيماني وقال"));
arasuperobject.biblemap.put("b18_15_2", new arasuperobject(18,15,2,"ألعل الحكيم يجيب عن معرفة باطلة ويملأ بطنه من ريح شرقية"));
arasuperobject.biblemap.put("b18_15_3", new arasuperobject(18,15,3,"فيحتج بكلام لا يفيد وباحاديث لا ينتفع بها"));
arasuperobject.biblemap.put("b18_15_4", new arasuperobject(18,15,4,"اما انت فتنافي المخافة وتناقض التقوى لدى الله"));
arasuperobject.biblemap.put("b18_15_5", new arasuperobject(18,15,5,"لان فمك يذيع اثمك وتختار لسان المحتالين"));
arasuperobject.biblemap.put("b18_15_6", new arasuperobject(18,15,6,"ان فمك يستذنبك لا انا وشفتاك تشهدان عليك"));
arasuperobject.biblemap.put("b18_15_7", new arasuperobject(18,15,7,"أصوّرت اول الناس ام أبدئت قبل التلال"));
arasuperobject.biblemap.put("b18_15_8", new arasuperobject(18,15,8,"هل تنصّت في مجلس الله او قصرت الحكمة على نفسك"));
arasuperobject.biblemap.put("b18_15_9", new arasuperobject(18,15,9,"ماذا تعرفه ولا نعرفه نحن وماذا تفهم وليس هو عندنا"));
arasuperobject.biblemap.put("b18_15_10", new arasuperobject(18,15,10,"عندنا الشيخ والاشيب اكبر اياما من ابيك"));
arasuperobject.biblemap.put("b18_15_11", new arasuperobject(18,15,11,"اقليلة عندك تعزيات الله والكلام معك بالرفق"));
arasuperobject.biblemap.put("b18_15_12", new arasuperobject(18,15,12,"لماذا ياخذك قلبك ولماذا تختلج عيناك"));
arasuperobject.biblemap.put("b18_15_13", new arasuperobject(18,15,13,"حتى ترد على الله وتخرج من فيك اقوالا"));
arasuperobject.biblemap.put("b18_15_14", new arasuperobject(18,15,14,"من هو الانسان حتى يزكو او مولود المرأة حتى يتبرر"));
arasuperobject.biblemap.put("b18_15_15", new arasuperobject(18,15,15,"هوذا قديسوه لا يأتمنهم والسموات غير طاهرة بعينيه"));
arasuperobject.biblemap.put("b18_15_16", new arasuperobject(18,15,16,"فبالحري مكروه وفاسد الانسان الشارب الاثم كالماء"));
arasuperobject.biblemap.put("b18_15_17", new arasuperobject(18,15,17,"أوحي اليك اسمع لي فاحدث بما رأيته"));
arasuperobject.biblemap.put("b18_15_18", new arasuperobject(18,15,18,"ما اخبر به حكماء عن آبائهم فلم يكتموه"));
arasuperobject.biblemap.put("b18_15_19", new arasuperobject(18,15,19,"الذين لهم وحدهم أعطيت الارض ولم يعبر بينهم غريب"));
arasuperobject.biblemap.put("b18_15_20", new arasuperobject(18,15,20,"الشرير هو يتلوى كل ايامه وكل عدد السنين المعدودة للعاتي"));
arasuperobject.biblemap.put("b18_15_21", new arasuperobject(18,15,21,"صوت رعوب في اذنيه في ساعة سلام ياتيه المخرب"));
arasuperobject.biblemap.put("b18_15_22", new arasuperobject(18,15,22,"لا يأمل الرجوع من الظلمة وهو مرتقب للسيف"));
arasuperobject.biblemap.put("b18_15_23", new arasuperobject(18,15,23,"تائه هو لاجل الخبز حيثما يجده ويعلم ان يوم الظلمة مهيأ بين يديه"));
arasuperobject.biblemap.put("b18_15_24", new arasuperobject(18,15,24,"يرهبه الضر والضيق. يتجبران عليه كملك مستعد للوغى"));
arasuperobject.biblemap.put("b18_15_25", new arasuperobject(18,15,25,"لانه مدّ على الله يده وعلى القدير تجبر"));
arasuperobject.biblemap.put("b18_15_26", new arasuperobject(18,15,26,"عاديا عليه متصلب العنق باوقاف مجانه معبأة"));
arasuperobject.biblemap.put("b18_15_27", new arasuperobject(18,15,27,"لانه قد كسا وجهه سمنا وربى شحما على كليتيه"));
arasuperobject.biblemap.put("b18_15_28", new arasuperobject(18,15,28,"فيسكن مدنا خربة بيوتا غير مسكونة عتيدة ان تصير رجما"));
arasuperobject.biblemap.put("b18_15_29", new arasuperobject(18,15,29,"لا يستغني ولا تثبت ثروته ولا يمتد في الارض مقتناه"));
arasuperobject.biblemap.put("b18_15_30", new arasuperobject(18,15,30,"لا تزول عنه الظلمة. خراعيبه تيبسها السموم وبنفخة فمه يزول"));
arasuperobject.biblemap.put("b18_15_31", new arasuperobject(18,15,31,"لا يتكل على السوء. يضل. لان السوء يكون اجرته"));
arasuperobject.biblemap.put("b18_15_32", new arasuperobject(18,15,32,"قبل يومه يتوفى وسعفه لا يخضرّ"));
arasuperobject.biblemap.put("b18_15_33", new arasuperobject(18,15,33,"يساقط كالجفنة حصرمه وينثر كالزيتون زهره"));
arasuperobject.biblemap.put("b18_15_34", new arasuperobject(18,15,34,"لان جماعة الفجار عاقر والنار تأكل خيام الرشوة"));
arasuperobject.biblemap.put("b18_15_35", new arasuperobject(18,15,35,"حبل شقاوة وولد اثما وبطنه أنشأ غشا"));
arasuperobject.biblemap.put("b18_16_1", new arasuperobject(18,16,1,"فاجاب ايوب وقال"));
arasuperobject.biblemap.put("b18_16_2", new arasuperobject(18,16,2,"قد سمعت كثيرا مثل هذا. معزون متعبون كلكم"));
arasuperobject.biblemap.put("b18_16_3", new arasuperobject(18,16,3,"هل من نهاية لكلام فارغ. او ماذا يهيجك حتى تجاوب"));
arasuperobject.biblemap.put("b18_16_4", new arasuperobject(18,16,4,"انا ايضا استطيع ان اتكلم مثلكم لو كانت انفسكم مكان نفسي وان اسرد عليكم اقوالا وأنغض راسي اليكم"));
arasuperobject.biblemap.put("b18_16_5", new arasuperobject(18,16,5,"بل كنت اشددكم بفمي وتعزية شفتيّ تمسككم"));
arasuperobject.biblemap.put("b18_16_6", new arasuperobject(18,16,6,"ان تكلمت لم تمتنع كآبتي. وان سكت فماذا يذهب عني"));
arasuperobject.biblemap.put("b18_16_7", new arasuperobject(18,16,7,"انه الآن ضجّرني. خربت كل جماعتي"));
arasuperobject.biblemap.put("b18_16_8", new arasuperobject(18,16,8,"قبضت عليّ. وجد شاهد. قام عليّ هزالي يجاوب في وجهي"));
arasuperobject.biblemap.put("b18_16_9", new arasuperobject(18,16,9,"غضبه افترسني واضطهدني. حرّق عليّ اسنانه. عدوي يحدد عينيه عليّ"));
arasuperobject.biblemap.put("b18_16_10", new arasuperobject(18,16,10,"فغروا عليّ افواههم. لطموني على فكّي تعييرا. تعاونوا عليّ جميعا"));
arasuperobject.biblemap.put("b18_16_11", new arasuperobject(18,16,11,"دفعني الله الى الظالم وفي ايدي الاشرار طرحني"));
arasuperobject.biblemap.put("b18_16_12", new arasuperobject(18,16,12,"كنت مستريحا فزعزعني وامسك بقفاي فحطمني ونصبني له غرضا"));
arasuperobject.biblemap.put("b18_16_13", new arasuperobject(18,16,13,"احاطت بي رماته. شق كليتيّ ولم يشفق. سفك مرارتي على الارض"));
arasuperobject.biblemap.put("b18_16_14", new arasuperobject(18,16,14,"يقتحمني اقتحاما على اقتحام. يعدو عليّ كجبار"));
arasuperobject.biblemap.put("b18_16_15", new arasuperobject(18,16,15,"خطت مسحا على جلدي ودسست في التراب قرني"));
arasuperobject.biblemap.put("b18_16_16", new arasuperobject(18,16,16,"احمر وجهي من البكاء وعلى هدبي ظل الموت"));
arasuperobject.biblemap.put("b18_16_17", new arasuperobject(18,16,17,"مع انه لا ظلم في يدي وصلاتي خالصة"));
arasuperobject.biblemap.put("b18_16_18", new arasuperobject(18,16,18,"يا ارض لا تغطي دمي ولا يكن مكان لصراخي"));
arasuperobject.biblemap.put("b18_16_19", new arasuperobject(18,16,19,"ايضا الآن هوذا في السموات شهيدي وشاهدي في الاعالي"));
arasuperobject.biblemap.put("b18_16_20", new arasuperobject(18,16,20,"المستهزئون بي هم اصحابي. لله تقطر عيني"));
arasuperobject.biblemap.put("b18_16_21", new arasuperobject(18,16,21,"لكي يحاكم الانسان عند الله كابن آدم لدى صاحبه"));
arasuperobject.biblemap.put("b18_16_22", new arasuperobject(18,16,22,"اذا مضت سنون قليلة اسلك في طريق لا اعود منها"));
arasuperobject.biblemap.put("b18_17_1", new arasuperobject(18,17,1,"روحي تلفت. ايامي انطفأت. انما القبور لي"));
arasuperobject.biblemap.put("b18_17_2", new arasuperobject(18,17,2,"لولا المخاتلون عندي وعيني تبيت على مشاجراتهم"));
arasuperobject.biblemap.put("b18_17_3", new arasuperobject(18,17,3,"كن ضامني عند نفسك. من هو الذي يصفق يدي"));
arasuperobject.biblemap.put("b18_17_4", new arasuperobject(18,17,4,"لانك منعت قلبهم عن الفطنة. لاجل ذلك لا ترفعهم"));
arasuperobject.biblemap.put("b18_17_5", new arasuperobject(18,17,5,"الذي يسلم الاصحاب للسلب تتلف عيون بنيه"));
arasuperobject.biblemap.put("b18_17_6", new arasuperobject(18,17,6,"اوقفني مثلا للشعوب وصرت للبصق في الوجه"));
arasuperobject.biblemap.put("b18_17_7", new arasuperobject(18,17,7,"كلت عيني من الحزن واعضائي كلها كالظل"));
arasuperobject.biblemap.put("b18_17_8", new arasuperobject(18,17,8,"يتعجب المستقيمون من هذا والبريء ينتهض على الفاجر"));
arasuperobject.biblemap.put("b18_17_9", new arasuperobject(18,17,9,"اما الصدّيق فيستمسك بطريقه والطاهر اليدين يزداد قوة"));
arasuperobject.biblemap.put("b18_17_10", new arasuperobject(18,17,10,"ولكن ارجعوا كلكم وتعالوا فلا اجد فيكم حكيما"));
arasuperobject.biblemap.put("b18_17_11", new arasuperobject(18,17,11,"ايامي قد عبرت. مقاصدي إرث قلبي قد انتزعت"));
arasuperobject.biblemap.put("b18_17_12", new arasuperobject(18,17,12,"يجعلون الليل نهارا نورا قريبا للظلمة"));
arasuperobject.biblemap.put("b18_17_13", new arasuperobject(18,17,13,"اذا رجوت الهاوية بيتا لي وفي الظلام مهدت فراشي"));
arasuperobject.biblemap.put("b18_17_14", new arasuperobject(18,17,14,"وقلت للقبر انت ابي وللدود انت امي واختي"));
arasuperobject.biblemap.put("b18_17_15", new arasuperobject(18,17,15,"فاين اذا آمالي. آمالي. من يعاينها"));
arasuperobject.biblemap.put("b18_17_16", new arasuperobject(18,17,16,"تهبط الى مغاليق الهاوية اذ ترتاح معا في التراب"));
arasuperobject.biblemap.put("b18_18_1", new arasuperobject(18,18,1,"فاجاب بلدد الشوحي وقال"));
arasuperobject.biblemap.put("b18_18_2", new arasuperobject(18,18,2,"الى متى تضعون اشراكا للكلام. تعقّلوا وبعد نتكلم"));
arasuperobject.biblemap.put("b18_18_3", new arasuperobject(18,18,3,"لماذا حسبنا كالبهيمة وتنجسنا في عيونكم"));
arasuperobject.biblemap.put("b18_18_4", new arasuperobject(18,18,4,"يا ايها المفترس نفسه في غيظه هل لاجلك تخلى الارض او يزحزح الصخر من مكانه"));
arasuperobject.biblemap.put("b18_18_5", new arasuperobject(18,18,5,"نعم. نور الاشرار ينطفئ ولا يضيء لهيب ناره"));
arasuperobject.biblemap.put("b18_18_6", new arasuperobject(18,18,6,"النور يظلم في خيمته وسراجه فوقه ينطفئ"));
arasuperobject.biblemap.put("b18_18_7", new arasuperobject(18,18,7,"تقصر خطوات قوته وتصرعه مشورته"));
arasuperobject.biblemap.put("b18_18_8", new arasuperobject(18,18,8,"لان رجليه تدفعانه في المصلاة فيمشي الى شبكة"));
arasuperobject.biblemap.put("b18_18_9", new arasuperobject(18,18,9,"يمسك الفخ بعقبه وتتمكن منه الشرك"));
arasuperobject.biblemap.put("b18_18_10", new arasuperobject(18,18,10,"مطمورة في الارض حبالته ومصيدته في السبيل"));
arasuperobject.biblemap.put("b18_18_11", new arasuperobject(18,18,11,"ترهبه اهوال من حوله وتذعره عند رجليه"));
arasuperobject.biblemap.put("b18_18_12", new arasuperobject(18,18,12,"تكون قوته جائعة والبوار مهيأ بجانبه"));
arasuperobject.biblemap.put("b18_18_13", new arasuperobject(18,18,13,"ياكل اعضاء جسده يأكل اعضاءه بكر الموت"));
arasuperobject.biblemap.put("b18_18_14", new arasuperobject(18,18,14,"ينقطع عن خيمته عن اعتماده ويساق الى ملك الاهوال"));
arasuperobject.biblemap.put("b18_18_15", new arasuperobject(18,18,15,"يسكن في خيمته من ليس له. يذر على مربضه كبريت"));
arasuperobject.biblemap.put("b18_18_16", new arasuperobject(18,18,16,"من تحت تيبس اصوله ومن فوق يقطع فرعه"));
arasuperobject.biblemap.put("b18_18_17", new arasuperobject(18,18,17,"ذكره يبيد من الارض ولا اسم له على وجه البر"));
arasuperobject.biblemap.put("b18_18_18", new arasuperobject(18,18,18,"يدفع من النور الى الظلمة ومن المسكونة يطرد"));
arasuperobject.biblemap.put("b18_18_19", new arasuperobject(18,18,19,"لا نسل ولا عقب له بين شعبه ولا شارد في محاله"));
arasuperobject.biblemap.put("b18_18_20", new arasuperobject(18,18,20,"يتعجب من يومه المتأخرون ويقشعر الاقدمون"));
arasuperobject.biblemap.put("b18_18_21", new arasuperobject(18,18,21,"انما تلك مساكن فاعلي الشر وهذا مقام من لا يعرف الله"));
arasuperobject.biblemap.put("b18_19_1", new arasuperobject(18,19,1,"فاجاب ايوب وقال"));
arasuperobject.biblemap.put("b18_19_2", new arasuperobject(18,19,2,"حتى متى تعذبون نفسي وتسحقونني بالكلام"));
arasuperobject.biblemap.put("b18_19_3", new arasuperobject(18,19,3,"هذه عشر مرات اخزيتموني. لم تخجلوا من ان تحكروني"));
arasuperobject.biblemap.put("b18_19_4", new arasuperobject(18,19,4,"وهبني ضللت حقا. عليّ تستقر ضلالتي"));
arasuperobject.biblemap.put("b18_19_5", new arasuperobject(18,19,5,"ان كنتم بالحق تستكبرون عليّ فثبتوا عليّ عاري"));
arasuperobject.biblemap.put("b18_19_6", new arasuperobject(18,19,6,"فاعلموا اذا ان الله قد عوّجني ولف عليّ احبولته"));
arasuperobject.biblemap.put("b18_19_7", new arasuperobject(18,19,7,"ها اني اصرخ ظلما فلا أستجاب. ادعو وليس حكم"));
arasuperobject.biblemap.put("b18_19_8", new arasuperobject(18,19,8,"قد حوّط طريقي فلا اعبر وعلى سبلي جعل ظلاما"));
arasuperobject.biblemap.put("b18_19_9", new arasuperobject(18,19,9,"ازال عني كرامتي ونزع تاج راسي"));
arasuperobject.biblemap.put("b18_19_10", new arasuperobject(18,19,10,"هدمني من كل جهة فذهبت. وقلع مثل شجرة رجائي"));
arasuperobject.biblemap.put("b18_19_11", new arasuperobject(18,19,11,"واضرم عليّ غضبه وحسبني كاعدائه"));
arasuperobject.biblemap.put("b18_19_12", new arasuperobject(18,19,12,"معا جاءت غزاته واعدّوا عليّ طريقهم وحلّوا حول خيمتي"));
arasuperobject.biblemap.put("b18_19_13", new arasuperobject(18,19,13,"قد ابعد عني اخوتي. ومعارفي زاغوا عني"));
arasuperobject.biblemap.put("b18_19_14", new arasuperobject(18,19,14,"اقاربي قد خذلوني والذين عرفوني نسوني"));
arasuperobject.biblemap.put("b18_19_15", new arasuperobject(18,19,15,"نزلاء بيتي وامائي يحسبونني اجنبيا. صرت في اعينهم غريبا"));
arasuperobject.biblemap.put("b18_19_16", new arasuperobject(18,19,16,"عبدي دعوت فلم يجب. بفمي تضرعت اليه"));
arasuperobject.biblemap.put("b18_19_17", new arasuperobject(18,19,17,"نكهتي مكروهة عند امرأتي وخممت عند ابناء احشائي"));
arasuperobject.biblemap.put("b18_19_18", new arasuperobject(18,19,18,"الاولاد ايضا قد رذلوني. اذا قمت يتكلمون عليّ"));
arasuperobject.biblemap.put("b18_19_19", new arasuperobject(18,19,19,"كرهني كل رجالي والذين احببتهم انقلبوا عليّ"));
arasuperobject.biblemap.put("b18_19_20", new arasuperobject(18,19,20,"عظمي قد لصق بجلدي ولحمي ونجوت بجلد اسناني"));
arasuperobject.biblemap.put("b18_19_21", new arasuperobject(18,19,21,"تراءفوا تراءفوا انتم عليّ يا اصحابي لان يد الله قد مسّتني"));
arasuperobject.biblemap.put("b18_19_22", new arasuperobject(18,19,22,"لماذا تطاردونني كما الله ولا تشبعون من لحمي"));
arasuperobject.biblemap.put("b18_19_23", new arasuperobject(18,19,23,"ليت كلماتي الآن تكتب. يا ليتها رسمت في سفر"));
arasuperobject.biblemap.put("b18_19_24", new arasuperobject(18,19,24,"ونقرت الى الابد في الصخر بقلم حديد وبرصاص"));
arasuperobject.biblemap.put("b18_19_25", new arasuperobject(18,19,25,"اما انا فقد علمت ان وليّي حيّ والآخر على الارض يقوم"));
arasuperobject.biblemap.put("b18_19_26", new arasuperobject(18,19,26,"وبعد ان يفنى جلدي هذا وبدون جسدي ارى الله"));
arasuperobject.biblemap.put("b18_19_27", new arasuperobject(18,19,27,"الذي اراه انا لنفسي وعيناي تنظران وليس آخر. الى ذلك تتوق كليتاي في جوفي"));
arasuperobject.biblemap.put("b18_19_28", new arasuperobject(18,19,28,"فانكم تقولون لماذا نطارده. والكلام الاصلي يوجد عندي"));
arasuperobject.biblemap.put("b18_19_29", new arasuperobject(18,19,29,"خافوا على انفسكم من السيف لان الغيظ من آثام السيف. لكي تعلموا ما هو القضاء"));
arasuperobject.biblemap.put("b18_20_1", new arasuperobject(18,20,1,"فاجاب صوفر النعماتي وقال"));
arasuperobject.biblemap.put("b18_20_2", new arasuperobject(18,20,2,"من اجل ذلك هواجسي تجيبني ولهذا هيجاني فيّ"));
arasuperobject.biblemap.put("b18_20_3", new arasuperobject(18,20,3,"تعيير توبيخي اسمع. وروح من فهمي يجيبني"));
arasuperobject.biblemap.put("b18_20_4", new arasuperobject(18,20,4,"أما علمت هذا من القديم منذ وضع الانسان على الارض"));
arasuperobject.biblemap.put("b18_20_5", new arasuperobject(18,20,5,"ان هتاف الاشرار من قريب وفرح الفاجر الى لحظة"));
arasuperobject.biblemap.put("b18_20_6", new arasuperobject(18,20,6,"ولو بلغ السموات طوله ومسّ راسه السحاب"));
arasuperobject.biblemap.put("b18_20_7", new arasuperobject(18,20,7,"كجلّته الى الابد يبيد. الذين رأوه يقولون اين هو"));
arasuperobject.biblemap.put("b18_20_8", new arasuperobject(18,20,8,"كالحلم يطير فلا يوجد ويطرد كطيف الليل"));
arasuperobject.biblemap.put("b18_20_9", new arasuperobject(18,20,9,"عين ابصرته لا تعود تراه ومكانه لن يراه بعد"));
arasuperobject.biblemap.put("b18_20_10", new arasuperobject(18,20,10,"بنوه يترضون الفقراء ويداه تردان ثروته"));
arasuperobject.biblemap.put("b18_20_11", new arasuperobject(18,20,11,"عظامه ملآنة شبيبة ومعه في التراب تضطجع"));
arasuperobject.biblemap.put("b18_20_12", new arasuperobject(18,20,12,"ان حلا في فمه الشر واخفاه تحت لسانه"));
arasuperobject.biblemap.put("b18_20_13", new arasuperobject(18,20,13,"اشفق عليه ولم يتركه بل حبسه وسط حنكه"));
arasuperobject.biblemap.put("b18_20_14", new arasuperobject(18,20,14,"فخبزه في امعائه يتحول. مرارة اصلال في بطنه"));
arasuperobject.biblemap.put("b18_20_15", new arasuperobject(18,20,15,"قد بلع ثروة فيتقيأها. الله يطردها من بطنه"));
arasuperobject.biblemap.put("b18_20_16", new arasuperobject(18,20,16,"سمّ الاصلال يرضع. يقتله لسان الافعى"));
arasuperobject.biblemap.put("b18_20_17", new arasuperobject(18,20,17,"لا يرى الجداول انهار سواقي عسل ولبن"));
arasuperobject.biblemap.put("b18_20_18", new arasuperobject(18,20,18,"يرد تعبه ولا يبلعه. كمال تحت رجع. ولا يفرح"));
arasuperobject.biblemap.put("b18_20_19", new arasuperobject(18,20,19,"لانه رضض المساكين وتركهم واغتصب بيتا ولم يبنه"));
arasuperobject.biblemap.put("b18_20_20", new arasuperobject(18,20,20,"لانه لم يعرف في بطنه قناعة لا ينجو بمشتهاه"));
arasuperobject.biblemap.put("b18_20_21", new arasuperobject(18,20,21,"ليست من اكله بقية لاجل ذلك لا يدوم خيره"));
arasuperobject.biblemap.put("b18_20_22", new arasuperobject(18,20,22,"مع ملء رغده يتضايق. تأتي عليه يد كل شقي"));
arasuperobject.biblemap.put("b18_20_23", new arasuperobject(18,20,23,"يكون عندما يملأ بطنه ان الله يرسل عليه حمو غضبه ويمطره عليه عند طعامه"));
arasuperobject.biblemap.put("b18_20_24", new arasuperobject(18,20,24,"يفر من سلاح حديد. تخرقه قوس نحاس"));
arasuperobject.biblemap.put("b18_20_25", new arasuperobject(18,20,25,"جذبه فخرج من بطنه والبارق من مرارته مرق. عليه رعوب"));
arasuperobject.biblemap.put("b18_20_26", new arasuperobject(18,20,26,"كل ظلمة مختبأة لذخائره. تأكله نار لم تنفخ. ترعى البقية في خيمته"));
arasuperobject.biblemap.put("b18_20_27", new arasuperobject(18,20,27,"السموات تعلن اثمه والارض تنهض عليه"));
arasuperobject.biblemap.put("b18_20_28", new arasuperobject(18,20,28,"تزول غلة بيته. تهراق في يوم غضبه"));
arasuperobject.biblemap.put("b18_20_29", new arasuperobject(18,20,29,"هذا نصيب الانسان الشرير من عند الله وميراث أمره من القدير"));
arasuperobject.biblemap.put("b18_21_1", new arasuperobject(18,21,1,"فاجاب ايوب وقال"));
arasuperobject.biblemap.put("b18_21_2", new arasuperobject(18,21,2,"اسمعوا قولي سمعا وليكن هذا تعزيتكم"));
arasuperobject.biblemap.put("b18_21_3", new arasuperobject(18,21,3,"احتملوني وانا اتكلم وبعد كلامي استهزئوا"));
arasuperobject.biblemap.put("b18_21_4", new arasuperobject(18,21,4,"اما انا فهل شكواي من انسان. وان كانت فلماذا لا تضيق روحي"));
arasuperobject.biblemap.put("b18_21_5", new arasuperobject(18,21,5,"تفرسوا فيّ وتعجبوا وضعوا اليد على الفم"));
arasuperobject.biblemap.put("b18_21_6", new arasuperobject(18,21,6,"عندما اتذكر ارتاع واخذت بشري رعدة"));
arasuperobject.biblemap.put("b18_21_7", new arasuperobject(18,21,7,"لماذا تحيا الاشرار ويشيخون نعم ويتجبّرون قوة"));
arasuperobject.biblemap.put("b18_21_8", new arasuperobject(18,21,8,"نسلهم قائم امامهم معهم وذريتهم في اعينهم"));
arasuperobject.biblemap.put("b18_21_9", new arasuperobject(18,21,9,"بيوتهم آمنة من الخوف وليس عليهم عصا الله"));
arasuperobject.biblemap.put("b18_21_10", new arasuperobject(18,21,10,"ثورهم يلقح ولا يخطئ. بقرتهم تنتج ولا تسقط"));
arasuperobject.biblemap.put("b18_21_11", new arasuperobject(18,21,11,"يسرحون مثل الغنم رضّعهم واطفالهم ترقص"));
arasuperobject.biblemap.put("b18_21_12", new arasuperobject(18,21,12,"يحملون الدف والعود ويطربون بصوت المزمار"));
arasuperobject.biblemap.put("b18_21_13", new arasuperobject(18,21,13,"يقضون ايامهم بالخير. في لحظة يهبطون الى الهاوية"));
arasuperobject.biblemap.put("b18_21_14", new arasuperobject(18,21,14,"فيقولون للّه ابعد عنا. وبمعرفة طرقك لا نسرّ"));
arasuperobject.biblemap.put("b18_21_15", new arasuperobject(18,21,15,"من هو القدير حتى نعبده وماذا ننتفع ان التمسناه"));
arasuperobject.biblemap.put("b18_21_16", new arasuperobject(18,21,16,"هوذا ليس في يدهم خيرهم. لتبعد عني مشورة الاشرار"));
arasuperobject.biblemap.put("b18_21_17", new arasuperobject(18,21,17,"كم ينطفئ سراج الاشرار ويأتي عليهم بوارهم او يقسم لهم اوجاعا في غضبه"));
arasuperobject.biblemap.put("b18_21_18", new arasuperobject(18,21,18,"او يكونون كالتبن قدام الريح وكالعاصفة التي تسرقها الزوبعة"));
arasuperobject.biblemap.put("b18_21_19", new arasuperobject(18,21,19,"الله يخزن اثمه لبنيه. ليجازه نفسه فيعلم"));
arasuperobject.biblemap.put("b18_21_20", new arasuperobject(18,21,20,"لتنظر عيناه هلاكه ومن حمة القدير يشرب"));
arasuperobject.biblemap.put("b18_21_21", new arasuperobject(18,21,21,"فما هي مسرّته في بيته بعده وقد تعيّن عدد شهوره"));
arasuperobject.biblemap.put("b18_21_22", new arasuperobject(18,21,22,"أالله يعلم معرفة وهو يقضي على العالين"));
arasuperobject.biblemap.put("b18_21_23", new arasuperobject(18,21,23,"هذا يموت في عين كماله. كله مطمئن وساكن"));
arasuperobject.biblemap.put("b18_21_24", new arasuperobject(18,21,24,"احواضه ملآنة لبنا ومخ عظامه طريء"));
arasuperobject.biblemap.put("b18_21_25", new arasuperobject(18,21,25,"وذلك يموت بنفس مرة ولم يذق خيرا"));
arasuperobject.biblemap.put("b18_21_26", new arasuperobject(18,21,26,"كلاهما يضطجعان معا في التراب والدود يغشاهما"));
arasuperobject.biblemap.put("b18_21_27", new arasuperobject(18,21,27,"هوذا قد علمت افكاركم والنيات التي بها تظلمونني"));
arasuperobject.biblemap.put("b18_21_28", new arasuperobject(18,21,28,"لانكم تقولون اين بيت العاتي واين خيمة مساكن الاشرار"));
arasuperobject.biblemap.put("b18_21_29", new arasuperobject(18,21,29,"أفلم تسالوا عابري السبيل ولم تفطنوا لدلائلهم"));
arasuperobject.biblemap.put("b18_21_30", new arasuperobject(18,21,30,"انه ليوم البوار يمسك الشرير ليوم السخط يقادون"));
arasuperobject.biblemap.put("b18_21_31", new arasuperobject(18,21,31,"من يعلن طريقه لوجهه. ومن يجازيه على ما عمل"));
arasuperobject.biblemap.put("b18_21_32", new arasuperobject(18,21,32,"هو الى القبور يقاد وعلى المدفن يسهر"));
arasuperobject.biblemap.put("b18_21_33", new arasuperobject(18,21,33,"حلو له مدر الوادي. يزحف كل انسان وراءه وقدامه ما لا عدد له"));
arasuperobject.biblemap.put("b18_21_34", new arasuperobject(18,21,34,"فكيف تعزونني باطلا واجوبتكم بقيت خيانة"));
arasuperobject.biblemap.put("b18_22_1", new arasuperobject(18,22,1,"فاجاب اليفاز التيماني وقال"));
arasuperobject.biblemap.put("b18_22_2", new arasuperobject(18,22,2,"هل ينفع الانسان الله. بل ينفع نفسه الفطن"));
arasuperobject.biblemap.put("b18_22_3", new arasuperobject(18,22,3,"هل من مسرّة للقدير اذا تبررت او من فائدة اذا قوّمت طرقك"));
arasuperobject.biblemap.put("b18_22_4", new arasuperobject(18,22,4,"هل على تقواك يوبّخك او يدخل معك في المحاكمة"));
arasuperobject.biblemap.put("b18_22_5", new arasuperobject(18,22,5,"أليس شرك عظيما وآثامك لا نهاية لها"));
arasuperobject.biblemap.put("b18_22_6", new arasuperobject(18,22,6,"لانك ارتهنت اخاك بلا سبب وسلبت ثياب العراة"));
arasuperobject.biblemap.put("b18_22_7", new arasuperobject(18,22,7,"ماء لم تسق العطشان وعن الجوعان منعت خبزا"));
arasuperobject.biblemap.put("b18_22_8", new arasuperobject(18,22,8,"اما صاحب القوة فله الارض والمترفّع الوجه ساكن فيها"));
arasuperobject.biblemap.put("b18_22_9", new arasuperobject(18,22,9,"الارامل ارسلت خاليات وذراع اليتامى انسحقت"));
arasuperobject.biblemap.put("b18_22_10", new arasuperobject(18,22,10,"لاجل ذلك حواليك فخاخ ويريعك رعب بغتة"));
arasuperobject.biblemap.put("b18_22_11", new arasuperobject(18,22,11,"او ظلمة فلا ترى وفيض المياه يغطيك"));
arasuperobject.biblemap.put("b18_22_12", new arasuperobject(18,22,12,"هوذا الله في علو السموات. وانظر راس الكواكب ما اعلاه"));
arasuperobject.biblemap.put("b18_22_13", new arasuperobject(18,22,13,"فقلت كيف يعلم الله. هل من وراء الضباب يقضي"));
arasuperobject.biblemap.put("b18_22_14", new arasuperobject(18,22,14,"السحاب ستر له فلا يرى وعلى دائرة السموات يتمشى"));
arasuperobject.biblemap.put("b18_22_15", new arasuperobject(18,22,15,"هل تحفظ طريق القدم الذي داسه رجال الاثم"));
arasuperobject.biblemap.put("b18_22_16", new arasuperobject(18,22,16,"الذين قبض عليهم قبل الوقت. الغمر انصبّ على اساسهم"));
arasuperobject.biblemap.put("b18_22_17", new arasuperobject(18,22,17,"القائلين لله ابعد عنا. وماذا يفعل القدير لهم"));
arasuperobject.biblemap.put("b18_22_18", new arasuperobject(18,22,18,"وهو قد ملأ بيوتهم خيرا. لتبعد عني مشورة الاشرار"));
arasuperobject.biblemap.put("b18_22_19", new arasuperobject(18,22,19,"الابرار ينظرون ويفرحون والبريء يستهزئ بهم قائلين"));
arasuperobject.biblemap.put("b18_22_20", new arasuperobject(18,22,20,"ألم يبد مقاومونا وبقيتهم قد اكلها النار"));
arasuperobject.biblemap.put("b18_22_21", new arasuperobject(18,22,21,"تعرّف به واسلم. بذلك ياتيك خير"));
arasuperobject.biblemap.put("b18_22_22", new arasuperobject(18,22,22,"اقبل الشريعة من فيه وضع كلامه في قلبك"));
arasuperobject.biblemap.put("b18_22_23", new arasuperobject(18,22,23,"ان رجعت الى القدير تبنى. ان ابعدت ظلما من خيمتك"));
arasuperobject.biblemap.put("b18_22_24", new arasuperobject(18,22,24,"والقيت التبر على التراب وذهب اوفير بين حصا الاودية"));
arasuperobject.biblemap.put("b18_22_25", new arasuperobject(18,22,25,"يكون القدير تبرك وفضة اتعاب لك"));
arasuperobject.biblemap.put("b18_22_26", new arasuperobject(18,22,26,"لانك حينئذ تتلذذ بالقدير وترفع الى الله وجهك"));
arasuperobject.biblemap.put("b18_22_27", new arasuperobject(18,22,27,"تصلّي له فيستمع لك ونذورك توفيها"));
arasuperobject.biblemap.put("b18_22_28", new arasuperobject(18,22,28,"وتجزم امرا فيثبت لك وعلى طرقك يضيء نور"));
arasuperobject.biblemap.put("b18_22_29", new arasuperobject(18,22,29,"اذا وضعوا تقول رفع. ويخلص المنخفض العينين"));
arasuperobject.biblemap.put("b18_22_30", new arasuperobject(18,22,30,"ينجي غير البريء وينجي بطهارة يديك"));
arasuperobject.biblemap.put("b18_23_1", new arasuperobject(18,23,1,"فاجاب ايوب وقال"));
arasuperobject.biblemap.put("b18_23_2", new arasuperobject(18,23,2,"اليوم ايضا شكواي تمرد. ضربتي اثقل من تنهدي"));
arasuperobject.biblemap.put("b18_23_3", new arasuperobject(18,23,3,"من يعطيني ان اجده فآتي الى كرسيه"));
arasuperobject.biblemap.put("b18_23_4", new arasuperobject(18,23,4,"احسن الدعوى امامه واملأ فمي حججا"));
arasuperobject.biblemap.put("b18_23_5", new arasuperobject(18,23,5,"فاعرف الاقوال التي بها يجيبني وافهم ما يقوله لي"));
arasuperobject.biblemap.put("b18_23_6", new arasuperobject(18,23,6,"أبكثرة قوة يخاصمني. كلا. ولكنه كان ينتبه اليّ"));
arasuperobject.biblemap.put("b18_23_7", new arasuperobject(18,23,7,"هنالك كان يحاجه المستقيم وكنت انجو الى الابد من قاضيّ"));
arasuperobject.biblemap.put("b18_23_8", new arasuperobject(18,23,8,"هانذا اذهب شرقا فليس هو هناك وغربا فلا اشعر به"));
arasuperobject.biblemap.put("b18_23_9", new arasuperobject(18,23,9,"شمالا حيث عمله فلا انظره. يتعطف الجنوب فلا اراه"));
arasuperobject.biblemap.put("b18_23_10", new arasuperobject(18,23,10,"لانه يعرف طريقي. اذا جربني اخرج كالذهب"));
arasuperobject.biblemap.put("b18_23_11", new arasuperobject(18,23,11,"بخطواته استمسكت رجلي حفظت طريقه ولم أحد"));
arasuperobject.biblemap.put("b18_23_12", new arasuperobject(18,23,12,"من وصية شفتيه لم ابرح. اكثر من فريضتي ذخرت كلام فيه"));
arasuperobject.biblemap.put("b18_23_13", new arasuperobject(18,23,13,"اما هو فوحده فمن يردّه. ونفسه تشتهي فيفعل"));
arasuperobject.biblemap.put("b18_23_14", new arasuperobject(18,23,14,"لانه يتمم المفروض عليّ وكثير مثل هذه عنده"));
arasuperobject.biblemap.put("b18_23_15", new arasuperobject(18,23,15,"من اجل ذلك ارتاع قدامه. اتأمل فارتعب منه"));
arasuperobject.biblemap.put("b18_23_16", new arasuperobject(18,23,16,"لان الله قد اضعف قلبي والقدير روّعني"));
arasuperobject.biblemap.put("b18_23_17", new arasuperobject(18,23,17,"لاني لم أقطع قبل الظلام ومن وجهي لم يغط الدجى"));
arasuperobject.biblemap.put("b18_24_1", new arasuperobject(18,24,1,"لماذا اذ لم تختبئ الازمنة من القدير لا يرى عارفوه يومه"));
arasuperobject.biblemap.put("b18_24_2", new arasuperobject(18,24,2,"ينقلون التخوم. يغتصبون قطيعا ويرعونه"));
arasuperobject.biblemap.put("b18_24_3", new arasuperobject(18,24,3,"يستاقون حمار اليتامى ويرتهنون ثور الارملة"));
arasuperobject.biblemap.put("b18_24_4", new arasuperobject(18,24,4,"يصدّون الفقراء عن الطريق. مساكين الارض يختبئون جميعا"));
arasuperobject.biblemap.put("b18_24_5", new arasuperobject(18,24,5,"ها هم كالفراء في القفر يخرجون الى عملهم يبكرون للطعام. البادية لهم خبز لاولادهم"));
arasuperobject.biblemap.put("b18_24_6", new arasuperobject(18,24,6,"في الحقل يحصدون علفهم ويعللون كرم الشرير"));
arasuperobject.biblemap.put("b18_24_7", new arasuperobject(18,24,7,"يبيتون عراة بلا لبس وليس لهم كسوة في البرد"));
arasuperobject.biblemap.put("b18_24_8", new arasuperobject(18,24,8,"يبتلّون من مطر الجبال ولعدم الملجإ يعتنقون الصخر"));
arasuperobject.biblemap.put("b18_24_9", new arasuperobject(18,24,9,"يخطفون اليتيم عن الثدي ومن المساكين يرتهنون"));
arasuperobject.biblemap.put("b18_24_10", new arasuperobject(18,24,10,"عراة يذهبون بلا لبس وجائعين يحملون حزما"));
arasuperobject.biblemap.put("b18_24_11", new arasuperobject(18,24,11,"يعصرون الزيت داخل اسوارهم. يدوسون المعاصر ويعطشون"));
arasuperobject.biblemap.put("b18_24_12", new arasuperobject(18,24,12,"من الوجع اناس يئنون ونفس الجرحى تستغيث والله لا ينتبه الى الظلم"));
arasuperobject.biblemap.put("b18_24_13", new arasuperobject(18,24,13,"اولئك يكونون بين المتمردين على النور لا يعرفون طرقه ولا يلبثون في سبله"));
arasuperobject.biblemap.put("b18_24_14", new arasuperobject(18,24,14,"مع النور يقوم القاتل يقتل المسكين والفقير وفي الليل يكون كاللص"));
arasuperobject.biblemap.put("b18_24_15", new arasuperobject(18,24,15,"وعين الزاني تلاحظ العشاء. يقول لا تراقبني عين. فيجعل سترا على وجهه"));
arasuperobject.biblemap.put("b18_24_16", new arasuperobject(18,24,16,"ينقبون البيوت في الظلام. في النهار يغلقون على انفسهم. لا يعرفون النور"));
arasuperobject.biblemap.put("b18_24_17", new arasuperobject(18,24,17,"لانه سواء عليهم الصباح وظل الموت. لانهم يعلمون اهوال ظل الموت"));
arasuperobject.biblemap.put("b18_24_18", new arasuperobject(18,24,18,"خفيف هو على وجه المياه. ملعون نصيبهم في الارض. لا يتوجه الى طريق الكروم"));
arasuperobject.biblemap.put("b18_24_19", new arasuperobject(18,24,19,"القحط والقيظ يذهبان بمياه الثلج. كذا الهاوية بالذين اخطأوا"));
arasuperobject.biblemap.put("b18_24_20", new arasuperobject(18,24,20,"تنساه الرحم يستحليه الدود. لا يذكر بعد وينكسر الاثيم كشجرة"));
arasuperobject.biblemap.put("b18_24_21", new arasuperobject(18,24,21,"يسيء الى العاقر التي لم تلد ولا يحسن الى الارملة"));
arasuperobject.biblemap.put("b18_24_22", new arasuperobject(18,24,22,"يمسك الاعزاء بقوته. يقوم فلا يأمن احد بحياته"));
arasuperobject.biblemap.put("b18_24_23", new arasuperobject(18,24,23,"يعطيه طمأنينة فيتوكل ولكن عيناه على طرقهم"));
arasuperobject.biblemap.put("b18_24_24", new arasuperobject(18,24,24,"يترفعون قليلا ثم لا يكونون ويحطون. كالكل يجمعون وكرأس السنبلة يقطعون"));
arasuperobject.biblemap.put("b18_24_25", new arasuperobject(18,24,25,"وان لم يكن كذا فمن يكذبني ويجعل كلامي لا شيئا"));
arasuperobject.biblemap.put("b18_25_1", new arasuperobject(18,25,1,"فاجاب بلدد الشوحي وقال"));
arasuperobject.biblemap.put("b18_25_2", new arasuperobject(18,25,2,"السلطان والهيبة عنده. هو صانع السلام في اعاليه"));
arasuperobject.biblemap.put("b18_25_3", new arasuperobject(18,25,3,"هل من عدد لجنوده وعلى من لا يشرق نوره"));
arasuperobject.biblemap.put("b18_25_4", new arasuperobject(18,25,4,"فكيف يتبرر الانسان عند الله وكيف يزكو مولود المرأة"));
arasuperobject.biblemap.put("b18_25_5", new arasuperobject(18,25,5,"هوذا نفس القمر لا يضيء والكواكب غير نقية في عينيه"));
arasuperobject.biblemap.put("b18_25_6", new arasuperobject(18,25,6,"فكم بالحري الانسان الرمّة وابن آدم الدود"));
arasuperobject.biblemap.put("b18_26_1", new arasuperobject(18,26,1,"فاجاب ايوب وقال"));
arasuperobject.biblemap.put("b18_26_2", new arasuperobject(18,26,2,"كيف اعنت من لا قوة له وخلصت ذراعا لا عزّ لها"));
arasuperobject.biblemap.put("b18_26_3", new arasuperobject(18,26,3,"كيف اشرت على من لا حكمة له واظهرت الفهم بكثرة"));
arasuperobject.biblemap.put("b18_26_4", new arasuperobject(18,26,4,"لمن اعلنت اقوالا ونسمة من خرجت منك"));
arasuperobject.biblemap.put("b18_26_5", new arasuperobject(18,26,5,"الأخيلة ترتعد من تحت المياه وسكانها"));
arasuperobject.biblemap.put("b18_26_6", new arasuperobject(18,26,6,"الهاوية عريانة قدامه والهلاك ليس له غطاء"));
arasuperobject.biblemap.put("b18_26_7", new arasuperobject(18,26,7,"يمد الشمال على الخلاء ويعلّق الارض على لا شيء"));
arasuperobject.biblemap.put("b18_26_8", new arasuperobject(18,26,8,"يصرّ المياه في سحبه فلا يتمزق الغيم تحتها"));
arasuperobject.biblemap.put("b18_26_9", new arasuperobject(18,26,9,"يحجب وجه كرسيه باسطا عليه سحابه"));
arasuperobject.biblemap.put("b18_26_10", new arasuperobject(18,26,10,"رسم حدّا على وجه المياه عند اتصال النور بالظلمة"));
