arasuperobject.biblemap.put("b18_27_8", new arasuperobject(18,27,8,"لانه ما هو رجاء الفاجر عندما يقطعه عندما يسلب الله نفسه"));
arasuperobject.biblemap.put("b18_27_9", new arasuperobject(18,27,9,"أفيسمع الله صراخه اذا جاء عليه ضيق"));
arasuperobject.biblemap.put("b18_27_10", new arasuperobject(18,27,10,"ام يتلذذ بالقدير. هل يدعو الله في كل حين"));
