arasuperobject.biblemap.put("b18_27_23", new arasuperobject(18,27,23,"يصفقون عليه بايديهم ويصفرون عليه من مكانه"));
arasuperobject.biblemap.put("b18_28_1", new arasuperobject(18,28,1,"لانه يوجد للفضة معدن وموضع للذهب حيث يمحصونه"));
