arasuperobject.biblemap.put("b18_31_14", new arasuperobject(18,31,14,"فماذا كنت اصنع حين يقوم الله واذا افتقد فبماذا اجيبه"));
arasuperobject.biblemap.put("b18_31_15", new arasuperobject(18,31,15,"أوليس صانعي في البطن صانعه وقد صوّرنا واحد في الرحم"));
arasuperobject.biblemap.put("b18_31_16", new arasuperobject(18,31,16,"ان كنت منعت المساكين عن مرادهم او افنيت عيني الارملة"));
arasuperobject.biblemap.put("b18_31_17", new arasuperobject(18,31,17,"او اكلت لقمتي وحدي فما اكل منها اليتيم"));
arasuperobject.biblemap.put("b18_31_18", new arasuperobject(18,31,18,"بل منذ صباي كبر عندي كأب ومن بطن امي هديتها"));
arasuperobject.biblemap.put("b18_31_19", new arasuperobject(18,31,19,"ان كنت رأيت هالكا لعدم اللبس او فقيرا بلا كسوة"));
arasuperobject.biblemap.put("b18_31_20", new arasuperobject(18,31,20,"ان لم تباركني حقواه وقد استدفأ بجزّة غنمي"));
arasuperobject.biblemap.put("b18_31_21", new arasuperobject(18,31,21,"ان كنت قد هززت يدي على اليتيم لما رأيت عوني في الباب"));
arasuperobject.biblemap.put("b18_31_22", new arasuperobject(18,31,22,"فلتسقط عضدي من كتفي ولتنكسر ذراعي من قصبتها"));
arasuperobject.biblemap.put("b18_31_23", new arasuperobject(18,31,23,"لان البوار من الله رعب عليّ ومن جلاله لم استطع"));
arasuperobject.biblemap.put("b18_31_24", new arasuperobject(18,31,24,"ان كنت قد جعلت الذهب عمدتي او قلت للابريز انت متكلي"));
arasuperobject.biblemap.put("b18_31_25", new arasuperobject(18,31,25,"ان كنت قد فرحت اذ كثرت ثروتي ولان يدي وجدت كثيرا"));
arasuperobject.biblemap.put("b18_31_26", new arasuperobject(18,31,26,"ان كنت قد نظرت الى النور حين ضاء او الى القمر يسير بالبهاء"));
arasuperobject.biblemap.put("b18_31_27", new arasuperobject(18,31,27,"وغوي قلبي سرّا ولثم يدي فمي"));
arasuperobject.biblemap.put("b18_31_28", new arasuperobject(18,31,28,"فهذا ايضا اثم يعرض للقضاة لاني اكون قد جحدت الله من فوق"));
arasuperobject.biblemap.put("b18_31_29", new arasuperobject(18,31,29,"ان كنت قد فرحت ببليّة مبغضي او شمتّ حين اصابه سوء"));
arasuperobject.biblemap.put("b18_31_30", new arasuperobject(18,31,30,"بل لم ادع حنكي يخطئ في طلب نفسه بلعنة"));
arasuperobject.biblemap.put("b18_31_31", new arasuperobject(18,31,31,"ان كان اهل خيمتي لم يقولوا من ياتي باحد لم يشبع من طعامه"));
arasuperobject.biblemap.put("b18_31_32", new arasuperobject(18,31,32,"غريب لم يبت في الخارج. فتحت للمسافر ابوابي"));
arasuperobject.biblemap.put("b18_31_33", new arasuperobject(18,31,33,"ان كنت قد كتمت كالناس ذنبي لاخفاء اثمي في حضني"));
arasuperobject.biblemap.put("b18_31_34", new arasuperobject(18,31,34,"اذ رهبت جمهورا غفيرا وروّعتني اهانة العشائر فكففت ولم اخرج من الباب"));
arasuperobject.biblemap.put("b18_31_35", new arasuperobject(18,31,35,"من لي بمن يسمعني. هوذا امضائي ليجبني القدير. ومن لي بشكوى كتبها خصمي"));
arasuperobject.biblemap.put("b18_31_36", new arasuperobject(18,31,36,"فكنت احملها على كتفي. كنت اعصبها تاجا لي"));
arasuperobject.biblemap.put("b18_31_37", new arasuperobject(18,31,37,"كنت اخبره بعدد خطواتي وادنو منه كشريف ــ"));
arasuperobject.biblemap.put("b18_31_38", new arasuperobject(18,31,38,"ان كانت ارضي قد صرخت عليّ وتباكت اتلامها جميعا"));
arasuperobject.biblemap.put("b18_31_39", new arasuperobject(18,31,39,"ان كنت قد اكلت غلّتها بلا فضة او اطفأت انفس اصحابها"));
arasuperobject.biblemap.put("b18_31_40", new arasuperobject(18,31,40,"فعوض الحنطة لينبت شوك وبدل الشعير زوان تمت اقوال ايوب"));
arasuperobject.biblemap.put("b18_32_1", new arasuperobject(18,32,1,"فكف هؤلاء الرجال الثلاثة عن مجاوبة ايوب لكونه بارا في عيني نفسه"));
arasuperobject.biblemap.put("b18_32_2", new arasuperobject(18,32,2,"فحمي غضب اليهو بن برخئيل البوزي من عشيرة رام. على ايوب حمي غضبه لانه حسب نفسه ابر من الله"));
arasuperobject.biblemap.put("b18_32_3", new arasuperobject(18,32,3,"وعلى اصحابه الثلاثة حمي غضبه لانهم لم يجدوا جوابا واستذنبوا ايوب"));
arasuperobject.biblemap.put("b18_32_4", new arasuperobject(18,32,4,"وكان اليهو قد صبر على ايوب بالكلام لانهم اكثر منه اياما"));
arasuperobject.biblemap.put("b18_32_5", new arasuperobject(18,32,5,"فلما رأى اليهو انه لا جواب في افواه الرجال الثلاثة حمي غضبه"));
arasuperobject.biblemap.put("b18_32_6", new arasuperobject(18,32,6,"فاجاب اليهو بن برخئيل البوزي وقال انا صغير في الايام وانتم شيوخ. لاجل ذلك خفت وخشيت ان ابدي لكم رأيي"));
arasuperobject.biblemap.put("b18_32_7", new arasuperobject(18,32,7,"قلت الايام تتكلم وكثرة السنين تظهر حكمة"));
arasuperobject.biblemap.put("b18_32_8", new arasuperobject(18,32,8,"ولكن في الناس روحا ونسمة القدير تعقلهم"));
arasuperobject.biblemap.put("b18_32_9", new arasuperobject(18,32,9,"ليس الكثيرو الايام حكماء ولا الشيوخ يفهمون الحق"));
arasuperobject.biblemap.put("b18_32_10", new arasuperobject(18,32,10,"لذلك قلت اسمعوني انا ايضا ابدي رأيي"));
arasuperobject.biblemap.put("b18_32_11", new arasuperobject(18,32,11,"هانذا قد صبرت لكلامكم. اصغيت الى حججكم حتى فحصتم الاقوال"));
arasuperobject.biblemap.put("b18_32_12", new arasuperobject(18,32,12,"فتأملت فيكم واذ ليس من حجّ ايوب ولا جواب منكم لكلامه"));
arasuperobject.biblemap.put("b18_32_13", new arasuperobject(18,32,13,"فلا تقولوا قد وجدنا حكمة. الله يغلبه لا الانسان"));
arasuperobject.biblemap.put("b18_32_14", new arasuperobject(18,32,14,"فانه لم يوجه اليّ كلامه ولا ارد عليه انا بكلامكم"));
arasuperobject.biblemap.put("b18_32_15", new arasuperobject(18,32,15,"تحيروا. لم يجيبوا بعد. انتزع عنهم الكلام"));
arasuperobject.biblemap.put("b18_32_16", new arasuperobject(18,32,16,"فانتظرت لانهم لم يتكلموا لانهم وقفوا لم يجيبوا بعد"));
arasuperobject.biblemap.put("b18_32_17", new arasuperobject(18,32,17,"فاجيب انا ايضا حصتي وابدي انا ايضا رأيي"));
arasuperobject.biblemap.put("b18_32_18", new arasuperobject(18,32,18,"لاني ملآن اقوالا. روح باطني تضايقني"));
arasuperobject.biblemap.put("b18_32_19", new arasuperobject(18,32,19,"هوذا بطني كخمر لم تفتح كالزقاق الجديدة يكاد ينشق"));
arasuperobject.biblemap.put("b18_32_20", new arasuperobject(18,32,20,"اتكلم فافرج. افتح شفتيّ واجيب"));
arasuperobject.biblemap.put("b18_32_21", new arasuperobject(18,32,21,"لا احابينّ وجه رجل ولا أملث انسانا"));
arasuperobject.biblemap.put("b18_32_22", new arasuperobject(18,32,22,"لاني لا اعرف الملث. لانه عن قليل يأخذني صانعي"));
arasuperobject.biblemap.put("b18_33_1", new arasuperobject(18,33,1,"ولكن اسمع الآن يا ايوب اقوالي واصغ الى كل كلامي"));
arasuperobject.biblemap.put("b18_33_2", new arasuperobject(18,33,2,"هانذا قد فتحت فمي. لساني نطق في حنكي"));
arasuperobject.biblemap.put("b18_33_3", new arasuperobject(18,33,3,"استقامة قلبي كلامي ومعرفة شفتيّ هما تنطقان بها خالصة"));
arasuperobject.biblemap.put("b18_33_4", new arasuperobject(18,33,4,"روح الله صنعني ونسمة القدير احيتني"));
arasuperobject.biblemap.put("b18_33_5", new arasuperobject(18,33,5,"ان استطعت فاجبني. احسن الدعوى امامي. انتصب"));
arasuperobject.biblemap.put("b18_33_6", new arasuperobject(18,33,6,"هانذا حسب قولك عوضا عن الله انا ايضا من الطين تقرصت"));
arasuperobject.biblemap.put("b18_33_7", new arasuperobject(18,33,7,"هوذا هيبتي لا ترهبك وجلالي لا يثقل عليك"));
arasuperobject.biblemap.put("b18_33_8", new arasuperobject(18,33,8,"انك قد قلت في مسامعي وصوت اقوالك سمعت"));
arasuperobject.biblemap.put("b18_33_9", new arasuperobject(18,33,9,"قلت انا بريء بلا ذنب. زكي انا ولا اثم لي"));
arasuperobject.biblemap.put("b18_33_10", new arasuperobject(18,33,10,"هوذا يطلب عليّ علل عداوة. يحسبني عدوا له"));
arasuperobject.biblemap.put("b18_33_11", new arasuperobject(18,33,11,"وضع رجليّ في المقطرة. يراقب كل طرقي"));
arasuperobject.biblemap.put("b18_33_12", new arasuperobject(18,33,12,"ها انك في هذا لم تصب. انا اجيبك. لان الله اعظم من الانسان"));
arasuperobject.biblemap.put("b18_33_13", new arasuperobject(18,33,13,"لماذا تخاصمه. لان كل اموره لا يجاوب عنها"));
arasuperobject.biblemap.put("b18_33_14", new arasuperobject(18,33,14,"لكن الله يتكلم مرة وباثنتين لا يلاحظ الانسان"));
arasuperobject.biblemap.put("b18_33_15", new arasuperobject(18,33,15,"في حلم في رؤيا الليل عند سقوط سبات على الناس في النعاس على المضجع"));
arasuperobject.biblemap.put("b18_33_16", new arasuperobject(18,33,16,"حينئذ يكشف آذان الناس ويختم على تاديبهم"));
arasuperobject.biblemap.put("b18_33_17", new arasuperobject(18,33,17,"ليحول الانسان عن عمله ويكتم الكبرياء عن الرجل"));
arasuperobject.biblemap.put("b18_33_18", new arasuperobject(18,33,18,"ليمنع نفسه عن الحفرة وحياته من الزوال بحربة الموت"));
arasuperobject.biblemap.put("b18_33_19", new arasuperobject(18,33,19,"ايضا يؤدب بالوجع على مضجعه ومخاصمة عظامه دائمة"));
arasuperobject.biblemap.put("b18_33_20", new arasuperobject(18,33,20,"فتكره حياته خبزا ونفسه الطعام الشهي"));
arasuperobject.biblemap.put("b18_33_21", new arasuperobject(18,33,21,"فيبلى لحمه عن العيان وتنبري عظامه فلا ترى"));
arasuperobject.biblemap.put("b18_33_22", new arasuperobject(18,33,22,"وتقرب نفسه الى القبر وحياته الى المميتين"));
arasuperobject.biblemap.put("b18_33_23", new arasuperobject(18,33,23,"ان وجد عنده مرسل وسيط واحد من الف ليعلن للانسان استقامته"));
arasuperobject.biblemap.put("b18_33_24", new arasuperobject(18,33,24,"يترءاف عليه ويقول اطلقه عن الهبوط الى الحفرة قد وجدت فدية"));
arasuperobject.biblemap.put("b18_33_25", new arasuperobject(18,33,25,"يصير لحمه اغض من لحم الصبي ويعود الى ايام شبابه"));
arasuperobject.biblemap.put("b18_33_26", new arasuperobject(18,33,26,"يصلّي الى الله فيرضى عنه ويعاين وجهه بهتاف فيرد على الانسان بره"));
arasuperobject.biblemap.put("b18_33_27", new arasuperobject(18,33,27,"يغني بين الناس فيقول قد اخطأت وعوجت المستقيم ولم أجاز عليه"));
arasuperobject.biblemap.put("b18_33_28", new arasuperobject(18,33,28,"فدى نفسي من العبور الى الحفرة فترى حياتي النور"));
arasuperobject.biblemap.put("b18_33_29", new arasuperobject(18,33,29,"هوذا كل هذه يفعلها الله مرتين وثلاثا بالانسان"));
arasuperobject.biblemap.put("b18_33_30", new arasuperobject(18,33,30,"ليرد نفسه من الحفرة ليستنير بنور الاحياء"));
arasuperobject.biblemap.put("b18_33_31", new arasuperobject(18,33,31,"فاصغ يا ايوب واستمع لي. انصت فانا اتكلم"));
arasuperobject.biblemap.put("b18_33_32", new arasuperobject(18,33,32,"ان كان عندك كلام فاجبني. تكلم. فاني اريد تبريرك"));
arasuperobject.biblemap.put("b18_33_33", new arasuperobject(18,33,33,"والا فاستمع انت لي. انصت فاعلمك الحكمة"));
arasuperobject.biblemap.put("b18_34_1", new arasuperobject(18,34,1,"فاجاب اليهو وقال"));
arasuperobject.biblemap.put("b18_34_2", new arasuperobject(18,34,2,"اسمعوا اقوالي ايها الحكماء واصغوا لي ايها العارفون"));
arasuperobject.biblemap.put("b18_34_3", new arasuperobject(18,34,3,"لان الاذن تمتحن الاقوال كما ان الحنك يذوق طعاما"));
arasuperobject.biblemap.put("b18_34_4", new arasuperobject(18,34,4,"لنمتحن لانفسنا الحق ونعرف بين انفسنا ما هو طيب"));
arasuperobject.biblemap.put("b18_34_5", new arasuperobject(18,34,5,"لان ايوب قال تبررت والله نزع حقي"));
arasuperobject.biblemap.put("b18_34_6", new arasuperobject(18,34,6,"عند محاكمتي اكذب. جرحي عديم الشفاء من دون ذنب"));
arasuperobject.biblemap.put("b18_34_7", new arasuperobject(18,34,7,"فاي انسان كايوب يشرب الهزء كالماء"));
arasuperobject.biblemap.put("b18_34_8", new arasuperobject(18,34,8,"ويسير متحدا مع فاعلي الاثم وذاهبا مع اهل الشر"));
arasuperobject.biblemap.put("b18_34_9", new arasuperobject(18,34,9,"لانه قال لا ينتفع الانسان بكونه مرضيا عند الله"));
arasuperobject.biblemap.put("b18_34_10", new arasuperobject(18,34,10,"لاجل ذلك اسمعوا لي يا ذوي الالباب. حاشا لله من الشر وللقدير من الظلم"));
arasuperobject.biblemap.put("b18_34_11", new arasuperobject(18,34,11,"لانه يجازي الانسان على فعله وينيل الرجل كطريقه"));
arasuperobject.biblemap.put("b18_34_12", new arasuperobject(18,34,12,"فحقا ان الله لا يفعل سوءا والقدير لا يعوج القضاء"));
arasuperobject.biblemap.put("b18_34_13", new arasuperobject(18,34,13,"من وكله بالارض ومن صنع المسكونه كلها"));
arasuperobject.biblemap.put("b18_34_14", new arasuperobject(18,34,14,"ان جعل عليه قلبه ان جمع الى نفسه روحه ونسمته"));
arasuperobject.biblemap.put("b18_34_15", new arasuperobject(18,34,15,"يسلم الروح كل بشر جميعا ويعود الانسان الى التراب"));
arasuperobject.biblemap.put("b18_34_16", new arasuperobject(18,34,16,"فان كان لك فهم فاسمع هذا واصغ الى صوت كلماتي"));
arasuperobject.biblemap.put("b18_34_17", new arasuperobject(18,34,17,"ألعل من يبغض الحق يتسلط ام البار الكبير تستذنب"));
arasuperobject.biblemap.put("b18_34_18", new arasuperobject(18,34,18,"أيقال للملك يا لئيم وللندباء يا اشرار"));
arasuperobject.biblemap.put("b18_34_19", new arasuperobject(18,34,19,"الذي لا يحابي بوجوه الرؤساء ولا يعتبر موسعا دون فقير. لانهم جميعهم عمل يديه"));
arasuperobject.biblemap.put("b18_34_20", new arasuperobject(18,34,20,"بغتة يموتون وفي نصف الليل. يرتج الشعب ويزولون وينزع الاعزاء لا بيد"));
arasuperobject.biblemap.put("b18_34_21", new arasuperobject(18,34,21,"لان عينيه على طرق الانسان وهو يرى كل خطواته"));
arasuperobject.biblemap.put("b18_34_22", new arasuperobject(18,34,22,"لا ظلام ولا ظل موت حيث تختفي عمّال الاثم"));
arasuperobject.biblemap.put("b18_34_23", new arasuperobject(18,34,23,"لانه لا يلاحظ الانسان زمانا للدخول في المحاكمة مع الله"));
arasuperobject.biblemap.put("b18_34_24", new arasuperobject(18,34,24,"يحطم الاعزاء من دون فحص ويقيم آخرين مكانهم"));
arasuperobject.biblemap.put("b18_34_25", new arasuperobject(18,34,25,"لكنه يعرف اعمالهم ويقلّبهم ليلا فينسحقون"));
arasuperobject.biblemap.put("b18_34_26", new arasuperobject(18,34,26,"لكونهم اشرارا يصفقهم في مرأى الناظرين"));
arasuperobject.biblemap.put("b18_34_27", new arasuperobject(18,34,27,"لانهم انصرفوا من ورائه وكل طرقه لم يتأملوها"));
arasuperobject.biblemap.put("b18_34_28", new arasuperobject(18,34,28,"حتى بلّغوا اليه صراخ المسكين فسمع زعقة البائسين"));
arasuperobject.biblemap.put("b18_34_29", new arasuperobject(18,34,29,"اذا هو سكّن فمن يشغب واذا حجب وجهه فمن يراه سواء كان على امة او على انسان"));
arasuperobject.biblemap.put("b18_34_30", new arasuperobject(18,34,30,"حتى لا يملك الفاجر ولا يكون شركا للشعب"));
arasuperobject.biblemap.put("b18_34_31", new arasuperobject(18,34,31,"ولكن هل للّه قال احتملت. لا اعود افسد"));
arasuperobject.biblemap.put("b18_34_32", new arasuperobject(18,34,32,"ما لم ابصره فأرنيه انت. ان كنت قد فعلت اثما فلا اعود افعله"));
arasuperobject.biblemap.put("b18_34_33", new arasuperobject(18,34,33,"هل كرأيك يجازيه قائلا لانك رفضت فانت تختار لا انا. وبما تعرفه تكلم"));
arasuperobject.biblemap.put("b18_34_34", new arasuperobject(18,34,34,"ذوو الالباب يقولون لي بل الرجل الحكيم الذي يسمعني يقول"));
arasuperobject.biblemap.put("b18_34_35", new arasuperobject(18,34,35,"ان ايوب يتكلم بلا معرفة وكلامه ليس بتعقّل"));
arasuperobject.biblemap.put("b18_34_36", new arasuperobject(18,34,36,"فليت ايوب كان يمتحن الى الغاية من اجل اجوبته كاهل الاثم"));
arasuperobject.biblemap.put("b18_34_37", new arasuperobject(18,34,37,"لكنه اضاف الى خطيته معصية. يصفق بيننا ويكثر كلامه على الله"));
arasuperobject.biblemap.put("b18_35_1", new arasuperobject(18,35,1,"فاجاب اليهو وقال"));
arasuperobject.biblemap.put("b18_35_2", new arasuperobject(18,35,2,"أتحسب هذا حقا. قلت انا ابر من الله"));
arasuperobject.biblemap.put("b18_35_3", new arasuperobject(18,35,3,"لانك قلت ماذا يفيدك بماذا انتفع اكثر من خطيتي"));
arasuperobject.biblemap.put("b18_35_4", new arasuperobject(18,35,4,"انا ارد عليك كلاما وعلى اصحابك معك"));
arasuperobject.biblemap.put("b18_35_5", new arasuperobject(18,35,5,"انظر الى السموات وابصر ولاحظ الغمام انها اعلى منك"));
arasuperobject.biblemap.put("b18_35_6", new arasuperobject(18,35,6,"ان اخطأت فماذا فعلت به وان كثرت معاصيك فماذا عملت له"));
arasuperobject.biblemap.put("b18_35_7", new arasuperobject(18,35,7,"ان كنت بارا فماذا اعطيته او ماذا ياخذه من يدك"));
arasuperobject.biblemap.put("b18_35_8", new arasuperobject(18,35,8,"لرجل مثلك شرك ولابن آدم برك"));
arasuperobject.biblemap.put("b18_35_9", new arasuperobject(18,35,9,"من كثرة المظالم يصرخون. يستغيثون من ذراع الاعزاء"));
arasuperobject.biblemap.put("b18_35_10", new arasuperobject(18,35,10,"ولم يقولوا اين الله صانعي مؤتي الاغاني في الليل"));
arasuperobject.biblemap.put("b18_35_11", new arasuperobject(18,35,11,"الذي يعلمنا اكثر من وحوش الارض ويجعلنا احكم من طيور السماء"));
arasuperobject.biblemap.put("b18_35_12", new arasuperobject(18,35,12,"ثم يصرخون من كبرياء الاشرار ولا يستجيب"));
arasuperobject.biblemap.put("b18_35_13", new arasuperobject(18,35,13,"ولكن الله لا يسمع كذبا والقدير لا ينظر اليه"));
arasuperobject.biblemap.put("b18_35_14", new arasuperobject(18,35,14,"فاذا قلت انك لست تراه فالدعوى قدامه فاصبر له"));
arasuperobject.biblemap.put("b18_35_15", new arasuperobject(18,35,15,"واما الآن فلأن غضبه لا يطالب ولا يبالي بكثرة الزلات"));
arasuperobject.biblemap.put("b18_35_16", new arasuperobject(18,35,16,"فغر ايوب فاه بالباطل وكبر الكلام بلا معرفة"));
arasuperobject.biblemap.put("b18_36_1", new arasuperobject(18,36,1,"وعاد اليهو فقال"));
arasuperobject.biblemap.put("b18_36_2", new arasuperobject(18,36,2,"اصبر عليّ قليلا فابدي لك انه بعد لاجل الله كلام"));
arasuperobject.biblemap.put("b18_36_3", new arasuperobject(18,36,3,"أحمل معرفتي من بعيد وأنسب برا لصانعي"));
arasuperobject.biblemap.put("b18_36_4", new arasuperobject(18,36,4,"حقا لا يكذب كلامي. صحيح المعرفة عندك"));
arasuperobject.biblemap.put("b18_36_5", new arasuperobject(18,36,5,"هوذا الله عزيز ولكنه لا يرذل احدا. عزيز قدرة القلب"));
arasuperobject.biblemap.put("b18_36_6", new arasuperobject(18,36,6,"لا يحيي الشرير بل يجري قضاء البائسين"));
arasuperobject.biblemap.put("b18_36_7", new arasuperobject(18,36,7,"لا يحوّل عينيه عن البار بل مع الملوك يجلسهم على الكرسي ابدا فيرتفعون"));
arasuperobject.biblemap.put("b18_36_8", new arasuperobject(18,36,8,"ان أوثقوا بالقيود ان أخذوا في حبالة الذل"));
arasuperobject.biblemap.put("b18_36_9", new arasuperobject(18,36,9,"فيظهر لهم افعالهم ومعاصيهم لانهم تجبّروا"));
arasuperobject.biblemap.put("b18_36_10", new arasuperobject(18,36,10,"ويفتح آذانهم للانذار ويامر بان يرجعوا عن الاثم"));
arasuperobject.biblemap.put("b18_36_11", new arasuperobject(18,36,11,"ان سمعوا واطاعوا قضوا ايامهم بالخير وسنيهم بالنّعم"));
arasuperobject.biblemap.put("b18_36_12", new arasuperobject(18,36,12,"وان لم يسمعوا فبحربة الموت يزولون ويموتون بعدم المعرفة"));
arasuperobject.biblemap.put("b18_36_13", new arasuperobject(18,36,13,"اما فجّار القلب فيذخرون غضبا. لا يستغيثون اذا هو قيّدهم"));
arasuperobject.biblemap.put("b18_36_14", new arasuperobject(18,36,14,"تموت نفسهم في الصبا وحياتهم بين المابونين"));
arasuperobject.biblemap.put("b18_36_15", new arasuperobject(18,36,15,"ينجّي البائس في ذلّه ويفتح آذانهم في الضيق"));
arasuperobject.biblemap.put("b18_36_16", new arasuperobject(18,36,16,"وايضا يقودك من وجه الضيق الى رحب لا حصر فيه ويملأ مؤونة مائدتك دهنا"));
arasuperobject.biblemap.put("b18_36_17", new arasuperobject(18,36,17,"حجة الشرير اكملت فالحجة والقضاء يمسكانك"));
arasuperobject.biblemap.put("b18_36_18", new arasuperobject(18,36,18,"عند غضبه لعله يقودك بصفقة. فكثرة الفدية لا تفكّك"));
arasuperobject.biblemap.put("b18_36_19", new arasuperobject(18,36,19,"هل يعتبر غناك. لا التبر ولا جميع قوى الثروة"));
arasuperobject.biblemap.put("b18_36_20", new arasuperobject(18,36,20,"لا تشتاق الى الليل الذي يرفع شعوبا من مواضعهم"));
arasuperobject.biblemap.put("b18_36_21", new arasuperobject(18,36,21,"احذر. لا تلتفت الى الاثم لانك اخترت هذا على الذل"));
arasuperobject.biblemap.put("b18_36_22", new arasuperobject(18,36,22,"هوذا الله يتعالى بقدرته. من مثله معلما"));
arasuperobject.biblemap.put("b18_36_23", new arasuperobject(18,36,23,"من فرض عليه طريقه او من يقول له قد فعلت شرا"));
arasuperobject.biblemap.put("b18_36_24", new arasuperobject(18,36,24,"اذكر ان تعظم عمله الذي يغني به الناس"));
arasuperobject.biblemap.put("b18_36_25", new arasuperobject(18,36,25,"كل انسان يبصر به. الناس ينظرونه من بعيد"));
arasuperobject.biblemap.put("b18_36_26", new arasuperobject(18,36,26,"هوذا الله عظيم ولا نعرفه وعدد سنيه لا يفحص"));
arasuperobject.biblemap.put("b18_36_27", new arasuperobject(18,36,27,"لانه يجذب قطار الماء. تسحّ مطرا من ضبابها"));
arasuperobject.biblemap.put("b18_36_28", new arasuperobject(18,36,28,"الذي تهطله السحب وتقطره على اناس كثيرين"));
arasuperobject.biblemap.put("b18_36_29", new arasuperobject(18,36,29,"فهل يعلل احد عن شق الغيم او قصيف مظلته"));
arasuperobject.biblemap.put("b18_36_30", new arasuperobject(18,36,30,"هوذا بسط نوره على نفسه ثم يتغطى باصول اليم"));
arasuperobject.biblemap.put("b18_36_31", new arasuperobject(18,36,31,"لانه بهذه يدين الشعوب ويرزق القوت بكثرة"));
arasuperobject.biblemap.put("b18_36_32", new arasuperobject(18,36,32,"يغطي كفّيه بالنور ويامره على العدو"));
arasuperobject.biblemap.put("b18_36_33", new arasuperobject(18,36,33,"يخبر به رعده المواشي ايضا بصعوده"));
arasuperobject.biblemap.put("b18_37_1", new arasuperobject(18,37,1,"فلهذا اضطرب قلبي وخفق من موضعه"));
arasuperobject.biblemap.put("b18_37_2", new arasuperobject(18,37,2,"اسمعوا سماعا رعد صوته والزمزمة الخارجة من فيه"));
arasuperobject.biblemap.put("b18_37_3", new arasuperobject(18,37,3,"تحت كل السموات يطلقها كذا نوره الى اكناف الارض"));
arasuperobject.biblemap.put("b18_37_4", new arasuperobject(18,37,4,"بعد يزمجر صوت يرعد بصوت جلاله ولا يؤخرها اذ سمع صوته"));
arasuperobject.biblemap.put("b18_37_5", new arasuperobject(18,37,5,"الله يرعد بصوته عجبا. يصنع عظائم لا ندركها"));
arasuperobject.biblemap.put("b18_37_6", new arasuperobject(18,37,6,"لانه يقول للثلج اسقط على الارض. كذا لوابل المطر وابل امطار عزه"));
arasuperobject.biblemap.put("b18_37_7", new arasuperobject(18,37,7,"يختم على يد كل انسان ليعلم كل الناس خالقهم"));
arasuperobject.biblemap.put("b18_37_8", new arasuperobject(18,37,8,"فتدخل الحيوانات المآوي وتستقر في اوجرتها"));
arasuperobject.biblemap.put("b18_37_9", new arasuperobject(18,37,9,"من الجنوب تأتي الاعصار ومن الشمال البرد"));
arasuperobject.biblemap.put("b18_37_10", new arasuperobject(18,37,10,"من نسمة الله يجعل الجمد وتتضيق سعة المياه"));
arasuperobject.biblemap.put("b18_37_11", new arasuperobject(18,37,11,"ايضا بريّ يطرح الغيم. يبدد سحاب نوره"));
arasuperobject.biblemap.put("b18_37_12", new arasuperobject(18,37,12,"فهي مدورة متقلبة بادارته لتفعل كل ما يأمر به على وجه الارض المسكونة"));
arasuperobject.biblemap.put("b18_37_13", new arasuperobject(18,37,13,"سواء كان للتأديب او لارضه او للرحمة يرسلها"));
arasuperobject.biblemap.put("b18_37_14", new arasuperobject(18,37,14,"انصت الى هذا يا ايوب وقف وتأمل بعجائب الله"));
arasuperobject.biblemap.put("b18_37_15", new arasuperobject(18,37,15,"أتدرك انتباه الله اليها او اضاءة نور سحابه"));
arasuperobject.biblemap.put("b18_37_16", new arasuperobject(18,37,16,"أتدرك موازنة السحاب معجزات الكامل المعارف"));
arasuperobject.biblemap.put("b18_37_17", new arasuperobject(18,37,17,"كيف تسخن ثيابك اذا سكنت الارض من ريح الجنوب"));
arasuperobject.biblemap.put("b18_37_18", new arasuperobject(18,37,18,"هل صفّحت معه الجلد الممكّن كالمرآة المسبوكة"));
arasuperobject.biblemap.put("b18_37_19", new arasuperobject(18,37,19,"علّمنا ما نقول له اننا لا نحسن الكلام بسبب الظلمة"));
arasuperobject.biblemap.put("b18_37_20", new arasuperobject(18,37,20,"هل يقص عليه كلامي اذا تكلمت. هل ينطق الانسان لكي يبتلع"));
arasuperobject.biblemap.put("b18_37_21", new arasuperobject(18,37,21,"والآن لا يرى النور الباهر الذي هو في الجلد ثم تعبر الريح فتنقّيه"));
arasuperobject.biblemap.put("b18_37_22", new arasuperobject(18,37,22,"من الشمال يأتي ذهب. عند الله جلال مرهب"));
arasuperobject.biblemap.put("b18_37_23", new arasuperobject(18,37,23,"القدير لا ندركه. عظيم القوة والحق وكثير البر. لا يجاوب"));
arasuperobject.biblemap.put("b18_37_24", new arasuperobject(18,37,24,"لذلك فلتخفه الناس. كل حكيم القلب لا يراعي"));
arasuperobject.biblemap.put("b18_38_1", new arasuperobject(18,38,1,"فاجاب الرب ايوب من العاصفة وقال"));
arasuperobject.biblemap.put("b18_38_2", new arasuperobject(18,38,2,"من هذا الذي يظلم القضاء بكلام بلا معرفة"));
arasuperobject.biblemap.put("b18_38_3", new arasuperobject(18,38,3,"اشدد الآن حقويك كرجل. فاني اسألك فتعلمني"));
arasuperobject.biblemap.put("b18_38_4", new arasuperobject(18,38,4,"اين كنت حين اسست الارض. أخبر ان كان عندك فهم"));
arasuperobject.biblemap.put("b18_38_5", new arasuperobject(18,38,5,"من وضع قياسها. لانك تعلم. او من مدّ عليها مطمارا"));
arasuperobject.biblemap.put("b18_38_6", new arasuperobject(18,38,6,"على اي شيء قرّت قواعدها او من وضع حجر زاويتها"));
arasuperobject.biblemap.put("b18_38_7", new arasuperobject(18,38,7,"عندما ترنمت كواكب الصبح معا وهتف جميع بني الله"));
arasuperobject.biblemap.put("b18_38_8", new arasuperobject(18,38,8,"ومن حجز البحر بمصاريع حين اندفق فخرج من الرحم"));
arasuperobject.biblemap.put("b18_38_9", new arasuperobject(18,38,9,"اذ جعلت السحاب لباسه الضباب قماطه"));
arasuperobject.biblemap.put("b18_38_10", new arasuperobject(18,38,10,"وجزمت عليه حدّي واقمت له مغاليق ومصاريع"));
arasuperobject.biblemap.put("b18_38_11", new arasuperobject(18,38,11,"وقلت الى هنا تاتي ولا تتعدى وهنا تتخم كبرياء لججك"));
arasuperobject.biblemap.put("b18_38_12", new arasuperobject(18,38,12,"هل في ايامك امرت الصبح. هل عرّفت الفجر موضعه"));
arasuperobject.biblemap.put("b18_38_13", new arasuperobject(18,38,13,"ليمسك باكناف الارض فينفض الاشرار منها"));
arasuperobject.biblemap.put("b18_38_14", new arasuperobject(18,38,14,"تتحول كطين الخاتم وتقف كانها لابسة"));
arasuperobject.biblemap.put("b18_38_15", new arasuperobject(18,38,15,"ويمنع عن الاشرار نورهم وتنكسر الذراع المرتفعة"));
arasuperobject.biblemap.put("b18_38_16", new arasuperobject(18,38,16,"هل انتهيت الى ينابيع البحر او في مقصورة الغمر تمشّيت"));
arasuperobject.biblemap.put("b18_38_17", new arasuperobject(18,38,17,"هل انكشفت لك ابواب الموت او عاينت ابواب ظل الموت"));
arasuperobject.biblemap.put("b18_38_18", new arasuperobject(18,38,18,"هل ادركت عرض الارض. اخبر ان عرفته كله"));
arasuperobject.biblemap.put("b18_38_19", new arasuperobject(18,38,19,"اين الطريق الى حيث يسكن النور. والظلمة اين مقامها"));
arasuperobject.biblemap.put("b18_38_20", new arasuperobject(18,38,20,"حتى تاخذها الى تخومها وتعرف سبل بيتها"));
arasuperobject.biblemap.put("b18_38_21", new arasuperobject(18,38,21,"تعلم لانك حينئذ كنت قد ولدت وعدد ايامك كثير"));
arasuperobject.biblemap.put("b18_38_22", new arasuperobject(18,38,22,"أدخلت الى خزائن الثلج ام ابصرت مخازن البرد"));
arasuperobject.biblemap.put("b18_38_23", new arasuperobject(18,38,23,"التي ابقيتها لوقت الضر ليوم القتال والحرب"));
arasuperobject.biblemap.put("b18_38_24", new arasuperobject(18,38,24,"في اي طريق يتوزع النور وتتفرق الشرقية على الارض"));
arasuperobject.biblemap.put("b18_38_25", new arasuperobject(18,38,25,"من فرّع قنوات للهطل وطريقا للصواعق"));
arasuperobject.biblemap.put("b18_38_26", new arasuperobject(18,38,26,"ليمطر على ارض حيث لا انسان. على قفر لا احد فيه"));
arasuperobject.biblemap.put("b18_38_27", new arasuperobject(18,38,27,"ليروي البلقع والخلاء وينبت مخرج العشب"));
arasuperobject.biblemap.put("b18_38_28", new arasuperobject(18,38,28,"هل للمطر اب ومن ولد مآجل الطل"));
arasuperobject.biblemap.put("b18_38_29", new arasuperobject(18,38,29,"من بطن من خرج الجمد. صقيع السماء من ولده"));
arasuperobject.biblemap.put("b18_38_30", new arasuperobject(18,38,30,"كحجر صارت المياه. أختبأت. وتلكد وجه الغمر"));
arasuperobject.biblemap.put("b18_38_31", new arasuperobject(18,38,31,"هل تربط انت عقد الثريا او تفك ربط الجبّار"));
arasuperobject.biblemap.put("b18_38_32", new arasuperobject(18,38,32,"أتخرج المنازل في اوقاتها وتهدي النعش مع بناته"));
arasuperobject.biblemap.put("b18_38_33", new arasuperobject(18,38,33,"هل عرفت سنن السموات او جعلت تسلطها على الارض"));
arasuperobject.biblemap.put("b18_38_34", new arasuperobject(18,38,34,"أترفع صوتك الى السحب فيغطيك فيض المياه"));
arasuperobject.biblemap.put("b18_38_35", new arasuperobject(18,38,35,"اترسل البروق فتذهب وتقول لك ها نحن"));
arasuperobject.biblemap.put("b18_38_36", new arasuperobject(18,38,36,"من وضع في الطخاء حكمة او من اظهر في الشّهب فطنة"));
arasuperobject.biblemap.put("b18_38_37", new arasuperobject(18,38,37,"من يحصي الغيوم بالحكمة ومن يسكب ازقاق السموات"));
arasuperobject.biblemap.put("b18_38_38", new arasuperobject(18,38,38,"اذ ينسبك التراب سبكا ويتلاصق المدر"));
arasuperobject.biblemap.put("b18_38_39", new arasuperobject(18,38,39,"اتصطاد للّبوة فريسة ام تشبع نفس الاشبال"));
arasuperobject.biblemap.put("b18_38_40", new arasuperobject(18,38,40,"حين تجرمز في عريّسها وتجلس في عيصها للكمون"));
arasuperobject.biblemap.put("b18_38_41", new arasuperobject(18,38,41,"من يهيئ للغراب صيده اذ تنعب فراخه الى الله وتتردد لعدم القوت"));
arasuperobject.biblemap.put("b18_39_1", new arasuperobject(18,39,1,"أتعرف وقت ولادة وعول الصخور او تلاحظ مخاض الايائل"));
arasuperobject.biblemap.put("b18_39_2", new arasuperobject(18,39,2,"أتحسب الشهور التي تكملها او تعلم ميقات ولادتهنّ"));
arasuperobject.biblemap.put("b18_39_3", new arasuperobject(18,39,3,"يبركن ويضعن اولادهنّ. يدفعن اوجاعهنّ"));
arasuperobject.biblemap.put("b18_39_4", new arasuperobject(18,39,4,"تبلغ اولادهنّ. تربو في البرية. تخرج ولا تعود اليهنّ"));
arasuperobject.biblemap.put("b18_39_5", new arasuperobject(18,39,5,"من سرّح الفراء حرّا ومن فكّ ربط حمار الوحش"));
arasuperobject.biblemap.put("b18_39_6", new arasuperobject(18,39,6,"الذي جعلت البرية بيته والسباخ مسكنه"));
arasuperobject.biblemap.put("b18_39_7", new arasuperobject(18,39,7,"يضحك على جمهور القرية. لا يسمع زجر السائق"));
arasuperobject.biblemap.put("b18_39_8", new arasuperobject(18,39,8,"دائرة الجبال مرعاه وعلى كل خضرة يفتش"));
arasuperobject.biblemap.put("b18_39_9", new arasuperobject(18,39,9,"أيرضى الثور الوحشي ان يخدمك ام يبيت عند معلفك"));
arasuperobject.biblemap.put("b18_39_10", new arasuperobject(18,39,10,"أتربط الثور الوحشي برباطه في التلم ام يمهد الاودية وراءك"));
arasuperobject.biblemap.put("b18_39_11", new arasuperobject(18,39,11,"أتثق به لان قوته عظيمة او تترك له تعبك"));
arasuperobject.biblemap.put("b18_39_12", new arasuperobject(18,39,12,"أتأتمنه انه يأتي بزرعك ويجمع الى بيدرك"));
arasuperobject.biblemap.put("b18_39_13", new arasuperobject(18,39,13,"جناح النعامة يرفرف. أفهو منكب رأوف ام ريش"));
arasuperobject.biblemap.put("b18_39_14", new arasuperobject(18,39,14,"لانها تترك بيضها وتحميه في التراب"));
arasuperobject.biblemap.put("b18_39_15", new arasuperobject(18,39,15,"وتنسى ان الرّجل تضغطه او حيوان البر يدوسه"));
arasuperobject.biblemap.put("b18_39_16", new arasuperobject(18,39,16,"تقسو على اولادها كانها ليست لها. باطل تعبها بلا اسف"));
arasuperobject.biblemap.put("b18_39_17", new arasuperobject(18,39,17,"لان الله قد انساها الحكمة ولم يقسم لها فهما"));
arasuperobject.biblemap.put("b18_39_18", new arasuperobject(18,39,18,"عندما تحوذ نفسها الى العلاء تضحك على الفرس وعلى راكبه"));
arasuperobject.biblemap.put("b18_39_19", new arasuperobject(18,39,19,"هل انت تعطي الفرس قوته وتكسو عنقه عرفا"));
arasuperobject.biblemap.put("b18_39_20", new arasuperobject(18,39,20,"أتوثبه كجرادة. نفخ منخره مرعب"));
arasuperobject.biblemap.put("b18_39_21", new arasuperobject(18,39,21,"يبحث في الوادي وينفز ببأس. يخرج للقاء الاسلحة"));
arasuperobject.biblemap.put("b18_39_22", new arasuperobject(18,39,22,"يضحك على الخوف ولا يرتاع ولا يرجع عن السيف"));
arasuperobject.biblemap.put("b18_39_23", new arasuperobject(18,39,23,"عليه تصل السهام وسنان الرمح والمزراق"));
arasuperobject.biblemap.put("b18_39_24", new arasuperobject(18,39,24,"في وثبه ورجزه يلتهم الارض ولا يؤمن انه صوت البوق"));
arasuperobject.biblemap.put("b18_39_25", new arasuperobject(18,39,25,"عند نفخ البوق يقول هه ومن بعيد يستروح القتال صياح القواد والهتاف"));
arasuperobject.biblemap.put("b18_39_26", new arasuperobject(18,39,26,"أمن فهمك يستقل العقاب وينشر جناحيه نحو الجنوب"));
arasuperobject.biblemap.put("b18_39_27", new arasuperobject(18,39,27,"او بأمرك يحلّق النسر ويعلّي وكره"));
arasuperobject.biblemap.put("b18_39_28", new arasuperobject(18,39,28,"يسكن الصخر ويبيت على سن الصخر والمعقل"));
arasuperobject.biblemap.put("b18_39_29", new arasuperobject(18,39,29,"من هناك يتحسس قوته. تبصره عيناه من بعيد"));
arasuperobject.biblemap.put("b18_39_30", new arasuperobject(18,39,30,"فراخه تحسو الدم وحيثما تكن القتلى فهناك هو"));
arasuperobject.biblemap.put("b18_40_1", new arasuperobject(18,40,1,"فاجاب الرب ايوب فقال"));
arasuperobject.biblemap.put("b18_40_2", new arasuperobject(18,40,2,"هل يخاصم القدير موبّخه ام المحاجّ الله يجاوبه"));
arasuperobject.biblemap.put("b18_40_3", new arasuperobject(18,40,3,"فاجاب ايوب الرب وقال"));
arasuperobject.biblemap.put("b18_40_4", new arasuperobject(18,40,4,"ها انا حقير فماذا اجاوبك. وضعت يدي على فمي"));
arasuperobject.biblemap.put("b18_40_5", new arasuperobject(18,40,5,"مرة تكلمت فلا اجيب ومرتين فلا ازيد"));
arasuperobject.biblemap.put("b18_40_6", new arasuperobject(18,40,6,"فاجاب الرب ايوب من العاصفة فقال"));
arasuperobject.biblemap.put("b18_40_7", new arasuperobject(18,40,7,"الآن شدّ حقويك كرجل. اسألك فتعلمني"));
arasuperobject.biblemap.put("b18_40_8", new arasuperobject(18,40,8,"لعلك تناقض حكمي. تستذنبني لكي تتبرر انت"));
arasuperobject.biblemap.put("b18_40_9", new arasuperobject(18,40,9,"هل لك ذراع كما لله وبصوت مثل صوته ترعد"));
arasuperobject.biblemap.put("b18_40_10", new arasuperobject(18,40,10,"تزيّن الآن بالجلال والعزّ والبس المجد والبهاء"));
arasuperobject.biblemap.put("b18_40_11", new arasuperobject(18,40,11,"فرّق فيض غضبك وانظر كل متعظم واخفضه"));
arasuperobject.biblemap.put("b18_40_12", new arasuperobject(18,40,12,"انظر الى كل متعظم وذلّله ودس الاشرار في مكانهم"));
arasuperobject.biblemap.put("b18_40_13", new arasuperobject(18,40,13,"اطمرهم في التراب معا واحبس وجوههم في الظلام"));
arasuperobject.biblemap.put("b18_40_14", new arasuperobject(18,40,14,"فانا ايضا احمدك لان يمينك تخلصك"));
arasuperobject.biblemap.put("b18_40_15", new arasuperobject(18,40,15,"هوذا بهيموث الذي صنعته معك. ياكل العشب مثل البقر"));
arasuperobject.biblemap.put("b18_40_16", new arasuperobject(18,40,16,"ها هي قوته في متنيه وشدته في عضل بطنه"));
arasuperobject.biblemap.put("b18_40_17", new arasuperobject(18,40,17,"يخفض ذنبه كارزة. عروق فخذيه مضفورة"));
arasuperobject.biblemap.put("b18_40_18", new arasuperobject(18,40,18,"عظامه انابيب نحاس. جرمها حديد ممطول"));
arasuperobject.biblemap.put("b18_40_19", new arasuperobject(18,40,19,"هو اول اعمال الله. الذي صنعه اعطاه سيفه"));
arasuperobject.biblemap.put("b18_40_20", new arasuperobject(18,40,20,"لان الجبال تخرج له مرعى وجميع وحوش البر تلعب هناك"));
arasuperobject.biblemap.put("b18_40_21", new arasuperobject(18,40,21,"تحت السدرات يضطجع في ستر القصب والغمقة"));
arasuperobject.biblemap.put("b18_40_22", new arasuperobject(18,40,22,"تظلله السدرات بظلها. يحيط به صفصاف السواقي"));
arasuperobject.biblemap.put("b18_40_23", new arasuperobject(18,40,23,"هوذا النهر يفيض فلا يفر هو. يطمئن ولو اندفق الاردن في فمه"));
arasuperobject.biblemap.put("b18_40_24", new arasuperobject(18,40,24,"هل يؤخذ من امامه. هل يثقب انفه بخزامة"));
arasuperobject.biblemap.put("b18_41_1", new arasuperobject(18,41,1,"أتصطاد لوياثان بشص او تضغط لسانه بحبل"));
arasuperobject.biblemap.put("b18_41_2", new arasuperobject(18,41,2,"أتضع أسلة في خطمه ام تثقب فكّه بخزامة"));
arasuperobject.biblemap.put("b18_41_3", new arasuperobject(18,41,3,"أيكثر التضرعات اليك ام يتكلم معك باللين"));
arasuperobject.biblemap.put("b18_41_4", new arasuperobject(18,41,4,"هل يقطع معك عهدا فتتخذه عبدا مؤبدا"));
arasuperobject.biblemap.put("b18_41_5", new arasuperobject(18,41,5,"أتلعب معه كالعصفور او تربطه لاجل فتياتك"));
arasuperobject.biblemap.put("b18_41_6", new arasuperobject(18,41,6,"هل تحفر جماعة الصيادين لاجله حفرة او يقسمونه بين الكنعانيين"));
arasuperobject.biblemap.put("b18_41_7", new arasuperobject(18,41,7,"أتملأ جلده حرابا وراسه بإلال السمك"));
arasuperobject.biblemap.put("b18_41_8", new arasuperobject(18,41,8,"ضع يدك عليه. لا تعد تذكر القتال"));
arasuperobject.biblemap.put("b18_41_9", new arasuperobject(18,41,9,"هوذا الرجاء به كاذب. ألا يكبّ ايضا برؤيته"));
arasuperobject.biblemap.put("b18_41_10", new arasuperobject(18,41,10,"ليس من شجاع يوقظه فمن يقف اذا بوجهي"));
arasuperobject.biblemap.put("b18_41_11", new arasuperobject(18,41,11,"من تقدمني فاوفيه. ما تحت كل السموات هو لي"));
arasuperobject.biblemap.put("b18_41_12", new arasuperobject(18,41,12,"لا اسكت عن اعضائه وخبر قوته وبهجة عدّته"));
arasuperobject.biblemap.put("b18_41_13", new arasuperobject(18,41,13,"من يكشف وجه لبسه ومن يدنو من مثنى لجمته"));
arasuperobject.biblemap.put("b18_41_14", new arasuperobject(18,41,14,"من يفتح مصراعي فمه. دائرة اسنانه مرعبة"));
arasuperobject.biblemap.put("b18_41_15", new arasuperobject(18,41,15,"فخره مجان مانعة محكّمة مضغوطة بخاتم"));
arasuperobject.biblemap.put("b18_41_16", new arasuperobject(18,41,16,"الواحد يمسّ الآخر فالريح لا تدخل بينها"));
arasuperobject.biblemap.put("b18_41_17", new arasuperobject(18,41,17,"كل منها ملتصق بصاحبه متلكّدة لا تنفصل"));
arasuperobject.biblemap.put("b18_41_18", new arasuperobject(18,41,18,"عطاسه يبعث نورا وعيناه كهدب الصبح"));
arasuperobject.biblemap.put("b18_41_19", new arasuperobject(18,41,19,"من فيه تخرج مصابيح. شرار نار تتطاير منه"));
arasuperobject.biblemap.put("b18_41_20", new arasuperobject(18,41,20,"من منخريه يخرج دخان كانه من قدر منفوخ او من مرجل"));
arasuperobject.biblemap.put("b18_41_21", new arasuperobject(18,41,21,"نفسه يشعل جمرا ولهيب يخرج من فيه"));
arasuperobject.biblemap.put("b18_41_22", new arasuperobject(18,41,22,"في عنقه تبيت القوة وامامه يدوس الهول"));
arasuperobject.biblemap.put("b18_41_23", new arasuperobject(18,41,23,"مطاوي لحمه متلاصقة مسبوكة عليه لا تتحرك"));
arasuperobject.biblemap.put("b18_41_24", new arasuperobject(18,41,24,"قلبه صلب كالحجر وقاس كالرحى"));
arasuperobject.biblemap.put("b18_41_25", new arasuperobject(18,41,25,"عند نهوضه تفزع الاقوياء. من المخاوف يتيهون"));
arasuperobject.biblemap.put("b18_41_26", new arasuperobject(18,41,26,"سيف الذي يلحقه لا يقوم ولا رمح ولا مزراق ولا درع"));
arasuperobject.biblemap.put("b18_41_27", new arasuperobject(18,41,27,"يحسب الحديد كالتبن والنحاس كالعود النخر"));
arasuperobject.biblemap.put("b18_41_28", new arasuperobject(18,41,28,"لا يستفزّه نبل القوس. حجارة المقلاع ترجع عنه كالقش"));
arasuperobject.biblemap.put("b18_41_29", new arasuperobject(18,41,29,"يحسب المقمعة كقش ويضحك على اهتزاز الرمح"));
arasuperobject.biblemap.put("b18_41_30", new arasuperobject(18,41,30,"تحته قطع خزف حادة. يمدد نورجا على الطين"));
arasuperobject.biblemap.put("b18_41_31", new arasuperobject(18,41,31,"يجعل العمق يغلي كالقدر ويجعل البحر كقدر عطارة"));
arasuperobject.biblemap.put("b18_41_32", new arasuperobject(18,41,32,"يضيء السبيل وراءه فيحسب اللج اشيب"));
arasuperobject.biblemap.put("b18_41_33", new arasuperobject(18,41,33,"ليس له في الارض نظير. صنع لعدم الخوف"));
arasuperobject.biblemap.put("b18_41_34", new arasuperobject(18,41,34,"يشرف على كل متعال. هو ملك على كل بني الكبرياء"));
arasuperobject.biblemap.put("b18_42_1", new arasuperobject(18,42,1,"فاجاب ايوب الرب فقال"));
arasuperobject.biblemap.put("b18_42_2", new arasuperobject(18,42,2,"قد علمت انك تستطيع كل شيء ولا يعسر عليك أمر"));
arasuperobject.biblemap.put("b18_42_3", new arasuperobject(18,42,3,"فمن ذا الذي يخفي القضاء بلا معرفة. ولكني قد نطقت بما لم افهم. بعجائب فوقي لم اعرفها"));
arasuperobject.biblemap.put("b18_42_4", new arasuperobject(18,42,4,"اسمع الآن وانا اتكلم. اسألك فتعلمني"));
arasuperobject.biblemap.put("b18_42_5", new arasuperobject(18,42,5,"بسمع الاذن قد سمعت عنك والآن رأتك عيني"));
arasuperobject.biblemap.put("b18_42_6", new arasuperobject(18,42,6,"لذلك ارفض واندم في التراب والرماد"));
arasuperobject.biblemap.put("b18_42_7", new arasuperobject(18,42,7,"وكان بعدما تكلم الرب مع ايوب بهذا الكلام ان الرب قال لأليفاز التيماني قد احتمى غضبي عليك وعلى كلا صاحبيك لانكم لم تقولوا فيّ الصواب كعبدي ايوب"));
arasuperobject.biblemap.put("b18_42_8", new arasuperobject(18,42,8,"والآن فخذوا لانفسكم سبعة ثيران وسبعة كباش واذهبوا الى عبدي ايوب واصعدوا محرقة لاجل انفسكم وعبدي ايوب يصلي من اجلكم لاني ارفع وجهه لئلا اصنع معكم حسب حماقتكم لانكم لم تقولوا فيّ الصواب كعبدي ايوب"));
arasuperobject.biblemap.put("b18_42_9", new arasuperobject(18,42,9,"فذهب اليفاز التيماني وبلدد الشوحي وصوفر النعماتي وفعلوا كما قال الرب لهم ورفع الرب وجه ايوب"));
arasuperobject.biblemap.put("b18_42_10", new arasuperobject(18,42,10,"ورد الرب سبي ايوب لما صلى لاجل اصحابه وزاد الرب على كل ما كان لايوب ضعفا"));
arasuperobject.biblemap.put("b18_42_11", new arasuperobject(18,42,11,"فجاء اليه كل اخوته وكل اخواته وكل معارفه من قبل وأكلوا معه خبزا في بيته ورثوا له وعزّوه عن كل الشر الذي جلبه الرب عليه واعطاه كل منهم قسيطة واحدة وكل واحد قرطا من ذهب"));
arasuperobject.biblemap.put("b18_42_12", new arasuperobject(18,42,12,"وبارك الرب آخرة ايوب اكثر من اولاه وكان له اربعة عشر الفا من الغنم وستة آلاف من الابل والف فدان من البقر والف اتان"));
arasuperobject.biblemap.put("b18_42_13", new arasuperobject(18,42,13,"وكان له سبعة بنين وثلاث بنات"));
arasuperobject.biblemap.put("b18_42_14", new arasuperobject(18,42,14,"وسمّى اسم الاولى يميمة واسم الثانية قصيعة واسم الثالثة قرن هفّوك"));
arasuperobject.biblemap.put("b18_42_15", new arasuperobject(18,42,15,"ولم توجد نساء جميلات كبنات ايوب في كل الارض واعطاهنّ ابوهنّ ميراثا بين اخوتهنّ"));
arasuperobject.biblemap.put("b18_42_16", new arasuperobject(18,42,16,"وعاش ايوب بعد هذا مئة واربعين سنة ورأى بنيه وبني بنيه الى اربعة اجيال"));
arasuperobject.biblemap.put("b18_42_17", new arasuperobject(18,42,17,"ثم مات ايوب شيخا وشبعان الايام"));

