arasuperobject.biblemap.put("b2_4_23", new arasuperobject(2,4,23,"فقلت لك اطلق ابني ليعبدني فأبيت ان تطلقه ها انا اقتل ابنك البكر"));
arasuperobject.biblemap.put("b2_4_24", new arasuperobject(2,4,24,"وحدث في الطريق في المنزل ان الرب التقاه وطلب ان يقتله"));
arasuperobject.biblemap.put("b2_4_25", new arasuperobject(2,4,25,"فاخذت صفّورة صوّانة وقطعت غرلة ابنها ومسّت رجليه. فقالت انك عريس دم لي"));
arasuperobject.biblemap.put("b2_4_26", new arasuperobject(2,4,26,"فانفكّ عنه. حينئذ قالت عريس دم من اجل الختان"));
arasuperobject.biblemap.put("b2_4_27", new arasuperobject(2,4,27,"وقال الرب لهرون اذهب الى البرية لاستقبال موسى. فذهب والتقاه في جبل الله وقبّله"));
arasuperobject.biblemap.put("b2_4_28", new arasuperobject(2,4,28,"فاخبر موسى هرون بجميع كلام الرب الذي ارسله وبكل الآيات التي اوصاه بها"));
arasuperobject.biblemap.put("b2_4_29", new arasuperobject(2,4,29,"ثم مضى موسى وهرون وجمعا جميع شيوخ بني اسرائيل"));
arasuperobject.biblemap.put("b2_4_30", new arasuperobject(2,4,30,"فتكلم هرون بجميع الكلام الذي كلم الرب موسى به وصنع الآيات امام عيون الشعب"));
arasuperobject.biblemap.put("b2_4_31", new arasuperobject(2,4,31,"فآمن الشعب. ولما سمعوا ان الرب افتقد بني اسرائيل وانه نظر مذلّتهم خرّوا وسجدوا"));
arasuperobject.biblemap.put("b2_5_1", new arasuperobject(2,5,1,"وبعد ذلك دخل موسى وهرون وقالا لفرعون هكذا يقول الرب اله اسرائيل اطلق شعبي ليعيّدوا لي في البرية"));
arasuperobject.biblemap.put("b2_5_2", new arasuperobject(2,5,2,"فقال فرعون من هو الرب حتى اسمع لقوله فأطلق اسرائيل. لا اعرف الرب واسرائيل لا اطلقه"));
arasuperobject.biblemap.put("b2_5_3", new arasuperobject(2,5,3,"فقالا اله العبرانيين قد التقانا. فنذهب سفر ثلاثة ايام في البرية ونذبح للرب الهنا. لئلا يصيبنا بالوبإ او بالسيف"));
arasuperobject.biblemap.put("b2_5_4", new arasuperobject(2,5,4,"فقال لهما ملك مصر لماذا يا موسى وهرون تبطلان الشعب من اعماله. اذهبا الى اثقالكما"));
arasuperobject.biblemap.put("b2_5_5", new arasuperobject(2,5,5,"وقال فرعون هوذا الآن شعب الارض كثير وانتما تريحانهم من اثقالهم"));
arasuperobject.biblemap.put("b2_5_6", new arasuperobject(2,5,6,"فامر فرعون في ذلك اليوم مسخّري الشعب ومدبّريه قائلا"));
arasuperobject.biblemap.put("b2_5_7", new arasuperobject(2,5,7,"لا تعودوا تعطون الشعب تبنا لصنع اللبن كامس واول من امس. ليذهبوا هم ويجمعوا تبنا لانفسهم"));
arasuperobject.biblemap.put("b2_5_8", new arasuperobject(2,5,8,"ومقدار اللبن الذي كانوا يصنعونه امس واول من امس تجعلون عليهم. لا تنقصوا منه. فانهم متكاسلون لذلك يصرخون قائلين نذهب ونذبح لالهنا"));
arasuperobject.biblemap.put("b2_5_9", new arasuperobject(2,5,9,"ليثقل العمل على القوم حتى يشتغلوا به ولا يلتفتوا الى كلام الكذب"));
arasuperobject.biblemap.put("b2_5_10", new arasuperobject(2,5,10,"فخرج مسخّرو الشعب ومدبّروه وكلموا الشعب قائلين هكذا يقول فرعون لست اعطيكم تبنا"));
arasuperobject.biblemap.put("b2_5_11", new arasuperobject(2,5,11,"اذهبوا انتم وخذوا لانفسكم تبنا من حيث تجدون. انه لا ينقص من عملكم شيء"));
arasuperobject.biblemap.put("b2_5_12", new arasuperobject(2,5,12,"فتفرّق الشعب في كل ارض مصر ليجمعوا قشا عوضا عن التبن"));
arasuperobject.biblemap.put("b2_5_13", new arasuperobject(2,5,13,"وكان المسخّرون يعجّلونهم قائلين كمّلوا اعمالكم امر كل يوم بيومه كما كان حينما كان التبن"));
arasuperobject.biblemap.put("b2_5_14", new arasuperobject(2,5,14,"فضرب مدبرو بني اسرائيل الذين اقامهم عليهم مسخّرو فرعون وقيل لهم لماذا لم تكملوا فريضتكم من صنع اللبن امس واليوم كالامس واول من امس"));
arasuperobject.biblemap.put("b2_5_15", new arasuperobject(2,5,15,"فأتى مدبرو بنو اسرائيل وصرخوا الى فرعون قائلين لماذا تفعل هكذا بعبيدك"));
arasuperobject.biblemap.put("b2_5_16", new arasuperobject(2,5,16,"التبن ليس يعطى لعبيدك واللبن يقولون لنا اصنعوه. وهوذا عبيدك مضروبون. وقد اخطأ شعبك"));
arasuperobject.biblemap.put("b2_5_17", new arasuperobject(2,5,17,"فقال متكاسلون انتم متكاسلون. لذلك تقولون نذهب ونذبح للرب"));
arasuperobject.biblemap.put("b2_5_18", new arasuperobject(2,5,18,"فالآن اذهبوا اعملوا. وتبن لا يعطى لكم ومقدار اللبن تقدمونه"));
arasuperobject.biblemap.put("b2_5_19", new arasuperobject(2,5,19,"فرأى مدبّرو بني اسرائيل انفسهم في بليّة اذ قيل لهم لا تنقصوا من لبنكم امر كل يوم بيومه"));
arasuperobject.biblemap.put("b2_5_20", new arasuperobject(2,5,20,"وصادفوا موسى وهرون واقفين للقائهم حين خرجوا من لدن فرعون"));
arasuperobject.biblemap.put("b2_5_21", new arasuperobject(2,5,21,"فقالوا لهما ينظر الرب اليكما ويقضي. لانكما انتنتما رائحتنا في عيني فرعون وفي عيون عبيده حتى تعطيا سيفا في ايديهم ليقتلونا"));
arasuperobject.biblemap.put("b2_5_22", new arasuperobject(2,5,22,"فرجع موسى الى الرب وقال يا سيد لماذا اسأت الى هذا الشعب. لماذا ارسلتني"));
arasuperobject.biblemap.put("b2_5_23", new arasuperobject(2,5,23,"فانه منذ دخلت الى فرعون لاتكلم باسمك اساء الى هذا الشعب. وانت لم تخلّص شعبك"));
arasuperobject.biblemap.put("b2_6_1", new arasuperobject(2,6,1,"فقال الرب لموسى الآن تنظر ما انا افعل بفرعون. فانه بيد قوية يطلقهم وبيد قوية يطردهم من ارضه"));
arasuperobject.biblemap.put("b2_6_2", new arasuperobject(2,6,2,"ثم كلم الله موسى وقال له انا الرب"));
arasuperobject.biblemap.put("b2_6_3", new arasuperobject(2,6,3,"وانا ظهرت لابراهيم واسحق ويعقوب باني الاله القادر على كل شيء. واما باسمي يهوه فلم أعرف عندهم"));
arasuperobject.biblemap.put("b2_6_4", new arasuperobject(2,6,4,"وايضا اقمت معهم عهدي ان اعطيهم ارض كنعان ارض غربتهم التي تغربوا فيها"));
arasuperobject.biblemap.put("b2_6_5", new arasuperobject(2,6,5,"وانا ايضا قد سمعت أنين بني اسرائيل الذين يستعبدهم المصريون وتذكرت عهدي"));
arasuperobject.biblemap.put("b2_6_6", new arasuperobject(2,6,6,"لذلك قل لبني اسرائيل انا الرب. وانا اخرجكم من تحت اثقال المصريين وانقذكم من عبوديتهم واخلصكم بذراع ممدودة وباحكام عظيمة"));
arasuperobject.biblemap.put("b2_6_7", new arasuperobject(2,6,7,"واتخذكم لي شعبا واكون لكم الها. فتعلمون اني انا الرب الهكم الذي يخرجكم من تحت اثقال المصريين"));
arasuperobject.biblemap.put("b2_6_8", new arasuperobject(2,6,8,"وادخلكم الى الارض التي رفعت يدي ان اعطيها لابراهيم واسحق ويعقوب. واعطيكم اياها ميراثا. انا الرب"));
arasuperobject.biblemap.put("b2_6_9", new arasuperobject(2,6,9,"فكلم موسى هكذا بني اسرئيل. ولكن لم يسمعوا لموسى من صغر النفس ومن العبودية القاسية"));
arasuperobject.biblemap.put("b2_6_10", new arasuperobject(2,6,10,"ثم كلم الرب موسى قائلا"));
arasuperobject.biblemap.put("b2_6_11", new arasuperobject(2,6,11,"ادخل قل لفرعون ملك مصر ان يطلق بني اسرائيل من ارضه"));
arasuperobject.biblemap.put("b2_6_12", new arasuperobject(2,6,12,"فتكلم موسى امام الرب قائلا هوذا بنو اسرائيل لم يسمعوا لي. فكيف يسمعني فرعون وانا اغلف الشفتين"));
arasuperobject.biblemap.put("b2_6_13", new arasuperobject(2,6,13,"فكلم الرب موسى وهرون واوصى معهما الى بني اسرائيل والى فرعون ملك مصر في اخراج بني اسرائيل من ارض مصر"));
arasuperobject.biblemap.put("b2_6_14", new arasuperobject(2,6,14,"هؤلاء رؤساء بيوت آبائهم. بنو رأوبين بكر اسرائيل حنوك وفلو وحصرون وكرمي. هذه عشائر رأوبين"));
arasuperobject.biblemap.put("b2_6_15", new arasuperobject(2,6,15,"وبنو شمعون يموئيل ويامين وأوهد وياكين وصوحر وشأول ابن الكنعانية. هذه عشائر شمعون"));
arasuperobject.biblemap.put("b2_6_16", new arasuperobject(2,6,16,"وهذه اسماء بني لاوي بحسب مواليدهم. جرشون وقهات ومراري. وكانت سنو حياة لاوي مئة وسبعا وثلاثين سنة"));
arasuperobject.biblemap.put("b2_6_17", new arasuperobject(2,6,17,"ابنا جرشون لبني وشمعي بحسب عشائرهما"));
arasuperobject.biblemap.put("b2_6_18", new arasuperobject(2,6,18,"وبنو قهات عمرام ويصهار وحبرون وعزّيئيل. وكانت سنو حياة قهات مئة وثلاثا وثلاثين سنة"));
arasuperobject.biblemap.put("b2_6_19", new arasuperobject(2,6,19,"وابنا مراري محلي وموشي. هذه عشائر اللاويين بحسب مواليدهم"));
arasuperobject.biblemap.put("b2_6_20", new arasuperobject(2,6,20,"واخذ عمرام يوكابد عمته زوجة له. فولدت له هرون وموسى. وكانت سنو حياة عمرام مئة وسبعا وثلاثين سنة"));
arasuperobject.biblemap.put("b2_6_21", new arasuperobject(2,6,21,"وبنو يصهار قورح ونافج وذكري"));
arasuperobject.biblemap.put("b2_6_22", new arasuperobject(2,6,22,"وبنو عزّيئيل ميشائيل وألصافان وسنري"));
arasuperobject.biblemap.put("b2_6_23", new arasuperobject(2,6,23,"واخذ هرون أليشابع بنت عمّيناداب اخت نحشون زوجة له. فولدت له ناداب وابيهو والعازار وإيثامار"));
arasuperobject.biblemap.put("b2_6_24", new arasuperobject(2,6,24,"وبنو قورح أسّير وألقانة وابيأساف. هذه عشائر القورحيين"));
arasuperobject.biblemap.put("b2_6_25", new arasuperobject(2,6,25,"والعازار ابن هرون اخذ لنفسه من بنات فوطيئيل زوجة. فولدت له فينحاس. هؤلاء هم رؤساء آباء اللاويين بحسب عشائرهم"));
arasuperobject.biblemap.put("b2_6_26", new arasuperobject(2,6,26,"هذان هما هرون وموسى اللذان قال الرب لهما أخرجا بني اسرائيل من ارض مصر بحسب اجنادهم"));
arasuperobject.biblemap.put("b2_6_27", new arasuperobject(2,6,27,"هما اللذان كلما فرعون ملك مصر في اخراج بني اسرائيل من مصر هذان هما موسى وهرون"));
arasuperobject.biblemap.put("b2_6_28", new arasuperobject(2,6,28,"وكان يوم كلم الرب موسى في ارض مصر"));
arasuperobject.biblemap.put("b2_6_29", new arasuperobject(2,6,29,"ان الرب كلمه قائلا انا الرب. كلم فرعون ملك مصر بكل ما انا اكلمك به"));
arasuperobject.biblemap.put("b2_6_30", new arasuperobject(2,6,30,"فقال موسى امام الرب ها انا اغلف الشفتين. فكيف يسمع لي فرعون"));
arasuperobject.biblemap.put("b2_7_1", new arasuperobject(2,7,1,"فقال الرب لموسى انظر. انا جعلتك الها لفرعون. وهرون اخوك يكون نبيّك"));
arasuperobject.biblemap.put("b2_7_2", new arasuperobject(2,7,2,"انت تتكلم بكل ما آمرك. وهرون اخوك يكلم فرعون ليطلق بني اسرائيل من ارضه"));
arasuperobject.biblemap.put("b2_7_3", new arasuperobject(2,7,3,"ولكني اقسّي قلب فرعون واكثّر آياتي وعجائبي في ارض مصر"));
arasuperobject.biblemap.put("b2_7_4", new arasuperobject(2,7,4,"ولا يسمع لكما فرعون حتى اجعل يدي على مصر فاخرج اجنادي شعبي بني اسرائيل من ارض مصر باحكام عظيمة"));
arasuperobject.biblemap.put("b2_7_5", new arasuperobject(2,7,5,"فيعرف المصريون اني انا الرب حينما امد يدي على مصر واخرج بني اسرائيل من بينهم"));
arasuperobject.biblemap.put("b2_7_6", new arasuperobject(2,7,6,"ففعل موسى وهرون كما امرهما الرب. هكذا فعلا"));
arasuperobject.biblemap.put("b2_7_7", new arasuperobject(2,7,7,"وكان موسى ابن ثمانين سنة وهرون ابن ثلاث وثمانين سنة حين كلما فرعون"));
arasuperobject.biblemap.put("b2_7_8", new arasuperobject(2,7,8,"وكلم الرب موسى وهرون قائلا"));
arasuperobject.biblemap.put("b2_7_9", new arasuperobject(2,7,9,"اذا كلمكما فرعون قائلا هاتيا عجيبة تقول لهرون خذ عصاك واطرحها امام فرعون فتصير ثعبانا"));
arasuperobject.biblemap.put("b2_7_10", new arasuperobject(2,7,10,"فدخل موسى وهرون الى فرعون وفعلا هكذا كما امر الرب. طرح هرون عصاه امام فرعون وامام عبيده فصارت ثعبانا"));
arasuperobject.biblemap.put("b2_7_11", new arasuperobject(2,7,11,"فدعا فرعون ايضا الحكماء والسحرة. ففعل عرّافو مصر ايضا بسحرهم كذلك"));
arasuperobject.biblemap.put("b2_7_12", new arasuperobject(2,7,12,"طرحوا كل واحد عصاه فصارت العصي ثعابين. ولكن عصا هرون ابتلعت عصيهم"));
arasuperobject.biblemap.put("b2_7_13", new arasuperobject(2,7,13,"فاشتدّ قلب فرعون فلم يسمع لهما كما تكلم الرب"));
arasuperobject.biblemap.put("b2_7_14", new arasuperobject(2,7,14,"ثم قال الرب لموسى قلب فرعون غليظ. قد أبى ان يطلق الشعب"));
arasuperobject.biblemap.put("b2_7_15", new arasuperobject(2,7,15,"اذهب الى فرعون في الصباح. انه يخرج الى الماء. وقف للقائه على حافة النهر. والعصا التي تحولت حية تاخذها في يدك"));
arasuperobject.biblemap.put("b2_7_16", new arasuperobject(2,7,16,"وتقول له الرب اله العبرانيين ارسلني اليك قائلا اطلق شعبي ليعبدوني في البرية. وهوذا حتى الآن لم تسمع"));
arasuperobject.biblemap.put("b2_7_17", new arasuperobject(2,7,17,"هكذا يقول الرب بهذا تعرف اني انا الرب. ها انا اضرب بالعصا التي في يدي على الماء الذي في النهر فيتحول دما"));
arasuperobject.biblemap.put("b2_7_18", new arasuperobject(2,7,18,"ويموت السمك الذي في النهر وينتن النهر. فيعاف المصريون ان يشربوا ماء النهر"));
arasuperobject.biblemap.put("b2_7_19", new arasuperobject(2,7,19,"ثم قال الرب لموسى قل لهرون خذ عصاك ومد يدك على مياه المصريين على انهارهم وعلى سواقيهم وعلى آجامهم وعلى كل مجتمعات مياههم لتصير دما. فيكون دم في كل ارض مصر في الاخشاب وفي الاحجار"));
arasuperobject.biblemap.put("b2_7_20", new arasuperobject(2,7,20,"ففعل هكذا موسى وهرون كما امر الرب. رفع العصا وضرب الماء الذي في النهر امام عيني فرعون وامام عيون عبيده. فتحول كل الماء الذي في النهر دما"));
arasuperobject.biblemap.put("b2_7_21", new arasuperobject(2,7,21,"ومات السمك الذي في النهر وأنتن النهر. فلم يقدر المصريون ان يشربوا ماء من النهر. وكان الدم في كل ارض مصر"));
arasuperobject.biblemap.put("b2_7_22", new arasuperobject(2,7,22,"وفعل عرّافو مصر كذلك بسحرهم. فاشتدّ قلب فرعون فلم يسمع لهما كما تكلم الرب"));
arasuperobject.biblemap.put("b2_7_23", new arasuperobject(2,7,23,"ثم انصرف فرعون ودخل بيته ولم يوجّه قلبه الى هذا ايضا"));
arasuperobject.biblemap.put("b2_7_24", new arasuperobject(2,7,24,"وحفر جميع المصريين حوالي النهر لاجل ماء ليشربوا. لانهم لم يقدروا ان يشربوا من ماء النهر"));
arasuperobject.biblemap.put("b2_7_25", new arasuperobject(2,7,25,"ولما كملت سبعة ايام بعدما ضرب الرب النهر"));
arasuperobject.biblemap.put("b2_8_1", new arasuperobject(2,8,1,"قال الرب لموسى ادخل الى فرعون وقل له هكذا يقول الرب اطلق شعبي ليعبدوني"));
arasuperobject.biblemap.put("b2_8_2", new arasuperobject(2,8,2,"وان كنت تأبى ان تطلقهم فها انا اضرب جميع تخومك بالضفادع"));
arasuperobject.biblemap.put("b2_8_3", new arasuperobject(2,8,3,"فيفيض النهر ضفادع. فتصعد وتدخل الى بيتك والى مخدع فراشك وعلى سريرك والى بيوت عبيدك وعلى شعبك والى تنانيرك والى معاجنك"));
arasuperobject.biblemap.put("b2_8_4", new arasuperobject(2,8,4,"عليك وعلى شعبك وعبيدك تصعد الضفادع"));
arasuperobject.biblemap.put("b2_8_5", new arasuperobject(2,8,5,"فقال الرب لموسى قل لهرون مدّ يدك بعصاك على الانهار والسواقي والآجام واصعد الضفادع على ارض مصر"));
arasuperobject.biblemap.put("b2_8_6", new arasuperobject(2,8,6,"فمدّ هرون يده على مياه مصر. فصعدت الضفادع وغطت ارض مصر"));
arasuperobject.biblemap.put("b2_8_7", new arasuperobject(2,8,7,"وفعل كذلك العرافون بسحرهم واصعدوا الضفادع على ارض مصر"));
arasuperobject.biblemap.put("b2_8_8", new arasuperobject(2,8,8,"فدعا فرعون موسى وهرون وقال صليا الى الرب ليرفع الضفادع عني وعن شعبي فأطلق الشعب ليذبحوا للرب"));
arasuperobject.biblemap.put("b2_8_9", new arasuperobject(2,8,9,"فقال موسى لفرعون عيّن لي متى اصلي لاجلك ولاجل عبيدك وشعبك لقطع الضفادع عنك وعن بيوتك. ولكنها تبقى في النهر"));
arasuperobject.biblemap.put("b2_8_10", new arasuperobject(2,8,10,"فقال غدا. فقال كقولك. لكي تعرف ان ليس مثل الرب الهنا"));
arasuperobject.biblemap.put("b2_8_11", new arasuperobject(2,8,11,"فترتفع الضفادع عنك وعن بيوتك وعبيدك وشعبك. ولكنها تبقى في النهر"));
arasuperobject.biblemap.put("b2_8_12", new arasuperobject(2,8,12,"ثم خرج موسى وهرون من لدن فرعون وصرخ موسى الى الرب من اجل الضفادع التي جعلها على فرعون"));
arasuperobject.biblemap.put("b2_8_13", new arasuperobject(2,8,13,"ففعل الرب كقول موسى. فماتت الضفادع من البيوت والدور والحقول"));
arasuperobject.biblemap.put("b2_8_14", new arasuperobject(2,8,14,"وجمعوها كوما كثيرة حتى أنتنت الارض"));
arasuperobject.biblemap.put("b2_8_15", new arasuperobject(2,8,15,"فلما رأى فرعون انه قد حصل الفرج اغلظ قلبه ولم يسمع لهما كما تكلم الرب"));
arasuperobject.biblemap.put("b2_8_16", new arasuperobject(2,8,16,"ثم قال الرب لموسى قل لهرون مدّ عصاك واضرب تراب الارض ليصير بعوضا في جميع ارض مصر"));
arasuperobject.biblemap.put("b2_8_17", new arasuperobject(2,8,17,"ففعلا كذلك. مدّ هرون يده بعصاه وضرب تراب الارض. فصار البعوض على الناس وعلى البهائم. كل تراب الارض صار بعوضا في جميع ارض مصر"));
arasuperobject.biblemap.put("b2_8_18", new arasuperobject(2,8,18,"وفعل كذلك العرافون بسحرهم ليخرجوا البعوض فلم يستطيعوا. وكان البعوض على الناس وعلى البهائم"));
arasuperobject.biblemap.put("b2_8_19", new arasuperobject(2,8,19,"فقال العرافون لفرعون هذا اصبع الله. ولكن اشتد قلب فرعون فلم يسمع لهما كما تكلم الرب"));
arasuperobject.biblemap.put("b2_8_20", new arasuperobject(2,8,20,"ثم قال الرب لموسى بكّر في الصباح وقف امام فرعون. انه يخرج الى الماء. وقل له هكذا يقول الرب اطلق شعبي ليعبدوني"));
arasuperobject.biblemap.put("b2_8_21", new arasuperobject(2,8,21,"فانه ان كنت لا تطلق شعبي ها انا ارسل عليك وعلى عبيدك وعلى شعبك وعلى بيوتك الذبّان. فتمتلئ بيوت المصريين ذبّانا. وايضا الارض التي هم عليها"));
arasuperobject.biblemap.put("b2_8_22", new arasuperobject(2,8,22,"ولكن أميّز في ذلك اليوم ارض جاسان حيث شعبي مقيم حتى لا يكون هناك ذبّان. لكي تعلم اني انا الرب في الارض"));
arasuperobject.biblemap.put("b2_8_23", new arasuperobject(2,8,23,"واجعل فرقا بين شعبي وشعبك. غدا تكون هذه الآية"));
arasuperobject.biblemap.put("b2_8_24", new arasuperobject(2,8,24,"ففعل الرب هكذا. فدخلت ذبان كثيرة الى بيت فرعون وبيوت عبيده وفي كل ارض مصر خربت الارض من الذبّان"));
arasuperobject.biblemap.put("b2_8_25", new arasuperobject(2,8,25,"فدعا فرعون موسى وهرون وقال اذهبوا اذبحوا لالهكم في هذه الارض"));
arasuperobject.biblemap.put("b2_8_26", new arasuperobject(2,8,26,"فقال موسى لا يصلح ان نفعل هكذا. لاننا انما نذبح رجس المصريين للرب الهنا. ان ذبحنا رجس المصريين امام عيونهم أفلا يرجموننا"));
arasuperobject.biblemap.put("b2_8_27", new arasuperobject(2,8,27,"نذهب سفر ثلاثة ايام في البرية ونذبح للرب الهنا كما يقول لنا"));
arasuperobject.biblemap.put("b2_8_28", new arasuperobject(2,8,28,"فقال فرعون انا اطلقكم لتذبحوا للرب الهكم في البرية ولكن لا تذهبوا بعيدا. صلّيا لاجلي"));
arasuperobject.biblemap.put("b2_8_29", new arasuperobject(2,8,29,"فقال موسى ها انا اخرج من لدنك واصلي الى الرب. فترتفع الذبان عن فرعون وعبيده وشعبه غدا. ولكن لا يعد فرعون يخاتل حتى لا يطلق الشعب ليذبح للرب"));
arasuperobject.biblemap.put("b2_8_30", new arasuperobject(2,8,30,"فخرج موسى من لدن فرعون وصلى الى الرب"));
arasuperobject.biblemap.put("b2_8_31", new arasuperobject(2,8,31,"ففعل الرب كقول موسى. فارتفع الذبان عن فرعون وعبيده وشعبه. لم تبق واحدة"));
arasuperobject.biblemap.put("b2_8_32", new arasuperobject(2,8,32,"ولكن اغلظ فرعون قلبه هذه المرة ايضا فلم يطلق الشعب"));
arasuperobject.biblemap.put("b2_9_1", new arasuperobject(2,9,1,"ثم قال الرب لموسى ادخل الى فرعون وقل له هكذا يقول الرب اله العبرانيين اطلق شعبي ليعبدوني"));
arasuperobject.biblemap.put("b2_9_2", new arasuperobject(2,9,2,"فانه ان كنت تأبى ان تطلقهم وكنت تمسكهم بعد"));
arasuperobject.biblemap.put("b2_9_3", new arasuperobject(2,9,3,"فها يد الرب تكون على مواشيك التي في الحقل على الخيل والحمير والجمال والبقر والغنم وبأ ثقيلا جدا"));
arasuperobject.biblemap.put("b2_9_4", new arasuperobject(2,9,4,"ويميّز الرب بين مواشي اسرائيل ومواشي المصريين. فلا يموت من كل ما لبني اسرائيل شيء"));
arasuperobject.biblemap.put("b2_9_5", new arasuperobject(2,9,5,"وعيّن الرب وقتا قائلا غدا يفعل الرب هذا الامر في الارض"));
arasuperobject.biblemap.put("b2_9_6", new arasuperobject(2,9,6,"ففعل الرب هذا الامر في الغد. فماتت جميع مواشي المصريين. واما مواشي بني اسرائيل فلم يمت منها واحد"));
arasuperobject.biblemap.put("b2_9_7", new arasuperobject(2,9,7,"وارسل فرعون واذا مواشي اسرائيل لم يمت منها ولا واحد. ولكن غلظ قلب فرعون فلم يطلق الشعب"));
arasuperobject.biblemap.put("b2_9_8", new arasuperobject(2,9,8,"ثم قال الرب لموسى وهرون خذا ملء ايديكما من رماد الأتون. وليذرّه موسى نحو السماء امام عيني فرعون"));
arasuperobject.biblemap.put("b2_9_9", new arasuperobject(2,9,9,"ليصير غبارا على كل ارض مصر. فيصير على الناس وعلى البهائم دمامل طالعة ببثور في كل ارض مصر"));
arasuperobject.biblemap.put("b2_9_10", new arasuperobject(2,9,10,"فأخذا رماد الأتون ووقفا امام فرعون وذراه موسى نحو السماء. فصار دمامل بثور طالعة في الناس وفي البهائم"));
arasuperobject.biblemap.put("b2_9_11", new arasuperobject(2,9,11,"ولم يستطع العرافون ان يقفوا امام موسى من اجل الدمامل. لان الدمامل كانت في العرافين وفي كل المصريين"));
arasuperobject.biblemap.put("b2_9_12", new arasuperobject(2,9,12,"ولكن شدّد الرب قلب فرعون فلم يسمع لهما كما كلم الرب موسى"));
arasuperobject.biblemap.put("b2_9_13", new arasuperobject(2,9,13,"ثم قال الرب لموسى بكر في الصباح وقف امام فرعون وقل له هكذا يقول الرب اله العبرانيين أطلق شعبي ليعبدوني"));
arasuperobject.biblemap.put("b2_9_14", new arasuperobject(2,9,14,"لاني هذه المرة ارسل جميع ضرباتي الى قلبك وعلى عبيدك وشعبك لكي تعرف ان ليس مثلي في كل الارض"));
arasuperobject.biblemap.put("b2_9_15", new arasuperobject(2,9,15,"فانه الآن لو كنت امد يدي واضربك وشعبك بالوبإ لكنت تباد من الارض"));
arasuperobject.biblemap.put("b2_9_16", new arasuperobject(2,9,16,"ولكن لاجل هذا اقمتك لكي اريك قوّتي ولكي يخبر باسمي في كل الارض"));
arasuperobject.biblemap.put("b2_9_17", new arasuperobject(2,9,17,"انت معاند بعد لشعبي حتى لا تطلقه"));
arasuperobject.biblemap.put("b2_9_18", new arasuperobject(2,9,18,"ها انا غدا مثل الآن امطر بردا عظيما جدا لم يكن مثله في مصر منذ يوم تاسيسها الى الآن"));
arasuperobject.biblemap.put("b2_9_19", new arasuperobject(2,9,19,"فالآن أرسل احم مواشيك وكل ما لك في الحقل. جميع الناس والبهائم الذين يوجدون في الحقل ولا يجمعون الى البيوت ينزل عليهم البرد فيموتون"));
arasuperobject.biblemap.put("b2_9_20", new arasuperobject(2,9,20,"فالذي خاف كلمة الرب من عبيد فرعون هرب بعبيده ومواشيه الى البيوت"));
arasuperobject.biblemap.put("b2_9_21", new arasuperobject(2,9,21,"واما الذي لم يوجّه قلبه الى كلمة الرب فترك عبيده ومواشيه في الحقل"));
arasuperobject.biblemap.put("b2_9_22", new arasuperobject(2,9,22,"ثم قال الرب لموسى مدّ يدك نحو السماء ليكون برد في كل ارض مصر على الناس وعلى البهائم وعلى كل عشب الحقل في ارض مصر"));
arasuperobject.biblemap.put("b2_9_23", new arasuperobject(2,9,23,"فمدّ موسى عصاه نحو السماء. فاعطى الرب رعودا وبردا وجرت نار على الارض وامطر الرب بردا على ارض مصر"));
arasuperobject.biblemap.put("b2_9_24", new arasuperobject(2,9,24,"فكان برد ونار متواصلة في وسط البرد. شيء عظيم جدا لم يكن مثله في كل ارض مصر منذ صارت امة"));
arasuperobject.biblemap.put("b2_9_25", new arasuperobject(2,9,25,"فضرب البرد في كل ارض مصر جميع ما في الحقل من الناس والبهائم. وضرب البرد جميع عشب الحقل وكسر جميع شجر الحقل"));
arasuperobject.biblemap.put("b2_9_26", new arasuperobject(2,9,26,"الا ارض جاسان حيث كان بنو اسرائيل فلم يكن فيها برد"));
arasuperobject.biblemap.put("b2_9_27", new arasuperobject(2,9,27,"فارسل فرعون ودعا موسى وهرون وقال لهما اخطأت هذه المرّة. الرب هو البار وانا وشعبي الاشرار"));
arasuperobject.biblemap.put("b2_9_28", new arasuperobject(2,9,28,"صليّا الى الرب وكفى حدوث رعود الله والبرد فاطلقكم ولا تعودوا تلبثون"));
arasuperobject.biblemap.put("b2_9_29", new arasuperobject(2,9,29,"فقال له موسى عند خروجي من المدينة ابسط يديّ الى الرب فتنقطع الرعود ولا يكون البرد ايضا لكي تعرف ان للرب الارض"));
arasuperobject.biblemap.put("b2_9_30", new arasuperobject(2,9,30,"واما انت وعبيدك فانا اعلم انكم لم تخشوا بعد من الرب الاله"));
arasuperobject.biblemap.put("b2_9_31", new arasuperobject(2,9,31,"فالكتان والشعير ضربا. لان الشعير كان مسبلا والكتان مبزرا"));
arasuperobject.biblemap.put("b2_9_32", new arasuperobject(2,9,32,"واما الحنطة والقطاني فلم تضرب لانها كانت متأخرة"));
arasuperobject.biblemap.put("b2_9_33", new arasuperobject(2,9,33,"فخرج موسى من المدينة من لدن فرعون وبسط يديه الى الرب. فانقطعت الرعود والبرد ولم ينصبّ المطر على الارض"));
arasuperobject.biblemap.put("b2_9_34", new arasuperobject(2,9,34,"ولكن فرعون لما رأى ان المطر والبرد والرعود انقطعت عاد يخطئ واغلظ قلبه هو وعبيده"));
arasuperobject.biblemap.put("b2_9_35", new arasuperobject(2,9,35,"فاشتدّ قلب فرعون فلم يطلق بني اسرائيل كما تكلم الرب عن يد موسى"));
arasuperobject.biblemap.put("b2_10_1", new arasuperobject(2,10,1,"ثم قال الرب لموسى ادخل الى فرعون. فاني اغلظت قلبه وقلوب عبيده لكي اصنع آياتي هذه بينهم"));
arasuperobject.biblemap.put("b2_10_2", new arasuperobject(2,10,2,"ولكي تخبر في مسامع ابنك وابن ابنك بما فعلته في مصر وبآياتي التي صنعتها بينهم. فتعلمون اني انا الرب"));
arasuperobject.biblemap.put("b2_10_3", new arasuperobject(2,10,3,"فدخل موسى وهرون الى فرعون وقالا له هكذا يقول الرب اله العبرانيين الى متى تأبى ان تخضع لي. اطلق شعبي ليعبدوني"));
arasuperobject.biblemap.put("b2_10_4", new arasuperobject(2,10,4,"فانه ان كنت تأبى ان تطلق شعبي ها انا اجيء غدا بجراد على تخومك"));
arasuperobject.biblemap.put("b2_10_5", new arasuperobject(2,10,5,"فيغطي وجه الارض حتى لا يستطاع نظر الارض. ويأكل الفضلة السالمة الباقية لكم من البرد. ويأكل جميع الشجر النابت لكم من الحقل"));
arasuperobject.biblemap.put("b2_10_6", new arasuperobject(2,10,6,"ويملأ بيوتك وبيوت جميع عبيدك وبيوت جميع المصريين. الامر الذي لم يره آباؤك ولا آباء آبائك منذ يوم وجدوا على الارض الى هذا اليوم. ثم تحوّل وخرج من لدن فرعون"));
arasuperobject.biblemap.put("b2_10_7", new arasuperobject(2,10,7,"فقال عبيد فرعون له الى متى يكون هذا لنا فخّا. اطلق الرجال ليعبدوا الرب الههم. ألم تعلم بعد ان مصر قد خربت"));
arasuperobject.biblemap.put("b2_10_8", new arasuperobject(2,10,8,"فرد موسى وهرون الى فرعون. فقال لهما اذهبوا اعبدوا الرب الهكم ولكن من ومن هم الذين يذهبون"));
arasuperobject.biblemap.put("b2_10_9", new arasuperobject(2,10,9,"فقال موسى نذهب بفتياننا وشيوخنا. نذهب ببنينا وبناتنا بغنمنا وبقرنا. لان لنا عيدا للرب"));
arasuperobject.biblemap.put("b2_10_10", new arasuperobject(2,10,10,"فقال لهما يكون الرب معكم هكذا كما أطلقكم واولادكم. انظروا. ان قدام وجوهكم شرا"));
arasuperobject.biblemap.put("b2_10_11", new arasuperobject(2,10,11,"ليس هكذا. اذهبوا انتم الرجال واعبدوا الرب. لانكم لهذا طالبون. فطردا من لدن فرعون"));
arasuperobject.biblemap.put("b2_10_12", new arasuperobject(2,10,12,"ثم قال الرب لموسى مدّ يدك على ارض مصر لاجل الجراد. ليصعد على ارض مصر وياكل كل عشب الارض كل ما تركه البرد"));
arasuperobject.biblemap.put("b2_10_13", new arasuperobject(2,10,13,"فمدّ موسى عصاه على ارض مصر. فجلب الرب على الارض ريحا شرقية كل ذلك النهار وكل الليل. ولما كان الصباح حملت الريح الشرقية الجراد"));
arasuperobject.biblemap.put("b2_10_14", new arasuperobject(2,10,14,"فصعد الجراد على كل ارض مصر وحلّ في جميع تخوم مصر. شيء ثقيل جدا لم يكن قبله جراد هكذا مثله ولا يكون بعده كذلك"));
arasuperobject.biblemap.put("b2_10_15", new arasuperobject(2,10,15,"وغطى وجه كل الارض حتى اظلمت الارض. واكل جميع عشب الارض وجميع ثمر الشجر الذي تركه البرد. حتى لم يبق شيء اخضر في الشجر ولا في عشب الحقل في كل ارض مصر"));
arasuperobject.biblemap.put("b2_10_16", new arasuperobject(2,10,16,"فدعا فرعون موسى وهرون مسرعا وقال اخطأت الى الرب الهكما واليكما"));
arasuperobject.biblemap.put("b2_10_17", new arasuperobject(2,10,17,"والآن اصفحا عن خطيتي هذه المرّة فقط. وصليّا الى الرب الهكما ليرفع عني هذا الموت فقط"));
arasuperobject.biblemap.put("b2_10_18", new arasuperobject(2,10,18,"فخرج موسى من لدن فرعون وصلى الى الرب"));
arasuperobject.biblemap.put("b2_10_19", new arasuperobject(2,10,19,"فرد الرب ريحا غربية شديدة جدا. فحملت الجراد وطرحته الى بحر سوف. لم تبق جرادة واحدة في كل تخوم مصر"));
arasuperobject.biblemap.put("b2_10_20", new arasuperobject(2,10,20,"ولكن شدد الرب قلب فرعون فلم يطلق بني اسرائيل"));
arasuperobject.biblemap.put("b2_10_21", new arasuperobject(2,10,21,"ثم قال الرب لموسى مدّ يدك نحو السماء ليكون ظلام على ارض مصر. حتى يلمس الظلام"));
arasuperobject.biblemap.put("b2_10_22", new arasuperobject(2,10,22,"فمدّ موسى يده نحو السماء فكان ظلام دامس في كل ارض مصر ثلاثة ايام"));
arasuperobject.biblemap.put("b2_10_23", new arasuperobject(2,10,23,"لم يبصر احد اخاه ولا قام احد من مكانه ثلاثة ايام. ولكن جميع بني اسرائيل كان لهم نور في مساكنهم"));
arasuperobject.biblemap.put("b2_10_24", new arasuperobject(2,10,24,"فدعا فرعون موسى وقال اذهبوا اعبدوا الرب. غير ان غنمكم وبقركم تبقى. اولادكم ايضا تذهب معكم"));
arasuperobject.biblemap.put("b2_10_25", new arasuperobject(2,10,25,"فقال موسى انت تعطي ايضا في ايدينا ذبائح ومحرقات لنصنعها للرب الهنا"));
arasuperobject.biblemap.put("b2_10_26", new arasuperobject(2,10,26,"فتذهب مواشينا ايضا معنا. لا يبقى ظلف. لاننا منها نأخذ لعبادة الرب الهنا. ونحن لا نعرف بماذا نعبد الرب حتى نأتي الى هناك"));
