arasuperobject.biblemap.put("b2_10_28", new arasuperobject(2,10,28,"وقال له فرعون اذهب عني. احترز. لا تر وجهي ايضا. انك يوم ترى وجهي تموت"));
arasuperobject.biblemap.put("b2_10_29", new arasuperobject(2,10,29,"فقال موسى نعما قلت. انا لا اعود ارى وجهك ايضا"));
arasuperobject.biblemap.put("b2_11_1", new arasuperobject(2,11,1,"ثم قال الرب لموسى ضربة واحدة ايضا اجلب على فرعون وعلى مصر. بعد ذلك يطلقكم من هنا. وعندما يطلقكم يطردكم طردا من هنا بالتمام"));
arasuperobject.biblemap.put("b2_11_2", new arasuperobject(2,11,2,"تكلم في مسامع الشعب ان يطلب كل رجل من صاحبه وكل امرأة من صاحبتها امتعة فضة وامتعة ذهب"));
arasuperobject.biblemap.put("b2_11_3", new arasuperobject(2,11,3,"واعطى الرب نعمة للشعب في عيون المصريين. وايضا الرجل موسى كان عظيما جدا في ارض مصر في عيون عبيد فرعون وعيون الشعب"));
arasuperobject.biblemap.put("b2_11_4", new arasuperobject(2,11,4,"وقال موسى هكذا يقول الرب اني نحو نصف الليل اخرج في وسط مصر"));
arasuperobject.biblemap.put("b2_11_5", new arasuperobject(2,11,5,"فيموت كل بكر في ارض مصر من بكر فرعون الجالس على كرسيه الى بكر الجارية التي خلف الرحى وكل بكر بهيمة"));
arasuperobject.biblemap.put("b2_11_6", new arasuperobject(2,11,6,"ويكون صراخ عظيم في كل ارض مصر لم يكن مثله ولا يكون مثله ايضا"));
arasuperobject.biblemap.put("b2_11_7", new arasuperobject(2,11,7,"ولكن جميع بني اسرائيل لا يسنن كلب لسانه اليهم لا الى الناس ولا الى البهائم. لكي تعلموا ان الرب يميّز بين المصريين واسرائيل"));
arasuperobject.biblemap.put("b2_11_8", new arasuperobject(2,11,8,"فينزل اليّ جميع عبيدك هؤلاء ويسجدون لي قائلين اخرج انت وجميع الشعب الذين في اثرك. وبعد ذلك اخرج. ثم خرج من لدن فرعون في حموّ الغضب"));
arasuperobject.biblemap.put("b2_11_9", new arasuperobject(2,11,9,"وقال الرب لموسى لا يسمع لكما فرعون لكي تكثر عجائبي في ارض مصر"));
arasuperobject.biblemap.put("b2_11_10", new arasuperobject(2,11,10,"وكان موسى وهرون يفعلان كل هذه العجائب امام فرعون. ولكن شدّد الرب قلب فرعون فلم يطلق بني اسرائيل من ارضه"));
arasuperobject.biblemap.put("b2_12_1", new arasuperobject(2,12,1,"وكلم الرب موسى وهرون في ارض مصر قائلا"));
arasuperobject.biblemap.put("b2_12_2", new arasuperobject(2,12,2,"هذا الشهر يكون لكم راس الشهور. هو لكم اول شهور السنة"));
arasuperobject.biblemap.put("b2_12_3", new arasuperobject(2,12,3,"كلّما كل جماعة اسرائيل قائلين في العاشر من هذا الشهر يأخذون لهم كل واحد شاة بحسب بيوت الآباء شاة للبيت"));
arasuperobject.biblemap.put("b2_12_4", new arasuperobject(2,12,4,"وان كان البيت صغيرا عن ان يكون كفوا لشاة يأخذ هو وجاره القريب من بيته بحسب عدد النفوس. كل واحد على حسب اكله تحسبون للشاة"));
arasuperobject.biblemap.put("b2_12_5", new arasuperobject(2,12,5,"تكون لكم شاة صحيحة ذكرا ابن سنة. تأخذونه من الخرفان او من المواعز"));
arasuperobject.biblemap.put("b2_12_6", new arasuperobject(2,12,6,"ويكون عندكم تحت الحفظ الى اليوم الرابع عشر من هذا الشهر. ثم يذبحه كل جمهور جماعة اسرائيل في العشية"));
arasuperobject.biblemap.put("b2_12_7", new arasuperobject(2,12,7,"ويأخذون من الدم ويجعلونه على القائمتين والعتبة العليا في البيوت التي يأكلونه فيها"));
arasuperobject.biblemap.put("b2_12_8", new arasuperobject(2,12,8,"ويأكلون اللحم تلك الليلة مشويا بالنار مع فطير. على اعشاب مرّة ياكلونه"));
arasuperobject.biblemap.put("b2_12_9", new arasuperobject(2,12,9,"لا تأكلوا منه نيئا او طبيخا مطبوخا بالماء بل مشويا بالنار. راسه مع اكارعه وجوفه"));
arasuperobject.biblemap.put("b2_12_10", new arasuperobject(2,12,10,"ولا تبقوا منه الى الصباح. والباقي منه الى الصباح تحرقونه بالنار"));
arasuperobject.biblemap.put("b2_12_11", new arasuperobject(2,12,11,"وهكذا تأكلونه احقاؤكم مشدودة واحذيتكم في ارجلكم وعصيّكم في ايديكم. وتاكلونه بعجلة. هو فصح للرب"));
arasuperobject.biblemap.put("b2_12_12", new arasuperobject(2,12,12,"فاني اجتاز في ارض مصر هذه الليلة واضرب كل بكر في ارض مصر من الناس والبهائم. واصنع احكاما بكل آلهة المصريين. انا الرب"));
arasuperobject.biblemap.put("b2_12_13", new arasuperobject(2,12,13,"ويكون لكم الدم علامة على البيوت التي انتم فيها. فأرى الدم واعبر عنكم. فلا يكون عليكم ضربة للهلاك حين اضرب ارض مصر"));
arasuperobject.biblemap.put("b2_12_14", new arasuperobject(2,12,14,"ويكون لكم هذا اليوم تذكارا فتعيّدونه عيدا للرب. في اجيالكم تعيّدونه فريضة ابدية"));
arasuperobject.biblemap.put("b2_12_15", new arasuperobject(2,12,15,"سبعة ايام تأكلون فطيرا. اليوم الاول تعزلون الخمير من بيوتكم. فان كل من اكل خميرا من اليوم الاول الى اليوم السابع تقطع تلك النفس من اسرائيل"));
arasuperobject.biblemap.put("b2_12_16", new arasuperobject(2,12,16,"ويكون لكم في اليوم الاول محفل مقدس وفي اليوم السابع محفل مقدس. لا يعمل فيهما عمل ما الا ما تاكله كل نفس فذلك وحده يعمل منكم"));
arasuperobject.biblemap.put("b2_12_17", new arasuperobject(2,12,17,"وتحفظون الفطير لاني في هذا اليوم عينه اخرجت اجنادكم من ارض مصر. فتحفظون هذا اليوم في اجيالكم فريضة ابدية"));
arasuperobject.biblemap.put("b2_12_18", new arasuperobject(2,12,18,"في الشهر الاول في اليوم الرابع عشر من الشهر مساء تاكلون فطيرا الى اليوم الحادي والعشرين من الشهر مساء"));
arasuperobject.biblemap.put("b2_12_19", new arasuperobject(2,12,19,"سبعة ايام لا يوجد خمير في بيوتكم. فان كل من اكل مختمرا تقطع تلك النفس من جماعة اسرائيل الغريب مع مولود الارض"));
arasuperobject.biblemap.put("b2_12_20", new arasuperobject(2,12,20,"لا تأكلوا شيئا مختمرا في جميع مساكنكم تأكلون فطيرا"));
arasuperobject.biblemap.put("b2_12_21", new arasuperobject(2,12,21,"فدعا موسى جميع شيوخ اسرائيل وقال لهم اسحبوا وخذوا لكم غنما بحسب عشائركم واذبحوا الفصح"));
arasuperobject.biblemap.put("b2_12_22", new arasuperobject(2,12,22,"وخذوا باقة زوفا واغمسوها في الدم الذي في الطست ومسّوا العتبة العليا والقائمتين بالدم الذي في الطست. وانتم لا يخرج احد منكم من باب بيته حتى الصباح"));
arasuperobject.biblemap.put("b2_12_23", new arasuperobject(2,12,23,"فان الرب يجتاز ليضرب المصريين. فحين يرى الدم على العتبة العليا والقائمتين يعبر الرب عن الباب ولا يدع المهلك يدخل بيوتكم ليضرب"));
arasuperobject.biblemap.put("b2_12_24", new arasuperobject(2,12,24,"فتحفظون هذا الامر فريضة لك ولاولادك الى الابد"));
arasuperobject.biblemap.put("b2_12_25", new arasuperobject(2,12,25,"ويكون حين تدخلون الارض التي يعطيكم الرب كما تكلم انكم تحفظون هذه الخدمة"));
