arasuperobject.biblemap.put("b2_12_27", new arasuperobject(2,12,27,"انكم تقولون هي ذبيحة فصح للرب الذي عبر عن بيوت بني اسرائيل في مصر لما ضرب المصريين وخلّص بيوتنا. فخرّ الشعب وسجدوا"));
arasuperobject.biblemap.put("b2_12_28", new arasuperobject(2,12,28,"ومضى بنو اسرائيل وفعلوا كما امر الرب موسى وهرون. هكذا فعلوا"));
arasuperobject.biblemap.put("b2_12_29", new arasuperobject(2,12,29,"فحدث في نصف الليل ان الرب ضرب كل بكر في ارض مصر من بكر فرعون الجالس على كرسيه الى بكر الاسير الذي في السجن وكل بكر بهيمة"));
arasuperobject.biblemap.put("b2_12_30", new arasuperobject(2,12,30,"فقام فرعون ليلا هو وكل عبيده وجميع المصريين. وكان صراخ عظيم في مصر. لانه لم يكن بيت ليس فيه ميت"));
arasuperobject.biblemap.put("b2_12_31", new arasuperobject(2,12,31,"فدعا موسى وهرون ليلا وقال قوموا اخرجوا من بين شعبي انتما وبنو اسرائيل جميعا. واذهبوا اعبدوا الرب كما تكلمتم"));
arasuperobject.biblemap.put("b2_12_32", new arasuperobject(2,12,32,"خذوا غنمكم ايضا وبقركم كما تكلمتم واذهبوا. وباركوني ايضا"));
arasuperobject.biblemap.put("b2_12_33", new arasuperobject(2,12,33,"والحّ المصريون على الشعب ليطلقوهم عاجلا من الارض. لانهم قالوا جميعنا اموات"));
arasuperobject.biblemap.put("b2_12_34", new arasuperobject(2,12,34,"فحمل الشعب عجينهم قبل ان يختمر ومعاجنهم مصرورة في ثيابهم على اكتافهم"));
arasuperobject.biblemap.put("b2_12_35", new arasuperobject(2,12,35,"وفعل بنو اسرائيل بحسب قول موسى. طلبوا من المصريين امتعة فضة وامتعة ذهب وثيابا"));
arasuperobject.biblemap.put("b2_12_36", new arasuperobject(2,12,36,"واعطى الرب نعمة للشعب في عيون المصريين حتى اعاروهم. فسلبوا المصريين"));
arasuperobject.biblemap.put("b2_12_37", new arasuperobject(2,12,37,"فارتحل بنو اسرائيل من رعمسيس الى سكوت نحو ست مئة الف ماش من الرجال عدا الاولاد"));
arasuperobject.biblemap.put("b2_12_38", new arasuperobject(2,12,38,"وصعد معهم لفيف كثير ايضا مع غنم وبقر مواش وافرة جدا"));
arasuperobject.biblemap.put("b2_12_39", new arasuperobject(2,12,39,"وخبزوا العجين الذي اخرجوه من مصر خبز ملة فطيرا اذ كان لم يختمر. لانهم طردوا من مصر ولم يقدروا ان يتأخروا. فلم يصنعوا لانفسهم زادا"));
arasuperobject.biblemap.put("b2_12_40", new arasuperobject(2,12,40,"واما اقامة بني اسرائيل التي اقاموها في مصر فكانت اربع مئة وثلاثين سنة"));
arasuperobject.biblemap.put("b2_12_41", new arasuperobject(2,12,41,"وكان عند نهاية اربع مئة وثلاثين سنة في ذلك اليوم عينه ان جميع اجناد الرب خرجت من ارض مصر"));
arasuperobject.biblemap.put("b2_12_42", new arasuperobject(2,12,42,"هي ليلة تحفظ للرب لاخراجه اياهم من ارض مصر. هذه الليلة هي للرب. تحفظ من جميع بني اسرائيل في اجيالهم"));
arasuperobject.biblemap.put("b2_12_43", new arasuperobject(2,12,43,"وقال الرب لموسى وهرون هذه فريضة الفصح. كل ابن غريب لا يأكل منه"));
arasuperobject.biblemap.put("b2_12_44", new arasuperobject(2,12,44,"ولكن كل عبد رجل مبتاع بفضة تختنه ثم يأكل منه"));
arasuperobject.biblemap.put("b2_12_45", new arasuperobject(2,12,45,"النزيل والاجير لا ياكلان منه"));
arasuperobject.biblemap.put("b2_12_46", new arasuperobject(2,12,46,"في بيت واحد يؤكل. لا تخرج من اللحم من البيت الى خارج. وعظما لا تكسروا منه"));
arasuperobject.biblemap.put("b2_12_47", new arasuperobject(2,12,47,"كل جماعة اسرائيل يصنعونه"));
arasuperobject.biblemap.put("b2_12_48", new arasuperobject(2,12,48,"واذا نزل عندك نزيل وصنع فصحا للرب فليختن منه كل ذكر ثم يتقدم ليصنعه. فيكون كمولود الارض. واما كل اغلف فلا ياكل منه"));
arasuperobject.biblemap.put("b2_12_49", new arasuperobject(2,12,49,"تكون شريعة واحدة لمولود الارض وللنزيل النازل بينكم"));
arasuperobject.biblemap.put("b2_12_50", new arasuperobject(2,12,50,"ففعل جميع بني اسرائيل كما امر الرب موسى وهرون. هكذا فعلوا"));
arasuperobject.biblemap.put("b2_12_51", new arasuperobject(2,12,51,"وكان في ذلك اليوم عينه ان الرب اخرج بني اسرائيل من ارض مصر بحسب اجنادهم"));
arasuperobject.biblemap.put("b2_13_1", new arasuperobject(2,13,1,"وكلم الرب موسى قائلا"));
arasuperobject.biblemap.put("b2_13_2", new arasuperobject(2,13,2,"قدس لي كل بكر كل فاتح رحم من بني اسرائيل من الناس ومن البهائم. انه لي"));
arasuperobject.biblemap.put("b2_13_3", new arasuperobject(2,13,3,"وقال موسى للشعب اذكروا هذا اليوم الذي فيه خرجتم من مصر من بيت العبودية. فانه بيد قوية اخرجكم الرب من هنا. ولا يؤكل خمير"));
arasuperobject.biblemap.put("b2_13_4", new arasuperobject(2,13,4,"اليوم انتم خارجون في شهر ابيب"));
arasuperobject.biblemap.put("b2_13_5", new arasuperobject(2,13,5,"ويكون متى ادخلك الرب ارض الكنعانيين والحثّيين والاموريين والحوّيين واليبوسيين التي حلف لآبائك ان يعطيك ارضا تفيض لبنا وعسلا انك تصنع هذه الخدمة في هذا الشهر"));
arasuperobject.biblemap.put("b2_13_6", new arasuperobject(2,13,6,"سبعة ايام تأكل فطيرا وفي اليوم السابع عيد للرب"));
arasuperobject.biblemap.put("b2_13_7", new arasuperobject(2,13,7,"فطيرا يؤكل السبعة الايام ولا يرى عندك مختمر ولا يرى عندك خمير في جميع تخومك"));
arasuperobject.biblemap.put("b2_13_8", new arasuperobject(2,13,8,"وتخبر ابنك في ذلك اليوم قائلا من اجل ما صنع اليّ الرب حين اخرجني من مصر"));
arasuperobject.biblemap.put("b2_13_9", new arasuperobject(2,13,9,"ويكون لك علامة على يدك وتذكارا بين عينيك لكي تكون شريعة الرب في فمك. لانه بيد قوية اخرجك الرب من مصر"));
arasuperobject.biblemap.put("b2_13_10", new arasuperobject(2,13,10,"فتحفظ هذه الفريضة في وقتها من سنة الى سنة"));
arasuperobject.biblemap.put("b2_13_11", new arasuperobject(2,13,11,"ويكون متى ادخلك الرب ارض الكنعانيين كما حلف لك ولآبائك واعطاك اياها"));
arasuperobject.biblemap.put("b2_13_12", new arasuperobject(2,13,12,"انك تقدم للرب كل فاتح رحم وكل بكر من نتاج البهائم التي تكون لك. الذكور للرب"));
arasuperobject.biblemap.put("b2_13_13", new arasuperobject(2,13,13,"ولكن كل بكر حمار تفديه بشاة. وان لم تفده فتكسر عنقه. وكل بكر انسان من اولادك تفديه"));
arasuperobject.biblemap.put("b2_13_14", new arasuperobject(2,13,14,"ويكون متى سألك ابنك غدا قائلا ما هذا تقول له بيد قوية اخرجنا الرب من مصر من بيت العبودية"));
arasuperobject.biblemap.put("b2_13_15", new arasuperobject(2,13,15,"وكان لما تقسى فرعون عن اطلاقنا ان الرب قتل كل بكر في ارض مصر من بكر الناس الى بكر البهائم. لذلك انا اذبح للرب الذكور من كل فاتح رحم وافدي كل بكر من اولادي"));
arasuperobject.biblemap.put("b2_13_16", new arasuperobject(2,13,16,"فيكون علامة على يدك وعصابة بين عينيك. لانه بيد قوية اخرجنا الرب من مصر"));
arasuperobject.biblemap.put("b2_13_17", new arasuperobject(2,13,17,"وكان لما اطلق فرعون الشعب ان الله لم يهدهم في طريق ارض الفلسطينيين مع انها قريبة. لان الله قال لئلا يندم الشعب اذا رأوا حربا ويرجعوا الى مصر"));
arasuperobject.biblemap.put("b2_13_18", new arasuperobject(2,13,18,"فادار الله الشعب في طريق برية بحر سوف. وصعد بنو اسرائيل متجهزين من ارض مصر"));
arasuperobject.biblemap.put("b2_13_19", new arasuperobject(2,13,19,"واخذ موسى عظام يوسف معه. لانه كان قد استحلف بني اسرائيل بحلف قائلا ان الله سيفتقدكم فتصعدون عظامي من هنا معكم"));
arasuperobject.biblemap.put("b2_13_20", new arasuperobject(2,13,20,"وارتحلوا من سكوت ونزلوا في إيثام في طرف البرية"));
arasuperobject.biblemap.put("b2_13_21", new arasuperobject(2,13,21,"وكان الرب يسير امامهم نهارا في عمود سحاب ليهديهم في الطريق وليلا في عمود نار ليضيء لهم. لكي يمشوا نهارا وليلا"));
arasuperobject.biblemap.put("b2_13_22", new arasuperobject(2,13,22,"لم يبرح عمود السحاب نهارا وعمود النار ليلا من امام الشعب"));
arasuperobject.biblemap.put("b2_14_1", new arasuperobject(2,14,1,"وكلم الرب موسى قائلا"));
arasuperobject.biblemap.put("b2_14_2", new arasuperobject(2,14,2,"كلم بني اسرائيل ان يرجعوا وينزلوا امام فم الحيروث بين مجدل والبحر امام بعل صفون. مقابله تنزلون عند البحر"));
arasuperobject.biblemap.put("b2_14_3", new arasuperobject(2,14,3,"فيقول فرعون عن بني اسرائيل هم مرتبكون في الارض. قد استغلق عليهم القفر"));
arasuperobject.biblemap.put("b2_14_4", new arasuperobject(2,14,4,"واشدّد قلب فرعون حتى يسعى وراءهم. فاتمجد بفرعون وبجميع جيشه. ويعرف المصريون اني انا الرب. ففعلوا هكذا"));
arasuperobject.biblemap.put("b2_14_5", new arasuperobject(2,14,5,"فلما أخبر ملك مصر ان الشعب قد هرب تغيّر قلب فرعون وعبيده على الشعب. فقالوا ماذا فعلنا حتى اطلقنا اسرائيل من خدمتنا"));
arasuperobject.biblemap.put("b2_14_6", new arasuperobject(2,14,6,"فشدّ مركبته واخذ قومه معه"));
arasuperobject.biblemap.put("b2_14_7", new arasuperobject(2,14,7,"واخذ ست مئة مركبة منتخبة وسائر مركبات مصر وجنودا مركبيّة على جميعها"));
arasuperobject.biblemap.put("b2_14_8", new arasuperobject(2,14,8,"وشدّد الرب قلب فرعون ملك مصر حتى سعى وراء بني اسرائيل وبنو اسرائيل خارجون بيد رفيعة"));
arasuperobject.biblemap.put("b2_14_9", new arasuperobject(2,14,9,"فسعى المصريون وراءهم وادركوهم. جميع خيل مركبات فرعون وفرسانه وجيشه وهم نازلون عند البحر عند فم الحيروث امام بعل صفون"));
arasuperobject.biblemap.put("b2_14_10", new arasuperobject(2,14,10,"فلما اقترب فرعون رفع بنو اسرائيل عيونهم واذ المصريون راحلون وراءهم. ففزعوا جدا وصرخ بنو اسرائيل الى الرب"));
arasuperobject.biblemap.put("b2_14_11", new arasuperobject(2,14,11,"وقالوا لموسى هل لانه ليست قبور في مصر اخذتنا لنموت في البرية. ماذا صنعت بنا حتى اخرجتنا من مصر"));
arasuperobject.biblemap.put("b2_14_12", new arasuperobject(2,14,12,"أليس هذا هو الكلام الذي كلمناك به في مصر قائلين كف عنا فنخدم المصريين. لانه خير لنا ان نخدم المصريين من ان نموت في البرية"));
arasuperobject.biblemap.put("b2_14_13", new arasuperobject(2,14,13,"فقال موسى للشعب لا تخافوا. قفوا وانظروا خلاص الرب الذي يصنعه لكم اليوم. فانه كما رأيتم المصريين اليوم لا تعودون ترونهم ايضا الى الابد"));
arasuperobject.biblemap.put("b2_14_14", new arasuperobject(2,14,14,"الرب يقاتل عنكم وانتم تصمتون"));
arasuperobject.biblemap.put("b2_14_15", new arasuperobject(2,14,15,"فقال الرب لموسى مالك تصرخ اليّ. قل لبني اسرائيل ان يرحلوا"));
arasuperobject.biblemap.put("b2_14_16", new arasuperobject(2,14,16,"وارفع انت عصاك ومدّ يدك على البحر وشقّه. فيدخل بنو اسرائيل في وسط البحر على اليابسة"));
arasuperobject.biblemap.put("b2_14_17", new arasuperobject(2,14,17,"وها انا اشدد قلوب المصريين حتى يدخلوا وراءهم. فاتمجد بفرعون وكل جيشه بمركباته وفرسانه"));
arasuperobject.biblemap.put("b2_14_18", new arasuperobject(2,14,18,"فيعرف المصريون اني انا الرب حين اتمجد بفرعون ومركباته وفرسانه"));
arasuperobject.biblemap.put("b2_14_19", new arasuperobject(2,14,19,"فانتقل ملاك الله السائر امام عسكر اسرائيل وسار وراءهم. وانتقل عمود السحاب من امامهم ووقف وراءهم"));
arasuperobject.biblemap.put("b2_14_20", new arasuperobject(2,14,20,"فدخل بين عسكر المصريين وعسكر اسرائيل وصار السحاب والظلام واضاء الليل. فلم يقترب هذا الى ذاك كل الليل"));
arasuperobject.biblemap.put("b2_14_21", new arasuperobject(2,14,21,"ومدّ موسى يده على البحر. فأجرى الرب البحر بريح شرقية شديدة كل الليل وجعل البحر يابسة وانشقّ الماء"));
arasuperobject.biblemap.put("b2_14_22", new arasuperobject(2,14,22,"فدخل بنو اسرائيل في وسط البحر على اليابسة والماء سور لهم عن يمينهم وعن يسارهم"));
arasuperobject.biblemap.put("b2_14_23", new arasuperobject(2,14,23,"وتبعهم المصريون ودخلوا وراءهم. جميع خيل فرعون ومركباته وفرسانه الى وسط البحر"));
arasuperobject.biblemap.put("b2_14_24", new arasuperobject(2,14,24,"وكان في هزيع الصبح ان الرب اشرف على عسكر المصريين في عمود النار والسحاب وأزعج عسكر المصريين"));
arasuperobject.biblemap.put("b2_14_25", new arasuperobject(2,14,25,"وخلع بكر مركباتهم حتى ساقوها بثقلة. فقال المصريون نهرب من اسرائيل. لان الرب يقاتل المصريين عنهم"));
arasuperobject.biblemap.put("b2_14_26", new arasuperobject(2,14,26,"فقال الرب لموسى مدّ يدك على البحر ليرجع الماء على المصريين على مركباتهم وفرسانهم"));
arasuperobject.biblemap.put("b2_14_27", new arasuperobject(2,14,27,"فمدّ موسى يده على البحر فرجع البحر عند اقبال الصبح الى حاله الدائمة والمصريون هاربون الى لقائه. فدفع الرب المصريين في وسط البحر"));
arasuperobject.biblemap.put("b2_14_28", new arasuperobject(2,14,28,"فرجع الماء وغطى مركبات وفرسان جميع جيش فرعون الذي دخل ورائهم في البحر. لم يبق منهم ولا واحد"));
arasuperobject.biblemap.put("b2_14_29", new arasuperobject(2,14,29,"واما بنو اسرائيل فمشوا على اليابسة في وسط البحر والماء سور لهم عن يمينهم وعن يسارهم"));
arasuperobject.biblemap.put("b2_14_30", new arasuperobject(2,14,30,"فخلّص الرب في ذلك اليوم اسرائيل من يد المصريين. ونظر اسرائيل المصريين امواتا على شاطئ البحر"));
arasuperobject.biblemap.put("b2_14_31", new arasuperobject(2,14,31,"ورأى اسرائيل الفعل العظيم الذي صنعه الرب بالمصريين. فخاف الشعب الرب وآمنوا بالرب وبعبده موسى"));
arasuperobject.biblemap.put("b2_15_1", new arasuperobject(2,15,1,"حينئذ رنم موسى وبنو اسرائيل هذه التسبيحة للرب وقالوا. ارنم للرب فانه قد تعظم. الفرس وراكبه طرحهما في البحر"));
arasuperobject.biblemap.put("b2_15_2", new arasuperobject(2,15,2,"الرب قوّتي ونشيدي. وقد صار خلاصي. هذا الهي فامجّده. اله ابي فارفعه"));
arasuperobject.biblemap.put("b2_15_3", new arasuperobject(2,15,3,"الرب رجل الحرب. الرب اسمه"));
arasuperobject.biblemap.put("b2_15_4", new arasuperobject(2,15,4,"مركبات فرعون وجيشه القاهما في البحر. فغرق افضل جنوده المركبيّة في بحر سوف"));
arasuperobject.biblemap.put("b2_15_5", new arasuperobject(2,15,5,"تغطيهم اللجج. قد هبطوا في الاعماق كحجر"));
arasuperobject.biblemap.put("b2_15_6", new arasuperobject(2,15,6,"يمينك يا رب معتزّة بالقدرة. يمينك يا رب تحطم العدو"));
arasuperobject.biblemap.put("b2_15_7", new arasuperobject(2,15,7,"وبكثرة عظمتك تهدم مقاوميك. ترسل سخطك فياكلهم كالقش"));
arasuperobject.biblemap.put("b2_15_8", new arasuperobject(2,15,8,"وبريح انفك تراكمت المياه. انتصبت المجاري كرابية. تجمّدت اللجج في قلب البحر"));
arasuperobject.biblemap.put("b2_15_9", new arasuperobject(2,15,9,"قال العدو اتبع ادرك اقسم غنيمة. تمتلئ منهم نفسي. اجردّ سيفي. تفنيهم يدي"));
arasuperobject.biblemap.put("b2_15_10", new arasuperobject(2,15,10,"نفخت بريحك فغطاهم البحر. غاصوا كالرصاص في مياه غامرة"));
arasuperobject.biblemap.put("b2_15_11", new arasuperobject(2,15,11,"من مثلك بين الآلهة يا رب. من مثلك معتزّا في القداسة. مخوفا بالتسابيح. صانعا عجائب"));
arasuperobject.biblemap.put("b2_15_12", new arasuperobject(2,15,12,"تمد يمينك فتبتلعهم الارض"));
arasuperobject.biblemap.put("b2_15_13", new arasuperobject(2,15,13,"ترشد برأفتك الشعب الذي فديته تهديه بقوتك الى مسكن قدسك"));
arasuperobject.biblemap.put("b2_15_14", new arasuperobject(2,15,14,"يسمع الشعوب فيرتعدون. تاخذ الرعدة سكان فلسطين"));
arasuperobject.biblemap.put("b2_15_15", new arasuperobject(2,15,15,"حينئذ يندهش امراء ادوم. اقوياء موآب تأخذهم الرجفة. يذوب جميع سكان كنعان"));
arasuperobject.biblemap.put("b2_15_16", new arasuperobject(2,15,16,"تقع عليهم الهيبة والرّعب. بعظمة ذراعك يصمتون كالحجر. حتى يعبر شعبك يا رب. حتى يعبر الشعب الذي اقتنيته"));
arasuperobject.biblemap.put("b2_15_17", new arasuperobject(2,15,17,"تجيء بهم وتغرسهم في جبل ميراثك المكان الذي صنعته يا رب لسكنك. المقدس الذي هيّأته يداك يا رب"));
arasuperobject.biblemap.put("b2_15_18", new arasuperobject(2,15,18,"الرب يملك الى الدهر والابد"));
arasuperobject.biblemap.put("b2_15_19", new arasuperobject(2,15,19,"فان خيل فرعون دخلت بمركباته وفرسانه الى البحر. وردّ الرب عليهم ماء البحر. واما بنو اسرائيل فمشوا على اليابسة في وسط البحر"));
arasuperobject.biblemap.put("b2_15_20", new arasuperobject(2,15,20,"فاخذت مريم النبية اخت هرون الدف بيدها. وخرجت جميع النساء وراءها بدفوف ورقص"));
arasuperobject.biblemap.put("b2_15_21", new arasuperobject(2,15,21,"واجابتهم مريم رنموا للرب فانه قد تعظم. الفرس وراكبه طرحهما في البحر"));
arasuperobject.biblemap.put("b2_15_22", new arasuperobject(2,15,22,"ثم ارتحل موسى باسرائيل من بحر سوف وخرجوا الى برية شور. فساروا ثلاثة ايام في البرية ولم يجدوا ماء"));
arasuperobject.biblemap.put("b2_15_23", new arasuperobject(2,15,23,"فجاءوا الى مارّة. ولم يقدروا ان يشربوا ماء من مارّة لانه مرّ. لذلك دعي اسمها مارّة"));
arasuperobject.biblemap.put("b2_15_24", new arasuperobject(2,15,24,"فتذمر الشعب على موسى قائلين ماذا نشرب"));
arasuperobject.biblemap.put("b2_15_25", new arasuperobject(2,15,25,"فصرخ الى الرب. فاراه الرب شجرة فطرحها في الماء فصار الماء عذبا. هناك وضع له فريضة وحكما وهناك امتحنه"));
arasuperobject.biblemap.put("b2_15_26", new arasuperobject(2,15,26,"فقال ان كنت تسمع لصوت الرب الهك وتصنع الحق في عينيه وتصغي الى وصاياه وتحفظ جميع فرائضه فمرضا ما مما وضعته على المصريين لا اضع عليك. فاني انا الرب شافيك"));
arasuperobject.biblemap.put("b2_15_27", new arasuperobject(2,15,27,"ثم جاءوا الى ايليم وهناك اثنتا عشرة عين ماء وسبعون نخلة. فنزلوا هناك عند الماء"));
arasuperobject.biblemap.put("b2_16_1", new arasuperobject(2,16,1,"ثم ارتحلوا من ايليم واتى كل جماعة بني اسرائيل الى برية سين التي بين ايليم وسيناء في اليوم الخامس عشر من الشهر الثاني بعد خروجهم من ارض مصر"));
arasuperobject.biblemap.put("b2_16_2", new arasuperobject(2,16,2,"فتذمر كل جماعة بني اسرائيل على موسى وهرون في البرية"));
arasuperobject.biblemap.put("b2_16_3", new arasuperobject(2,16,3,"وقال لهما بنو اسرائيل ليتنا متنا بيد الرب في ارض مصر اذ كنا جالسين عند قدور اللحم نأكل خبزا للشبع. فانكما اخرجتمانا الى هذا القفر لكي تميتا كل هذا الجمهور بالجوع"));
arasuperobject.biblemap.put("b2_16_4", new arasuperobject(2,16,4,"فقال الرب لموسى ها انا امطر لكم خبزا من السماء. فيخرج الشعب ويلتقطون حاجة اليوم بيومها. لكي امتحنهم أيسلكون في ناموسي ام لا"));
arasuperobject.biblemap.put("b2_16_5", new arasuperobject(2,16,5,"ويكون في اليوم السادس انهم يهيئون ما يجيئون به فيكون ضعف ما يلتقطونه يوما فيوما"));
arasuperobject.biblemap.put("b2_16_6", new arasuperobject(2,16,6,"فقال موسى وهرون لجميع بني اسرائيل في المساء تعلمون ان الرب اخرجكم من ارض مصر"));
arasuperobject.biblemap.put("b2_16_7", new arasuperobject(2,16,7,"وفي الصباح ترون مجد الرب لاستماعه تذمّركم على الرب. واما نحن فماذا حتى تتذمروا علينا"));
arasuperobject.biblemap.put("b2_16_8", new arasuperobject(2,16,8,"وقال موسى. ذلك بان الرب يعطيكم في المساء لحما لتاكلوا وفي الصباح خبزا لتشبعوا لاستماع الرب تذمّركم الذي تتذمرون عليه. واما نحن فماذا. ليس علينا تذمّركم بل على الرب"));
arasuperobject.biblemap.put("b2_16_9", new arasuperobject(2,16,9,"وقال موسى لهرون قل لكل جماعة بني اسرائيل اقتربوا الى امام الرب لانه قد سمع تذمّركم"));
arasuperobject.biblemap.put("b2_16_10", new arasuperobject(2,16,10,"فحدث اذ كان هرون يكلم كل جماعة بني اسرائيل انهم التفتوا نحو البرية. واذا مجد الرب قد ظهر في السحاب"));
arasuperobject.biblemap.put("b2_16_11", new arasuperobject(2,16,11,"فكلم الرب موسى قائلا"));
arasuperobject.biblemap.put("b2_16_12", new arasuperobject(2,16,12,"سمعت تذمر بني اسرائيل. كلمهم قائلا في العشية تاكلون لحما وفي الصباح تشبعون خبزا. وتعلمون اني انا الرب الهكم"));
arasuperobject.biblemap.put("b2_16_13", new arasuperobject(2,16,13,"فكان في المساء ان السلوى صعدت وغطت المحلّة. وفي الصباح كان سقيط الندى حوالي المحلّة"));
arasuperobject.biblemap.put("b2_16_14", new arasuperobject(2,16,14,"ولما ارتفع سقيط الندى اذا على وجه البرية شيء دقيق مثل قشور. دقيق كالجليد على الارض"));
arasuperobject.biblemap.put("b2_16_15", new arasuperobject(2,16,15,"فلما رأى بنو اسرائيل قالوا بعضهم لبعض من هو. لانهم لم يعرفوا ما هو. فقال لهم موسى هو الخبز الذي اعطاكم الرب لتاكلوا"));
arasuperobject.biblemap.put("b2_16_16", new arasuperobject(2,16,16,"هذا هو الشيء الذي امر به الرب التقطوا منه كل واحد على حسب اكله عمرا للراس على عدد نفوسكم تاخذون كل واحد للذين في خيمته"));
arasuperobject.biblemap.put("b2_16_17", new arasuperobject(2,16,17,"ففعل بنو اسرائيل هكذا والتقطوا بين مكثّر ومقلّل"));
arasuperobject.biblemap.put("b2_16_18", new arasuperobject(2,16,18,"ولما كالوا بالعمر لم يفضل المكثر والمقلل لم ينقص. كانوا قد التقطوا كل واحد على حسب اكله"));
arasuperobject.biblemap.put("b2_16_19", new arasuperobject(2,16,19,"وقال لهم موسى لا يبقي احد منه الى الصباح"));
arasuperobject.biblemap.put("b2_16_20", new arasuperobject(2,16,20,"لكنهم لم يسمعوا لموسى بل ابقى منه اناس الى الصباح. فتولّد فيه دود وانتن. فسخط عليهم موسى"));
arasuperobject.biblemap.put("b2_16_21", new arasuperobject(2,16,21,"وكانوا يلتقطونه صباحا فصباحا كل واحد على حسب اكله. واذا حميت الشمس كان يذوب"));
arasuperobject.biblemap.put("b2_16_22", new arasuperobject(2,16,22,"ثم كان في اليوم السادس انهم التقطوا خبزا مضاعفا عمرين للواحد. فجاء كل رؤساء الجماعة واخبروا موسى"));
arasuperobject.biblemap.put("b2_16_23", new arasuperobject(2,16,23,"فقال لهم هذا ما قال الرب غدا عطلة سبت مقدس للرب. اخبزوا ما تخبزون واطبخوا ما تطبخون. وكل ما فضل ضعوه عندكم ليحفظ الى الغد"));
arasuperobject.biblemap.put("b2_16_24", new arasuperobject(2,16,24,"فوضعوه الى الغد كما امر موسى. فلم ينتن ولا صار فيه دود"));
arasuperobject.biblemap.put("b2_16_25", new arasuperobject(2,16,25,"فقال موسى كلوه اليوم لان للرب اليوم سبتا. اليوم لا تجدونه في الحقل"));
arasuperobject.biblemap.put("b2_16_26", new arasuperobject(2,16,26,"ستة ايام تلتقطونه. واما اليوم السابع ففيه سبت. لا يوجد فيه"));
arasuperobject.biblemap.put("b2_16_27", new arasuperobject(2,16,27,"وحدث في اليوم السابع ان بعض الشعب خرجوا ليلتقطوا فلم يجدوا"));
arasuperobject.biblemap.put("b2_16_28", new arasuperobject(2,16,28,"فقال الرب لموسى الى متى تأبون ان تحفظوا وصاياي وشرائعي"));
arasuperobject.biblemap.put("b2_16_29", new arasuperobject(2,16,29,"انظروا. ان الرب اعطاكم السبت لذلك هو يعطيكم في اليوم السادس خبز يومين. اجلسوا كل واحد في مكانه. لا يخرج احد من مكانه في اليوم السابع"));
arasuperobject.biblemap.put("b2_16_30", new arasuperobject(2,16,30,"فاستراح الشعب في اليوم السابع"));
arasuperobject.biblemap.put("b2_16_31", new arasuperobject(2,16,31,"ودعا بيت اسرائيل اسمه منّا. وهو كبزر الكزبرة ابيض وطعمه كرقاق بعسل"));
arasuperobject.biblemap.put("b2_16_32", new arasuperobject(2,16,32,"وقال موسى هذا هو الشيء الذي امر به الرب. ملء العمر منه يكون للحفظ في اجيالكم. لكي يروا الخبز الذي اطعمتكم في البرية حين اخرجتكم من ارض مصر"));
arasuperobject.biblemap.put("b2_16_33", new arasuperobject(2,16,33,"وقال موسى لهرون خذ قسطا واحدا واجعل فيه ملء العمر منّا وضعه امام الرب للحفظ في اجيالكم"));
arasuperobject.biblemap.put("b2_16_34", new arasuperobject(2,16,34,"كما امر الرب موسى وضعه هرون امام الشهادة للحفظ"));
arasuperobject.biblemap.put("b2_16_35", new arasuperobject(2,16,35,"واكل بنو اسرائيل المنّ اربعين سنة حتى جاءوا الى ارض عامرة. اكلوا المنّ حتى جاءوا الى طرف ارض كنعان"));
arasuperobject.biblemap.put("b2_16_36", new arasuperobject(2,16,36,"واما العمر فهو عشر الإيفة"));
arasuperobject.biblemap.put("b2_17_1", new arasuperobject(2,17,1,"ثم ارتحل كل جماعة بني اسرائيل من برية سين بحسب مراحلهم على موجب امر الرب ونزلوا في رفيديم. ولم يكن ماء ليشرب الشعب"));
arasuperobject.biblemap.put("b2_17_2", new arasuperobject(2,17,2,"فخاصم الشعب موسى وقالوا اعطونا ماء لنشرب. فقال لهم موسى لماذا تخاصمونني. لماذا تجربون الرب"));
arasuperobject.biblemap.put("b2_17_3", new arasuperobject(2,17,3,"وعطش هناك الشعب الى الماء. وتذمّر الشعب على موسى وقالوا لماذا اصعدتنا من مصر لتميتنا واولادنا ومواشينا بالعطش"));
arasuperobject.biblemap.put("b2_17_4", new arasuperobject(2,17,4,"فصرخ موسى الى الرب قائلا ماذا افعل بهذا الشعب. بعد قليل يرجمونني"));
arasuperobject.biblemap.put("b2_17_5", new arasuperobject(2,17,5,"فقال الرب لموسى مرّ قدّام الشعب وخذ معك من شيوخ اسرائيل. وعصاك التي ضربت بها النهر خذها في يدك واذهب"));
arasuperobject.biblemap.put("b2_17_6", new arasuperobject(2,17,6,"ها انا اقف امامك هناك على الصخرة في حوريب فتضرب الصخرة فيخرج منها ماء ليشرب الشعب. ففعل موسى هكذا امام عيون شيوخ اسرائيل"));
arasuperobject.biblemap.put("b2_17_7", new arasuperobject(2,17,7,"ودعا اسم الموضع مسّة ومريبة من اجل مخاصمة بني اسرائيل ومن اجل تجربتهم للرب قائلين أفي وسطنا الرب ام لا"));
arasuperobject.biblemap.put("b2_17_8", new arasuperobject(2,17,8,"وأتى عماليق وحارب اسرائيل في رفيديم"));
arasuperobject.biblemap.put("b2_17_9", new arasuperobject(2,17,9,"فقال موسى ليشوع انتخب لنا رجالا واخرج حارب عماليق. وغدا اقف انا على راس التلّة وعصا الله في يدي"));
arasuperobject.biblemap.put("b2_17_10", new arasuperobject(2,17,10,"ففعل يشوع كما قال له موسى ليحارب عماليق. واما موسى وهرون وحور فصعدوا على راس التلّة"));
arasuperobject.biblemap.put("b2_17_11", new arasuperobject(2,17,11,"وكان اذا رفع موسى يده ان اسرائيل يغلب واذا خفض يده ان عماليق يغلب"));
arasuperobject.biblemap.put("b2_17_12", new arasuperobject(2,17,12,"فلما صارت يدا موسى ثقيلتين اخذا حجرا ووضعاه تحته فجلس عليه. ودعم هرون وحور يديه الواحد من هنا والآخر من هناك. فكانت يداه ثابتتين الى غروب الشمس"));
arasuperobject.biblemap.put("b2_17_13", new arasuperobject(2,17,13,"فهزم يشوع عماليق وقومه بحد السيف"));
arasuperobject.biblemap.put("b2_17_14", new arasuperobject(2,17,14,"فقال الرب لموسى اكتب هذا تذكارا في الكتاب وضعه في مسامع يشوع. فاني سوف امحو ذكر عماليق من تحت السماء"));
arasuperobject.biblemap.put("b2_17_15", new arasuperobject(2,17,15,"فبنى موسى مذبحا ودعا اسمه يهوه نسّي"));
arasuperobject.biblemap.put("b2_17_16", new arasuperobject(2,17,16,"وقال ان اليد على كرسي الرب. للرب حرب مع عماليق من دور الى دور"));
arasuperobject.biblemap.put("b2_18_1", new arasuperobject(2,18,1,"فسمع يثرون كاهن مديان حمو موسى كل ما صنع الله الى موسى والى اسرائيل شعبه. ان الرب اخرج اسرائيل من مصر"));
arasuperobject.biblemap.put("b2_18_2", new arasuperobject(2,18,2,"فأخذ يثرون حمو موسى صفّورة امرأة موسى بعد صرفها"));
arasuperobject.biblemap.put("b2_18_3", new arasuperobject(2,18,3,"وابنيها اللذين اسم احدهما جرشوم لانه قال كنت نزيلا في ارض غريبة"));
arasuperobject.biblemap.put("b2_18_4", new arasuperobject(2,18,4,"واسم الآخر أليعازر لانه قال اله ابي كان عوني وانقذني من سيف فرعون"));
arasuperobject.biblemap.put("b2_18_5", new arasuperobject(2,18,5,"وأتى يثرون حمو موسى وابناه وامرأته الى موسى الى البرية حيث كان نازلا عند جبل الله"));
arasuperobject.biblemap.put("b2_18_6", new arasuperobject(2,18,6,"فقال لموسى انا حموك يثرون آت اليك وامرأتك وابناها معها"));
arasuperobject.biblemap.put("b2_18_7", new arasuperobject(2,18,7,"فخرج موسى لاستقبال حميه وسجد وقبّله. وسأل كل واحد صاحبه عن سلامته. ثم دخلا الى الخيمة"));
arasuperobject.biblemap.put("b2_18_8", new arasuperobject(2,18,8,"فقصّ موسى على حميه كل ما صنع الرب بفرعون والمصريين من اجل اسرائيل وكل المشقة التي اصابتهم في الطريق فخلصهم الرب"));
arasuperobject.biblemap.put("b2_18_9", new arasuperobject(2,18,9,"ففرح يثرون بجميع الخير الذي صنعه الى اسرائيل الرب الذي انقذه من ايدي المصريين"));
arasuperobject.biblemap.put("b2_18_10", new arasuperobject(2,18,10,"وقال يثرون مبارك الرب الذي انقذكم من ايدي المصريين ومن يد فرعون. الذي انقذ الشعب من تحت ايدي المصريين"));
arasuperobject.biblemap.put("b2_18_11", new arasuperobject(2,18,11,"الآن علمت ان الرب اعظم من جميع الآلهة لانه في الشيء الذي بغوا به كان عليهم"));
arasuperobject.biblemap.put("b2_18_12", new arasuperobject(2,18,12,"فأخذ يثرون حمو موسى محرقة وذبائح لله. وجاء هرون وجميع شيوخ اسرائيل ليأكلوا طعاما مع حمي موسى امام الله"));
arasuperobject.biblemap.put("b2_18_13", new arasuperobject(2,18,13,"وحدث في الغد ان موسى جلس ليقضي للشعب. فوقف الشعب عند موسى من الصباح الى المساء"));
arasuperobject.biblemap.put("b2_18_14", new arasuperobject(2,18,14,"فلما رأى حمو موسى كل ما هو صانع للشعب قال ما هذا الامر الذي انت صانع للشعب. ما بالك جالسا وحدك وجميع الشعب واقف عندك من الصباح الى المساء"));
arasuperobject.biblemap.put("b2_18_15", new arasuperobject(2,18,15,"فقال موسى لحميه ان الشعب يأتي اليّ ليسأل الله"));
arasuperobject.biblemap.put("b2_18_16", new arasuperobject(2,18,16,"اذا كان لهم دعوى ياتون اليّ فاقضي بين الرجل وصاحبه واعرّفهم فرائض الله وشرائعه"));
arasuperobject.biblemap.put("b2_18_17", new arasuperobject(2,18,17,"فقال حمو موسى له ليس جيدا الامر الذي انت صانع"));
arasuperobject.biblemap.put("b2_18_18", new arasuperobject(2,18,18,"انك تكلّ انت وهذا الشعب الذي معك جميعا. لان الامر اعظم منك. لا تستطيع ان تصنعه وحدك"));
arasuperobject.biblemap.put("b2_18_19", new arasuperobject(2,18,19,"الآن اسمع لصوتي فانصحك. فليكن الله معك. كن انت للشعب امام الله. وقدم انت الدعاوي الى الله"));
arasuperobject.biblemap.put("b2_18_20", new arasuperobject(2,18,20,"وعلّمهم الفرائض والشرائع وعرّفهم الطريق الذي يسلكونه والعمل الذي يعملونه"));
arasuperobject.biblemap.put("b2_18_21", new arasuperobject(2,18,21,"وانت تنظر من جميع الشعب ذوي قدرة خائفين الله امناء مبغضين الرشوة وتقيمهم عليهم رؤساء الوف ورؤساء مئات ورؤساء خماسين ورؤساء عشرات"));
arasuperobject.biblemap.put("b2_18_22", new arasuperobject(2,18,22,"فيقضون للشعب كل حين. ويكون ان كل الدعاوي الكبيرة يجيئون بها اليك وكل الدعاوي الصغيرة يقضون هم فيها. وخفف عن نفسك فهم يحملون معك"));
arasuperobject.biblemap.put("b2_18_23", new arasuperobject(2,18,23,"ان فعلت هذا الامر واوصاك الله تستطيع القيام. وكل هذا الشعب ايضا يأتي الى مكانه بالسلام"));
arasuperobject.biblemap.put("b2_18_24", new arasuperobject(2,18,24,"فسمع موسى لصوت حميه وفعل كل ما قال"));
arasuperobject.biblemap.put("b2_18_25", new arasuperobject(2,18,25,"واختار موسى ذوي قدرة من جميع اسرائيل وجعلهم رؤوسا على الشعب رؤساء الوف ورؤساء مئات ورؤساء خماسين ورؤساء عشرات"));
arasuperobject.biblemap.put("b2_18_26", new arasuperobject(2,18,26,"فكانوا يقضون للشعب كل حين. الدعاوي العسرة يجيئون بها الى موسى وكل الدعاوي الصغيرة يقضون هم فيها"));
arasuperobject.biblemap.put("b2_18_27", new arasuperobject(2,18,27,"ثم صرف موسى حماه فمضى الى ارضه"));
arasuperobject.biblemap.put("b2_19_1", new arasuperobject(2,19,1,"في الشهر الثالث بعد خروج بني اسرائيل من ارض مصر في ذلك اليوم جاءوا الى برية سيناء"));
arasuperobject.biblemap.put("b2_19_2", new arasuperobject(2,19,2,"ارتحلوا من رفيديم وجاءوا الى برية سيناء فنزلوا في البرية. هناك نزل اسرائيل مقابل الجبل"));
arasuperobject.biblemap.put("b2_19_3", new arasuperobject(2,19,3,"واما موسى فصعد الى الله. فناداه الرب من الجبل قائلا هكذا تقول لبيت يعقوب وتخبر بني اسرائيل"));
arasuperobject.biblemap.put("b2_19_4", new arasuperobject(2,19,4,"انتم رأيتم ما صنعت بالمصريين. وانا حملتكم على اجنحة النسور وجئت بكم اليّ"));
arasuperobject.biblemap.put("b2_19_5", new arasuperobject(2,19,5,"فالآن ان سمعتم لصوتي وحفظتم عهدي تكونون لي خاصة من بين جميع الشعوب. فان لي كل الارض"));
arasuperobject.biblemap.put("b2_19_6", new arasuperobject(2,19,6,"وانتم تكونون لي مملكة كهنة وامة مقدسة. هذه هي الكلمات التي تكلّم بها بني اسرائيل"));
arasuperobject.biblemap.put("b2_19_7", new arasuperobject(2,19,7,"فجاء موسى ودعا شيوخ الشعب ووضع قدامهم كل هذه الكلمات التي اوصاه بها الرب"));
arasuperobject.biblemap.put("b2_19_8", new arasuperobject(2,19,8,"فاجاب جميع الشعب معا وقالوا كل ما تكلم به الرب نفعل. فرد موسى كلام الشعب الى الرب"));
arasuperobject.biblemap.put("b2_19_9", new arasuperobject(2,19,9,"فقال الرب لموسى ها انا آت اليك في ظلام السحاب لكي يسمع الشعب حينما اتكلم معك فيؤمنوا بك ايضا الى الابد. واخبر موسى الرب بكلام الشعب"));
arasuperobject.biblemap.put("b2_19_10", new arasuperobject(2,19,10,"فقال الرب لموسى اذهب الى الشعب وقدسهم اليوم وغدا. وليغسلوا ثيابهم"));
arasuperobject.biblemap.put("b2_19_11", new arasuperobject(2,19,11,"ويكونوا مستعدين لليوم الثالث. لانه في اليوم الثالث ينزل الرب امام عيون جميع الشعب على جبل سيناء"));
arasuperobject.biblemap.put("b2_19_12", new arasuperobject(2,19,12,"وتقيم للشعب حدودا من كل ناحية قائلا احترزوا من ان تصعدوا الى الجبل او تمسوا طرفه. كل من يمسّ الجبل يقتل قتلا"));
arasuperobject.biblemap.put("b2_19_13", new arasuperobject(2,19,13,"لا تمسه يد بل يرجم رجما او يرمى رميا. بهيمة كان ام انسانا لا يعيش. اما عند صوت البوق فهم يصعدون الى الجبل"));
arasuperobject.biblemap.put("b2_19_14", new arasuperobject(2,19,14,"فانحدر موسى من الجبل الى الشعب وقدّس الشعب وغسلوا ثيابهم"));
arasuperobject.biblemap.put("b2_19_15", new arasuperobject(2,19,15,"وقال للشعب كونوا مستعدين لليوم الثالث. لا تقربوا امرأة"));
arasuperobject.biblemap.put("b2_19_16", new arasuperobject(2,19,16,"وحدث في اليوم الثالث لما كان الصباح انه صارت رعود وبروق وسحاب ثقيل على الجبل وصوت بوق شديد جدا. فارتعد كل الشعب الذي في المحلّة"));
arasuperobject.biblemap.put("b2_19_17", new arasuperobject(2,19,17,"واخرج موسى الشعب من المحلّة لملاقاة الله. فوقفوا في اسفل الجبل"));
arasuperobject.biblemap.put("b2_19_18", new arasuperobject(2,19,18,"وكان جبل سيناء كله يدخن من اجل ان الرب نزل عليه بالنار. وصعد دخانه كدخان الأتون وارتجف كل الجبل جدا"));
arasuperobject.biblemap.put("b2_19_19", new arasuperobject(2,19,19,"فكان صوت البوق يزداد اشتدادا جدا وموسى يتكلم والله يجيبه بصوت"));
arasuperobject.biblemap.put("b2_19_20", new arasuperobject(2,19,20,"ونزل الرب على جبل سيناء الى راس الجبل. ودعا الله موسى الى راس الجبل. فصعد موسى"));
arasuperobject.biblemap.put("b2_19_21", new arasuperobject(2,19,21,"فقال الرب لموسى انحدر حذّر الشعب لئلا يقتحموا الى الرب لينظروا فيسقط منهم كثيرون"));
arasuperobject.biblemap.put("b2_19_22", new arasuperobject(2,19,22,"وليتقدس ايضا الكهنة الذين يقتربون الى الرب لئلا يبطش بهم الرب"));
arasuperobject.biblemap.put("b2_19_23", new arasuperobject(2,19,23,"فقال موسى للرب لا يقدر الشعب ان يصعد الى جبل سيناء. لانك انت حذرتنا قائلا أقم حدودا للجبل وقدسه"));
arasuperobject.biblemap.put("b2_19_24", new arasuperobject(2,19,24,"فقال له الرب اذهب انحدر ثم اصعد انت وهرون معك. واما الكهنة والشعب فلا يقتحموا ليصعدوا الى الرب لئلا يبطش بهم"));
arasuperobject.biblemap.put("b2_19_25", new arasuperobject(2,19,25,"فانحدر موسى الى الشعب وقال لهم"));
arasuperobject.biblemap.put("b2_20_1", new arasuperobject(2,20,1,"ثم تكلم الله بجميع هذه الكلمات قائلا"));
arasuperobject.biblemap.put("b2_20_2", new arasuperobject(2,20,2,"انا الرب الهك الذي اخرجك من ارض مصر من بيت العبودية"));
arasuperobject.biblemap.put("b2_20_3", new arasuperobject(2,20,3,"لا يكن لك آلهة اخرى امامي"));
arasuperobject.biblemap.put("b2_20_4", new arasuperobject(2,20,4,"لا تصنع لك تمثالا منحوتا ولا صورة ما مّما في السماء من فوق وما في الارض من تحت وما في الماء من تحت الارض"));
arasuperobject.biblemap.put("b2_20_5", new arasuperobject(2,20,5,"لا تسجد لهنّ ولا تعبدهنّ. لاني انا الرب الهك اله غيور افتقد ذنوب الآباء في الابناء في الجيل الثالث والرابع من مبغضيّ"));
arasuperobject.biblemap.put("b2_20_6", new arasuperobject(2,20,6,"واصنع احسانا الى الوف من محبيّ وحافظي وصاياي"));
arasuperobject.biblemap.put("b2_20_7", new arasuperobject(2,20,7,"لا تنطق باسم الرب الهك باطلا لان الرب لا يبرئ من نطق باسمه باطلا"));
arasuperobject.biblemap.put("b2_20_8", new arasuperobject(2,20,8,"اذكر يوم السبت لتقدسه"));
arasuperobject.biblemap.put("b2_20_9", new arasuperobject(2,20,9,"ستة ايام تعمل وتصنع جميع عملك"));
arasuperobject.biblemap.put("b2_20_10", new arasuperobject(2,20,10,"واما اليوم السابع ففيه سبت للرب الهك. لا تصنع عملا ما انت وابنك وابنتك وعبدك وامتك وبهيمتك ونزيلك الذي داخل ابوابك"));
arasuperobject.biblemap.put("b2_20_11", new arasuperobject(2,20,11,"لان في ستة ايام صنع الرب السماء والارض والبحر وكل ما فيها. واستراح في اليوم السابع. لذلك بارك الرب يوم السبت وقدّسه"));
arasuperobject.biblemap.put("b2_20_12", new arasuperobject(2,20,12,"اكرم اباك وامك لكي تطول ايامك على الارض التي يعطيك الرب الهك"));
arasuperobject.biblemap.put("b2_20_13", new arasuperobject(2,20,13,"لا تقتل"));
arasuperobject.biblemap.put("b2_20_14", new arasuperobject(2,20,14,"لا تزن"));
arasuperobject.biblemap.put("b2_20_15", new arasuperobject(2,20,15,"لا تسرق"));
arasuperobject.biblemap.put("b2_20_16", new arasuperobject(2,20,16,"لا تشهد على قريبك شهادة زور"));
arasuperobject.biblemap.put("b2_20_17", new arasuperobject(2,20,17,"لا تشته بيت قريبك. لا تشته امرأة قريبك ولا عبده ولا امته ولا ثوره ولا حماره ولا شيئا مما لقريبك"));
arasuperobject.biblemap.put("b2_20_18", new arasuperobject(2,20,18,"وكان جميع الشعب يرون الرعود والبروق وصوت البوق والجبل يدخّن. ولما رأى الشعب ارتعدوا ووقفوا من بعيد"));
arasuperobject.biblemap.put("b2_20_19", new arasuperobject(2,20,19,"وقالوا لموسى تكلم انت معنا فنسمع. ولا يتكلم معنا الله لئلا نموت"));
arasuperobject.biblemap.put("b2_20_20", new arasuperobject(2,20,20,"فقال موسى للشعب لا تخافوا. لان الله انما جاء لكي يمتحنكم ولكي تكون مخافته امام وجوهكم حتى لا تخطئوا"));
arasuperobject.biblemap.put("b2_20_21", new arasuperobject(2,20,21,"فوقف الشعب من بعيد واما موسى فاقترب الى الضباب حيث كان الله"));
arasuperobject.biblemap.put("b2_20_22", new arasuperobject(2,20,22,"فقال الرب لموسى هكذا تقول لبني اسرائيل. انتم رأيتم انني من السماء تكلمت معكم"));
arasuperobject.biblemap.put("b2_20_23", new arasuperobject(2,20,23,"لا تصنعوا معي آلهة فضة ولا تصنعوا لكم آلهة ذهب"));
arasuperobject.biblemap.put("b2_20_24", new arasuperobject(2,20,24,"مذبحا من تراب تصنع لي وتذبح عليه محرقاتك وذبائح سلامتك غنمك وبقرك. في كل الاماكن التي فيها اصنع لاسمي ذكرا آتي اليك واباركك"));
arasuperobject.biblemap.put("b2_20_25", new arasuperobject(2,20,25,"وان صنعت لي مذبحا من حجارة فلا تبنه منها منحوتة. اذا رفعت عليها ازميلك تدنّسها"));
arasuperobject.biblemap.put("b2_20_26", new arasuperobject(2,20,26,"ولا تصعد بدرج الى مذبحي لكيلا تنكشف عورتك عليه"));
arasuperobject.biblemap.put("b2_21_1", new arasuperobject(2,21,1,"وهذه هي الاحكام التي تضع امامهم"));
arasuperobject.biblemap.put("b2_21_2", new arasuperobject(2,21,2,"اذا اشتريت عبدا عبرانيا فست سنين يخدم وفي السابعة يخرج حرا مجانا"));
arasuperobject.biblemap.put("b2_21_3", new arasuperobject(2,21,3,"ان دخل وحده فوحده يخرج. ان كان بعل امرأة تخرج امرأته معه"));
arasuperobject.biblemap.put("b2_21_4", new arasuperobject(2,21,4,"ان اعطاه سيده امرأة وولدت له بنين او بنات فالمرأة واولادها يكونون لسيده وهو يخرج وحده"));
arasuperobject.biblemap.put("b2_21_5", new arasuperobject(2,21,5,"ولكن ان قال العبد احب سيدي وامرأتي واولادي لا اخرج حرا"));
arasuperobject.biblemap.put("b2_21_6", new arasuperobject(2,21,6,"يقدمه سيده الى الله ويقربه الى الباب او الى القائمة ويثقب سيده اذنه بالمثقب. فيخدمه الى الابد"));
arasuperobject.biblemap.put("b2_21_7", new arasuperobject(2,21,7,"واذا باع رجل ابنته امة لا تخرج كما يخرج العبيد"));
arasuperobject.biblemap.put("b2_21_8", new arasuperobject(2,21,8,"ان قبحت في عيني سيدها الذي خطبها لنفسه يدعها تفك. وليس له سلطان ان يبيعها لقوم اجانب لغدره بها"));
arasuperobject.biblemap.put("b2_21_9", new arasuperobject(2,21,9,"وان خطبها لابنه فبحسب حق البنات يفعل لها"));
arasuperobject.biblemap.put("b2_21_10", new arasuperobject(2,21,10,"ان اتخذ لنفسه اخرى لا ينقّص طعامها وكسوتها ومعاشرتها"));
arasuperobject.biblemap.put("b2_21_11", new arasuperobject(2,21,11,"وان لم يفعل لها هذه الثلاث تخرج مجانا بلا ثمن"));
arasuperobject.biblemap.put("b2_21_12", new arasuperobject(2,21,12,"من ضرب انسانا فمات يقتل قتلا"));
arasuperobject.biblemap.put("b2_21_13", new arasuperobject(2,21,13,"ولكن الذي لم يتعمّد بل اوقع الله في يده فانا اجعل لك مكانا يهرب اليه"));
arasuperobject.biblemap.put("b2_21_14", new arasuperobject(2,21,14,"واذا بغى انسان على صاحبه ليقتله بغدر فمن عند مذبحي تاخذه للموت"));
arasuperobject.biblemap.put("b2_21_15", new arasuperobject(2,21,15,"ومن ضرب اباه او امه يقتل قتلا"));
arasuperobject.biblemap.put("b2_21_16", new arasuperobject(2,21,16,"ومن سرق انسانا وباعه او وجد في يده يقتل قتلا"));
arasuperobject.biblemap.put("b2_21_17", new arasuperobject(2,21,17,"ومن شتم اباه او امه يقتل قتلا"));
arasuperobject.biblemap.put("b2_21_18", new arasuperobject(2,21,18,"واذا تخاصم رجلان فضرب احدهما الآخر بحجر او بلكمة ولم يقتل بل سقط في الفراش"));
arasuperobject.biblemap.put("b2_21_19", new arasuperobject(2,21,19,"فان قام وتمشى خارجا على عكّازه يكون الضارب بريئا. الا انه يعوض عطلته وينفق على شفائه"));
arasuperobject.biblemap.put("b2_21_20", new arasuperobject(2,21,20,"واذا ضرب انسان عبده او امته بالعصا فمات تحت يده ينتقم منه"));
arasuperobject.biblemap.put("b2_21_21", new arasuperobject(2,21,21,"ولكن ان بقي يوما او يومين لا ينتقم منه لانه ماله"));
arasuperobject.biblemap.put("b2_21_22", new arasuperobject(2,21,22,"واذا تخاصم رجال وصدموا امرأة حبلى فسقط ولدها ولم تحصل اذيّة يغرم كما يضع عليه زوج المرأة ويدفع عن يد القضاة"));
arasuperobject.biblemap.put("b2_21_23", new arasuperobject(2,21,23,"وان حصلت اذيّة تعطي نفسا بنفس"));
arasuperobject.biblemap.put("b2_21_24", new arasuperobject(2,21,24,"وعينا بعين وسنا بسن ويدا بيد ورجلا برجل"));
arasuperobject.biblemap.put("b2_21_25", new arasuperobject(2,21,25,"وكيّا بكي وجرحا بجرح ورضّا برضّ"));
arasuperobject.biblemap.put("b2_21_26", new arasuperobject(2,21,26,"واذا ضرب انسان عين عبده او عين امته فاتلفها يطلقه حرا عوضا عن عينه"));
arasuperobject.biblemap.put("b2_21_27", new arasuperobject(2,21,27,"وان اسقط سن عبده او سنّ امته يطلقه حرا عوضا عن سنّه"));
arasuperobject.biblemap.put("b2_21_28", new arasuperobject(2,21,28,"واذا نطح ثور رجلا او امرأة فمات يرجم الثور ولا يؤكل لحمه. واما صاحب الثور فيكون بريئا"));
arasuperobject.biblemap.put("b2_21_29", new arasuperobject(2,21,29,"ولكن ان كان ثورا نطّاحا من قبل وقد أشهد على صاحبه ولم يضبطه فقتل رجلا او امرأة فالثور يرجم وصاحبه ايضا يقتل"));
arasuperobject.biblemap.put("b2_21_30", new arasuperobject(2,21,30,"ان وضعت عليه فدية يدفع فداء نفسه كل ما يوضع عليه"));
arasuperobject.biblemap.put("b2_21_31", new arasuperobject(2,21,31,"او اذا نطح ابنا او نطح ابنة فبحسب هذا الحكم يفعل به"));
arasuperobject.biblemap.put("b2_21_32", new arasuperobject(2,21,32,"ان نطح الثور عبدا او امة يعطي سيده ثلاثين شاقل فضة والثور يرجم"));
arasuperobject.biblemap.put("b2_21_33", new arasuperobject(2,21,33,"واذا فتح انسان بئرا او حفر انسان بئرا ولم يغطه فوقع فيها ثور او حمار"));
arasuperobject.biblemap.put("b2_21_34", new arasuperobject(2,21,34,"فصاحب البئر يعوض ويرد فضة لصاحبه والميت يكون له"));
arasuperobject.biblemap.put("b2_21_35", new arasuperobject(2,21,35,"واذا نطح ثور انسان ثور صاحبه فمات يبيعان الثور الحي ويقتسمان ثمنه. والميت ايضا يقتسمانه"));
arasuperobject.biblemap.put("b2_21_36", new arasuperobject(2,21,36,"لكن اذا علم انه ثور نطّاح من قبل ولم يضبطه صاحبه يعوّض عن الثور بثور والميت يكون له"));
arasuperobject.biblemap.put("b2_22_1", new arasuperobject(2,22,1,"اذا سرق انسان ثورا او شاة فذبحه او باعه يعوّض عن الثور بخمسة ثيران وعن الشاة باربعة من الغنم"));
arasuperobject.biblemap.put("b2_22_2", new arasuperobject(2,22,2,"ان وجد السارق وهو ينقب فضرب ومات فليس له دم"));
arasuperobject.biblemap.put("b2_22_3", new arasuperobject(2,22,3,"ولكن ان اشرقت عليه الشمس فله دم. انه يعوّض. ان لم يكن له يبع بسرقته"));
arasuperobject.biblemap.put("b2_22_4", new arasuperobject(2,22,4,"ان وجدت السرقة في يده حيّة ثورا كانت ام حمارا ام شاة يعوّض باثنين"));
arasuperobject.biblemap.put("b2_22_5", new arasuperobject(2,22,5,"اذا رعى انسان حقلا او كرما وسرّح مواشيه فرعت في حقل غيره فمن اجود حقله واجود كرمه يعوّض"));
arasuperobject.biblemap.put("b2_22_6", new arasuperobject(2,22,6,"اذا خرجت نار واصابت شوكا فاحترقت اكداس او زرع او حقل فالذي اوقد الوقيد يعوّض"));
arasuperobject.biblemap.put("b2_22_7", new arasuperobject(2,22,7,"اذا اعطى انسان صاحبه فضة او امتعة للحفظ فسرقت من بيت الانسان فان وجد السارق يعوّض باثنين"));
arasuperobject.biblemap.put("b2_22_8", new arasuperobject(2,22,8,"وان لم يوجد السارق يقدم صاحب البيت الى الله ليحكم هل لم يمدّ يده الى ملك صاحبه"));
arasuperobject.biblemap.put("b2_22_9", new arasuperobject(2,22,9,"في كل دعوى جناية من جهة ثور او حمار او شاة او ثوب او مفقود ما يقال ان هذا هو تقدم الى الله دعواهما. فالذي يحكم الله بذنبه يعوّض صاحبه باثنين"));
arasuperobject.biblemap.put("b2_22_10", new arasuperobject(2,22,10,"اذا اعطى انسان صاحبه حمارا او ثورا او شاة او بهيمة ما للحفظ فمات او انكسر او نهب وليس ناظر"));
arasuperobject.biblemap.put("b2_22_11", new arasuperobject(2,22,11,"فيمين الرب تكون بينهما هل لم يمدّ يده الى ملك صاحبه. فيقبل صاحبه. فلا يعوّض"));
arasuperobject.biblemap.put("b2_22_12", new arasuperobject(2,22,12,"وان سرق من عنده يعوّض صاحبه"));
arasuperobject.biblemap.put("b2_22_13", new arasuperobject(2,22,13,"ان افترس يحضره شهادة. لا يعوّض عن المفترس"));
arasuperobject.biblemap.put("b2_22_14", new arasuperobject(2,22,14,"واذا استعار انسان من صاحبه شيئا فانكسر او مات وصاحبه ليس معه يعوّض"));
arasuperobject.biblemap.put("b2_22_15", new arasuperobject(2,22,15,"وان كان صاحبه معه لا يعوّض. ان كان مستأجرا اتى باجرته"));
arasuperobject.biblemap.put("b2_22_16", new arasuperobject(2,22,16,"واذا راود رجل عذراء لم تخطب فاضطجع معها يمهرها لنفسه زوجة"));
arasuperobject.biblemap.put("b2_22_17", new arasuperobject(2,22,17,"ان ابى ابوها ان يعطيه اياها يزن له فضة كمهر العذارى"));
arasuperobject.biblemap.put("b2_22_18", new arasuperobject(2,22,18,"لا تدع ساحرة تعيش"));
arasuperobject.biblemap.put("b2_22_19", new arasuperobject(2,22,19,"كل من اضطجع من بهيمة يقتل قتلا"));
arasuperobject.biblemap.put("b2_22_20", new arasuperobject(2,22,20,"من ذبح لآلهة غير الرب وحده يهلك"));
arasuperobject.biblemap.put("b2_22_21", new arasuperobject(2,22,21,"ولا تضطهد الغريب ولا تضايقه. لانكم كنتم غرباء في ارض مصر"));
arasuperobject.biblemap.put("b2_22_22", new arasuperobject(2,22,22,"لا تسئ الى ارملة ما ولا يتيم"));
arasuperobject.biblemap.put("b2_22_23", new arasuperobject(2,22,23,"ان اسأت اليه فاني ان صرخ اليّ اسمع صراخه"));
arasuperobject.biblemap.put("b2_22_24", new arasuperobject(2,22,24,"فيحمى غضبي واقتلكم بالسيف. فتصير نساؤكم ارامل واولادكم يتامى"));
arasuperobject.biblemap.put("b2_22_25", new arasuperobject(2,22,25,"ان اقرضت فضة لشعبي الفقير الذي عندك فلا تكن له كالمرابي. لا تضعوا عليه ربا"));
arasuperobject.biblemap.put("b2_22_26", new arasuperobject(2,22,26,"ان ارتهنت ثوب صاحبك فالى غروب الشمس ترده له"));
arasuperobject.biblemap.put("b2_22_27", new arasuperobject(2,22,27,"لانه وحده غطاؤه. هو ثوبه لجلده. في ماذا ينام. فيكون اذا صرخ اليّ اني اسمع. لاني رؤوف"));
arasuperobject.biblemap.put("b2_22_28", new arasuperobject(2,22,28,"لا تسبّ الله. ولا تلعن رئيسا في شعبك"));
arasuperobject.biblemap.put("b2_22_29", new arasuperobject(2,22,29,"لا تؤخر ملء بيدرك وقطر معصرتك. وابكار بنيك تعطيني"));
arasuperobject.biblemap.put("b2_22_30", new arasuperobject(2,22,30,"كذلك تفعل ببقرك وغنمك. سبعة ايام يكون مع امه وفي اليوم الثامن تعطيني اياه"));
arasuperobject.biblemap.put("b2_22_31", new arasuperobject(2,22,31,"وتكونون لي اناس مقدّسين. ولحم فريسة في الصحراء لا تأكلوا. للكلاب تطرحونه"));
arasuperobject.biblemap.put("b2_23_1", new arasuperobject(2,23,1,"لا تقبل خبرا كاذبا. ولا تضع يدك مع المنافق لتكون شاهد ظلم"));
arasuperobject.biblemap.put("b2_23_2", new arasuperobject(2,23,2,"لا تتبع الكثيرين الى فعل الشر. ولا تجب في دعوى مائلا وراء الكثيرين للتحريف"));
arasuperobject.biblemap.put("b2_23_3", new arasuperobject(2,23,3,"ولا تحاب مع المسكين في دعواه"));
arasuperobject.biblemap.put("b2_23_4", new arasuperobject(2,23,4,"اذا صادفت ثور عدوك او حماره شاردا ترده اليه"));
arasuperobject.biblemap.put("b2_23_5", new arasuperobject(2,23,5,"اذا رأيت حمار مبغضك واقعا تحت حمله وعدلت عن حلّه فلا بد ان تحل معه"));
arasuperobject.biblemap.put("b2_23_6", new arasuperobject(2,23,6,"لا تحرّف حق فقيرك في دعواه"));
arasuperobject.biblemap.put("b2_23_7", new arasuperobject(2,23,7,"ابتعد عن كلام الكذب. ولا تقتل البريء والبار. لاني لا أبرّر المذنب"));
arasuperobject.biblemap.put("b2_23_8", new arasuperobject(2,23,8,"ولا تأخذ رشوة. لان الرشوة تعمي المبصرين وتعوّج كلام الابرار"));
arasuperobject.biblemap.put("b2_23_9", new arasuperobject(2,23,9,"ولا تضايق الغريب فانكم عارفون نفس الغريب. لانكم كنتم غرباء في ارض مصر"));
arasuperobject.biblemap.put("b2_23_10", new arasuperobject(2,23,10,"وست سنين تزرع ارضك وتجمع غلتها"));
arasuperobject.biblemap.put("b2_23_11", new arasuperobject(2,23,11,"واما في السابعة فتريحها وتتركها ليأكل فقراء شعبك. وفضلتهم تاكلها وحوش البرية. كذلك تفعل بكرمك وزيتونك"));
arasuperobject.biblemap.put("b2_23_12", new arasuperobject(2,23,12,"ستة ايام تعمل عملك. واما اليوم السابع ففيه تستريح لكي يستريح ثورك وحمارك ويتنفس ابن امتك والغريب"));
arasuperobject.biblemap.put("b2_23_13", new arasuperobject(2,23,13,"وكل ما قلت لكم احتفظوا به. ولا تذكروا اسم آلهة اخرى ولا يسمع من فمك"));
arasuperobject.biblemap.put("b2_23_14", new arasuperobject(2,23,14,"ثلاث مرات تعيّد لي في السنة"));
arasuperobject.biblemap.put("b2_23_15", new arasuperobject(2,23,15,"تحفظ عيد الفطير. تاكل فطيرا سبعة ايام كما امرتك في وقت شهر ابيب. لانه فيه خرجت من مصر. ولا يظهروا امامي فارغين"));
arasuperobject.biblemap.put("b2_23_16", new arasuperobject(2,23,16,"وعيد الحصاد ابكار غلاتك التي تزرع في الحقل. وعيد الجمع في نهاية السنة عندما تجمع غلاتك من الحقل"));
arasuperobject.biblemap.put("b2_23_17", new arasuperobject(2,23,17,"ثلاث مرات في السنة يظهر جميع ذكورك امام السيد الرب"));
arasuperobject.biblemap.put("b2_23_18", new arasuperobject(2,23,18,"لا تذبح على خمير دم ذبيحتي. ولا يبيت شحم عيدي الى الغد"));
arasuperobject.biblemap.put("b2_23_19", new arasuperobject(2,23,19,"اول ابكار ارضك تحضره الى بيت الرب الهك. لا تطبخ جديا بلبن امه"));
arasuperobject.biblemap.put("b2_23_20", new arasuperobject(2,23,20,"ها انا مرسل ملاكا امام وجهك ليحفظك في الطريق وليجيء بك الى المكان الذي اعددته"));
arasuperobject.biblemap.put("b2_23_21", new arasuperobject(2,23,21,"احترز منه واسمع لصوته ولا تتمرد عليه. لانه لا يصفح عن ذنوبكم لان اسمي فيه"));
arasuperobject.biblemap.put("b2_23_22", new arasuperobject(2,23,22,"ولكن ان سمعت لصوته وفعلت كل ما اتكلم به اعادي اعداءك واضايق مضايقيك"));
arasuperobject.biblemap.put("b2_23_23", new arasuperobject(2,23,23,"فان ملاكي يسير امامك ويجيء بك الى الاموريين والحثّيين والفرزّيين والكنعانيين والحوّيين واليبوسيين. فابيدهم"));
arasuperobject.biblemap.put("b2_23_24", new arasuperobject(2,23,24,"لا تسجد لآلهتهم ولا تعبدها ولا تعمل كاعمالهم. بل تبيدهم وتكسر انصابهم"));
arasuperobject.biblemap.put("b2_23_25", new arasuperobject(2,23,25,"وتعبدون الرب الهكم. فيبارك خبزك وماءك وأزيل المرض من بينكم"));
arasuperobject.biblemap.put("b2_23_26", new arasuperobject(2,23,26,"لا تكون مسقطة ولا عاقر في ارضك. واكمّل عدد ايامك"));
arasuperobject.biblemap.put("b2_23_27", new arasuperobject(2,23,27,"ارسل هيبتي امامك وازعج جميع الشعوب الذين تاتي عليهم واعطيك جميع اعدائك مدبرين"));
arasuperobject.biblemap.put("b2_23_28", new arasuperobject(2,23,28,"وأرسل امامك الزنابير. فتطرد الحوّيين والكنعانيين والحثّيين من امامك"));
arasuperobject.biblemap.put("b2_23_29", new arasuperobject(2,23,29,"لا اطردهم من امامك في سنة واحدة لئلا تصير الارض خربة فتكثر عليك وحوش البرية"));
arasuperobject.biblemap.put("b2_23_30", new arasuperobject(2,23,30,"قليلا قليلا اطردهم من امامك الى ان تثمر وتملك الارض"));
arasuperobject.biblemap.put("b2_23_31", new arasuperobject(2,23,31,"واجعل تخومك من بحر سوف الى بحر فلسطين ومن البرية الى النهر. فاني ادفع الى ايديكم سكان الارض فتطردهم من امامك"));
arasuperobject.biblemap.put("b2_23_32", new arasuperobject(2,23,32,"لا تقطع معهم ولا مع آلهتهم عهدا"));
arasuperobject.biblemap.put("b2_23_33", new arasuperobject(2,23,33,"لا يسكنوا في ارضك لئلا يجعلوك تخطئ اليّ. اذا عبدت آلهتهم فانه يكون لك فخا"));
arasuperobject.biblemap.put("b2_24_1", new arasuperobject(2,24,1,"وقال لموسى اصعد الى الرب انت وهرون وناداب وابيهو وسبعون من شيوخ اسرائيل. واسجدوا من بعيد"));
arasuperobject.biblemap.put("b2_24_2", new arasuperobject(2,24,2,"ويقترب موسى وحده الى الرب وهم لا يقتربون. واما الشعب فلا يصعد معه"));
arasuperobject.biblemap.put("b2_24_3", new arasuperobject(2,24,3,"فجاء موسى وحدّث الشعب بجميع اقوال الرب وجميع الاحكام. فاجاب جميع الشعب بصوت واحد وقالوا كل الاقوال التي تكلم بها الرب نفعل"));
arasuperobject.biblemap.put("b2_24_4", new arasuperobject(2,24,4,"فكتب موسى جميع اقوال الرب. وبكر في الصباح وبنى مذبحا في اسفل الجبل واثني عشر عمودا لاسباط اسرائيل الاثني عشر"));
arasuperobject.biblemap.put("b2_24_5", new arasuperobject(2,24,5,"وارسل فتيان بني اسرائيل فاصعدوا محرقات وذبحوا ذبائح سلامة للرب من الثيران"));
arasuperobject.biblemap.put("b2_24_6", new arasuperobject(2,24,6,"فأخذ موسى نصف الدم ووضعه في الطسوس. ونصف الدم رشّه على المذبح"));
arasuperobject.biblemap.put("b2_24_7", new arasuperobject(2,24,7,"واخذ كتاب العهد وقرأ في مسامع الشعب. فقالوا كل ما تكلم به الرب نفعل ونسمع له"));
arasuperobject.biblemap.put("b2_24_8", new arasuperobject(2,24,8,"واخذ موسى الدم ورشّ على الشعب وقال هوذا دم العهد الذي قطعه الرب معكم على جميع هذه الاقوال"));
arasuperobject.biblemap.put("b2_24_9", new arasuperobject(2,24,9,"ثم صعد موسى وهرون وناداب وابيهو وسبعون من شيوخ اسرائيل"));
arasuperobject.biblemap.put("b2_24_10", new arasuperobject(2,24,10,"ورأوا اله اسرائيل وتحت رجليه شبه صنعة من العقيق الازرق الشفّاف وكذات السماء في النقاوة"));
arasuperobject.biblemap.put("b2_24_11", new arasuperobject(2,24,11,"ولكنه لم يمدّ يده الى اشراف بني اسرائيل. فرأوا الله وأكلوا وشربوا"));
arasuperobject.biblemap.put("b2_24_12", new arasuperobject(2,24,12,"وقال الرب لموسى اصعد اليّ الى الجبل وكن هناك. فاعطيك لوحي الحجارة والشريعة والوصية التي كتبتها لتعليمهم"));
arasuperobject.biblemap.put("b2_24_13", new arasuperobject(2,24,13,"فقام موسى ويشوع خادمه. وصعد موسى الى جبل الله"));
arasuperobject.biblemap.put("b2_24_14", new arasuperobject(2,24,14,"واما الشيوخ فقال لهم اجلسوا لنا ههنا حتى نرجع اليكم. وهوذا هرون وحور معكم. فمن كان صاحب دعوة فليتقدّم اليهما"));
arasuperobject.biblemap.put("b2_24_15", new arasuperobject(2,24,15,"فصعد موسى الى الجبل. فغطّى السحاب الجبل"));
arasuperobject.biblemap.put("b2_24_16", new arasuperobject(2,24,16,"وحلّ مجد الرب على جبل سيناء وغطّاه السحاب ستة ايام. وفي اليوم السابع دعي موسى من وسط السحاب"));
arasuperobject.biblemap.put("b2_24_17", new arasuperobject(2,24,17,"وكان منظر مجد الرب كنار آكلة على راس الجبل امام عيون بني اسرائيل"));
arasuperobject.biblemap.put("b2_24_18", new arasuperobject(2,24,18,"ودخل موسى في وسط السحاب وصعد الى الجبل. وكان موسى في الجبل اربعين نهارا واربعين ليلة"));
arasuperobject.biblemap.put("b2_25_1", new arasuperobject(2,25,1,"وكلم الرب موسى قائلا"));
arasuperobject.biblemap.put("b2_25_2", new arasuperobject(2,25,2,"كلم بني اسرائيل ان يأخذوا لي تقدمة. من كل من يحثّه قلبه تأخذون تقدمتي"));
arasuperobject.biblemap.put("b2_25_3", new arasuperobject(2,25,3,"وهذه هي التقدمة التي تاخذونها منهم. ذهب وفضة ونحاس"));
arasuperobject.biblemap.put("b2_25_4", new arasuperobject(2,25,4,"واسمانجوني وارجوان وقرمز وبوص وشعر معزى"));
arasuperobject.biblemap.put("b2_25_5", new arasuperobject(2,25,5,"وجلود كباش محمّرة وجلود تخس وخشب سنط"));
arasuperobject.biblemap.put("b2_25_6", new arasuperobject(2,25,6,"وزيت للمنارة واطياب لدهن المسحة وللبخور العطر"));
arasuperobject.biblemap.put("b2_25_7", new arasuperobject(2,25,7,"وحجارة جزع وحجارة ترصيع للرداء والصدرة"));
