arasuperobject.biblemap.put("b2_25_9", new arasuperobject(2,25,9,"بحسب جميع ما انا أريك من مثال المسكن ومثال جميع آنيته هكذا تصنعون"));
arasuperobject.biblemap.put("b2_25_10", new arasuperobject(2,25,10,"فيصنعون تابوتا من خشب السنط طوله ذراعان ونصف وعرضه ذراع ونصف وارتفاعه ذراع ونصف"));
arasuperobject.biblemap.put("b2_25_11", new arasuperobject(2,25,11,"وتغشّيه بذهب نقي. من داخل ومن خارج تغشّيه. وتصنع عليه اكليلا من ذهب حواليه"));
arasuperobject.biblemap.put("b2_25_12", new arasuperobject(2,25,12,"وتسبك له اربع حلقات من ذهب وتجعلها على قوائمه الاربع. على جانبه الواحد حلقتان وعلى جانبه الثاني حلقتان"));
arasuperobject.biblemap.put("b2_25_13", new arasuperobject(2,25,13,"وتصنع عصوين من خشب السنط وتغشّيهما بذهب"));
arasuperobject.biblemap.put("b2_25_14", new arasuperobject(2,25,14,"وتدخل العصوين في الحلقات على جانبي التابوت ليحمل التابوت بهما"));
