arasuperobject.biblemap.put("b2_25_16", new arasuperobject(2,25,16,"وتضع في التابوت الشهادة التي اعطيك"));
arasuperobject.biblemap.put("b2_25_17", new arasuperobject(2,25,17,"وتصنع غطاء من ذهب نقي طوله ذراعان ونصف وعرضه ذراع ونصف"));
arasuperobject.biblemap.put("b2_25_18", new arasuperobject(2,25,18,"وتصنع كروبين من ذهب. صنعة خراطة تصنعهما على طرفي الغطاء"));
arasuperobject.biblemap.put("b2_25_19", new arasuperobject(2,25,19,"فاصنع كروبا واحدا على الطرف من هنا. وكروبا آخر على الطرف من هناك. من الغطاء تصنعون الكروبين على طرفيه"));
arasuperobject.biblemap.put("b2_25_20", new arasuperobject(2,25,20,"ويكون الكروبان باسطين اجنحتهما الى فوق مظلّلين باجنحتهما على الغطاء ووجههما كل واحد الى الآخر. نحو الغطاء يكون وجها الكروبين"));
arasuperobject.biblemap.put("b2_25_21", new arasuperobject(2,25,21,"وتجعل الغطاء على التابوت من فوق. وفي التابوت تضع الشهادة التي اعطيك"));
arasuperobject.biblemap.put("b2_25_22", new arasuperobject(2,25,22,"وانا اجتمع بك هناك واتكلم معك من على الغطاء من بين الكروبين اللذين على تابوت الشهادة بكل ما اوصيك به الى بني اسرائيل"));
arasuperobject.biblemap.put("b2_25_23", new arasuperobject(2,25,23,"وتصنع مائدة من خشب السنط طولها ذراعان وعرضها ذراع وارتفاعها ذراع ونصف"));
arasuperobject.biblemap.put("b2_25_24", new arasuperobject(2,25,24,"وتغشّيها بذهب نقي. وتصنع لها اكليلا من ذهب حواليها"));
arasuperobject.biblemap.put("b2_25_25", new arasuperobject(2,25,25,"وتصنع لها حاجبا على شبر حواليها. وتصنع لحاجبها اكليلا من ذهب حواليها"));
arasuperobject.biblemap.put("b2_25_26", new arasuperobject(2,25,26,"وتصنع لها اربع حلقات من ذهب وتجعل الحلقات على الزوايا الاربع التي لقوائهما الاربع"));
