arasuperobject.biblemap.put("b2_25_28", new arasuperobject(2,25,28,"وتصنع العصوين من خشب السنط وتغشّيهما بذهب. فتحمل بهما المائدة"));
arasuperobject.biblemap.put("b2_25_29", new arasuperobject(2,25,29,"وتصنع صحافها وصحونها وكاساتها وجاماتها التي يسكب بها. من ذهب نقي تصنعها"));
arasuperobject.biblemap.put("b2_25_30", new arasuperobject(2,25,30,"وتجعل على المائدة خبز الوجوه امامي دائما"));
arasuperobject.biblemap.put("b2_25_31", new arasuperobject(2,25,31,"وتصنع منارة من ذهب نقي. عمل الخراطة تصنع المنارة قاعدتها وساقها. تكون كاساتها وعجرها وازهارها منها"));
arasuperobject.biblemap.put("b2_25_32", new arasuperobject(2,25,32,"وست شعب خارجة من جانبيها. من جانبها الواحد ثلاث شعب منارة. ومن جانبها الثاني ثلاث شعب منارة"));
arasuperobject.biblemap.put("b2_25_33", new arasuperobject(2,25,33,"في الشعبة الواحدة ثلاث كاسات لوزية بعجرة وزهر. وفي الشعبة الثانية ثلاث كاسات لوزية بعجرة وزهر. وهكذا الى الست الشعب الخارجة من المنارة"));
arasuperobject.biblemap.put("b2_25_34", new arasuperobject(2,25,34,"وفي المنارة اربع كاسات لوزية بعجرها وازهارها"));
arasuperobject.biblemap.put("b2_25_35", new arasuperobject(2,25,35,"وتحت الشعبتين منها عجرة وتحت الشعبتين منها عجرة وتحت الشعبتين منها عجرة الى الست الشعب الخارجة من المنارة"));
arasuperobject.biblemap.put("b2_25_36", new arasuperobject(2,25,36,"تكون عجرها وشعبها منها. جميعها خراطة واحدة من ذهب نقي"));
arasuperobject.biblemap.put("b2_25_37", new arasuperobject(2,25,37,"وتصنع سرجها سبعة. فتصعد سرجها لتضيء الى مقابلها"));
arasuperobject.biblemap.put("b2_25_38", new arasuperobject(2,25,38,"وملاقطها ومنافضها من ذهب نقي"));
arasuperobject.biblemap.put("b2_25_39", new arasuperobject(2,25,39,"من وزنة ذهب نقي تصنع مع جميع هذه الاواني"));
arasuperobject.biblemap.put("b2_25_40", new arasuperobject(2,25,40,"وانظر فاصنعها على مثالها الذي أظهر لك في الجبل"));
arasuperobject.biblemap.put("b2_26_1", new arasuperobject(2,26,1,"واما المسكن فتصنعه من عشر شقق بوص مبروم واسمانجوني وارجوان وقرمز. بكروبيم صنعة حائك حاذق تصنعها"));
arasuperobject.biblemap.put("b2_26_2", new arasuperobject(2,26,2,"طول الشقة الواحدة ثمان وعشرون ذراعا وعرض الشقة الواحدة اربع اذرع. قياسا واحدا لجميع الشقق"));
arasuperobject.biblemap.put("b2_26_3", new arasuperobject(2,26,3,"تكون خمس من الشقق بعضها موصول ببعض وخمس شقق بعضها موصول ببعض"));
